INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então foi conduzido Jesus pelo Espírito ao deserto, para ser tentado pelo diabo.', 1, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo jejuado quarenta dias e quarenta noites, depois teve fome;', 2, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando-se a ele o tentador, disse: Se tu és o Filho de Deus, manda que estas pedras se tornem em pães.', 3, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, respondendo, disse: Está escrito: Nem só de pão viverá o homem, mas de toda a palavra que sai da boca de Deus.', 4, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o diabo o transportou à cidade santa, e colocou-o sobre o pináculo do templo,', 5, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe: Se tu és o Filho de Deus, lança-te de aqui abaixo; porque está escrito: Que aos seus anjos dará ordens a teu respeito, E tomar-te-ão nas mãos, Para que nunca tropeces com o teu pé em alguma pedra.', 6, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Também está escrito: Não tentarás o Senhor teu Deus.', 7, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Novamente o transportou o diabo a um monte muito alto; e mostrou-lhe todos os reinos do mundo, e a glória deles.', 8, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe: Tudo isto te darei se, prostrado, me adorares.', 9, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então disse-lhe Jesus: Vai-te, Satanás, porque está escrito: Ao Senhor teu Deus adorarás, e só a ele servirás.', 10, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o diabo o deixou; e, eis que chegaram os anjos, e o serviam.', 11, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, ouvindo que João estava preso, voltou para a Galiléia;', 12, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, deixando Nazaré, foi habitar em Cafarnaum, cidade marítima, nos confins de Zebulom e Naftali;', 13, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que se cumprisse o que foi dito pelo profeta Isaías, que diz:', 14, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A terra de Zebulom, e a terra de Naftali, Junto ao caminho do mar, além do Jordão, A Galiléia das nações;', 15, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O povo, que estava assentado em trevas, Viu uma grande luz; aos que estavam assentados na região e sombra da morte,A luz raiou.', 16, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde então começou Jesus a pregar, e a dizer: Arrependei-vos, porque é chegado o reino dos céus.', 17, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, andando junto ao mar da Galiléia, viu a dois irmãos, Simão, chamado Pedro, e André, seu irmão, os quais lançavam as redes ao mar, porque eram pescadores;', 18, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Vinde após mim, e eu vos farei pescadores de homens.', 19, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então eles, deixando logo as redes, seguiram-no.', 20, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, adiantando-se dali, viu outros dois irmãos, Tiago, filho de Zebedeu, e João, seu irmão, num barco com seu pai, Zebedeu, consertando as redes;', 21, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chamou-os; eles, deixando imediatamente o barco e seu pai, seguiram-no.', 22, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E percorria Jesus toda a Galiléia, ensinando nas suas sinagogas e pregando o evangelho do reino, e curando todas as enfermidades e moléstias entre o povo.', 23, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a sua fama correu por toda a Síria, e traziam-lhe todos os que padeciam, acometidos de várias enfermidades e tormentos, os endemoninhados, os lunáticos, e os paralíticos, e ele os curava.', 24, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E seguia-o uma grande multidão da Galiléia, de Decápolis, de Jerusalém, da Judéia, e de além do Jordão.', 25, 4, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo Jesus saído de casa, naquele dia, estava assentado junto ao mar;', 1, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ajuntou-se muita gente ao pé dele, de sorte que, entrando num barco, se assentou; e toda a multidão estava em pé na praia.', 2, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E falou-lhe de muitas coisas por parábolas, dizendo: Eis que o semeador saiu a semear.', 3, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando semeava, uma parte da semente caiu ao pé do caminho, e vieram as aves, e comeram-na;', 4, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra parte caiu em pedregais, onde não havia terra bastante, e logo nasceu, porque não tinha terra funda;', 5, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, vindo o sol, queimou-se, e secou-se, porque não tinha raiz.', 6, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra caiu entre espinhos, e os espinhos cresceram e sufocaram-na.', 7, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra caiu em boa terra, e deu fruto: um a cem, outro a sessenta e outro a trinta.', 8, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem tem ouvidos para ouvir, ouça.', 9, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, acercando-se dele os discípulos, disseram-lhe: Por que lhes falas por parábolas?', 10, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, respondendo, disse-lhes: Porque a vós é dado conhecer os mistérios do reino dos céus, mas a eles não lhes é dado;', 11, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque àquele que tem, se dará, e terá em abundância; mas àquele que não tem, até aquilo que tem lhe será tirado.', 12, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso lhes falo por parábolas; porque eles, vendo, não vêem; e, ouvindo, não ouvem nem compreendem.', 13, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E neles se cumpre a profecia de Isaías, que diz:Ouvindo, ouvireis, mas não compreendereis,e, vendo, vereis, mas não percebereis.', 14, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o coração deste povo está endurecido,E ouviram de mau grado com seus ouvidos,E fecharam seus olhos;Para que não vejam com os olhos,E ouçam com os ouvidos,e compreendam com o coração,e se convertam,e eu os cure.', 15, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, bem-aventurados os vossos olhos, porque vêem, e os vossos ouvidos, porque ouvem.', 16, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque em verdade vos digo que muitos profetas e justos desejaram ver o que vós vedes, e não o viram; e ouvir o que vós ouvis, e não o ouviram.', 17, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Escutai vós, pois, a parábola do semeador.', 18, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvindo alguém a palavra do reino, e não a entendendo, vem o maligno, e arrebata o que foi semeado no seu coração; este é o que foi semeado ao pé do caminho.', 19, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que foi semeado em pedregais é o que ouve a palavra, e logo a recebe com alegria;', 20, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não tem raiz em si mesmo, antes é de pouca duração; e, chegada a angústia e a perseguição, por causa da palavra, logo se ofende;', 21, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que foi semeado entre espinhos é o que ouve a palavra, mas os cuidados deste mundo, e a sedução das riquezas sufocam a palavra, e fica infrutífera;', 22, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, o que foi semeado em boa terra é o que ouve e compreende a palavra; e dá fruto, e um produz cem, outro sessenta, e outro trinta.', 23, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Propôs-lhes outra parábola, dizendo: O reino dos céus é semelhante ao homem que semeia a boa semente no seu campo;', 24, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, dormindo os homens, veio o seu inimigo, e semeou joio no meio do trigo, e retirou-se.', 25, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando a erva cresceu e frutificou, apareceu também o joio.', 26, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os servos do pai de família, indo ter com ele, disseram-lhe: Senhor, não semeaste tu, no teu campo, boa semente? Por que tem, então, joio?', 27, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Um inimigo é quem fez isso. E os servos lhe disseram: Queres pois que vamos arrancá-lo?', 28, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, lhes disse: Não; para que, ao colher o joio, não arranqueis também o trigo com ele.', 29, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deixai crescer ambos juntos até à ceifa; e, por ocasião da ceifa, direi aos ceifeiros: Colhei primeiro o joio, e atai-o em molhos para o queimar; mas, o trigo, ajuntai-o no meu celeiro.', 30, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Outra parábola lhes propôs, dizendo: O reino dos céus é semelhante ao grão de mostarda que o homem, pegando nele, semeou no seu campo;', 31, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual é, realmente, a menor de todas as sementes; mas, crescendo, é a maior das plantas, e faz-se uma árvore, de sorte que vêm as aves do céu, e se aninham nos seus ramos.', 32, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Outra parábola lhes disse: O reino dos céus é semelhante ao fermento, que uma mulher toma e introduz em três medidas de farinha, até que tudo esteja levedado.', 33, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tudo isto disse Jesus, por parábolas à multidão, e nada lhes falava sem parábolas;', 34, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que se cumprisse o que fora dito pelo profeta, que disse: Abrirei em parábolas a minha boca; Publicarei coisas ocultas desde a fundação do mundo.', 35, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, tendo despedido a multidão, foi Jesus para casa. E chegaram ao pé dele os seus discípulos, dizendo: Explica-nos a parábola do joio do campo.', 36, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, respondendo, disse-lhes: O que semeia a boa semente, é o Filho do homem;', 37, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O campo é o mundo; e a boa semente são os filhos do reino; e o joio são os filhos do maligno;', 38, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O inimigo, que o semeou, é o diabo; e a ceifa é o fim do mundo; e os ceifeiros são os anjos.', 39, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim como o joio é colhido e queimado no fogo, assim será na consumação deste mundo.', 40, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mandará o Filho do homem os seus anjos, e eles colherão do seu reino tudo o que causa escândalo, e os que cometem iniqüidade.', 41, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lançá-los-ão na fornalha de fogo; ali haverá pranto e ranger de dentes.', 42, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então os justos resplandecerão como o sol, no reino de seu Pai. Quem tem ouvidos para ouvir, ouça.', 43, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Também o reino dos céus é semelhante a um tesouro escondido num campo, que um homem achou e escondeu; e, pelo gozo dele, vai, vende tudo quanto tem, e compra aquele campo.', 44, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Outrossim o reino dos céus é semelhante ao homem, negociante, que busca boas pérolas;', 45, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, encontrando uma pérola de grande valor, foi, vendeu tudo quanto tinha, e comprou-a.', 46, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Igualmente o reino dos céus é semelhante a uma rede lançada ao mar, e que apanha toda a qualidade de peixes.', 47, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando cheia, a puxam para a praia; e, assentando-se, apanham para os cestos os bons; os ruins, porém, lançam fora.', 48, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim será na consumação dos séculos: virão os anjos, e separarão os maus de entre os justos,', 49, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lançá-los-ão na fornalha de fogo; ali haverá pranto e ranger de dentes.', 50, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes Jesus: Entendestes todas estas coisas? Disseram-lhe eles: Sim, Senhor.', 51, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse-lhes: Por isso, todo o escriba instruído acerca do reino dos céus é semelhante a um pai de família, que tira do seu tesouro coisas novas e velhas.', 52, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que Jesus, concluindo estas parábolas, se retirou dali.', 53, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando à sua pátria, ensinava-os na sinagoga deles, de sorte que se maravilhavam, e diziam: De onde veio a este a sabedoria, e estas maravilhas?', 54, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não é este o filho do carpinteiro? e não se chama sua mãe Maria, e seus irmãos Tiago, e José, e Simão, e Judas?', 55, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não estão entre nós todas as suas irmãs? De onde lhe veio, pois, tudo isto?', 56, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E escandalizavam-se nele. Jesus, porém, lhes disse: Não há profeta sem honra, a não ser na sua pátria e na sua casa.', 57, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não fez ali muitas maravilhas, por causa da incredulidade deles.', 58, 13, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando os seus doze discípulos, deu-lhes poder sobre os espíritos imundos, para os expulsarem, e para curarem toda a enfermidade e todo o mal.', 1, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, os nomes dos doze apóstolos são estes: O primeiro, Simão, chamado Pedro, e André, seu irmão; Tiago, filho de Zebedeu, e João, seu irmão;', 2, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Filipe e Bartolomeu; Tomé e Mateus, o publicano; Tiago, filho de Alfeu, e Lebeu, apelidado Tadeu;', 3, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Simão, o Cananita, e Judas Iscariotes, aquele que o traiu.', 4, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus enviou estes doze, e lhes ordenou, dizendo: Não ireis pelo caminho dos gentios, nem entrareis em cidade de samaritanos;', 5, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ide antes às ovelhas perdidas da casa de Israel;', 6, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo, pregai, dizendo: É chegado o reino dos céus.', 7, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Curai os enfermos, limpai os leprosos, ressuscitai os mortos, expulsai os demônios; de graça recebestes, de graça dai.', 8, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não possuais ouro, nem prata, nem cobre, em vossos cintos,', 9, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem alforges para o caminho, nem duas túnicas, nem alparcas, nem bordões; porque digno é o operário do seu alimento.', 10, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, em qualquer cidade ou aldeia em que entrardes, procurai saber quem nela seja digno, e hospedai-vos aí, até que vos retireis.', 11, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando entrardes nalguma casa, saudai-a;', 12, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se a casa for digna, desça sobre ela a vossa paz; mas, se não for digna, torne para vós a vossa paz.', 13, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se ninguém vos receber, nem escutar as vossas palavras, saindo daquela casa ou cidade, sacudi o pó dos vossos pés.', 14, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que, no dia do juízo, haverá menos rigor para o país de Sodoma e Gomorra do que para aquela cidade.', 15, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que vos envio como ovelhas ao meio de lobos; portanto, sede prudentes como as serpentes e inofensivos como as pombas.', 16, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Acautelai-vos, porém, dos homens; porque eles vos entregarão aos sinédrios, e vos açoitarão nas suas sinagogas;', 17, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sereis até conduzidos à presença dos governadores, e dos reis, por causa de mim, para lhes servir de testemunho a eles, e aos gentios.', 18, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando vos entregarem, não vos dê cuidado como, ou o que haveis de falar, porque naquela mesma hora vos será ministrado o que haveis de dizer.', 19, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não sois vós quem falará, mas o Espírito de vosso Pai é que fala em vós.', 20, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o irmão entregará à morte o irmão, e o pai o filho; e os filhos se levantarão contra os pais, e os matarão.', 21, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E odiados de todos sereis por causa do meu nome; mas aquele que perseverar até ao fim, esse será salvo.', 22, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando pois vos perseguirem nesta cidade, fugi para outra; porque em verdade vos digo que não acabareis de percorrer as cidades de Israel sem que venha o Filho do homem.', 23, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não é o discípulo mais do que o mestre, nem o servo mais do que o seu senhor.', 24, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Basta ao discípulo ser como seu mestre, e ao servo como seu senhor. Se chamaram Belzebu ao pai de família, quanto mais aos seus domésticos?', 25, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, não os temais; porque nada há encoberto que não haja de revelar-se, nem oculto que não haja de saber-se.', 26, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que vos digo em trevas dizei-o em luz; e o que escutais ao ouvido pregai-o sobre os telhados.', 27, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não temais os que matam o corpo e não podem matar a alma; temei antes aquele que pode fazer perecer no inferno a alma e o corpo.', 28, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não se vendem dois passarinhos por um ceitil? e nenhum deles cairá em terra sem a vontade de vosso Pai.', 29, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E até mesmo os cabelos da vossa cabeça estão todos contados.', 30, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não temais, pois; mais valeis vós do que muitos passarinhos.', 31, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, qualquer que me confessar diante dos homens, eu o confessarei diante de meu Pai, que está nos céus.', 32, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas qualquer que me negar diante dos homens, eu o negarei também diante de meu Pai, que está nos céus.', 33, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não cuideis que vim trazer a paz à terra; não vim trazer paz, mas espada;', 34, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu vim pôr em dissensão o homem contra seu pai, e a filha contra sua mãe, e a nora contra sua sogra;', 35, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim os inimigos do homem serão os seus familiares.', 36, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem ama o pai ou a mãe mais do que a mim não é digno de mim; e quem ama o filho ou a filha mais do que a mim não é digno de mim.', 37, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quem não toma a sua cruz, e não segue após mim, não é digno de mim.', 38, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem achar a sua vida perdê-la-á; e quem perder a sua vida, por amor de mim, achá-la-á.', 39, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem vos recebe, a mim me recebe; e quem me recebe a mim, recebe aquele que me enviou.', 40, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem recebe um profeta em qualidade de profeta, receberá galardão de profeta; e quem recebe um justo na qualidade de justo, receberá galardão de justo.', 41, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qualquer que tiver dado só que seja um copo de água fria a um destes pequenos, em nome de discípulo, em verdade vos digo que de modo algum perderá o seu galardão.', 42, 10, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo nascido Jesus em Belém de Judéia, no tempo do rei Herodes, eis que uns magos vieram do oriente a Jerusalém,', 1, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Onde está aquele que é nascido rei dos judeus? porque vimos a sua estrela no oriente, e viemos a adorá-lo.', 2, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o rei Herodes, ouvindo isto, perturbou-se, e toda Jerusalém com ele.', 3, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, congregados todos os príncipes dos sacerdotes, e os escribas do povo, perguntou-lhes onde havia de nascer o Cristo.', 4, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles lhe disseram: Em Belém de Judéia; porque assim está escrito pelo profeta:', 5, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tu, Belém, terra de Judá,De modo nenhum és a menor entre as capitais de Judá;porque de ti sairá o Guia que há de apascentar o meu povo Israel.', 6, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Herodes, chamando secretamente os magos, inquiriu exatamente deles acerca do tempo em que a estrela lhes aparecera.', 7, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, enviando-os a Belém, disse: Ide, e perguntai diligentemente pelo menino e, quando o achardes, participai-mo, para que também eu vá e o adore.', 8, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo eles ouvido o rei, partiram; e eis que a estrela, que tinham visto no oriente, ia adiante deles, até que, chegando, se deteve sobre o lugar onde estava o menino.', 9, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo eles a estrela, regoziram-se muito com grande alegria.', 10, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando na casa, acharam o menino com Maria sua mãe e, prostrando-se, o adoraram; e abrindo os seus tesouros, ofertaram-lhe dádivas: ouro, incenso e mirra.', 11, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo por divina revelação avisados num sonho para que não voltassem para junto de Herodes, partiram para a sua terra por outro caminho.', 12, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo eles se retirado, eis que o anjo do Senhor apareceu a José num sonho, dizendo: Levanta-te, e toma o menino e sua mãe, e foge para o Egito, e demora-te lá até que eu te diga; porque Herodes há de procurar o menino para o matar.', 13, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se ele, tomou o menino e sua mãe, de noite, e foi para o Egito.', 14, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E esteve lá, até à morte de Herodes, para que se cumprisse o que foi dito da parte do Senhor pelo profeta, que diz: Do Egito chamei o meu Filho.', 15, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Herodes, vendo que tinha sido iludido pelos magos, irritou-se muito, e mandou matar todos os meninos que havia em Belém, e em todos os seus contornos, de dois anos para baixo, segundo o tempo que diligentemente inquirira dos magos.', 16, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então se cumpriu o que foi dito pelo profeta Jeremias, que diz:', 17, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em Ramá se ouviu uma voz, Lamentação, choro e grande pranto: Raquel chorando os seus filhos, E não quer ser consolada, porque já não existem.', 18, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Morto, porém, Herodes, eis que o anjo do Senhor apareceu num sonho a José no Egito,', 19, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Levanta-te, e toma o menino e sua mãe, e vai para a terra de Israel; porque já estão mortos os que procuravam a morte do menino.', 20, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então ele se levantou, e tomou o menino e sua mãe, e foi para a terra de Israel.', 21, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo que Arquelau reinava na Judéia em lugar de Herodes, seu pai, receou ir para lá; mas avisado num sonho, por divina revelação, foi para as partes da Galiléia.', 22, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegou, e habitou numa cidade chamada Nazaré, para que se cumprisse o que fora dito pelos profetas: Ele será chamado Nazareno.', 23, 2, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Naquele tempo passou Jesus pelas searas, em um sábado; e os seus discípulos, tendo fome, começaram a colher espigas, e a comer.', 1, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os fariseus, vendo isto, disseram-lhe: Eis que os teus discípulos fazem o que não é lícito fazer num sábado.', 2, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, lhes disse: Não tendes lido o que fez Davi, quando teve fome, ele e os que com ele estavam?', 3, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como entrou na casa de Deus, e comeu os pães da proposição, que não lhe era lícito comer, nem aos que com ele estavam, mas só aos sacerdotes?', 4, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou não tendes lido na lei que, aos sábados, os sacerdotes no templo violam o sábado, e ficam sem culpa?', 5, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois eu vos digo que está aqui quem é maior do que o templo.', 6, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se vós soubésseis o que significa: Misericórdia quero, e não sacrifício, não condenaríeis os inocentes.', 7, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Filho do homem até do sábado é Senhor.', 8, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, partindo dali, chegou à sinagoga deles.', 9, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estava ali um homem que tinha uma das mãos mirrada; e eles, para o acusarem, o interrogaram, dizendo: É lícito curar nos sábados?', 10, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Qual dentre vós será o homem que tendo uma ovelha, se num sábado ela cair numa cova, não lançará mão dela, e a levantará?', 11, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, quanto mais vale um homem do que uma ovelha? É, por conseqüência, lícito fazer bem nos sábados.', 12, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então disse àquele homem: Estende a tua mão. E ele a estendeu, e ficou sã como a outra.', 13, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os fariseus, tendo saído, formaram conselho contra ele, para o matarem.', 14, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, sabendo isso, retirou-se dali, e acompanharam-no grandes multidões, e ele curou a todas.', 15, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E recomendava-lhes rigorosamente que o não descobrissem,', 16, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que se cumprisse o que fora dito pelo profeta Isaías, que diz:', 17, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis aqui o meu servo, que escolhi,o meu amado, em quem a minha alma se compraz;porei sobre ele o meu espírito,e anunciará aos gentios o juízo.', 18, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não contenderá, nem clamará,Nem alguém ouvirá pelas ruas a sua voz;', 19, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não esmagará a cana quebrada,enão apagará o morrão que fumega,até que faça triunfar o juízo;', 20, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no seu nome os gentios esperarão.', 21, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Trouxeram-lhe, então, um endemoninhado cego e mudo; e, de tal modo o curou, que o cego e mudo falava e via.', 22, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E toda a multidão se admirava e dizia: Não é este o Filho de Davi?', 23, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os fariseus, ouvindo isto, diziam: Este não expulsa os demônios senão por Belzebu, príncipe dos demônios.', 24, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, conhecendo os seus pensamentos, disse-lhes: Todo o reino dividido contra si mesmo é devastado; e toda a cidade, ou casa, dividida contra si mesma não subsistirá.', 25, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se Satanás expulsa a Satanás, está dividido contra si mesmo; como subsistirá, pois, o seu reino?', 26, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se eu expulso os demônios por Belzebu, por quem os expulsam então vossos filhos? Portanto, eles mesmos serão os vossos juízes.', 27, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se eu expulso os demônios pelo Espírito de Deus, logo é chegado a vós o reino de Deus.', 28, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou, como pode alguém entrar na casa do homem valente, e furtar os seus bens, se primeiro não maniatar o valente, saqueando então a sua casa?', 29, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem não é comigo é contra mim; e quem comigo não ajunta, espalha.', 30, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, eu vos digo: Todo o pecado e blasfêmia se perdoará aos homens; mas a blasfêmia contra o Espírito não será perdoada aos homens.', 31, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se qualquer disser alguma palavra contra o Filho do homem, ser-lhe-á perdoado; mas, se alguém falar contra o Espírito Santo, não lhe será perdoado, nem neste século nem no futuro.', 32, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou fazei a árvore boa, e o seu fruto bom, ou fazei a árvore má, e o seu fruto mau; porque pelo fruto se conhece a árvore.', 33, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Raça de víboras, como podeis vós dizer boas coisas, sendo maus? Pois do que há em abundância no coração, disso fala a boca.', 34, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O homem bom tira boas coisas do bom tesouro do seu coração, e o homem mau do mau tesouro tira coisas más.', 35, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eu vos digo que de toda a palavra ociosa que os homens disserem hão de dar conta no dia do juízo.', 36, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque por tuas palavras serás justificado, e por tuas palavras serás condenado.', 37, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então alguns dos escribas e dos fariseus tomaram a palavra, dizendo: Mestre, quiséramos ver da tua parte algum sinal.', 38, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele lhes respondeu, e disse: Uma geração má e adúltera pede um sinal, porém, não se lhe dará outro sinal senão o sinal do profeta Jonas;', 39, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, como Jonas esteve três dias e três noites no ventre da baleia, assim estará o Filho do homem três dias e três noites no seio da terra.', 40, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os ninivitas ressurgirão no juízo com esta geração, e a condenarão, porque se arrependeram com a pregação de Jonas. E eis que está aqui quem é maior do que Jonas.', 41, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A rainha do sul se levantará no dia do juízo com esta geração, e a condenará; porque veio dos confins da terra para ouvir a sabedoria de Salomão. E eis que está aqui quem é maior do que Salomão.', 42, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando o espírito imundo tem saído do homem, anda por lugares áridos, buscando repouso, e não o encontra.', 43, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então diz: Voltarei para a minha casa, de onde saí. E, voltando, acha-a desocupada, varrida e adornada.', 44, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então vai, e leva consigo outros sete espíritos piores do que ele e, entrando, habitam ali; e são os últimos atos desse homem piores do que os primeiros. Assim acontecerá também a esta geração má.', 45, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, falando ele ainda à multidão, eis que estavam fora sua mãe e seus irmãos, pretendendo falar-lhe.', 46, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe alguém: Eis que estão ali fora tua mãe e teus irmãos, que querem falar-te.', 47, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, respondendo, disse ao que lhe falara: Quem é minha mãe? E quem são meus irmãos?', 48, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estendendo a sua mão para os seus discípulos, disse: Eis aqui minha mãe e meus irmãos;', 49, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, qualquer que fizer a vontade de meu Pai que está nos céus, este é meu irmão, e irmã e mãe.', 50, 12, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando a manhã, todos os príncipes dos sacerdotes, e os anciãos do povo, formavam juntamente conselho contra Jesus, para o matarem;', 1, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E maniatando-o, o levaram e entregaram ao presidente Pôncio Pilatos.', 2, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Judas, o que o traíra, vendo que fora condenado, trouxe, arrependido, as trinta moedas de prata aos príncipes dos sacerdotes e aos anciãos,', 3, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Pequei, traindo o sangue inocente. Eles, porém, disseram: Que nos importa? Isso é contigo.', 4, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, atirando para o templo as moedas de prata, retirou-se e foi-se enforcar.', 5, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os príncipes dos sacerdotes, tomando as moedas de prata, disseram: Não é lícito colocá-las no cofre das ofertas, porque são preço de sangue.', 6, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo deliberado em conselho, compraram com elas o campo de um oleiro, para sepultura dos estrangeiros.', 7, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso foi chamado aquele campo, até ao dia de hoje, Campo de Sangue.', 8, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então se realizou o que vaticinara o profeta Jeremias: Tomaram as trinta moedas de prata, preço do que foi avaliado, que certos filhos de Israel avaliaram,', 9, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E deram-nas pelo campo do oleiro, segundo o que o Senhor me determinou.', 10, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi Jesus apresentado ao presidente, e o presidente o interrogou, dizendo: És tu o Rei dos Judeus? E disse-lhe Jesus: Tu o dizes.', 11, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo acusado pelos príncipes dos sacerdotes e pelos anciãos, nada respondeu.', 12, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe então Pilatos: Não ouves quanto testificam contra ti?', 13, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nem uma palavra lhe respondeu, de sorte que o presidente estava muito maravilhado.', 14, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, por ocasião da festa, costumava o presidente soltar um preso, escolhendo o povo aquele que quisesse.', 15, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tinham então um preso bem conhecido, chamado Barrabás.', 16, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, estando eles reunidos, disse-lhes Pilatos: Qual quereis que vos solte? Barrabás, ou Jesus, chamado Cristo?', 17, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque sabia que por inveja o haviam entregado.', 18, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando ele assentado no tribunal, sua mulher mandou-lhe dizer: Não entres na questão desse justo, porque num sonho muito sofri por causa dele.', 19, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os príncipes dos sacerdotes e os anciãos persuadiram à multidão que pedisse Barrabás e matasse Jesus.', 20, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo o presidente, disse-lhes: Qual desses dois quereis vós que eu solte? E eles disseram: Barrabás.', 21, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes Pilatos: Que farei então de Jesus, chamado Cristo? Disseram-lhe todos: Seja crucificado.', 22, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O presidente, porém, disse: Mas que mal fez ele? E eles mais clamavam, dizendo: Seja crucificado.', 23, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pilatos, vendo que nada aproveitava, antes o tumulto crescia, tomando água, lavou as mãos diante da multidão, dizendo: Estou inocente do sangue deste justo. Considerai isso.', 24, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo todo o povo, disse: O seu sangue caia sobre nós e sobre nossos filhos.', 25, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então soltou-lhes Barrabás, e, tendo mandado açoitar a Jesus, entregou-o para ser crucificado.', 26, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo os soldados do presidente, conduzindo Jesus à audiência, reuniram junto dele toda a coorte.', 27, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, despindo-o, o cobriram com uma capa de escarlate;', 28, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tecendo uma coroa de espinhos, puseram-lha na cabeça, e em sua mão direita uma cana; e, ajoelhando diante dele, o escarneciam, dizendo: Salve, Rei dos judeus.', 29, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, cuspindo nele, tiraram-lhe a cana, e batiam-lhe com ela na cabeça.', 30, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois de o haverem escarnecido, tiraram-lhe a capa, vestiram-lhe as suas vestes e o levaram para ser crucificado.', 31, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando saíam, encontraram um homem cireneu, chamado Simão, a quem constrangeram a levar a sua cruz.', 32, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando ao lugar chamado Gólgota, que se diz: Lugar da Caveira,', 33, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deram-lhe a beber vinagre misturado com fel; mas ele, provando-o, não quis beber.', 34, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-o crucificado, repartiram as suas vestes, lançando sortes, para que se cumprisse o que foi dito pelo profeta: Repartiram entre si as minhas vestes, e sobre a minha túnica lançaram sortes.', 35, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, assentados, o guardavam ali.', 36, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por cima da sua cabeça puseram escrita a sua acusação: este e³ jesus, o rei dos judeus.', 37, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram crucificados com ele dois salteadores, um à direita, e outro à esquerda.', 38, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que passavam blasfemavam dele, meneando as cabeças,', 39, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizendo: Tu, que destróis o templo, e em três dias o reedificas, salva-te a ti mesmo. Se és Filho de Deus, desce da cruz.', 40, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E da mesma maneira também os príncipes dos sacerdotes, com os escribas, e anciãos, e fariseus, escarnecendo, diziam:', 41, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Salvou os outros, e a si mesmo não pode salvar-se. Se é o Rei de Israel, desça agora da cruz, e creremos nele.', 42, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Confiou em Deus; livre-o agora, se o ama; porque disse: Sou Filho de Deus.', 43, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o mesmo lhe lançaram também em rosto os salteadores que com ele estavam crucificados.', 44, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desde a hora sexta houve trevas sobre toda a terra, até à hora nona.', 45, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perto da hora nona exclamou Jesus em alta voz, dizendo: Eli, Eli, lamá sabactâni; isto é, Deus meu, Deus meu, por que me desamparaste?', 46, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns dos que ali estavam, ouvindo isto, diziam: Este chama por Elias,', 47, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo um deles, correndo, tomou uma esponja, e embebeu-a em vinagre, e, pondo-a numa cana, dava-lhe de beber.', 48, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os outros, porém, diziam: Deixa, vejamos se Elias vem livrá-lo.', 49, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, clamando outra vez com grande voz, rendeu o espírito.', 50, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que o véu do templo se rasgou em dois, de alto a baixo; e tremeu a terra, e fenderam-se as pedras;', 51, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E abriram-se os sepulcros, e muitos corpos de santos que dormiam foram ressuscitados;', 52, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo dos sepulcros, depois da ressurreição dele, entraram na cidade santa, e apareceram a muitos.', 53, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o centurião e os que com ele guardavam a Jesus, vendo o terremoto, e as coisas que haviam sucedido, tiveram grande temor, e disseram: Verdadeiramente este era o Filho de Deus.', 54, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estavam ali, olhando de longe, muitas mulheres que tinham seguido Jesus desde a Galiléia, para o servir;', 55, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entre as quais estavam Maria Madalena, e Maria, mãe de Tiago e de José, e a mãe dos filhos de Zebedeu.', 56, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vinda já a tarde, chegou um homem rico, de Arimatéia, por nome José, que também era discípulo de Jesus.', 57, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este foi ter com Pilatos, e pediu-lhe o corpo de Jesus. Então Pilatos mandou que o corpo lhe fosse dado.', 58, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E José, tomando o corpo, envolveu-o num fino e limpo lençol,', 59, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o pôs no seu sepulcro novo, que havia aberto em rocha, e, rodando uma grande pedra para a porta do sepulcro, retirou-se.', 60, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estavam ali Maria Madalena e a outra Maria, assentadas defronte do sepulcro.', 61, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no dia seguinte, que é o dia depois da Preparação, reuniram-se os príncipes dos sacerdotes e os fariseus em casa de Pilatos,', 62, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Senhor, lembramo-nos de que aquele enganador, vivendo ainda, disse: Depois de três dias ressuscitarei.', 63, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Manda, pois, que o sepulcro seja guardado com segurança até ao terceiro dia, não se dê o caso que os seus discípulos vão de noite, e o furtem, e digam ao povo: Ressuscitou dentre os mortos; e assim o último erro será pior do que o primeiro.', 64, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes Pilatos: Tendes a guarda; ide, guardai-o como entenderdes.', 65, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo eles, seguraram o sepulcro com a guarda, selando a pedra.', 66, 27, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, vendo a multidão, subiu a um monte, e, assentando-se, aproximaram-se dele os seus discípulos;', 1, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, abrindo a sua boca, os ensinava, dizendo:', 2, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados os pobres de espírito, porque deles é o reino dos céus;', 3, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados os que choram, porque eles serão consolados;', 4, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados os mansos, porque eles herdarão a terra;', 5, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados os que têm fome e sede de justiça, porque eles serão fartos;', 6, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados os misericordiosos, porque eles alcançarão misericórdia;', 7, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados os limpos de coração, porque eles verão a Deus;', 8, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados os pacificadores, porque eles serão chamados filhos de Deus;', 9, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados os que sofrem perseguição por causa da justiça, porque deles é o reino dos céus;', 10, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados sois vós, quando vos injuriarem e perseguirem e, mentindo, disserem todo o mal contra vós por minha causa.', 11, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Exultai e alegrai-vos, porque é grande o vosso galardão nos céus; porque assim perseguiram os profetas que foram antes de vós.', 12, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós sois o sal da terra; e se o sal for insípido, com que se há de salgar? Para nada mais presta senão para se lançar fora, e ser pisado pelos homens.', 13, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós sois a luz do mundo; não se pode esconder uma cidade edificada sobre um monte;', 14, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem se acende a candeia e se coloca debaixo do alqueire, mas no velador, e dá luz a todos que estão na casa.', 15, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim resplandeça a vossa luz diante dos homens, para que vejam as vossas boas obras e glorifiquem a vosso Pai, que está nos céus.', 16, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não cuideis que vim destruir a lei ou os profetas: não vim abrogar, mas cumprir.', 17, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque em verdade vos digo que, até que o céu e a terra passem, nem um jota ou um til jamais passará da lei, sem que tudo seja cumprido.', 18, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer, pois, que violar um destes mandamentos, por menor que seja, e assim ensinar aos homens, será chamado o menor no reino dos céus; aquele, porém, que os cumprir e ensinar será chamado grande no reino dos céus.', 19, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque vos digo que, se a vossa justiça não exceder a dos escribas e fariseus, de modo nenhum entrareis no reino dos céus.', 20, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvistes que foi dito aos antigos: Não matarás; mas qualquer que matar será réu de juízo.', 21, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, porém, vos digo que qualquer que, sem motivo, se encolerizar contra seu irmão, será réu de juízo; e qualquer que disser a seu irmão: Raca, será réu do sinédrio; e qualquer que lhe disser: Louco, será réu do fogo do inferno.', 22, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, se trouxeres a tua oferta ao altar, e aí te lembrares de que teu irmão tem alguma coisa contra ti,', 23, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deixa ali diante do altar a tua oferta, e vai reconciliar-te primeiro com teu irmão e, depois, vem e apresenta a tua oferta.', 24, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Concilia-te depressa com o teu adversário, enquanto estás no caminho com ele, para que não aconteça que o adversário te entregue ao juiz, e o juiz te entregue ao oficial, e te encerrem na prisão.', 25, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade te digo que de maneira nenhuma sairás dali enquanto não pagares o último ceitil.', 26, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvistes que foi dito aos antigos: Não cometerás adultério.', 27, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, porém, vos digo, que qualquer que atentar numa mulher para a cobiçar, já em seu coração cometeu adultério com ela.', 28, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, se o teu olho direito te escandalizar, arranca-o e atira-o para longe de ti; pois te é melhor que se perca um dos teus membros do que seja todo o teu corpo lançado no inferno.', 29, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se a tua mão direita te escandalizar, corta-a e atira-a para longe de ti, porque te é melhor que um dos teus membros se perca do que seja todo o teu corpo lançado no inferno.', 30, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Também foi dito: Qualquer que deixar sua mulher, dê-lhe carta de divórcio.', 31, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, porém, vos digo que qualquer que repudiar sua mulher, a não ser por causa de fornicação, faz que ela cometa adultério, e qualquer que casar com a repudiada comete adultério.', 32, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Outrossim, ouvistes que foi dito aos antigos: Não perjurarás, mas cumprirás os teus juramentos ao Senhor.', 33, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, porém, vos digo que de maneira nenhuma jureis; nem pelo céu, porque é o trono de Deus;', 34, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem pela terra, porque é o escabelo de seus pés; nem por Jerusalém, porque é a cidade do grande Rei;', 35, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem jurarás pela tua cabeça, porque não podes tornar um cabelo branco ou preto.', 36, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Seja, porém, o vosso falar: Sim, sim; Não, não; porque o que passa disto é de procedência maligna.', 37, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvistes que foi dito: Olho por olho, e dente por dente.', 38, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, porém, vos digo que não resistais ao mau; mas, se qualquer te bater na face direita, oferece-lhe também a outra;', 39, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ao que quiser pleitear contigo, e tirar-te a túnica, larga-lhe também a capa;', 40, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se qualquer te obrigar a caminhar uma milha, vai com ele duas.', 41, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dá a quem te pedir, e não te desvies daquele que quiser que lhe emprestes.', 42, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvistes que foi dito: Amarás o teu próximo, e odiarás o teu inimigo.', 43, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, porém, vos digo: Amai a vossos inimigos, bendizei os que vos maldizem, fazei bem aos que vos odeiam, e orai pelos que vos maltratam e vos perseguem; para que sejais filhos do vosso Pai que está nos céus;', 44, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque faz que o seu sol se levante sobre maus e bons, e a chuva desça sobre justos e injustos.', 45, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, se amardes os que vos amam, que galardão tereis? Não fazem os publicanos também o mesmo?', 46, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se saudardes unicamente os vossos irmãos, que fazeis de mais? Não fazem os publicanos também assim?', 47, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sede vós pois perfeitos, como é perfeito o vosso Pai que está nos céus.', 48, 5, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, naqueles dias, apareceu João o Batista pregando no deserto da Judéia,', 1, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizendo: Arrependei-vos, porque é chegado o reino dos céus.', 2, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque este é o anunciado pelo profeta Isaías, que disse:Voz do que clama no deserto:Preparai o caminho do Senhor,Endireitai as suas veredas.', 3, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E este João tinha as suas vestes de pelos de camelo, e um cinto de couro em torno de seus lombos; e alimentava-se de gafanhotos e de mel silvestre.', 4, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então ia ter com ele Jerusalém, e toda a Judéia, e toda a província adjacente ao Jordão;', 5, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eram por ele batizados no rio Jordão, confessando os seus pecados.', 6, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo ele muitos dos fariseus e dos saduceus, que vinham ao seu batismo, dizia-lhes: Raça de víboras, quem vos ensinou a fugir da ira futura?', 7, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Produzi, pois, frutos dignos de arrependimento;', 8, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não presumais, de vós mesmos, dizendo: Temos por pai a Abraão; porque eu vos digo que, mesmo destas pedras, Deus pode suscitar filhos a Abraão.', 9, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também agora está posto o machado à raiz das árvores; toda a árvore, pois, que não produz bom fruto, é cortada e lançada no fogo.', 10, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu, em verdade, vos batizo com água, para o arrependimento; mas aquele que vem após mim é mais poderoso do que eu; cujas alparcas não sou digno de levar; ele vos batizará com o Espírito Santo, e com fogo.', 11, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em sua mão tem a pá, e limpará a sua eira, e recolherá no celeiro o seu trigo, e queimará a palha com fogo que nunca se apagará.', 12, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então veio Jesus da Galiléia ter com João, junto do Jordão, para ser batizado por ele.', 13, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas João opunha-se-lhe, dizendo: Eu careço de ser batizado por ti, e vens tu a mim?', 14, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, respondendo, disse-lhe: Deixa por agora, porque assim nos convém cumprir toda a justiça. Então ele o permitiu.', 15, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo Jesus batizado, saiu logo da água, e eis que se lhe abriram os céus, e viu o Espírito de Deus descendo como pomba e vindo sobre ele.', 16, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que uma voz dos céus dizia: Este é o meu Filho amado, em quem me comprazo.', 17, 3, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Livro da geração de Jesus Cristo, filho de Davi, filho de Abraão.', 1, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Abraão gerou a Isaque; e Isaque gerou a Jacó; e Jacó gerou a Judá e a seus irmãos;', 2, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Judá gerou, de Tamar, a Perez e a Zerá; e Perez gerou a Esrom; e Esrom gerou a Arão;', 3, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Arão gerou a Aminadabe; e Aminadabe gerou a Naassom; e Naassom gerou a Salmom;', 4, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Salmom gerou, de Raabe, a Boaz; e Boaz gerou de Rute a Obede; e Obede gerou a Jessé;', 5, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jessé gerou ao rei Davi; e o rei Davi gerou a Salomão da que foi mulher de Urias.', 6, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Salomão gerou a Roboão; e Roboão gerou a Abias; e Abias gerou a Asa;', 7, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Asa gerou a Josafá; e Josafá gerou a Jorão; e Jorão gerou a Uzias;', 8, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Uzias gerou a Jotão; e Jotão gerou a Acaz; e Acaz gerou a Ezequias;', 9, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Ezequias gerou a Manassés; e Manassés gerou a Amom; e Amom gerou a Josias;', 10, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Josias gerou a Jeconias e a seus irmãos na deportação para babilônia.', 11, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois da deportação para a babilônia, Jeconias gerou a Salatiel; e Salatiel gerou a Zorobabel;', 12, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Zorobabel gerou a Abiúde; e Abiúde gerou a Eliaquim; e Eliaquim gerou a Azor;', 13, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Azor gerou a Sadoque; e Sadoque gerou a Aquim; e Aquim gerou a Eliúde;', 14, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Eliúde gerou a Eleazar; e Eleazar gerou a Matã; e Matã gerou a Jacó;', 15, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jacó gerou a José, marido de Maria, da qual nasceu JESUS, que se chama o Cristo.', 16, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que todas as gerações, desde Abraão até Davi, são catorze gerações; e desde Davi até a deportação para a babilônia, catorze gerações; e desde a deportação para a babilônia até Cristo, catorze gerações.', 17, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o nascimento de Jesus Cristo foi assim: Que estando Maria, sua mãe, desposada com José, antes de se ajuntarem, achou-se ter concebido do Espírito Santo.', 18, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então José, seu marido, como era justo, e a não queria infamar, intentou deixá-la secretamente.', 19, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, projetando ele isto, eis que em sonho lhe apareceu um anjo do Senhor, dizendo: José, filho de Davi, não temas receber a Maria, tua mulher, porque o que nela está gerado é do Espírito Santo;', 20, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dará à luz um filho e chamarás o seu nome JESUS; porque ele salvará o seu povo dos seus pecados.', 21, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tudo isto aconteceu para que se cumprisse o que foi dito da parte do Senhor, pelo profeta, que diz;', 22, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que a virgem conceberá, e dará à luz um filho, E chamá-lo-ão pelo nome de EMANUEL, Que traduzido é: Deus conosco.', 23, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E José, despertando do sono, fez como o anjo do Senhor lhe ordenara, e recebeu a sua mulher;', 24, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não a conheceu até que deu à luz seu filho, o primogênito; e pôs-lhe por nome Jesus.', 25, 1, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, descendo ele do monte, seguiu-o uma grande multidão.', 1, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, eis que veio um leproso, e o adorou, dizendo: Senhor, se quiseres, podes tornar-me limpo.', 2, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, estendendo a mão, tocou-o, dizendo: Quero; sê limpo. E logo ficou purificado da lepra.', 3, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe então Jesus: Olha, não o digas a alguém, mas vai, mostra-te ao sacerdote, e apresenta a oferta que Moisés determinou, para lhes servir de testemunho.', 4, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando Jesus em Cafarnaum, chegou junto dele um centurião, rogando-lhe,', 5, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizendo: Senhor, o meu criado jaz em casa, paralítico, e violentamente atormentado.', 6, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhe disse: Eu irei, e lhe darei saúde.', 7, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o centurião, respondendo, disse: Senhor, não sou digno de que entres debaixo do meu telhado, mas dize somente uma palavra, e o meu criado há de sarar.', 8, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois também eu sou homem sob autoridade, e tenho soldados às minhas ordens; e digo a este: Vai, e ele vai; e a outro: Vem, e ele vem; e ao meu criado: Faze isto, e ele o faz.', 9, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E maravilhou-se Jesus, ouvindo isto, e disse aos que o seguiam: Em verdade vos digo que nem mesmo em Israel encontrei tanta fé.', 10, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eu vos digo que muitos virão do oriente e do ocidente, e assentar-se-ão à mesa com Abraão, e Isaque, e Jacó, no reino dos céus;', 11, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os filhos do reino serão lançados nas trevas exteriores; ali haverá pranto e ranger de dentes.', 12, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então disse Jesus ao centurião: Vai, e como creste te seja feito. E naquela mesma hora o seu criado sarou.', 13, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, entrando em casa de Pedro, viu a sogra deste acamada, e com febre.', 14, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tocou-lhe na mão, e a febre a deixou; e levantou-se, e serviu-os.', 15, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegada a tarde, trouxeram-lhe muitos endemoninhados, e ele com a sua palavra expulsou deles os espíritos, e curou todos os que estavam enfermos;', 16, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que se cumprisse o que fora dito pelo profeta Isaías, que diz: Ele tomou sobre si as nossas enfermidades, e levou as nossas doenças.', 17, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, vendo em torno de si uma grande multidão, ordenou que passassem para o outro lado;', 18, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, aproximando-se dele um escriba, disse-lhe: Mestre, aonde quer que fores, eu te seguirei.', 19, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Jesus: As raposas têm covis, e as aves do céu têm ninhos, mas o Filho do homem não tem onde reclinar a cabeça.', 20, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outro de seus discípulos lhe disse: Senhor, permite-me que primeiramente vá sepultar meu pai.', 21, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, disse-lhe: Segue-me, e deixa os mortos sepultar os seus mortos.', 22, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando ele no barco, seus discípulos o seguiram;', 23, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que no mar se levantou uma tempestade, tão grande que o barco era coberto pelas ondas; ele, porém, estava dormindo.', 24, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus discípulos, aproximando-se, o despertaram, dizendo: Senhor, salva-nos! que perecemos.', 25, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse-lhes: Por que temeis, homens de pouca fé? Então, levantando-se, repreendeu os ventos e o mar, e seguiu-se uma grande bonança.', 26, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aqueles homens se maravilharam, dizendo: Que homem é este, que até os ventos e o mar lhe obedecem?', 27, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo chegado ao outro lado, à província dos gergesenos, saíram-lhe ao encontro dois endemoninhados, vindos dos sepulcros; tão ferozes eram que ninguém podia passar por aquele caminho.', 28, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que clamaram, dizendo: Que temos nós contigo, Jesus, Filho de Deus? Vieste aqui atormentar-nos antes do tempo?', 29, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E andava pastando distante deles uma manada de muitos porcos.', 30, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os demônios rogaram-lhe, dizendo: Se nos expulsas, permite-nos que entremos naquela manada de porcos.', 31, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Ide. E, saindo eles, se introduziram na manada dos porcos; e eis que toda aquela manada de porcos se precipitou no mar por um despenhadeiro, e morreram nas águas.', 32, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os porqueiros fugiram e, chegando à cidade, divulgaram tudo o que acontecera aos endemoninhados.', 33, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que toda aquela cidade saiu ao encontro de Jesus e, vendo-o, rogaram-lhe que se retirasse dos seus termos.', 34, 8, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não julgueis, para que não sejais julgados.', 1, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque com o juízo com que julgardes sereis julgados, e com a medida com que tiverdes medido vos hão de medir a vós.', 2, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por que reparas tu no argueiro que está no olho do teu irmão, e não vês a trave que está no teu olho?', 3, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou como dirás a teu irmão: Deixa-me tirar o argueiro do teu olho, estando uma trave no teu?', 4, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Hipócrita, tira primeiro a trave do teu olho, e então cuidarás em tirar o argueiro do olho do teu irmão.', 5, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não deis aos cães as coisas santas, nem deiteis aos porcos as vossas pérolas, não aconteça que as pisem com os pés e, voltando-se, vos despedacem.', 6, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pedi, e dar-se-vos-á; buscai, e encontrareis; batei, e abrir-se-vos-á.', 7, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, aquele que pede, recebe; e, o que busca, encontra; e, ao que bate, abrir-se-lhe-á.', 8, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qual dentre vós é o homem que, pedindo-lhe pão o seu filho, lhe dará uma pedra?', 9, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pedindo-lhe peixe, lhe dará uma serpente?', 10, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se vós, pois, sendo maus, sabeis dar boas coisas aos vossos filhos, quanto mais vosso Pai, que está nos céus, dará bens aos que lhe pedirem?', 11, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, tudo o que vós quereis que os homens vos façam, fazei-lho também vós, porque esta é a lei e os profetas.', 12, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entrai pela porta estreita; porque larga é a porta, e espaçoso o caminho que conduz à perdição, e muitos são os que entram por ela;', 13, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E porque estreita é a porta, e apertado o caminho que leva à vida, e poucos há que a encontrem.', 14, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Acautelai-vos, porém, dos falsos profetas, que vêm até vós vestidos como ovelhas, mas, interiormente, são lobos devoradores.', 15, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por seus frutos os conhecereis. Porventura colhem-se uvas dos espinheiros, ou figos dos abrolhos?', 16, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, toda a árvore boa produz bons frutos, e toda a árvore má produz frutos maus.', 17, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não pode a árvore boa dar maus frutos; nem a árvore má dar frutos bons.', 18, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Toda a árvore que não dá bom fruto corta-se e lança-se no fogo.', 19, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, pelos seus frutos os conhecereis.', 20, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem todo o que me diz: Senhor, Senhor! entrará no reino dos céus, mas aquele que faz a vontade de meu Pai, que está nos céus.', 21, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Muitos me dirão naquele dia: Senhor, Senhor, não profetizamos nós em teu nome? e em teu nome não expulsamos demônios? e em teu nome não fizemos muitas maravilhas?', 22, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E então lhes direi abertamente: Nunca vos conheci; apartai-vos de mim, vós que praticais a iniqüidade.', 23, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todo aquele, pois, que escuta estas minhas palavras, e as pratica, assemelhá-lo-ei ao homem prudente, que edificou a sua casa sobre a rocha;', 24, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desceu a chuva, e correram rios, e assopraram ventos, e combateram aquela casa, e não caiu, porque estava edificada sobre a rocha.', 25, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele que ouve estas minhas palavras, e não as cumpre, compará-lo-ei ao homem insensato, que edificou a sua casa sobre a areia;', 26, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desceu a chuva, e correram rios, e assopraram ventos, e combateram aquela casa, e caiu, e foi grande a sua queda.', 27, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, concluindo Jesus este discurso, a multidão se admirou da sua doutrina;', 28, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto os ensinava como tendo autoridade; e não como os escribas.', 29, 7, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Guardai-vos de fazer a vossa esmola diante dos homens, para serdes vistos por eles; aliás, não tereis galardão junto de vosso Pai, que está nos céus.', 1, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando, pois, deres esmola, não faças tocar trombeta diante de ti, como fazem os hipócritas nas sinagogas e nas ruas, para serem glorificados pelos homens. Em verdade vos digo que já receberam o seu galardão.', 2, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando tu deres esmola, não saiba a tua mão esquerda o que faz a tua direita;', 3, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que a tua esmola seja dada em secreto; e teu Pai, que vê em secreto, ele mesmo te recompensará publicamente.', 4, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando orares, não sejas como os hipócritas; pois se comprazem em orar em pé nas sinagogas, e às esquinas das ruas, para serem vistos pelos homens. Em verdade vos digo que já receberam o seu galardão.', 5, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas tu, quando orares, entra no teu aposento e, fechando a tua porta, ora a teu Pai que está em secreto; e teu Pai, que vê em secreto, te recompensará publicamente.', 6, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, orando, não useis de vãs repetições, como os gentios, que pensam que por muito falarem serão ouvidos.', 7, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos assemelheis, pois, a eles; porque vosso Pai sabe o que vos é necessário, antes de vós lho pedirdes.', 8, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, vós orareis assim: Pai nosso, que estás nos céus, santificado seja o teu nome;', 9, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Venha o teu reino, seja feita a tua vontade, assim na terra como no céu;', 10, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O pão nosso de cada dia nos dá hoje;', 11, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perdoa-nos as nossas dívidas, assim como nós perdoamos aos nossos devedores;', 12, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não nos conduzas à tentação; mas livra-nos do mal; porque teu é o reino, e o poder, e a glória, para sempre. Amém.', 13, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se perdoardes aos homens as suas ofensas, também vosso Pai celestial vos perdoará a vós;', 14, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, porém, não perdoardes aos homens as suas ofensas, também vosso Pai vos não perdoará as vossas ofensas.', 15, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando jejuardes, não vos mostreis contristados como os hipócritas; porque desfiguram os seus rostos, para que aos homens pareça que jejuam. Em verdade vos digo que já receberam o seu galardão.', 16, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu, porém, quando jejuares, unge a tua cabeça, e lava o teu rosto,', 17, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para não pareceres aos homens que jejuas, mas a teu Pai, que está em secreto; e teu Pai, que vê em secreto, te recompensará publicamente.', 18, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não ajunteis tesouros na terra, onde a traça e a ferrugem tudo consomem, e onde os ladrões minam e roubam;', 19, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ajuntai tesouros no céu, onde nem a traça nem a ferrugem consomem, e onde os ladrões não minam nem roubam.', 20, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque onde estiver o vosso tesouro, aí estará também o vosso coração.', 21, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A candeia do corpo são os olhos; de sorte que, se os teus olhos forem bons, todo o teu corpo terá luz;', 22, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, porém, os teus olhos forem maus, o teu corpo será tenebroso. Se, portanto, a luz que em ti há são trevas, quão grandes serão tais trevas!', 23, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém pode servir a dois senhores; porque ou há de odiar um e amar o outro, ou se dedicará a um e desprezará o outro. Não podeis servir a Deus e a Mamom.', 24, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso vos digo: Não andeis cuidadosos quanto à vossa vida, pelo que haveis de comer ou pelo que haveis de beber; nem quanto ao vosso corpo, pelo que haveis de vestir. Não é a vida mais do que o mantimento, e o corpo mais do que o vestuário?', 25, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Olhai para as aves do céu, que nem semeiam, nem segam, nem ajuntam em celeiros; e vosso Pai celestial as alimenta. Não tendes vós muito mais valor do que elas?', 26, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qual de vós poderá, com todos os seus cuidados, acrescentar um côvado à sua estatura?', 27, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quanto ao vestuário, por que andais solícitos? Olhai para os lírios do campo, como eles crescem; não trabalham nem fiam;', 28, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu vos digo que nem mesmo Salomão, em toda a sua glória, se vestiu como qualquer deles.', 29, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, se Deus assim veste a erva do campo, que hoje existe, e amanhã é lançada no forno, não vos vestirá muito mais a vós, homens de pouca fé?', 30, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não andeis, pois, inquietos, dizendo: Que comeremos, ou que beberemos, ou com que nos vestiremos?', 31, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todas estas coisas os gentios procuram.  Decerto vosso Pai celestial bem sabe que necessitais de todas estas coisas;', 32, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, buscai primeiro o reino de Deus, e a sua justiça, e todas estas coisas vos serão acrescentadas.', 33, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos inquieteis, pois, pelo dia de amanhã, porque o dia de amanhã cuidará de si mesmo. Basta a cada dia o seu mal.', 34, 6, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando no barco, passou para o outro lado, e chegou à sua cidade. E eis que lhe trouxeram um paralítico, deitado numa cama.', 1, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, vendo a fé deles, disse ao paralítico: Filho, tem bom ânimo, perdoados te são os teus pecados.', 2, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que alguns dos escribas diziam entre si: Ele blasfema.', 3, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus, conhecendo os seus pensamentos, disse: Por que pensais mal em vossos corações?', 4, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, qual é mais fácil? dizer: Perdoados te são os teus pecados; ou dizer: Levanta-te e anda?', 5, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, para que saibais que o Filho do homem tem na terra autoridade para perdoar pecados (disse então ao paralítico): Levanta-te, toma a tua cama, e vai para tua casa.', 6, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se, foi para sua casa.', 7, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a multidão, vendo isto, maravilhou-se, e glorificou a Deus, que dera tal poder aos homens.', 8, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, passando adiante dali, viu assentado na alfândega um homem, chamado Mateus, e disse-lhe: Segue-me. E ele, levantando-se, o seguiu.', 9, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, estando ele em casa sentado à mesa, chegaram muitos publicanos e pecadores, e sentaram-se juntamente com Jesus e seus discípulos.', 10, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os fariseus, vendo isto, disseram aos seus discípulos: Por que come o vosso Mestre com os publicanos e pecadores?', 11, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, ouvindo, disse-lhes: Não necessitam de médico os sãos, mas, sim, os doentes.', 12, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ide, porém, e aprendei o que significa: Misericórdia quero, e não sacrifício. Porque eu nào vim a chamar os justos, mas os pecadores, ao arrependimento.', 13, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, chegaram ao pé dele os discípulos de João, dizendo: Por que jejuamos nós e os fariseus muitas vezes, e os teus discípulos não jejuam?', 14, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes Jesus: Podem porventura andar tristes os filhos das bodas, enquanto o esposo está com eles? Dias, porém, virão, em que lhes será tirado o esposo, e então jejuarão.', 15, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém deita remendo de pano novo em roupa velha, porque semelhante remendo rompe a roupa, e faz-se maior a rotura.', 16, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem se deita vinho novo em odres velhos; aliás rompem-se os odres, e entorna-se o vinho, e os odres estragam-se; mas deita-se vinho novo em odres novos, e assim ambos se conservam.', 17, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo-lhes ele estas coisas, eis que chegou um chefe, e o adorou, dizendo: Minha filha faleceu agora mesmo; mas vem, impõe-lhe a tua mão, e ela viverá.', 18, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, levantando-se, seguiu-o, ele e os seus discípulos.', 19, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que uma mulher que havia já doze anos padecia de um fluxo de sangue, chegando por detrás dele, tocou a orla de sua roupa;', 20, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque dizia consigo: Se eu tão-somente tocar a sua roupa, ficarei sã.', 21, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, voltando-se, e vendo-a, disse: Tem ânimo, filha, a tua fé te salvou. E imediatamente a mulher ficou sã.', 22, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, chegando à casa daquele chefe, e vendo os instrumentistas, e o povo em alvoroço,', 23, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes: Retirai-vos, que a menina não está morta, mas dorme. E riam-se dele.', 24, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo que o povo foi posto fora, entrou Jesus, e pegou-lhe na mão, e a menina levantou-se.', 25, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E espalhou-se aquela notícia por todo aquele país.', 26, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, partindo Jesus dali, seguiram-no dois cegos, clamando, e dizendo: Tem compaixão de nós, filho de Davi.', 27, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando chegou à casa, os cegos se aproximaram dele; e Jesus disse-lhes: Credes vós que eu possa fazer isto? Disseram-lhe eles: Sim, Senhor.', 28, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tocou então os olhos deles, dizendo: Seja-vos feito segundo a vossa fé.', 29, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os olhos se lhes abriram. E Jesus ameaçou-os, dizendo: Olhai que ninguém o saiba.', 30, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, tendo eles saído, divulgaram a sua fama por toda aquela terra.', 31, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-se eles retirado, trouxeram-lhe um homem mudo e endemoninhado.', 32, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, expulso o demônio, falou o mudo; e a multidão se maravilhou, dizendo: Nunca tal se viu em Israel.', 33, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os fariseus diziam: Ele expulsa os demônios pelo príncipe dos demônios.', 34, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E percorria Jesus todas as cidades e aldeias, ensinando nas sinagogas deles, e pregando o evangelho do reino, e curando todas as enfermidades e moléstias entre o povo.', 35, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo as multidões, teve grande compaixão delas, porque andavam cansadas e desgarradas, como ovelhas que não têm pastor.', 36, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, disse aos seus discípulos: A seara é realmente grande, mas poucos os ceifeiros.', 37, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Rogai, pois, ao Senhor da seara, que mande ceifeiros para a sua seara.', 38, 9, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o reino dos céus é semelhante a um homem, pai de família, que saiu de madrugada a assalariar trabalhadores para a sua vinha.', 1, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ajustando com os trabalhadores a um dinheiro por dia, mandou-os para a sua vinha.', 2, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo perto da hora terceira, viu outros que estavam ociosos na praça,', 3, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Ide vós também para a vinha, e dar-vos-ei o que for justo. E eles foram.', 4, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saindo outra vez, perto da hora sexta e nona, fez o mesmo.', 5, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo perto da hora undécima, encontrou outros que estavam ociosos, e perguntou- lhes: Por que estais ociosos todo o dia?', 6, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe eles: Porque ninguém nos assalariou. Diz-lhes ele: Ide vós também para a vinha, e recebereis o que for justo.', 7, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, aproximando-se a noite, diz o senhor da vinha ao seu mordomo: Chama os trabalhadores, e paga-lhes o jornal, começando pelos derradeiros, até aos primeiros.', 8, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando os que tinham ido perto da hora undécima, receberam um dinheiro cada um.', 9, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vindo, porém, os primeiros, cuidaram que haviam de receber mais; mas do mesmo modo receberam um dinheiro cada um.', 10, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, recebendo-o, murmuravam contra o pai de família,', 11, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Estes derradeiros trabalharam só uma hora, e tu os igualaste conosco, que suportamos a fadiga e a calma do dia.', 12, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, respondendo, disse a um deles: Amigo, não te faço agravo; não ajustaste tu comigo um dinheiro?', 13, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Toma o que é teu, e retira-te; eu quero dar a este derradeiro tanto como a ti.', 14, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou não me é lícito fazer o que quiser do que é meu? Ou é mau o teu olho porque eu sou bom?', 15, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim os derradeiros serão primeiros, e os primeiros derradeiros; porque muitos são chamados, mas poucos escolhidos.', 16, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, subindo Jesus a Jerusalém, chamou à parte os seus doze discípulos, e no caminho disse-lhes:', 17, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que vamos para Jerusalém, e o Filho do homem será entregue aos príncipes dos sacerdotes, e aos escribas, e condená-lo-ão à morte.', 18, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o entregarão aos gentios para que dele escarneçam, e o açoitem e crucifiquem, e ao terceiro dia ressuscitará.', 19, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então se aproximou dele a mãe dos filhos de Zebedeu, com seus filhos, adorando-o, e fazendo-lhe um pedido.', 20, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele diz-lhe: Que queres? Ela respondeu: Dize que estes meus dois filhos se assentem, um à tua direita e outro à tua esquerda, no teu reino.', 21, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, respondendo, disse: Não sabeis o que pedis. Podeis vós beber o cálice que eu hei de beber, e ser batizados com o batismo com que eu sou batizado? Dizem-lhe eles: Podemos.', 22, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E diz-lhes ele: Na verdade bebereis o meu cálice e sereis batizados com o batismo com que eu sou batizado, mas o assentar-se à minha direita ou à minha esquerda não me pertence dá-lo, mas é para aqueles para quem meu Pai o tem preparado.', 23, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando os dez ouviram isto, indignaram-se contra os dois irmãos.', 24, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus, chamando-os para junto de si, disse: Bem sabeis que pelos príncipes dos gentios são estes dominados, e que os grandes exercem autoridade sobre eles.', 25, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não será assim entre vós; mas todo aquele que quiser entre vós fazer-se grande seja vosso serviçal;', 26, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, qualquer que entre vós quiser ser o primeiro, seja vosso servo;', 27, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem como o Filho do homem não veio para ser servido, mas para servir, e para dar a sua vida em resgate de muitos.', 28, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo eles de Jericó, seguiu-o grande multidão.', 29, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que dois cegos, assentados junto do caminho, ouvindo que Jesus passava, clamaram, dizendo: Senhor, Filho de Davi, tem misericórdia de nós!', 30, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a multidão os repreendia, para que se calassem; eles, porém, cada vez clamavam mais, dizendo: Senhor, Filho de Davi, tem misericórdia de nós!', 31, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, parando, chamou-os, e disse: Que quereis que vos faça?', 32, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe eles: Senhor, que os nossos olhos sejam abertos.', 33, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus, movido de íntima compaixão, tocou-lhes nos olhos, e logo seus olhos viram; e eles o seguiram.', 34, 20, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, concluindo Jesus estes discursos, saiu da Galiléia, e dirigiu- se aos confins da Judéia, além do Jordão;', 1, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E seguiram-no grandes multidões, e curou-as ali.', 2, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então chegaram ao pé dele os fariseus, tentando-o, e dizendo-lhe: É lícito ao homem repudiar sua mulher por qualquer motivo?', 3, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, respondendo, disse-lhes: Não tendes lido que aquele que os fez no princípio macho e fêmea os fez,', 4, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse: Portanto, deixará o homem pai e mãe, e se unirá a sua mulher, e serão dois numa só carne?', 5, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim não são mais dois, mas uma só carne. Portanto, o que Deus ajuntou não o separe o homem.', 6, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe eles: Então, por que mandou Moisés dar-lhe carta de divórcio, e repudiá-la?', 7, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes ele: Moisés, por causa da dureza dos vossos corações, vos permitiu repudiar vossas mulheres; mas ao princípio não foi assim.', 8, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu vos digo, porém, que qualquer que repudiar sua mulher, não sendo por causa de fornicação, e casar com outra, comete adultério; e o que casar com a repudiada também comete adultério.', 9, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe seus discípulos: Se assim é a condição do homem relativamente à mulher, não convém casar.', 10, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, lhes disse: Nem todos podem receber esta palavra, mas só aqueles a quem foi concedido.', 11, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque há eunucos que assim nasceram do ventre da mãe; e há eunucos que foram castrados pelos homens; e há eunucos que se castraram a si mesmos, por causa do reino dos céus. Quem pode receber isto, receba-o.', 12, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Trouxeram-lhe, então, alguns meninos, para que sobre eles pusesse as mãos, e orasse; mas os discípulos os repreendiam.', 13, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, disse: Deixai os meninos, e não os estorveis de vir a mim; porque dos tais é o reino dos céus.', 14, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo-lhes imposto as mãos, partiu dali.', 15, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que, aproximando-se dele um jovem, disse-lhe: Bom Mestre, que bem farei para conseguir a vida eterna?', 16, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse-lhe: Por que me chamas bom? Não há bom senão um só, que é Deus. Se queres, porém, entrar na vida, guarda os mandamentos.', 17, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe ele: Quais? E Jesus disse: Não matarás, não cometerás adultério, não furtarás, não dirás falso testemunho;', 18, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Honra teu pai e tua mãe, e amarás o teu próximo como a ti mesmo.', 19, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe o jovem: Tudo isso tenho guardado desde a minha mocidade; que me falta ainda?', 20, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Se queres ser perfeito, vai, vende tudo o que tens e dá-o aos pobres, e terás um tesouro no céu; e vem, e segue-me.', 21, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o jovem, ouvindo esta palavra, retirou-se triste, porque possuía muitas propriedades.', 22, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse então Jesus aos seus discípulos: Em verdade vos digo que é difícil entrar um rico no reino dos céus.', 23, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, outra vez vos digo que é mais fácil passar um camelo pelo fundo de uma agulha do que entrar um rico no reino de Deus.', 24, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os seus discípulos, ouvindo isto, admiraram-se muito, dizendo: Quem poderá pois salvar-se?', 25, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, olhando para eles, disse-lhes: Aos homens é isso impossível, mas a Deus tudo é possível.', 26, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pedro, tomando a palavra, disse-lhe: Eis que nós deixamos tudo, e te seguimos; que receberemos?', 27, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus disse-lhes: Em verdade vos digo que vós, que me seguistes, quando, na regeneração, o Filho do homem se assentar no trono da sua glória, também vos assentareis sobre doze tronos, para julgar as doze tribos de Israel.', 28, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todo aquele que tiver deixado casas, ou irmãos, ou irmãs, ou pai, ou mãe, ou mulher, ou filhos, ou terras, por amor de meu nome, receberá cem vezes tanto, e herdará a vida eterna.', 29, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porém, muitos primeiros serão os derradeiros, e muitos derradeiros serão os primeiros.', 30, 19, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, no fim do sábado, quando já despontava o primeiro dia da semana, Maria Madalena e a outra Maria foram ver o sepulcro.', 1, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que houvera um grande terremoto, porque um anjo do Senhor, descendo do céu, chegou, removendo a pedra da porta, e sentou-se sobre ela.', 2, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o seu aspecto era como um relâmpago, e as suas vestes brancas como neve.', 3, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os guardas, com medo dele, ficaram muito assombrados, e como mortos.', 4, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o anjo, respondendo, disse às mulheres: Não tenhais medo; pois eu sei que buscais a Jesus, que foi crucificado.', 5, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele não está aqui, porque já ressuscitou, como havia dito. Vinde, vede o lugar onde o Senhor jazia.', 6, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ide pois, imediatamente, e dizei aos seus discípulos que já ressuscitou dentre os mortos. E eis que ele vai adiante de vós para a Galiléia; ali o vereis. Eis que eu vo-lo tenho dito.', 7, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo elas pressurosamente do sepulcro, com temor e grande alegria, correram a anunciá-lo aos seus discípulos.', 8, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo elas a dar as novas aos seus discípulos, eis que Jesus lhes sai ao encontro, dizendo: Eu vos saúdo. E elas, chegando, abraçaram os seus pés, e o adoraram.', 9, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus disse-lhes: Não temais; ide dizer a meus irmãos que vão à Galiléia, e lá me verão.', 10, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando iam, eis que alguns da guarda, chegando à cidade, anunciaram aos príncipes dos sacerdotes todas as coisas que haviam acontecido.', 11, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, congregados eles com os anciãos, e tomando conselho entre si, deram muito dinheiro aos soldados,', 12, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Dizei: Vieram de noite os seus discípulos e, dormindo nós, o furtaram.', 13, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se isto chegar a ser ouvido pelo presidente, nós o persuadiremos, e vos poremos em segurança.', 14, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles, recebendo o dinheiro, fizeram como estavam instruídos. E foi divulgado este dito entre os judeus, até ao dia de hoje.', 15, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os onze discípulos partiram para a Galiléia, para o monte que Jesus lhes tinha designado.', 16, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando o viram, o adoraram; mas alguns duvidaram.', 17, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando-se Jesus, falou-lhes, dizendo: É-me dado todo o poder no céu e na terra.', 18, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto ide, fazei discípulos de todas as nações, batizando-os em nome do Pai, e do Filho, e do Espírito Santo;', 19, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ensinando-os a guardar todas as coisas que eu vos tenho mandado; e eis que eu estou convosco todos os dias, até a consumação dos séculos. Amém.', 20, 28, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando se aproximaram de Jerusalém, e chegaram a Betfagé, ao Monte das Oliveiras, enviou, então, Jesus dois discípulos, dizendo-lhes:', 1, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ide à aldeia que está defronte de vós, e logo encontrareis uma jumenta presa, e um jumentinho com ela; desprendei-a, e trazei-mos.', 2, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se alguém vos disser alguma coisa, direis que o Senhor os há de mister; e logo os enviará.', 3, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, tudo isto aconteceu para que se cumprisse o que foi dito pelo profeta, que diz:', 4, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizei à filha de Sião: Eis que o teu Rei aí te vem,Manso, e assentado sobre uma jumenta,E sobre um jumentinho, filho de animal de carga.', 5, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo os discípulos, e fazendo como Jesus lhes ordenara,', 6, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Trouxeram a jumenta e o jumentinho, e sobre eles puseram as suas vestes, e fizeram-no assentar em cima.', 7, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitíssima gente estendia as suas vestes pelo caminho, e outros cortavam ramos de árvores, e os espalhavam pelo caminho.', 8, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a multidão que ia adiante, e a que seguia, clamava, dizendo: Hosana ao Filho de Davi; bendito o que vem em nome do Senhor. Hosana nas alturas!', 9, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando ele em Jerusalém, toda a cidade se alvoroçou, dizendo: Quem é este?', 10, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a multidão dizia: Este é Jesus, o profeta de Nazaré da Galiléia.', 11, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E entrou Jesus no templo de Deus, e expulsou todos os que vendiam e compravam no templo, e derribou as mesas dos cambistas e as cadeiras dos que vendiam pombas;', 12, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Está escrito: A minha casa será chamada casa de oração; mas vós a tendes convertido em covil de ladrões.', 13, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram ter com ele no templo cegos e coxos, e curou-os.', 14, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vendo, então, os principais dos sacerdotes e os escribas as maravilhas que fazia, e os meninos clamando no templo: Hosana ao Filho de Davi, indignaram-se,', 15, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disseram-lhe: Ouves o que estes dizem? E Jesus lhes disse: Sim; nunca lestes: Pela boca dos meninos e das criancinhas de peito tiraste o perfeito louvor?', 16, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, deixando-os, saiu da cidade para Betânia, e ali passou a noite.', 17, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, de manhã, voltando para a cidade, teve fome;', 18, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, avistando uma figueira perto do caminho, dirigiu-se a ela, e não achou nela senão folhas. E disse-lhe: Nunca mais nasça fruto de ti! E a figueira secou imediatamente.', 19, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os discípulos, vendo isto, maravilharam-se, dizendo: Como secou imediatamente a figueira?', 20, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, respondendo, disse-lhes: Em verdade vos digo que, se tiverdes fé e não duvidardes, não só fareis o que foi feito à figueira, mas até se a este monte disserdes: Ergue-te, e precipita-te no mar, assim será feito;', 21, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tudo o que pedirdes em oração, crendo, o recebereis.', 22, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando ao templo, acercaram-se dele, estando já ensinando, os príncipes dos sacerdotes e os anciãos do povo, dizendo: Com que autoridade fazes isto? e quem te deu tal autoridade?', 23, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhes: Eu também vos perguntarei uma coisa; se ma disserdes, também eu vos direi com que autoridade faço isto.', 24, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O batismo de João, de onde era? Do céu, ou dos homens? E pensavam entre si, dizendo: Se dissermos: Do céu, ele nos dirá: Então por que não o crestes?', 25, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se dissermos: Dos homens, tememos o povo, porque todos consideram João como profeta.', 26, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo a Jesus, disseram: Não sabemos. Ele disse-lhes: Nem eu vos digo com que autoridade faço isto.', 27, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, que vos parece? Um homem tinha dois filhos, e, dirigindo-se ao primeiro, disse: Filho, vai trabalhar hoje na minha vinha.', 28, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, respondendo, disse: Não quero. Mas depois, arrependendo-se, foi.', 29, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dirigindo-se ao segundo, falou-lhe de igual modo; e, respondendo ele, disse: Eu vou, senhor; e não foi.', 30, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qual dos dois fez a vontade do pai? Disseram-lhe eles: O primeiro. Disse-lhes Jesus: Em verdade vos digo que os publicanos e as meretrizes entram adiante de vós no reino de Deus.', 31, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque João veio a vós no caminho da justiça, e não o crestes, mas os publicanos e as meretrizes o creram; vós, porém, vendo isto, nem depois vos arrependestes para o crer.', 32, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvi, ainda, outra parábola: Houve um homem, pai de família, que plantou uma vinha, e circundou-a de um valado, e construiu nela um lagar, e edificou uma torre, e arrendou-a a uns lavradores, e ausentou-se para longe.', 33, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando o tempo dos frutos, enviou os seus servos aos lavradores, para receber os seus frutos.', 34, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os lavradores, apoderando-se dos servos, feriram um, mataram outro, e apedrejaram outro.', 35, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois enviou outros servos, em maior número do que os primeiros; e eles fizeram-lhes o mesmo.', 36, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, por último, enviou-lhes seu filho, dizendo: Terão respeito a meu filho.', 37, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os lavradores, vendo o filho, disseram entre si: Este é o herdeiro; vinde, matemo-lo, e apoderemo-nos da sua herança.', 38, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, lançando mão dele, o arrastaram para fora da vinha, e o mataram.', 39, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando, pois, vier o senhor da vinha, que fará àqueles lavradores?', 40, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizem-lhe eles: Dará afrontosa morte aos maus, e arrendará a vinha a outros lavradores, que a seu tempo lhe dêem os frutos.', 41, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Diz-lhes Jesus: Nunca lestes nas Escrituras:A pedra, que os edificadores rejeitaram,essa foi posta por cabeça do ângulo;pelo Senhor foi feito isto,E é maravilhoso aos nossos olhos?', 42, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, eu vos digo que o reino de Deus vos será tirado, e será dado a uma nação que dê os seus frutos.', 43, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quem cair sobre esta pedra, despedaçar-se-á; e aquele sobre quem ela cair ficará reduzido a pó.', 44, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os príncipes dos sacerdotes e os fariseus, ouvindo estas palavras, entenderam que falava deles;', 45, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pretendendo prendê-lo, recearam o povo, porquanto o tinham por profeta.', 46, 21, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Seis dias depois, tomou Jesus consigo a Pedro, e a Tiago, e a João, seu irmão, e os conduziu em particular a um alto monte,', 1, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E transfigurou-se diante deles; e o seu rosto resplandeceu como o sol, e as suas vestes se tornaram brancas como a luz.', 2, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que lhes apareceram Moisés e Elias, falando com ele.', 3, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro, tomando a palavra, disse a Jesus: Senhor, bom é estarmos aqui; se queres, façamos aqui três tabernáculos, um para ti, um para Moisés, e um para Elias.', 4, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando ele ainda a falar, eis que uma nuvem luminosa os cobriu. E da nuvem saiu uma voz que dizia: Este é o meu amado Filho, em quem me comprazo; escutai-o.', 5, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os discípulos, ouvindo isto, caíram sobre os seus rostos, e tiveram grande medo.', 6, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, aproximando-se Jesus, tocou-lhes, e disse: Levantai-vos, e não tenhais medo.', 7, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, erguendo eles os olhos, ninguém viram senão unicamente a Jesus.', 8, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, descendo eles do monte, Jesus lhes ordenou, dizendo: A ninguém conteis a visão, até que o Filho do homem seja ressuscitado dentre os mortos.', 9, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus discípulos o interrogaram, dizendo: Por que dizem então os escribas que é mister que Elias venha primeiro?', 10, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhes: Em verdade Elias virá primeiro, e restaurará todas as coisas;', 11, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas digo-vos que Elias já veio, e não o conheceram, mas fizeram-lhe tudo o que quiseram. Assim farão eles também padecer o Filho do homem.', 12, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então entenderam os discípulos que lhes falara de João o Batista.', 13, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando chegaram à multidão, aproximou-se-lhe um homem, pondo-se de joelhos diante dele, e dizendo:', 14, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Senhor, tem misericórdia de meu filho, que é lunático e sofre muito; pois muitas vezes cai no fogo, e muitas vezes na água;', 15, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E trouxe-o aos teus discípulos; e não puderam curá-lo.', 16, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse: Ó geração incrédula e perversa! até quando estarei eu convosco, e até quando vos sofrerei? Trazeimo aqui.', 17, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, repreendeu Jesus o demônio, que saiu dele, e desde aquela hora o menino sarou.', 18, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então os discípulos, aproximando-se de Jesus em particular, disseram: Por que não pudemos nós expulsá-lo?', 19, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhes disse: Por causa de vossa incredulidade; porque em verdade vos digo que, se tiverdes fé como um grão de mostarda, direis a este monte: Passa daqui para acolá, e há de passar; e nada vos será impossível.', 20, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas esta casta de demônios não se expulsa senão pela oração e pelo jejum.', 21, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, achando-se eles na Galiléia, disse-lhes Jesus: O Filho do homem será entregue nas mãos dos homens;', 22, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E matá-lo-ão, e ao terceiro dia ressuscitará. E eles se entristeceram muito.', 23, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando eles a Cafarnaum, aproximaram-se de Pedro os que cobravam as dracmas, e disseram: O vosso mestre não paga as dracmas?', 24, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse ele: Sim. E, entrando em casa, Jesus se lhe antecipou, dizendo: Que te parece, Simão? De quem cobram os reis da terra os tributos, ou o censo? Dos seus filhos, ou dos alheios?', 25, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Pedro: Dos alheios. Disse-lhe Jesus: Logo, estão livres os filhos.', 26, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, para que os não escandalizemos, vai ao mar, lança o anzol, tira o primeiro peixe que subir, e abrindo-lhe a boca, encontrarás um estáter; toma-o, e dá-o por mim e por ti.', 27, 17, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando Jesus ia saindo do templo, aproximaram-se dele os seus discípulos para lhe mostrarem a estrutura do templo.', 1, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, lhes disse: Não vedes tudo isto? Em verdade vos digo que não ficará aqui pedra sobre pedra que não seja derrubada.', 2, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando assentado no Monte das Oliveiras, chegaram-se a ele os seus discípulos em particular, dizendo: Dize-nos, quando serão essas coisas, e que sinal haverá da tua vinda e do fim do mundo?', 3, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhes: Acautelai-vos, que ninguém vos engane;', 4, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque muitos virão em meu nome, dizendo: Eu sou o Cristo; e enganarão a muitos.', 5, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvireis de guerras e de rumores de guerras; olhai, não vos assusteis, porque é mister que isso tudo aconteça, mas ainda não é o fim.', 6, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto se levantará nação contra nação, e reino contra reino, e haverá fomes, e pestes, e terremotos, em vários lugares.', 7, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas todas estas coisas são o princípio de dores.', 8, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então vos hão de entregar para serdes atormentados, e matar-vosão; e sereis odiados de todas as nações por causa do meu nome.', 9, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nesse tempo muitos serão escandalizados, e trair-se-ão uns aos outros, e uns aos outros se odiarào.', 10, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E surgirão muitos falsos profetas, e enganarão a muitos.', 11, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, por se multiplicar a iniqüidade, o amor de muitos esfriará.', 12, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas aquele que perseverar até ao fim, esse será salvo.', 13, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E este evangelho do reino será pregado em todo o mundo, em testemunho a todas as nações, e então virá o fim.', 14, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando, pois, virdes que a abominação da desolação, de que falou o profeta Daniel, está no lugar santo; quem lê, entenda;', 15, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, os que estiverem na Judéia, fujam para os montes;', 16, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quem estiver sobre o telhado não desça a tirar alguma coisa de sua casa;', 17, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quem estiver no campo não volte atrás a buscar as suas vestes.', 18, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ai das grávidas e das que amamentarem naqueles dias!', 19, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E orai para que a vossa fuga não aconteça no inverno nem no sábado;', 20, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque haverá então grande aflição, como nunca houve desde o princípio do mundo até agora, nem tampouco há de haver.', 21, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se aqueles dias não fossem abreviados, nenhuma carne se salvaria; mas por causa dos escolhidos serão abreviados aqueles dias.', 22, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, se alguém vos disser: Eis que o Cristo está aqui, ou ali, não lhe deis crédito;', 23, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque surgirão falsos cristos e falsos profetas, e farão tão grandes sinais e prodígios que, se possível fora, enganariam até os escolhidos.', 24, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que eu vo-lo tenho predito.', 25, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, se vos disserem: Eis que ele está no deserto, não saiais. Eis que ele está no interior da casa; não acrediteis.', 26, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, assim como o relâmpago sai do oriente e se mostra até ao ocidente, assim será também a vinda do Filho do homem.', 27, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois onde estiver o cadáver, aí se ajuntarão as águias.', 28, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo depois da aflição daqueles dias, o sol escurecerá, e a lua não dará a sua luz, e as estrelas cairão do céu, e as potências dos céus serão abaladas.', 29, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então aparecerá no céu o sinal do Filho do homem; e todas as tribos da terra se lamentarão, e verão o Filho do homem, vindo sobre as nuvens do céu, com poder e grande glória.', 30, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele enviará os seus anjos com rijo clamor de trombeta, os quais ajuntarão os seus escolhidos desde os quatro ventos, de uma à outra extremidade dos céus.', 31, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aprendei, pois, esta parábola da figueira: Quando já os seus ramos se tornam tenros e brotam folhas, sabeis que está próximo o verão.', 32, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Igualmente, quando virdes todas estas coisas, sabei que ele está próximo, às portas.', 33, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que não passará esta geração sem que todas estas coisas aconteçam.', 34, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O céu e a terra passarão, mas as minhas palavras não hão de passar.', 35, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas daquele dia e hora ninguém sabe, nem os anjos do céu, mas unicamente meu Pai.', 36, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como foi nos dias de Noé, assim será também a vinda do Filho do homem.', 37, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto, assim como, nos dias anteriores ao dilúvio, comiam, bebiam, casavam e davam-se em casamento, até ao dia em que Noé entrou na arca,', 38, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não o perceberam, até que veio o dilúvio, e os levou a todos, assim será também a vinda do Filho do homem.', 39, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, estando dois no campo, será levado um, e deixado o outro;', 40, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estando duas moendo no moinho, será levada uma, e deixada outra.', 41, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vigiai, pois, porque não sabeis a que hora há de vir o vosso Senhor.', 42, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas considerai isto: se o pai de família soubesse a que vigília da noite havia de vir o ladrão, vigiaria e não deixaria minar a sua casa.', 43, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, estai vós apercebidos também; porque o Filho do homem há de vir à hora em que não penseis.', 44, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem é, pois, o servo fiel e prudente, que o seu senhor constituiu sobre a sua casa, para dar o sustento a seu tempo?', 45, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurado aquele servo que o seu senhor, quando vier, achar servindo assim.', 46, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que o porá sobre todos os seus bens.', 47, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas se aquele mau servo disser no seu coração: O meu senhor tarde virá;', 48, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E começar a espancar os seus conservos, e a comer e a beber com os ébrios,', 49, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Virá o senhor daquele servo num dia em que o não espera, e à hora em que ele não sabe,', 50, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E separá-lo-á, e destinará a sua parte com os hipócritas; ali haverá pranto e ranger de dentes.', 51, 24, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando-se os fariseus e os saduceus, para o tentarem, pediram-lhe que lhes mostrasse algum sinal do céu.', 1, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, respondendo, disse-lhes: Quando é chegada a tarde, dizeis: Haverá bom tempo, porque o céu está rubro.', 2, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pela manhã: Hoje haverá tempestade, porque o céu está de um vermelho sombrio. Hipócritas, sabeis discernir a face do céu, e não conheceis os sinais dos tempos?', 3, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Uma geração má e adúltera pede um sinal, e nenhum sinal lhe será dado, senão o sinal do profeta Jonas. E, deixando-os, retirou-se.', 4, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passando seus discípulos para o outro lado, tinham-se esquecido de trazer pão.', 5, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus disse-lhes: Adverti, e acautelai-vos do fermento dos fariseus e saduceus.', 6, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles arrazoavam entre si, dizendo: É porque não trouxemos pão.', 7, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, percebendo isso, disse: Por que arrazoais entre vós, homens de pouca fé, sobre o não terdes trazido pão?', 8, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não compreendeis ainda, nem vos lembrais dos cinco pães para cinco mil homens, e de quantas alcofas levantastes?', 9, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem dos sete pães para quatro mil, e de quantos cestos levantastes?', 10, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como não compreendestes que não vos falei a respeito do pão, mas que vos guardásseis do fermento dos fariseus e saduceus?', 11, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então compreenderam que não dissera que se guardassem do fermento do pão, mas da doutrina dos fariseus.', 12, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando Jesus às partes de Cesaréia de Filipe, interrogou os seus discípulos, dizendo: Quem dizem os homens ser o Filho do homem?', 13, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles disseram: Uns, João o Batista; outros, Elias; e outros, Jeremias, ou um dos profetas.', 14, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes ele: E vós, quem dizeis que eu sou?', 15, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Simão Pedro, respondendo, disse: Tu és o Cristo, o Filho do Deus vivo.', 16, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhe: Bem-aventurado és tu, Simão Barjonas, porque to não revelou a carne e o sangue, mas meu Pai, que está nos céus.', 17, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois também eu te digo que tu és Pedro, e sobre esta pedra edificarei a minha igreja, e as portas do inferno não prevalecerão contra ela;', 18, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu te darei as chaves do reino dos céus; e tudo o que ligares na terra será ligado nos céus, e tudo o que desligares na terra será desligado nos céus.', 19, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então mandou aos seus discípulos que a ninguém dissessem que ele era Jesus o Cristo.', 20, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde então começou Jesus a mostrar aos seus discípulos que convinha ir a Jerusalém, e padecer muitas coisas dos anciãos, e dos principais dos sacerdotes, e dos escribas, e ser morto, e ressuscitar ao terceiro dia.', 21, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro, tomando-o de parte, começou a repreendê-lo, dizendo: Senhor, tem compaixão de ti; de modo nenhum te acontecerá isso.', 22, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, voltando-se, disse a Pedro: Para trás de mim, Satanás, que me serves de escândalo; porque não compreendes as coisas que são de Deus, mas só as que são dos homens.', 23, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então disse Jesus aos seus discípulos: Se alguém quiser vir após mim, renuncie-se a si mesmo, tome sobre si a sua cruz, e siga-me;', 24, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque aquele que quiser salvar a sua vida, perdê-la-á, e quem perder a sua vida por amor de mim, achá-la-á.', 25, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois que aproveita ao homem ganhar o mundo inteiro, se perder a sua alma? Ou que dará o homem em recompensa da sua alma?', 26, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Filho do homem virá na glória de seu Pai, com os seus anjos; e então dará a cada um segundo as suas obras.', 27, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que alguns há, dos que aqui estão, que não provarão a morte até que vejam vir o Filho do homem no seu reino.', 28, 16, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Naquela mesma hora chegaram os discípulos ao pé de Jesus, dizendo: Quem é o maior no reino dos céus?', 1, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, chamando um menino, o pôs no meio deles,', 2, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse: Em verdade vos digo que, se não vos converterdes e não vos fizerdes como meninos, de modo algum entrareis no reino dos céus.', 3, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, aquele que se tornar humilde como este menino, esse é o maior no reino dos céus.', 4, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qualquer que receber em meu nome um menino, tal como este, a mim me recebe.', 5, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, qualquer que escandalizar um destes pequeninos, que crêem em mim, melhor lhe fora que se lhe pendurasse ao pescoço uma mó de azenha, e se submergisse na profundeza do mar.', 6, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai do mundo, por causa dos escândalos; porque é mister que venham escândalos, mas ai daquele homem por quem o escândalo vem!', 7, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, se a tua mão ou o teu pé te escandalizar, corta-o, e atira-o para longe de ti; melhor te é entrar na vida coxo, ou aleijado, do que, tendo duas mãos ou dois pés, seres lançado no fogo eterno.', 8, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se o teu olho te escandalizar, arranca-o, e atira-o para longe de ti; melhor te é entrar na vida com um só olho, do que, tendo dois olhos, seres lançado no fogo do inferno.', 9, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede, não desprezeis algum destes pequeninos, porque eu vos digo que os seus anjos nos céus sempre vêem a face de meu Pai que está nos céus.', 10, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Filho do homem veio salvar o que se tinha perdido.', 11, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que vos parece? Se algum homem tiver cem ovelhas, e uma delas se desgarrar, não irá pelos montes, deixando as noventa e nove, em busca da que se desgarrou?', 12, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se porventura achá-la, em verdade vos digo que maior prazer tem por aquela do que pelas noventa e nove que se não desgarraram.', 13, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, também, não é vontade de vosso Pai, que está nos céus, que um destes pequeninos se perca.', 14, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, se teu irmão pecar contra ti, vai, e repreende-o entre ti e ele só; se te ouvir, ganhaste a teu irmão;', 15, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se não te ouvir, leva ainda contigo um ou dois, para que pela boca de duas ou três testemunhas toda a palavra seja confirmada.', 16, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se não as escutar, dize-o à igreja; e, se também não escutar a igreja, considera-o como um gentio e publicano.', 17, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que tudo o que ligardes na terra será ligado no céu, e tudo o que desligardes na terra será desligado no céu.', 18, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Também vos digo que, se dois de vós concordarem na terra acerca de qualquer coisa que pedirem, isso lhes será feito por meu Pai, que está nos céus.', 19, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, onde estiverem dois ou três reunidos em meu nome, aí estou eu no meio deles.', 20, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pedro, aproximando-se dele, disse: Senhor, até quantas vezes pecará meu irmão contra mim, e eu lhe perdoarei? Até sete?', 21, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus lhe disse: Não te digo que até sete; mas, até setenta vezes sete.', 22, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso o reino dos céus pode comparar-se a um certo rei que quis fazer contas com os seus servos;', 23, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, começando a fazer contas, foi-lhe apresentado um que lhe devia dez mil talentos;', 24, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, não tendo ele com que pagar, o seu senhor mandou que ele, e sua mulher e seus filhos fossem vendidos, com tudo quanto tinha, para que a dívida se lhe pagasse.', 25, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então aquele servo, prostrando-se, o reverenciava, dizendo: Senhor, sê generoso para comigo, e tudo te pagarei.', 26, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o Senhor daquele servo, movido de íntima compaixão, soltou-o e perdoou-lhe a dívida.', 27, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saindo, porém, aquele servo, encontrou um dos seus conservos, que lhe devia cem dinheiros, e, lançando mão dele, sufocava-o, dizendo: Paga-me o que me deves.', 28, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o seu companheiro, prostrando-se a seus pés, rogava-lhe, dizendo: Sê generoso para comigo, e tudo te pagarei.', 29, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, não quis, antes foi encerrá-lo na prisão, até que pagasse a dívida.', 30, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vendo, pois, os seus conservos o que acontecia, contristaram-se muito, e foram declarar ao seu senhor tudo o que se passara.', 31, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o seu senhor, chamando-o à sua presença, disse-lhe: Servo malvado, perdoei-te toda aquela dívida, porque me suplicaste.', 32, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não devias tu, igualmente, ter compaixão do teu companheiro, como eu também tive misericórdia de ti?', 33, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indignado, o seu senhor o entregou aos atormentadores, até que pagasse tudo o que lhe devia.', 34, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim vos fará, também, meu Pai celestial, se do coração não perdoardes, cada um a seu irmão, as suas ofensas.', 35, 18, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Naquele tempo ouviu Herodes, o tetrarca, a fama de Jesus,', 1, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse aos seus criados: Este é João o Batista; ressuscitou dos mortos, e por isso estas maravilhas operam nele.', 2, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Herodes tinha prendido João, e tinha-o maniatado e encerrado no cárcere, por causa de Herodias, mulher de seu irmão Filipe;', 3, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque João lhe dissera: Não te é lícito possuí-la.', 4, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, querendo matá-lo, temia o povo; porque o tinham como profeta.', 5, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Festejando-se, porém, o dia natalício de Herodes, dançou a filha de Herodias diante dele, e agradou a Herodes.', 6, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso prometeu, com juramento, dar-lhe tudo o que pedisse;', 7, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ela, instruída previamente por sua mãe, disse: Dá-me aqui, num prato, a cabeça de João o Batista.', 8, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o rei afligiu-se, mas, por causa do juramento, e dos que estavam à mesa com ele, ordenou que se lhe desse.', 9, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mandou degolar João no cárcere.', 10, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a sua cabeça foi trazida num prato, e dada à jovem, e ela a levou a sua mãe.', 11, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegaram os seus discípulos, e levaram o corpo, e o sepultaram; e foram anunciá-lo a Jesus.', 12, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, ouvindo isto, retirou-se dali num barco, para um lugar deserto, apartado; e, sabendo-o o povo, seguiu-o a pé desde as cidades.', 13, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, Jesus, saindo, viu uma grande multidão, e possuído de íntima compaixão para com ela, curou os seus enfermos.', 14, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo chegada a tarde, os seus discípulos aproximaram-se dele, dizendo: O lugar é deserto, e a hora é já avançada; despede a multidão, para que vão pelas aldeias, e comprem comida para si.', 15, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, lhes disse: Não é mister que vão; dai-lhes vós de comer.', 16, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então eles lhe disseram: Não temos aqui senão cinco pães e dois peixes.', 17, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse: Trazei-mos aqui.', 18, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo mandado que a multidão se assentasse sobre a erva, tomou os cinco pães e os dois peixes, e, erguendo os olhos ao céu, os abençoou, e, partindo os pães, deu-os aos discípulos, e os discípulos à multidão.', 19, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E comeram todos, e saciaramse; e levantaram dos pedaços, que sobejaram, doze alcofas cheias.', 20, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que comeram foram quase cinco mil homens, além das mulheres e crianças.', 21, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo ordenou Jesus que os seus discípulos entrassem no barco, e fossem adiante para o outro lado, enquanto despedia a multidão.', 22, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, despedida a multidão, subiu ao monte para orar, à parte. E, chegada já a tarde, estava ali só.', 23, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o barco estava já no meio do mar, açoitado pelas ondas; porque o vento era contrário;', 24, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, à quarta vigília da noite, dirigiu-se Jesus para eles, andan-do por cima do mar.', 25, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os discípulos, vendo-o an-dando sobre o mar, assustaram-se, dizendo: É um fantasma. E gritaram com medo.', 26, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, lhes falou logo, dizendo: Tende bom ânimo, sou eu, não temais.', 27, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E respondeu-lhe Pedro, e disse: Senhor, se és tu, manda-me ir ter contigo por cima das águas.', 28, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse: Vem. E Pedro, descendo do barco, andou sobre as águas para ir ter com Jesus.', 29, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, sentindo o vento forte, teve medo; e, começando a ir para o fundo, clamou, dizendo: Senhor, salva-me!', 30, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo Jesus, estendendo a mão, segurou-o, e disse-lhe: Homem de pouca fé, por que duvidaste?', 31, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando subiram para o barco, acalmou o vento.', 32, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então aproximaram-se os que estavam no barco, e adoraram-no, dizendo: És verdadeiramente o Filho de Deus.', 33, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo passado para o outro lado, chegaram à terra de Genesaré.', 34, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando os homens daquele lugar o conheceram, mandaram por todas aquelas terras em redor e trouxeram-lhe todos os que estavam enfermos.', 35, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rogavam-lhe que ao menos eles pudessem tocar a orla da sua roupa; e todos os que a tocavam ficavam sãos.', 36, 14, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, quando Jesus concluiu todos estes discursos, disse aos seus discípulos:', 1, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem sabeis que daqui a dois dias é a páscoa; e o Filho do homem será entregue para ser crucificado.', 2, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois os príncipes dos sacerdotes, e os escribas, e os anciãos do povo reuniram-se na sala do sumo sacerdote, o qual se chamava Caifás.', 3, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E consultaram-se mutuamente para prenderem Jesus com dolo e o matarem.', 4, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas diziam: Não durante a festa, para que não haja alvoroço entre o povo.', 5, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando Jesus em Betânia, em casa de Simão, o leproso,', 6, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aproximou-se dele uma mulher com um vaso de alabastro, com ungüento de grande valor, e derramou-lho sobre a cabeça, quando ele estava assentado à mesa.', 7, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus discípulos, vendo isto, indignaram-se, dizendo: Por que é este desperdício?', 8, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois este ungüento podia vender-se por grande preço, e dar-se o dinheiro aos pobres.', 9, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, conhecendo isto, disse-lhes: Por que afligis esta mulher? pois praticou uma boa ação para comigo.', 10, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto sempre tendes convosco os pobres, mas a mim não me haveis de ter sempre.', 11, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, derramando ela este ungüento sobre o meu corpo, fê-lo preparando-me para o meu sepultamento.', 12, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que, onde quer que este evangelho for pregado em todo o mundo, também será referido o que ela fez, para memória sua.', 13, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então um dos doze, chamado Judas Iscariotes, foi ter com os príncipes dos sacerdotes,', 14, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse: Que me quereis dar, e eu vo-lo entregarei? E eles lhe pesaram trinta moedas de prata,', 15, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desde então buscava oportunidade para o entregar.', 16, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, no primeiro dia da festa dos pães ázimos, chegaram os discípulos junto de Jesus, dizendo: Onde queres que façamos os preparativos para comeres a páscoa?', 17, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse: Ide à cidade, a um certo homem, e dizei-lhe: O Mestre diz: O meu tempo está próximo; em tua casa celebrarei a páscoa com os meus discípulos.', 18, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os discípulos fizeram como Jesus lhes ordenara, e prepararam a páscoa.', 19, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegada a tarde, assentou-se à mesa com os doze.', 20, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, comendo eles, disse: Em verdade vos digo que um de vós me há de trair.', 21, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles, entristecendo-se muito, começaram cada um a dizer-lhe: Porventura sou eu, Senhor?', 22, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, respondendo, disse: O que põe comigo a mão no prato, esse me há de trair.', 23, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade o Filho do homem vai, como acerca dele está escrito, mas ai daquele homem por quem o Filho do homem é traído! Bom seria para esse homem se não houvera nascido.', 24, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo Judas, o que o traía, disse: Porventura sou eu, Rabi? Ele disse: Tu o disseste.', 25, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando comiam, Jesus tomou o pão, e abençoando-o, o partiu, e o deu aos discípulos, e disse: Tomai, comei, isto é o meu corpo.', 26, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando o cálice, e dando graças, deu-lho, dizendo: Bebei dele todos;', 27, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque isto é o meu sangue, o sangue do novo testamento, que é derramado por muitos, para remissão dos pecados.', 28, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E digo-vos que, desde agora, não beberei deste fruto da vide, até aquele dia em que o beba novo convosco no reino de meu Pai.', 29, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo cantado o hino, saíram para o Monte das Oliveiras.', 30, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus lhes disse: Todos vós esta noite vos escandalizareis em mim; porque está escrito: Ferirei o pastor, e as ovelhas do rebanho se dispersarão.', 31, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, depois de eu ressuscitar, irei adiante de vós para a Galiléia.', 32, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Pedro, respondendo, disse-lhe: Ainda que todos se escandalizem em ti, eu nunca me escandalizarei.', 33, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Em verdade te digo que, nesta mesma noite, antes que o galo cante, três vezes me negarás.', 34, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Pedro: Ainda que me seja mister morrer contigo, não te negarei. E todos os discípulos disseram o mesmo.', 35, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então chegou Jesus com eles a um lugar chamado Getsêmani, e disse a seus discípulos: Assentai-vos aqui, enquanto vou além orar.', 36, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levando consigo Pedro e os dois filhos de Zebedeu, começou a entristecer-se e a angustiar-se muito.', 37, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então lhes disse: A minha alma está cheia de tristeza até a morte; ficai aqui, e velai comigo.', 38, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo um pouco mais para diante, prostrou-se sobre o seu rosto, orando e dizendo: Meu Pai, se é possível, passe de mim este cálice; todavia, não seja como eu quero, mas como tu queres.', 39, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, voltando para os seus discípulos, achou-os adormecidos; e disse a Pedro: Então nem uma hora pudeste velar comigo?', 40, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vigiai e orai, para que não entreis em tentação; na verdade, o espírito está pronto, mas a carne é fraca.', 41, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo segunda vez, orou, dizendo: Pai meu, se este cálice não pode passar de mim sem eu o beber, faça-se a tua vontade.', 42, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, voltando, achou-os outra vez adormecidos; porque os seus olhos estavam pesados.', 43, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, deixando-os de novo, foi orar pela terceira vez, dizendo as mesmas palavras.', 44, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então chegou junto dos seus discípulos, e disse-lhes: Dormi agora, e repousai; eis que é chegada a hora, e o Filho do homem será entregue nas mãos dos pecadores.', 45, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Levantai-vos, partamos; eis que é chegado o que me trai.', 46, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando ele ainda a falar, eis que chegou Judas, um dos doze, e com ele grande multidão com espadas e varapaus, enviada pelos príncipes dos sacerdotes e pelos anciãos do povo.', 47, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que o traía tinha-lhes dado um sinal, dizendo: O que eu beijar é esse; prendei-o.', 48, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo, aproximando-se de Jesus, disse: Eu te saúdo, Rabi; e beijou-o.', 49, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, lhe disse: Amigo, a que vieste? Então, aproximando-se eles, lançaram mão de Jesus, e o prenderam.', 50, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que um dos que estavam com Jesus, estendendo a mão, puxou da espada e, ferindo o servo do sumo sacerdote, cortou-lhe uma orelha.', 51, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus disse-lhe: Embainha a tua espada; porque todos os que lançarem mão da espada, à espada morrerão.', 52, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou pensas tu que eu não poderia agora orar a meu Pai, e que ele não me daria mais de doze legiões de anjos?', 53, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como, pois, se cumpririam as Escrituras, que dizem que assim convém que aconteça?', 54, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então disse Jesus à multidão: Saístes, como para um salteador, com espadas e varapaus para me prender? Todos os dias me assentava junto de vós, ensinando no templo, e não me prendestes.', 55, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas tudo isto aconteceu para que se cumpram as escrituras dos profetas. Então, todos os discípulos, deixando-o, fugiram.', 56, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que prenderam a Jesus o conduziram à casa do sumo sacerdote Caifás, onde os escribas e os anciãos estavam reunidos.', 57, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro o seguiu de longe, até ao pátio do sumo sacerdote e, entrando, assentou-se entre os criados, para ver o fim.', 58, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, os príncipes dos sacerdotes, e os anciãos, e todo o conselho, buscavam falso testemunho contra Jesus, para poderem dar-lhe a morte;', 59, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não o achavam; apesar de se apresentarem muitas testemunhas falsas, nào o achavam. Mas, por fim chegaram duas testemunhas falsas,', 60, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disseram: Este disse: Eu posso derrubar o templo de Deus, e reedificá-lo em três dias.', 61, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se o sumo sacerdote, disse-lhe: Não respondes coisa alguma ao que estes depõem contra ti?', 62, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, guardava silêncio. E, insistindo o sumo sacerdote, disse-lhe: Conjuro-te pelo Deus vivo que nos digas se tu és o Cristo, o Filho de Deus.', 63, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Tu o disseste; digo-vos, porém, que vereis em breve o Filho do homem assentado à direita do Poder, e vindo sobre as nuvens do céu.', 64, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o sumo sacerdote rasgou as suas vestes, dizendo: Blasfemou; para que precisamos ainda de testemunhas? Eis que bem ouvistes agora a sua blasfêmia.', 65, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que vos parece? E eles, respondendo, disseram: É réu de morte.', 66, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então cuspiram-lhe no rosto e lhe davam punhadas, e outros o esbofeteavam,', 67, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Profetiza-nos, Cristo, quem é o que te bateu?', 68, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, Pedro estava assentado fora, no pátio; e, aproximando-se dele uma criada, disse: Tu também estavas com Jesus, o galileu.', 69, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele negou diante de todos, dizendo: Não sei o que dizes.', 70, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo para o vestíbulo, outra criada o viu, e disse aos que ali estavam: Este também estava com Jesus, o Nazareno.', 71, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele negou outra vez com juramento: Não conheço tal homem.', 72, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, daí a pouco, aproximando-se os que ali estavam, disseram a Pedro: Verdadeiramente também tu és deles, pois a tua fala te denuncia.', 73, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então começou ele a praguejar e a jurar, dizendo: Não conheço esse homem. E imediatamente o galo cantou.', 74, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lembrou-se Pedro das palavras de Jesus, que lhe dissera: Antes que o galo cante, três vezes me negarás. E, saindo dali, chorou amargamente.', 75, 26, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então chegaram ao pé de Jesus uns escribas e fariseus de Jerusalém, dizendo:', 1, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por que transgridem os teus discípulos a tradição dos anciãos? pois não lavam as mãos quando comem pão.', 2, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, respondendo, disse-lhes: Por que transgredis vós, também, o mandamento de Deus pela vossa tradição?', 3, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus ordenou, dizendo: Honra a teu pai e a tua mãe; e: Quem maldisser ao pai ou à mãe, certamente morrerá.', 4, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós dizeis: Qualquer que disser ao pai ou à mãe: É oferta ao Senhor o que poderias aproveitar de mim; esse não precisa honrar nem a seu pai nem a sua mãe,', 5, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim invalidastes, pela vossa tradição, o mandamento de Deus.', 6, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Hipócritas, bem profetizou Isaías a vosso respeito, dizendo:', 7, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este povo se aproxima de mim com a sua boca e me honra com os seus lábios, mas o seu coração está longe de mim.', 8, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, em vão me adoram, ensinando doutrinas que são preceitos dos homens.', 9, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando a si a multidão, disse-lhes: Ouvi, e entendei:', 10, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que contamina o homem não é o que entra na boca, mas o que sai da boca, isso é o que contamina o homem.', 11, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, acercando-se dele os seus discípulos, disseram-lhe: Sabes que os fariseus, ouvindo essas palavras, se escandalizaram?', 12, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, respondendo, disse: Toda a planta, que meu Pai celestial não plantou, será arrancada.', 13, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deixai-os; são cegos condutores de cegos. Ora, se um cego guiar outro cego, ambos cairão na cova.', 14, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro, tomando a palavra, disse-lhe: Explica-nos essa parábola.', 15, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, disse: Até vós mesmos estais ainda sem entender?', 16, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ainda não compreendeis que tudo o que entra pela boca desce para o ventre, e é lançado fora?', 17, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, o que sai da boca, procede do coração, e isso contamina o homem.', 18, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque do coração procedem os maus pensamentos, mortes, adultérios, fornicação, furtos, falsos testemunhos e blasfêmias.', 19, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('São estas coisas que contaminam o homem; mas comer sem lavar as mãos, isso não contamina o homem.', 20, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, partindo Jesus dali, foi para as partes de Tiro e de Sidom.', 21, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que uma mulher cananéia, que saíra daquelas cercanias, clamou, dizendo: Senhor, Filho de Davi, tem misericórdia de mim, que minha filha está miseravelmente endemoninhada.', 22, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele não lhe respondeu palavra. E os seus discípulos, chegando ao pé dele, rogaram-lhe, dizendo: Despede-a, que vem gritando atrás de nós.', 23, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, respondendo, disse: Eu não fui enviado senão às ovelhas perdidas da casa de Israel.', 24, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então chegou ela, e adorou-o, dizendo: Senhor, socorre-me!', 25, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, respondendo, disse: Não é bom pegar no pão dos filhos e deitá-lo aos cachorrinhos.', 26, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ela disse: Sim, Senhor, mas também os cachorrinhos comem das migalhas que caem da mesa dos seus senhores.', 27, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então respondeu Jesus, e disse-lhe: Ó mulher, grande é a tua fé! Seja isso feito para contigo como tu desejas. E desde aquela hora a sua filha ficou sã.', 28, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Partindo Jesus dali, chegou ao pé do mar da Galiléia, e, subindo a um monte, assentou-se lá.', 29, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E veio ter com ele grandes multidões, que traziam coxos, cegos, mudos, aleijados, e outros muitos, e os puseram aos pés de Jesus, e ele os sarou,', 30, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De tal sorte, que a multidão se maravilhou vendo os mudos a falar, os aleijados sãos, os coxos a andar, e os cegos a ver; e glorificava o Deus de Israel.', 31, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, chamando os seus discípulos, disse: Tenho compaixão da multidão, porque já está comigo há três dias, e não tem o que comer; e não quero despedi-la em jejum, para que não desfaleça no caminho.', 32, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus discípulos disseram-lhe: De onde nos viriam, num deserto, tantos pães, para saciar tal multidão?', 33, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus disse-lhes: Quantos pães tendes? E eles disseram: Sete, e uns poucos de peixinhos.', 34, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então mandou à multidão que se assentasse no chão,', 35, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando os sete pães e os peixes, e dando graças, partiu-os, e deu-os aos seus discípulos, e os discípulos à multidão.', 36, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos comeram e se saciaram; e levantaram, do que sobejou, sete cestos cheios de pedaços.', 37, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, os que tinham comido eram quatro mil homens, além de mulheres e crianças.', 38, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo despedido a multidão, entrou no barco, e dirigiu-se ao território de Magadã.', 39, 15, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o reino dos céus será semelhante a dez virgens que, tomando as suas lâmpadas, saíram ao encontro do esposo.', 1, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E cinco delas eram prudentes, e cinco loucas.', 2, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As loucas, tomando as suas lâmpadas, não levaram azeite consigo.', 3, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas as prudentes levaram azeite em suas vasilhas, com as suas lâmpadas.', 4, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tardando o esposo, tosquenejaram todas, e adormeceram.', 5, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas à meia-noite ouviu-se um clamor: Aí vem o esposo, saí-lhe ao encontro.', 6, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então todas aquelas virgens se levantaram, e prepararam as suas lâmpadas.', 7, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as loucas disseram às prudentes: Dai-nos do vosso azeite, porque as nossas lâmpadas se apagam.', 8, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas as prudentes responderam, dizendo: Não seja caso que nos falte a nós e a vós, ide antes aos que o vendem, e comprai-o para vós.', 9, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo elas ido comprá-lo, chegou o esposo, e as que estavam preparadas entraram com ele para as bodas, e fechou-se a porta.', 10, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois chegaram também as outras virgens, dizendo: Senhor, Senhor, abre-nos.', 11, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, respondendo, disse: Em verdade vos digo que vos não conheço.', 12, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vigiai, pois, porque não sabeis o dia nem a hora em que o Filho do homem há de vir.', 13, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque isto é também como um homem que, partindo para fora da terra, chamou os seus servos, e entregou-lhes os seus bens.', 14, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a um deu cinco talentos, e a outro dois, e a outro um, a cada um segundo a sua capacidade, e ausentou-se logo para longe.', 15, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo ele partido, o que recebera cinco talentos negociou com eles, e granjeou outros cinco talentos.', 16, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da mesma sorte, o que recebera dois, granjeou também outros dois.', 17, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que recebera um, foi e cavou na terra e escondeu o dinheiro do seu senhor.', 18, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muito tempo depois veio o senhor daqueles servos, e fez contas com eles.', 19, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então aproximou-se o que recebera cinco talentos, e trouxe-lhe outros cinco talentos, dizendo: Senhor, entregaste-me cinco talentos; eis aqui outros cinco talentos que granjeei com eles.', 20, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o seu senhor lhe disse: Bem está, servo bom e fiel. Sobre o pouco foste fiel, sobre muito te colocarei; entra no gozo do teu senhor.', 21, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando também o que tinha recebido dois talentos, disse: Senhor, entregaste-me dois talentos; eis que com eles granjeei outros dois talentos.', 22, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe o seu senhor: Bem está, bom e fiel servo. Sobre o pouco foste fiel, sobre muito te colocarei; entra no gozo do teu senhor.', 23, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, chegando também o que recebera um talento, disse: Senhor, eu conhecia-te, que és um homem duro, que ceifas onde não semeaste e ajuntas onde não espalhaste;', 24, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, atemorizado, escondi na terra o teu talento; aqui tens o que é teu.', 25, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondendo, porém, o seu senhor, disse-lhe: Mau e negligente servo; sabias que ceifo onde não semeei e ajunto onde não espalhei?', 26, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Devias então ter dado o meu dinheiro aos banqueiros e, quando eu viesse, receberia o meu com os juros.', 27, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tirai-lhe pois o talento, e dai-o ao que tem os dez talentos.', 28, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a qualquer que tiver será dado, e terá em abundância; mas ao que não tiver até o que tem ser-lhe-á tirado.', 29, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lançai, pois, o servo inútil nas trevas exteriores; ali haverá pranto e ranger de dentes.', 30, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando o Filho do homem vier em sua glória, e todos os santos anjos com ele, então se assentará no trono da sua glória;', 31, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todas as nações serão reunidas diante dele, e apartará uns dos outros, como o pastor aparta dos bodes as ovelhas;', 32, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E porá as ovelhas à sua direita, mas os bodes à esquerda.', 33, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então dirá o Rei aos que estiverem à sua direita: Vinde, benditos de meu Pai, possuí por herança o reino que vos está preparado desde a fundação do mundo;', 34, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tive fome, e destes-me de comer; tive sede, e destes-me de beber; era estrangeiro, e hospedastes-me;', 35, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estava nu, e vestistes-me; adoeci, e visitastes-me; estive na prisão, e foste me ver.', 36, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então os justos lhe responderão, dizendo: Senhor, quando te vimos com fome, e te demos de comer? ou com sede, e te demos de beber?', 37, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando te vimos estrangeiro, e te hospedamos? ou nu, e te vestimos?', 38, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando te vimos enfermo, ou na prisão, e fomos ver-te?', 39, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo o Rei, lhes dirá: Em verdade vos digo que quando o fizestes a um destes meus pequeninos irmãos, a mim o fizestes.', 40, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então dirá também aos que estiverem à sua esquerda: Apartai-vos de mim, malditos, para o fogo eterno, preparado para o diabo e seus anjos;', 41, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tive fome, e não me destes de comer; tive sede, e não me destes de beber;', 42, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo estrangeiro, não me recolhestes; estando nu, não me vestistes; e enfermo, e na prisão, não me visitastes.', 43, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então eles também lhe responderão, dizendo: Senhor, quando te vimos com fome, ou com sede, ou estrangeiro, ou nu, ou enfermo, ou na prisão, e não te servimos?', 44, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então lhes responderá, dizendo: Em verdade vos digo que, quando a um destes pequeninos o não fizestes, não o fizestes a mim.', 45, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E irão estes para o tormento eterno, mas os justos para a vida eterna.', 46, 25, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então falou Jesus à multidão, e aos seus discípulos,', 1, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Na cadeira de Moisés estão assentados os escribas e fariseus.', 2, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todas as coisas, pois, que vos disserem que observeis, observai-as e fazei-as; mas não procedais em conformidade com as suas obras, porque dizem e não fazem;', 3, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois atam fardos pesados e difíceis de suportar, e os põem aos ombros dos homens; eles, porém, nem com seu dedo querem movê-los;', 4, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E fazem todas as obras a fim de serem vistos pelos homens; pois trazem largos filactérios, e alargam as franjas das suas vestes,', 5, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E amam os primeiros lugares nas ceias e as primeiras cadeiras nas sinagogas,', 6, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as saudações nas praças, e o serem chamados pelos homens; Rabi, Rabi.', 7, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, porém, não queirais ser chamados Rabi, porque um só é o vosso Mestre, a saber, o Cristo, e todos vós sois irmãos.', 8, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a ninguém na terra chameis vosso pai, porque um só é o vosso Pai, o qual está nos céus.', 9, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem vos chameis mestres, porque um só é o vosso Mestre, que é o Cristo.', 10, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O maior dentre vós será vosso servo.', 11, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que a si mesmo se exaltar será humilhado; e o que a si mesmo se humilhar será exaltado.', 12, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ai de vós, escribas e fariseus, hipócritas! pois que fechais aos homens o reino dos céus; e nem vós entrais nem deixais entrar aos que estão entrando.', 13, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, escribas e fariseus, hipócritas! pois que devorais as casas das viúvas, sob pretexto de prolongadas orações; por isso sofrereis mais rigoroso juízo.', 14, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, escribas e fariseus, hipócritas! pois que percorreis o mar e a terra para fazer um prosélito; e, depois de o terdes feito, o fazeis filho do inferno duas vezes mais do que vós.', 15, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, condutores cegos! pois que dizeis: Qualquer que jurar pelo templo, isso nada é; mas o que jurar pelo ouro do templo, esse é devedor.', 16, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Insensatos e cegos! Pois qual é maior: o ouro, ou o templo, que santifica o ouro?', 17, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele que jurar pelo altar isso nada é; mas aquele que jurar pela oferta que está sobre o altar, esse é devedor.', 18, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Insensatos e cegos! Pois qual é maior: a oferta, ou o altar, que santifica a oferta?', 19, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, o que jurar pelo altar, jura por ele e por tudo o que sobre ele está;', 20, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, o que jurar pelo templo, jura por ele e por aquele que nele habita;', 21, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, o que jurar pelo céu, jura pelo trono de Deus e por aquele que está assentado nele.', 22, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, escribas e fariseus, hipócritas! pois que dizimais a hortelã, o endro e o cominho, e desprezais o mais importante da lei, o juízo, a misericórdia e a fé; deveis, porém, fazer estas coisas, e não omitir aquelas.', 23, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Condutores cegos! que coais um mosquito e engulis um camelo.', 24, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, escribas e fariseus, hipócritas! pois que limpais o exterior do copo e do prato, mas o interior está cheio de rapina e de intemperança.', 25, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fariseu cego! limpa primeiro o interior do copo e do prato, para que também o exterior fique limpo.', 26, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, escribas e fariseus, hipócritas! pois que sois semelhantes aos sepulcros caiados, que por fora realmente parecem formosos, mas interiormente estão cheios de ossos de mortos e de toda a imundícia.', 27, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também vós exteriormente pareceis justos aos homens, mas interiormente estais cheios de hipocrisia e de iniqüidade.', 28, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, escribas e fariseus, hipócritas! pois que edificais os sepulcros dos profetas e adornais os monumentos dos justos,', 29, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizeis: Se existíssemos no tempo de nossos pais, nunca nos associaríamos com eles para derramar o sangue dos profetas.', 30, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, vós mesmos testificais que sois filhos dos que mataram os profetas.', 31, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Enchei vós, pois, a medida de vossos pais.', 32, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Serpentes, raça de víboras! como escapareis da condenação do inferno?', 33, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, eis que eu vos envio profetas, sábios e escribas; a uns deles matareis e crucificareis; e a outros deles açoitareis nas vossas sinagogas e os perseguireis de cidade em cidade;', 34, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que sobre vós caia todo o sangue justo, que foi derramado sobre a terra, desde o sangue de Abel, o justo, até ao sangue de Zacarias, filho de Baraquias, que matastes entre o santuário e o altar.', 35, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que todas estas coisas hão de vir sobre esta geração.', 36, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jerusalém, Jerusalém, que matas os profetas, e apedrejas os que te são enviados! quantas vezes quis eu ajuntar os teus filhos, como a galinha ajunta os seus pintos debaixo das asas, e tu não quiseste!', 37, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que a vossa casa vai ficar-vos deserta;', 38, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu vos digo que desde agora me não vereis mais, até que digais: Bendito o que vem em nome do Senhor.', 39, 23, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus, tomando a palavra, tornou a falar-lhes em parábolas, dizendo:', 1, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O reino dos céus é semelhante a um certo rei que celebrou as bodas de seu filho;', 2, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E enviou os seus servos a chamar os convidados para as bodas, e estes não quiseram vir.', 3, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois, enviou outros servos, dizendo: Dizei aos convidados: Eis que tenho o meu jantar preparado, os meus bois e cevados já mortos, e tudo já pronto; vinde às bodas.', 4, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eles, porém, não fazendo caso, foram, um para o seu campo, outro para o seu negócio;', 5, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os outros, apoderando-se dos servos, os ultrajaram e mataram.', 6, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o rei, tendo notícia disto, encolerizou-se e, enviando os seus exércitos, destruiu aqueles homicidas, e incendiou a sua cidade.', 7, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então diz aos servos: As bodas, na verdade, estão preparadas, mas os convidados não eram dignos.', 8, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ide, pois, às saídas dos caminhos, e convidai para as bodas a todos os que encontrardes.', 9, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os servos, saindo pelos caminhos, ajuntaram todos quantos encontraram, tanto maus como bons; e a festa nupcial foi cheia de convidados.', 10, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o rei, entrando para ver os convidados, viu ali um homem que não estava trajado com veste de núpcias.', 11, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe: Amigo, como entraste aqui, não tendo veste nupcial? E ele emudeceu.', 12, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse, então, o rei aos servos: Amarrai-o de pés e mãos, levai-o, e lançai-o nas trevas exteriores; ali haverá pranto e ranger de dentes.', 13, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque muitos são chamados, mas poucos escolhidos.', 14, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, retirando-se os fariseus, consultaram entre si como o surpreenderiam nalguma palavra;', 15, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E enviaram-lhe os seus discípulos, com os herodianos, dizendo: Mestre, bem sabemos que és verdadeiro, e ensinas o caminho de Deus segundo a verdade, e de ninguém se te dá, porque não olhas a aparência dos homens.', 16, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dize-nos, pois, que te parece? É lícito pagar o tributo a César, ou não?', 17, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, conhecendo a sua malícia, disse: Por que me experimentais, hipócritas?', 18, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mostrai-me a moeda do tributo. E eles lhe apresentaram um dinheiro.', 19, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele diz-lhes: De quem é esta efígie e esta inscrição?', 20, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizem-lhe eles: De César. Então ele lhes disse: Dai pois a César o que é de César, e a Deus o que é de Deus.', 21, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles, ouvindo isto, maravilharam-se, e, deixando-o, se retiraram.', 22, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No mesmo dia chegaram junto dele os saduceus, que dizem não haver ressurreição, e o interrogaram,', 23, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Mestre, Moisés disse: Se morrer alguém, não tendo filhos, casará o seu irmão com a mulher dele, e suscitará descendência a seu irmão.', 24, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, houve entre nós sete irmãos; e o primeiro, tendo casado, morreu e, não tendo descendência, deixou sua mulher a seu irmão.', 25, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da mesma sorte o segundo, e o terceiro, até ao sétimo;', 26, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por fim, depois de todos, morreu também a mulher.', 27, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, na ressurreição, de qual dos sete será a mulher, visto que todos a possuíram?', 28, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, respondendo, disse-lhes: Errais, não conhecendo as Escrituras, nem o poder de Deus.', 29, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque na ressurreição nem casam nem são dados em casamento; mas serão como os anjos de Deus no céu.', 30, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, acerca da ressurreição dos mortos, não tendes lido o que Deus vos declarou, dizendo:', 31, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou o Deus de Abraão, o Deus de Isaque, e o Deus de Jacó? Ora, Deus não é Deus dos mortos, mas dos vivos.', 32, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, as turbas, ouvindo isto, ficaram maravilhadas da sua doutrina.', 33, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os fariseus, ouvindo que ele fizera emudecer os saduceus, reuniram-se no mesmo lugar.', 34, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um deles, doutor da lei, interrogou-o para o experimentar, dizendo:', 35, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mestre, qual é o grande mandamento na lei?', 36, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus disse-lhe: Amarás o Senhor teu Deus de todo o teu coração, e de toda a tua alma, e de todo o teu pensamento.', 37, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este é o primeiro e grande mandamento.', 38, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o segundo, semelhante a este, é: Amarás o teu próximo como a ti mesmo.', 39, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Destes dois mandamentos dependem toda a lei e os profetas.', 40, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando reunidos os fariseus, interrogou-os Jesus,', 41, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Que pensais vós do Cristo? De quem é filho? Eles disseram-lhe: De Davi.', 42, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes ele: Como é então que Davi, em espírito, lhe chama Senhor, dizendo:', 43, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse o Senhor ao meu Senhor:Assenta-te à minha direita,Até que eu ponha os teus inimigos por escabelo de teus pés?', 44, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se Davi, pois, lhe chama Senhor, como é seu filho?', 45, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ninguém podia responder-lhe uma palavra; nem desde aquele dia ousou mais alguém interrogá-lo.', 46, 22, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eia, pois, agora vós, ricos, chorai e pranteai, por vossas misérias, que sobre vós hão de vir.', 1, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As vossas riquezas estão apodrecidas, e as vossas vestes estão comidas de traça.', 2, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O vosso ouro e a vossa prata se enferrujaram; e a sua ferrugem dará testemunho contra vós, e comerá como fogo a vossa carne. Entesourastes para os últimos dias.', 3, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que o jornal dos trabalhadores que ceifaram as vossas terras, e que por vós foi diminuído, clama; e os clamores dos que ceifaram entraram nos ouvidos do Senhor dos exércitos.', 4, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deliciosamente vivestes sobre a terra, e vos deleitastes; cevastes os vossos corações, como num dia de matança.', 5, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Condenastes e matastes o justo; ele não vos resistiu.', 6, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sede pois, irmãos, pacientes até à vinda do Senhor. Eis que o lavrador espera o precioso fruto da terra, aguardando-o com paciência, até que receba a chuva temporã e serôdia.', 7, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sede vós também pacientes, fortalecei os vossos corações; porque já a vinda do Senhor está próxima.', 8, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, não vos queixeis uns contra os outros, para que não sejais condenados. Eis que o juiz está à porta.', 9, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meus irmãos, tomai por exemplo de aflição e paciência os profetas que falaram em nome do Senhor.', 10, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que temos por bem-aventurados os que sofreram. Ouvistes qual foi a paciência de Jó, e vistes o fim que o Senhor lhe deu; porque o Senhor é muito misericordioso e piedoso.', 11, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, sobretudo, meus irmãos, não jureis, nem pelo céu, nem pela terra, nem façais qualquer outro juramento; mas que a vossa palavra seja sim, sim, e não, não; para que não caiais em condenação.', 12, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Está alguém entre vós aflito? Ore. Está alguém contente? Cante louvores.', 13, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Está alguém entre vós doente? Chame os presbíteros da igreja, e orem sobre ele, ungindo-o com azeite em nome do Senhor;', 14, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a oração da fé salvará o doente, e o Senhor o levantará; e, se houver cometido pecados, ser-lhe-ão perdoados.', 15, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Confessai as vossas culpas uns aos outros, e orai uns pelos outros, para que sareis. A oração feita por um justo pode muito em seus efeitos.', 16, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Elias era homem sujeito às mesmas paixões que nós e, orando, pediu que não chovesse e, por três anos e seis meses, não choveu sobre a terra.', 17, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E orou outra vez, e o céu deu chuva, e a terra produziu o seu fruto.', 18, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, se algum dentre vós se tem desviado da verdade, e alguém o converter,', 19, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saiba que aquele que fizer converter do erro do seu caminho um pecador, salvará da morte uma alma, e cobrirá uma multidão de pecados.', 20, 5, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meus irmãos, não tenhais a fé de nosso Senhor Jesus Cristo, Senhor da glória, em acepção de pessoas.', 1, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se no vosso ajuntamento entrar algum homem com anel de ouro no dedo, com trajes preciosos, e entrar também algum pobre com sórdido traje,', 2, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E atentardes para o que traz o traje precioso, e lhe disserdes: Assenta-te tu aqui num lugar de honra, e disserdes ao pobre: Tu, fica aí em pé, ou assenta-te abaixo do meu estrado,', 3, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura não fizestes distinção entre vós mesmos, e não vos fizestes juízes de maus pensamentos?', 4, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvi, meus amados irmãos: Porventura não escolheu Deus aos pobres deste mundo para serem ricos na fé, e herdeiros do reino que prometeu aos que o amam?', 5, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós desonrastes o pobre. Porventura não vos oprimem os ricos, e não vos arrastam aos tribunais?', 6, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura não blasfemam eles o bom nome que sobre vós foi invocado?', 7, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia, se cumprirdes, conforme a Escritura, a lei real: Amarás a teu próximo como a ti mesmo, bem fazeis.', 8, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se fazeis acepção de pessoas, cometeis pecado, e sois redargüidos pela lei como transgressores.', 9, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque qualquer que guardar toda a lei, e tropeçar em um só ponto, tornou-se culpado de todos.', 10, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque aquele que disse: Não cometerás adultério, também disse: Não matarás. Se tu pois não cometeres adultério, mas matares, estás feito transgressor da lei.', 11, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim falai, e assim procedei, como devendo ser julgados pela lei da liberdade.', 12, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o juízo será sem misericórdia sobre aquele que não fez misericórdia; e a misericórdia triunfa do juízo.', 13, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meus irmãos, que aproveita se alguém disser que tem fé, e não tiver as obras? Porventura a fé pode salvá-lo?', 14, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se o irmão ou a irmã estiverem nus, e tiverem falta de mantimento quotidiano,', 15, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E algum de vós lhes disser: Ide em paz, aquentai-vos, e fartai-vos; e nào lhes derdes as coisas necessárias para o corpo, que proveito virá daí?', 16, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também a fé, se não tiver as obras, é morta em si mesma.', 17, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas dirá alguém: Tu tens a fé, e eu tenho as obras; mostra-me a tua fé sem as tuas obras, e eu te mostrarei a minha fé pelas minhas obras.', 18, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu crês que há um só Deus; fazes bem. Também os demônios o crêem, e estremecem.', 19, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, ó homem vão, queres tu saber que a fé sem as obras é morta?', 20, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura o nosso pai Abraão não foi justificado pelas obras, quando ofereceu sobre o altar o seu filho Isaque?', 21, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem vês que a fé cooperou com as suas obras, e que pelas obras a fé foi aperfeiçoada.', 22, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E cumpriu-se a Escritura, que diz: E creu Abraão em Deus, e foi-lhe isso imputado como justiça, e foi chamado o amigo de Deus.', 23, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vedes então que o homem é justificado pelas obras, e não somente pela fé.', 24, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de igual modo Raabe, a meretriz, não foi também justificada pelas obras, quando recolheu os emissários, e os despediu por outro caminho?', 25, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, assim como o corpo sem o espírito está morto, assim também a fé sem obras é morta.', 26, 2, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No princípio era o Verbo, e o Verbo estava com Deus, e o Verbo era Deus.', 1, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele estava no princípio com Deus.', 2, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todas as coisas foram feitas por ele, e sem ele nada do que foi feito se fez.', 3, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nele estava a vida, e a vida era a luz dos homens.', 4, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a luz resplandece nas trevas, e as trevas não a compreenderam.', 5, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Houve um homem enviado de Deus, cujo nome era João.', 6, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este veio para testemunho, para que testificasse da luz, para que todos cressem por ele.', 7, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não era ele a luz, mas para que testificasse da luz.', 8, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ali estava a luz verdadeira, que ilumina a todo o homem que vem ao mundo.', 9, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estava no mundo, e o mundo foi feito por ele, e o mundo não o conheceu.', 10, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Veio para o que era seu, e os seus não o receberam.', 11, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, a todos quantos o receberam, deu-lhes o poder de serem feitos filhos de Deus, aos que crêem no seu nome;', 12, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais não nasceram do sangue, nem da vontade da carne, nem da vontade do homem, mas de Deus.', 13, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o Verbo se fez carne, e habitou entre nós, e vimos a sua glória, como a glória do unigênito do Pai, cheio de graça e de verdade.', 14, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('João testificou dele, e clamou, dizendo: Este era aquele de quem eu dizia: O que vem após mim é antes de mim, porque foi primeiro do que eu.', 15, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos nós recebemos também da sua plenitude, e graça por graça.', 16, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a lei foi dada por Moisés; a graça e a verdade vieram por Jesus Cristo.', 17, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deus nunca foi visto por alguém. O Filho unigênito, que está no seio do Pai, esse o revelou.', 18, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E este é o testemunho de João, quando os judeus mandaram de Jerusalém sacerdotes e levitas para que lhe perguntassem: Quem és tu?', 19, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E confessou, e não negou; confessou: Eu não sou o Cristo.', 20, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntaram-lhe: Então quê? És tu Elias? E disse: Não sou. És tu profeta? E respondeu: Não.', 21, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe pois: Quem és? para que demos resposta àqueles que nos enviaram; que dizes de ti mesmo?', 22, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse: Eu sou a voz do que clama no deserto: Endireitai o caminho do Senhor, como disse o profeta Isaías.', 23, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que tinham sido enviados eram dos fariseus.', 24, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntaram-lhe, e disseram-lhe: Por que batizas, pois, se tu não és o Cristo, nem Elias, nem o profeta?', 25, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('João respondeu-lhes, dizendo: Eu batizo com água; mas no meio devós está um a quem vós não conheceis.', 26, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este é aquele que vem após mim, que é antes de mim, do qual eu não sou digno de desatar a correia da alparca.', 27, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estas coisas aconteceram em Betabara, do outro lado do Jordão, onde João estava batizando.', 28, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No dia seguinte João viu a Jesus, que vinha para ele, e disse: Eis o Cordeiro de Deus, que tira o pecado do mundo.', 29, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este é aquele do qual eu disse: Após mim vem um homem que é antes de mim, porque foi primeiro do que eu.', 30, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu não o conhecia; mas, para que ele fosse manifestado a Israel, vim eu, por isso, batizando com água.', 31, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E João testificou, dizendo: Eu vi o Espírito descer do céu como pomba, e repousar sobre ele.', 32, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu não o conhecia, mas o que me mandou a batizar com água, esse me disse: Sobre aquele que vires descer o Espírito, e sobre ele repousar, esse é o que batiza com o Espírito Santo.', 33, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu vi, e tenho testificado que este é o Filho de Deus.', 34, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No dia seguinte João estava outra vez ali, e dois dos seus discípulos;', 35, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo passar a Jesus, disse: Eis aqui o Cordeiro de Deus.', 36, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os dois discípulos ouviram-no dizer isto, e seguiram a Jesus.', 37, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, voltando-se e vendo que eles o seguiam, disse-lhes: Que buscais? E eles disseram: Rabi (que, traduzido, quer dizer Mestre), onde moras?', 38, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele lhes disse: Vinde, e vede. Foram, e viram onde morava, e ficaram com ele aquele dia; e era já quase a hora décima.', 39, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Era André, irmão de Simão Pedro, um dos dois que ouviram aquilo de João, e o haviam seguido.', 40, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este achou primeiro a seu irmão Simão, e disse-lhe: Achamos o Messias (que, traduzido, é o Cristo).', 41, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levou-o a Jesus. E, olhando Jesus para ele, disse: Tu és Simão, filho de Jonas; tu serás chamado Cefas (que quer dizer Pedro).', 42, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No dia seguinte quis Jesus ir à Galiléia, e achou a Filipe, e disse-lhe: Segue-me.', 43, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Filipe era de Betsaida, cidade de André e de Pedro.', 44, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Filipe achou Natanael, e disse-lhe: Havemos achado aquele de quem Moisés escreveu na lei, e os profetas: Jesus de Nazaré, filho de José.', 45, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Natanael: Pode vir alguma coisa boa de Nazaré? Disse-lhe Filipe: Vem, e vê.', 46, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus viu Natanael vir ter com ele, e disse dele: Eis aqui um verdadeiro israelita, em quem não há dolo.', 47, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Natanael: De onde me conheces tu? Jesus respondeu, e disse-lhe: Antes que Filipe te chamasse, te vi eu, estando tu debaixo da figueira.', 48, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Natanael respondeu, e disse-lhe: Rabi, tu és o Filho de Deus; tu és o Rei de Israel.', 49, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu, e disse-lhe: Porque te disse: Vi-te debaixo da figueira, crês? Coisas maiores do que estas verás.', 50, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe: Na verdade, na verdade vos digo que daqui em diante vereis o céu aberto, e os anjos de Deus subindo e descendo sobre o Filho do homem.', 51, 1, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando o Senhor entendeu que os fariseus tinham ouvido que Jesus fazia e batizava mais discípulos do que João', 1, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Ainda que Jesus mesmo não batizava, mas os seus discípulos),', 2, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deixou a Judéia, e foi outra vez para a Galiléia.', 3, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E era-lhe necessário passar por Samaria.', 4, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Foi, pois, a uma cidade de Samaria, chamada Sicar, junto da herdade que Jacó tinha dado a seu filho José.', 5, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava ali a fonte de Jacó. Jesus, pois, cansado do caminho, assentou-se assim junto da fonte. Era isto quase à hora sexta.', 6, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Veio uma mulher de Samaria tirar água. Disse-lhe Jesus: Dá-me de beber.', 7, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os seus discípulos tinham ido à cidade comprar comida.', 8, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe, pois, a mulher samaritana: Como, sendo tu judeu, me pedes de beber a mim, que sou mulher samaritana? (porque os judeus não se comunicam com os samaritanos).', 9, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu, e disse-lhe: Se tu conheceras o dom de Deus, e quem é o que te diz: Dá-me de beber, tu lhe pedirias, e ele te daria água viva.', 10, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe a mulher: Senhor, tu não tens com que a tirar, e o poço é fundo; onde, pois, tens a água viva?', 11, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('És tu maior do que o nosso pai Jacó, que nos deu o poço, bebendo ele próprio dele, e os seus filhos, e o seu gado?', 12, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu, e disse-lhe: Qualquer que beber desta água tornará a ter sede;', 13, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas aquele que beber da água que eu lhe der nunca terá sede, porque a água que eu lhe der se fará nele uma fonte de água que salte para a vida eterna.', 14, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe a mulher: Senhor, dá-me dessa água, para que não mais tenha sede, e não venha aqui tirá-la.', 15, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Vai, chama o teu marido, e vem cá.', 16, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A mulher respondeu, e disse: Não tenho marido. Disse-lhe Jesus: Disseste bem: Não tenho marido;', 17, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tiveste cinco maridos, e o que agora tens não é teu marido; isto disseste com verdade.', 18, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe a mulher: Senhor, vejo que és profeta.', 19, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nossos pais adoraram neste monte, e vós dizeis que é em Jerusalém o lugar onde se deve adorar.', 20, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Mulher, crê-me que a hora vem, em que nem neste monte nem em Jerusalém adorareis o Pai.', 21, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós adorais o que não sabeis; nós adoramos o que sabemos porque a salvação vem dos judeus.', 22, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a hora vem, e agora é, em que os verdadeiros adoradores adorarão o Pai em espírito e em verdade; porque o Pai procura a tais que assim o adorem.', 23, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deus é Espírito, e importa que os que o adoram o adorem em espírito e em verdade.', 24, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A mulher disse-lhe: Eu sei que o Messias (que se chama o Cristo) vem; quando ele vier, nos anunciará tudo.', 25, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus disse-lhe: Eu o sou, eu que falo contigo.', 26, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nisto vieram os seus discípulos, e maravilharam-se de que estivesse falando com uma mulher; todavia nenhum lhe disse: Que perguntas? ou: Por que falas com ela?', 27, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deixou, pois, a mulher o seu cântaro, e foi à cidade, e disse àqueles homens:', 28, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vinde, vede um homem que me disse tudo quanto tenho feito. Porventura não é este o Cristo?', 29, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saíram, pois, da cidade, e foram ter com ele.', 30, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E entretanto os seus discípulos lhe rogaram, dizendo: Rabi, come.', 31, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, lhes disse: Uma comida tenho para comer, que vós não conheceis.', 32, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então os discípulos diziam uns aos outros: Trouxe-lhe, porventura, alguém algo de comer?', 33, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus disse-lhes: A minha comida é fazer a vontade daquele que me enviou, e realizar a sua obra.', 34, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não dizeis vós que ainda há quatro meses até que venha a ceifa? Eis que eu vos digo: Levantai os vossos olhos, e vede as terras, que já estão brancas para a ceifa.', 35, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que ceifa recebe galardão, e ajunta fruto para a vida eterna; para que, assim o que semeia como o que ceifa, ambos se regozijem.', 36, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nisto é verdadeiro o ditado, que um é o que semeia, e outro o que ceifa.', 37, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu vos enviei a ceifar onde vós não trabalhastes; outros trabalharam, e vós entrastes no seu trabalho.', 38, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos dos samaritanos daquela cidade creram nele, pela palavra da mulher, que testificou: Disse-me tudo quanto tenho feito.', 39, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Indo, pois, ter com ele os samaritanos, rogaram-lhe que ficasse com eles; e ficou ali dois dias.', 40, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos mais creram nele, por causa da sua palavra.', 41, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E diziam à mulher: Já não é pelo teu dito que nós cremos; porque nós mesmos o temos ouvido, e sabemos que este é verdadeiramente o Cristo, o Salvador do mundo.', 42, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dois dias depois partiu dali, e foi para a Galiléia.', 43, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Jesus mesmo testificou que um profeta não tem honra na sua própria pátria.', 44, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chegando, pois, à Galiléia, os galileus o receberam, vistas todas as coisas que fizera em Jerusalém, no dia da festa; porque também eles tinham ido à festa.', 45, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segunda vez foi Jesus a Caná da Galiléia, onde da água fizera vinho. E havia ali um nobre, cujo filho estava enfermo em Cafarnaum.', 46, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvindo este que Jesus vinha da Judéia para a Galiléia, foi ter com ele, e rogou-lhe que descesse, e curasse o seu filho, porque já estava à morte.', 47, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus lhe disse: Se não virdes sinais e milagres, não crereis.', 48, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe o nobre: Senhor, desce, antes que meu filho morra.', 49, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Vai, o teu filho vive. E o homem creu na palavra que Jesus lhe disse, e partiu.', 50, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E descendo ele logo, saíram-lhe ao encontro os seus servos, e lhe anunciaram, dizendo: O teu filho vive.', 51, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Perguntou-lhes, pois, a que hora se achara melhor. E disseram-lhe: Ontem às sete horas a febre o deixou.', 52, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entendeu, pois, o pai que era aquela hora a mesma em que Jesus lhe disse: O teu filho vive; e creu ele, e toda a sua casa.', 53, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus fez este segundo milagre, quando ia da Judéia para a Galiléia.', 54, 4, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, acabando Jesus de dar instruções aos seus doze discípulos, partiu dali a ensinar e a pregar nas cidades deles.', 1, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E João, ouvindo no cárcere falar dos feitos de Cristo, enviou dois dos seus discípulos,', 2, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A dizer-lhe: És tu aquele que havia de vir, ou esperamos outro?', 3, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhes: Ide, e anunciai a João as coisas que ouvis e vedes:', 4, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os cegos vêem, e os coxos andam; os leprosos são limpos, e os surdos ouvem; os mortos são ressuscitados, e aos pobres é anunciado o evangelho.', 5, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E bem-aventurado é aquele que não se escandalizar em mim.', 6, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, partindo eles, começou Jesus a dizer às turbas, a respeito de João: Que fostes ver no deserto? uma cana agitada pelo vento?', 7, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sim, que fostes ver? um homem ricamente vestido? Os que trajam ricamente estão nas casas dos reis.', 8, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, então que fostes ver? um profeta? Sim, vos digo eu, e muito mais do que profeta;', 9, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque é este de quem está escrito:Eis que diante da tua face envio o meu anjo,que preparará diante de ti o teu caminho.', 10, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que, entre os que de mulher têm nascido, não apareceu alguém maior do que João o Batista; mas aquele que é o menor no reino dos céus é maior do que ele.', 11, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, desde os dias de João o Batista até agora, se faz violência ao reino dos céus, e pela força se apoderam dele.', 12, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos os profetas e a lei profetizaram até João.', 13, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se quereis dar crédito, é este o Elias que havia de vir.', 14, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem tem ouvidos para ouvir, ouça.', 15, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, a quem assemelharei esta geração? É semelhante aos meninos que se assentam nas praças, e clamam aos seus companheiros,', 16, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizem: Tocamo-vos flauta, e não dançastes; cantamo-vos lamentações, e não chorastes.', 17, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto veio João, não comendo nem bebendo, e dizem: Tem demônio.', 18, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Veio o Filho do homem, comendo e bebendo, e dizem: Eis aí um homem comilão e beberrão, amigo dos publicanos e pecadores. Mas a sabedoria é justificada por seus filhos.', 19, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então começou ele a lançar em rosto às cidades onde se operou a maior parte dos seus prodígios o não se haverem arrependido, dizendo:', 20, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de ti, Corazim! ai de ti, Betsaida! porque, se em Tiro e em Sidom fossem feitos os prodígios que em vós se fizeram, há muito que se teriam arrependido, com saco e com cinza.', 21, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso eu vos digo que haverá menos rigor para Tiro e Sidom, no dia do juízo, do que para vós.', 22, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tu, Cafarnaum, que te ergues até ao céu, serás abatida até ao inferno; porque, se em Sodoma tivessem sido feitos os prodígios que em ti se operaram, teria ela permanecido até hoje.', 23, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu vos digo, porém, que haverá menos rigor para os de Sodoma, no dia do juízo, do que para ti.', 24, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Naquele tempo, respondendo Jesus, disse: Graças te dou, ó Pai, Senhor do céu e da terra, que ocultaste estas coisas aos sábios e entendidos, e as revelaste aos pequeninos.', 25, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sim, ó Pai, porque assim te aprouve.', 26, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todas as coisas me foram entregues por meu Pai, e ninguém conhece o Filho, senão o Pai; e ninguém conhece o Pai, senão o Filho, e aquele a quem o Filho o quiser revelar.', 27, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vinde a mim, todos os que estais cansados e oprimidos, e eu vos aliviarei.', 28, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tomai sobre vós o meu jugo, e aprendei de mim, que sou manso e humilde de coração; e encontrareis descanso para as vossas almas.', 29, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o meu jugo é suave e o meu fardo é leve.', 30, 11, 1);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia entre os fariseus um homem, chamado Nicodemos, príncipe dos judeus.', 1, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este foi ter de noite com Jesus, e disse-lhe: Rabi, bem sabemos que és Mestre, vindo de Deus; porque ninguém pode fazer estes sinais que tu fazes, se Deus não for com ele.', 2, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu, e disse-lhe: Na verdade, na verdade te digo que aquele que não nascer de novo, não pode ver o reino de Deus.', 3, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Nicodemos: Como pode um homem nascer, sendo velho? Pode, porventura, tornar a entrar no ventre de sua mãe, e nascer?', 4, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu: Na verdade, na verdade te digo que aquele que não nascer da água e do Espírito, não pode entrar no reino de Deus.', 5, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que é nascido da carne é carne, e o que é nascido do Espírito é espírito.', 6, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não te maravilhes de te ter dito: Necessário vos é nascer de novo.', 7, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O vento assopra onde quer, e ouves a sua voz, mas não sabes de onde vem, nem para onde vai; assim étodo aquele que é nascido do Espírito.', 8, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nicodemos respondeu, e disse-lhe: Como pode ser isso?', 9, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu, e disse-lhe: Tu és mestre de Israel, e não sabes isto?', 10, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade, na verdade te digo que nós dizemos o que sabemos, e testificamos o que vimos; e não aceitais o nosso testemunho.', 11, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se vos falei de coisas terrestres, e não crestes, como crereis, se vos falar das celestiais?', 12, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, ninguém subiu ao céu, senão o que desceu do céu, o Filho do homem, que está no céu.', 13, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como Moisés levantou a serpente no deserto, assim importa que o Filho do homem seja levantado;', 14, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que todo aquele que nele crê não pereça, mas tenha a vida eterna.', 15, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus amou o mundo de tal maneira que deu o seu Filho unigênito, para que todo aquele que nele crê não pereça, mas tenha a vida eterna.', 16, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus enviou o seu Filho ao mundo, não para que condenasse o mundo, mas para que o mundo fosse salvo por ele.', 17, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem crê nele não é condenado; mas quem não crê já está condenado, porquanto não crê no nome do unigênito Filho de Deus.', 18, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a condenação é esta: Que a luz veio ao mundo, e os homens amaram mais as trevas do que a luz, porque as suas obras eram más.', 19, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todo aquele que faz o mal odeia a luz, e não vem para a luz, para que as suas obras não sejam reprovadas.', 20, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas quem pratica a verdade vem para a luz, a fim de que as suas obras sejam manifestas, porque são feitas em Deus.', 21, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disto foi Jesus com os seus discípulos para a terra da Judéia; e estava ali com eles, e batizava.', 22, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, João batizava também em Enom, junto a Salim, porque havia ali muitas águas; e vinham ali, e eram batizados.', 23, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ainda João não tinha sido lançado na prisão.', 24, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Houve então uma questão entre os discípulos de João e os judeus acerca da purificação.', 25, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram ter com João, e disseram-lhe: Rabi, aquele que estava contigo além do Jordão, do qual tu deste testemunho, ei-lo batizando, e todos vão ter com ele.', 26, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('João respondeu, e disse: O homem não pode receber coisa alguma, se não lhe for dada do céu.', 27, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós mesmos me sois testemunhas de que disse: Eu não sou o Cristo, mas sou enviado adiante dele.', 28, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que tem a esposa é o esposo; mas o amigo do esposo, que lhe assiste e o ouve, alegra-se muito com a voz do esposo. Assim, pois, já este meu gozo está cumprido.', 29, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É necessário que ele cresça e que eu diminua.', 30, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que vem de cima é sobre todos; aquele que vem da terra é da terra e fala da terra. Aquele que vem do céu é sobre todos.', 31, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquilo que ele viu e ouviu isso testifica; e ninguém aceita o seu testemunho.', 32, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que aceitou o seu testemunho, esse confirmou que Deus é verdadeiro.', 33, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque aquele que Deus enviou fala as palavras de Deus; pois não lhe dá Deus o Espírito por medida.', 34, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Pai ama o Filho, e todas as coisas entregou nas suas mãos.', 35, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que crê no Filho tem a vida eterna; mas aquele que não crê no Filho não verá a vida, mas a ira de Deus sobre ele permanece.', 36, 3, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De onde vêm as guerras e pelejas entre vós? Porventura não vêm disto, a saber, dos vossos deleites, que nos vossos membros guerreiam?', 1, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cobiçais, e nada tendes; matais, e sois invejosos, e nada podeis alcançar; combateis e guerreais, e nada tendes, porque não pedis.', 2, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pedis, e não recebeis, porque pedis mal, para o gastardes em vossos deleites.', 3, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Adúlteros e adúlteras, não sabeis vós que a amizade do mundo é inimizade contra Deus? Portanto, qualquer que quiser ser amigo do mundo constitui-se inimigo de Deus.', 4, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou cuidais vós que em vão diz a Escritura: O Espírito que em nós habita tem ciúmes?', 5, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, ele dá maior graça. Portanto diz: Deus resiste aos soberbos, mas dá graça aos humildes.', 6, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sujeitai-vos, pois, a Deus, resisti ao diabo, e ele fugirá de vós.', 7, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chegai-vos a Deus, e ele se chegará a vós. Alimpai as mãos, pecadores; e, vós de duplo ânimo, purificai os corações.', 8, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Senti as vossas misérias, e lamentai e chorai; converta-se o vosso riso em pranto, e o vosso gozo em tristeza.', 9, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Humilhai-vos perante o Senhor, e ele vos exaltará.', 10, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, não faleis mal uns dos outros. Quem fala mal de um irmão, e julga a seu irmão, fala mal da lei, e julga a lei; e, se tu julgas a lei, já não és observador da lei, mas juiz.', 11, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Há só um legislador que pode salvar e destruir. Tu, porém, quem és, que julgas a outrem?', 12, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eia agora vós, que dizeis: Hoje, ou amanhã, iremos a tal cidade, e lá passaremos um ano, e contrataremos, e ganharemos;', 13, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo-vos que não sabeis o que acontecerá amanhã. Porque, que é a vossa vida? É um vapor que aparece por um pouco, e depois se desvanece.', 14, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em lugar do que devíeis dizer: Se o Senhor quiser, e se vivermos, faremos isto ou aquilo.', 15, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora vos gloriais em vossas presunções; toda a glória tal como esta é maligna.', 16, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele, pois, que sabe fazer o bem e não o faz, comete pecado.', 17, 4, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disto havia uma festa entre os judeus, e Jesus subiu a Jerusalém.', 1, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, em Jerusalém há, próximo à porta das ovelhas, um tanque, chamado em hebreu Betesda, o qual tem cinco alpendres.', 2, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nestes jazia grande multidão de enfermos, cegos, mancos e ressicados, esperando o movimento da água.', 3, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto um anjo descia em certo tempo ao tanque, e agitava a água; e o primeiro que ali descia, depois do movimento da água, sarava de qualquer enfermidade que tivesse.', 4, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava ali um homem que, havia trinta e oito anos, se achava enfermo.', 5, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, vendo este deitado, e sabendo que estava neste estado havia muito tempo, disse-lhe: Queres ficar são?', 6, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O enfermo respondeu-lhe: Senhor, não tenho homem algum que, quando a água é agitada, me ponha no tanque; mas, enquanto eu vou, desce outro antes de mim.', 7, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus disse-lhe: Levanta-te, toma o teu leito, e anda.', 8, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Logo aquele homem ficou são; e tomou o seu leito, e andava. E aquele dia era sábado.', 9, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então os judeus disseram àquele que tinha sido curado: É sábado, não te é lícito levar o leito.', 10, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele respondeu-lhes: Aquele que me curou, ele próprio disse: Toma o teu leito, e anda.', 11, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Perguntaram-lhe, pois: Quem é o homem que te disse: Toma o teu leito, e anda?', 12, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que fora curado não sabia quem era; porque Jesus se havia retirado, em razão de naquele lugar haver grande multidão.', 13, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois Jesus encontrou-o no templo, e disse-lhe: Eis que já estás são; não peques mais, para que não te suceda alguma coisa pior.', 14, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele homem foi, e anunciou aos judeus que Jesus era o que o curara.', 15, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por esta causa os judeus perseguiram a Jesus, e procuravam matá-lo, porque fazia estas coisas no sábado.', 16, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhes respondeu: Meu Pai trabalha até agora, e eu trabalho também.', 17, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, pois, os judeus ainda mais procuravam matá-lo, porque não só quebrantava o sábado, mas também dizia que Deus era seu próprio Pai, fazendo-se igual a Deus.', 18, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus respondeu, e disse-lhes: Na verdade, na verdade vos digo que o Filho por si mesmo não pode fazer coisa alguma, se o não vir fazer o Pai; porque tudo quanto ele faz, o Filho o faz igualmente.', 19, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Pai ama o Filho, e mostra-lhe tudo o que faz; e ele lhe mostrará maiores obras do que estas, para que vos maravilheis.', 20, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, assim como o Pai ressuscita os mortos, e os vivifica, assim também o Filho vivifica aqueles que quer.', 21, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também o Pai a ninguém julga, mas deu ao Filho todo o juízo;', 22, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que todos honrem o Filho, como honram o Pai. Quem não honra o Filho, não honra o Pai que o enviou.', 23, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade, na verdade vos digo que quem ouve a minha palavra, e crê naquele que me enviou, tem a vida eterna, e não entrará em condenação, mas passou da morte para a vida.', 24, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade, em verdade vos digo que vem a hora, e agora é, em que os mortos ouvirão a voz do Filho de Deus, e os que a ouvirem viverão.', 25, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, como o Pai tem a vida em si mesmo, assim deu também ao Filho ter a vida em si mesmo;', 26, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E deu-lhe o poder de exercer o juízo, porque é o Filho do homem.', 27, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos maravilheis disto; porque vem a hora em que todos os que estão nos sepulcros ouvirão a sua voz.', 28, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que fizeram o bem sairão para a ressurreição da vida; e os que fizeram o mal para a ressurreição da condenação.', 29, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu não posso de mim mesmo fazer coisa alguma. Como ouço, assim julgo; e o meu juízo é justo, porque não busco a minha vontade, mas a vontade do Pai que me enviou.', 30, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se eu testifico de mim mesmo, o meu testemunho não é verdadeiro.', 31, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Há outro que testifica de mim, e sei que o testemunho que ele dá de mim é verdadeiro.', 32, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós mandastes mensageiros a João, e ele deu testemunho da verdade.', 33, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, porém, não recebo testemunho de homem; mas digo isto, para que vos salveis.', 34, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele era a candeia que ardia e alumiava, e vós quisestes alegrar-vos por um pouco de tempo com a sua luz.', 35, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eu tenho maior testemunho do que o de João; porque as obras que o Pai me deu para realizar, as mesmas obras que eu faço, testificam de mim, que o Pai me enviou.', 36, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o Pai, que me enviou, ele mesmo testificou de mim. Vós nunca ouvistes a sua voz, nem vistes o seu parecer.', 37, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a sua palavra não permanece em vós, porque naquele que ele enviou não credes vós.', 38, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Examinais as Escrituras, porque vós cuidais ter nelas a vida eterna, e são elas que de mim testificam;', 39, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não quereis vir a mim para terdes vida.', 40, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu não recebo glória dos homens;', 41, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas bem vos conheço, que não tendes em vós o amor de Deus.', 42, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu vim em nome de meu Pai, e não me aceitais; se outro vier em seu próprio nome, a esse aceitareis.', 43, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como podeis vós crer, recebendo honra uns dos outros, e não buscando a honra que vem só de Deus?', 44, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não cuideis que eu vos hei de acusar para com o Pai. Há um que vos acusa, Moisés, em quem vós esperais.', 45, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se vós crêsseis em Moisés, creríeis em mim; porque de mim escreveu ele.', 46, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se não credes nos seus escritos, como crereis nas minhas palavras?', 47, 5, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Foi, pois, Jesus seis dias antes da páscoa a betânia, onde estava Lázaro, o que falecera, e a quem ressuscitara dentre os mortos.', 1, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fizeram-lhe, pois, ali uma ceia, e Marta servia, e Lázaro era um dos que estavam à mesa com ele.', 2, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Maria, tomando um arrátel de ungüento de nardo puro, de muito preço, ungiu os pés de Jesus, e enxugou-lhe os pés com os seus cabelos; e encheu-se a casa do cheiro do ungüento.', 3, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, um dos seus discípulos, Judas Iscariotes, filho de Simão, o que havia de traí-lo, disse:', 4, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por que não se vendeu este ungüento por trezentos dinheiros e não se deu aos pobres?', 5, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, ele disse isto, não pelo cuidado que tivesse dos pobres, mas porque era ladrão e tinha a bolsa, e tirava o que ali se lançava.', 6, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse, pois, Jesus: Deixai-a; para o dia da minha sepultura guardou isto;', 7, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os pobres sempre os tendes convosco, mas a mim nem sempre me tendes.', 8, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muita gente dos judeus soube que ele estava ali; e foram, não só por causa de Jesus, mas também para ver a Lázaro, a quem ressuscitara dentre os mortos.', 9, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os principais dos sacerdotes tomaram deliberação para matar também a Lázaro;', 10, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque muitos dos judeus, por causa dele, iam e criam em Jesus.', 11, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No dia seguinte, ouvindo uma grande multidão, que viera à festa, que Jesus vinha a Jerusalém,', 12, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tomaram ramos de palmeiras, e saíram-lhe ao encontro, e clamavam: Hosana! Bendito o Rei de Israel que vem em nome do Senhor.', 13, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E achou Jesus um jumentinho, e assentou-se sobre ele, como está escrito:', 14, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não temas, ó filha de Sião; eis que o teu Rei vem assentado sobre o filho de uma jumenta.', 15, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os seus discípulos, porém, não entenderam isto no princípio; mas, quando Jesus foi glorificado, então se lembraram de que isto estava escrito dele, e que isto lhe fizeram.', 16, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A multidão, pois, que estava com ele quando Lázaro foi chamado da sepultura, testificava que ele o ressuscitara dentre os mortos.', 17, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso a multidão lhe saiu ao encontro, porque tinham ouvido que ele fizera este sinal.', 18, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram, pois, os fariseus entre si: Vedes que nada aproveitais? Eis que toda a gente vai após ele.', 19, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, havia alguns gregos, entre os que tinham subido a adorar no dia da festa.', 20, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes, pois, dirigiram-se a Filipe, que era de Betsaida da Galiléia, e rogaram-lhe, dizendo: Senhor, queríamos ver a Jesus.', 21, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Filipe foi dizê-lo a André, e então André e Filipe o disseram a Jesus.', 22, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhes respondeu, dizendo: É chegada a hora em que o Filho do homem há de ser glorificado.', 23, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade, na verdade vos digo que, se o grão de trigo, caindo na terra, não morrer, fica ele só; mas se morrer, dá muito fruto.', 24, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem ama a sua vida perdê-la-á, e quem neste mundo odeia a sua vida, guardá-la-á para a vida eterna.', 25, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém me serve, siga-me, e onde eu estiver, ali estará também o meu servo. E, se alguém me servir, meu Pai o honrará.', 26, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora a minha alma está perturbada; e que direi eu? Pai, salva-me desta hora; mas para isto vim a esta hora.', 27, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pai, glorifica o teu nome. Então veio uma voz do céu que dizia: Já o tenho glorificado, e outra vez o glorificarei.', 28, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, a multidão que ali estava, e que a ouvira, dizia que havia sido um trovão. Outros diziam: Um anjo lhe falou.', 29, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu Jesus, e disse: Não veio esta voz por amor de mim, mas por amor de vós.', 30, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora é o juízo deste mundo; agora será expulso o príncipe deste mundo.', 31, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu, quando for levantado da terra, todos atrairei a mim.', 32, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia isto, significando de que morte havia de morrer.', 33, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhe a multidão: Nós temos ouvido da lei, que o Cristo permanece para sempre; e como dizes tu que convém que o Filho do homem seja levantado? Quem é esse Filho do homem?', 34, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes, pois, Jesus: A luz ainda está convosco por um pouco de tempo. Andai enquanto tendes luz, para que as trevas não vos apanhem; pois quem anda nas trevas não sabe para onde vai.', 35, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Enquanto tendes luz, crede na luz, para que sejais filhos da luz. Estas coisas disse Jesus e, retirando-se, escondeu-se deles.', 36, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ainda que tinha feito tantos sinais diante deles, não criam nele;', 37, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que se cumprisse a palavra do profeta Isaías, que diz: Senhor, quem creu na nossa pregação? E a quem foi revelado o braço do Senhor?', 38, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso não podiam crer, entào Isaías disse outra vez:', 39, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cegou-lhes os olhos, e endureceu-lhes o coração, A fim de que não vejam com os olhos, e compreendam no coração, E se convertam, E eu os cure.', 40, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isaías disse isto quando viu a sua glória e falou dele.', 41, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Apesar de tudo, até muitos dos principais creram nele; mas não o confessavam por causa dos fariseus, para não serem expulsos da sinagoga.', 42, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque amavam mais a glória dos homens do que a glória de Deus.', 43, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus clamou, e disse: Quem crê em mim, crê, não em mim, mas naquele que me enviou.', 44, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quem me vê a mim, vê aquele que me enviou.', 45, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou a luz que vim ao mundo, para que todo aquele que crê em mim não permaneça nas trevas.', 46, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se alguém ouvir as minhas palavras, e não crer, eu não o julgo; porque eu vim, não para julgar o mundo, mas para salvar o mundo.', 47, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem me rejeitar a mim, e não receber as minhas palavras, já tem quem o julgue; a palavra que tenho pregado, essa o há de julgar no último dia.', 48, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu não tenho falado de mim mesmo; mas o Pai, que me enviou, ele me deu mandamento sobre o que hei de dizer e sobre o que hei de falar.', 49, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sei que o seu mandamento é a vida eterna. Portanto, o que eu falo, falo-o como o Pai mo tem dito.', 50, 12, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disto partiu Jesus para o outro lado do mar da Galiléia, que é o de Tiberíades.', 1, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E grande multidão o seguia, porque via os sinais que operava sobre os enfermos.', 2, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus subiu ao monte, e assentou-se ali com os seus discípulos.', 3, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a páscoa, a festa dos judeus, estava próxima.', 4, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus, levantando os olhos, e vendo que uma grande multidão vinha ter com ele, disse a Filipe: Onde compraremos pão, para estes comerem?', 5, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas dizia isto para o experimentar; porque ele bem sabia o que havia de fazer.', 6, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Filipe respondeu-lhe: Duzentos dinheiros de pão não lhes bastarão, para que cada um deles tome um pouco.', 7, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um dos seus discípulos, André, irmão de Simão Pedro, disse-lhe:', 8, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Está aqui um rapaz que tem cinco pães de cevada e dois peixinhos; mas que é isto para tantos?', 9, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Jesus: Mandai assentar os homens. E havia muita relva naquele lugar. Assentaram-se, pois, os homens em número de quase cinco mil.', 10, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus tomou os pães e, havendo dado graças, repartiu-os pelos discípulos, e os discípulos pelos que estavam assentados; e igualmente também dos peixes, quanto eles queriam.', 11, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando estavam saciados, disse aos seus discípulos: Recolhei os pedaços que sobejaram, para que nada se perca.', 12, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Recolheram-nos, pois, e encheram doze alcofas de pedaços dos cinco pães de cevada, que sobejaram aos que haviam comido.', 13, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vendo, pois, aqueles homens o milagre que Jesus tinha feito, diziam: Este é verdadeiramente o profeta que devia vir ao mundo.', 14, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo, pois, Jesus que haviam de vir arrebatá-lo, para o fazerem rei, tornou a retirar-se, ele só, para o monte.', 15, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando veio a tarde, os seus discípulos desceram para o mar.', 16, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando no barco, atravessaram o mar em direção a Cafarnaum; e era já escuro, e ainda Jesus não tinha chegado ao pé deles.', 17, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o mar se levantou, porque um grande vento assoprava.', 18, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo navegado uns vinte e cinco ou trinta estádios, viram a Jesus, andando sobre o mar e aproximando-se do barco; e temeram.', 19, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele lhes disse: Sou eu, não temais.', 20, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então eles de boa mente o receberam no barco; e logo o barco chegou à terra para onde iam.', 21, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No dia seguinte, a multidão que estava do outro lado do mar, vendo que não havia ali mais do que um barquinho, a não ser aquele no qual os discípulos haviam entrado, e que Jesus não entrara com os seus discípulos naquele barquinho, mas que os seus discípulos tinham ido sozinhos', 22, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Contudo, outros barquinhos tinham chegado de Tiberíades, perto do lugar onde comeram o pão, havendo o Senhor dado graças).', 23, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vendo, pois, a multidão que Jesus não estava ali nem os seus discípulos, entraram eles também nos barcos, e foram a Cafarnaum, em busca de Jesus.', 24, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, achando-o no outro lado do mar, disseram-lhe: Rabi, quando chegaste aqui?', 25, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu-lhes e disse: Na verdade, na verdade vos digo que me buscais, não pelos sinais que vistes, mas porque comestes do pão e vos saciastes.', 26, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Trabalhai, não pela comida que perece, mas pela comida que permanece para a vida eterna, a qual o Filho do homem vos dará; porque a este o Pai, Deus, o selou.', 27, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois: Que faremos para executarmos as obras de Deus?', 28, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu, e disse-lhes: A obra de Deus é esta: Que creiais naquele que ele enviou.', 29, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois: Que sinal, pois, fazes tu, para que o vejamos, e creiamos em ti? Que operas tu?', 30, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nossos pais comeram o maná no deserto, como está escrito: Deu-lhes a comer o pão do céu.', 31, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes, pois, Jesus: Na verdade, na verdade vos digo: Moisés não vos deu o pão do céu; mas meu Pai vos dá o verdadeiro pão do céu.', 32, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o pão de Deus é aquele que desce do céu e dá vida ao mundo.', 33, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois: Senhor, dá-nos sempre desse pão.', 34, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhes disse: Eu sou o pão da vida; aquele que vem a mim não terá fome, e quem crê em mim nunca terá sede.', 35, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas já vos disse que também vós me vistes, e contudo não credes.', 36, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todo o que o Pai me dá virá a mim; e o que vem a mim de maneira nenhuma o lançarei fora.', 37, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu desci do céu, não para fazer a minha vontade, mas a vontade daquele que me enviou.', 38, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a vontade do Pai que me enviou é esta: Que nenhum de todos aqueles que me deu se perca, mas que o ressuscite no último dia.', 39, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto a vontade daquele que me enviou é esta: Que todo aquele que vê o Filho, e crê nele, tenha a vida eterna; e eu o ressuscitarei no último dia.', 40, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Murmuravam, pois, dele os judeus, porque dissera: Eu sou o pão que desceu do céu.', 41, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E diziam: Não é este Jesus, o filho de José, cujo pai e mãe nós conhecemos? Como, pois, diz ele: Desci do céu?', 42, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu, pois, Jesus, e disse-lhes: Não murmureis entre vós.', 43, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém pode vir a mim, se o Pai que me enviou o não trouxer; e eu o ressuscitarei no último dia.', 44, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Está escrito nos profetas: E serão todos ensinados por Deus. Portanto, todo aquele que do Pai ouviu e aprendeu vem a mim.', 45, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não que alguém visse ao Pai, a não ser aquele que é de Deus; este tem visto ao Pai.', 46, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade, na verdade vos digo que aquele que crê em mim tem a vida eterna.', 47, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou o pão da vida.', 48, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vossos pais comeram o maná no deserto, e morreram.', 49, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este é o pão que desce do céu, para que o que dele comer não morra.', 50, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou o pão vivo que desceu do céu; se alguém comer deste pão, viverá para sempre; e o pão que eu der é a minha carne, que eu darei pela vida do mundo.', 51, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disputavam, pois, os judeus entre si, dizendo: Como nos pode dar este a sua carne a comer?', 52, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, pois, lhes disse: Na verdade, na verdade vos digo que, se não comerdes a carne do Filho do homem, e não beberdes o seu sangue, não tereis vida em vós mesmos.', 53, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem come a minha carne e bebe o meu sangue tem a vida eterna, e eu o ressuscitarei no último dia.', 54, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a minha carne verdadeiramente é comida, e o meu sangue verdadeiramente é bebida.', 55, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem come a minha carne e bebe o meu sangue permanece em mim e eu nele.', 56, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim como o Pai, que vive, me enviou, e eu vivo pelo Pai, assim, quem de mim se alimenta, também viverá por mim.', 57, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este é o pão que desceu do céu; não é o caso de vossos pais, que comeram o maná e morreram; quem comer este pão viverá para sempre.', 58, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele disse estas coisas na sinagoga, ensinando em Cafarnaum.', 59, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Muitos, pois, dos seus discípulos, ouvindo isto, disseram: Duro é este discurso; quem o pode ouvir?', 60, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo, pois, Jesus em si mesmo que os seus discípulos murmuravam disto, disse-lhes: Isto escandaliza-vos?', 61, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que seria, pois, se vísseis subir o Filho do homem para onde primeiro estava?', 62, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O espírito é o que vivifica, a carne para nada aproveita; as palavras que eu vos digo são espírito e vida.', 63, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas há alguns de vós que não crêem. Porque bem sabia Jesus, desde o princípio, quem eram os que não criam, e quem era o que o havia de entregar.', 64, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia: Por isso eu vos disse que ninguém pode vir a mim, se por meu Pai não lhe for concedido.', 65, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde então muitos dos seus discípulos tornaram para trás, e já não andavam com ele.', 66, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então disse Jesus aos doze: Quereis vós também retirar-vos?', 67, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhe, pois, Simão Pedro: Senhor, para quem iremos nós? Tu tens as palavras da vida eterna.', 68, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nós temos crido e conhecido que tu és o Cristo, o Filho do Deus vivente.', 69, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhe Jesus: Não vos escolhi a vós os doze? e um de vós é um diabo.', 70, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E isto dizia ele de Judas Iscariotes, filho de Simão; porque este o havia de entregar, sendo um dos doze.', 71, 6, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade, na verdade vos digo que aquele que não entra pela porta no curral das ovelhas, mas sobe por outra parte, é ladrão e salteador.', 1, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele, porém, que entra pela porta é o pastor das ovelhas.', 2, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A este o porteiro abre, e as ovelhas ouvem a sua voz, e chama pelo nome às suas ovelhas, e as traz para fora.', 3, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando tira para fora as suas ovelhas, vai adiante delas, e as ovelhas o seguem, porque conhecem a sua voz.', 4, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas de modo nenhum seguirão o estranho, antes fugirão dele, porque não conhecem a voz dos estranhos.', 5, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus disse-lhes esta parábola; mas eles não entenderam o que era que lhes dizia.', 6, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tornou, pois, Jesus a dizer-lhes: Em verdade, em verdade vos digo que eu sou a porta das ovelhas.', 7, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos quantos vieram antes de mim são ladrões e salteadores; mas as ovelhas não os ouviram.', 8, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou a porta; se alguém entrar por mim, salvar-se-á, e entrará, e sairá, e achará pastagens.', 9, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O ladrão não vem senão a roubar, a matar, e a destruir; eu vim para que tenham vida, e a tenham com abundância.', 10, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou o bom Pastor; o bom Pastor dá a sua vida pelas ovelhas.', 11, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o mercenário, e o que não é pastor, de quem não são as ovelhas, vê vir o lobo, e deixa as ovelhas, e foge; e o lobo as arrebata e dispersa as ovelhas.', 12, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o mercenário foge, porque é mercenário, e não tem cuidado das ovelhas.', 13, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou o bom Pastor, e conheço as minhas ovelhas, e das minhas sou conhecido.', 14, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim como o Pai me conhece a mim, também eu conheço o Pai, e dou a minha vida pelas ovelhas.', 15, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ainda tenho outras ovelhas que não são deste aprisco; também me convém agregar estas, e elas ouvirão a minha voz, e haverá um rebanho e um Pastor.', 16, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isto o Pai me ama, porque dou a minha vida para tornar a tomá-la.', 17, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém ma tira de mim, mas eu de mim mesmo a dou; tenho poder para a dar, e poder para tornar a tomá-la. Este mandamento recebi de meu Pai.', 18, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tornou, pois, a haver divisão entre os judeus por causa destas palavras.', 19, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos deles diziam: Tem demônio, e está fora de si; por que o ouvis?', 20, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Diziam outros: Estas palavras não são de endemoninhado. Pode, porventura, um demônio abrir os olhos aos cegos?', 21, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E em Jerusalém havia a festa da dedicação, e era inverno.', 22, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus andava passeando no templo, no alpendre de Salomão.', 23, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Rodearam-no, pois, os judeus, e disseram-lhe: Até quando terás a nossa alma suspensa? Se tu és o Cristo, dize-no-lo abertamente.', 24, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhes Jesus: Já vo-lo tenho dito, e não o credes. As obras que eu faço, em nome de meu Pai, essas testificam de mim.', 25, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós não credes porque não sois das minhas ovelhas, como já vo-lo tenho dito.', 26, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As minhas ovelhas ouvem a minha voz, e eu conheço-as, e elas me seguem;', 27, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dou-lhes a vida eterna, e nunca hão de perecer, e ninguém as arrebatará da minha mão.', 28, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meu Pai, que mas deu, é maior do que todos; e ninguém pode arrebatá-las da mão de meu Pai.', 29, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu e o Pai somos um.', 30, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os judeus pegaram então outra vez em pedras para o apedrejar.', 31, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhes Jesus: Tenho-vos mostrado muitas obras boas procedentes de meu Pai; por qual destas obras me apedrejais?', 32, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os judeus responderam, dizendo-lhe: Não te apedrejamos por alguma obra boa, mas pela blasfêmia; porque, sendo tu homem, te fazes Deus a ti mesmo.', 33, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhes Jesus: Não está escrito na vossa lei: Eu disse: Sois deuses?', 34, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, se a lei chamou deuses àqueles a quem a palavra de Deus foi dirigida, e a Escritura não pode ser anulada,', 35, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('«quele a quem o Pai santificou, e enviou ao mundo, vós dizeis: Blasfemas, porque disse: Sou Filho de Deus?', 36, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se não faço as obras de meu Pai, não me acrediteis.', 37, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se as faço, e não credes em mim, crede nas obras; para que conheçais e acrediteis que o Pai está em mim e eu nele.', 38, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Procuravam, pois, prendê-lo outra vez, mas ele escapou-se de suas mãos,', 39, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E retirou-se outra vez para além do Jordão, para o lugar onde João tinha primeiramente batizado; e ali ficou.', 40, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos iam ter com ele, e diziam: Na verdade João não fez sinal algum, mas tudo quanto João disse deste era verdade.', 41, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos ali creram nele.', 42, 10, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ao terceiro dia, fizeram-se umas bodas em Caná da Galiléia; e estava ali a mãe de Jesus.', 1, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi também convidado Jesus e os seus discípulos para as bodas.', 2, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, faltando vinho, a mãe de Jesus lhe disse: Não têm vinho.', 3, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Mulher, que tenho eu contigo? Ainda não é chegada a minha hora.', 4, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sua mãe disse aos serventes: Fazei tudo quanto ele vos disser.', 5, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estavam ali postas seis talhas de pedra, para as purificações dos judeus, e em cada uma cabiam dois ou três almudes.', 6, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes Jesus: Enchei de água essas talhas. E encheram-nas até em cima.', 7, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Tirai agora, e levai ao mestre-sala. E levaram.', 8, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo que o mestre-sala provou a água feita vinho (não sabendo de onde viera, se bem que o sabiam os serventes que tinham tirado a água), chamou o mestre-sala ao esposo,', 9, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe: Todo o homem põe primeiro o vinho bom e, quando já têm bebido bem, então o inferior; mas tu guardaste até agora o bom vinho.', 10, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus principiou assim os seus sinais em Caná da Galiléia, e manifestou a sua glória; e os seus discípulos creram nele.', 11, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disto desceu a Cafarnaum, ele, e sua mãe, e seus irmãos, e seus discípulos; e ficaram ali não muitos dias.', 12, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava próxima a páscoa dos judeus, e Jesus subiu a Jerusalém.', 13, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E achou no templo os que vendiam bois, e ovelhas, e pombos, e os cambiadores assentados.', 14, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tendo feito um azorrague de cordéis, lançou todos fora do templo, também os bois e ovelhas; e espalhou o dinheiro dos cambiadores, e derribou as mesas;', 15, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse aos que vendiam pombos: Tirai daqui estes, e não façais da casa de meu Pai casa de venda.', 16, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus discípulos lembraram-se do que está escrito: O zelo da tua casa me devorou.', 17, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam, pois, os judeus, e disseram-lhe: Que sinal nos mostras para fazeres isto?', 18, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu, e disse-lhes: Derribai este templo, e em três dias o levantarei.', 19, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram, pois, os judeus: Em quarenta e seis anos foi edificado este templo, e tu o levantarás em três dias?', 20, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele falava do templo do seu corpo.', 21, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando, pois, ressuscitou dentre os mortos, os seus discípulos lembraram-se de que lhes dissera isto; e creram na Escritura, e na palavra que Jesus tinha dito.', 22, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando ele em Jerusalém pela páscoa, durante a festa, muitos, vendo os sinais que fazia, creram no seu nome.', 23, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o mesmo Jesus não confiava neles, porque a todos conhecia;', 24, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não necessitava de que alguém testificasse do homem, porque ele bem sabia o que havia no homem.', 25, 2, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, antes da festa da páscoa, sabendo Jesus que já era chegada a sua hora de passar deste mundo para o Pai, como havia amado os seus, que estavam no mundo, amou-os até o fim.', 1, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, acabada a ceia, tendo já o diabo posto no coração de Judas Iscariotes, filho de Simão, que o traísse,', 2, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, sabendo que o Pai tinha depositado nas suas mãos todas as coisas, e que havia saído de Deus e ia para Deus,', 3, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Levantou-se da ceia, tirou as vestes, e, tomando uma toalha, cingiu-se.', 4, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois deitou água numa bacia, e começou a lavar os pés aos discípulos, e a enxugar-lhos com a toalha com que estava cingido.', 5, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aproximou-se, pois, de Simão Pedro, que lhe disse: Senhor, tu lavas-me os pés a mim?', 6, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu Jesus, e disse-lhe: O que eu faço não o sabes tu agora, mas tu o saberás depois.', 7, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Pedro: Nunca me lavarás os pés. Respondeu-lhe Jesus: Se eu te não lavar, não tens parte comigo.', 8, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Simão Pedro: Senhor, não só os meus pés, mas também as mãos e a cabeça.', 9, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Aquele que está lavado não necessita de lavar senão os pés, pois no mais todo está limpo. Ora vós estais limpos, mas não todos.', 10, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque bem sabia ele quem o havia de trair; por isso disse: Nem todos estais limpos.', 11, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois que lhes lavou os pés, e tomou as suas vestes, e se assentou outra vez à mesa, disse-lhes: Entendeis o que vos tenho feito?', 12, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós me chamais Mestre e Senhor, e dizeis bem, porque eu o sou.', 13, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, se eu, Senhor e Mestre, vos lavei os pés, vós deveis também lavar os pés uns aos outros.', 14, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu vos dei o exemplo, para que, como eu vos fiz, façais vós também.', 15, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade, na verdade vos digo que não é o servo maior do que o seu senhor, nem o enviado maior do que aquele que o enviou.', 16, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se sabeis estas coisas, bem-aventurados sois se as fizerdes.', 17, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não falo de todos vós; eu bem sei os que tenho escolhido; mas para que se cumpra a Escritura: O que come o pão comigo, levantou contra mim o seu calcanhar.', 18, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde agora vo-lo digo, antes que aconteça, para que, quando acontecer, acrediteis que eu sou.', 19, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade, na verdade vos digo: Se alguém receber o que eu enviar, me recebe a mim, e quem me recebe a mim, recebe aquele que me enviou.', 20, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo Jesus dito isto, turbou-se em espírito, e afirmou, dizendo: Na verdade, na verdade vos digo que um de vós me há de trair.', 21, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então os discípulos olhavam uns para os outros, duvidando de quem ele falava.', 22, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, um de seus discípulos, aquele a quem Jesus amava, estava reclinado no seio de Jesus.', 23, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Simão Pedro fez sinal a este, para que perguntasse quem era aquele de quem ele falava.', 24, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, inclinando-se ele sobre o peito de Jesus, disse-lhe: Senhor, quem é?', 25, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu: É aquele a quem eu der o bocado molhado. E, molhando o bocado, o deu a Judas Iscariotes, filho de Simão.', 26, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, após o bocado, entrou nele Satanás. Disse, pois, Jesus: O que fazes, faze-o depressa.', 27, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nenhum dos que estavam assentados à mesa compreendeu a que propósito lhe dissera isto.', 28, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, como Judas tinha a bolsa, pensavam alguns que Jesus lhe tinha dito: Compra o que nos é necessário para a festa; ou que desse alguma coisa aos pobres.', 29, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo Judas tomado o bocado, saiu logo. E era já noite.', 30, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo ele, pois, saído, disse Jesus: Agora é glorificado o Filho do homem, e Deus é glorificado nele.', 31, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se Deus é glorificado nele, também Deus o glorificará em si mesmo, e logo o há de glorificar.', 32, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Filhinhos, ainda por um pouco estou convosco. Vós me buscareis, mas, como tenho dito aos judeus: Para onde eu vou não podeis vós ir; eu vo-lo digo também agora.', 33, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Um novo mandamento vos dou: Que vos ameis uns aos outros; como eu vos amei a vós, que também vós uns aos outros vos ameis.', 34, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto todos conhecerão que sois meus discípulos, se vos amardes uns aos outros.', 35, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Simão Pedro: Senhor, para onde vais? Jesus lhe respondeu: Para onde eu vou não podes agora seguir-me, mas depois me seguirás.', 36, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Pedro: Por que não posso seguir-te agora? Por ti darei a minha vida.', 37, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhe Jesus: Tu darás a tua vida por mim? Na verdade, na verdade te digo que não cantará o galo enquanto não me tiveres negado três vezes.', 38, 13, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O presbítero ao amado Gaio, a quem em verdade eu amo.', 1, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amado, desejo que te vá bem em todas as coisas, e que tenhas saúde, assim como bem vai a tua alma.', 2, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque muito me alegrei quando os irmãos vieram, e testificaram da tua verdade, como tu andas na verdade.', 3, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não tenho maior gozo do que este, o de ouvir que os meus filhos andam na verdade.', 4, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amado, procedes fielmente em tudo o que fazes para com os irmãos, e para com os estranhos,', 5, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que em presença da igreja testificaram do teu amor; aos quais, se conduzires como é digno para com Deus, bem farás;', 6, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque pelo seu Nome saíram, nada tomando dos gentios.', 7, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, aos tais devemos receber, para que sejamos cooperadores da verdade.', 8, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho escrito à igreja; mas Diótrefes, que procura ter entre eles o primado, não nos recebe.', 9, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, se eu for, trarei à memória as obras que ele faz, proferindo contra nós palavras maliciosas; e, não contente com isto, não recebe os irmãos, e impede os que querem recebê-los, e os lança fora da igreja.', 10, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amado, não sigas o mal, mas o bem. Quem faz o bem é de Deus; mas quem faz o mal não tem visto a Deus.', 11, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos dão testemunho de Demétrio, até a mesma verdade; e também nós testemunhamos; e vós bem sabeis que o nosso testemunho é verdadeiro.', 12, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tinha muito que escrever, mas não quero escrever-te com tinta e pena.', 13, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Espero, porém, ver-te brevemente, e falaremos face a face.', 14, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paz seja contigo. Os amigos te saúdam. Saúda os amigos por nome.', 15, 1, 25);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, foi para o Monte das Oliveiras.', 1, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pela manhã cedo tornou para o templo, e todo o povo vinha ter com ele, e, assentando-se, os ensinava.', 2, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os escribas e fariseus trouxeram-lhe uma mulher apanhada em adultério;', 3, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pondo-a no meio, disseram-lhe: Mestre, esta mulher foi apanhada, no próprio ato, adulterando.', 4, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E na lei nos mandou Moisés que as tais sejam apedrejadas. Tu, pois, que dizes?', 5, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto diziam eles, tentando-o, para que tivessem de que o acusar. Mas Jesus, inclinando-se, escrevia com o dedo na terra.', 6, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como insistissem, perguntando-lhe, endireitou-se, e disse-lhes: Aquele que de entre vós está sem pecado seja o primeiro que atire pedra contra ela.', 7, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tornando a inclinar-se, escrevia na terra.', 8, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando ouviram isto, redargüidos da consciência, saíram um a um, a começar pelos mais velhos até aos últimos; ficou só Jesus e a mulher que estava no meio.', 9, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, endireitando-se Jesus, e não vendo ninguém mais do que a mulher, disse-lhe: Mulher, onde estão aqueles teus acusadores? Ninguém te condenou?', 10, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ela disse: Ninguém, Senhor. E disse-lhe Jesus: Nem eu também te condeno; vai-te, e não peques mais.', 11, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Falou-lhes, pois, Jesus outra vez, dizendo: Eu sou a luz do mundo; quem me segue não andará em trevas, mas terá a luz da vida.', 12, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois, os fariseus: Tu testificas de ti mesmo; o teu testemunho não é verdadeiro.', 13, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu Jesus, e disse-lhes: Ainda que eu testifico de mim mesmo, o meu testemunho é verdadeiro, porque sei de onde vim, e para onde vou; mas vós não sabeis de onde venho, nem para onde vou.', 14, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós julgais segundo a carne; eu a ninguém julgo.', 15, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se na verdade julgo, o meu juízo é verdadeiro, porque não sou eu só, mas eu e o Pai que me enviou.', 16, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E na vossa lei está também escrito que o testemunho de dois homens é verdadeiro.', 17, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou o que testifico de mim mesmo, e de mim testifica também o Pai que me enviou.', 18, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois: Onde está teu Pai? Jesus respondeu: Não me conheceis a mim, nem a meu Pai; se vós me conhecêsseis a mim, também conheceríeis a meu Pai.', 19, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estas palavras disse Jesus no lugar do tesouro, ensinando no templo, e ninguém o prendeu, porque ainda não era chegada a sua hora.', 20, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes, pois, Jesus outra vez: Eu retiro-me, e buscar-me-eis, e morrereis no vosso pecado. Para onde eu vou, não podeis vós vir.', 21, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Diziam, pois, os judeus: Porventura quererá matar-se a si mesmo, pois diz: Para onde eu vou não podeis vir?', 22, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia-lhes: Vós sois de baixo, eu sou de cima; vós sois deste mundo, eu não sou deste mundo.', 23, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso vos disse que morrereis em vossos pecados, porque se não crerdes que eu sou, morrereis em vossos pecados.', 24, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois: Quem és tu? Jesus lhes disse: Isso mesmo que já desde o princípio vos disse.', 25, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Muito tenho que dizer e julgar de vós, mas aquele que me enviou é verdadeiro; e o que dele tenho ouvido, isso falo ao mundo.', 26, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não entenderam que ele lhes falava do Pai.', 27, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes, pois, Jesus: Quando levantardes o Filho do homem, então conhecereis que EU SOU, e que nada faço por mim mesmo; mas isto falo como meu Pai me ensinou.', 28, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele que me enviou está comigo. O Pai não me tem deixado só, porque eu faço sempre o que lhe agrada.', 29, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo ele estas coisas, muitos creram nele.', 30, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus dizia, pois, aos judeus que criam nele: Se vós permanecerdes na minha palavra, verdadeiramente sereis meus discípulos;', 31, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E conhecereis a verdade, e a verdade vos libertará.', 32, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam-lhe: Somos descendência de Abraão, e nunca servimos a ninguém; como dizes tu: Sereis livres?', 33, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhes Jesus: Em verdade, em verdade vos digo que todo aquele que comete pecado é servo do pecado.', 34, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora o servo não fica para sempre em casa; o Filho fica para sempre.', 35, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, pois, o Filho vos libertar, verdadeiramente sereis livres.', 36, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem sei que sois descendência de Abraão; contudo, procurais matar-me, porque a minha palavra não entra em vós.', 37, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu falo do que vi junto de meu Pai, e vós fazeis o que também vistes junto de vosso pai.', 38, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam, e disseram-lhe: Nosso pai é Abraão. Jesus disse-lhes: Se fôsseis filhos de Abraão, faríeis as obras de Abraão.', 39, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora procurais matar-me, a mim, homem que vos tem dito a verdade que de Deus tem ouvido; Abraão não fez isto.', 40, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós fazeis as obras de vosso pai. Disseram-lhe, pois: Nós não somos nascidos de fornicação; temos um Pai, que é Deus.', 41, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes, pois, Jesus: Se Deus fosse o vosso Pai, certamente me amaríeis, pois que eu saí, e vim de Deus; não vim de mim mesmo, mas ele me enviou.', 42, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por que não entendeis a minha linguagem? Por não poderdes ouvir a minha palavra.', 43, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós tendes por pai ao diabo, e quereis satisfazer os desejos de vosso pai. Ele foi homicida desde o princípio, e não se firmou na verdade, porque não há verdade nele. Quando ele profere mentira, fala do que lhe é próprio, porque é mentiroso, e pai da mentira.', 44, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, porque vos digo a verdade, não me credes.', 45, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem dentre vós me convence de pecado? E se vos digo a verdade, por que não credes?', 46, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem é de Deus escuta as palavras de Deus; por isso vós não as escutais, porque não sois de Deus.', 47, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam, pois, os judeus, e disseram-lhe: Não dizemos nós bem que és samaritano, e que tens demônio?', 48, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu: Eu não tenho demônio, antes honro a meu Pai, e vós me desonrais.', 49, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu não busco a minha glória; há quem a busque, e julgue.', 50, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade, em verdade vos digo que, se alguém guardar a minha palavra, nunca verá a morte.', 51, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois, os judeus: Agora conhecemos que tens demônio. Morreu Abraão e os profetas; e tu dizes: Se alguém guardar a minha palavra, nunca provará a morte.', 52, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('És tu maior do que o nosso pai Abraão, que morreu? E também os profetas morreram. Quem te fazes tu ser?', 53, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu: Se eu me glorifico a mim mesmo, a minha glória não é nada; quem me glorifica é meu Pai, o qual dizeis que é vosso Deus.', 54, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós não o conheceis, mas eu conheço-o. E, se disser que o não conheço, serei mentiroso como vós; mas conheço-o e guardo a sua palavra.', 55, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Abraão, vosso pai, exultou por ver o meu dia, e viu-o, e alegrou-se.', 56, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois, os judeus: Ainda não tens cinqüenta anos, e viste Abraão?', 57, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes Jesus: Em verdade, em verdade vos digo que antes que Abraão existisse, eu sou.', 58, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então pegaram em pedras para lhe atirarem; mas Jesus ocultou-se, e saiu do templo, passando pelo meio deles, e assim se retirou.', 59, 8, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meus irmãos, muitos de vós não sejam mestres, sabendo que receberemos mais duro juízo.', 1, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos tropeçamos em muitas coisas. Se alguém não tropeça em palavra, o tal é perfeito, e poderoso para também refrear todo o corpo.', 2, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, nós pomos freio nas bocas dos cavalos, para que nos obedeçam; e conseguimos dirigir todo o seu corpo.', 3, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede também as naus que, sendo tão grandes, e levadas de impetuosos ventos, se viram com um bem pequeno leme para onde quer a vontade daquele que as governa.', 4, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também a língua é um pequeno membro, e gloria-se de grandes coisas. Vede quão grande bosque um pequeno fogo incendeia.', 5, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A língua também é um fogo; como mundo de iniqüidade, a língua está posta entre os nossos membros, e contamina todo o corpo, e inflama o curso da natureza, e é inflamada pelo inferno.', 6, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque toda a natureza, tanto de bestas feras como de aves, tanto de répteis como de animais do mar, se amansa e foi domada pela natureza humana;', 7, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nenhum homem pode domar a língua. É um mal que não se pode refrear; está cheia de peçonha mortal.', 8, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Com ela bendizemos a Deus e Pai, e com ela amaldiçoamos os homens, feitos à semelhança de Deus.', 9, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De uma mesma boca procede bênção e maldição. Meus irmãos, não convém que isto se faça assim.', 10, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura deita alguma fonte de um mesmo manancial água doce e água amargosa?', 11, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meus irmãos, pode também a figueira produzir azeitonas, ou a videira figos? Assim tampouco pode uma fonte dar água salgada e doce.', 12, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem dentre vós é sábio e entendido? Mostre pelo seu bom trato as suas obras em mansidão de sabedoria.', 13, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se tendes amarga inveja, e sentimento faccioso em vosso coração, não vos glorieis, nem mintais contra a verdade.', 14, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Essa não é a sabedoria que vem do alto, mas é terrena, animal e diabólica.', 15, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque onde há inveja e espírito faccioso aí há perturbação e toda a obra perversa.', 16, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a sabedoria que do alto vem é, primeiramente pura, depois pacífica, moderada, tratável, cheia de misericórdia e de bons frutos, sem parcialidade, e sem hipocrisia.', 17, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o fruto da justiça semeia-se na paz, para os que exercitam a paz.', 18, 3, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois disto Jesus andava pela Galiléia, e já não queria andar pela Judéia, pois os judeus procuravam matá-lo.', 1, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava próxima a festa dos judeus, a dos tabernáculos.', 2, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois, seus irmãos: Sai daqui, e vai para a Judéia, para que também os teus discípulos vejam as obras que fazes.', 3, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não há ninguém que procure ser conhecido que faça coisa alguma em oculto. Se fazes estas coisas, manifesta-te ao mundo.', 4, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nem mesmo seus irmãos criam nele.', 5, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes, pois, Jesus: Ainda não é chegado o meu tempo, mas o vosso tempo sempre está pronto.', 6, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O mundo não vos pode odiar, mas ele me odeia a mim, porquanto dele testifico que as suas obras são más.', 7, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Subi vós a esta festa; eu não subo ainda a esta festa, porque ainda o meu tempo não está cumprido.', 8, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-lhes dito isto, ficou na Galiléia.', 9, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando seus irmãos já tinham subido à festa, então subiu ele também, não manifestamente, mas como em oculto.', 10, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, os judeus procuravam-no na festa, e diziam: Onde está ele?', 11, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia grande murmuração entre a multidão a respeito dele. Diziam alguns: Ele é bom. E outros diziam: Não, antes engana o povo.', 12, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia ninguém falava dele abertamente, por medo dos judeus.', 13, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, no meio da festa subiu Jesus ao templo, e ensinava.', 14, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os judeus maravilhavam-se, dizendo: Como sabe este letras, não as tendo aprendido?', 15, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus lhes respondeu, e disse: A minha doutrina não é minha, mas daquele que me enviou.', 16, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém quiser fazer a vontade dele, pela mesma doutrina conhecerá se ela é de Deus, ou se eu falo de mim mesmo.', 17, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem fala de si mesmo busca a sua própria glória; mas o que busca a glória daquele que o enviou, esse é verdadeiro, e não há nele injustiça.', 18, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos deu Moisés a lei? e nenhum de vós observa a lei. Por que procurais matar-me?', 19, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A multidão respondeu, e disse: Tens demônio; quem procura matar-te?', 20, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu Jesus, e disse-lhes: Fiz uma só obra, e todos vos maravilhais.', 21, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelo motivo de que Moisés vos deu a circuncisão (não que fosse de Moisés, mas dos pais), no sábado circuncidais um homem.', 22, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se o homem recebe a circuncisão no sábado, para que a lei de Moisés não seja quebrantada, indignais-vos contra mim, porque no sábado curei de todo um homem?', 23, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não julgueis segundo a aparência, mas julgai segundo a reta justiça.', 24, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então alguns dos de Jerusalém diziam: Não é este o que procuram matar?', 25, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ei-lo aí está falando abertamente, e nada lhe dizem. Porventura sabem verdadeiramente os príncipes que de fato este é o Cristo?', 26, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia bem sabemos de onde este é; mas, quando vier o Cristo, ninguém saberá de onde ele é.', 27, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Clamava, pois, Jesus no templo, ensinando, e dizendo: Vós conheceis-me, e sabeis de onde sou; e eu não vim de mim mesmo, mas aquele que me enviou é verdadeiro, o qual vós não conheceis.', 28, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eu conheço-o, porque dele sou e ele me enviou.', 29, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Procuravam, pois, prendê-lo, mas ninguém lançou mão dele, porque ainda não era chegada a sua hora.', 30, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos da multidão creram nele, e diziam: Quando o Cristo vier, fará ainda mais sinais do que os que este tem feito?', 31, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os fariseus ouviram que a multidão murmurava dele estas coisas; e os fariseus e os principais dos sacerdotes mandaram servidores para o prenderem.', 32, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes, pois, Jesus: Ainda um pouco de tempo estou convosco, e depois vou para aquele que me enviou.', 33, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós me buscareis, e não me achareis; e onde eu estou, vós não podeis vir.', 34, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram, pois, os judeus uns para os outros: Para onde irá este, que o não acharemos? Irá porventura para os dispersos entre os gregos, e ensinará os gregos?', 35, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que palavra é esta que disse: Buscar-me-eis, e não me achareis; e: Aonde eu estou vós não podeis ir?', 36, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no último dia, o grande dia da festa, Jesus pôs-se em pé, e clamou, dizendo: Se alguém tem sede, venha a mim, e beba.', 37, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem crê em mim, como diz a Escritura, rios de água viva correrão do seu ventre.', 38, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E isto disse ele do Espírito que haviam de receber os que nele cressem; porque o Espírito Santo ainda não fora dado, por ainda Jesus não ter sido glorificado.', 39, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então muitos da multidão, ouvindo esta palavra, diziam: Verdadeiramente este é o Profeta.', 40, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Outros diziam: Este é o Cristo; mas diziam outros: Vem, pois, o Cristo da Galiléia?', 41, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não diz a Escritura que o Cristo vem da descendência de Davi, e de Belém, da aldeia de onde era Davi?', 42, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim entre o povo havia dissensão por causa dele.', 43, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns deles queriam prendê-lo, mas ninguém lançou mão dele.', 44, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os servidores foram ter com os principais dos sacerdotes e fariseus; e eles lhes perguntaram: Por que não o trouxestes?', 45, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam os servidores: Nunca homem algum falou assim como este homem.', 46, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam-lhes, pois, os fariseus: Também vós fostes enganados?', 47, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Creu nele porventura algum dos principais ou dos fariseus?', 48, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas esta multidão, que não sabe a lei, é maldita.', 49, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nicodemos, que era um deles (o que de noite fora ter com Jesus), disse-lhes:', 50, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura condena a nossa lei um homem sem primeiro o ouvir e ter conhecimento do que faz?', 51, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam eles, e disseram-lhe: És tu também da Galiléia? Examina, e verás que da Galiléia nenhum profeta surgiu.', 52, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E cada um foi para sua casa.', 53, 7, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passando Jesus, viu um homem cego de nascença.', 1, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus discípulos lhe perguntaram, dizendo: Rabi, quem pecou, este ou seus pais, para que nascesse cego?', 2, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu: Nem ele pecou nem seus pais; mas foi assim para que se manifestem nele as obras de Deus.', 3, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Convém que eu faça as obras daquele que me enviou, enquanto é dia; a noite vem, quando ninguém pode trabalhar.', 4, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Enquanto estou no mundo, sou a luz do mundo.', 5, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo dito isto, cuspiu na terra, e com a saliva fez lodo, e untou com o lodo os olhos do cego.', 6, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe: Vai, lava-te no tanque de Siloé (que significa o Enviado). Foi, pois, e lavou-se, e voltou vendo.', 7, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então os vizinhos, e aqueles que dantes tinham visto que era cego, diziam: Não é este aquele que estava assentado e mendigava?', 8, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Uns diziam: É este. E outros: Parece-se com ele. Ele dizia: Sou eu.', 9, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Diziam-lhe, pois: Como se te abriram os olhos?', 10, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele respondeu, e disse: O homem, chamado Jesus, fez lodo, e untou-me os olhos, e disse-me: Vai ao tanque de Siloé, e lava-te. Então fui, e lavei-me, e vi.', 11, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois: Onde está ele? Respondeu: Não sei.', 12, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Levaram, pois, aos fariseus o que dantes era cego.', 13, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E era sábado quando Jesus fez o lodo e lhe abriu os olhos.', 14, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tornaram, pois, também os fariseus a perguntar-lhe como vira, e ele lhes disse: Pôs-me lodo sobre os olhos, lavei-me, e vejo.', 15, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então alguns dos fariseus diziam: Este homem não é de Deus, pois não guarda o sábado. Diziam outros: Como pode um homem pecador fazer tais sinais? E havia dissensão entre eles.', 16, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tornaram, pois, a dizer ao cego: Tu, que dizes daquele que te abriu os olhos? E ele respondeu: Que é profeta.', 17, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os judeus, porém, não creram que ele tivesse sido cego, e que agora visse, enquanto não chamaram os pais do que agora via.', 18, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntaram-lhes, dizendo: É este o vosso filho, que vós dizeis ter nascido cego? Como, pois, vê agora?', 19, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Seus pais lhes responderam, e disseram: Sabemos que este é o nosso filho, e que nasceu cego;', 20, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas como agora vê, não sabemos; ou quem lhe tenha aberto os olhos, não sabemos. Tem idade, perguntai-lho a ele mesmo; e ele falará por si mesmo.', 21, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Seus pais disseram isto, porque temiam os judeus. Porquanto já os judeus tinham resolvido que, se alguém confessasse ser ele o Cristo, fosse expulso da sinagoga.', 22, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso é que seus pais disseram: Tem idade, perguntai-lho a ele mesmo.', 23, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chamaram, pois, pela segunda vez o homem que tinha sido cego, e disseram-lhe: Dá glória a Deus; nós sabemos que esse homem é pecador.', 24, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu ele pois, e disse: Se é pecador, não sei; uma coisa sei, é que, havendo eu sido cego, agora vejo.', 25, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tornaram a dizer-lhe: Que te fez ele? Como te abriu os olhos?', 26, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhes: Já vo-lo disse, e não ouvistes; para que o quereis tornar a ouvir? Quereis vós porventura fazer-vos também seus discípulos?', 27, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o injuriaram, e disseram: Discípulo dele sejas tu; nós, porém, somos discípulos de Moisés.', 28, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nós bem sabemos que Deus falou a Moisés, mas este não sabemos de onde é.', 29, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O homem respondeu, e disse-lhes: Nisto, pois, está a maravilha, que vós não saibais de onde ele é, e contudo me abrisse os olhos.', 30, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, nós sabemos que Deus não ouve a pecadores; mas, se alguém é temente a Deus, e faz a sua vontade, a esse ouve.', 31, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde o princípio do mundo nunca se ouviu que alguém abrisse os olhos a um cego de nascença.', 32, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se este não fosse de Deus, nada poderia fazer.', 33, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam eles, e disseram-lhe: Tu és nascido todo em pecados, e nos ensinas a nós? E expulsaram-no.', 34, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus ouviu que o tinham expulsado e, encontrando-o, disse-lhe: Crês tu no Filho de Deus?', 35, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele respondeu, e disse: Quem é ele, Senhor, para que nele creia?', 36, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhe disse: Tu já o tens visto, e é aquele que fala contigo.', 37, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele disse: Creio, Senhor. E o adorou.', 38, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe Jesus: Eu vim a este mundo para juízo, a fim de que os que não vêem vejam, e os que vêem sejam cegos.', 39, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aqueles dos fariseus, que estavam com ele, ouvindo isto, disseram-lhe: Também nós somos cegos?', 40, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes Jesus: Se fôsseis cegos, não teríeis pecado; mas como agora dizeis: Vemos; por isso o vosso pecado permanece.', 41, 9, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estava, porém, enfermo um certo Lázaro, de betânia, aldeia de Maria e de sua irmã Marta.', 1, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Maria era aquela que tinha ungido o Senhor com ungüento, e lhe tinha enxugado os pés com os seus cabelos, cujo irmão Lázaro estava enfermo.', 2, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mandaram-lhe, pois, suas irmãs dizer: Senhor, eis que está enfermo aquele que tu amas.', 3, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, ouvindo isto, disse: Esta enfermidade não é para morte, mas para glória de Deus, para que o Filho de Deus seja glorificado por ela.', 4, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, Jesus amava a Marta, e a sua irmã, e a Lázaro.', 5, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvindo, pois, que estava enfermo, ficou ainda dois dias no lugar onde estava.', 6, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disto, disse aos seus discípulos: Vamos outra vez para a Judéia.', 7, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe os discípulos: Rabi, ainda agora os judeus procuravam apedrejar-te, e tornas para lá?', 8, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu: Não há doze horas no dia? Se alguém andar de dia, não tropeça, porque vê a luz deste mundo;', 9, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se andar de noite, tropeça, porque nele não há luz.', 10, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim falou; e depois disse-lhes: Lázaro, o nosso amigo, dorme, mas vou despertá-lo do sono.', 11, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram, pois, os seus discípulos: Senhor, se dorme, estará salvo.', 12, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus dizia isto da sua morte; eles, porém, cuidavam que falava do repouso do sono.', 13, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus disse-lhes claramente: Lázaro está morto;', 14, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E folgo, por amor de vós, de que eu lá não estivesse, para que acrediteis; mas vamos ter com ele.', 15, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse, pois, Tomé, chamado Dídimo, aos condiscípulos: Vamos nós também, para morrermos com ele.', 16, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chegando, pois, Jesus, achou que já havia quatro dias que estava na sepultura.', 17, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Ora Betânia distava de Jerusalém quase quinze estádios. )', 18, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos dos judeus tinham ido consolar a Marta e a Maria, acerca de seu irmão.', 19, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvindo, pois, Marta que Jesus vinha, saiu-lhe ao encontro; Maria, porém, ficou assentada em casa.', 20, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse, pois, Marta a Jesus: Senhor, se tu estivesses aqui, meu irmão não teria morrido.', 21, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas também agora sei que tudo quanto pedires a Deus, Deus to concederá.', 22, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Teu irmão há de ressuscitar.', 23, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Marta: Eu sei que há de ressuscitar na ressurreição do último dia.', 24, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Eu sou a ressurreição e a vida; quem crê em mim, ainda que esteja morto, viverá;', 25, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todo aquele que vive, e crê em mim, nunca morrerá. Crês tu isto?', 26, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe ela: Sim, Senhor, creio que tu és o Cristo, o Filho de Deus, que havia de vir ao mundo.', 27, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dito isto, partiu, e chamou em segredo a Maria, sua irmã, dizendo: O Mestre está cá, e chama-te.', 28, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ela, ouvindo isto, levantou-se logo, e foi ter com ele.', 29, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Pois, Jesus ainda não tinha chegado à aldeia, mas estava no lugar onde Marta o encontrara.)', 30, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vendo, pois, os judeus, que estavam com ela em casa e a consolavam, que Maria apressadamente se levantara e saíra, seguiram-na, dizendo: Vai ao sepulcro para chorar ali.', 31, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo, pois, Maria chegado aonde Jesus estava, e vendo-o, lançou-se aos seus pés, dizendo-lhe: Senhor, se tu estivesses aqui, meu irmão não teria morrido.', 32, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus pois, quando a viu chorar, e também chorando os judeus que com ela vinham, moveu-se muito em espírito, e perturbou-se.', 33, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse: Onde o pusestes? Disseram-lhe: Senhor, vem, e vê.', 34, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus chorou.', 35, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram, pois, os judeus: Vede como o amava.', 36, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns deles disseram: Não podia ele, que abriu os olhos ao cego, fazer também com que este não morresse?', 37, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, pois, movendo-se outra vez muito em si mesmo, veio ao sepulcro; e era uma caverna, e tinha uma pedra posta sobre ela.', 38, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse Jesus: Tirai a pedra. Marta, irmã do defunto, disse-lhe: Senhor, já cheira mal, porque é já de quatro dias.', 39, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Não te hei dito que, se creres, verás a glória de Deus?', 40, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tiraram, pois, a pedra de onde o defunto jazia. E Jesus, levantando os olhos para cima, disse: Pai, graças te dou, por me haveres ouvido.', 41, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu bem sei que sempre me ouves, mas eu disse isto por causa da multidão que está em redor, para que creiam que tu me enviaste.', 42, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo dito isto, clamou com grande voz: Lázaro, sai para fora.', 43, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o defunto saiu, tendo as mãos e os pés ligados com faixas, e o seu rosto envolto num lenço. Disse-lhes Jesus: Desligai-o, e deixai-o ir.', 44, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Muitos, pois, dentre os judeus que tinham vindo a Maria, e que tinham visto o que Jesus fizera, creram nele.', 45, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas alguns deles foram ter com os fariseus, e disseram-lhes o que Jesus tinha feito.', 46, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois os principais dos sacerdotes e os fariseus formaram conselho, e diziam: Que faremos? porquanto este homem faz muitos sinais.', 47, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se o deixamos assim, todos crerão nele, e virão os romanos, e tirar-nos-ão o nosso lugar e a nação.', 48, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Caifás, um deles que era sumo sacerdote naquele ano, lhes disse: Vós nada sabeis,', 49, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem considerais que nos convém que um homem morra pelo povo, e que não pereça toda a nação.', 50, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora ele não disse isto de si mesmo, mas, sendo o sumo sacerdote naquele ano, profetizou que Jesus devia morrer pela nação.', 51, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não somente pela nação, mas também para reunir em um corpo os filhos de Deus que andavam dispersos.', 52, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde aquele dia, pois, consultavam-se para o matarem.', 53, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, pois, já não andava manifestamente entre os judeus, mas retirou-se dali para a terra junto do deserto, para uma cidade chamada Efraim; e ali ficou com os seus discípulos.', 54, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava próxima a páscoa dos judeus, e muitos daquela região subiram a Jerusalém antes da páscoa para se purificarem.', 55, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Buscavam, pois, a Jesus, e diziam uns aos outros, estando no templo: Que vos parece? Não virá à festa?', 56, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, os principais dos sacerdotes e os fariseus tinham dado ordem para que, se alguém soubesse onde ele estava, o denunciasse, para o prenderem.', 57, 11, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus falou assim e, levantando seus olhos ao céu, e disse: Pai, é chegada a hora; glorifica a teu Filho, para que também o teu Filho te glorifique a ti;', 1, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim como lhe deste poder sobre toda a carne, para que dê a vida eterna a todos quantos lhe deste.', 2, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a vida eterna é esta: que te conheçam, a ti só, por único Deus verdadeiro, e a Jesus Cristo, a quem enviaste.', 3, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu glorifiquei-te na terra, tendo consumado a obra que me deste a fazer.', 4, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora glorifica-me tu, ó Pai, junto de ti mesmo, com aquela glória que tinha contigo antes que o mundo existisse.', 5, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Manifestei o teu nome aos homens que do mundo me deste; eram teus, e tu mos deste, e guardaram a tua palavra.', 6, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora já têm conhecido que tudo quanto me deste provém de ti;', 7, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque lhes dei as palavras que tu me deste; e eles as receberam, e têm verdadeiramente conhecido que saí de ti, e creram que me enviaste.', 8, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu rogo por eles; não rogo pelo mundo, mas por aqueles que me deste, porque são teus.', 9, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todas as minhas coisas são tuas, e as tuas coisas são minhas; e neles sou glorificado.', 10, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu já não estou mais no mundo, mas eles estão no mundo, e eu vou para ti. Pai santo, guarda em teu nome aqueles que me deste, para que sejam um, assim como nós.', 11, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estando eu com eles no mundo, guardava-os em teu nome. Tenho guardado aqueles que tu me deste, e nenhum deles se perdeu, senão o filho da perdição, para que a Escritura se cumprisse.', 12, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora vou para ti, e digo isto no mundo, para que tenham a minha alegria completa em si mesmos.', 13, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dei-lhes a tua palavra, e o mundo os odiou, porque não são do mundo, assim como eu não sou do mundo.', 14, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não peço que os tires do mundo, mas que os livres do mal.', 15, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não são do mundo, como eu do mundo não sou.', 16, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Santifica-os na tua verdade; a tua palavra é a verdade.', 17, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim como tu me enviaste ao mundo, também eu os enviei ao mundo.', 18, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por eles me santifico a mim mesmo, para que também eles sejam santificados na verdade.', 19, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não rogo somente por estes, mas também por aqueles que pela tua palavra hão de crer em mim;', 20, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que todos sejam um, como tu, ó Pai, o és em mim, e eu em ti; que também eles sejam um em nós, para que o mundo creia que tu me enviaste.', 21, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu dei-lhes a glória que a mim me deste, para que sejam um, como nós somos um.', 22, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu neles, e tu em mim, para que eles sejam perfeitos em unidade, e para que o mundo conheça que tu me enviaste a mim, e que os tens amado a eles como me tens amado a mim.', 23, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pai, aqueles que me deste quero que, onde eu estiver, também eles estejam comigo, para que vejam a minha glória que me deste; porque tu me amaste antes da fundação do mundo.', 24, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pai justo, o mundo não te conheceu; mas eu te conheci, e estes conheceram que tu me enviaste a mim.', 25, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu lhes fiz conhecer o teu nome, e lho farei conhecer mais, para que o amor com que me tens amado esteja neles, e eu neles esteja.', 26, 17, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não se turbe o vosso coração; credes em Deus, crede também em mim.', 1, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na casa de meu Pai há muitas moradas; se não fosse assim, eu vo-lo teria dito. Vou preparar-vos lugar.', 2, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando eu for, e vos preparar lugar, virei outra vez, e vos levarei para mim mesmo, para que onde eu estiver estejais vós também.', 3, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mesmo vós sabeis para onde vou, e conheceis o caminho.', 4, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Tomé: Senhor, nós não sabemos para onde vais; e como podemos saber o caminho?', 5, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Eu sou o caminho, e a verdade e a vida; ninguém vem ao Pai, senão por mim.', 6, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se vós me conhecêsseis a mim, também conheceríeis a meu Pai; e já desde agora o conheceis, e o tendes visto.', 7, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Filipe: Senhor, mostra-nos o Pai, o que nos basta.', 8, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Estou há tanto tempo convosco, e não me tendes conhecido, Filipe? Quem me vê a mim vê o Pai; e como dizes tu: Mostra-nos o Pai?', 9, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não crês tu que eu estou no Pai, e que o Pai está em mim? As palavras que eu vos digo não as digo de mim mesmo, mas o Pai, que está em mim, é quem faz as obras.', 10, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Crede-me que estou no Pai, e o Pai em mim; crede-me, ao menos, por causa das mesmas obras.', 11, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade, na verdade vos digo que aquele que crê em mim também fará as obras que eu faço, e as fará maiores do que estas, porque eu vou para meu Pai.', 12, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tudo quanto pedirdes em meu nome eu o farei, para que o Pai seja glorificado no Filho.', 13, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se pedirdes alguma coisa em meu nome, eu o farei.', 14, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se me amais, guardai os meus mandamentos.', 15, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu rogarei ao Pai, e ele vos dará outro Consolador, para que fique convosco para sempre;', 16, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Espírito de verdade, que o mundo não pode receber, porque não o vê nem o conhece; mas vós o conheceis, porque habita convosco, e estará em vós.', 17, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos deixarei órfãos; voltarei para vós.', 18, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ainda um pouco, e o mundo não me verá mais, mas vós me vereis; porque eu vivo, e vós vivereis.', 19, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Naquele dia conhecereis que estou em meu Pai, e vós em mim, e eu em vós.', 20, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que tem os meus mandamentos e os guarda esse é o que me ama; e aquele que me ama será amado de meu Pai, e eu o amarei, e me manifestarei a ele.', 21, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Judas (não o Iscariotes): Senhor, de onde vem que te hás de manifestar a nós, e não ao mundo?', 22, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu, e disse-lhe: Se alguém me ama, guardará a minha palavra, e meu Pai o amará, e viremos para ele, e faremos nele morada.', 23, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem não me ama não guarda as minhas palavras; ora, a palavra que ouvistes não é minha, mas do Pai que me enviou.', 24, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho-vos dito isto, estando convosco.', 25, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas aquele Consolador, o Espírito Santo, que o Pai enviará em meu nome, esse vos ensinará todas as coisas, e vos fará lembrar de tudo quanto vos tenho dito.', 26, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deixo-vos a paz, a minha paz vos dou; não vo-la dou como o mundo a dá. Não se turbe o vosso coração, nem se atemorize.', 27, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvistes que eu vos disse: Vou, e venho para vós. Se me amásseis, certamente exultaríeis porque eu disse: Vou para o Pai; porque meu Pai é maior do que eu.', 28, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu vo-lo disse agora antes que aconteça, para que, quando acontecer, vós acrediteis.', 29, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Já não falarei muito convosco, porque se aproxima o príncipe deste mundo, e nada tem em mim;', 30, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas é para que o mundo saiba que eu amo o Pai, e que faço como o Pai me mandou. Levantai-vos, vamo-nos daqui.', 31, 14, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho-vos dito estas coisas para que vos não escandalizeis.', 1, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Expulsar-vos-ão das sinagogas; vem mesmo a hora em que qualquer que vos matar cuidará fazer um serviço a Deus.', 2, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E isto vos farão, porque não conheceram ao Pai nem a mim.', 3, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas tenho-vos dito isto, a fim de que, quando chegar aquela hora, vos lembreis de que já vo-lo tinha dito. E eu não vos disse isto desde o princípio, porque estava convosco.', 4, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora vou para aquele que me enviou; e nenhum de vós me pergunta: Para onde vais?', 5, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, porque isto vos tenho dito, o vosso coração se encheu de tristeza.', 6, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia digo-vos a verdade, que vos convém que eu vá; porque, se eu não for, o Consolador não virá a vós; mas, quando eu for, vo-lo enviarei.', 7, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando ele vier, convencerá o mundo do pecado, e da justiça e do juízo.', 8, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Do pecado, porque não crêem em mim;', 9, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da justiça, porque vou para meu Pai, e não me vereis mais;', 10, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E do juízo, porque já o príncipe deste mundo está julgado.', 11, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ainda tenho muito que vos dizer, mas vós não o podeis suportar agora.', 12, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando vier aquele Espírito de verdade, ele vos guiará em toda a verdade; porque não falará de si mesmo, mas dirá tudo o que tiver ouvido, e vos anunciará o que há de vir.', 13, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele me glorificará, porque há de receber do que é meu, e vo-lo há de anunciar.', 14, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tudo quanto o Pai tem é meu; por isso vos disse que há de receber do que é meu e vo-lo há de anunciar.', 15, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Um pouco, e não me vereis; e outra vez um pouco, e ver-me-eis; porquanto vou para o Pai.', 16, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então alguns dos seus discípulos disseram uns aos outros: Que é isto que nos diz? Um pouco, e não me vereis; e outra vez um pouco, e ver-me-eis; e: Porquanto vou para o Pai?', 17, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Diziam, pois: Que quer dizer isto: Um pouco? Não sabemos o que diz.', 18, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conheceu, pois, Jesus que o queriam interrogar, e disse-lhes: Indagais entre vós acerca disto que disse: Um pouco, e não me vereis, e outra vez um pouco, e ver-me-eis?', 19, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade, na verdade vos digo que vós chorareis e vos lamentareis, e o mundo se alegrará, e vós estareis tristes, mas a vossa tristeza se converterá em alegria.', 20, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A mulher, quando está para dar à luz, sente tristeza, porque é chegada a sua hora; mas, depois de ter dado à luz a criança, já não se lembra da aflição, pelo prazer de haver nascido um homem no mundo.', 21, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também vós agora, na verdade, tendes tristeza; mas outra vez vos verei, e o vosso coração se alegrará, e a vossa alegria ninguém vo-la tirará.', 22, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E naquele dia nada me perguntareis. Na verdade, na verdade vos digo que tudo quanto pedirdes a meu Pai, em meu nome, ele vo-lo há de dar.', 23, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Até agora nada pedistes em meu nome; pedi, e recebereis, para que o vosso gozo se cumpra.', 24, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-vos isto por parábolas; chega, porém, a hora em que não vos falarei mais por parábolas, mas abertamente vos falarei acerca do Pai.', 25, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Naquele dia pedireis em meu nome, e não vos digo que eu rogarei por vós ao Pai;', 26, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois o mesmo Pai vos ama, visto como vós me amastes, e crestes que saí de Deus.', 27, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saí do Pai, e vim ao mundo; outra vez deixo o mundo, e vou para o Pai.', 28, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe os seus discípulos: Eis que agora falas abertamente, e não dizes parábola alguma.', 29, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora conhecemos que sabes tudo, e não precisas de que alguém te interrogue. Por isso cremos que saíste de Deus.', 30, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhes Jesus: Credes agora?', 31, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que chega a hora, e já se aproxima, em que vós sereis dispersos cada um para sua parte, e me deixareis só; mas não estou só, porque o Pai está comigo.', 32, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho-vos dito isto, para que em mim tenhais paz; no mundo tereis aflições, mas tende bom ânimo, eu venci o mundo.', 33, 16, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo Jesus dito isto, saiu com os seus discípulos para além do ribeiro de Cedrom, onde havia um horto, no qual ele entrou e seus discípulos.', 1, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Judas, que o traía, também conhecia aquele lugar, porque Jesus muitas vezes se ajuntava ali com os seus discípulos.', 2, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo, pois, Judas recebido a coorte e oficiais dos principais sacerdotes e fariseus, veio para ali com lanternas, e archotes e armas.', 3, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo, pois, Jesus todas as coisas que sobre ele haviam de vir, adiantou-se, e disse-lhes: A quem buscais?', 4, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam-lhe: A Jesus Nazareno. Disse-lhes Jesus: Sou eu. E Judas, que o traía, estava com eles.', 5, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando, pois, lhes disse: Sou eu, recuaram, e caíram por terra.', 6, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tornou-lhes, pois, a perguntar: A quem buscais? E eles disseram: A Jesus Nazareno.', 7, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus respondeu: Já vos disse que sou eu; se, pois, me buscais a mim, deixai ir estes;', 8, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que se cumprisse a palavra que tinha dito: Dos que me deste nenhum deles perdi.', 9, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Simão Pedro, que tinha espada, desembainhou-a, e feriu o servo do sumo sacerdote, cortando-lhe a orelha direita. E o nome do servo era Malco.', 10, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus disse a Pedro: Põe a tua espada na bainha; não beberei eu o cálice que o Pai me deu?', 11, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então a coorte, e o tribuno, e os servos dos judeus prenderam a Jesus e o maniataram.', 12, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E conduziram-no primeiramente a Anás, por ser sogro de Caifás, que era o sumo sacerdote daquele ano.', 13, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, Caifás era quem tinha aconselhado aos judeus que convinha que um homem morresse pelo povo.', 14, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Simão Pedro e outro discípulo seguiam a Jesus. E este discípulo era conhecido do sumo sacerdote, e entrou com Jesus na sala do sumo sacerdote.', 15, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro estava da parte de fora, à porta. Saiu então o outro discípulo que era conhecido do sumo sacerdote, e falou à porteira, levando Pedro para dentro.', 16, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então a porteira disse a Pedro: Não és tu também dos discípulos deste homem? Disse ele: Não sou.', 17, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, estavam ali os servos e os servidores, que tinham feito brasas, e se aquentavam, porque fazia frio; e com eles estava Pedro, aquentando-se também.', 18, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o sumo sacerdote interrogou Jesus acerca dos seus discípulos e da sua doutrina.', 19, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus lhe respondeu: Eu falei abertamente ao mundo; eu sempre ensinei na sinagoga e no templo, onde os judeus sempre se ajuntam, e nada disse em oculto.', 20, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que me perguntas a mim? Pergunta aos que ouviram o que é que lhes ensinei; eis que eles sabem o que eu lhes tenho dito.', 21, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo dito isto, um dos servidores que ali estavam, deu uma bofetada em Jesus, dizendo: Assim respondes ao sumo sacerdote?', 22, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhe Jesus: Se falei mal, dá testemunho do mal; e, se bem, por que me feres?', 23, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Anás mandou-o, maniatado, ao sumo sacerdote Caifás.', 24, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Simão Pedro estava ali, e aquentava-se. Disseram-lhe, pois: Não és também tu um dos seus discípulos? Ele negou, e disse: Não sou.', 25, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um dos servos do sumo sacerdote, parente daquele a quem Pedro cortara a orelha, disse: Não te vi eu no horto com ele?', 26, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro negou outra vez, e logo o galo cantou.', 27, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois levaram Jesus da casa de Caifás para a audiência. E era pela manhã cedo. E não entraram na audiência, para não se contaminarem, mas poderem comer a páscoa.', 28, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pilatos saiu fora e disse-lhes: Que acusação trazeis contra este homem?', 29, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam, e disseram-lhe: Se este não fosse malfeitor, não to entregaríamos.', 30, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes, pois, Pilatos: Levai-o vós, e julgai-o segundo a vossa lei. Disseram-lhe então os judeus: A nós não nos é lícito matar pessoa alguma.', 31, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Para que se cumprisse a palavra que Jesus tinha dito, significando de que morte havia de morrer).', 32, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tornou, pois, a entrar Pilatos na audiência, e chamou a Jesus, e disse-lhe: Tu és o Rei dos Judeus?', 33, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhe Jesus: Tu dizes isso de ti mesmo, ou disseram-to outros de mim?', 34, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pilatos respondeu: Porventura sou eu judeu? A tua nação e os principais dos sacerdotes entregaram-te a mim. Que fizeste?', 35, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu Jesus: O meu reino não é deste mundo; se o meu reino fosse deste mundo, pelejariam os meus servos, para que eu não fosse entregue aos judeus; mas agora o meu reino não é daqui.', 36, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe, pois, Pilatos: Logo tu és rei? Jesus respondeu: Tu dizes que eu sou rei. Eu para isso nasci, e para isso vim ao mundo, a fim de dar testemunho da verdade. Todo aquele que é da verdade ouve a minha voz.', 37, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Pilatos: Que é a verdade? E, dizendo isto, tornou a ir ter com os judeus, e disse-lhes: Não acho nele crime algum.', 38, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós tendes por costume que eu vos solte alguém pela páscoa. Quereis, pois, que vos solte o Rei dos Judeus?', 39, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então todos tornaram a clamar, dizendo: Este não, mas Barrabás. E Barrabás era um salteador.', 40, 18, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disto manifestou-se Jesus outra vez aos discípulos junto do mar de Tiberíades; e manifestou-se assim:', 1, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estavam juntos Simão Pedro, e Tomé, chamado Dídimo, e Natanael, que era de Caná da Galiléia, os filhos de Zebedeu, e outros dois dos seus discípulos.', 2, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes Simão Pedro: Vou pescar. Dizem-lhe eles: Também nós vamos contigo. Foram, e subiram logo para o barco, e naquela noite nada apanharam.', 3, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo já manhã, Jesus se apresentou na praia, mas os discípulos não conheceram que era Jesus.', 4, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes, pois, Jesus: Filhos, tendes alguma coisa de comer? Responderam-lhe: Não.', 5, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Lançai a rede para o lado direito do barco, e achareis. Lançaram-na, pois, e já não a podiam tirar, pela multidão dos peixes.', 6, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então aquele discípulo, a quem Jesus amava, disse a Pedro: É o Senhor. E, quando Simão Pedro ouviu que era o Senhor, cingiu-se com a túnica (porque estava nu) e lançou-se ao mar.', 7, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os outros discípulos foram com o barco (porque não estavam distantes da terra senão quase duzentos côvados), levando a rede cheia de peixes.', 8, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Logo que desceram para terra, viram ali brasas, e um peixe posto em cima, e pão.', 9, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes Jesus: Trazei dos peixes que agora apanhastes.', 10, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Simão Pedro subiu e puxou a rede para terra, cheia de cento e cinqüenta e três grandes peixes e, sendo tantos, não se rompeu a rede.', 11, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes Jesus: Vinde, comei. E nenhum dos discípulos ousava perguntar-lhe: Quem és tu? sabendo que era o Senhor.', 12, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chegou, pois, Jesus, e tomou o pão, e deu-lhes e, semelhantemente o peixe.', 13, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E já era a terceira vez que Jesus se manifestava aos seus discípulos, depois de ter ressuscitado dentre os mortos.', 14, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois de terem jantado, disse Jesus a Simão Pedro: Simão, filho de Jonas, amas-me mais do que estes? E ele respondeu: Sim, Senhor, tu sabes que te amo. Disse-lhe: Apascenta os meus cordeiros.', 15, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tornou a dizer-lhe segunda vez: Simão, filho de Jonas, amas-me? Disse-lhe: Sim, Senhor, tu sabes que te amo. Disse-lhe: Apascenta as minhas ovelhas.', 16, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe terceira vez: Simão, filho de Jonas, amas-me? Simão entristeceu-se por lhe ter dito terceira vez: Amas-me? E disse-lhe: Senhor, tu sabes tudo; tu sabes que eu te amo. Jesus disse-lhe: Apascenta as minhas ovelhas.', 17, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade, na verdade te digo que, quando eras mais moço, te cingias a ti mesmo, e andavas por onde querias; mas, quando já fores velho, estenderás as tuas mãos, e outro te cingirá, e te levará para onde tu não queiras.', 18, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse isto, significando com que morte havia ele de glorificar a Deus. E, dito isto, disse-lhe: Segue-me.', 19, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro, voltando-se, viu que o seguia aquele discípulo a quem Jesus amava, e que na ceia se recostara também sobre o seu peito, e que dissera: Senhor, quem é que te há de trair?', 20, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vendo Pedro a este, disse a Jesus: Senhor, e deste que será?', 21, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Se eu quero que ele fique até que eu venha, que te importa a ti? Segue-me tu.', 22, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Divulgou-se, pois, entre os irmãos este dito, que aquele discípulo não havia de morrer. Jesus, porém, não lhe disse que não morreria, mas: Se eu quero que ele fique até que eu venha, que te importa a ti?', 23, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este é o discípulo que testifica destas coisas e as escreveu; e sabemos que o seu testemunho é verdadeiro.', 24, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Há, porém, ainda muitas outras coisas que Jesus fez; e se cada uma das quais fosse escrita, cuido que nem ainda o mundo todo poderia conter os livros que se escrevessem. Amém.', 25, 21, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pilatos, pois, tomou então a Jesus, e o açoitou.', 1, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os soldados, tecendo uma coroa de espinhos, lha puseram sobre a cabeça, e lhe vestiram roupa de púrpura.', 2, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E diziam: Salve, Rei dos Judeus. E davam-lhe bofetadas.', 3, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pilatos saiu outra vez fora, e disse-lhes: Eis aqui vo-lo trago fora, para que saibais que não acho nele crime algum.', 4, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saiu, pois, Jesus fora, levando a coroa de espinhos e roupa de púrpura. E disse-lhes Pilatos: Eis aqui o homem.', 5, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vendo-o, pois, os principais dos sacerdotes e os servos, clamaram, dizendo: Crucifica-o, crucifica-o. Disse-lhes Pilatos: Tomai-o vós, e crucificai-o; porque eu nenhum crime acho nele.', 6, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Responderam-lhe os judeus: Nós temos uma lei e, segundo a nossa lei, deve morrer, porque se fez Filho de Deus.', 7, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pilatos, quando ouviu esta palavra, mais atemorizado ficou.', 8, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E entrou outra vez na audiência, e disse a Jesus: De onde és tu? Mas Jesus não lhe deu resposta.', 9, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe, pois, Pilatos: Não me falas a mim? Não sabes tu que tenho poder para te crucificar e tenho poder para te soltar?', 10, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu Jesus: Nenhum poder terias contra mim, se de cima não te fosse dado; mas aquele que me entregou a ti maior pecado tem.', 11, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde então Pilatos procurava soltá-lo; mas os judeus clamavam, dizendo: Se soltas este, não és amigo de César; qualquer que se faz rei é contra César.', 12, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvindo, pois, Pilatos este dito, levou Jesus para fora, e assentou-se no tribunal, no lugar chamado Litóstrotos, e em hebraico Gabatá.', 13, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E era a preparação da páscoa, e quase à hora sexta; e disse aos judeus: Eis aqui o vosso Rei.', 14, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eles bradaram: Tira, tira, crucifica-o. Disse-lhes Pilatos: Hei de crucificar o vosso Rei? Responderam os principais dos sacerdotes: Não temos rei, senão César.', 15, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, consequentemente entregou-lho, para que fosse crucificado. E tomaram a Jesus, e o levaram.', 16, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levando ele às costas a sua cruz, saiu para o lugar chamado Caveira, que em hebraico se chama Gólgota,', 17, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde o crucificaram, e com ele outros dois, um de cada lado, e Jesus no meio.', 18, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pilatos escreveu também um título, e pô-lo em cima da cruz; e nele estava escrito: JESUS NAZARENO, O REI DOS JUDEUS.', 19, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos dos judeus leram este título; porque o lugar onde Jesus estava crucificado era próximo da cidade; e estava escrito em hebraico, grego e latim.', 20, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Diziam, pois, os principais sacerdotes dos judeus a Pilatos: Não escrevas, O Rei dos Judeus, mas que ele disse: Sou o Rei dos Judeus.', 21, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu Pilatos: O que escrevi, escrevi.', 22, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo, pois, os soldados crucificado a Jesus, tomaram as suas vestes, e fizeram quatro partes, para cada soldado uma parte; e também a túnica. A túnica, porém, tecida toda de alto a baixo, não tinha costura.', 23, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram, pois, uns aos outros: Não a rasguemos, mas lancemos sortes sobre ela, para ver de quem será. Para que se cumprisse a Escritura que diz: Repartiram entre si as minhas vestes, E sobre a minha vestidura lançaram sortes. Os soldados, pois, fizeram estas coisas.', 24, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E junto à cruz de Jesus estava sua mãe, e a irmã de sua mãe, Maria mulher de Clopas, e Maria Madalena.', 25, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora Jesus, vendo ali sua mãe, e que o discípulo a quem ele amava estava presente, disse a sua mãe: Mulher, eis aí o teu filho.', 26, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disse ao discípulo: Eis aí tua mãe. E desde aquela hora o discípulo a recebeu em sua casa.', 27, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois, sabendo Jesus que já todas as coisas estavam terminadas, para que a Escritura se cumprisse, disse: Tenho sede.', 28, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estava, pois, ali um vaso cheio de vinagre. E encheram de vinagre uma esponja, e, pondo-a num hissopo, lha chegaram à boca.', 29, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando Jesus tomou o vinagre, disse: Está consumado. E, inclinando a cabeça, entregou o espírito.', 30, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os judeus, pois, para que no sábado não ficassem os corpos na cruz, visto como era a preparação (pois era grande o dia de sábado), rogaram a Pilatos que se lhes quebrassem as pernas, e fossem tirados.', 31, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Foram, pois, os soldados, e, na verdade, quebraram as pernas ao primeiro, e ao outro que como ele fora crucificado;', 32, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, vindo a Jesus, e vendo-o já morto, não lhe quebraram as pernas.', 33, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Contudo um dos soldados lhe furou o lado com uma lança, e logo saiu sangue e água.', 34, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele que o viu testificou, e o seu testemunho é verdadeiro; e sabe que é verdade o que diz, para que também vós o creiais.', 35, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque isto aconteceu para que se cumprisse a Escritura, que diz: Nenhum dos seus ossos será quebrado.', 36, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra vez diz a Escritura: Verão aquele que traspassaram.', 37, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disto, José de Arimatéia (o que era discípulo de Jesus, mas oculto, por medo dos judeus) rogou a Pilatos que lhe permitisse tirar o corpo de Jesus. E Pilatos lho permitiu. Então foi e tirou o corpo de Jesus.', 38, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi também Nicodemos (aquele que anteriormente se dirigira de noite a Jesus), levando quase cem arráteis de um composto de mirra e aloés.', 39, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tomaram, pois, o corpo de Jesus e o envolveram em lençóis com as especiarias, como os judeus costumam fazer, na preparação para o sepulcro.', 40, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia um horto naquele lugar onde fora crucificado, e no horto um sepulcro novo, em que ainda ninguém havia sido posto.', 41, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ali, pois (por causa da preparação dos judeus, e por estar perto aquele sepulcro), puseram a Jesus.', 42, 19, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no primeiro dia da semana, Maria Madalena foi ao sepulcro de madrugada, sendo ainda escuro, e viu a pedra tirada do sepulcro.', 1, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Correu, pois, e foi a Simão Pedro, e ao outro discípulo, a quem Jesus amava, e disse-lhes: Levaram o Senhor do sepulcro, e não sabemos onde o puseram.', 2, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pedro saiu com o outro discípulo, e foram ao sepulcro.', 3, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os dois corriam juntos, mas o outro discípulo correu mais apressadamente do que Pedro, e chegou primeiro ao sepulcro.', 4, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, abaixando-se, viu no chão os lençóis; todavia não entrou.', 5, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chegou, pois, Simão Pedro, que o seguia, e entrou no sepulcro, e viu no chão os lençóis,', 6, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que o lenço, que tinha estado sobre a sua cabeça, não estava com os lençóis, mas enrolado num lugar à parte.', 7, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então entrou também o outro discípulo, que chegara primeiro ao sepulcro, e viu, e creu.', 8, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ainda não sabiam a Escritura, que era necessário que ressuscitasse dentre os mortos.', 9, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tornaram, pois, os discípulos para casa.', 10, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Maria estava chorando fora, junto ao sepulcro. Estando ela, pois, chorando, abaixou-se para o sepulcro.', 11, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E viu dois anjos vestidos de branco, assentados onde jazera o corpo de Jesus, um à cabeceira e outro aos pés.', 12, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disseram-lhe eles: Mulher, por que choras? Ela lhes disse: Porque levaram o meu Senhor, e não sei onde o puseram.', 13, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo dito isto, voltou-se para trás, e viu Jesus em pé, mas não sabia que era Jesus.', 14, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Mulher, por que choras? Quem buscas? Ela, cuidando que era o hortelão, disse-lhe: Senhor, se tu o levaste, dize-me onde o puseste, e eu o levarei.', 15, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Maria! Ela, voltando-se, disse-lhe: Raboni, que quer dizer: Mestre.', 16, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Não me detenhas, porque ainda não subi para meu Pai, mas vai para meus irmãos, e dize-lhes que eu subo para meu Pai e vosso Pai, meu Deus e vosso Deus.', 17, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Maria Madalena foi e anunciou aos discípulos que vira o Senhor, e que ele lhe dissera isto.', 18, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chegada, pois, a tarde daquele dia, o primeiro da semana, e cerradas as portas onde os discípulos, com medo dos judeus, se tinham ajuntado, chegou Jesus, e pôs-se no meio, e disse-lhes: Paz seja convosco.', 19, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dizendo isto, mostrou-lhes as suas mãos e o lado. De sorte que os discípulos se alegraram, vendo o Senhor.', 20, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes, pois, Jesus outra vez: Paz seja convosco; assim como o Pai me enviou, também eu vos envio a vós.', 21, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo dito isto, assoprou sobre eles e disse-lhes: Recebei o Espírito Santo.', 22, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('«queles a quem perdoardes os pecados lhes são perdoados; e àqueles a quem os retiverdes lhes são retidos.', 23, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, Tomé, um dos doze, chamado Dídimo, não estava com eles quando veio Jesus.', 24, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, pois, os outros discípulos: Vimos o Senhor. Mas ele disse-lhes: Se eu não vir o sinal dos cravos em suas mãos, e não puser o meu dedo no lugar dos cravos, e não puser a minha mão no seu lado, de maneira nenhuma o crerei.', 25, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E oito dias depois estavam outra vez os seus discípulos dentro, e com eles Tomé. Chegou Jesus, estando as portas fechadas, e apresentou-se no meio, e disse: Paz seja convosco.', 26, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disse a Tomé: Põe aqui o teu dedo, e vê as minhas mãos; e chega a tua mão, e põe-na no meu lado; e não sejas incrédulo, mas crente.', 27, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Tomé respondeu, e disse-lhe: Senhor meu, e Deus meu!', 28, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Jesus: Porque me viste, Tomé, creste; bem-aventurados os que não viram e creram.', 29, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, pois, operou também em presença de seus discípulos muitos outros sinais, que não estão escritos neste livro.', 30, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes, porém, foram escritos para que creiais que Jesus é o Cristo, o Filho de Deus, e para que, crendo, tenhais vida em seu nome.', 31, 20, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu, pois, meu filho, fortifica-te na graça que há em Cristo Jesus.', 1, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que de mim, entre muitas testemunhas, ouviste, confia-o a homens fiéis, que sejam idôneos para também ensinarem os outros.', 2, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu pois, sofre as aflições, como bom soldado de Jesus Cristo.', 3, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém que milita se embaraça com negócios desta vida, a fim de agradar àquele que o alistou para a guerra.', 4, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se alguém também milita, não é coroado se não militar legitimamente.', 5, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O lavrador que trabalha deve ser o primeiro a gozar dos frutos.', 6, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Considera o que digo, e o Senhor te dê entendimento em tudo.', 7, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lembra-te de que Jesus Cristo, que é da descendência de Davi, ressuscitou dentre os mortos, segundo o meu evangelho;', 8, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso sofro trabalhos e até prisões, como um malfeitor; mas a palavra de Deus não está presa.', 9, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, tudo sofro por amor dos escolhidos, para que também eles alcancem a salvação que está em Cristo Jesus com glória eterna.', 10, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Palavra fiel é esta: que, se morrermos com ele, também com ele viveremos;', 11, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se sofrermos, também com ele reinaremos; se o negarmos, também ele nos negará;', 12, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se formos infiéis, ele permanece fiel; não pode negar-se a si mesmo.', 13, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Traze estas coisas à memória, ordenando-lhes diante do Senhor que não tenham contendas de palavras, que para nada aproveitam e são para perversão dos ouvintes.', 14, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Procura apresentar-te a Deus aprovado, como obreiro que não tem de que se envergonhar, que maneja bem a palavra da verdade.', 15, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas evita os falatórios profanos, porque produzirão maior impiedade.', 16, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a palavra desses roerá como gangrena; entre os quais são Himeneu e Fileto;', 17, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais se desviaram da verdade, dizendo que a ressurreição era já feita, e perverteram a fé de alguns.', 18, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia o fundamento de Deus fica firme, tendo este selo: O Senhor conhece os que são seus, e qualquer que profere o nome de Cristo aparte-se da iniqüidade.', 19, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, numa grande casa não somente há vasos de ouro e de prata, mas também de pau e de barro; uns para honra, outros, porém, para desonra.', 20, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que, se alguém se purificar destas coisas, será vaso para honra, santificado e idôneo para uso do Senhor, e preparado para toda a boa obra.', 21, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Foge também das paixões da mocidade; e segue a justiça, a fé, o amor, e a paz com os que, com um coração puro, invocam o Senhor.', 22, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rejeita as questões loucas, e sem instrução, sabendo que produzem contendas.', 23, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao servo do Senhor não convém contender, mas sim, ser manso para com todos, apto para ensinar, sofredor;', 24, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Instruindo com mansidão os que resistem, a ver se porventura Deus lhes dará arrependimento para conhecerem a verdade,', 25, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tornarem a despertar, desprendendo-se dos laços do diabo, em que à vontade dele estão presos.', 26, 2, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tiago, servo de Deus, e do Senhor Jesus Cristo, às doze tribos que andam dispersas, saúde.', 1, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meus irmãos, tende grande gozo quando cairdes em várias tentações;', 2, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo que a prova da vossa fé opera a paciência.', 3, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenha, porém, a paciência a sua obra perfeita, para que sejais perfeitos e completos, sem faltar em coisa alguma.', 4, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se algum de vós tem falta de sabedoria, peça-a a Deus, que a todos dá liberalmente, e o não lança em rosto, e ser-lhe-á dada.', 5, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Peça-a, porém, com fé, em nada duvidando; porque o que duvida é semelhante à onda do mar, que é levada pelo vento, e lançada de uma para outra parte.', 6, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não pense tal homem que receberá do Senhor alguma coisa.', 7, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O homem de coração dobre é inconstante em todos os seus caminhos.', 8, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas glorie-se o irmão abatido na sua exaltação,', 9, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o rico em seu abatimento; porque ele passará como a flor da erva.', 10, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque sai o sol com ardor, e a erva seca, e a sua flor cai, e a formosa aparência do seu aspecto perece; assim se murchará também o rico em seus caminhos.', 11, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurado o homem que sofre a tentação; porque, quando for provado, receberá a coroa da vida, a qual o Senhor tem prometido aos que o amam.', 12, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém, sendo tentado, diga: De Deus sou tentado; porque Deus não pode ser tentado pelo mal, e a ninguém tenta.', 13, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas cada um é tentado, quando atraído e engodado pela sua própria concupiscência.', 14, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois, havendo a concupiscência concebido, dá à luz o pecado; e o pecado, sendo consumado, gera a morte.', 15, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não erreis, meus amados irmãos.', 16, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Toda a boa dádiva e todo o dom perfeito vem do alto, descendo do Pai das luzes, em quem não há mudança nem sombra de variação.', 17, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segundo a sua vontade, ele nos gerou pela palavra da verdade, para que fôssemos como primícias das suas criaturas.', 18, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, meus amados irmãos, todo o homem seja pronto para ouvir, tardio para falar, tardio para se irar.', 19, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a ira do homem não opera a justiça de Deus.', 20, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, rejeitando toda a imundícia e superfluidade de malícia, recebei com mansidão a palavra em vós enxertada, a qual pode salvar as vossas almas.', 21, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sede cumpridores da palavra, e não somente ouvintes, enganando-vos a vós mesmos.', 22, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se alguém é ouvinte da palavra, e não cumpridor, é semelhante ao homem que contempla ao espelho o seu rosto natural;', 23, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque se contempla a si mesmo, e vai-se, e logo se esquece de como era.', 24, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele, porém, que atenta bem para a lei perfeita da liberdade, e nisso persevera, não sendo ouvinte esquecediço, mas fazedor da obra, este tal será bem-aventurado no seu feito.', 25, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém entre vós cuida ser religioso, e não refreia a sua língua, antes engana o seu coração, a religião desse é vã.', 26, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A religião pura e imaculada para com Deus e Pai, é esta: Visitar os órfãos e as viúvas nas suas tribulações, e guardar-se da corrupção do mundo.', 27, 1, 20);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro e João subiam juntos ao templo à hora da oração, a nona.', 1, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E era trazido um homem que desde o ventre de sua mãe era coxo, o qual todos os dias punham à porta do templo, chamada Formosa, para pedir esmola aos que entravam.', 2, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, vendo a Pedro e a João que iam entrando no templo, pediu que lhe dessem uma esmola.', 3, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro, com João, fitando os olhos nele, disse: Olha para nós.', 4, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E olhou para eles, esperando receber deles alguma coisa.', 5, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Pedro: Não tenho prata nem ouro; mas o que tenho isso te dou. Em nome de Jesus Cristo, o Nazareno, levanta-te e anda.', 6, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando-o pela mão direita, o levantou, e logo os seus pés e artelhos se firmaram.', 7, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saltando ele, pôs-se em pé, e andou, e entrou com eles no templo, andando, e saltando, e louvando a Deus.', 8, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todo o povo o viu andar e louvar a Deus;', 9, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E conheciam-no, pois era ele o que se assentava a pedir esmola à porta Formosa do templo; e ficaram cheios de pasmo e assombro, pelo que lhe acontecera.', 10, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, apegando-se o coxo, que fora curado, a Pedro e João, todo o povo correu atônito para junto deles, ao alpendre chamado de Salomão.', 11, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando Pedro viu isto, disse ao povo: Homens israelitas, por que vos maravilhais disto? Ou, por que olhais tanto para nós, como se por nossa própria virtude ou santidade fizéssemos andar este homem?', 12, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Deus de Abraão, de Isaque e de Jacó, o Deus de nossos pais, glorificou a seu filho Jesus, a quem vós entregastes e perante a face de Pilatos negastes, tendo ele determinado que fosse solto.', 13, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós negastes o Santo e o Justo, e pedistes que se vos desse um homem homicida.', 14, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E matastes o Príncipe da vida, ao qual Deus ressuscitou dentre os mortos, do que nós somos testemunhas.', 15, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pela fé no seu nome fez o seu nome fortalecer a este que vedes e conheceis; sim, a fé que vem por ele, deu a este, na presença de todos vós, esta perfeita saúde.', 16, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora, irmãos, eu sei que o fizestes por ignorância, como também os vossos príncipes.', 17, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus assim cumpriu o que já dantes pela boca de todos os seus profetas havia anunciado; que o Cristo havia de padecer.', 18, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Arrependei-vos, pois, e convertei-vos, para que sejam apagados os vossos pecados, e venham assim os tempos do refrigério pela presença do Senhor,', 19, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E envie ele a Jesus Cristo, que já dantes vos foi pregado.', 20, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual convém que o céu contenha até aos tempos da restauração de tudo, dos quais Deus falou pela boca de todos os seus santos profetas, desde o princípio.', 21, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Moisés disse aos pais: O Senhor vosso Deus levantará de entre vossos irmãos um profeta semelhante a mim; a ele ouvireis em tudo quanto vos disser.', 22, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E acontecerá que toda a alma que não escutar esse profeta será exterminada dentre o povo.', 23, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sim, e todos os profetas, desde Samuel, todos quantos depois falaram, também predisseram estes dias.', 24, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós sois os filhos dos profetas e da aliança que Deus fez com nossos pais, dizendo a Abraão: Na tua descendência serão benditas todas as famílias da terra.', 25, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ressuscitando Deus a seu Filho Jesus, primeiro o enviou a vós, para que nisso vos abençoasse, no apartar, a cada um de vós, das vossas maldades.', 26, 3, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que em Icônio entraram juntos na sinagoga dos judeus, e falaram de tal modo que creu uma grande multidão, não só de judeus mas de gregos.', 1, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os judeus incrédulos incitaram e irritaram, contra os irmãos, os ânimos dos gentios.', 2, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Detiveram-se, pois, muito tempo, falando ousadamente acerca do Senhor, o qual dava testemunho à palavra da sua graça, permitindo que por suas mãos se fizessem sinais e prodígios.', 3, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dividiu-se a multidão da cidade; e uns eram pelos judeus, e outros pelos apóstolos.', 4, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havendo um motim, tanto dos judeus como dos gentios, com os seus principais, para os insultarem e apedrejarem,', 5, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo-o eles, fugiram para Listra e Derbe, cidades de Licaônia, e para a província circunvizinha;', 6, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ali pregavam o evangelho.', 7, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava assentado em Listra certo homem leso dos pés, coxo desde o ventre de sua mãe, o qual nunca tinha andado.', 8, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este ouviu falar Paulo, que, fixando nele os olhos, e vendo que tinha fé para ser curado,', 9, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse em voz alta: Levanta-te direito sobre teus pés. E ele saltou e andou.', 10, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as multidões, vendo o que Paulo fizera, levantaram a sua voz, dizendo em língua licaônica: Fizeram-se os deuses semelhantes aos homens, e desceram até nós.', 11, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chamavam Júpiter a Barnabé, e Mercúrio a Paulo; porque este era o que falava.', 12, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o sacerdote de Júpiter, cujo templo estava em frente da cidade, trazendo para a entrada da porta touros e grinaldas, queria com a multidão sacrificar-lhes.', 13, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvindo, porém, isto os apóstolos Barnabé e Paulo, rasgaram as suas vestes, e saltaram para o meio da multidão, clamando,', 14, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizendo: Senhores, por que fazeis essas coisas? Nós também somos homens como vós, sujeitos às mesmas paixões, e vos anunciamos que vos convertais dessas vaidades ao Deus vivo, que fez o céu, e a terra, o mar, e tudo quanto há neles;', 15, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual nos tempos passados deixou andar todas as nações em seus próprios caminhos.', 16, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E contudo, não se deixou a si mesmo sem testemunho, beneficiando-vos lá do céu, dando-vos chuvas e tempos frutíferos, enchendo de mantimento e de alegria os vossos corações.', 17, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dizendo isto, com dificuldade impediram que as multidões lhes sacrificassem.', 18, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sobrevieram, porém, uns judeus de Antioquia e de Icônio que, tendo convencido a multidão, apedrejaram a Paulo e o arrastaram para fora da cidade, cuidando que estava morto.', 19, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, rodeando-o os discípulos, levantou-se, e entrou na cidade, e no dia seguinte saiu com Barnabé para Derbe.', 20, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo anunciado o evangelho naquela cidade e feito muitos discípulos, voltaram para Listra, e Icônio e Antioquia,', 21, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Confirmando os ânimos dos discípulos, exortando-os a permanecer na fé, pois que por muitas tribulações nos importa entrar no reino de Deus.', 22, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-lhes, por comum consentimento, eleito anciãos em cada igreja, orando com jejuns, os encomendaram ao Senhor em quem haviam crido.', 23, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Passando depois por Pisídia, dirigiram-se a Panfília.', 24, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo anunciado a palavra em Perge, desceram a Atália.', 25, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dali navegaram para Antioquia, de onde tinham sido encomendados à graça de Deus para a obra que já haviam cumprido.', 26, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando chegaram e reuniram a igreja, relataram quão grandes coisas Deus fizera por eles, e como abrira aos gentios a porta da fé.', 27, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ficaram ali não pouco tempo com os discípulos.', 28, 14, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E na igreja que estava em Antioquia havia alguns profetas e doutores, a saber: Barnabé e Simeão chamado Níger, e Lúcio, cireneu, e Manaém, que fora criado com Herodes o tetrarca, e Saulo.', 1, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, servindo eles ao Senhor, e jejuando, disse o Espírito Santo: Apartai-me a Barnabé e a Saulo para a obra a que os tenho chamado.', 2, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, jejuando e orando, e pondo sobre eles as mãos, os despediram.', 3, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim estes, enviados pelo Espírito Santo, desceram a Selêucia e dali navegaram para Chipre.', 4, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegados a Salamina, anunciavam a palavra de Deus nas sinagogas dos judeus; e tinham também a João como cooperador.', 5, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo atravessado a ilha até Pafos, acharam um certo judeu mágico, falso profeta, chamado Barjesus,', 6, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual estava com o procônsul Sérgio Paulo, homem prudente. Este, chamando a si Barnabé e Saulo, procurava muito ouvir a palavra de Deus.', 7, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas resistia-lhes Elimas, o encantador (porque assim se interpreta o seu nome), procurando apartar da fé o procônsul.', 8, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia Saulo, que também se chama Paulo, cheio do Espírito Santo, e fixando os olhos nele,', 9, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse: Ó filho do diabo, cheio de todo o engano e de toda a malícia, inimigo de toda a justiça, não cessarás de perturbar os retos caminhos do Senhor?', 10, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis aí, pois, agora contra ti a mão do Senhor, e ficarás cego, sem ver o sol por algum tempo. E no mesmo instante a escuridão e as trevas caíram sobre ele e, andando à roda, buscava a quem o guiasse pela mão.', 11, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o procônsul, vendo o que havia acontecido, creu, maravilhado da doutrina do Senhor.', 12, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, partindo de Pafos, Paulo e os que estavam com ele chegaram a Perge, da Panfília. Mas João, apartando-se deles, voltou para Jerusalém.', 13, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles, saindo de Perge, chegaram a Antioquia, da Pisídia, e, entrando na sinagoga, num dia de sábado, assentaram-se;', 14, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois da lição da lei e dos profetas, lhes mandaram dizer os principais da sinagoga: Homens irmãos, se tendes alguma palavra de consolação para o povo, falai.', 15, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se Paulo, e pedindo silêncio com a mão, disse: Homens israelitas, e os que temeis a Deus, ouvi:', 16, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Deus deste povo de Israel escolheu a nossos pais, e exaltou o povo, sendo eles estrangeiros na terra do Egito; e com braço poderoso os tirou dela;', 17, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E suportou os seus costumes no deserto por espaço de quase quarenta anos.', 18, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, destruindo a sete nações na terra de Canaã, deu-lhes por sorte a terra deles.', 19, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois disto, por quase quatrocentos e cinqüenta anos, lhes deu juízes, até ao profeta Samuel.', 20, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois pediram um rei, e Deus lhes deu por quarenta anos, a Saul filho de Cis, homem da tribo de Benjamim.', 21, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando este foi retirado, levantou-lhes como rei a Davi, ao qual também deu testemunho, e disse: Achei a Davi, filho de Jessé, homem conforme o meu coração, que executará toda a minha vontade.', 22, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da descendência deste, conforme a promessa, levantou Deus a Jesus para Salvador de Israel;', 23, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo primeiramente João, antes da vinda dele, pregado a todo o povo de Israel o batismo de arrependimento.', 24, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas João, quando completava a carreira, disse: Quem pensais vós que eu sou? Eu não sou o Cristo; mas eis que após mim vem aquele a quem não sou digno de desatar as alparcas dos pés.', 25, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Homens irmãos, filhos da geração de Abraão, e os que dentre vós temem a Deus, a vós vos é enviada a palavra desta salvação.', 26, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por não terem conhecido a este, os que habitavam em Jerusalém, e os seus príncipes, condenaram-no, cumprindo assim as vozes dos profetas que se lêem todos os sábados.', 27, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, embora não achassem alguma causa de morte, pediram a Pilatos que ele fosse morto.', 28, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo eles cumprido todas as coisas que dele estavam escritas, tirando-o do madeiro, o puseram na sepultura;', 29, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus o ressuscitou dentre os mortos.', 30, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele por muitos dias foi visto pelos que subiram com ele da Galiléia a Jerusalém, e são suas testemunhas para com o povo.', 31, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nós vos anunciamos que a promessa que foi feita aos pais, Deus a cumpriu a nós, seus filhos, ressuscitando a Jesus;', 32, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como também está escrito no salmo segundo: Meu filho és tu, hoje te gerei.', 33, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que o ressuscitaria dentre os mortos, para nunca mais tornar à corrupção, disse-o assim: As santas e fiéis bênçãos de Davi vos darei.', 34, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso também em outro salmo diz: Não permitirás que o teu santo veja corrupção.', 35, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, na verdade, tendo Davi no seu tempo servido conforme a vontade de Deus, dormiu, foi posto junto de seus pais e viu a corrupção.', 36, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas aquele a quem Deus ressuscitou nenhuma corrupção viu.', 37, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Seja-vos, pois, notório, homens irmàos, que por este se vos anuncia a remissão dos pecados.', 38, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de tudo o que, pela lei de Moisés, não pudestes ser justificados, por ele é justificado todo aquele que crê.', 39, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede, pois, que não venha sobre vós o que está dito nos profetas:', 40, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede, ó desprezadores, e espantai-vos e desaparecei;porque opero uma obra em vossos dias,ora tal que não crereis, se alguém vo-la contar.', 41, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saídos os judeus da sinagoga, os gentios rogaram que no sábado seguinte lhes fossem ditas as mesmas coisas.', 42, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, despedida a sinagoga, muitos dos judeus e dos prosélitos religiosos seguiram Paulo e Barnabé; os quais, falando-lhes, os exortavam a que permanecessem na graça de Deus.', 43, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no sábado seguinte ajuntou-se quase toda a cidade para ouvir a palavra de Deus.', 44, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então os judeus, vendo a multidão, encheram-se de inveja e, blasfemando, contradiziam o que Paulo falava.', 45, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Paulo e Barnabé, usando de ousadia, disseram: Era mister que a vós se vos pregasse primeiro a palavra de Deus; mas, visto que a rejeitais, e não vos julgais dignos da vida eterna, eis que nos voltamos para os gentios;', 46, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Senhor assim no-lo mandou:eu te pus para luz dos gentios,a fim de que sejas para salvação até os confins da terra.', 47, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os gentios, ouvindo isto, alegraram-se, e glorificavam a palavra do Senhor; e creram todos quantos estavam ordenados para a vida eterna.', 48, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a palavra do Senhor se divulgava por toda aquela província.', 49, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os judeus incitaram algumas mulheres religiosas e honestas, e os principais da cidade, e levantaram perseguição contra Paulo e Barnabé, e os lançaram fora dos seus termos.', 50, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sacudindo, porém, contra eles o pó dos seus pés, partiram para Icônio.', 51, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os discípulos estavam cheios de alegria e do Espírito Santo.', 52, 13, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabe, porém, isto: que nos últimos dias sobrevirão tempos trabalhosos.', 1, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque haverá homens amantes de si mesmos, avarentos, presunçosos, soberbos, blasfemos, desobedientes a pais e mães, ingratos, profanos,', 2, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sem afeto natural, irreconciliáveis, caluniadores, incontinentes, cruéis, sem amor para com os bons,', 3, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Traidores, obstinados, orgulhosos, mais amigos dos deleites do que amigos de Deus,', 4, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo aparência de piedade, mas negando a eficácia dela. Destes afasta-te.', 5, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque deste número são os que se introduzem pelas casas, e levam cativas mulheres néscias carregadas de pecados, levadas de várias concupiscências;', 6, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que aprendem sempre, e nunca podem chegar ao conhecimento da verdade.', 7, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como Janes e Jambres resistiram a Moisés, assim também estes resistem à verdade, sendo homens corruptos de entendimento e réprobos quanto à fé.', 8, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não irão, porém, avante; porque a todos será manifesto o seu desvario, como também o foi o daqueles.', 9, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu, porém, tens seguido a minha doutrina, modo de viver, intenção, fé, longanimidade, amor, paciência,', 10, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Perseguições e aflições tais quais me aconteceram em Antioquia, em Icônio, e em Listra; quantas perseguições sofri, e o Senhor de todas me livrou;', 11, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também todos os que piamente querem viver em Cristo Jesus padecerão perseguições.', 12, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os homens maus e enganadores irão de mal para pior, enganando e sendo enganados.', 13, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu, porém, permanece naquilo que aprendeste, e de que foste inteirado, sabendo de quem o tens aprendido,', 14, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que desde a tua meninice sabes as sagradas Escrituras, que podem fazer-te sábio para a salvação, pela fé que há em Cristo Jesus.', 15, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Toda a Escritura é divinamente inspirada, e proveitosa para ensinar, para redargüir, para corrigir, para instruir em justiça;', 16, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que o homem de Deus seja perfeito, e perfeitamente instruído para toda a boa obra.', 17, 3, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouviram os apóstolos, e os irmãos que estavam na Judéia, que também os gentios tinham recebido a palavra de Deus.', 1, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, subindo Pedro a Jerusalém, disputavam com ele os que eram da circuncisão,', 2, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Entraste em casa de homens incircuncisos, e comeste com eles.', 3, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Pedro começou a fazer-lhes uma exposição por ordem, dizendo:', 4, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estando eu orando na cidade de Jope, tive, num arrebatamento dos sentidos, uma visão; via um vaso, como um grande lençol que descia do céu e vinha até junto de mim.', 5, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pondo nele os olhos, considerei, e vi animais da terra, quadrúpedes, e feras, e répteis e aves do céu.', 6, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi uma voz que me dizia: Levanta-te, Pedro; mata e come.', 7, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eu disse: De maneira nenhuma, Senhor; pois, nunca em minha boca entrou coisa alguma comum ou imunda.', 8, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a voz respondeu-me do céu segunda vez: Não chames tu comum ao que Deus purificou.', 9, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sucedeu isto por três vezes; e tudo tornou a recolher-se ao céu.', 10, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que, na mesma hora, pararam, junto da casa em que eu estava, três homens que me foram enviados de Cesaréia.', 11, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-me o Espírito que fosse com eles, nada duvidando; e também estes seis irmãos foram comigo, e entramos em casa daquele homem;', 12, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E contou-nos como vira em pé um anjo em sua casa, e lhe dissera: Envia homens a Jope, e manda chamar a Simão, que tem por sobrenome Pedro,', 13, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual te dirá palavras com que te salves, tu e toda a tua casa.', 14, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando comecei a falar, caiu sobre eles o Espírito Santo, como também sobre nós ao princípio.', 15, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lembrei-me do dito do Senhor, quando disse: João certamente batizou com água; mas vós sereis batizados com o Espírito Santo.', 16, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, se Deus lhes deu o mesmo dom que a nós, quando havemos crido no Senhor Jesus Cristo, quem era então eu, para que pudesse resistir a Deus?', 17, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo estas coisas, apaziguaram-se, e glorificaram a Deus, dizendo: Na verdade até aos gentios deu Deus o arrependimento para a vida.', 18, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que foram dispersos pela perseguição que sucedeu por causa de Estêvão caminharam até à Fenícia, Chipre e Antioquia, não anunciando a ninguém a palavra, senão somente aos judeus.', 19, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia entre eles alguns homens chíprios e cirenenses, os quais entrando em Antioquia falaram aos gregos, anunciando o Senhor Jesus.', 20, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a mão do Senhor era com eles; e grande número creu e se converteu ao Senhor.', 21, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegou a fama destas coisas aos ouvidos da igreja que estava em Jerusalém; e enviaram Barnabé a Antioquia.', 22, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, quando chegou, e viu a graça de Deus, se alegrou, e exortou a todos a que permanecessem no Senhor, com propósito de coração;', 23, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque era homem de bem e cheio do Espírito Santo e de fé. E muita gente se uniu ao Senhor.', 24, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E partiu Barnabé para Tarso, a buscar Saulo; e, achando-o, o conduziu para Antioquia.', 25, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sucedeu que todo um ano se reuniram naquela igreja, e ensinaram muita gente; e em Antioquia foram os discípulos, pela primeira vez, chamados cristãos.', 26, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E naqueles dias desceram profetas de Jerusalém para Antioquia.', 27, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se um deles, por nome Ágabo, dava a entender pelo Espírito, que haveria uma grande fome em todo o mundo, e isso aconteceu no tempo de Cláudio César.', 28, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os discípulos determinaram mandar, cada um conforme o que pudesse, socorro aos irmãos que habitavam na Judéia.', 29, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que eles com efeito fizeram, enviando-o aos anciãos por mão de Barnabé e de Saulo.', 30, 11, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conjuro-te, pois, diante de Deus, e do Senhor Jesus Cristo, que há de julgar os vivos e os mortos, na sua vinda e no seu reino,', 1, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que pregues a palavra, instes a tempo e fora de tempo, redarguas, repreendas, exortes, com toda a longanimidade e doutrina.', 2, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque virá tempo em que não suportarão a sã doutrina; mas, tendo comichão nos ouvidos, amontoarão para si doutores conforme as suas próprias concupiscências;', 3, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desviarão os ouvidos da verdade, voltando às fábulas.', 4, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas tu, sê sóbrio em tudo, sofre as aflições, faze a obra de um evangelista, cumpre o teu ministério.', 5, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu já estou sendo oferecido por aspersão de sacrifício, e o tempo da minha partida está próximo.', 6, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Combati o bom combate, acabei a carreira, guardei a fé.', 7, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde agora, a coroa da justiça me está guardada, a qual o Senhor, justo juiz, me dará naquele dia; e não somente a mim, mas também a todos os que amarem a sua vinda.', 8, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Procura vir ter comigo depressa,', 9, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Demas me desamparou, amando o presente século, e foi para Tessalônica, Crescente para Galácia, Tito para Dalmácia.', 10, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Só Lucas está comigo. Toma Marcos, e traze-o contigo, porque me é muito útil para o ministério.', 11, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Também enviei Tíquico a Éfeso.', 12, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando vieres, traze a capa que deixei em Trôade, em casa de Carpo, e os livros, principalmente os pergaminhos.', 13, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Alexandre, o latoeiro, causoume muitos males; o Senhor lhe pague segundo as suas obras.', 14, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu, guarda-te também dele, porque resistiu muito às nossas palavras.', 15, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém me assistiu na minha primeira defesa, antes todos me desampararam. Que isto lhes não seja imputado.', 16, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o Senhor assistiu-me e fortaleceu-me, para que por mim fosse cumprida a pregação, e todos os gentios a ouvissem; e fiquei livre da boca do leão.', 17, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o Senhor me livrará de toda a má obra, e guardar-me-á para o seu reino celestial; a quem seja glória para todo o sempre. Amém.', 18, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saúda a Prisca e a Áqüila, e à casa de Onesíforo.', 19, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Erasto ficou em Corinto, e deixei Trófimo doente em Mileto.', 20, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Procura vir antes do inverno. Eubulo, e Prudente, e Lino, e Cláudia, e todos os irmãos te saúdam.', 21, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Senhor Jesus Cristo seja com o teu espírito. A graça seja convosco. Amém.', 22, 4, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse o sumo sacerdote: Porventura é isto assim?', 1, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse: Homens, irmãos, e pais, ouvi. O Deus da glória apareceu a nosso pai Abraão, estando na mesopotâmia, antes de habitar em Harã,', 2, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe: Sai da tua terra e dentre a tua parentela, e dirige-te à terra que eu te mostrar.', 3, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então saiu da terra dos caldeus, e habitou em Harã. E dali, depois que seu pai faleceu, Deus o trouxe para esta terra em que habitais agora.', 4, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não lhe deu nela herança, nem ainda o espaço de um pé; mas prometeu que lhe daria a posse dela, e depois dele, à sua descendência, não tendo ele ainda filho.', 5, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E falou Deus assim: Que a sua descendência seria peregrina em terra alheia, e a sujeitariam à escravidão, e a maltratariam por quatrocentos anos.', 6, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu julgarei a nação que os tiver escravizado, disse Deus. E depois disto sairão e me servirão neste lugar.', 7, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E deu-lhe a aliança da circuncisão; e assim gerou a Isaque, e o circuncidou ao oitavo dia; e Isaque a Jacó; e Jacó aos doze patriarcas.', 8, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os patriarcas, movidos de inveja, venderam José para o Egito; mas Deus era com ele.', 9, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E livrou-o de todas as suas tribulações, e lhe deu graça e sabedoria ante Faraó, rei do Egito, que o constituiu governador sobre o Egito e toda a sua casa.', 10, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sobreveio então a todo o país do Egito e de Canaã fome e grande tribulação; e nossos pais não achavam alimentos.', 11, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas tendo ouvido Jacó que no Egito havia trigo, enviou ali nossos pais, a primeira vez.', 12, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E na segunda vez foi José conhecido por seus irmãos, e a sua linhagem foi manifesta a Faraó.', 13, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E José mandou chamar a seu pai Jacó, e a toda a sua parentela, que era de setenta e cinco almas.', 14, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jacó desceu ao Egito, e morreu, ele e nossos pais;', 15, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram transportados para Siquém, e depositados na sepultura que Abraão comprara por certa soma de dinheiro aos filhos de Emor, pai de Siquém.', 16, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aproximando-se, porém, o tempo da promessa que Deus tinha feito a Abraão, o povo cresceu e se multiplicou no Egito;', 17, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Até que se levantou outro rei, que não conhecia a José.', 18, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esse, usando de astúcia contra a nossa linhagem, maltratou nossos pais, ao ponto de os fazer enjeitar as suas crianças, para que não se multiplicassem.', 19, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nesse tempo nasceu Moisés, e era mui formoso, e foi criado três meses em casa de seu pai.', 20, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo enjeitado, tomou-o a filha de Faraó, e o criou como seu filho.', 21, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Moisés foi instruído em toda a ciência dos egípcios; e era poderoso em suas palavras e obras.', 22, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando completou a idade de quarenta anos, veio-lhe ao coração ir visitar seus irmãos, os filhos de Israel.', 23, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo maltratado um deles, o defendeu, e vingou o ofendido, matando o egípcio.', 24, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele cuidava que seus irmãos entenderiam que Deus lhes havia de dar a liberdade pela sua mão; mas eles não entenderam.', 25, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no dia seguinte, pelejando eles, foi por eles visto, e quis levá-los à paz, dizendo: Homens, sois irmãos; por que vos agravais um ao outro?', 26, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que ofendia o seu próximo o repeliu, dizendo: Quem te constituiu príncipe e juiz sobre nós?', 27, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Queres tu matar-me, como ontem mataste o egípcio?', 28, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a esta palavra fugiu Moisés, e esteve como estrangeiro na terra de Midiã, onde gerou dois filhos.', 29, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, completados quarenta anos, apareceu-lhe o anjo do Senhor no deserto do monte Sinai, numa chama de fogo no meio de uma sarça.', 30, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Moisés, quando viu isto, se maravilhou da visão; e, aproximando-se para observar, foi-lhe dirigida a voz do Senhor,', 31, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Eu sou o Deus de teus pais, o Deus de Abraão, e o Deus de Isaque, e o Deus de Jacó. E Moisés, todo trêmulo, não ousava olhar.', 32, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe o Senhor: Tira as alparcas dos teus pés, porque o lugar em que estás é terra santa.', 33, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho visto atentamente a aflição do meu povo que está no Egito, e ouvi os seus gemidos, e desci a livrá-los. Agora, pois, vem, e enviar-te-ei ao Egito.', 34, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A este Moisés, ao qual haviam negado, dizendo: Quem te constituiu príncipe e juiz? a este enviou Deus como príncipe e libertador, pela mão do anjo que lhe aparecera na sarça.', 35, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Foi este que os conduziu para fora, fazendo prodígios e sinais na terra do Egito, e no Mar Vermelho, e no deserto, por quarenta anos.', 36, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este é aquele Moisés que disse aos filhos de Israel: O Senhor vosso Deus vos levantará dentre vossos irmãos um profeta como eu; a ele ouvireis.', 37, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este é o que esteve entre a congregação no deserto, com o anjo que lhe falava no monte Sinai, e com nossos pais, o qual recebeu as palavras de vida para no-las dar.', 38, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao qual nossos pais não quiseram obedecer, antes o rejeitaram e em seu coração se tornaram ao Egito,', 39, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo a Arão: Faze-nos deuses que vão adiante de nós; porque a esse Moisés, que nos tirou da terra do Egito, não sabemos o que lhe aconteceu.', 40, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E naqueles dias fizeram o bezerro, e ofereceram sacrifícios ao ídolo, e se alegraram nas obras das suas mãos.', 41, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus se afastou, e os abandonou a que servissem ao exército do céu, como está escrito no livro dos profetas: Porventura me oferecestes vítimas e sacrifícios No deserto por quarenta anos, ó casa de Israel?', 42, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes tomastes o tabernáculo de Moloque,E a estrela do vosso deus Renfã, figuras que vós fizestes para as adorar.Transportar-vos-ei, pois, para além da Babilônia.', 43, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estava entre nossos pais no deserto o tabernáculo do testemunho, como ordenara aquele que disse a Moisés que o fizesse segundo o modelo que tinha visto.', 44, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, nossos pais, recebendo-o também, o levaram com Josué quando entraram na posse das nações que Deus lançou para fora da presença de nossos pais, até aos dias de Davi,', 45, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que achou graça diante de Deus, e pediu que pudesse achar tabernáculo para o Deus de Jacó.', 46, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Salomão lhe edificou casa;', 47, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o Altíssimo não habita em templos feitos por mãos de homens, como diz o profeta:', 48, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O céu é o meu trono,e a terra o estrado dos meus pés.Que casa me edificareis? diz o Senhor,Ou qual é o lugar do meu repouso?', 49, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura não fez a minha mão todas estas coisas?', 50, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Homens de dura cerviz, e incircuncisos de coração e ouvido, vós sempre resistis ao Espírito Santo; assim vós sois como vossos pais.', 51, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A qual dos profetas não perseguiram vossos pais? Até mataram os que anteriormente anunciaram a vinda do Justo, do qual vós agora fostes traidores e homicidas;', 52, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, que recebestes a lei por ordenação dos anjos, e não a guardastes.', 53, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo eles isto, enfureciam-se em seus corações, e rangiam os dentes contra ele.', 54, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, estando cheio do Espírito Santo, fixando os olhos no céu, viu a glória de Deus, e Jesus, que estava à direita de Deus;', 55, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse: Eis que vejo os céus abertos, e o Filho do homem, que está em pé à mão direita de Deus.', 56, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eles gritaram com grande voz, taparam os seus ouvidos, e arremeteram unânimes contra ele.', 57, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, expulsando-o da cidade, o apedrejavam. E as testemunhas depuseram as suas capas aos pés de um jovem chamado Saulo.', 58, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E apedrejaram a Estêvão que em invocação dizia: Senhor Jesus, recebe o meu espírito.', 59, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pondo-se de joelhos, clamou com grande voz: Senhor, não lhes imputes este pecado. E, tendo dito isto, adormeceu.', 60, 7, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas um certo homem chamado Ananias, com Safira, sua mulher, vendeu uma propriedade,', 1, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E reteve parte do preço, sabendo-o também sua mulher; e, levando uma parte, a depositou aos pés dos apóstolos.', 2, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse então Pedro: Ananias, por que encheu Satanás o teu coração, para que mentisses ao Espírito Santo, e retivesses parte do preço da herdade?', 3, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Guardando-a não ficava para ti? E, vendida, não estava em teu poder? Por que formaste este desígnio em teu coração? Não mentiste aos homens, mas a Deus.', 4, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Ananias, ouvindo estas palavras, caiu e expirou. E um grande temor veio sobre todos os que isto ouviram.', 5, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se os moços, cobriram o morto e, transportando-o para fora, o sepultaram.', 6, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passando um espaço quase de três horas, entrou também sua mulher, não sabendo o que havia acontecido.', 7, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe Pedro: Dize-me, vendestes por tanto aquela herdade? E ela disse: Sim, por tanto.', 8, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pedro lhe disse: Por que é que entre vós vos concertastes para tentar o Espírito do Senhor? Eis aí à porta os pés dos que sepultaram o teu marido, e também te levarão a ti.', 9, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo caiu aos seus pés, e expirou. E, entrando os moços, acharam-na morta, e a sepultaram junto de seu marido.', 10, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E houve um grande temor em toda a igreja, e em todos os que ouviram estas coisas.', 11, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos sinais e prodígios eram feitos entre o povo pelas mãos dos apóstolos. E estavam todos unanimemente no alpendre de Salomão.', 12, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dos outros, porém, ninguém ousava ajuntar-se a eles; mas o povo tinha-os em grande estima.', 13, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a multidão dos que criam no Senhor, tanto homens como mulheres, crescia cada vez mais.', 14, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que transportavam os enfermos para as ruas, e os punham em leitos e em camilhas para que ao menos a sombra de Pedro, quando este passasse, cobrisse alguns deles.', 15, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E até das cidades circunvizinhas concorria muita gente a Jerusalém, conduzindo enfermos e atormentados de espíritos imundos; os quais eram todos curados.', 16, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se o sumo sacerdote, e todos os que estavam com ele (e eram eles da seita dos saduceus), encheram-se de inveja,', 17, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lançaram mão dos apóstolos, e os puseram na prisão pública.', 18, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas de noite um anjo do Senhor abriu as portas da prisão e, tirando-os para fora, disse:', 19, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ide e apresentai-vos no templo, e dizei ao povo todas as palavras desta vida.', 20, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo eles isto, entraram de manhã cedo no templo, e ensinavam. Chegando, porém, o sumo sacerdote e os que estavam com ele, convocaram o conselho, e a todos os anciãos dos filhos de Israel, e enviaram ao cárcere, para que de lá os trouxessem.', 21, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, tendo lá ido os servidores, não os acharam na prisão e, voltando, lho anunciaram,', 22, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Achamos realmente o cárcere fechado, com toda a segurança, e os guardas, que estavam fora, diante das portas; mas, quando abrimos, ninguém achamos dentro.', 23, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o sumo sacerdote, o capitão do templo e os chefes dos sacerdotes, ouvindo estas palavras, estavam perplexos acerca deles e do que viria a ser aquilo.', 24, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando um, anunciou-lhes, dizendo: Eis que os homens que encerrastes na prisão estão no templo e ensinam ao povo.', 25, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então foi o capitão com os servidores, e os trouxe, não com violência (porque temiam ser apedrejados pelo povo).', 26, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, trazendo-os, os apresentaram ao conselho. E o sumo sacerdote os interrogou,', 27, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Não vos admoestamos nós expressamente que não ensinásseis nesse nome? E eis que enchestes Jerusalém dessa vossa doutrina, e quereis lançar sobre nós o sangue desse homem.', 28, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porém, respondendo Pedro e os apóstolos, disseram: Mais importa obedecer a Deus do que aos homens.', 29, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Deus de nossos pais ressuscitou a Jesus, ao qual vós matastes, suspendendo-o no madeiro.', 30, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deus com a sua destra o elevou a Príncipe e Salvador, para dar a Israel o arrependimento e a remissão dos pecados.', 31, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nós somos testemunhas acerca destas palavras, nós e também o Espírito Santo, que Deus deu àqueles que lhe obedecem.', 32, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo eles isto, se enfureciam, e deliberaram matá-los.', 33, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, levantando-se no conselho um certo fariseu, chamado Gamaliel, doutor da lei, venerado por todo o povo, mandou que por um pouco levassem para fora os apóstolos;', 34, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Homens israelitas, acautelai-vos a respeito do que haveis de fazer a estes homens,', 35, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque antes destes dias levantou-se Teudas, dizendo ser alguém; a este se ajuntou o número de uns quatrocentos homens; o qual foi morto, e todos os que lhe deram ouvidos foram dispersos e reduzidos a nada.', 36, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois deste levantou-se Judas, o galileu, nos dias do alistamento, e levou muito povo após si; mas também este pereceu, e todos os que lhe deram ouvidos foram dispersos.', 37, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora digo-vos: Dai de mão a estes homens, e deixai-os, porque, se este conselho ou esta obra é de homens, se desfará,', 38, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se é de Deus, não podereis desfazê-la; para que não aconteça serdes também achados combatendo contra Deus.', 39, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E concordaram com ele. E, chamando os apóstolos, e tendo-os açoitado, mandaram que não falassem no nome de Jesus, e os deixaram ir.', 40, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Retiraram-se, pois, da presença do conselho, regozijando-se de terem sido julgados dignos de padecer afronta pelo nome de Jesus.', 41, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os dias, no templo e nas casas, não cessavam de ensinar, e de anunciar a Jesus Cristo.', 42, 5, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Saulo, respirando ainda ameaças e mortes contra os discípulos do Senhor, dirigiu-se ao sumo sacerdote.', 1, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pediu-lhe cartas para Damasco, para as sinagogas, a fim de que, se encontrasse alguns deste Caminho, quer homens quer mulheres, os conduzisse presos a Jerusalém.', 2, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo no caminho, aconteceu que, chegando perto de Damasco, subitamente o cercou um resplendor de luz do céu.', 3, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, caindo em terra, ouviu uma voz que lhe dizia: Saulo, Saulo, por que me persegues?', 4, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse: Quem és, Senhor? E disse o Senhor: Eu sou Jesus, a quem tu persegues. Duro é para ti recalcitrar contra os aguilhões.', 5, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, tremendo e atônito, disse: Senhor, que queres que eu faça? E disse-lhe o Senhor: Levanta-te, e entra na cidade, e lá te será dito o que te convém fazer.', 6, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os homens, que iam com ele, pararam espantados, ouvindo a voz, mas não vendo ninguém.', 7, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Saulo levantou-se da terra, e, abrindo os olhos, não via a ninguém. E, guiando-o pela mão, o conduziram a Damasco.', 8, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E esteve três dias sem ver, e não comeu nem bebeu.', 9, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia em Damasco um certo discípulo chamado Ananias; e disse-lhe o Senhor em visão: Ananias! E ele respondeu: Eis-me aqui, Senhor.', 10, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe o Senhor: Levanta-te, e vai à rua chamada Direita, e pergunta em casa de Judas por um homem de Tarso chamado Saulo; pois eis que ele está orando;', 11, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E numa visão ele viu que entrava um homem chamado Ananias, e punha sobre ele a mão, para que tornasse a ver.', 12, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E respondeu Ananias: Senhor, a muitos ouvi acerca deste homem, quantos males tem feito aos teus santos em Jerusalém;', 13, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aqui tem poder dos principais dos sacerdotes para prender a todos os que invocam o teu nome.', 14, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe, porém, o Senhor: Vai, porque este é para mim um vaso escolhido, para levar o meu nome diante dos gentios, e dos reis e dos filhos de Israel.', 15, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu lhe mostrarei quanto deve padecer pelo meu nome.', 16, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Ananias foi, e entrou na casa e, impondo-lhe as mãos, disse: Irmão Saulo, o Senhor Jesus, que te apareceu no caminho por onde vinhas, me enviou, para que tornes a ver e sejas cheio do Espírito Santo.', 17, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo lhe caíram dos olhos como que umas escamas, e recuperou a vista; e, levantando-se, foi batizado.', 18, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo comido, ficou confortado. E esteve Saulo alguns dias com os discípulos que estavam em Damasco.', 19, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo nas sinagogas pregava a Cristo, que este é o Filho de Deus.', 20, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os que o ouviam estavam atônitos, e diziam: Não é este o que em Jerusalém perseguia os que invocavam este nome, e para isso veio aqui, para os levar presos aos principais dos sacerdotes?', 21, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saulo, porém, se esforçava muito mais, e confundia os judeus que habitavam em Damasco, provando que aquele era o Cristo.', 22, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo passado muitos dias, os judeus tomaram conselho entre si para o matar.', 23, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas as suas ciladas vieram ao conhecimento de Saulo; e como eles guardavam as portas, tanto de dia como de noite, para poderem tirar-lhe a vida,', 24, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tomando-o de noite os discípulos o desceram, dentro de um cesto, pelo muro.', 25, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando Saulo chegou a Jerusalém, procurava ajuntar-se aos discípulos, mas todos o temiam, não crendo que fosse discípulo.', 26, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Barnabé, tomando-o consigo, o trouxe aos apóstolos, e lhes contou como no caminho ele vira ao Senhor e lhe falara, e como em Damasco falara ousadamente no nome de Jesus.', 27, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E andava com eles em Jerusalém, entrando e saindo,', 28, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E falava ousadamente no nome do Senhor Jesus. Falava e disputava também contra os gregos, mas eles procuravam matá-lo.', 29, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo-o, porém, os irmãos, o acompanharam até Cesaréia, e o enviaram a Tarso.', 30, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, pois, as igrejas em toda a Judéia, e Galiléia e Samaria tinham paz, e eram edificadas; e se multiplicavam, andando no temor do Senhor e consolação do Espírito Santo.', 31, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, passando Pedro por toda a parte, veio também aos santos que habitavam em Lida.', 32, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E achou ali certo homem, chamado Enéias, jazendo numa cama havia oito anos, o qual era paralítico.', 33, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe Pedro: Enéias, Jesus Cristo te dá saúde; levanta-te e faze a tua cama. E logo se levantou.', 34, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E viram-no todos os que habitavam em Lida e Sarona, os quais se converteram ao Senhor.', 35, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia em Jope uma discípula chamada Tabita, que traduzido se diz Dorcas. Esta estava cheia de boas obras e esmolas que fazia.', 36, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu naqueles dias que, enfermando ela, morreu; e, tendo-a lavado, a depositaram num quarto alto.', 37, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como Lida era perto de Jope, ouvindo os discípulos que Pedro estava ali, lhe mandaram dois homens, rogando-lhe que não se demorasse em vir ter com eles.', 38, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se Pedro, foi com eles; e quando chegou o levaram ao quarto alto, e todas as viúvas o rodearam, chorando e mostrando as túnicas e roupas que Dorcas fizera quando estava com elas.', 39, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Pedro, fazendo sair a todos, pôs-se de joelhos e orou: e, voltando-se para o corpo, disse: Tabita, levanta-te. E ela abriu os olhos, e, vendo a Pedro, assentou-se.', 40, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, dando-lhe a mão, a levantou e, chamando os santos e as viúvas, apresentou-lha viva.', 41, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi isto notório por toda a Jope, e muitos creram no Senhor.', 42, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ficou muitos dias em Jope, com um certo Simão curtidor.', 43, 9, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, separando-nos deles, navegamos e fomos correndo caminho direito, e chegamos a Cós, e no dia seguinte a Rodes, de onde passamos a Pátara.', 1, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, achando um navio, que ia para a Fenícia, embarcamos nele, e partimos.', 2, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo já à vista de Chipre, deixando-a à esquerda, navegamos para a Síria e chegamos a Tiro; porque o navio havia de ser descarregado ali.', 3, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, achando discípulos, ficamos ali sete dias; e eles pelo Espírito diziam a Paulo que não subisse a Jerusalém.', 4, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo passado ali aqueles dias, saímos, e seguimos nosso caminho, acompanhando-nos todos, com suas mulheres e filhos até fora da cidade; e, postos de joelhos na praia, oramos.', 5, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, despedindo-nos uns dos outros, subimos ao navio; e eles voltaram para suas casas.', 6, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nós, concluída a navegação de Tiro, viemos a Ptolemaida; e, havendo saudado os irmãos, ficamos com eles um dia.', 7, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no dia seguinte, partindo dali Paulo, e nós que com ele estávamos, chegamos a Cesaréia; e, entrando em casa de Filipe, o evangelista, que era um dos sete, ficamos com ele.', 8, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tinha este quatro filhas virgens, que profetizavam.', 9, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, demorando-nos ali por muitos dias, chegou da Judéia um profeta, por nome Ágabo;', 10, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vindo ter conosco, tomou a cinta de Paulo, e ligando-se os seus próprios pés e mãos, disse: Isto diz o Espírito Santo: Assim ligarão os judeus em Jerusalém o homem de quem é esta cinta, e o entregarão nas mãos dos gentios.', 11, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo nós isto, rogamos-lhe, tanto nós como os que eram daquele lugar, que não subisse a Jerusalém.', 12, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Paulo respondeu: Que fazeis vós, chorando e magoando-me o coração? Porque eu estou pronto não só a ser ligado, mas ainda a morrer em Jerusalém pelo nome do Senhor Jesus.', 13, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como não podíamos convencê-lo, nos aquietamos, dizendo: Faça-se a vontade do Senhor.', 14, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois daqueles dias, havendo feito os nossos preparativos, subimos a Jerusalém.', 15, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram também conosco alguns discípulos de Cesaréia, levando consigo um certo Mnasom, chíprio, discípulo antigo, com quem havíamos de hospedar-nos.', 16, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo que chegamos a Jerusalém, os irmãos nos receberam de muito boa vontade.', 17, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no dia seguinte, Paulo entrou conosco em casa de Tiago, e todos os anciãos vieram ali.', 18, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-os saudado, contou-lhes por miúdo o que por seu ministério Deus fizera entre os gentios.', 19, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo-o eles, glorificaram ao Senhor, e disseram-lhe: Bem vês, irmão, quantos milhares de judeus há que crêem, e todos são zeladores da lei.', 20, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E já acerca de ti foram informados de que ensinas todos os judeus que estão entre os gentios a apartarem-se de Moisés, dizendo que não devem circuncidar seus filhos, nem andar segundo o costume da lei.', 21, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que faremos pois? em todo o caso é necessário que a multidão se ajunte; porque terão ouvido que já és vindo.', 22, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Faze, pois, isto que te dizemos: Temos quatro homens que fizeram voto.', 23, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Toma estes contigo, e santifica-te com eles, e faze por eles os gastos para que rapem a cabeça, e todos ficarão sabendo que nada há daquilo de que foram informados acerca de ti, mas que também tu mesmo andas guardando a lei.', 24, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia, quanto aos que creem dos gentios, já nós havemos escrito, e achado por bem, que nada disto observem; mas que só se guardem do que se sacrifica aos ídolos, e do sangue, e do sufocado e da fornicação.', 25, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Paulo, tomando consigo aqueles homens, entrou no dia seguinte no templo, já santificado com eles, anunciando serem já cumpridos os dias da purificação; e ficou ali até se oferecer por cada um deles a oferta.', 26, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando os sete dias estavam quase a terminar, os judeus da Ásia, vendo-o no templo, alvoroçaram todo o povo e lançaram mão dele,', 27, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Clamando: Homens israelitas, acudi; este é o homem que por todas as partes ensina a todos contra o povo e contra a lei, e contra este lugar; e, demais disto, introduziu também no templo os gregos, e profanou este santo lugar.', 28, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tinham visto com ele na cidade a Trófimo de Éfeso, o qual pensavam que Paulo introduzira no templo.', 29, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alvoroçou-se toda a cidade, e houve grande concurso de povo; e, pegando Paulo, o arrastaram para fora do templo, e logo as portas se fecharam.', 30, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, procurando eles matá-lo, chegou ao tribuno da coorte o aviso de que Jerusalém estava toda em confusão;', 31, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, tomando logo consigo soldados e centuriões, correu para eles. E, quando viram o tribuno e os soldados, cessaram de ferir a Paulo.', 32, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, aproximando-se o tribuno, o prendeu e o mandou atar com duas cadeias, e lhe perguntou quem era e o que tinha feito.', 33, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E na multidão uns clamavam de uma maneira, outros de outra; mas, como nada podia saber ao certo, por causa do alvoroço, mandou conduzi-lo para a fortaleza.', 34, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sucedeu que, chegando às escadas, os soldados tiveram de lhe pegar por causa da violência da multidão.', 35, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a multidão do povo o seguia, clamando: Mata-o!', 36, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando iam a introduzir Paulo na fortaleza, disse Paulo ao tribuno: É-me permitido dizer-te alguma coisa? E ele disse: Sabes o grego?', 37, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não és tu porventura aquele egípcio que antes destes dias fez uma sedição e levou ao deserto quatro mil salteadores?', 38, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Paulo lhe disse: Na verdade que sou um homem judeu, cidadão de Tarso, cidade não pouco célebre na Cilícia; rogo-te, porém, que me permitas falar ao povo.', 39, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-lho permitido, Paulo, pondo-se em pé nas escadas, fez sinal com a mão ao povo; e, feito grande silêncio, falou-lhes em língua hebraica, dizendo:', 40, 21, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entrando, pois, Festo na província, subiu dali a três dias de Cesaréia a Jerusalém.', 1, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o sumo sacerdote e os principais dos judeus compareceram perante ele contra Paulo, e lhe rogaram,', 2, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pedindo como favor contra ele que o fizesse vir a Jerusalém, armando ciladas para o matarem no caminho.', 3, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Festo respondeu que Paulo estava guardado em Cesaréia, e que ele brevemente partiria para lá.', 4, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os que, pois, disse, dentre vós, têm poder, desçam comigo e, se neste homem houver algum crime, acusem-no.', 5, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-se demorado entre eles mais de dez dias, desceu a Cesaréia; e no dia seguinte, assentando-se no tribunal, mandou que trouxessem Paulo.', 6, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando ele, rodearam-no os judeus que haviam descido de Jerusalém, trazendo contra Paulo muitas e graves acusações, que não podiam provar.', 7, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, em sua defesa, disse: Eu não pequei em coisa alguma contra a lei dos judeus, nem contra o templo, nem contra César.', 8, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia Festo, querendo comprazer aos judeus, respondendo a Paulo, disse: Queres tu subir a Jerusalém, e ser lá perante mim julgado acerca destas coisas?', 9, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Paulo disse: Estou perante o tribunal de César, onde convém que seja julgado; não fiz agravo algum aos judeus, como tu muito bem sabes.', 10, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se fiz algum agravo, ou cometi alguma coisa digna de morte, não recuso morrer; mas, se nada há das coisas de que estes me acusam, ninguém me pode entregar a eles; apelo para César.', 11, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Festo, tendo falado com o conselho, respondeu: Apelaste para César? para César irás.', 12, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passados alguns dias, o rei Agripa e Berenice vieram a Cesaréia, a saudar Festo.', 13, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como ali ficassem muitos dias, Festo contou ao rei os negócios de Paulo, dizendo: Um certo homem foi deixado por Félix aqui preso,', 14, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por cujo respeito os principais dos sacerdotes e os anciãos dos judeus, estando eu em Jerusalém, compareceram perante mim, pedindo sentença contra ele.', 15, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aos quais respondi não ser costume dos romanos entregar algum homem à morte, sem que o acusado tenha presentes os seus acusadores, e possa defender-se da acusação.', 16, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que, chegando eles aqui juntos, no dia seguinte, sem fazer dilação alguma, assentado no tribunal, mandei que trouxessem o homem.', 17, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Acerca do qual, estando presentes os acusadores, nenhuma coisa apontaram daquelas que eu suspeitava.', 18, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tinham, porém, contra ele algumas questões acerca da sua superstição, e de um tal Jesus, morto, que Paulo afirmava viver.', 19, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando eu perplexo acerca da inquirição desta causa, disse se queria ir a Jerusalém, e lá ser julgado acerca destas coisas.', 20, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, apelando Paulo para que fosse reservado ao conhecimento de Augusto, mandei que o guardassem até que o envie a César.', 21, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Agripa disse a Festo: Bem quisera eu também ouvir esse homem. E ele disse: Amanhã o ouvirás.', 22, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, no dia seguinte, vindo Agripa e Berenice, com muito aparato, entraram no auditório com os tribunos e homens principais da cidade, sendo trazido Paulo por mandado de Festo.', 23, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Festo disse: Rei Agripa, e todos os senhores que estais presentes conosco; aqui vedes um homem de quem toda a multidão dos judeus me tem falado, tanto em Jerusalém como aqui, clamando que não convém que viva mais.', 24, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, achando eu que nenhuma coisa digna de morte fizera, e apelando ele mesmo também para Augusto, tenho determinado enviar-lho.', 25, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Do qual não tenho coisa alguma certa que escreva ao meu senhor, e por isso perante vós o trouxe, principalmente perante ti, ó rei Agripa, para que, depois de interrogado, tenha alguma coisa que escrever.', 26, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque me parece contra a razão enviar um preso, e não notificar contra ele as acusações.', 27, 25, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também Saulo consentiu na morte dele. E fez-se naquele dia uma grande perseguição contra a igreja que estava em Jerusalém; e todos foram dispersos pelas terras da Judéia e de Samaria, exceto os apóstolos.', 1, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E uns homens piedosos foram enterrar Estêvão, e fizeram sobre ele grande pranto.', 2, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Saulo assolava a igreja, entrando pelas casas; e, arrastando homens e mulheres, os encerrava na prisão.', 3, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os que andavam dispersos iam por toda a parte, anunciando a palavra.', 4, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, descendo Filipe à cidade de Samaria lhes pregava a Cristo.', 5, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as multidões unanimemente prestavam atenção ao que Filipe dizia, porque ouviam e viam os sinais que ele fazia;', 6, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois que os espíritos imundos saíam de muitos que os tinham, clamando em alta voz; e muitos paralíticos e coxos eram curados.', 7, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia grande alegria naquela cidade.', 8, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava ali um certo homem, chamado Simão, que anteriormente exercera naquela cidade a arte mágica, e tinha iludido o povo de Samaria, dizendo que era uma grande personagem;', 9, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao qual todos atendiam, desde o menor até ao maior, dizendo: Este é a grande virtude de Deus.', 10, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E atendiam-no, porque já desde muito tempo os havia iludido com artes mágicas.', 11, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, como cressem em Filipe, que lhes pregava acerca do reino de Deus, e do nome de Jesus Cristo, se batizavam, tanto homens como mulheres.', 12, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E creu até o próprio Simão; e, sendo batizado, ficou de contínuo com Filipe; e, vendo os sinais e as grandes maravilhas que se faziam, estava atônito.', 13, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os apóstolos, pois, que estavam em Jerusalém, ouvindo que Samaria recebera a palavra de Deus, enviaram para lá Pedro e João.', 14, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais, tendo descido, oraram por eles para que recebessem o Espírito Santo', 15, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Porque sobre nenhum deles tinha ainda descido; mas somente eram batizados em nome do Senhor Jesus).', 16, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então lhes impuseram as mãos, e receberam o Espírito Santo.', 17, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Simão, vendo que pela imposição das mãos dos apóstolos era dado o Espírito Santo, lhes ofereceu dinheiro,', 18, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Dai-me também a mim esse poder, para que aquele sobre quem eu puser as mãos receba o Espírito Santo.', 19, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas disse-lhe Pedro: O teu dinheiro seja contigo para perdição, pois cuidaste que o dom de Deus se alcança por dinheiro.', 20, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu não tens parte nem sorte nesta palavra, porque o teu coração não é reto diante de Deus.', 21, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Arrepende-te, pois, dessa tua iniqüidade, e ora a Deus, para que porventura te seja perdoado o pensamento do teu coração;', 22, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois vejo que estás em fel de amargura, e em laço de iniqüidade.', 23, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondendo, porém, Simão, disse: Orai vós por mim ao Senhor, para que nada do que dissestes venha sobre mim.', 24, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo eles, pois, testificado e falado a palavra do Senhor, voltaram para Jerusalém e em muitas aldeias dos samaritanos anunciaram o evangelho.', 25, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o anjo do Senhor falou a Filipe, dizendo: Levanta-te, e vai para o lado do sul, ao caminho que desce de Jerusalém para Gaza, que está deserta.', 26, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levantou-se, e foi; e eis que um homem etíope, eunuco, mordomo-mor de Candace, rainha dos etíopes, o qual era superintendente de todos os seus tesouros, e tinha ido a Jerusalém para adoração,', 27, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Regressava e, assentado no seu carro, lia o profeta Isaías.', 28, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse o Espírito a Filipe: Chega-te, e ajunta-te a esse carro.', 29, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, correndo Filipe, ouviu que lia o profeta Isaías, e disse: Entendes tu o que lês?', 30, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse: Como poderei entender, se alguém não me ensinar? E rogou a Filipe que subisse e com ele se assentasse.', 31, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o lugar da Escritura que lia era este: Foi levado como a ovelha para o matadouro; e, como está mudo o cordeiro diante do que o tosquia, Assim não abriu a sua boca.', 32, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na sua humilhação foi tirado o seu julgamento; E quem contará a sua geração? Porque a sua vida é tirada da terra.', 33, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo o eunuco a Filipe, disse: Rogo-te, de quem diz isto o profeta? De si mesmo, ou de algum outro?', 34, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Filipe, abrindo a sua boca, e começando nesta Escritura, lhe anunciou a Jesus.', 35, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo eles caminhando, chegaram ao pé de alguma água, e disse o eunuco: Eis aqui água; que impede que eu seja batizado?', 36, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Filipe: É lícito, se crês de todo o coração. E, respondendo ele, disse: Creio que Jesus Cristo é o Filho de Deus.', 37, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mandou parar o carro, e desceram ambos à água, tanto Filipe como o eunuco, e o batizou.', 38, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando saíram da água, o Espírito do Senhor arrebatou a Filipe, e não o viu mais o eunuco; e, jubiloso, continuou o seu caminho.', 39, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Filipe se achou em Azoto e, indo passando, anunciava o evangelho em todas as cidades, até que chegou a Cesaréia.', 40, 8, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E passando por Anfípolis e Apolônia, chegaram a Tessalônica, onde havia uma sinagoga de judeus.', 1, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Paulo, como tinha por costume, foi ter com eles; e por três sábados disputou com eles sobre as Escrituras,', 2, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Expondo e demonstrando que convinha que o Cristo padecesse e ressuscitasse dentre os mortos. E este Jesus, que vos anuncio, dizia ele, é o Cristo.', 3, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns deles creram, e ajuntaram-se com Paulo e Silas; e também uma grande multidão de gregos religiosos, e não poucas mulheres principais.', 4, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os judeus desobedientes, movidos de inveja, tomaram consigo alguns homens perversos, dentre os vadios e, ajuntando o povo, alvoroçaram a cidade, e assaltando a casa de Jasom, procuravam trazê-los para junto do povo.', 5, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, não os achando, trouxeram Jasom e alguns irmãos à presença dos magistrados da cidade, clamando: Estes que têm alvoroçado o mundo, chegaram também aqui;', 6, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais Jasom recolheu; e todos estes procedem contra os decretos de César, dizendo que há outro rei, Jesus.', 7, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alvoroçaram a multidão e os principais da cidade, que ouviram estas coisas.', 8, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo, porém, recebido satisfação de Jasom e dos demais, os soltaram.', 9, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo os irmãos enviaram de noite Paulo e Silas a Beréia; e eles, chegando lá, foram à sinagoga dos judeus.', 10, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, estes foram mais nobres do que os que estavam em Tessalônica, porque de bom grado receberam a palavra, examinando cada dia nas Escrituras se estas coisas eram assim.', 11, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que creram muitos deles, e também mulheres gregas da classe nobre, e não poucos homens.', 12, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, logo que os judeus de Tessalônica souberam que a palavra de Deus também era anunciada por Paulo em Beréia, foram lá, e excitaram as multidões.', 13, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No mesmo instante os irmãos mandaram a Paulo que fosse até ao mar, mas Silas e Timóteo ficaram ali.', 14, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que acompanhavam Paulo o levaram até Atenas, e, recebendo ordem para que Silas e Timóteo fossem ter com ele o mais depressa possível, partiram.', 15, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, enquanto Paulo os esperava em Atenas, o seu espírito se comovia em si mesmo, vendo a cidade tão entregue à idolatria.', 16, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que disputava na sinagoga com os judeus e religiosos, e todos os dias na praça com os que se apresentavam.', 17, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns dos filósofos epicureus e estóicos contendiam com ele; e uns diziam: Que quer dizer este paroleiro? E outros: Parece que é pregador de deuses estranhos; porque lhes anunciava a Jesus e a ressurreição.', 18, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tomando-o, o levaram ao Areópago, dizendo: Poderemos nós saber que nova doutrina é essa de que falas?', 19, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois coisas estranhas nos trazes aos ouvidos; queremos pois saber o que vem a ser isto', 20, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Pois todos os atenienses e estrangeiros residentes, de nenhuma outra coisa se ocupavam, senão de dizer e ouvir alguma novidade).', 21, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando Paulo no meio do Areópago, disse: Homens atenienses, em tudo vos vejo um tanto supersticiosos;', 22, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, passando eu e vendo os vossos santuários, achei também um altar em que estava escrito: AO DEUS DESCONHECIDO. Esse, pois, que vós honrais, não o conhecendo, é o que eu vos anuncio.', 23, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Deus que fez o mundo e tudo que nele há, sendo Senhor do céu e da terra, não habita em templos feitos por mãos de homens;', 24, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem tampouco é servido por mãos de homens, como que necessitando de alguma coisa; pois ele mesmo é quem dá a todos a vida, e a respiração, e todas as coisas;', 25, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de um só sangue fez toda a geração dos homens, para habitar sobre toda a face da terra, determinando os tempos já dantes ordenados, e os limites da sua habitação;', 26, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que buscassem ao Senhor, se porventura, tateando, o pudessem achar; ainda que não está longe de cada um de nós;', 27, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nele vivemos, e nos movemos, e existimos; como também alguns dos vossos poetas disseram: Pois somos também sua geração.', 28, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo nós, pois, geração de Deus, não havemos de cuidar que a divindade seja semelhante ao ouro, ou à prata, ou à pedra esculpida por artifício e imaginação dos homens.', 29, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus, não tendo em conta os tempos da ignorância, anuncia agora a todos os homens, e em todo o lugar, que se arrependam;', 30, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto tem determinado um dia em que com justiça há de julgar o mundo, por meio do homem que destinou; e disso deu certeza a todos, ressuscitando-o dentre os mortos.', 31, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como ouviram falar da ressurreição dos mortos, uns escarneciam, e outros diziam: Acerca disso te ouviremos outra vez.', 32, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim Paulo saiu do meio deles.', 33, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia, chegando alguns homens a ele, creram; entre os quais foi Dionísio, areopagita, uma mulher por nome Dâmaris, e com eles outros.', 34, 17, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois disto partiu Paulo de Atenas, e chegou a Corinto.', 1, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, achando um certo judeu por nome Áqüila, natural do Ponto, que havia pouco tinha vindo da Itália, e Priscila, sua mulher (pois Cláudio tinha mandado que todos os judeus saíssem de Roma), ajuntou-se com eles,', 2, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como era do mesmo ofício, ficou com eles, e trabalhava; pois tinham por ofício fazer tendas.', 3, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os sábados disputava na sinagoga, e convencia a judeus e gregos.', 4, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando Silas e Timóteo desceram da Macedônia, foi Paulo impulsionado no espírito, testificando aos judeus que Jesus era o Cristo.', 5, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, resistindo e blasfemando eles, sacudiu as vestes, e disse-lhes: O vosso sangue seja sobre a vossa cabeça; eu estou limpo, e desde agora parto para os gentios.', 6, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo dali, entrou em casa de um homem chamado chamado Justo, que servia a Deus, e cuja casa estava junto da sinagoga.', 7, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Crispo, principal da sinagoga, creu no Senhor com toda a sua casa; e muitos dos coríntios, ouvindo-o, creram e foram batizados.', 8, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse o Senhor em visão a Paulo: Não temas, mas fala, e não te cales;', 9, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu sou contigo, e ninguém lançará mão de ti para te fazer mal, pois tenho muito povo nesta cidade.', 10, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ficou ali um ano e seis meses, ensinando entre eles a palavra de Deus.', 11, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, sendo Gálio procônsul da Acaia, levantaram-se os judeus concordemente contra Paulo, e o levaram ao tribunal,', 12, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Este persuade os homens a servir a Deus contra a lei.', 13, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, querendo Paulo abrir a boca, disse Gálio aos judeus: Se houvesse, ó judeus, algum agravo ou crime enorme, com razão vos sofreria,', 14, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se a questão é de palavras, e de nomes, e da lei que entre vós há, vede-o vós mesmos; porque eu não quero ser juiz dessas coisas.', 15, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E expulsou-os do tribunal.', 16, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então todos os gregos agarraram Sóstenes, principal da sinagoga, e o feriram diante do tribunal; e a Gálio nada destas coisas o incomodava.', 17, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Paulo, ficando ainda ali muitos dias, despediu-se dos irmãos, e dali navegou para a Síria, e com ele Priscila e Áqüila, tendo rapado a cabeça em Cencréia, porque tinha voto.', 18, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegou a Éfeso, e deixou-os ali; mas ele, entrando na sinagoga, disputava com os judeus.', 19, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, rogando-lhe eles que ficasse por mais algum tempo, não conveio nisso.', 20, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes se despediu deles, dizendo: É-me de todo preciso celebrar a solenidade que vem em Jerusalém; mas querendo Deus, outra vez voltarei a vós. E partiu de Éfeso.', 21, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando a Cesaréia, subiu a Jerusalém e, saudando a igreja, desceu a Antioquia.', 22, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando ali algum tempo, partiu, passando sucessivamente pela província da Galácia e da Frígia, confirmando a todos os discípulos.', 23, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegou a Éfeso um certo judeu chamado Apolo, natural de Alexandria, homem eloqüente e poderoso nas Escrituras.', 24, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este era instruído no caminho do Senhor e, fervoroso de espírito, falava e ensinava diligentemente as coisas do Senhor, conhecendo somente o batismo de João.', 25, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele começou a falar ousadamente na sinagoga; e, quando o ouviram Priscila e Aqüila, o levaram consigo e lhe declararam mais precisamente o caminho de Deus.', 26, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Querendo ele passar à Acaia, o animaram os irmãos, e escreveram aos discípulos que o recebessem; o qual, tendo chegado, aproveitou muito aos que pela graça criam.', 27, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque com grande veemência, convencia publicamente os judeus, mostrando pelas Escrituras que Jesus era o Cristo.', 28, 18, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, cumprindo-se o dia de Pentecostes, estavam todos concordemente no mesmo lugar;', 1, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de repente veio do céu um som, como de um vento veemente e impetuoso, e encheu toda a casa em que estavam assentados.', 2, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram vistas por eles línguas repartidas, como que de fogo, as quais pousaram sobre cada um deles.', 3, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos foram cheios do Espírito Santo, e começaram a falar noutras línguas, conforme o Espírito Santo lhes concedia que falassem.', 4, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E em Jerusalém estavam habitando judeus, homens religiosos, de todas as nações que estão debaixo do céu.', 5, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando aquele som ocorreu, ajuntou-se uma multidão, e estava confusa, porque cada um os ouvia falar na sua própria língua.', 6, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos pasmavam e se maravilhavam, dizendo uns aos outros: Pois quê! não são galileus todos esses homens que estão falando?', 7, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como, pois, os ouvimos, cada um, na nossa própria língua em que somos nascidos?', 8, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Partos e medos, elamitas e os que habitam na Mesopotâmia, Judéia, Capadócia, Ponto e Asia,', 9, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Frígia e Panfília, Egito e partes da Líbia, junto a Cirene, e forasteiros romanos, tanto judeus como prosélitos,', 10, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cretenses e árabes, todos nós temos ouvido em nossas próprias línguas falar das grandezas de Deus.', 11, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos se maravilhavam e estavam suspensos, dizendo uns para os outros: Que quer isto dizer?', 12, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outros, zombando, diziam: Estão cheios de mosto.', 13, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pedro, porém, pondo-se em pé com os onze, levantou a sua voz, e disse-lhes: Homens judeus, e todos os que habitais em Jerusalém, seja-vos isto notório, e escutai as minhas palavras.', 14, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes homens não estão embriagados, como vós pensais, sendo a terceira hora do dia.', 15, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas isto é o que foi dito pelo profeta Joel:', 16, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nos últimos dias acontecerá, diz Deus, Que do meu Espírito derramarei sobre toda a carne; E os vossos filhos e as vossas filhas profetizarão, Os vossos jovens terão visões, E os vossos velhos sonharão sonhos;', 17, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também do meu Espírito derramarei sobre os meus servos e as minhas servas naqueles dias, e profetizarão;', 18, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E farei aparecer prodígios em cima, no céu; E sinais em baixo na terra, Sangue, fogo e vapor de fumo.', 19, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O sol se converterá em trevas, E a lua em sangue, Antes de chegar o grande e glorioso dia do Senhor;', 20, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E acontecerá que todo aquele que invocar o nome do Senhor será salvo.', 21, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Homens israelitas, escutai estas palavras: A Jesus Nazareno, homem aprovado por Deus entre vós com maravilhas, prodígios e sinais, que Deus por ele fez no meio de vós, como vós mesmos bem sabeis;', 22, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A este que vos foi entregue pelo determinado conselho e presciência de Deus, prendestes, crucificastes e matastes pelas mãos de injustos;', 23, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao qual Deus ressuscitou, soltas as ânsias da morte, pois não era possível que fosse retido por ela;', 24, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque dele disse Davi: Sempre via diante de mim o Senhor, Porque está à minha direita, para que eu não seja comovido;', 25, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso se alegrou o meu coração, e a minha língua exultou; E ainda a minha carne há de repousar em esperança;', 26, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois não deixarás a minha alma no inferno, Nem permitirás que o teu Santo veja a corrupção;', 27, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fizeste-me conhecidos os caminhos da vida; Com a tua face me encherás de júbilo.', 28, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Homens irmãos, seja-me lícito dizer-vos livremente acerca do patriarca Davi, que ele morreu e foi sepultado, e entre nós está até hoje a sua sepultura.', 29, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo, pois, ele profeta, e sabendo que Deus lhe havia prometido com juramento que do fruto de seus lombos, segundo a carne, levantaria o Cristo, para o assentar sobre o seu trono,', 30, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nesta previsão, disse da ressurreição de Cristo, que a sua alma não foi deixada no inferno, nem a sua carne viu a corrupção.', 31, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deus ressuscitou a este Jesus, do que todos nós somos testemunhas.', 32, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que, exaltado pela destra de Deus, e tendo recebido do Pai a promessa do Espírito Santo, derramou isto que vós agora vedes e ouvis.', 33, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Davi não subiu aos céus, mas ele próprio diz:Disse o Senhor ao meu Senhor:Assenta-te à minha direita,', 34, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Até que ponha os teus inimigos por escabelo de teus pés.', 35, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saiba pois com certeza toda a casa de Israel que a esse Jesus, a quem vós crucificastes, Deus o fez Senhor e Cristo.', 36, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo eles isto, compungiram-se em seu coração, e perguntaram a Pedro e aos demais apóstolos: Que faremos, homens irmãos?', 37, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes Pedro: Arrependei-vos, e cada um de vós seja batizado em nome de Jesus Cristo, para perdão dos pecados; e recebereis o dom do Espírito Santo;', 38, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a promessa vos diz respeito a vós, a vossos filhos, e a todos os que estão longe, a tantos quantos Deus nosso Senhor chamar.', 39, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E com muitas outras palavras isto testificava, e os exortava, dizendo: Salvai-vos desta geração perversa.', 40, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que foram batizados os que de bom grado receberam a sua palavra; e naquele dia agregaram-se quase três mil almas,', 41, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perseveravam na doutrina dos apóstolos, e na comunhão, e no partir do pão, e nas orações.', 42, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E em toda a alma havia temor, e muitas maravilhas e sinais se faziam pelos apóstolos.', 43, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os que criam estavam juntos, e tinham tudo em comum.', 44, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vendiam suas propriedades e bens, e repartiam com todos, segundo cada um havia de mister.', 45, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, perseverando unânimes todos os dias no templo, e partindo o pão em casa, comiam juntos com alegria e singeleza de coração,', 46, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Louvando a Deus, e caindo na graça de todo o povo. E todos os dias acrescentava o Senhor à igreja aqueles que se haviam de salvar.', 47, 2, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, apóstolo de Jesus Cristo, pela vontade de Deus, segundo a promessa da vida que está em Cristo Jesus,', 1, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A Timóteo, meu amado filho: Graça, misericórdia, e paz da parte de Deus Pai, e da de Cristo Jesus, Senhor nosso.', 2, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dou graças a Deus, a quem desde os meus antepassados sirvo com uma consciência pura, de que sem cessar faço memória de ti nas minhas orações noite e dia;', 3, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desejando muito ver-te, lembrando-me das tuas lágrimas, para me encher de gozo;', 4, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Trazendo à memória a fé não fingida que em ti há, a qual habitou primeiro em tua avó Lóide, e em tua mãe Eunice, e estou certo de que também habita em ti.', 5, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por cujo motivo te lembro que despertes o dom de Deus que existe em ti pela imposição das minhas mãos.', 6, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus não nos deu o espírito de temor, mas de fortaleza, e de amor, e de moderação.', 7, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, não te envergonhes do testemunho de nosso Senhor, nem de mim, que sou prisioneiro seu; antes participa das aflições do evangelho segundo o poder de Deus,', 8, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que nos salvou, e chamou com uma santa vocação; não segundo as nossas obras, mas segundo o seu próprio propósito e graça que nos foi dada em Cristo Jesus antes dos tempos dos séculos;', 9, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que é manifesta agora pela aparição de nosso Salvador Jesus Cristo, o qual aboliu a morte, e trouxe à luz a vida e a incorrupção pelo evangelho;', 10, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para o que fui constituído pregador, e apóstolo, e doutor dos gentios.', 11, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por cuja causa padeço também isto, mas não me envergonho; porque eu sei em quem tenho crido, e estou certo de que é poderoso para guardar o meu depósito até àquele dia.', 12, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conserva o modelo das sãs palavras que de mim tens ouvido, na fé e no amor que há em Cristo Jesus.', 13, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Guarda o bom depósito pelo Espírito Santo que habita em nós.', 14, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem sabes isto, que os que estão na Ásia todos se apartaram de mim; entre os quais foram Figelo e Hermógenes.', 15, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Senhor conceda misericórdia à casa de Onesíforo, porque muitas vezes me recreou, e não se envergonhou das minhas cadeias.', 16, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, vindo ele a Roma, com muito cuidado me procurou e me achou.', 17, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Senhor lhe conceda que naquele dia ache misericórdia diante do Senhor. E, quanto me ajudou em Éfeso, melhor o sabes tu.', 18, 1, 16);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Homens, irmàos e pais, ouvi agora a minha defesa perante vós', 1, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(E, quando ouviram falar-lhes em língua hebraica, maior silêncio guardaram). E disse:', 2, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto a mim, sou judeu, nascido em Tarso da Cilícia, e nesta cidade criado aos pés de Gamaliel, instruído conforme a verdade da lei de nossos pais, zelador de Deus, como todos vós hoje sois.', 3, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E persegui este caminho até à morte, prendendo, e pondo em prisões, tanto homens como mulheres,', 4, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como também o sumo sacerdote me é testemunha, e todo o conselho dos anciãos. E, recebendo destes cartas para os irmãos, fui a Damasco, para trazer maniatados para Jerusalém aqueles que ali estivessem, a fim de que fossem castigados.', 5, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, aconteceu que, indo eu já de caminho, e chegando perto de Damasco, quase ao meio-dia, de repente me rodeou uma grande luz do céu.', 6, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E caí por terra, e ouvi uma voz que me dizia: Saulo, Saulo, por que me persegues?', 7, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu respondi: Quem és, Senhor? E disse-me: Eu sou Jesus Nazareno, a quem tu persegues.', 8, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que estavam comigo viram, em verdade, a luz, e se atemorizaram muito, mas não ouviram a voz daquele que falava comigo.', 9, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então disse eu: Senhor, que farei? E o Senhor disse-me: Levanta-te, e vai a Damasco, e ali se te dirá tudo o que te é ordenado fazer.', 10, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como eu não via, por causa do esplendor daquela luz, fui levado pela mão dos que estavam comigo, e cheguei a Damasco.', 11, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um certo Ananias, homem piedoso conforme a lei, que tinha bom testemunho de todos os judeus que ali moravam,', 12, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vindo ter comigo, e apresentando-se, disse-me: Saulo, irmão, recobra a vista. E naquela mesma hora o vi.', 13, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse: O Deus de nossos pais de antemão te designou para que conheças a sua vontade, e vejas aquele Justo e ouças a voz da sua boca.', 14, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque hás de ser sua testemunha para com todos os homens do que tens visto e ouvido.', 15, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora por que te deténs? Levanta-te, e batiza-te, e lava os teus pecados, invocando o nome do Senhor.', 16, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, tornando eu para Jerusalém, quando orava no templo, fui arrebatado para fora de mim.', 17, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi aquele que me dizia: Dá-te pressa e sai apressadamente de Jerusalém; porque não receberão o teu testemunho acerca de mim.', 18, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu disse: Senhor, eles bem sabem que eu lançava na prisão e açoitava nas sinagogas os que criam em ti.', 19, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando o sangue de Estêvão, tua testemunha, se derramava, também eu estava presente, e consentia na sua morte, e guardava as capas dos que o matavam.', 20, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-me: Vai, porque hei de enviar-te aos gentios de longe.', 21, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouviram-no até esta palavra, e levantaram a voz, dizendo: Tira da terra um tal homem, porque não convém que viva.', 22, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, clamando eles, e arrojando de si as vestes, e lançando pó para o ar,', 23, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O tribuno mandou que o levassem para a fortaleza, dizendo que o examinassem com açoites, para saber por que causa assim clamavam contra ele.', 24, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando o estavam atando com correias, disse Paulo ao centurião que ali estava: É-vos lícito açoitar um romano, sem ser condenado?', 25, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo isto, o centurião foi, e anunciou ao tribuno, dizendo: Vê o que vais fazer, porque este homem é romano.', 26, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vindo o tribuno, disse-lhe: Dize-me, és tu romano? E ele disse: Sim.', 27, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E respondeu o tribuno: Eu com grande soma de dinheiro alcancei este direito de cidadão. Paulo disse: Mas eu o sou de nascimento.', 28, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo dele se apartaram os que o haviam de examinar; e até o tribuno teve temor, quando soube que era romano, visto que o tinha ligado.', 29, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no dia seguinte, querendo saber ao certo a causa por que era acusado pelos judeus, soltou-o das prisões, e mandou vir o principais dos sacerdotes, e todo o seu conselho; e, trazendo Paulo, o apresentou diante deles.', 30, 22, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amados, não creiais a todo o espírito, mas provai se os espíritos são de Deus, porque já muitos falsos profetas se têm levantado no mundo.', 1, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto conhecereis o Espírito de Deus: Todo o espírito que confessa que Jesus Cristo veio em carne é de Deus;', 2, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todo o espírito que não confessa que Jesus Cristo veio em carne não é de Deus; mas este é o espírito do anticristo, do qual já ouvistes que há de vir, e eis que já agora está no mundo.', 3, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Filhinhos, sois de Deus, e já os tendes vencido; porque maior é o que está em vós do que o que está no mundo.', 4, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Do mundo são, por isso falam do mundo, e o mundo os ouve.', 5, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nós somos de Deus; aquele que conhece a Deus ouve-nos; aquele que não é de Deus não nos ouve. Nisto conhecemos nós o espírito da verdade e o espírito do erro.', 6, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amados, amemo-nos uns aos outros; porque o amor é de Deus; e qualquer que ama é nascido de Deus e conhece a Deus.', 7, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que não ama não conhece a Deus; porque Deus é amor.', 8, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto se manifestou o amor de Deus para conosco: que Deus enviou seu Filho unigênito ao mundo, para que por ele vivamos.', 9, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto está o amor, não em que nós tenhamos amado a Deus, mas em que ele nos amou a nós, e enviou seu Filho para propiciação pelos nossos pecados.', 10, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amados, se Deus assim nos amou, também nòs devemos amar uns aos outros.', 11, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém jamais viu a Deus; se nos amamos uns aos outros, Deus está em nós, e em nós é perfeito o seu amor.', 12, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto conhecemos que estamos nele, e ele em nós, pois que nos deu do seu Espírito.', 13, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vimos, e testificamos que o Pai enviou seu Filho para Salvador do mundo.', 14, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que confessar que Jesus é o Filho de Deus, Deus está nele, e ele em Deus.', 15, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nós conhecemos, e cremos no amor que Deus nos tem. Deus é amor; e quem está em amor está em Deus, e Deus nele.', 16, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto é perfeito o amor para conosco, para que no dia do juízo tenhamos confiança; porque, qual ele é, somos nós também neste mundo.', 17, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No amor não há temor, antes o perfeito amor lança fora o temor; porque o temor tem consigo a pena, e o que teme não é perfeito em amor.', 18, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nós o amamos a ele porque ele nos amou primeiro.', 19, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém diz: Eu amo a Deus, e odeia a seu irmão, é mentiroso. Pois quem não ama a seu irmão, ao qual viu, como pode amar a Deus, a quem não viu?', 20, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dele temos este mandamento: que quem ama a Deus, ame também a seu irmão.', 21, 4, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então alguns que tinham descido da Judéia ensinavam assim os irmãos: Se não vos circuncidardes conforme o uso de Moisés, não podeis salvar-vos.', 1, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo tido Paulo e Barnabé não pequena discussão e contenda contra eles, resolveu-se que Paulo e Barnabé, e alguns dentre eles, subissem a Jerusalém, aos apóstolos e aos anciãos, sobre aquela questão.', 2, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles, sendo acompanhados pela igreja, passavam pela Fenícia e por Samaria, contando a conversão dos gentios; e davam grande alegria a todos os irmãos.', 3, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando chegaram a Jerusalém, foram recebidos pela igreja e pelos apóstolos e anciãos, e lhes anunciaram quão grandes coisas Deus tinha feito com eles.', 4, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Alguns, porém, da seita dos fariseus, que tinham crido, se levantaram, dizendo que era mister circuncidá-los e mandar-lhes que guardassem a lei de Moisés.', 5, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Congregaram-se, pois, os apóstolos e os anciãos para considerar este assunto.', 6, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo grande contenda, levantou-se Pedro e disse-lhes: Homens irmãos, bem sabeis que já há muito tempo Deus me elegeu dentre nós, para que os gentios ouvissem da minha boca a palavra do evangelho, e cressem.', 7, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Deus, que conhece os corações, lhes deu testemunho, dando-lhes o Espírito Santo, assim como também a nós;', 8, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não fez diferença alguma entre eles e nós, purificando os seus corações pela fé.', 9, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora, pois, por que tentais a Deus, pondo sobre a cerviz dos discípulos um jugo que nem nossos pais nem nós pudemos suportar?', 10, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas cremos que seremos salvos pela graça do Senhor Jesus Cristo, como eles também.', 11, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então toda a multidão se calou e escutava a Barnabé e a Paulo, que contavam quão grandes sinais e prodígios Deus havia feito por meio deles entre os gentios.', 12, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-se eles calado, tomou Tiago a palavra, dizendo: Homens irmãos, ouvi-me:', 13, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Simão relatou como primeiramente Deus visitou os gentios, para tomar deles um povo para o seu nome.', 14, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E com isto concordam as palavras dos profetas; como está escrito:', 15, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disto voltarei,e reedificarei o tabernáculo de Davi, que está caído, levantá-lo-ei das suas ruínas, e tornarei a edificá-lo.', 16, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que o restante dos homens busque ao Senhor,e todos os gentios, sobre os quais o meu nome é invocado,diz o Senhor, que faz todas estas coisas,', 17, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conhecidas sào a Deus, desde o princípio do mundo, todas as suas obras.', 18, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso julgo que não se deve perturbar aqueles, dentre os gentios, que se convertem a Deus.', 19, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas escrever-lhes que se abstenham das contaminações dos ídolos, da fornicação, do que é sufocado e do sangue.', 20, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Moisés, desde os tempos antigos, tem em cada cidade quem o pregue, e cada sábado é lido nas sinagogas.', 21, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então pareceu bem aos apóstolos e aos anciãos, com toda a igreja, eleger homens dentre eles e enviá-los com Paulo e Barnabé a Antioquia, a saber: Judas, chamado Barsabás, e Silas, homens distintos entre os irmãos.', 22, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por intermédio deles escreveram o seguinte: Os apóstolos, e os anciãos e os irmãos, aos irmãos dentre os gentios que estão em Antioquia, e Síria e Cilícia, saúde.', 23, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto ouvimos que alguns que saíram dentre nós vos perturbaram com palavras, e transtornaram as vossas almas, dizendo que deveis circuncidar-vos e guardar a lei, não lhes tendo nós dado mandamento,', 24, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pareceu-nos bem, reunidos concordemente, eleger alguns homens e enviá-los com os nossos amados Barnabé e Paulo,', 25, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Homens que já expuseram as suas vidas pelo nome de nosso Senhor Jesus Cristo.', 26, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Enviamos, portanto, Judas e Silas, os quais por palavra vos anunciarão também as mesmas coisas.', 27, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade pareceu bem ao Espírito Santo e a nós, não vos impor mais encargo algum, senão estas coisas necessárias:', 28, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que vos abstenhais das coisas sacrificadas aos ídolos, e do sangue, e da carne sufocada, e da fornicação, das quais coisas bem fazeis se vos guardardes. Bem vos vá.', 29, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo eles então se despedido, partiram para Antioquia e, ajuntando a multidão, entregaram a carta.', 30, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando a leram, alegraram-se pela exortação.', 31, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois Judas e Silas, que também eram profetas, exortaram e confirmaram os irmãos com muitas palavras.', 32, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, detendo-se ali algum tempo, os irmãos os deixaram voltar em paz para os apóstolos;', 33, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas pareceu bem a Silas ficar ali.', 34, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Paulo e Barnabé ficaram em Antioquia, ensinando e pregando, com muitos outros, a palavra do Senhor.', 35, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns dias depois, disse Paulo a Barnabé: Tornemos a visitar nossos irmãos por todas as cidades em que já anunciamos a palavra do Senhor, para ver como estão.', 36, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Barnabé aconselhava que tomassem consigo a João, chamado Marcos.', 37, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a Paulo parecia razoável que não tomassem consigo aquele que desde a Panfília se tinha apartado deles e não os acompanhou naquela obra.', 38, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tal contenda houve entre eles, que se apartaram um do outro. Barnabé, levando consigo a Marcos, navegou para Chipre.', 39, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Paulo, tendo escolhido a Silas, partiu, encomendado pelos irmãos à graça de Deus.', 40, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E passou pela Síria e Cilícia, confirmando as igrejas.', 41, 15, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegou a Derbe e Listra. E eis que estava ali um certo discípulo por nome Timóteo, filho de uma judia que era crente, mas de pai grego;', 1, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Do qual davam bom testemunho os irmãos que estavam em Listra e em Icônio.', 2, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo quis que este fosse com ele; e tomando-o, o circuncidou, por causa dos judeus que estavam naqueles lugares; porque todos sabiam que seu pai era grego.', 3, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando iam passando pelas cidades, lhes entregavam, para serem observados, os decretos que haviam sido estabelecidos pelos apóstolos e anciãos em Jerusalém.', 4, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que as igrejas eram confirmadas na fé, e cada dia cresciam em número.', 5, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passando pela Frígia e pela província da Galácia, foram impedidos pelo Espírito Santo de anunciar a palavra na Ásia.', 6, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando chegaram a Mísia, intentavam ir para Bitínia, mas o Espírito não lho permitiu.', 7, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo passado por Mísia, desceram a Trôade.', 8, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Paulo teve de noite uma visão, em que se apresentou um homem da Macedônia, e lhe rogou, dizendo: Passa à Macedônia, e ajuda-nos.', 9, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo depois desta visão, procuramos partir para a Macedônia, concluindo que o Senhor nos chamava para lhes anunciarmos o evangelho.', 10, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, navegando de Trôade, fomos correndo em caminho direito para a Samotrácia e, no dia seguinte, para Neápolis;', 11, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dali para Filipos, que é a primeira cidade desta parte da Macedônia, e é uma colônia; e estivemos alguns dias nesta cidade.', 12, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no dia de sábado saímos fora das portas, para a beira do rio, onde se costumava fazer oração; e, assentando-nos, falamos às mulheres que ali se ajuntaram.', 13, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E uma certa mulher, chamada Lídia, vendedora de púrpura, da cidade de Tiatira, e que servia a Deus, nos ouvia, e o Senhor lhe abriu o coração para que estivesse atenta ao que Paulo dizia.', 14, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois que foi batizada, ela e a sua casa, nos rogou, dizendo: Se haveis julgado que eu seja fiel ao Senhor, entrai em minha casa, e ficai ali. E nos constrangeu a isso.', 15, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, indo nós à oração, nos saiu ao encontro uma jovem, que tinha espírito de adivinhação, a qual, adivinhando, dava grande lucro aos seus senhores.', 16, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esta, seguindo a Paulo e a nós, clamava, dizendo: Estes homens, que nos anunciam o caminho da salvação, são servos do Deus Altíssimo.', 17, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E isto fez ela por muitos dias. Mas Paulo, perturbado, voltou-se e disse ao espírito: Em nome de Jesus Cristo, te mando que saias dela. E na mesma hora saiu.', 18, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo seus senhores que a esperança do seu lucro estava perdida, prenderam Paulo e Silas, e os levaram à praça, à presença dos magistrados.', 19, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, apresentando-os aos magistrados, disseram: Estes homens, sendo judeus, perturbaram a nossa cidade,', 20, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nos expõem costumes que não nos é lícito receber nem praticar, visto que somos romanos.', 21, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a multidão se levantou unida contra eles, e os magistrados, rasgando-lhes as vestes, mandaram açoitá-los com varas.', 22, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-lhes dado muitos açoites, os lançaram na prisão, mandando ao carcereiro que os guardasse com segurança.', 23, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, tendo recebido tal ordem, os lançou no cárcere interior, e lhes segurou os pés no tronco.', 24, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, perto da meia-noite, Paulo e Silas oravam e cantavam hinos a Deus, e os outros presos os escutavam.', 25, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de repente sobreveio um tão grande terremoto, que os alicerces do cárcere se moveram, e logo se abriram todas as portas, e foram soltas as prisões de todos.', 26, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, acordando o carcereiro, e vendo abertas as portas da prisão, tirou a espada, e quis matar-se, cuidando que os presos já tinham fugido.', 27, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Paulo clamou com grande voz, dizendo: Não te faças nenhum mal, que todos aqui estamos.', 28, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pedindo luz, saltou dentro e, todo trêmulo, se prostrou ante Paulo e Silas.', 29, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tirando-os para fora, disse: Senhores, que é necessário que eu faça para me salvar?', 30, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles disseram: Crê no Senhor Jesus Cristo e serás salvo, tu e a tua casa.', 31, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lhe pregavam a palavra do Senhor, e a todos os que estavam em sua casa.', 32, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando-os ele consigo naquela mesma hora da noite, lavou-lhes os vergões; e logo foi batizado, ele e todos os seus.', 33, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levando-os à sua casa, lhes pôs a mesa; e, na sua crença em Deus, alegrou-se com toda a sua casa.', 34, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo já dia, os magistrados mandaram quadrilheiros, dizendo: Soltai aqueles homens.', 35, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o carcereiro anunciou a Paulo estas palavras, dizendo: Os magistrados mandaram que vos soltasse; agora, pois, saí e ide em paz.', 36, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Paulo replicou: Açoitaram-nos publicamente e, sem sermos condenados, sendo homens romanos, nos lançaram na prisão, e agora encobertamente nos lançam fora? Não será assim; mas venham eles mesmos e tirem-nos para fora.', 37, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os quadrilheiros foram dizer aos magistrados estas palavras; e eles temeram, ouvindo que eram romanos.', 38, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vindo, lhes dirigiram súplicas; e, tirando-os para fora, lhes pediram que saíssem da cidade.', 39, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo da prisão, entraram em casa de Lídia e, vendo os irmãos, os confortaram, e depois partiram.', 40, 16, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo escapado, então souberam que a ilha se chamava Malta.', 1, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os bárbaros usaram conosco de não pouca humanidade; porque, acendendo uma grande fogueira, nos recolheram a todos por causa da chuva que caía, e por causa do frio.', 2, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo Paulo ajuntado uma quantidade de vides, e pondo-as no fogo, uma víbora, fugindo do calor, lhe acometeu a mão.', 3, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os bárbaros, vendo-lhe a víbora pendurada na mão, diziam uns aos outros: Certamente este homem é homicida, visto como, escapando do mar, a justiça não o deixa viver.', 4, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, sacudindo ele a víbora no fogo, não sofreu nenhum mal.', 5, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles esperavam que viesse a inchar ou a cair morto de repente; mas tendo esperado já muito, e vendo que nenhum incômodo lhe sobrevinha, mudando de parecer, diziam que era um deus.', 6, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ali, próximo daquele lugar, havia umas herdades que pertenciam ao principal da ilha, por nome Públio, o qual nos recebeu e hospedou benignamente por três dias.', 7, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu estar de cama enfermo de febre e disenteria o pai de Públio, que Paulo foi ver, e, havendo orado, pôs as mãos sobre ele, e o curou.', 8, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Feito, pois, isto, vieram também ter com ele os demais que na ilha tinham enfermidades, e sararam.', 9, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais nos distinguiram também com muitas honras; e, havendo de navegar, nos proveram das coisas necessárias.', 10, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E três meses depois partimos num navio de Alexandria que invernara na ilha, o qual tinha por insígnia Castor e Pólux.', 11, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando a Siracusa, ficamos ali três dias.', 12, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De onde, indo costeando, viemos a Régio; e soprando, um dia depois, um vento do sul, chegamos no segundo dia a Potéoli.', 13, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde, achando alguns irmãos, nos rogaram que por sete dias ficássemos com eles; e depois nos dirigimos a Roma.', 14, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de lá, ouvindo os irmãos novas de nós, nos saíram ao encontro à Praça de Ápio e às Três Vendas, e Paulo, vendo-os, deu graças a Deus e tomou ânimo.', 15, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo que chegamos a Roma, o centurião entregou os presos ao capitào da guarda; mas a Paulo se lhe permitiu morar por sua conta à parte, com o soldado que o guardava.', 16, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, três dias depois, Paulo convocou os principais dos judeus, e, juntos eles, lhes disse: Homens irmãos, não havendo eu feito nada contra o povo, ou contra os ritos paternos, vim contudo preso desde Jerusalém, entregue nas mãos dos romanos;', 17, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais, havendo-me examinado, queriam soltar-me, por não haver em mim crime algum de morte.', 18, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, opondo-se os judeus, foi-me forçoso apelar para César, não tendo, contudo, de que acusar a minha nação.', 19, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por esta causa vos chamei, para vos ver e falar; porque pela esperança de Israel estou com esta cadeia.', 20, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então eles lhe disseram: Nós não recebemos acerca de ti carta alguma da Judéia, nem veio aqui algum dos irmãos, que nos anunciasse ou dissesse de ti mal algum.', 21, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No entanto bem quiséramos ouvir de ti o que sentes; porque, quanto a esta seita, notório nos é que em toda a parte se fala contra ela.', 22, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-lhe eles assinalado um dia, muitos foram ter com ele à pousada, aos quais declarava com bom testemunho o reino de Deus, e procurava persuadi-los à fé em Jesus, tanto pela lei de Moisés como pelos profetas, desde a manhã até à tarde.', 23, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns criam no que se dizia; mas outros não criam.', 24, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como ficaram entre si discordes, despediram-se, dizendo Paulo esta palavra: Bem falou o Espírito Santo a nossos pais pelo profeta Isaías,', 25, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo:Vai a este povo, e dize:De ouvido ouvireis, e de maneira nenhuma entendereis;E, vendo vereis, e de maneira nenhuma percebereis.', 26, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto o coração deste povo está endurecido,e com os ouvidos ouviram pesadamente,e fecharam os olhos,para que nunca com os olhos vejam,Nem com os ouvidos ouçam,Nem do coração entendam,E se convertam,E eu os cure.', 27, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Seja-vos, pois, notório que esta salvação de Deus é enviada aos gentios, e eles a ouvirão.', 28, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo ele dito estas palavras, partiram os judeus, tendo entre si grande contenda.', 29, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Paulo ficou dois anos inteiros na sua própria habitação que alugara, e recebia todos quantos vinham vê-lo;', 30, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pregando o reino de Deus, e ensinando com toda a liberdade as coisas pertencentes ao Senhor Jesus Cristo, sem impedimento algum.', 31, 28, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando eles falando ao povo, sobrevieram os sacerdotes, e o capitão do templo, e os saduceus,', 1, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Doendo-se muito de que ensinassem o povo, e anunciassem em Jesus a ressurreição dentre os mortos.', 2, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lançaram mão deles, e os encerraram na prisão até ao dia seguinte, pois já era tarde.', 3, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Muitos, porém, dos que ouviram a palavra creram, e chegou o número desses homens a quase cinco mil.', 4, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu, no dia seguinte, reunirem-se em Jerusalém os seus principais, os anciãos, os escribas,', 5, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Anás, o sumo sacerdote, e Caifás, e João, e Alexandre, e todos quantos havia da linhagem do sumo sacerdote.', 6, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pondo-os no meio, perguntaram: Com que poder ou em nome de quem fizestes isto?', 7, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pedro, cheio do Espírito Santo, lhes disse: Principais do povo, e vós, anciãos de Israel,', 8, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto que hoje somos interrogados acerca do benefício feito a um homem enfermo, e do modo como foi curado,', 9, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Seja conhecido de vós todos, e de todo o povo de Israel, que em nome de Jesus Cristo, o Nazareno, aquele a quem vós crucificastes e a quem Deus ressuscitou dentre os mortos, em nome desse é que este está são diante de vós.', 10, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele é a pedra que foi rejeitada por vós, os edificadores, a qual foi posta por cabeça de esquina.', 11, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E em nenhum outro há salvação, porque também debaixo do céu nenhum outro nome há, dado entre os homens, pelo qual devamos ser salvos.', 12, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então eles, vendo a ousadia de Pedro e João, e informados de que eram homens sem letras e indoutos, maravilharam-se e reconheceram que eles haviam estado com Jesus.', 13, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo estar com eles o homem que fora curado, nada tinham que dizer em contrário.', 14, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia, mandando-os sair fora do conselho, conferenciaram entre si,', 15, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Que havemos de fazer a estes homens? porque a todos os que habitam em Jerusalém é manifesto que por eles foi feito um sinal notório, e não o podemos negar;', 16, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, para que não se divulgue mais entre o povo, ameacemo-los para que não falem mais nesse nome a homem algum.', 17, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando-os, disseram-lhes que absolutamente não falassem, nem ensinassem, no nome de Jesus.', 18, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondendo, porém, Pedro e João, lhes disseram: Julgai vós se é justo, diante de Deus, ouvir-vos antes a vós do que a Deus;', 19, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não podemos deixar de falar do que temos visto e ouvido.', 20, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eles ainda os ameaçaram mais e, não achando motivo para os castigar, deixaram-nos ir, por causa do povo; porque todos glorificavam a Deus pelo que acontecera;', 21, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois tinha mais de quarenta anos o homem em quem se operara aquele milagre de saúde.', 22, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, soltos eles, foram para os seus, e contaram tudo o que lhes disseram os principais dos sacerdotes e os anciãos.', 23, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo eles isto, unânimes levantaram a voz a Deus, e disseram: Senhor, tu és o Deus que fizeste o céu, e a terra, e o mar e tudo o que neles há;', 24, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que disseste pela boca de Davi, teu servo: Por que bramaram os gentios, e os povos pensaram coisas vãs?', 25, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Levantaram-se os reis da terra,E os príncipes se ajuntaram à uma,Contra o Senhor e contra o seu Ungido.', 26, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque verdadeiramente contra o teu santo Filho Jesus, que tu ungiste, se ajuntaram, não só Herodes, mas Pôncio Pilatos, com os gentios e os povos de Israel;', 27, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para fazerem tudo o que a tua mão e o teu conselho tinham anteriormente determinado que se havia de fazer.', 28, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora, pois, ó Senhor, olha para as suas ameaças, e concede aos teus servos que falem com toda a ousadia a tua palavra;', 29, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Enquanto estendes a tua mão para curar, e para que se façam sinais e prodígios pelo nome de teu santo Filho Jesus.', 30, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo orado, moveu-se o lugar em que estavam reunidos; e todos foram cheios do Espírito Santo, e anunciavam com ousadia a palavra de Deus.', 31, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E era um o coração e a alma da multidão dos que criam, e ninguém dizia que coisa alguma do que possuía era sua própria, mas todas as coisas lhes eram comuns.', 32, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os apóstolos davam, com grande poder, testemunho da ressurreição do Senhor Jesus, e em todos eles havia abundante graça.', 33, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não havia, pois, entre eles necessitado algum; porque todos os que possuíam herdades ou casas, vendendo-as, traziam o preço do que fora vendido, e o depositavam aos pés dos apóstolos.', 34, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E repartia-se a cada um, segundo a necessidade que cada um tinha.', 35, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então José, cognominado pelos apóstolos Barnabé(que, traduzido, é Filho da consolação), levita, natural de Chipre,', 36, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Possuindo uma herdade, vendeu-a, e trouxe o preço, e o depositou aos pés dos apóstolos.', 37, 4, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pondo Paulo os olhos no conselho, disse: Homens irmãos, até ao dia de hoje tenho andado diante de Deus com toda a boa consciência.', 1, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o sumo sacerdote, Ananias, mandou aos que estavam junto dele que o ferissem na boca.', 2, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Paulo lhe disse: Deus te ferirá, parede branqueada; tu estás aqui assentado para julgar-me conforme a lei, e contra a lei me mandas ferir?', 3, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que ali estavam disseram: Injurias o sumo sacerdote de Deus?', 4, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Paulo disse: Não sabia, irmãos, que era o sumo sacerdote; porque está escrito: Não dirás mal do príncipe do teu povo.', 5, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Paulo, sabendo que uma parte era de saduceus e outra de fariseus, clamou no conselho: Homens irmãos, eu sou fariseu, filho de fariseu; no tocante à esperança e ressurreição dos mortos sou julgado.', 6, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo dito isto, houve dissensão entre os fariseus e saduceus; e a multidão se dividiu.', 7, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os saduceus dizem que não há ressurreição, nem anjo, nem espírito; mas os fariseus reconhecem uma e outra coisa.', 8, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E originou-se um grande clamor; e, levantando-se os escribas da parte dos fariseus, contendiam, dizendo: Nenhum mal achamos neste homem, e, se algum espírito ou anjo lhe falou, não lutemos contra Deus.', 9, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo grande dissensão, o tribuno, temendo que Paulo fosse despedaçado por eles, mandou descer a soldadesca, para que o tirassem do meio deles, e o levassem para a fortaleza.', 10, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E na noite seguinte, apresentando-se-lhe o Senhor, disse: Paulo, tem ânimo; porque, como de mim testificaste em Jerusalém, assim importa que testifiques também em Roma.', 11, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando já era dia, alguns dos judeus fizeram uma conspiração, e juraram, dizendo que não comeriam nem beberiam enquanto não matassem a Paulo.', 12, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eram mais de quarenta os que fizeram esta conjuração.', 13, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estes foram ter com os principais dos sacerdotes e anciãos, e disseram: Conjuramo-nos, sob pena de maldição, a nada provarmos até que matemos a Paulo.', 14, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora, pois, vós, com o conselho, rogai ao tribuno que vo-lo traga amanhã, como que querendo saber mais alguma coisa de seus negócios, e, antes que chegue, estaremos prontos para o matar.', 15, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o filho da irmã de Paulo, tendo ouvido acerca desta cilada, foi, e entrou na fortaleza, e o anunciou a Paulo.', 16, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Paulo, chamando a si um dos centuriões, disse: Leva este jovem ao tribuno, porque tem alguma coisa que lhe comunicar.', 17, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tomando-o ele, pois, o levou ao tribuno, e disse: O preso Paulo, chamando-me a si, rogou-me que trouxesse este jovem, que tem alguma coisa para dizer-te.', 18, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o tribuno, tomando-o pela mão, e pondo-se à parte, perguntou-lhe em particular: Que tens que me contar?', 19, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse ele: Os judeus se concertaram rogar-te que amanhã leves Paulo ao conselho, como que tendo de inquirir dele mais alguma coisa ao certo.', 20, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas tu não os creias; porque mais de quarenta homens de entre eles lhe andam armando ciladas; os quais se obrigaram, sob pena de maldição, a não comer nem beber até que o tenham morto; e já estão apercebidos, esperando de ti promessa.', 21, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o tribuno despediu o jovem, mandando-lhe que a ninguém dissesse que lhe havia contado aquilo.', 22, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando dois centuriões, lhes disse: Aprontai para as três horas da noite duzentos soldados, e setenta de cavalaria, e duzentos archeiros para irem até Cesaréia;', 23, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aparelhai animais, para que, pondo neles a Paulo, o levem salvo ao presidente Félix.', 24, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E escreveu uma carta, que continha isto:', 25, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cláudio Lísias, a Félix, potentíssimo presidente, saúde.', 26, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esse homem foi preso pelos judeus; e, estando já a ponto de ser morto por eles, sobrevim eu com a soldadesca, e o livrei, informado de que era romano.', 27, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, querendo saber a causa por que o acusavam, o levei ao seu conselho.', 28, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E achei que o acusavam de algumas questões da sua lei; mas que nenhum crime havia nele digno de morte ou de prisão.', 29, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo-me notificado que os judeus haviam de armar ciladas a esse homem, logo to enviei, mandando também aos acusadores que perante ti digam o que tiverem contra ele. Passa bem.', 30, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tomando, pois, os soldados a Paulo, como lhe fora mandado, o trouxeram de noite a Antipátride.', 31, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no dia seguinte, deixando aos de cavalo irem com ele, tornaram à fortaleza.', 32, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais, logo que chegaram a Cesaréia, e entregaram a carta ao presidente, lhe apresentaram Paulo.', 33, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o presidente, lida a carta, perguntou de que província era; e, sabendo que era da Cilícia,', 34, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse: Ouvir-te-ei, quando também aqui vierem os teus acusadores. E mandou que o guardassem no pretório de Herodes.', 35, 23, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou a videira verdadeira, e meu Pai é o lavrador.', 1, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Toda a vara em mim, que não dá fruto, a tira; e limpa toda aquela que dá fruto, para que dê mais fruto.', 2, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós já estais limpos, pela palavra que vos tenho falado.', 3, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estai em mim, e eu em vós; como a vara de si mesma não pode dar fruto, se não estiver na videira, assim também vós, se não estiverdes em mim.', 4, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou a videira, vós as varas; quem está em mim, e eu nele, esse dá muito fruto; porque sem mim nada podeis fazer.', 5, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém não estiver em mim, será lançado fora, como a vara, e secará; e os colhem e lançam no fogo, e ardem.', 6, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se vós estiverdes em mim, e as minhas palavras estiverem em vós, pedireis tudo o que quiserdes, e vos será feito.', 7, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto é glorificado meu Pai, que deis muito fruto; e assim sereis meus discípulos.', 8, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como o Pai me amou, também eu vos amei a vós; permanecei no meu amor.', 9, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se guardardes os meus mandamentos, permanecereis no meu amor; do mesmo modo que eu tenho guardado os mandamentos de meu Pai, e permaneço no seu amor.', 10, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho-vos dito isto, para que o meu gozo permaneça em vós, e o vosso gozo seja completo.', 11, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O meu mandamento é este: Que vos ameis uns aos outros, assim como eu vos amei.', 12, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém tem maior amor do que este, de dar alguém a sua vida pelos seus amigos.', 13, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós sereis meus amigos, se fizerdes o que eu vos mando.', 14, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Já vos não chamarei servos, porque o servo não sabe o que faz o seu senhor; mas tenho-vos chamado amigos, porque tudo quanto ouvi de meu Pai vos tenho feito conhecer.', 15, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não me escolhestes vós a mim, mas eu vos escolhi a vós, e vos nomeei, para que vades e deis fruto, e o vosso fruto permaneça; a fim de que tudo quanto em meu nome pedirdes ao Pai ele vo-lo conceda.', 16, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto vos mando: Que vos ameis uns aos outros.', 17, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se o mundo vos odeia, sabei que, primeiro do que a vós, me odiou a mim.', 18, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se vós fôsseis do mundo, o mundo amaria o que era seu, mas porque não sois do mundo, antes eu vos escolhi do mundo, por isso é que o mundo vos odeia.', 19, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lembrai-vos da palavra que vos disse: Não é o servo maior do que o seu senhor. Se a mim me perseguiram, também vos perseguirão a vós; se guardaram a minha palavra, também guardarão a vossa.', 20, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas tudo isto vos farão por causa do meu nome, porque não conhecem aquele que me enviou.', 21, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se eu não viera, nem lhes houvera falado, não teriam pecado, mas agora não têm desculpa do seu pecado.', 22, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que me odeia, odeia também a meu Pai.', 23, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se eu entre eles não fizesse tais obras, quais nenhum outro tem feito, não teriam pecado; mas agora, viram-nas e me odiaram a mim e a meu Pai.', 24, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas é para que se cumpra a palavra que está escrita na sua lei: Odiaram-me sem causa.', 25, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando vier o Consolador, que eu da parte do Pai vos hei de enviar, aquele Espírito de verdade, que procede do Pai, ele testificará de mim.', 26, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós também testificareis, pois estivestes comigo desde o princípio.', 27, 15, 4);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que era desde o princípio, o que ouvimos, o que vimos com os nossos olhos, o que temos contemplado, e as nossas mãos tocaram da Palavra da vida', 1, 1, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Porque a vida foi manifestada, e nós a vimos, e testificamos dela, e vos anunciamos a vida eterna, que estava com o Pai, e nos foi manifestada);', 2, 1, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que vimos e ouvimos, isso vos anunciamos, para que também tenhais comunhão conosco; e a nossa comunhão é com o Pai, e com seu Filho Jesus Cristo.', 3, 1, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estas coisas vos escrevemos, para que o vosso gozo se cumpra.', 4, 1, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E esta é a mensagem que dele ouvimos, e vos anunciamos: que Deus é luz, e não há nele trevas nenhumas.', 5, 1, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se dissermos que temos comunhão com ele, e andarmos em trevas, mentimos, e não praticamos a verdade.', 6, 1, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se andarmos na luz, como ele na luz está, temos comunhão uns com os outros, e o sangue de Jesus Cristo, seu Filho, nos purifica de todo o pecado.', 7, 1, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se dissermos que não temos pecado, enganamo-nos a nós mesmos, e não há verdade em nós.', 8, 1, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se confessarmos os nossos pecados, ele é fiel e justo para nos perdoar os pecados, e nos purificar de toda a injustiça.', 9, 1, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se dissermos que não pecamos, fazemo-lo mentiroso, e a sua palavra não está em nós.', 10, 1, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, se há algum conforto em Cristo, se alguma consolação de amor, se alguma comunhão no Espírito, se alguns entranháveis afetos e compaixões,', 1, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Completai o meu gozo, para que sintais o mesmo, tendo o mesmo amor, o mesmo ânimo, sentindo uma mesma coisa.', 2, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nada façais por contenda ou por vanglória, mas por humildade; cada um considere os outros superiores a si mesmo.', 3, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não atente cada um para o que é propriamente seu, mas cada qual também para o que é dos outros.', 4, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que haja em vós o mesmo sentimento que houve também em Cristo Jesus,', 5, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que, sendo em forma de Deus, não teve por usurpação ser igual a Deus,', 6, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas esvaziou-se a si mesmo, tomando a forma de servo, fazendo-se semelhante aos homens;', 7, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, achado na forma de homem, humilhou-se a si mesmo, sendo obediente até à morte, e morte de cruz.', 8, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, também Deus o exaltou soberanamente, e lhe deu um nome que é sobre todo o nome;', 9, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que ao nome de Jesus se dobre todo o joelho dos que estão nos céus, e na terra, e debaixo da terra,', 10, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E toda a língua confesse que Jesus Cristo é o Senhor, para glória de Deus Pai.', 11, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que, meus amados, assim como sempre obedecestes, não só na minha presença, mas muito mais agora na minha ausência, assim também operai a vossa salvação com temor e tremor;', 12, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus é o que opera em vós tanto o querer como o efetuar, segundo a sua boa vontade.', 13, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fazei todas as coisas sem murmurações nem contendas;', 14, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que sejais irrepreensíveis e sinceros, filhos de Deus inculpáveis, no meio de uma geração corrompida e perversa, entre a qual resplandeceis como astros no mundo;', 15, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Retendo a palavra da vida, para que no dia de Cristo possa gloriar-me de não ter corrido nem trabalhado em vão.', 16, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ainda que seja oferecido por libação sobre o sacrifício e serviço da vossa fé, folgo e me regozijo com todos vós.', 17, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós também regozijai-vos e alegrai-vos comigo por isto mesmo.', 18, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E espero no Senhor Jesus que em breve vos mandarei Timóteo, para que também eu esteja de bom ânimo, sabendo dos vossos negócios.', 19, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a ninguém tenho de igual sentimento, que sinceramente cuide do vosso estado;', 20, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos buscam o que é seu, e não o que é de Cristo Jesus.', 21, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas bem sabeis qual a sua experiência, e que serviu comigo no evangelho, como filho ao pai.', 22, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que espero vo-lo enviar logo que tenha provido a meus negócios.', 23, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas confio no Senhor, que também eu mesmo em breve irei ter convosco.', 24, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Julguei, contudo, necessário mandar-vos Epafrodito, meu irmão e cooperador, e companheiro nos combates, e vosso enviado para prover às minhas necessidades.', 25, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto tinha muitas saudades de vós todos, e estava muito angustiado de que tivésseis ouvido que ele estivera doente.', 26, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de fato esteve doente, e quase à morte; mas Deus se apiedou dele, e não somente dele, mas também de mim, para que eu não tivesse tristeza sobre tristeza.', 27, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso vo-lo enviei mais depressa, para que, vendo-o outra vez, vos regozijeis, e eu tenha menos tristeza.', 28, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Recebei-o, pois, no Senhor com todo o gozo, e tende-o em honra;', 29, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque pela obra de Cristo chegou até bem próximo da morte, não fazendo caso da vida para suprir para comigo a falta do vosso serviço.', 30, 2, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois Agripa disse a Paulo: É permitido que te defendas. Então Paulo, estendendo a mão em sua defesa, respondeu:', 1, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho-me por feliz, ó rei Agripa, de que perante ti me haja hoje de defender de todas as coisas de que sou acusado pelos judeus;', 2, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mormente sabendo eu que tens conhecimento de todos os costumes e questões que há entre os judeus; por isso te rogo que me ouças com paciência.', 3, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto à minha vida, desde a mocidade, como decorreu desde o princípio entre os da minha nação, em Jerusalém, todos os judeus a conhecem,', 4, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo de mim desde o princípio (se o quiserem testificar), que, conforme a mais severa seita da nossa religião, vivi fariseu.', 5, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora pela esperança da promessa que por Deus foi feita a nossos pais estou aqui e sou julgado.', 6, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('À qual as nossas doze tribos esperam chegar, servindo a Deus continuamente, noite e dia. Por esta esperança, ó rei Agripa, eu sou acusado pelos judeus.', 7, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois quê? julga-se coisa incrível entre vós que Deus ressuscite os mortos?', 8, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem tinha eu imaginado que contra o nome de Jesus Nazareno devia eu praticar muitos atos;', 9, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que também fiz em Jerusalém. E, havendo recebido autorização dos principais dos sacerdotes, encerrei muitos dos santos nas prisões; e quando os matavam eu dava o meu voto contra eles.', 10, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, castigando-os muitas vezes por todas as sinagogas, os obriguei a blasfemar. E, enfurecido demasiadamente contra eles, até nas cidades estranhas os persegui.', 11, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sobre o que, indo então a Damasco, com poder e comissão dos principais dos sacerdotes,', 12, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao meio-dia, ó rei, vi no caminho uma luz do céu, que excedia o esplendor do sol, cuja claridade me envolveu a mim e aos que iam comigo.', 13, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, caindo nós todos por terra, ouvi uma voz que me falava, e em língua hebraica dizia: Saulo, Saulo, por que me persegues? Dura coisa te é recalcitrar contra os aguilhões.', 14, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse eu: Quem és, Senhor? E ele respondeu: Eu sou Jesus, a quem tu persegues;', 15, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas levanta-te e põe-te sobre teus pés, porque te apareci por isto, para te pôr por ministro e testemunha tanto das coisas que tens visto como daquelas pelas quais te aparecerei ainda;', 16, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Livrando-te deste povo, e dos gentios, a quem agora te envio,', 17, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para lhes abrires os olhos, e das trevas os converteres à luz, e do poder de Satanás a Deus; a fim de que recebam a remissão de pecados, e herança entre os que são santificados pela fé em mim.', 18, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, ó rei Agripa, não fui desobediente à visão celestial.', 19, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes anunciei primeiramente aos que estão em Damasco e em Jerusalém, e por toda a terra da Judéia, e aos gentios, que se emendassem e se convertessem a Deus, fazendo obras dignas de arrependimento.', 20, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por causa disto os judeus lançaram mão de mim no templo, e procuraram matar-me.', 21, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, alcançando socorro de Deus, ainda até ao dia de hoje permaneço dando testemunho tanto a pequenos como a grandes, não dizendo nada mais do que o que os profetas e Moisés disseram que devia acontecer,', 22, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto é, que o Cristo devia padecer, e sendo o primeiro da ressurreição dentre os mortos, devia anunciar a luz a este povo e aos gentios.', 23, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dizendo ele isto em sua defesa, disse Festo em alta voz: Estás louco, Paulo; as muitas letras te fazem delirar.', 24, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele disse: Não deliro, ó potentíssimo Festo; antes digo palavras de verdade e de um são juízo.', 25, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o rei, diante de quem também falo com ousadia, sabe estas coisas, pois não creio que nada disto lhe é oculto; porque isto não se fez em qualquer canto.', 26, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Crês tu nos profetas, ó rei Agripa? Bem sei que crês.', 27, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Agripa a Paulo: Por pouco me queres persuadir a que me faça cristão!', 28, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Paulo: Prouvera a Deus que, ou por pouco ou por muito, não somente tu, mas também todos quantos hoje me estão ouvindo, se tornassem tais qual eu sou, exceto estas cadeias.', 29, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dizendo ele isto, levantou-se o rei, o presidente, e Berenice, e os que com eles estavam assentados.', 30, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, apartando-se dali falavam uns com os outros, dizendo: Este homem nada fez digno de morte ou de prisões.', 31, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Agripa disse a Festo: Bem podia soltar-se este homem, se não houvera apelado para César.', 32, 26, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todo aquele que crê que Jesus é o Cristo, é nascido de Deus; e todo aquele que ama ao que o gerou também ama ao que dele é nascido.', 1, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto conhecemos que amamos os filhos de Deus, quando amamos a Deus e guardamos os seus mandamentos.', 2, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque este é o amor de Deus: que guardemos os seus mandamentos; e os seus mandamentos não são pesados.', 3, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todo o que é nascido de Deus vence o mundo; e esta é a vitória que vence o mundo, a nossa fé.', 4, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem é que vence o mundo, senão aquele que crê que Jesus é o Filho de Deus?', 5, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este é aquele que veio por água e sangue, isto é, Jesus Cristo; não só por água, mas por água e por sangue. E o Espírito é o que testifica, porque o Espírito é a verdade.', 6, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque três são os que testificam no céu: o Pai, a Palavra, e o Espírito Santo; e estes três são um.', 7, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E três são os que testificam na terra: o Espírito, e a água e o sangue; e estes três concordam num.', 8, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se recebemos o testemunho dos homens, o testemunho de Deus é maior; porque o testemunho de Deus é este, que de seu Filho testificou.', 9, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem crê no Filho de Deus, em si mesmo tem o testemunho; quem a Deus não crê mentiroso o fez, porquanto não creu no testemunho que Deus de seu Filho deu.', 10, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o testemunho é este: que Deus nos deu a vida eterna; e esta vida está em seu Filho.', 11, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem tem o Filho tem a vida; quem não tem o Filho de Deus não tem a vida.', 12, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estas coisas vos escrevi a vós, os que credes no nome do Filho de Deus, para que saibais que tendes a vida eterna, e para que creiais no nome do Filho de Deus.', 13, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E esta é a confiança que temos nele, que, se pedirmos alguma coisa, segundo a sua vontade, ele nos ouve.', 14, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se sabemos que nos ouve em tudo o que pedimos, sabemos que alcançamos as petições que lhe fizemos.', 15, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém vir pecar seu irmão, pecado que não é para morte, orará, e Deus dará a vida àqueles que não pecarem para morte. Há pecado para morte, e por esse não digo que ore.', 16, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Toda a iniqüidade é pecado, e há pecado que não é para morte.', 17, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabemos que todo aquele que é nascido de Deus não peca; mas o que de Deus é gerado conserva-se a si mesmo, e o maligno não lhe toca.', 18, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabemos que somos de Deus, e que todo o mundo está no maligno.', 19, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sabemos que já o Filho de Deus é vindo, e nos deu entendimento para que conheçamos ao Verdadeiro; e no que é verdadeiro estamos, isto é, em seu Filho Jesus Cristo. Este é o verdadeiro Deus e a vida eterna.', 20, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Filhinhos, guardai-vos dos ídolos. Amém.', 21, 5, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fiz o primeiro tratado, ó Teófilo, acerca de tudo que Jesus começou, não só a fazer, mas a ensinar,', 1, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Até ao dia em que foi recebido em cima, depois de ter dado mandamentos, pelo Espírito Santo, aos apóstolos que escolhera;', 2, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aos quais também, depois de ter padecido, se apresentou vivo, com muitas e infalíveis provas, sendo visto por eles por espaço de quarenta dias, e falando das coisas concernentes ao reino de Deus.', 3, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando com eles, determinou-lhes que não se ausentassem de Jerusalém, mas que esperassem a promessa do Pai, que, disse ele, de mim ouvistes.', 4, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, na verdade, João batizou com água, mas vós sereis batizados com o Espírito Santo, não muito depois destes dias.', 5, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aqueles, pois, que se haviam reunido perguntaram-lhe, dizendo: Senhor, restaurarás tu neste tempo o reino a Israel?', 6, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Não vos pertence saber os tempos ou as estações que o Pai estabeleceu pelo seu próprio poder.', 7, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas recebereis a virtude do Espírito Santo, que há de vir sobre vós; e ser-me-eis testemunhas, tanto em Jerusalém como em toda a Judéia e Samaria, e até aos confins da terra.', 8, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando dizia isto, vendo-o eles, foi elevado às alturas, e uma nuvem o recebeu, ocultando-o a seus olhos.', 9, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando com os olhos fitos no céu, enquanto ele subia, eis que junto deles se puseram dois homens vestidos de branco.', 10, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais lhes disseram: Homens galileus, por que estais olhando para o céu? Esse Jesus, que dentre vós foi recebido em cima no céu, há de vir assim como para o céu o vistes ir.', 11, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então voltaram para Jerusalém, do monte chamado das Oliveiras, o qual está perto de Jerusalém, à distância do caminho de um sábado.', 12, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando, subiram ao cenáculo, onde habitavam Pedro e Tiago, João e André, Filipe e Tomé, Bartolomeu e Mateus, Tiago, filho de Alfeu, Simão, o Zelote, e Judas, irmào de Tiago.', 13, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos estes perseveravam unanimemente em oração e súplicas, com as mulheres, e Maria mãe de Jesus, e com seus irmãos.', 14, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E naqueles dias, levantando-se Pedro no meio dos discípulos (ora a multidão junta era de quase cento e vinte pessoas) disse:', 15, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Homens irmãos, convinha que se cumprisse a Escritura que o Espírito Santo predisse pela boca de Davi, acerca de Judas, que foi o guia daqueles que prenderam a Jesus;', 16, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque foi contado conosco e alcançou sorte neste ministério.', 17, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, este adquiriu um campo com o galardão da iniqüidade; e, precipitando-se, rebentou pelo meio, e todas as suas entranhas se derramaram.', 18, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi notório a todos os que habitam em Jerusalém; de maneira que na sua própria língua esse campo se chama Aceldama, isto é, Campo de Sangue.', 19, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque no livro dos Salmos está escrito: Fique deserta a sua habitação, E não haja quem nela habite, e: Tome outro o seu bispado.', 20, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É necessário, pois, que, dos homens que conviveram conosco todo o tempo em que o Senhor Jesus entrou e saiu dentre nós,', 21, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Começando desde o batismo de João até ao dia em que de entre nós foi recebido em cima, um deles se faça conosco testemunha da sua ressurreição.', 22, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E apresentaram dois: José, chamado Barsabás, que tinha por sobrenome o Justo, e Matias.', 23, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, orando, disseram: Tu, Senhor, conhecedor dos corações de todos, mostra qual destes dois tens escolhido,', 24, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que tome parte neste ministério e apostolado, de que Judas se desviou, para ir para o seu próprio lugar.', 25, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, lançando-lhes sortes, caiu a sorte sobre Matias. E por voto comum foi contado com os onze apóstolos.', 26, 1, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por aquele mesmo tempo o rei Herodes estendeu as mãos sobre alguns da igreja, para os maltratar;', 1, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E matou à espada Tiago, irmão de João.', 2, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo que isso agradara aos judeus, continuou, mandando prender também a Pedro. E eram os dias dos ázimos.', 3, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-o prendido, o encerrou na prisão, entregando-o a quatro quaternos de soldados, para que o guardassem, querendo apresentá-lo ao povo depois da páscoa.', 4, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pedro, pois, era guardado na prisão; mas a igreja fazia contínua oração por ele a Deus.', 5, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando Herodes estava para o fazer comparecer, nessa mesma noite estava Pedro dormindo entre dois soldados, ligado com duas cadeias, e os guardas diante da porta guardavam a prisão.', 6, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que sobreveio o anjo do Senhor, e resplandeceu uma luz na prisão; e, tocando a Pedro na ilharga, o despertou, dizendo: Levanta-te depressa. E caíram-lhe das mãos as cadeias.', 7, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe o anjo: Cinge-te, e ata as tuas alparcas. E ele assim o fez. Disse-lhe mais: Lança às costas a tua capa, e segue-me.', 8, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo, o seguia. E não sabia que era real o que estava sendo feito pelo anjo, mas cuidava que via alguma visão.', 9, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando passaram a primeira e segunda guarda, chegaram à porta de ferro, que dá para a cidade, a qual se lhes abriu por si mesma; e, tendo saído, percorreram uma rua, e logo o anjo se apartou dele.', 10, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro, tornando a si, disse: Agora sei verdadeiramente que o Senhor enviou o seu anjo, e me livrou da mão de Herodes, e de tudo o que o povo dos judeus esperava.', 11, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, considerando ele nisto, foi à casa de Maria, mãe de João, que tinha por sobrenome Marcos, onde muitos estavam reunidos e oravam.', 12, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, batendo Pedro à porta do pátio, uma menina chamada Rode saiu a escutar;', 13, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, conhecendo a voz de Pedro, de gozo não abriu a porta, mas, correndo para dentro, anunciou que Pedro estava à porta.', 14, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disseram-lhe: Estás fora de ti. Mas ela afirmava que assim era. E diziam: É o seu anjo.', 15, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Pedro perseverava em bater e, quando abriram, viram-no, e se espantaram.', 16, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E acenando-lhes ele com a mão para que se calassem, contou-lhes como o Senhor o tirara da prisão, e disse: Anunciai isto a Tiago e aos irmãos. E, saindo, partiu para outro lugar.', 17, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo já dia, houve não pouco alvoroço entre os soldados sobre o que seria feito de Pedro.', 18, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando Herodes o procurou e o não achou, feita inquirição aos guardas, mandou-os justiçar. E, partindo da Judéia para Cesaréia, ficou ali.', 19, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele estava irritado com os de Tiro e de Sidom; mas estes, vindo de comum acordo ter com ele, e obtendo a amizade de Blasto, que era o camarista do rei, pediam paz; porquanto o seu país se abastecia do país do rei.', 20, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E num dia designado, vestindo Herodes as vestes reais, estava assentado no tribunal e lhes fez uma prática.', 21, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o povo exclamava: Voz de Deus, e não de homem.', 22, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no mesmo instante feriu-o o anjo do Senhor, porque não deu glória a Deus e, comido de bichos, expirou.', 23, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a palavra de Deus crescia e se multiplicava.', 24, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Barnabé e Saulo, havendo terminado aquele serviço, voltaram de Jerusalém, levando também consigo a João, que tinha por sobrenome Marcos.', 25, 12, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, meus amados e mui queridos irmãos, minha alegria e coroa, estai assim firmes no Senhor, amados.', 1, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Rogo a Evódia, e rogo a Síntique, que sintam o mesmo no Senhor.', 2, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E peço-te também a ti, meu verdadeiro companheiro, que ajudes essas mulheres que trabalharam comigo no evangelho, e com Clemente, e com os meus outros cooperadores, cujos nomes estão no livro da vida.', 3, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Regozijai-vos sempre no Senhor; outra vez digo, regozijai-vos.', 4, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Seja a vossa eqüidade notória a todos os homens. Perto está o Senhor.', 5, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não estejais inquietos por coisa alguma; antes as vossas petições sejam em tudo conhecidas diante de Deus pela oração e súplica, com ação de graças.', 6, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a paz de Deus, que excede todo o entendimento, guardará os vossos corações e os vossos pensamentos em Cristo Jesus.', 7, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto ao mais, irmãos, tudo o que é verdadeiro, tudo o que é honesto, tudo o que é justo, tudo o que é puro, tudo o que é amável, tudo o que é de boa fama, se há alguma virtude, e se há algum louvor, nisso pensai.', 8, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que também aprendestes, e recebestes, e ouvistes, e vistes em mim, isso fazei; e o Deus de paz será convosco.', 9, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, muito me regozijei no Senhor por finalmente reviver a vossa lembrança de mim; pois já vos tínheis lembrado, mas não tínheis tido oportunidade.', 10, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não digo isto como por necessidade, porque já aprendi a contentar-me com o que tenho.', 11, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sei estar abatido, e sei também ter abundância; em toda a maneira, e em todas as coisas estou instruído, tanto a ter fartura, como a ter fome; tanto a ter abundância, como a padecer necessidade.', 12, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Posso todas as coisas em Cristo que me fortalece.', 13, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia fizestes bem em tomar parte na minha aflição.', 14, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E bem sabeis também, ó filipenses, que, no princípio do evangelho, quando parti da macedônia, nenhuma igreja comunicou comigo com respeito a dar e a receber, senão vós somente;', 15, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também uma e outra vez me mandastes o necessário a tessalônica.', 16, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não que procure dádivas, mas procuro o fruto que cresça para a vossa conta.', 17, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas bastante tenho recebido, e tenho abundância. Cheio estou, depois que recebi de Epafrodito o que da vossa parte me foi enviado, como cheiro de suavidade e sacrifício agradável e aprazível a Deus.', 18, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O meu Deus, segundo as suas riquezas, suprirá todas as vossas necessidades em glória, por Cristo Jesus.', 19, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, a nosso Deus e Pai seja dada glória para todo o sempre. Amém.', 20, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a todos os santos em Cristo Jesus. Os irmãos que estão comigo vos saúdam.', 21, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos os santos vos saúdam, mas principalmente os que são da casa de César.', 22, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça de nosso Senhor Jesus Cristo seja com vós todos. Amém.', 23, 4, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois que cessou o alvoroço, Paulo chamou a si os discípulos e, abraçando-os, saiu para a macedônia.', 1, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo andado por aquelas terras, exortando-os com muitas palavras, veio à Grécia.', 2, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passando ali três meses, e sendo-lhe pelos judeus postas ciladas, como tivesse de navegar para a Síria, determinou voltar pela macedônia.', 3, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E acompanhou-o, até à Ásia, Sópater, de Beréia, e, dos de Tessalônica, Aristarco, e Segundo, e Gaio de Derbe, e Timóteo, e, dos da Ásia, Tíquico e Trófimo.', 4, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes, indo adiante, nos esperaram em Trôade.', 5, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois dos dias dos pães ázimos, navegamos de Filipos, e em cinco dias fomos ter com eles a Trôade, onde estivemos sete dias.', 6, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no primeiro dia da semana, ajuntando-se os discípulos para partir o pão, Paulo, que havia de partir no dia seguinte, falava com eles; e prolongou a prática até à meia-noite.', 7, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia muitas luzes no cenáculo onde estavam juntos.', 8, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando um certo jovem, por nome Eutico, assentado numa janela, caiu do terceiro andar, tomado de um sono profundo que lhe sobreveio durante o extenso discurso de Paulo; e foi levantado morto.', 9, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, porém, descendo, inclinou-se sobre ele e, abraçando-o, disse: Não vos perturbeis, que a sua alma nele está.', 10, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E subindo, e partindo o pão, e comendo, ainda lhes falou largamente até à alvorada; e assim partiu.', 11, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levaram vivo o jovem, e ficaram não pouco consolados.', 12, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nós, porém, subindo ao navio, navegamos até Assôs, onde devíamos receber a Paulo, porque assim o ordenara, indo ele por terra.', 13, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo que se ajuntou conosco em Assôs, o recebemos, e fomos a Mitilene.', 14, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, navegando dali, chegamos no dia seguinte defronte de Quios, e no outro aportamos a Samos e, ficando em Trogílio, chegamos no dia seguinte a Mileto.', 15, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já Paulo tinha determinado passar ao largo de Éfeso, para não gastar tempo na Ásia. Apressava-se, pois, para estar, se lhe fosse possível, em Jerusalém no dia de Pentecostes.', 16, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de Mileto mandou a Éfeso, a chamar os anciãos da igreja.', 17, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo que chegaram junto dele, disse-lhes: Vós bem sabeis, desde o primeiro dia em que entrei na Ásia, como em todo esse tempo me portei no meio de vós,', 18, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Servindo ao Senhor com toda a humildade, e com muitas lágrimas e tentações, que pelas ciladas dos judeus me sobrevieram;', 19, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como nada, que útil seja, deixei de vos anunciar, e ensinar publicamente e pelas casas,', 20, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Testificando, tanto aos judeus como aos gregos, a conversão a Deus, e a fé em nosso Senhor Jesus Cristo.', 21, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora, eis que, ligado eu pelo espírito, vou para Jerusalém, não sabendo o que lá me há de acontecer,', 22, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Senão o que o Espírito Santo de cidade em cidade me revela, dizendo que me esperam prisões e tribulações.', 23, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas de nada faço questão, nem tenho a minha vida por preciosa, contanto que cumpra com alegria a minha carreira, e o ministério que recebi do Senhor Jesus, para dar testemunho do evangelho da graça de Deus.', 24, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora, na verdade, sei que todos vós, por quem passei pregando o reino de Deus, não vereis mais o meu rosto.', 25, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, no dia de hoje, vos protesto que estou limpo do sangue de todos.', 26, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nunca deixei de vos anunciar todo o conselho de Deus.', 27, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Olhai, pois, por vós, e por todo o rebanho sobre que o Espírito Santo vos constituiu bispos, para apascentardes a igreja de Deus, que ele resgatou com seu próprio sangue.', 28, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu sei isto que, depois da minha partida, entrarão no meio de vós lobos cruéis, que não pouparão ao rebanho;', 29, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que de entre vós mesmos se levantarão homens que falarão coisas perversas, para atraírem os discípulos após si.', 30, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, vigiai, lembrando-vos de que durante três anos, não cessei, noite e dia, de admoestar com lágrimas a cada um de vós.', 31, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora, pois, irmãos, encomendo-vos a Deus e à palavra da sua graça; a ele que é poderoso para vos edificar e dar herança entre todos os santificados.', 32, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De ninguém cobicei a prata, nem o ouro, nem o vestuário.', 33, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sim, vós mesmos sabeis que para o que me era necessário a mim, e aos que estão comigo, estas mãos me serviram.', 34, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho-vos mostrado em tudo que, trabalhando assim, é necessário auxiliar os enfermos, e recordar as palavras do Senhor Jesus, que disse: Mais bem-aventurada coisa é dar do que receber.', 35, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo dito isto, pôs-se de joelhos, e orou com todos eles.', 36, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levantou-se um grande pranto entre todos e, lançando-se ao pescoço de Paulo, o beijavam,', 37, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entristecendo-se muito, principalmente pela palavra que dissera, que não veriam mais o seu rosto. E acompanharam-no até o navio.', 38, 20, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo e Timóteo, servos de Jesus Cristo, a todos os santos em Cristo Jesus, que estão em Filipos, com os bispos e diáconos:', 1, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graça a vós, e paz da parte de Deus nosso Pai e da do Senhor Jesus Cristo.', 2, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dou graças ao meu Deus todas as vezes que me lembro de vós,', 3, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fazendo sempre com alegria oração por vós em todas as minhas súplicas,', 4, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela vossa cooperação no evangelho desde o primeiro dia até agora.', 5, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo por certo isto mesmo, que aquele que em vós começou a boa obra a aperfeiçoará até ao dia de Jesus Cristo;', 6, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como tenho por justo sentir isto de vós todos, porque vos retenho em meu coração, pois todos vós fostes participantes da minha graça, tanto nas minhas prisões como na minha defesa e confirmação do evangelho.', 7, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus me é testemunha das saudades que de todos vós tenho, em entranhável afeição de Jesus Cristo.', 8, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E peço isto: que o vosso amor cresça mais e mais em ciência e em todo o conhecimento,', 9, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que aproveis as coisas excelentes, para que sejais sinceros, e sem escândalo algum até ao dia de Cristo;', 10, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cheios dos frutos de justiça, que são por Jesus Cristo, para glória e louvor de Deus.', 11, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quero, irmãos, que saibais que as coisas que me aconteceram contribuíram para maior proveito do evangelho;', 12, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que as minhas prisões em Cristo foram manifestas por toda a guarda pretoriana, e por todos os demais lugares;', 13, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos dos irmãos no Senhor, tomando ânimo com as minhas prisões, ousam falar a palavra mais confiadamente, sem temor.', 14, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Verdade é que também alguns pregam a Cristo por inveja e porfia, mas outros de boa vontade;', 15, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Uns, na verdade, anunciam a Cristo por contenção, não puramente, julgando acrescentar aflição às minhas prisões.', 16, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas outros, por amor, sabendo que fui posto para defesa do evangelho.', 17, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas que importa? Contanto que Cristo seja anunciado de toda a maneira, ou com fingimento ou em verdade, nisto me regozijo, e me regozijarei ainda.', 18, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque sei que disto me resultará salvação, pela vossa oração e pelo socorro do Espírito de Jesus Cristo,', 19, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segundo a minha intensa expectação e esperança, de que em nada serei confundido; antes, com toda a confiança, Cristo será, tanto agora como sempre, engrandecido no meu corpo, seja pela vida, seja pela morte.', 20, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque para mim o viver é Cristo, e o morrer é ganho.', 21, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se o viver na carne me der fruto da minha obra, não sei então o que deva escolher.', 22, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas de ambos os lados estou em aperto, tendo desejo de partir, e estar com Cristo, porque isto é ainda muito melhor.', 23, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas julgo mais necessário, por amor de vós, ficar na carne.', 24, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo esta confiança, sei que ficarei, e permanecerei com todos vós para proveito vosso e gozo da fé,', 25, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que a vossa glória cresça por mim em Cristo Jesus, pela minha nova ida a vós.', 26, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Somente deveis portar-vos dignamente conforme o evangelho de Cristo, para que, quer vá e vos veja, quer esteja ausente, ouça acerca de vós que estais num mesmo espírito, combatendo juntamente com o mesmo ânimo pela fé do evangelho.', 27, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E em nada vos espanteis dos que resistem, o que para eles, na verdade, é indício de perdição, mas para vós de salvação, e isto de Deus.', 28, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a vós vos foi concedido, em relação a Cristo, não somente crer nele, como também padecer por ele,', 29, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo o mesmo combate que já em mim tendes visto e agora ouvis estar em mim.', 30, 1, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como se determinou que havíamos de navegar para a Itália, entregaram Paulo, e alguns outros presos, a um centurião por nome Júlio, da coorte augusta.', 1, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, embarcando nós em um navio adramitino, partimos navegando pelos lugares da costa da Ásia, estando conosco Aristarco, macedônio, de Tessalônica.', 2, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegamos no dia seguinte a Sidom, e Júlio, tratando Paulo humanamente, lhe permitiu ir ver os amigos, para que cuidassem dele.', 3, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, partindo dali, fomos navegando abaixo de Chipre, porque os ventos eram contrários.', 4, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo atravessado o mar, ao longo da Cilícia e Panfília, chegamos a Mirra, na Lícia.', 5, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, achando ali o centurião um navio de Alexandria, que navegava para a Itália, nos fez embarcar nele.', 6, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como por muitos dias navegássemos vagarosamente, havendo chegado apenas defronte de Cnido, não nos permitindo o vento ir mais adiante, navegamos abaixo de Creta, junto de Salmone.', 7, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, consteando-a dificilmente, chegamos a um lugar chamando Bons Portos, perto do qual estava a cidade de Laséia.', 8, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passado muito tempo, e sendo já perigosa a navegação, pois, também o jejum já tinha passado, Paulo os admoestava,', 9, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo-lhes: Senhores, vejo que a navegação há de ser incômoda, e com muito dano, não só para o navio e carga, mas também para as nossas vidas.', 10, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o centurião cria mais no piloto e no mestre, do que no que dizia Paulo.', 11, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como aquele porto não era cômodo para invernar, os mais deles foram de parecer que se partisse dali para ver se podiam chegar a Fenice, que é um porto de Creta que olha para o lado do vento da África e do Coro, e invernar ali.', 12, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, soprando o sul brandamente, lhes pareceu terem já o que desejavam e, fazendo-se de vela, foram de muito perto costeando Creta.', 13, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não muito depois deu nela um pé de vento, chamado Euro-aquilão.', 14, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo o navio arrebatado, e não podendo navegar contra o vento, dando de mão a tudo, nos deixamos ir à toa.', 15, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, correndo abaixo de uma pequena ilha chamada Clauda, apenas pudemos ganhar o batel.', 16, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levado este para cima, usaram de todos os meios, cingindo o navio; e, temendo darem à costa na Sirte, amainadas as velas, assim foram à toa.', 17, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, andando nós agitados por uma veemente tempestade, no dia seguinte aliviaram o navio.', 18, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao terceiro dia nós mesmos, com as nossas próprias mãos, lançamos ao mar a armação do navio.', 19, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, não aparecendo, havia já muitos dias, nem sol nem estrelas, e caindo sobre nós uma não pequena tempestade, fugiu-nos toda a esperança de nos salvarmos.', 20, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo já muito que não se comia, então Paulo, pondo-se em pé no meio deles, disse: Fora, na verdade, razoável, ó senhores, ter-me ouvido a mim e não partir de Creta, e assim evitariam este incômodo e esta perda.', 21, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora vos admoesto a que tenhais bom ânimo, porque não se perderá a vida de nenhum de vós, mas somente o navio.', 22, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque esta mesma noite o anjo de Deus, de quem eu sou, e a quem sirvo, esteve comigo,', 23, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Paulo, não temas; importa que sejas apresentado a César, e eis que Deus te deu todos quantos navegam contigo.', 24, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, ó senhores, tende bom ânimo; porque creio em Deus, que há de acontecer assim como a mim me foi dito.', 25, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É contudo necessário irmos dar numa ilha.', 26, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando chegou a décima quarta noite, sendo impelidos de um e outro lado no mar Adriático, lá pela meia-noite suspeitaram os marinheiros que estavam próximos de alguma terra.', 27, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, lançando o prumo, acharam vinte braças; e, passando um pouco mais adiante, tornando a lançar o prumo, acharam quinze braças.', 28, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, temendo ir dar em alguns rochedos, lançaram da popa quatro âncoras, desejando que viesse o dia.', 29, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Procurando, porém, os marinheiros fugir do navio, e tendo já deitado o batel ao mar, como que querendo lançar as âncoras pela proa,', 30, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse Paulo ao centurião e aos soldados: Se estes não ficarem no navio, não podereis salvar-vos.', 31, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então os soldados cortaram os cabos do batel, e o deixaram cair.', 32, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entretanto que o dia vinha, Paulo exortava a todos a que comessem alguma coisa, dizendo: É já hoje o décimo quarto dia que esperais, e permaneceis sem comer, não havendo provado nada.', 33, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, exorto-vos a que comais alguma coisa, pois é para a vossa saúde; porque nem um cabelo cairá da cabeça de qualquer de vós.', 34, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo dito isto, tomando o pão, deu graças a Deus na presença de todos; e, partindo-o, começou a comer.', 35, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo já todos bom ânimo, puseram-se também a comer.', 36, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E éramos ao todo, no navio, duzentas e setenta e seis almas.', 37, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, refeitos com a comida, aliviaram o navio, lançando o trigo ao mar.', 38, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo já dia, não conheceram a terra; enxergaram, porém, uma enseada que tinha praia, e consultaram-se sobre se deveriam encalhar nela o navio.', 39, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando as âncoras, deixaram-no ir ao mar, largando também as amarras do leme; e, alçando a vela maior ao vento, dirigiram-se para a praia.', 40, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dando, porém, num lugar de dois mares, encalharam ali o navio; e, fixa a proa, ficou imóvel, mas a popa abria-se com a força das ondas.', 41, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então a idéia dos soldados foi que matassem os presos para que nenhum fugisse, escapando a nado.', 42, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o centurião, querendo salvar a Paulo, lhes estorvou este intento; e mandou que os que pudessem nadar se lançassem primeiro ao mar, e se salvassem em terra;', 43, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os demais, uns em tábuas e outros em coisas do navio. E assim aconteceu que todos chegaram à terra a salvo.', 44, 27, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sucedeu que, enquanto Apolo estava em Corinto, Paulo, tendo passado por todas as regiões superiores, chegou a Éfeso; e achando ali alguns discípulos,', 1, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes: Recebestes vós já o Espírito Santo quando crestes? E eles disseram-lhe: Nós nem ainda ouvimos que haja Espírito Santo.', 2, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Perguntou-lhes, então: Em que sois batizados então? E eles disseram: No batismo de João.', 3, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Paulo disse: Certamente João batizou com o batismo de arrependimento, dizendo ao povo que cresse no que após ele havia de vir, isto é, em Jesus Cristo.', 4, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que ouviram foram batizados em nome do Senhor Jesus.', 5, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, impondo-lhes Paulo as mãos, veio sobre eles o Espírito Santo; e falavam línguas, e profetizavam.', 6, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estes eram, ao todo, uns doze homens.', 7, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando na sinagoga, falou ousadamente por espaço de três meses, disputando e persuadindo-os acerca do reino de Deus.', 8, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, como alguns deles se endurecessem e não obedecessem, falando mal do Caminho perante a multidão, retirou-se deles, e separou os discípulos, disputando todos os dias na escola de um certo Tirano.', 9, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E durou isto por espaço de dois anos; de tal maneira que todos os que habitavam na Ásia ouviram a palavra do Senhor Jesus, assim judeus como gregos.', 10, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Deus pelas mãos de Paulo fazia maravilhas extraordinárias.', 11, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que até os lenços e aventais se levavam do seu corpo aos enfermos, e as enfermidades fugiam deles, e os espíritos malignos saíam.', 12, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns dos exorcistas judeus ambulantes tentavam invocar o nome do Senhor Jesus sobre os que tinham espíritos malignos, dizendo: Esconjuro-vos por Jesus a quem Paulo prega.', 13, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que faziam isto eram sete filhos de Ceva, judeu, principal dos sacerdotes.', 14, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondendo, porém, o espírito maligno, disse: Conheço a Jesus, e bem sei quem é Paulo; mas vós quem sois?', 15, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saltando neles o homem que tinha o espírito maligno, e assenhoreando-se de todos, pôde mais do que eles; de tal maneira que, nus e feridos, fugiram daquela casa.', 16, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi isto notório a todos os que habitavam em Éfeso, tanto judeus como gregos; e caiu temor sobre todos eles, e o nome do Senhor Jesus era engrandecido.', 17, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos dos que tinham crido vinham, confessando e publicando os seus feitos.', 18, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Também muitos dos que seguiam artes mágicas trouxeram os seus livros, e os queimaram na presença de todos e, feita a conta do seu preço, acharam que montava a cinqüenta mil peças de prata.', 19, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim a palavra do Senhor crescia poderosamente e prevalecia.', 20, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, cumpridas estas coisas, Paulo propôs, em espírito, ir a Jerusalém, passando pela Macedônia e pela Acaia, dizendo: Depois que houver estado ali, importa-me ver também Roma.', 21, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, enviando à Macedônia dois daqueles que o serviam, Timóteo e Erasto, ficou ele por algum tempo na Ásia.', 22, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, naquele mesmo tempo, houve um não pequeno alvoroço acerca do Caminho.', 23, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque um certo ourives da prata, por nome Demétrio, que fazia de prata nichos de Diana, dava não pouco lucro aos artífices,', 24, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aos quais, havendo-os ajuntado com os oficiais de obras semelhantes, disse: Senhores, vós bem sabeis que deste ofício temos a nossa prosperidade;', 25, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E bem vedes e ouvis que não só em Éfeso, mas até quase em toda a Ásia, este Paulo tem convencido e afastado uma grande multidão, dizendo que não são deuses os que se fazem com as mãos.', 26, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não somente há o perigo de que a nossa profissão caia em descrédito, mas também de que o próprio templo da grande deusa Diana seja estimado em nada, vindo a ser destruída a majestade daquela que toda a Ásia e o mundo veneram.', 27, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo-o, encheram-se de ira, e clamaram, dizendo: Grande é a Diana dos efésios.', 28, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E encheu-se de confusão toda a cidade e, unânimes, correram ao teatro, arrebatando a Gaio e a Aristarco, macedônios, companheiros de Paulo na viagem.', 29, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, querendo Paulo apresentar-se ao povo, não lho permitiram os discípulos.', 30, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também alguns dos principais da Ásia, que eram seus amigos, lhe rogaram que não se apresentasse no teatro.', 31, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Uns, pois, clamavam de uma maneira, outros de outra, porque o ajuntamento era confuso; e os mais deles não sabiam por que causa se tinham ajuntado.', 32, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então tiraram Alexandre dentre a multidão, impelindo-o os judeus para diante; e Alexandre, acenando com a mão, queria dar razão disto ao povo.', 33, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas quando conheceram que era judeu, todos unanimemente levantaram a voz, clamando por espaço de quase duas horas: Grande é a Diana dos efésios.', 34, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o escrivão da cidade, tendo apaziguado a multidão, disse: Homens efésios, qual é o homem que não sabe que a cidade dos efésios é a guardadora do templo da grande deusa Diana, e da imagem que desceu de Júpiter?', 35, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, não podendo isto ser contraditado, convém que vos aplaqueis e nada façais temerariamente;', 36, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque estes homens que aqui trouxestes nem são sacrílegos nem blasfemam da vossa deusa.', 37, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se Demétrio e os artífices que estão com ele têm alguma coisa contra alguém, há audiências e há procônsules; que se acusem uns aos outros;', 38, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se alguma outra coisa demandais, averiguar-se-á em legítima assembléia.', 39, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade até corremos perigo de que, por hoje, sejamos acusados de sedição, não havendo causa alguma com que possamos justificar este concurso.', 40, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo dito isto, despediu a assembléia.', 41, 19, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede quão grande amor nos tem concedido o Pai, que fôssemos chamados filhos de Deus. Por isso o mundo não nos conhece; porque não o conhece a ele.', 1, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amados, agora somos filhos de Deus, e ainda não é manifestado o que havemos de ser. Mas sabemos que, quando ele se manifestar, seremos semelhantes a ele; porque assim como é o veremos.', 2, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qualquer que nele tem esta esperança purifica-se a si mesmo, como também ele é puro.', 3, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que comete pecado, também comete iniqüidade; porque o pecado é iniqüidade.', 4, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E bem sabeis que ele se manifestou para tirar os nossos pecados; e nele não há pecado.', 5, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que permanece nele não peca; qualquer que peca não o viu nem o conheceu.', 6, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Filhinhos, ninguém vos engane. Quem pratica justiça é justo, assim como ele é justo.', 7, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem comete o pecado é do diabo; porque o diabo peca desde o princípio. Para isto o Filho de Deus se manifestou: para desfazer as obras do diabo.', 8, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que é nascido de Deus não comete pecado; porque a sua semente permanece nele; e não pode pecar, porque é nascido de Deus.', 9, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto são manifestos os filhos de Deus, e os filhos do diabo. Qualquer que não pratica a justiça, e não ama a seu irmão, não é de Deus.', 10, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque esta é a mensagem que ouvistes desde o princípio: que nos amemos uns aos outros.', 11, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não como Caim, que era do maligno, e matou a seu irmão. E por que causa o matou? Porque as suas obras eram más e as de seu irmão justas.', 12, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meus irmãos, não vos maravilheis, se o mundo vos odeia.', 13, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nós sabemos que passamos da morte para a vida, porque amamos os irmãos. Quem não ama a seu irmão permanece na morte.', 14, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que odeia a seu irmão é homicida. E vós sabeis que nenhum homicida tem a vida eterna permanecendo nele.', 15, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conhecemos o amor nisto: que ele deu a sua vida por nós, e nós devemos dar a vida pelos irmãos.', 16, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem, pois, tiver bens do mundo, e, vendo o seu irmão necessitado, lhe cerrar as suas entranhas, como estará nele o amor de Deus?', 17, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meus filhinhos, não amemos de palavra, nem de língua, mas por obra e em verdade.', 18, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nisto conhecemos que somos da verdade, e diante dele asseguraremos nossos corações;', 19, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo que, se o nosso coração nos condena, maior é Deus do que o nosso coração, e conhece todas as coisas.', 20, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amados, se o nosso coração não nos condena, temos confiança para com Deus;', 21, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qualquer coisa que lhe pedirmos, dele a receberemos, porque guardamos os seus mandamentos, e fazemos o que é agradável à sua vista.', 22, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o seu mandamento é este: que creiamos no nome de seu Filho Jesus Cristo, e nos amemos uns aos outros, segundo o seu mandamento.', 23, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele que guarda os seus mandamentos nele está, e ele nele. E nisto conhecemos que ele está em nós, pelo Espírito que nos tem dado.', 24, 3, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, naqueles dias, crescendo o número dos discípulos, houve uma murmuração dos gregos contra os hebreus, porque as suas viúvas eram desprezadas no ministério cotidiano.', 1, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os doze, convocando a multidão dos discípulos, disseram: Não é razoável que nós deixemos a palavra de Deus e sirvamos às mesas.', 2, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Escolhei, pois, irmãos, dentre vós, sete homens de boa reputação, cheios do Espírito Santo e de sabedoria, aos quais constituamos sobre este importante negócio.', 3, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nós perseveraremos na oração e no ministério da palavra.', 4, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E este parecer contentou a toda a multidão, e elegeram Estêvão, homem cheio de fé e do Espírito Santo, e Filipe, e Prócoro, e Nicanor, e Timão, e Parmenas e Nicolau, prosélito de Antioquia;', 5, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os apresentaram ante os apóstolos, e estes, orando, lhes impuseram as mãos.', 6, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E crescia a palavra de Deus, e em Jerusalém se multiplicava muito o número dos discípulos, e grande parte dos sacerdotes obedecia à fé.', 7, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Estêvão, cheio de fé e de poder, fazia prodígios e grandes sinais entre o povo.', 8, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levantaram-se alguns que eram da sinagoga chamada dos libertinos, e dos cireneus e dos alexandrinos, e dos que eram da Cilícia e da Asia, e disputavam com Estêvão.', 9, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não podiam resistir à sabedoria, e ao Espírito com que falava.', 10, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então subornaram uns homens, para que dissessem: Ouvimos-lhe proferir palavras blasfemas contra Moisés e contra Deus.', 11, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E excitaram o povo, os anciãos e os escribas; e, investindo contra ele, o arrebataram e o levaram ao conselho.', 12, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E apresentaram falsas testemunhas, que diziam: Este homem não cessa de proferir palavras blasfemas contra este santo lugar e a lei;', 13, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nós lhe ouvimos dizer que esse Jesus Nazareno há de destruir este lugar e mudar os costumes que Moisés nos deu.', 14, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então todos os que estavam assentados no conselho, fixando os olhos nele, viram o seu rosto como o rosto de um anjo.', 15, 6, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, cinco dias depois, o sumo sacerdote Ananias desceu com os anciãos, e um certo Tértulo, orador, os quais compareceram perante o presidente contra Paulo.', 1, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo chamado, Tértulo começou a acusá-lo, dizendo: Visto como por ti temos tanta paz e por tua prudência se fazem a este povo muitos e louváveis serviços,', 2, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sempre e em todo o lugar, ó potentíssimo Félix, com todo o agradecimento o queremos reconhecer.', 3, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, para que não te detenha muito, rogo-te que, conforme a tua eqüidade, nos ouças por pouco tempo.', 4, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Temos achado que este homem é uma peste, e promotor de sedições entre todos os judeus, por todo o mundo; e o principal defensor da seita dos nazarenos;', 5, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual intentou também profanar o templo; e nós o prendemos, e conforme a nossa lei o quisemos julgar.', 6, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, sobrevindo o tribuno Lísias, no-lo tirou de entre as mãos com grande violência,', 7, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mandando aos seus acusadores que viessem a ti; e dele tu mesmo, examinando-o, poderás entender tudo o de que o acusamos.', 8, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também os judeus consentiam, dizendo serem estas coisas assim.', 9, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, porém, fazendo-lhe o presidente sinal que falasse, respondeu: Porque sei que já vai para muitos anos que desta nação és juiz, com tanto melhor ânimo respondo por mim.', 10, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois bem podes saber que não há mais de doze dias que subi a Jerusalém a adorar;', 11, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não me acharam no templo falando com alguém, nem amotinando o povo nas sinagogas, nem na cidade.', 12, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem tampouco podem provar as coisas de que agora me acusam.', 13, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas confesso-te isto que, conforme aquele caminho que chamam seita, assim sirvo ao Deus de nossos pais, crendo tudo quanto está escrito na lei e nos profetas.', 14, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo esperança em Deus, como estes mesmos também esperam, de que há de haver ressurreição de mortos, assim dos justos como dos injustos.', 15, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por isso procuro sempre ter uma consciência sem ofensa, tanto para com Deus como para com os homens.', 16, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, muitos anos depois, vim trazer à minha nação esmolas e ofertas.', 17, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto me acharam já santificado no templo, não em ajuntamentos, nem com alvoroços, uns certos judeus da Ásia,', 18, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais convinha que estivessem presentes perante ti, e me acusassem, se alguma coisa contra mim tivessem.', 19, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou digam estes mesmos, se acharam em mim alguma iniqüidade, quando compareci perante o conselho,', 20, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A não ser estas palavras que, estando entre eles, clamei: Hoje sou julgado por vós acerca da ressurreição dos mortos.', 21, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Félix, havendo ouvido estas coisas, lhes pôs dilação, dizendo: Havendo-me informado melhor deste Caminho, quando o tribuno Lísias tiver descido, então tomarei inteiro conhecimento dos vossos negócios.', 22, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mandou ao centurião que o guardasse em prisão, tratando-o com brandura, e que a ninguém dos seus proibisse servi-lo ou vir ter com ele.', 23, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns dias depois, vindo Félix com sua mulher Drusila, que era judia, mandou chamar a Paulo, e ouviu-o acerca da fé em Cristo.', 24, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tratando ele da justiça, e da temperança, e do juízo vindouro, Félix, espavorido, respondeu: Por agora vai-te, e em tendo oportunidade te chamarei.', 25, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esperando ao mesmo tempo que Paulo lhe desse dinheiro, para que o soltasse; pelo que também muitas vezes o mandava chamar, e falava com ele.', 26, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, passados dois anos, Félix teve por sucessor a Pórcio Festo; e, querendo Félix comprazer aos judeus, deixou a Paulo preso.', 27, 24, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Resta, irmãos meus, que vos regozijeis no Senhor. Não me aborreço de escrever-vos as mesmas coisas, e é segurança para vós.', 1, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Guardai-vos dos cães, guardai-vos dos maus obreiros, guardai-vos da circuncisão;', 2, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a circuncisão somos nós, que servimos a Deus em espírito, e nos gloriamos em Jesus Cristo, e não confiamos na carne.', 3, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ainda que também podia confiar na carne; se algum outro cuida que pode confiar na carne, ainda mais eu:', 4, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Circuncidado ao oitavo dia, da linhagem de Israel, da tribo de Benjamim, hebreu de hebreus; segundo a lei, fui fariseu;', 5, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segundo o zelo, perseguidor da igreja, segundo a justiça que há na lei, irrepreensível.', 6, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que para mim era ganho reputei-o perda por Cristo.', 7, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, na verdade, tenho também por perda todas as coisas, pela excelência do conhecimento de Cristo Jesus, meu Senhor; pelo qual sofri a perda de todas estas coisas, e as considero como escória, para que possa ganhar a Cristo,', 8, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E seja achado nele, não tendo a minha justiça que vem da lei, mas a que vem pela fé em Cristo, a saber, a justiça que vem de Deus pela fé;', 9, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para conhecê-lo, e à virtude da sua ressurreição, e à comunicação de suas aflições, sendo feito conforme à sua morte;', 10, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para ver se de alguma maneira posso chegar à ressurreição dentre os mortos.', 11, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não que já a tenha alcançado, ou que seja perfeito; mas prossigo para alcançar aquilo para o que fui também preso por Cristo Jesus.', 12, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, quanto a mim, não julgo que o haja alcançado; mas uma coisa faço, e é que, esquecendo-me das coisas que atrás ficam, e avançando para as que estão diante de mim,', 13, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Prossigo para o alvo, pelo prêmio da soberana vocação de Deus em Cristo Jesus.', 14, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso todos quantos já somos perfeitos, sintamos isto mesmo; e, se sentis alguma coisa de outra maneira, também Deus vo-lo revelará.', 15, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, naquilo a que já chegamos, andemos segundo a mesma regra, e sintamos o mesmo.', 16, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sede também meus imitadores, irmãos, e tende cuidado, segundo o exemplo que tendes em nós, pelos que assim andam.', 17, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque muitos há, dos quais muitas vezes vos disse, e agora também digo, chorando, que são inimigos da cruz de Cristo,', 18, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cujo fim é a perdição; cujo Deus é o ventre, e cuja glória é para confusão deles, que só pensam nas coisas terrenas.', 19, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a nossa cidade está nos céus, de onde também esperamos o Salvador, o Senhor Jesus Cristo,', 20, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que transformará o nosso corpo abatido, para ser conforme o seu corpo glorioso, segundo o seu eficaz poder de sujeitar também a si todas as coisas.', 21, 3, 11);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meus filhinhos, estas coisas vos escrevo, para que não pequeis; e, se alguém pecar, temos um Advogado para com o Pai, Jesus Cristo, o justo.', 1, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele é a propiciação pelos nossos pecados, e não somente pelos nossos, mas também pelos de todo o mundo.', 2, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nisto sabemos que o conhecemos: se guardarmos os seus mandamentos.', 3, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que diz: Eu conheço-o, e não guarda os seus mandamentos, é mentiroso, e nele não está a verdade.', 4, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas qualquer que guarda a sua palavra, o amor de Deus está nele verdadeiramente aperfeiçoado; nisto conhecemos que estamos nele.', 5, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que diz que está nele, também deve andar como ele andou.', 6, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, não vos escrevo mandamento novo, mas o mandamento antigo, que desde o princípio tivestes. Este mandamento antigo é a palavra que desde o princípio ouvistes.', 7, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Outra vez vos escrevo um mandamento novo, que é verdadeiro nele e em vós; porque vão passando as trevas, e já a verdadeira luz ilumina.', 8, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que diz que está na luz, e odeia a seu irmão, até agora está em trevas.', 9, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que ama a seu irmão está na luz, e nele não há escândalo.', 10, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas aquele que odeia a seu irmão está em trevas, e anda em trevas, e não sabe para onde deva ir; porque as trevas lhe cegaram os olhos.', 11, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Filhinhos, escrevo-vos, porque pelo seu nome vos são perdoados os pecados.', 12, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pais, escrevo-vos, porque conhecestes aquele que é desde o princípio. Jovens, escrevo-vos, porque vencestes o maligno. Eu vos escrevo, filhos, porque conhecestes o Pai.', 13, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu vos escrevi, pais, porque já conhecestes aquele que é desde o princípio. Eu vos escrevi, jovens, porque sois fortes, e a palavra de Deus está em vós, e já vencestes o maligno.', 14, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não ameis o mundo, nem o que no mundo há. Se alguém ama o mundo, o amor do Pai não está nele.', 15, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tudo o que há no mundo, a concupiscência da carne, a concupiscência dos olhos e a soberba da vida, não é do Pai, mas do mundo.', 16, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o mundo passa, e a sua concupiscência; mas aquele que faz a vontade de Deus permanece para sempre.', 17, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Filhinhos, é já a última hora; e, como ouvistes que vem o anticristo, também agora muitos se têm feito anticristos, por onde conhecemos que é já a última hora.', 18, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saíram de nós, mas não eram de nós; porque, se fossem de nós, ficariam conosco; mas isto é para que se manifestasse que não são todos de nós.', 19, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós tendes a unção do Santo, e sabeis todas as coisas.', 20, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos escrevi porque não soubésseis a verdade, mas porque a sabeis, e porque nenhuma mentira vem da verdade.', 21, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem é o mentiroso, senão aquele que nega que Jesus é o Cristo? É o anticristo esse mesmo que nega o Pai e o Filho.', 22, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que nega o Filho, também não tem o Pai; mas aquele que confessa o Filho, tem também o Pai.', 23, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, o que desde o princípio ouvistes permaneça em vós. Se em vós permanecer o que desde o princípio ouvistes, também permanecereis no Filho e no Pai.', 24, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E esta é a promessa que ele nos fez: a vida eterna.', 25, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estas coisas vos escrevi acerca dos que vos enganam.', 26, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a unção que vós recebestes dele, fica em vós, e não tendes necessidade de que alguém vos ensine; mas, como a sua unção vos ensina todas as coisas, e é verdadeira, e não é mentira, como ela vos ensinou, assim nele permanecereis.', 27, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora, filhinhos, permanecei nele; para que, quando ele se manifestar, tenhamos confiança, e não sejamos confundidos por ele na sua vinda.', 28, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se sabeis que ele é justo, sabeis que todo aquele que pratica a justiça é nascido dele.', 29, 2, 23);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia em Cesaréia um homem por nome Cornélio, centurião da coorte chamada italiana,', 1, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Piedoso e temente a Deus, com toda a sua casa, o qual fazia muitas esmolas ao povo, e de contínuo orava a Deus.', 2, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este, quase à hora nona do dia, viu claramente numa visão um anjo de Deus, que se dirigia para ele e dizia: Cornélio.', 3, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, fixando os olhos nele, e muito atemorizado, disse: Que é, Senhor? E disse-lhe: As tuas orações e as tuas esmolas têm subido para memória diante de Deus;', 4, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora, pois, envia homens a Jope, e manda chamar a Simão, que tem por sobrenome Pedro.', 5, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este está hospedado com um certo Simão curtidor, que tem a sua casa junto do mar. Ele te dirá o que deves fazer.', 6, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, retirando-se o anjo que lhe falava, chamou dois dos seus criados, e a um piedoso soldado dos que estavam ao seu serviço.', 7, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-lhes contado tudo, os enviou a Jope.', 8, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no dia seguinte, indo eles seu caminho, e estando já perto da cidade, subiu Pedro ao terraço para orar, quase à hora sexta.', 9, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tendo fome, quis comer; e, enquanto lho preparavam, sobreveio-lhe um arrebatamento de sentidos,', 10, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E viu o céu aberto, e que descia um vaso, como se fosse um grande lençol atado pelas quatro pontas, e vindo para a terra.', 11, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No qual havia de todos os animais quadrúpedes e feras e répteis da terra, e aves do céu.', 12, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi-lhe dirigida uma voz: Levanta-te, Pedro, mata e come.', 13, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Pedro disse: De modo nenhum, Senhor, porque nunca comi coisa alguma comum e imunda.', 14, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E segunda vez lhe disse a voz: Não faças tu comum ao que Deus purificou.', 15, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu isto por três vezes; e o vaso tornou a recolher-se ao céu.', 16, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estando Pedro duvidando entre si acerca do que seria aquela visão que tinha visto, eis que os homens que foram enviados por Cornélio pararam à porta, perguntando pela casa de Simão.', 17, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando, perguntaram se Simão, que tinha por sobrenome Pedro, morava ali.', 18, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pensando Pedro naquela visão, disse-lhe o Espírito: Eis que três homens te buscam.', 19, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Levanta-te pois, desce, e vai com eles, não duvidando; porque eu os enviei.', 20, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, descendo Pedro para junto dos homens que lhe foram enviados por Cornélio, disse: Eis que sou eu a quem procurais; qual é a causa por que estais aqui?', 21, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles disseram: Cornélio, o centurião, homem justo e temente a Deus, e que tem bom testemunho de toda a nação dos judeus, foi avisado por um santo anjo para que te chamasse a sua casa, e ouvisse as tuas palavras.', 22, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, chamando-os para dentro, os recebeu em casa. E no dia seguinte foi Pedro com eles, e foram com ele alguns irmãos de Jope.', 23, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no dia imediato chegaram a Cesaréia. E Cornélio os estava esperando, tendo já convidado os seus parentes e amigos mais íntimos.', 24, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, entrando Pedro, saiu Cornélio a recebê-lo, e, prostrando-se a seus pés o adorou.', 25, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Pedro o levantou, dizendo: Levanta-te, que eu também sou homem.', 26, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, falando com ele, entrou, e achou muitos que ali se haviam ajuntado.', 27, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Vós bem sabeis que não é lícito a um homem judeu ajuntar-se ou chegar-se a estrangeiros; mas Deus mostrou-me que a nenhum homem chame comum ou imundo.', 28, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, sendo chamado, vim sem contradizer. Pergunto, pois, por que razão mandastes chamar-me?', 29, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Cornélio: Há quatro dias estava eu em jejum até esta hora, orando em minha casa à hora nona.', 30, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que diante de mim se apresentou um homem com vestes resplandecentes, e disse: Cornélio, a tua oração foi ouvida, e as tuas esmolas estão em memória diante de Deus.', 31, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Envia, pois, a Jope, e manda chamar Simão, o que tem por sobrenome Pedro; este está hospedado em casa de Simão o curtidor, junto do mar, e ele, vindo, te falará.', 32, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo mandei chamar-te, e bem fizeste em vir. Agora, pois, estamos todos presentes diante de Deus, para ouvir tudo quanto por Deus te é mandado.', 33, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, abrindo Pedro a boca, disse: Reconheço por verdade que Deus não faz acepção de pessoas;', 34, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas que lhe é agradável aquele que, em qualquer nação, o teme e faz o que é justo.', 35, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A palavra que ele enviou aos filhos de Israel, anunciando a paz por Jesus Cristo (este é o Senhor de todos);', 36, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esta palavra, vós bem sabeis, veio por toda a Judéia, começando pela Galiléia, depois do batismo que João pregou;', 37, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como Deus ungiu a Jesus de Nazaré com o Espírito Santo e com virtude; o qual andou fazendo bem, e curando a todos os oprimidos do diabo, porque Deus era com ele.', 38, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nós somos testemunhas de todas as coisas que fez, tanto na terra da Judéia como em Jerusalém; ao qual mataram, pendurando-o num madeiro.', 39, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A este ressuscitou Deus ao terceiro dia, e fez que se manifestasse,', 40, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não a todo o povo, mas às testemunhas que Deus antes ordenara; a nós, que comemos e bebemos juntamente com ele, depois que ressuscitou dentre os mortos.', 41, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nos mandou pregar ao povo, e testificar que ele é o que por Deus foi constituído juiz dos vivos e dos mortos.', 42, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A este dão testemunho todos os profetas, de que todos os que nele crêem receberão o perdão dos pecados pelo seu nome.', 43, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dizendo Pedro ainda estas palavras, caiu o Espírito Santo sobre todos os que ouviam a palavra.', 44, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os fiéis que eram da circuncisão, todos quantos tinham vindo com Pedro, maravilharam-se de que o dom do Espírito Santo se derramasse também sobre os gentios.', 45, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os ouviam falar línguas, e magnificar a Deus.', 46, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu, então, Pedro: Pode alguém porventura recusar a água, para que não sejam batizados estes, que também receberam como nós o Espírito Santo?', 47, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mandou que fossem batizados em nome do Senhor. Então rogaram-lhe que ficasse com eles por alguns dias.', 48, 10, 5);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Semelhantemente, vós, mulheres, sede sujeitas aos vossos próprios maridos; para que também, se alguns não obedecem à palavra, pelo porte de suas mulheres sejam ganhos sem palavra;', 1, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Considerando a vossa vida casta, em temor.', 2, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O enfeite delas não seja o exterior, no frisado dos cabelos, no uso de jóias de ouro, na compostura dos vestidos;', 3, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o homem encoberto no coração; no incorruptível traje de um espírito manso e quieto, que é precioso diante de Deus.', 4, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque assim se adornavam também antigamente as santas mulheres que esperavam em Deus, e estavam sujeitas aos seus próprios maridos;', 5, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como Sara obedecia a Abraão, chamando-lhe senhor; da qual vós sois filhas, fazendo o bem, e não temendo nenhum espanto.', 6, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Igualmente vós, maridos, coabitai com elas com entendimento, dando honra à mulher, como vaso mais fraco; como sendo vós os seus co-herdeiros da graça da vida; para que não sejam impedidas as vossas orações.', 7, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, finalmente, sede todos de um mesmo sentimento, compassivos, amando os irmãos, entranhavelmente misericordiosos e afáveis.', 8, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não tornando mal por mal, ou injúria por injúria; antes, pelo contrário, bendizendo; sabendo que para isto fostes chamados, para que por herança alcanceis a bênção.', 9, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Quem quer amar a vida, E ver os dias bons, Refreie a sua língua do mal, E os seus lábios não falem engano.', 10, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aparte-se do mal, e faça o bem; Busque a paz, e siga-a.', 11, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os olhos do Senhor estão sobre os justos, E os seus ouvidos atentos às suas orações; Mas o rosto do Senhor é contra os que fazem o mal.', 12, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qual é aquele que vos fará mal, se fordes seguidores do bem?', 13, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas também, se padecerdes por amor da justiça, sois bemaventurados. E não temais com medo deles, nem vos turbeis;', 14, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, santificai ao Senhor Deus em vossos corações; e estai sempre preparados para responder com mansidão e temor a qualquer que vos pedir a razão da esperança que há em vós,', 15, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo uma boa consciência, para que, naquilo em que falam mal de vós, como de malfeitores, fiquem confundidos os que blasfemam do vosso bom porte em Cristo.', 16, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque melhor é que padeçais fazendo bem (se a vontade de Deus assim o quer), do que fazendo mal.', 17, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também Cristo padeceu uma vez pelos pecados, o justo pelos injustos, para levar-nos a Deus; mortificado, na verdade, na carne, mas vivificado pelo Espírito;', 18, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No qual também foi, e pregou aos espíritos em prisão;', 19, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais noutro tempo foram rebeldes, quando a longanimidade de Deus esperava nos dias de Noé, enquanto se preparava a arca; na qual poucas (isto é, oito) almas se salvaram pela água;', 20, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que também, como uma verdadeira figura, agora vos salva, o batismo, não do despojamento da imundícia da carne, mas da indagação de uma boa consciência para com Deus, pela ressurreição de Jesus Cristo;', 21, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual está à destra de Deus, tendo subido ao céu, havendo-se-lhe sujeitado os anjos, e as autoridades, e as potências.', 22, 3, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, pois, já que Cristo padeceu por nós na carne, armai-vos também vós com este mesmo pensamento, que aquele que padeceu na carne já cessou do pecado;', 1, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que, no tempo que vos resta na carne, não vivais mais segundo as concupiscências dos homens, mas segundo a vontade de Deus.', 2, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque é bastante que no tempo passado da vida fizéssemos a vontade dos gentios, andando em dissoluções, concupiscências, borrachices, glutonarias, bebedices e abomináveis idolatrias;', 3, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E acham estranho não correrdes com eles no mesmo desenfreamento de dissolução, blasfemando de vós.', 4, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais hão de dar conta ao que está preparado para julgar os vivos e os mortos.', 5, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque por isto foi pregado o evangelho também aos mortos, para que, na verdade, fossem julgados segundo os homens na carne, mas vivessem segundo Deus em espírito;', 6, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E já está próximo o fim de todas as coisas; portanto sede sóbrios e vigiai em oração.', 7, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, sobretudo, tende ardente amor uns para com os outros; porque o amor cobrirá a multidão de pecados.', 8, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo hospitaleiros uns para com os outros, sem murmurações,', 9, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cada um administre aos outros o dom como o recebeu, como bons despenseiros da multiforme graça de Deus.', 10, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém falar, fale segundo as palavras de Deus; se alguém administrar, administre segundo o poder que Deus dá; para que em tudo Deus seja glorificado por Jesus Cristo, a quem pertence a glória e poder para todo o sempre. Amém.', 11, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amados, não estranheis a ardente prova que vem sobre vós para vos tentar, como se coisa estranha vos acontecesse;', 12, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas alegrai-vos no fato de serdes participantes das aflições de Cristo, para que também na revelação da sua glória vos regozijeis e alegreis.', 13, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se pelo nome de Cristo sois vituperados, bem-aventurados sois, porque sobre vós repousa o Espírito da glória e de Deus; quanto a eles, é ele, sim, blasfemado, mas quanto a vós, é glorificado.', 14, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que nenhum de vós padeça como homicida, ou ladrão, ou malfeitor, ou como o que se entremete em negócios alheios;', 15, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se padece como cristão, não se envergonhe, antes glorifique a Deus nesta parte.', 16, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já é tempo que comece o julgamento pela casa de Deus; e, se primeiro começa por nós, qual será o fim daqueles que são desobedientes ao evangelho de Deus?', 17, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se o justo apenas se salva, onde aparecerá o ímpio e o pecador?', 18, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto também os que padecem segundo a vontade de Deus encomendem-lhe as suas almas, como ao fiel Criador, fazendo o bem.', 19, 4, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo, pois, muitos empreendido pôr em ordem a narração dos fatos que entre nós se cumpriram,', 1, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segundo nos transmitiram os mesmos que os presenciaram desde oprincípio, e foram ministros da palavra,', 2, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pareceu-me também a mim conveniente descrevê-los a ti, ó excelente Teófilo, por sua ordem, havendo-me já informado minuciosamente de tudo desde o princípio;', 3, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que conheças a certeza das coisas de que já estás informado.', 4, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Existiu, no tempo de Herodes, rei da Judéia, um sacerdote chamado Zacarias, da ordem de Abias, e cuja mulher era das filhas de Arão; e o seu nome era Isabel.', 5, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eram ambos justos perante Deus, andando sem repreensão em todos os mandamentos e preceitos do Senhor.', 6, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não tinham filhos, porque Isabel era estéril, e ambos eram avançados em idade.', 7, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, exercendo ele o sacerdócio diante de Deus, na ordem da sua turma,', 8, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segundo o costume sacerdotal, coube-lhe em sorte entrar no templo do Senhor para oferecer o incenso.', 9, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E toda a multidão do povo estava fora, orando, à hora do incenso.', 10, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um anjo do Senhor lhe apareceu, posto em pé, à direita do altar do incenso.', 11, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Zacarias, vendo-o, turbou-se, e caiu temor sobre ele.', 12, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o anjo lhe disse: Zacarias, não temas, porque a tua oração foi ouvida, e Isabel, tua mulher, dará à luz um filho, e lhe porás o nome de João.', 13, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E terás prazer e alegria, e muitos se alegrarão no seu nascimento,', 14, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque será grande diante do Senhor, e não beberá vinho, nem bebida forte, e será cheio do Espírito Santo, já desde o ventre de sua mãe.', 15, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E converterá muitos dos filhos de Israel ao Senhor seu Deus,', 16, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E irá adiante dele no espírito e virtude de Elias, para converter os corações dos pais aos filhos, e os rebeldes à prudência dos justos, com o fim de preparar ao Senhor um povo bem disposto.', 17, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse então Zacarias ao anjo: Como saberei isto? pois eu já sou velho, e minha mulher avançada em idade.', 18, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo o anjo, disse-lhe: Eu sou Gabriel, que assisto diante de Deus, e fui enviado a falar-te e dar-te estas alegres novas.', 19, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que ficarás mudo, e não poderás falar até ao dia em que estas coisas aconteçam; porquanto não creste nas minhas palavras, que a seu tempo se hão de cumprir.', 20, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o povo estava esperando a Zacarias, e maravilhava-se de que tanto se demorasse no templo.', 21, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo ele, não lhes podia falar; e entenderam que tinha tido uma visão no templo. E falava por acenos, e ficou mudo.', 22, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sucedeu que, terminados os dias de seu ministério, voltou para sua casa.', 23, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois daqueles dias, Isabel, sua mulher, concebeu, e por cinco meses se ocultou, dizendo:', 24, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim me fez o Senhor, nos dias em que atentou em mim, para destruir o meu opróbrio entre os homens.', 25, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, no sexto mês, foi o anjo Gabriel enviado por Deus a uma cidade da Galiléia, chamada Nazaré,', 26, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A uma virgem desposada com um homem, cujo nome era José, da casa de Davi; e o nome da virgem era Maria.', 27, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando o anjo aonde ela estava, disse: Salve, agraciada; o Senhor é contigo; bendita és tu entre as mulheres.', 28, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo-o ela, turbou-se muito com aquelas palavras, e considerava que saudação seria esta.', 29, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe, então, o anjo: Maria, não temas, porque achaste graça diante de Deus.', 30, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que em teu ventre conceberás e darás à luz um filho, e pôr-lhe-ás o nome de Jesus.', 31, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este será grande, e será chamado filho do Altíssimo; e o Senhor Deus lhe dará o trono de Davi, seu pai;', 32, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E reinará eternamente na casa de Jacó, e o seu reino não terá fim.', 33, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Maria ao anjo: Como se fará isto, visto que não conheço homem algum?', 34, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo o anjo, disse-lhe: Descerá sobre ti o Espírito Santo, e a virtude do Altíssimo te cobrirá com a sua sombra; por isso também o Santo, que de ti há de nascer, será chamado Filho de Deus.', 35, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que também Isabel, tua prima, concebeu um filho em sua velhice; e é este o sexto mês para aquela que era chamada estéril;', 36, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque para Deus nada é impossível.', 37, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse então Maria: Eis aqui a serva do Senhor; cumpra-se em mim segundo a tua palavra. E o anjo ausentou-se dela.', 38, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, naqueles dias, levantando-se Maria, foi apressada às montanhas, a uma cidade de Judá,', 39, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E entrou em casa de Zacarias, e saudou a Isabel.', 40, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, ao ouvir Isabel a saudação de Maria, a criancinha saltou no seu ventre; e Isabel foi cheia do Espírito Santo.', 41, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E exclamou com grande voz, e disse: Bendita és tu entre as mulheres, e bendito o fruto do teu ventre.', 42, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de onde me provém isto a mim, que venha visitar-me a mãe do meu Senhor?', 43, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois eis que, ao chegar aos meus ouvidos a voz da tua saudação, a criancinha saltou de alegria no meu ventre.', 44, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurada a que creu, pois hão de cumprir-se as coisas que da parte do Senhor lhe foram ditas.', 45, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse então Maria: A minha alma engrandece ao Senhor,', 46, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o meu espírito se alegra em Deus meu Salvador;', 47, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque atentou na baixeza de sua serva; Pois eis que desde agora todas as gerações me chamarão bem-aventurada,', 48, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque me fez grandes coisas o Poderoso; E santo é seu nome.', 49, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a sua misericórdia é de geração em geração Sobre os que o temem.', 50, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Com o seu braço agiu valorosamente; Dissipou os soberbos no pensamento de seus corações.', 51, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depôs dos tronos os poderosos, E elevou os humildes.', 52, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Encheu de bens os famintos, E despediu vazios os ricos.', 53, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Auxiliou a Israel seu servo, Recordando-se da sua misericórdia;', 54, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como falou a nossos pais, Para com Abraão e a sua posteridade, para sempre.', 55, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Maria ficou com ela quase três meses, e depois voltou para sua casa.', 56, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E completou-se para Isabel o tempo de dar à luz, e teve um filho.', 57, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus vizinhos e parentes ouviram que tinha Deus usado para com ela de grande misericórdia, e alegraram-se com ela.', 58, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, ao oitavo dia, vieram circuncidar o menino, e lhe chamavam Zacarias, o nome de seu pai.', 59, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo sua mãe, disse: Não, porém será chamado João.', 60, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disseram-lhe: Ninguém há na tua parentela que se chame por este nome.', 61, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntaram por acenos ao pai como queria que lhe chamassem.', 62, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pedindo ele uma tabuinha de escrever, escreveu, dizendo: O seu nome é João. E todos se maravilharam.', 63, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo a boca se lhe abriu, e a língua se lhe soltou; e falava, louvando a Deus.', 64, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E veio temor sobre todos os seus vizinhos, e em todas as montanhas da Judéia foram divulgadas todas estas coisas.', 65, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os que as ouviam as conservavam em seus corações, dizendo: Quem será, pois, este menino? E a mão do Senhor estava com ele.', 66, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Zacarias, seu pai, foi cheio do Espírito Santo, e profetizou, dizendo:', 67, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bendito o Senhor Deus de Israel, Porque visitou e remiu o seu povo,', 68, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nos levantou uma salvação poderosa Na casa de Davi seu servo.', 69, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como falou pela boca dos seus santos profetas, desde o princípio do mundo;', 70, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para nos livrar dos nossos inimigos e da mão de todos os que nos odeiam;', 71, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para manifestar misericórdia a nossos pais, E lembrar-se da sua santa aliança,', 72, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E do juramento que jurou a Abraão nosso pai,', 73, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De conceder-nos que, Libertados da mão de nossos inimigos, o serviríamos sem temor,', 74, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em santidade e justiça perante ele, todos os dias da nossa vida.', 75, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tu, ó menino, serás chamado profeta do Altíssimo, Porque hás de ir ante a face do Senhor, a preparar os seus caminhos;', 76, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para dar ao seu povo conhecimento da salvação, Na remissão dos seus pecados;', 77, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelas entranhas da misericórdia do nosso Deus, Com que o oriente do alto nos visitou;', 78, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para iluminar aos que estão assentados em trevas e na sombra da morte; A fim de dirigir os nossos pés pelo caminho da paz.', 79, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o menino crescia, e se robustecia em espírito. E esteve nos desertos até ao dia em que havia de mostrar-se a Israel.', 80, 1, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, cheio do Espírito Santo, voltou do Jordão e foi levado pelo Espírito ao deserto;', 1, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quarenta dias foi tentado pelo diabo, e naqueles dias não comeu coisa alguma; e, terminados eles, teve fome.', 2, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe o diabo: Se tu és o Filho de Deus, dize a esta pedra que se transforme em pão.', 3, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhe respondeu, dizendo: Está escrito que nem só de pão viverá o homem, mas de toda a palavra de Deus.', 4, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o diabo, levando-o a um alto monte, mostrou-lhe num momento de tempo todos os reinos do mundo.', 5, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe o diabo: Dar-te-ei a ti todo este poder e a sua glória; porque a mim me foi entregue, e dou-o a quem quero.', 6, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, se tu me adorares, tudo será teu.', 7, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhe: Vai-te para trás de mim, Satanás; porque está escrito: Adorarás o Senhor teu Deus, e só a ele servirás.', 8, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Levou-o também a Jerusalém, e pô-lo sobre o pináculo do templo, e disse-lhe: Se tu és o Filho de Deus, lança-te daqui abaixo;', 9, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque está escrito: Mandará aos seus anjos, acerca de ti, que te guardem,', 10, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que te sustenham nas mãos, Para que nunca tropeces com o teu pé em alguma pedra.', 11, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhe: Dito está: Não tentarás ao Senhor teu Deus.', 12, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, acabando o diabo toda a tentação, ausentou-se dele por algum tempo.', 13, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, pela virtude do Espírito, voltou Jesus para a Galiléia, e a sua fama correu por todas as terras em derredor.', 14, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ensinava nas suas sinagogas, e por todos era louvado.', 15, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando a Nazaré, onde fora criado, entrou num dia de sábado, segundo o seu costume, na sinagoga, e levantou-se para ler.', 16, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi-lhe dado o livro do profeta Isaías; e, quando abriu o livro, achou o lugar em que estava escrito:', 17, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Espírito do Senhor é sobre mim, Pois que me ungiu para evangelizar os pobres. Enviou-me a curar os quebrantados de coração,', 18, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A pregar liberdade aos cativos, E restauração da vista aos cegos, A pôr em liberdade os oprimidos, A anunciar o ano aceitável do Senhor.', 19, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, cerrando o livro, e tornando-o a dar ao ministro, assentou-se; e os olhos de todos na sinagoga estavam fitos nele.', 20, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então começou a dizer-lhes: Hoje se cumpriu esta Escritura em vossos ouvidos.', 21, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos lhe davam testemunho, e se maravilhavam das palavras de graça que saíam da sua boca; e diziam: Não é este o filho de José?', 22, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Sem dúvida me direis este provérbio: Médico, cura-te a ti mesmo; faze também aqui na tua pátria tudo que ouvimos ter sido feito em Cafarnaum.', 23, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse: Em verdade vos digo que nenhum profeta é bem recebido na sua pátria.', 24, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que muitas viúvas existiam em Israel nos dias de Elias, quando o céu se cerrou por três anos e seis meses, de sorte que em toda a terra houve grande fome;', 25, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a nenhuma delas foi enviado Elias, senão a Sarepta de Sidom, a uma mulher viúva.', 26, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos leprosos havia em Israel no tempo do profeta Eliseu, e nenhum deles foi purificado, senão Naamã, o siro.', 27, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos, na sinagoga, ouvindo estas coisas, se encheram de ira.', 28, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se, o expulsaram da cidade, e o levaram até ao cume do monte em que a cidade deles estava edificada, para dali o precipitarem.', 29, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, passando pelo meio deles, retirou-se.', 30, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desceu a Cafarnaum, cidade da Galiléia, e os ensinava nos sábados.', 31, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E admiravam a sua doutrina porque a sua palavra era com autoridade.', 32, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava na sinagoga um homem que tinha o espírito de um demônio imundo, e exclamou em alta voz,', 33, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Ah! que temos nós contigo, Jesus Nazareno? Vieste a destruir-nos? Bem sei quem és: O Santo de Deus.', 34, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus o repreendeu, dizendo: Cala-te, e sai dele. E o demônio, lançando-o por terra no meio do povo, saiu dele sem lhe fazer mal.', 35, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E veio espanto sobre todos, e falavam uns com os outros, dizendo: Que palavra é esta, que até aos espíritos imundos manda com autoridade e poder, e eles saem?', 36, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a sua fama divulgava-se por todos os lugares, em redor daquela comarca.', 37, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, levantando-se Jesus da sinagoga, entrou em casa de Simão; e a sogra de Simão estava enferma com muita febre, e rogaram-lhe por ela.', 38, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, inclinando-se para ela, repreendeu a febre, e esta a deixou. E ela, levantando-se logo, servia-os.', 39, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ao pôr do sol, todos os que tinham enfermos de várias doenças lhos traziam; e, pondo as mãos sobre cada um deles, os curava.', 40, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também de muitos saíam demônios, clamando e dizendo: Tu és o Cristo, o Filho de Deus. E ele, repreendendo-os, não os deixava falar, pois sabiam que ele era o Cristo.', 41, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo já dia, saiu, e foi para um lugar deserto; e a multidão o procurava, e chegou junto dele; e o detinham, para que não se ausentasse deles.', 42, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, lhes disse: Também é necessário que eu anuncie a outras cidades o evangelho do reino de Deus; porque para isso fui enviado.', 43, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pregava nas sinagogas da Galiléia.', 44, 4, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deixando, pois, toda a malícia, e todo o engano, e fingimentos, e invejas, e todas as murmurações,', 1, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desejai afetuosamente, como meninos novamente nascidos, o leite racional, não falsificado, para que por ele vades crescendo;', 2, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se é que já provastes que o Senhor é benigno;', 3, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando-vos para ele, pedra viva, reprovada, na verdade, pelos homens, mas para com Deus eleita e preciosa,', 4, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vòs também, como pedras vivas, sois edificados casa espiritual e sacerdòcio santo, para oferecer sacrifícios espirituais agradáveis a Deus por Jesus Cristo.', 5, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso também na Escritura se contém:Eis que ponho em Sião a pedra principal da esquina, eleita e preciosa;e quem nela crer não será confundido.', 6, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim para vòs, os que credes, é preciosa, mas, para os rebeldes,a pedra que os edificadores reprovaram,essa foi a principal da esquina,', 7, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E uma pedra de tropeço e rocha de escândalo,para aqueles que tropeçam na palavra, sendo desobedientes; para o que também foram destinados.', 8, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vòs sois a geração eleita, o sacerdòcio real, a nação santa, o povo adquirido, para que anuncieis as virtudes daquele que vos chamou das trevas para a sua maravilhosa luz;', 9, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, que em outro tempo não éreis povo, mas agora sois povo de Deus; que não tínheis alcançado misericórdia, mas agora alcançastes misericórdia.', 10, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amados, peço-vos, como a peregrinos e forasteiros, que vos abstenhais das concupiscências carnais, que combatem contra a alma;', 11, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo o vosso viver honesto entre os gentios; para que, naquilo em que falam mal de vós, como de malfeitores, glorifiquem a Deus no dia da visitação, pelas boas obras que em vós observem.', 12, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sujeitai-vos, pois, a toda a ordenação humana por amor do Senhor; quer ao rei, como superior;', 13, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quer aos governadores, como por ele enviados para castigo dos malfeitores, e para louvor dos que fazem o bem.', 14, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque assim é a vontade de Deus, que, fazendo bem, tapeis a boca à ignorância dos homens insensatos;', 15, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como livres, e não tendo a liberdade por cobertura da malícia, mas como servos de Deus.', 16, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Honrai a todos. Amai a fraternidade. Temei a Deus. Honrai ao rei.', 17, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, servos, sujeitai-vos com todo o temor aos senhores, não somente aos bons e humanos, mas também aos maus.', 18, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque é coisa agradável, que alguém, por causa da consciência para com Deus, sofra agravos, padecendo injustamente.', 19, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, que glória será essa, se, pecando, sois esbofeteados e sofreis? Mas se, fazendo o bem, sois afligidos e o sofreis, isso é agradável a Deus.', 20, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque para isto sois chamados; pois também Cristo padeceu por nós, deixando-nos o exemplo, para que sigais as suas pisadas.', 21, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual não cometeu pecado, nem na sua boca se achou engano.', 22, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, quando o injuriavam, não injuriava, e quando padecia não ameaçava, mas entregava-se àquele que julga justamente;', 23, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Levando ele mesmo em seu corpo os nossos pecados sobre o madeiro, para que, mortos para os pecados, pudéssemos viver para a justiça; e pelas suas feridas fostes sarados.', 24, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque éreis como ovelhas desgarradas; mas agora tendes voltado ao Pastor e Bispo das vossas almas.', 25, 2, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, no segundo sábado após o primeiro, passou pelas searas, e os seus discípulos iam arrancando espigas e, esfregando-as com as mãos, as comiam.', 1, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns dos fariseus lhes disseram: Por que fazeis o que não é lícito fazer nos sábados?', 2, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo-lhes, disse: Nunca lestes o que fez Davi quando teve fome, ele e os que com ele estavam?', 3, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como entrou na casa de Deus, e tomou os pães da proposição, e os comeu, e deu também aos que estavam com ele, os quais não é lícito comer senão só aos sacerdotes?', 4, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia-lhes: O Filho do homem é Senhor até do sábado.', 5, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu também noutro sábado, que entrou na sinagoga, e estava ensinando; e havia ali um homem que tinha a mão direita mirrada.', 6, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os escribas e fariseus observavam-no, se o curaria no sábado, para acharem de que o acusar.', 7, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele bem conhecia os seus pensamentos; e disse ao homem que tinha a mão mirrada: Levanta-te, e fica em pé no meio. E, levantando-se ele, ficou em pé.', 8, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus lhes disse: Uma coisa vos hei de perguntar: É lícito nos sábados fazer bem, ou fazer mal? salvar a vida, ou matar?', 9, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, olhando para todos em redor, disse ao homem: Estende a tua mão. E ele assim o fez, e a mão lhe foi restituída sã como a outra.', 10, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ficaram cheios de furor, e uns com os outros conferenciavam sobre o que fariam a Jesus.', 11, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que naqueles dias subiu ao monte a orar, e passou a noite em oração a Deus.', 12, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando já era dia, chamou a si os seus discípulos, e escolheu doze deles, a quem também deu o nome de apóstolos:', 13, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Simão, ao qual também chamou Pedro, e André, seu irmão; Tiago e João; Filipe e Bartolomeu;', 14, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mateus e Tomé; Tiago, filho de Alfeu, e Simão, chamado Zelote;', 15, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Judas, irmão de Tiago, e Judas Iscariotes, que foi o traidor.', 16, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, descendo com eles, parou num lugar plano, e também um grande número de seus discípulos, e grande multidão de povo de toda a Judéia, e de Jerusalém, e da costa marítima de Tiro e de Sidom; os quais tinham vindo para o ouvir, e serem curados das suas enfermidades,', 17, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como também os atormentados dos espíritos imundos; e eram curados.', 18, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E toda a multidão procurava tocar-lhe, porque saía dele virtude, e curava a todos.', 19, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando ele os olhos para os seus discípulos, dizia: Bem-aventurados vós, os pobres, porque vosso é o reino de Deus.', 20, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados vós, que agora tendes fome, porque sereis fartos. Bem-aventurados vós, que agora chorais, porque haveis de rir.', 21, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados sereis quando os homens vos odiarem e quando vos separarem, e vos injuriarem, e rejeitarem o vosso nome como mau, por causa do Filho do homem.', 22, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Folgai nesse dia, exultai; porque eis que é grande o vosso galardão no céu, pois assim faziam os seus pais aos profetas.', 23, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ai de vós, ricos! porque já tendes a vossa consolação.', 24, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, os que estais fartos, porque tereis fome. Ai de vós, os que agora rides, porque vos lamentareis e chorareis.', 25, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós quando todos os homens de vós disserem bem, porque assim faziam seus pais aos falsos profetas.', 26, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a vós, que isto ouvis, digo: Amai a vossos inimigos, fazei bem aos que vos odeiam;', 27, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bendizei os que vos maldizem, e orai pelos que vos caluniam.', 28, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao que te ferir numa face, oferece-lhe também a outra; e ao que te houver tirado a capa, nem a túnica recuses;', 29, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dá a qualquer que te pedir; e ao que tomar o que é teu, não lho tornes a pedir.', 30, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E como vós quereis que os homens vos façam, da mesma maneira lhes fazei vós, também.', 31, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se amardes aos que vos amam, que recompensa tereis? Também os pecadores amam aos que os amam.', 32, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se fizerdes bem aos que vos fazem bem, que recompensa tereis? Também os pecadores fazem o mesmo.', 33, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se emprestardes àqueles de quem esperais tornar a receber, que recompensa tereis? Também os pecadores emprestam aos pecadores, para tornarem a receber outro tanto.', 34, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amai, pois, a vossos inimigos, e fazei bem, e emprestai, sem nada esperardes, e será grande o vosso galardão, e sereis filhos do Altíssimo; porque ele é benigno até para com os ingratos e maus.', 35, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sede, pois, misericordiosos, como também vosso Pai é misericordioso.', 36, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não julgueis, e não sereis julgados; não condeneis, e não sereis condenados; soltai, e soltar-vos-ão.', 37, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dai, e ser-vos-á dado; boa medida, recalcada, sacudida e transbordando, vos deitarão no vosso regaço; porque com a mesma medida com que medirdes também vos medirão de novo.', 38, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia-lhes uma parábola: Pode porventura o cego guiar o cego? Não cairão ambos na cova?', 39, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O discípulo não é superior a seu mestre, mas todo o que for perfeito será como o seu mestre.', 40, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por que atentas tu no argueiro que está no olho de teu irmão, e não reparas na trave que está no teu próprio olho?', 41, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou como podes dizer a teu irmão: Irmão, deixa-me tirar o argueiro que está no teu olho, não atentando tu mesmo na trave que está no teu olho? Hipócrita, tira primeiro a trave do teu olho, e então verás bem para tirar o argueiro que está no olho de teu irmão.', 42, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não há boa árvore que dê mau fruto, nem má árvore que dê bom fruto.', 43, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque cada árvore se conhece pelo seu próprio fruto; pois não se colhem figos dos espinheiros, nem se vindimam uvas dos abrolhos.', 44, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O homem bom, do bom tesouro do seu coração tira o bem, e o homem mau, do mau tesouro do seu coração tira o mal, porque da abundância do seu coração fala a boca.', 45, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por que me chamais, Senhor, Senhor, e não fazeis o que eu digo?', 46, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que vem a mim e ouve as minhas palavras, e as observa, eu vos mostrarei a quem é semelhante:', 47, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É semelhante ao homem que edificou uma casa, e cavou, e abriu bem fundo, e pôs os alicerces sobre a rocha; e, vindo a enchente, bateu com ímpeto a corrente naquela casa, e não a pôde abalar, porque estava fundada sobre a rocha.', 48, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que ouve e não pratica é semelhante ao homem que edificou uma casa sobre terra, sem alicerces, na qual bateu com ímpeto a corrente, e logo caiu; e foi grande a ruína daquela casa.', 49, 6, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois de concluir todos estes discursos perante o povo, entrou em Cafarnaum.', 1, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o servo de um certo centurião, a quem muito estimava, estava doente, e moribundo.', 2, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando ouviu falar de Jesus, enviou-lhe uns anciãos dos judeus, rogando-lhe que viesse curar o seu servo.', 3, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando eles junto de Jesus, rogaram-lhe muito, dizendo: É digno de que lhe concedas isto,', 4, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ama a nossa nação, e ele mesmo nos edificou a sinagoga.', 5, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi Jesus com eles; mas, quando já estava perto da casa, enviou-lhe o centurião uns amigos, dizendo-lhe: Senhor, não te incomodes, porque não sou digno de que entres debaixo do meu telhado.', 6, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por isso nem ainda me julguei digno de ir ter contigo; dize, porém, uma palavra, e o meu criado sarará.', 7, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também eu sou homem sujeito à autoridade, e tenho soldados sob o meu poder, e digo a este: Vai, e ele vai; e a outro: Vem, e ele vem; e ao meu servo: Faze isto, e ele o faz.', 8, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo isto Jesus, maravilhou-se dele, e voltando-se, disse à multidão que o seguia: Digo-vos que nem ainda em Israel tenho achado tanta fé.', 9, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, voltando para casa os que foram enviados, acharam são o servo enfermo.', 10, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, no dia seguinte, ele foi à cidade chamada Naim, e com ele iam muitos dos seus discípulos, e uma grande multidão;', 11, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando chegou perto da porta da cidade, eis que levavam um defunto, filho único de sua mãe, que era viúva; e com ela ia uma grande multidão da cidade.', 12, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo-a, o Senhor moveu-se de íntima compaixão por ela, e disse-lhe: Não chores.', 13, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando-se, tocou o esquife (e os que o levavam pararam), e disse: Jovem, a ti te digo: Levanta-te. E o que fora defunto assentou-se, e começou a falar.', 14, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E entregou-o à sua mãe.', 15, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de todos se apoderou o temor, e glorificavam a Deus, dizendo: Um grande profeta se levantou entre nós, e Deus visitou o seu povo.', 16, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E correu dele esta fama por toda a Judéia e por toda a terra circunvizinha.', 17, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os discípulos de João anunciaram-lhe todas estas coisas.', 18, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E João, chamando dois dos seusdiscípulos, enviou-os a Jesus, dizendo: És tu aquele que havia de vir, ou esperamos outro?', 19, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando aqueles homens chegaram junto dele, disseram: João o Batista enviou-nos a perguntar-te: És tu aquele que havia de vir, ou esperamos outro?', 20, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, na mesma hora, curou muitos de enfermidades, e males, e espíritos maus, e deu vista a muitos cegos.', 21, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondendo, então, Jesus, disse-lhes: Ide, e anunciai a João o que tendes visto e ouvido: que os cegos vêem, os coxos andam, os leprosos são purificados, os surdos ouvem, os mortos ressuscitam e aos pobres anuncia-se o evangelho.', 22, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E bem-aventurado é aquele que em mim se não escandalizar.', 23, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo-se retirado os mensageiros de João, começou a dizer à multidão acerca de João: Que saístes a ver no deserto? uma cana abalada pelo vento?', 24, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas que saístes a ver? um homem trajado de vestes delicadas? Eis que os que andam com preciosas vestiduras, e em delícias, estão nos paços reais.', 25, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas que saístes a ver? um profeta? Sim, vos digo, e muito mais do que profeta.', 26, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este é aquele de quem está escrito: Eis que envio o meu anjo diante da tua face, O qual preparará diante de ti o teu caminho.', 27, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu vos digo que, entre os nascidos de mulheres, não há maior profeta do que João o Batista; mas o menor no reino de Deus é maior do que ele.', 28, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todo o povo que o ouviu e os publicanos, tendo sido batizados com o batismo de João, justificaram a Deus.', 29, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os fariseus e os doutores da lei rejeitaram o conselho de Deus contra si mesmos, não tendo sido batizados por ele.', 30, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse o Senhor: A quem, pois, compararei os homens desta geração, e a quem são semelhantes?', 31, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('São semelhantes aos meninos que, assentados nas praças, clamam uns aos outros, e dizem: Tocamo-vos flauta, e não dançastes; cantamo-vos lamentações, e não chorastes.', 32, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque veio João o Batista, que não comia pão nem bebia vinho, e dizeis: Tem demônio;', 33, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Veio o Filho do homem, que come e bebe, e dizeis: Eis aí um homem comilão e bebedor de vinho, amigo dos publicanos e pecadores.', 34, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a sabedoria é justificada por todos os seus filhos.', 35, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rogou-lhe um dos fariseus que comesse com ele; e, entrando em casa do fariseu, assentou-se à mesa.', 36, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que uma mulher da cidade, uma pecadora, sabendo que ele estava à mesa em casa do fariseu, levou um vaso de alabastro com ungüento;', 37, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando por detrás, aos seus pés, chorando, começou a regar-lhe os pés com lágrimas, e enxugava-lhos com os cabelos da sua cabeça; e beijava-lhe os pés, e ungia-lhos com o ungüento.', 38, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando isto viu o fariseu que o tinha convidado, falava consigo, dizendo: Se este fora profeta, bem saberia quem e qual é a mulher que lhe tocou, pois é uma pecadora.', 39, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E respondendo, Jesus disse-lhe: Simão, uma coisa tenho a dizer-te. E ele disse: Dize-a, Mestre.', 40, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Um certo credor tinha dois devedores: um devia-lhe quinhentos dinheiros, e outro cinqüenta.', 41, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, não tendo eles com que pagar, perdoou-lhes a ambos. Dize, pois, qual deles o amará mais?', 42, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Simão, respondendo, disse: Tenho para mim que é aquele a quem mais perdoou. E ele lhe disse: Julgaste bem.', 43, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, voltando-se para a mulher, disse a Simão: Vês tu esta mulher? Entrei em tua casa, e não me deste água para os pés; mas esta regou-me os pés com lágrimas, e os enxugou com os cabelos de sua cabeça.', 44, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não me deste ósculo, mas esta, desde que entrou, não tem cessado de me beijar os pés.', 45, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não me ungiste a cabeça com óleo, mas esta ungiu-me os pés com ungüento.', 46, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso te digo que os seus muitos pecados lhe são perdoados, porque muito amou; mas aquele a quem pouco é perdoado pouco ama.', 47, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe a ela: Os teus pecados te são perdoados.', 48, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que estavam à mesa começaram a dizer entre si: Quem é este, que até perdoa pecados?', 49, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse à mulher: A tua fé te salvou; vai-te em paz.', 50, 7, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pedro, apóstolo de Jesus Cristo, aos estrangeiros dispersos no Ponto, Galácia, Capadócia, Ásia e Bitínia;', 1, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eleitos segundo a presciência de Deus Pai, em santificação do Espírito, para a obediência e aspersão do sangue de Jesus Cristo: Graça e paz vos sejam multiplicadas.', 2, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bendito seja o Deus e Pai de nosso Senhor Jesus Cristo que, segundo a sua grande misericórdia, nos gerou de novo para uma viva esperança, pela ressurreição de Jesus Cristo dentre os mortos,', 3, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para uma herança incorruptível, incontaminável, e que não se pode murchar, guardada nos céus para vós,', 4, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que mediante a fé estais guardados na virtude de Deus para a salvação, já prestes para se revelar no último tempo,', 5, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em que vós grandemente vos alegrais, ainda que agora importa, sendo necessário, que estejais por um pouco contristados com várias tentações,', 6, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que a prova da vossa fé, muito mais preciosa do que o ouro que perece e é provado pelo fogo, se ache em louvor, e honra, e glória, na revelação de Jesus Cristo;', 7, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao qual, não o havendo visto, amais; no qual, não o vendo agora, mas crendo, vos alegrais com gozo inefável e glorioso;', 8, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Alcançando o fim da vossa fé, a salvação das vossas almas.', 9, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da qual salvação inquiriram e trataram diligentemente os profetas que profetizaram da graça que vos foi dada,', 10, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Indagando que tempo ou que ocasião de tempo o Espírito de Cristo, que estava neles, indicava, anteriormente testificando os sofrimentos que a Cristo haviam de vir, e a glória que se lhes havia de seguir.', 11, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aos quais foi revelado que, não para si mesmos, mas para nós, eles ministravam estas coisas que agora vos foram anunciadas por aqueles que, pelo Espírito Santo enviado do céu, vos pregaram o evangelho; para as quais coisas os anjos desejam bem atentar.', 12, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, cingindo os lombos do vosso entendimento, sede sóbrios, e esperai inteiramente na graça que se vos ofereceu na revelação de Jesus Cristo;', 13, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como filhos obedientes, não vos conformando com as concupiscências que antes havia em vossa ignorância;', 14, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, como é santo aquele que vos chamou, sede vós também santos em toda a vossa maneira de viver;', 15, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto está escrito: Sede santos, porque eu sou santo.', 16, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se invocais por Pai aquele que, sem acepção de pessoas, julga segundo a obra de cada um, andai em temor, durante o tempo da vossa peregrinação,', 17, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo que não foi com coisas corruptíveis, como prata ou ouro, que fostes resgatados da vossa vã maneira de viver que por tradição recebestes dos vossos pais,', 18, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas com o precioso sangue de Cristo, como de um cordeiro imaculado e incontaminado,', 19, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, na verdade, em outro tempo foi conhecido, ainda antes da fundação do mundo, mas manifestado nestes últimos tempos por amor de vós;', 20, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por ele credes em Deus, que o ressuscitou dentre os mortos, e lhe deu glória, para que a vossa fé e esperança estivessem em Deus;', 21, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Purificando as vossas almas pelo Espírito na obediência à verdade, para o amor fraternal, não fingido; amai-vos ardentemente uns aos outros com um coração puro;', 22, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo de novo gerados, não de semente corruptível, mas da incorruptível, pela palavra de Deus, viva, e que permanece para sempre.', 23, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque toda a carne é como a erva,e toda a glória do homem como a flor da erva.Secou-se a erva, e caiu a sua flor;', 24, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a palavra do Senhor permanece para sempre.E esta é a palavra que entre vós foi evangelizada.', 25, 1, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu naqueles dias que saiu um decreto da parte de César Augusto, para que todo o mundo se alistasse', 1, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Este primeiro alistamento foi feito sendo Quirino presidente da Síria).', 2, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos iam alistar-se, cada um à sua própria cidade.', 3, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E subiu também José da Galiléia, da cidade de Nazaré, à Judéia, à cidade de Davi, chamada Belém (porque era da casa e família de Davi),', 4, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A fim de alistar-se com Maria, sua esposa, que estava grávida.', 5, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, estando eles ali, se cumpriram os dias em que ela havia de dar à luz.', 6, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E deu à luz a seu filho primogênito, e envolveu-o em panos, e deitou-o numa manjedoura, porque não havia lugar para eles na estalagem.', 7, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, havia naquela mesma comarca pastores que estavam no campo, e guardavam, durante as vigílias da noite, o seu rebanho.', 8, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que o anjo do Senhor veio sobre eles, e a glória do Senhor os cercou de resplendor, e tiveram grande temor.', 9, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o anjo lhes disse: Não temais, porque eis aqui vos trago novas de grande alegria, que será para todo o povo:', 10, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, na cidade de Davi, vos nasceu hoje o Salvador, que é Cristo, o Senhor.', 11, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E isto vos será por sinal: Achareis o menino envolto em panos, e deitado numa manjedoura.', 12, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, no mesmo instante, apareceu com o anjo uma multidão dos exércitos celestiais, louvando a Deus, e dizendo:', 13, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Glória a Deus nas alturas, Paz na terra, boa vontade para com os homens.', 14, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, ausentando-se deles os anjos para o céu, disseram os pastores uns aos outros: Vamos, pois, até Belém, e vejamos isso que aconteceu, e que o Senhor nos fez saber.', 15, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram apressadamente, e acharam Maria, e José, e o menino deitado na manjedoura.', 16, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo-o, divulgaram a palavra que acerca do menino lhes fora dita;', 17, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os que a ouviram se maravilharam do que os pastores lhes diziam.', 18, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Maria guardava todas estas coisas, conferindo-as em seu coração.', 19, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E voltaram os pastores, glorificando e louvando a Deus por tudo o que tinham ouvido e visto, como lhes havia sido dito.', 20, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando os oito dias foram cumpridos, para circuncidar o menino, foi-lhe dado o nome de Jesus, que pelo anjo lhe fora posto antes de ser concebido.', 21, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, cumprindo-se os dias da purificação dela, segundo a lei de Moisés, o levaram a Jerusalém, para o apresentarem ao Senhor', 22, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Segundo o que está escrito na lei do Senhor: Todo o macho primogênito será consagrado ao Senhor);', 23, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E para darem a oferta segundo o disposto na lei do Senhor: Um par de rolas ou dois pombinhos.', 24, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Havia em Jerusalém um homem cujo nome era Simeão; e este homem era justo e temente a Deus, esperando a consolação de Israel; e o Espírito Santo estava sobre ele.', 25, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E fora-lhe revelado, pelo Espírito Santo, que ele não morreria antes de ter visto o Cristo do Senhor.', 26, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pelo Espírito foi ao templo e, quando os pais trouxeram o menino Jesus, para com ele procederem segundo o uso da lei,', 27, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, então, o tomou em seus braços, e louvou a Deus, e disse:', 28, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora, Senhor, despedes em paz o teu servo, Segundo a tua palavra;', 29, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois já os meus olhos viram a tua salvação,', 30, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A qual tu preparaste perante a face de todos os povos;', 31, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Luz para iluminar as nações, E para glória de teu povo Israel.', 32, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E José, e sua mãe, se maravilharam das coisas que dele se diziam.', 33, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Simeão os abençoou, e disse a Maria, sua mãe: Eis que este é posto para queda e elevação de muitos em Israel, e para sinal que é contraditado', 34, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(E uma espada traspassará também a tua própria alma); para que se manifestem os pensamentos de muitos corações.', 35, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava ali a profetisa Ana, filha de Fanuel, da tribo de Aser. Esta era já avançada em idade, e tinha vivido com o marido sete anos, desde a sua virgindade;', 36, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E era viúva, de quase oitenta e quatro anos, e não se afastava do templo, servindo a Deus em jejuns e orações, de noite e de dia.', 37, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sobrevindo na mesma hora, ela dava graças a Deus, e falava dele a todos os que esperavam a redenção em Jerusalém.', 38, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando acabaram de cumprir tudo segundo a lei do Senhor, voltaram à Galiléia, para a sua cidade de Nazaré.', 39, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o menino crescia, e se fortalecia em espírito, cheio de sabedoria; e a graça de Deus estava sobre ele.', 40, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, todos os anos iam seus pais a Jerusalém à festa da páscoa;', 41, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo ele já doze anos, subiram a Jerusalém, segundo o costume do dia da festa.', 42, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, regressando eles, terminados aqueles dias, ficou o menino Jesus em Jerusalém, e não o soube José, nem sua mãe.', 43, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pensando, porém, eles que viria de companhia pelo caminho, andaram caminho de um dia, e procuravam-no entre os parentes e conhecidos;', 44, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como o não encontrassem, voltaram a Jerusalém em busca dele.', 45, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, passados três dias, o acharam no templo, assentado no meio dos doutores, ouvindo-os, e interrogando-os.', 46, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os que o ouviam admiravam a sua inteligência e respostas.', 47, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando o viram, maravilharam-se, e disse-lhe sua mãe: Filho, por que fizeste assim para conosco? Eis que teu pai e eu ansiosos te procurávamos.', 48, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Por que é que me procuráveis? Não sabeis que me convém tratar dos negócios de meu Pai?', 49, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles não compreenderam as palavras que lhes dizia.', 50, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desceu com eles, e foi para Nazaré, e era-lhes sujeito. E sua mãe guardava no seu coração todas estas coisas.', 51, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E crescia Jesus em sabedoria, e em estatura, e em graça para com Deus e os homens.', 52, 2, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no ano quinze do império de Tibério César, sendo Pôncio Pilatos presidente da Judéia, e Herodes tetrarca da Galiléia, e seu irmão Filipe tetrarca da Ituréia e da província de Traconites, e Lisânias tetrarca de Abilene,', 1, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo Anás e Caifás sumos sacerdotes, veio no deserto a palavra de Deus a João, filho de Zacarias.', 2, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E percorreu toda a terra ao redor do Jordão, pregando o batismo de arrependimento, para o perdão dos pecados;', 3, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segundo o que está escrito no livro das palavras do profeta Isaías, que diz: Voz do que clama no deserto: Preparai o caminho do Senhor; Endireitai as suas veredas.', 4, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todo o vale se encherá, E se abaixará todo o monte e outeiro; E o que é tortuoso se endireitará, E os caminhos escabrosos se aplanarão;', 5, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E toda a carne verá a salvação de Deus.', 6, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizia, pois, João à multidão que saía para ser batizada por ele: Raça de víboras, quem vos ensinou a fugir da ira que está para vir?', 7, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Produzi, pois, frutos dignos de arrependimento, e não comeceis a dizer em vós mesmos: Temos Abraão por pai; porque eu vos digo que até destas pedras pode Deus suscitar filhos a Abraão.', 8, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também já está posto o machado à raiz das árvores; toda a árvore, pois, que não dá bom fruto, corta-se e lança-se no fogo.', 9, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a multidão o interrogava, dizendo: Que faremos, pois?', 10, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo ele, disse-lhes: Quem tiver duas túnicas, reparta com o que não tem, e quem tiver alimentos, faça da mesma maneira.', 11, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegaram também uns publicanos, para serem batizados, e disseram-lhe: Mestre, que devemos fazer?', 12, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Não peçais mais do que o que vos está ordenado.', 13, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E uns soldados o interrogaram também, dizendo: E nós que faremos? E ele lhes disse: A ninguém trateis mal nem defraudeis, e contentai-vos com o vosso soldo.', 14, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando o povo em expectação, e pensando todos de João, em seus corações, se porventura seria o Cristo,', 15, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu João a todos, dizendo: Eu, na verdade, batizo-vos com água, mas eis que vem aquele que é mais poderoso do que eu, do qual não sou digno de desatar a correia das alparcas; esse vos batizará com o Espírito Santo e com fogo.', 16, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele tem a pá na sua mão; e limpará a sua eira, e ajuntará o trigo no seu celeiro, mas queimará a palha com fogo que nunca se apaga.', 17, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim, admoestando-os, muitas outras coisas também anunciava ao povo.', 18, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo, porém, o tetrarca Herodes repreendido por ele por causa de Herodias, mulher de seu irmão Filipe, e por todas as maldades que Herodes tinha feito,', 19, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Acrescentou a todas as outras ainda esta, a de encerrar João num cárcere.', 20, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, como todo o povo se batizava, sendo batizado também Jesus, orando ele, o céu se abriu;', 21, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o Espírito Santo desceu sobre ele em forma corpórea, como pomba; e ouviu-se uma voz do céu, que dizia: Tu és o meu Filho amado, em ti me comprazo.', 22, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o mesmo Jesus começava a ser de quase trinta anos, sendo (como se cuidava) filho de José, e José de Heli,', 23, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Heli de Matã, e Matã de Levi, e Levi de Melqui, e Melqui de Janai, e Janai de José,', 24, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E José de Matatias, e Matatias de Amós, e Amós de Naum, e Naum de Esli, e Esli de Nagaí,', 25, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Nagaí de Máate, e Máate de Matatias, e Matatias de Semei, e Semei de José, e José de Jodá,', 26, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jodá de Joanã, e Joanã de Resá, e Resá de Zorobabel, e Zorobabel de Salatiel, e Salatiel de Neri,', 27, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Neri de Melqui, e Melqui de Adi, e Adi de Cosã, e Cosã de Elmadã, e Elmadã de Er,', 28, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Er de Josué, e Josué de Eliézer, e Eliézer de Jorim, e Jorim de Matã, e Matã de Levi,', 29, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Levi de Simeão, e Simeão de Judá, e Judá de José, e José de Jonã, e Jonã de Eliaquim,', 30, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Eliaquim de Meleá, e Meleá de Mená, e Mená de Matatá, e Matatá de Natã, e Natã de Davi,', 31, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Davi de Jessé, e Jessé de Obede, e Obede de Boaz, e Boaz de Salá, e Salá de Naassom,', 32, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Naassom de Aminadabe, e Aminadabe de Arão, e Arão de Esrom, e Esrom Perez, e Perez de Judá,', 33, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Judá de Jacó, e Jacó de Isaque, e Isaque de Abraão, e Abraão de Terá, e Terá de Nacor,', 34, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Nacor de Seruque, e Seruque de Ragaú, e Ragaú de Fáleque, e Fáleque de Eber, e Eber de Salá,', 35, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Salá de Cainã, e Cainã de Arfaxade, e Arfaxade de Sem, e Sem de Noé, e Noé de Lameque,', 36, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Lameque de Matusalém, e Matusalém de Enoque, e Enoque de Jarete, e Jarete de Maleleel, e Maleleel de Cainã,', 37, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Cainã de Enos, e Enos de Sete, e Sete de Adão, e Adão de Deus.', 38, 3, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, apertando-o a multidão, para ouvir a palavra de Deus, estava ele junto ao lago de Genesaré;', 1, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E viu estar dois barcos junto à praia do lago; e os pescadores, havendo descido deles, estavam lavando as redes.', 2, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando num dos barcos, que era o de Simão, pediu-lhe que o afastasse um pouco da terra; e, assentando-se, ensinava do barco a multidão.', 3, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando acabou de falar, disse a Simão: Faze-te ao mar alto, e lançai as vossas redes para pescar.', 4, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo Simão, disse-lhe: Mestre, havendo trabalhado toda a noite, nada apanhamos; mas, sobre a tua palavra, lançarei a rede.', 5, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, fazendo assim, colheram uma grande quantidade de peixes, e rompia-se-lhes a rede.', 6, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E fizeram sinal aos companheiros que estavam no outro barco, para que os fossem ajudar. E foram, e encheram ambos os barcos, de maneira tal que quase iam a pique.', 7, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vendo isto Simão Pedro, prostrou-se aos pés de Jesus, dizendo: Senhor, ausenta-te de mim, que sou um homem pecador.', 8, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois que o espanto se apoderara dele, e de todos os que com ele estavam, por causa da pesca de peixe que haviam feito.', 9, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, de igual modo, também de Tiago e João, filhos de Zebedeu, que eram companheiros de Simão. E disse Jesus a Simão: Não temas; de agora em diante serás pescador de homens.', 10, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levando os barcos para terra, deixaram tudo, e o seguiram.', 11, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, quando estava numa daquelas cidades, eis que um homem cheio de lepra, vendo a Jesus, prostrou-se sobre o rosto, e rogou-lhe, dizendo: Senhor, se quiseres, bem podes limpar-me.', 12, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, estendendo a mão, tocou-lhe, dizendo: Quero, sê limpo. E logo a lepra desapareceu dele.', 13, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ordenou-lhe que a ninguém o dissesse. Mas vai, disse, mostra-te ao sacerdote, e oferece pela tua purificação, o que Moisés determinou para que lhes sirva de testemunho.', 14, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A sua fama, porém, se propagava ainda mais, e ajuntava-se muita gente para o ouvir e para ser por ele curada das suas enfermidades.', 15, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, retirava-se para os desertos, e ali orava.', 16, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, num daqueles dias, estava ensinando, e estavam ali assentados fariseus e doutores da lei, que tinham vindo de todas as aldeias da Galiléia, e da Judéia, e de Jerusalém. E a virtude do Senhor estava ali para os curar.', 17, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que uns homens transportaram numa cama um homem que estava paralítico, e procuravam fazê-lo entrar e pô-lo diante dele.', 18, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, não achando por onde o pudessem levar, por causa da multidão, subiram ao telhado, e por entre as telhas o baixaram com a cama, até ao meio, diante de Jesus.', 19, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo ele a fé deles, disse-lhe: Homem, os teus pecados te são perdoados.', 20, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os escribas e os fariseus começaram a arrazoar, dizendo: Quem é este que diz blasfêmias? Quem pode perdoar pecados, senão só Deus?', 21, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, conhecendo os seus pensamentos, respondeu, e disse-lhes: Que arrazoais em vossos corações?', 22, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qual é mais fácil? dizer: Os teus pecados te são perdoados; ou dizer: Levanta-te, e anda?', 23, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, para que saibais que o Filho do homem tem sobre a terra poder de perdoar pecados (disse ao paralítico), a ti te digo: Levanta-te, toma a tua cama, e vai para tua casa.', 24, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se logo diante deles, e tomando a cama em que estava deitado, foi para sua casa, glorificando a Deus.', 25, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos ficaram maravilhados, e glorificaram a Deus; e ficaram cheios de temor, dizendo: Hoje vimos prodígios.', 26, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois disto, saiu, e viu um publicano, chamado Levi, assentado na recebedoria, e disse-lhe: Segue-me.', 27, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, deixando tudo, levantou-se e o seguiu.', 28, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E fez-lhe Levi um grande banquete em sua casa; e havia ali uma multidão de publicanos e outros que estavam com eles à mesa.', 29, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os escribas deles, e os fariseus, murmuravam contra os seus discípulos, dizendo: Por que comeis e bebeis com publicanos e pecadores?', 30, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhes: Não necessitam de médico os que estão sãos, mas, sim, os que estão enfermos;', 31, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu não vim chamar os justos, mas, sim, os pecadores, ao arrependimento.', 32, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram-lhe, então, eles: Por que jejuam os discípulos de João muitas vezes, e fazem orações, como também os dos fariseus, mas os teus comem e bebem?', 33, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Podeis vós fazer jejuar os filhos das bodas, enquanto o esposo está com eles?', 34, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dias virão, porém, em que o esposo lhes será tirado, e então, naqueles dias, jejuarão.', 35, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes também uma parábola: Ninguém deita um pedaço de uma roupa nova para a coser em roupa velha, pois romperá a nova e o remendo não condiz com a velha.', 36, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ninguém deita vinho novo em odres velhos; de outra sorte o vinho novo romperá os odres, e entornar-se-á o vinho, e os odres se estragarão;', 37, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o vinho novo deve deitar-se em odres novos, e ambos juntamente se conservarão.', 38, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ninguém tendo bebido o velho quer logo o novo, porque diz: Melhor é o velho.', 39, 5, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aos presbíteros, que estão entre vós, admoesto eu, que sou também presbítero com eles, e testemunha das aflições de Cristo, e participante da glória que se há de revelar:', 1, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Apascentai o rebanho de Deus, que está entre vós, tendo cuidado dele, não por força, mas voluntariamente; nem por torpe ganância, mas de ânimo pronto;', 2, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem como tendo domínio sobre a herança de Deus, mas servindo de exemplo ao rebanho.', 3, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando aparecer o Sumo Pastor, alcançareis a incorruptível coroa da glória.', 4, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Semelhantemente vós jovens, sede sujeitos aos anciãos; e sede todos sujeitos uns aos outros, e revesti-vos de humildade, porque Deus resiste aos soberbos, mas dá graça aos humildes.', 5, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Humilhai-vos, pois, debaixo da potente mão de Deus, para que a seu tempo vos exalte;', 6, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lançando sobre ele toda a vossa ansiedade, porque ele tem cuidado de vós.', 7, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sede sóbrios; vigiai; porque o diabo, vosso adversário, anda em derredor, bramando como leão, buscando a quem possa tragar;', 8, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao qual resisti firmes na fé, sabendo que as mesmas aflições se cumprem entre os vossos irmãos no mundo.', 9, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o Deus de toda a graça, que em Cristo Jesus nos chamou à sua eterna glória, depois de havemos padecido um pouco, ele mesmo vos aperfeiçoe, confirme, fortifique e estabeleça.', 10, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A ele seja a glória e o poderio para todo o sempre. Amém.', 11, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por Silvano, vosso fiel irmão, como cuido, escrevi brevemente, exortando e testificando que esta é a verdadeira graça de Deus, na qual estais firmes.', 12, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A vossa co-eleita em babilônia vos saúda, e meu filho Marcos.', 13, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai-vos uns aos outros com ósculo de amor. Paz seja com todos vós que estais em Cristo Jesus. Amém.', 14, 5, 21);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, convocando os seus doze discípulos, deu-lhes virtude e poder sobre todos os demônios, para curarem enfermidades.', 1, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E enviou-os a pregar o reino de Deus, e a curar os enfermos.', 2, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Nada leveis convosco para o caminho, nem bordões, nem alforje, nem pão, nem dinheiro; nem tenhais duas túnicas.', 3, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E em qualquer casa em que entrardes, ficai ali, e de lá saireis.', 4, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se em qualquer cidade vos não receberem, saindo vós dali, sacudi o pó dos vossos pés, em testemunho contra eles.', 5, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo eles, percorreram todas as aldeias, anunciando o evangelho, e fazendo curas por toda a parte.', 6, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o tetrarca Herodes ouviu todas as coisas que por ele foram feitas, e estava em dúvida, porque diziam alguns que João ressuscitara dentre os mortos; e outros que Elias tinha aparecido;', 7, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outros que um profeta dos antigos havia ressuscitado.', 8, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Herodes: A João mandei eu degolar; quem é, pois, este de quem ouço dizer tais coisas? E procurava vê-lo.', 9, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, regressando os apóstolos, contaram-lhe tudo o que tinham feito. E, tomando-os consigo, retirou-se para um lugar deserto de uma cidade chamada Betsaida.', 10, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sabendo-o a multidão, o seguiu; e ele os recebeu, e falava-lhes do reino de Deus, e sarava os que necessitavam de cura.', 11, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E já o dia começava a declinar; então, chegando-se a ele os doze, disseram-lhe: Despede a multidão, para que, indo aos lugares e aldeias em redor, se agasalhem, e achem o que comer; porque aqui estamos em lugar deserto.', 12, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele lhes disse: Dai-lhes vós de comer. E eles disseram: Não temos senão cinco pães e dois peixes, salvo se nós próprios formos comprar comida para todo este povo.', 13, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto estavam ali quase cinco mil homens. Disse, então, aos seus discípulos: Fazei-os assentar, em ranchos de cinqüenta em cinqüenta.', 14, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim o fizeram, fazendo-os assentar a todos.', 15, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando os cinco pães e os dois peixes, e olhando para o céu, abençoou-os, e partiu-os, e deu-os aos seus discípulos para os porem diante da multidão.', 16, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E comeram todos, e saciaram-se; e levantaram, do que lhes sobejou, doze alcofas de pedaços.', 17, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, estando ele só, orando, estavam com ele os discípulos; e perguntou-lhes, dizendo: Quem diz a multidão que eu sou?', 18, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo eles, disseram: João o Batista; outros, Elias, e outros que um dos antigos profetas ressuscitou.', 19, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: E vós, quem dizeis que eu sou? E, respondendo Pedro, disse: O Cristo de Deus.', 20, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, admoestando-os, mandou que a ninguém referissem isso,', 21, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: É necessário que o Filho do homem padeça muitas coisas, e seja rejeitado dos anciãos edos escribas, e seja morto, e ressuscite ao terceiro dia.', 22, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia a todos: Se alguém quer vir após mim, negue-se a si mesmo, e tome cada dia a sua cruz, e siga-me.', 23, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, qualquer que quiser salvar a sua vida, perdê-la-á; mas qualquer que, por amor de mim, perder a sua vida, a salvará.', 24, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, que aproveita ao homem granjear o mundo todo, perdendo-se ou prejudicando-se a si mesmo?', 25, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, qualquer que de mim e das minhas palavras se envergonhar, dele se envergonhará o Filho do homem, quando vier na sua glória, e na do Pai e dos santos anjos.', 26, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E em verdade vos digo que, dos que aqui estão, alguns há que não provarão a morte até que vejam o reino de Deus.', 27, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, quase oito dias depois destas palavras, tomou consigo a Pedro, a João e a Tiago, e subiu ao monte a orar.', 28, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando ele orando, transfigurou-se a aparência do seu rosto, e a sua roupa ficou branca e mui resplandecente.', 29, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que estavam falando com ele dois homens, que eram Moisés e Elias,', 30, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais apareceram com glória, e falavam da sua morte, a qual havia de cumprir-se em Jerusalém.', 31, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro e os que estavam com ele estavam carregados de sono; e, quando despertaram, viram a sua glória e aqueles dois homens que estavam com ele.', 32, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, quando aqueles se apartaram dele, disse Pedro a Jesus: Mestre, bom é que nós estejamos aqui, e façamos três tendas: uma para ti, uma para Moisés, e uma para Elias, não sabendo o que dizia.', 33, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dizendo ele isto, veio uma nuvem que os cobriu com a sua sombra; e, entrando eles na nuvem, temeram.', 34, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E saiu da nuvem uma voz que dizia: Este é o meu amado Filho; a ele ouvi.', 35, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo soado aquela voz, Jesus foi achado só; e eles calaram-se, e por aqueles dias não contaram a ninguém nada do que tinham visto.', 36, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu, no dia seguinte, que, descendo eles do monte, lhes saiu ao encontro uma grande multidão;', 37, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que um homem da multidão clamou, dizendo: Mestre, peço-te que olhes para meu filho, porque é o único que eu tenho.', 38, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que um espírito o toma e de repente clama, e o despedaça até espumar; e só o larga depois de o ter quebrantado.', 39, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E roguei aos teus discípulos que o expulsassem, e não puderam.', 40, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse: Ó geração incrédula e perversa! até quando estarei ainda convosco e vos sofrerei? Traze-me aqui o teu filho.', 41, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando vinha chegando, o demônio o derrubou e convulsionou; porém, Jesus repreendeu o espírito imundo, e curou o menino, e o entregou a seu pai.', 42, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos pasmavam da majestade de Deus. E, maravilhando-se todos de todas as coisas que Jesus fazia, disse aos seus discípulos:', 43, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ponde vós estas palavras em vossos ouvidos, porque o Filho do homem será entregue nas mãos dos homens.', 44, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eles não entendiam esta palavra, que lhes era encoberta, para que a não compreendessem; e temiam interrogá-lo acerca desta palavra.', 45, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E suscitou-se entre eles uma discussão sobre qual deles seria o maior.', 46, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus, vendo o pensamento de seus corações, tomou um menino, pô-lo junto a si,', 47, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Qualquer que receber este menino em meu nome, recebe-me a mim; e qualquer que me receber a mim, recebe o que me enviou; porque aquele que entre vós todos for o menor, esse mesmo será grande.', 48, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo João, disse: Mestre, vimos um que em teu nome expulsava os demônios, e lho proibimos, porque não te segue conosco.', 49, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhe disse: Não o proibais, porque quem não é contra nós é por nós.', 50, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, completando-se os dias para a sua assunção, manifestou o firme propósito de ir a Jerusalém.', 51, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mandou mensageiros adiante de si; e, indo eles, entraram numa aldeia de samaritanos, para lhe prepararem pousada,', 52, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não o receberam, porque o seu aspecto era como de quem ia a Jerusalém.', 53, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus discípulos, Tiago e João, vendo isto, disseram: Senhor, queres que digamos que desça fogo do céu e os consuma, como Elias também fez?', 54, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Voltando-se, porém, repreendeu-os, e disse: Vós não sabeis de que espírito sois.', 55, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Filho do homem não veio para destruir as almas dos homens, mas para salvá-las. E foram para outra aldeia.', 56, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, indo eles pelo caminho, lhe disse um: Senhor, seguir-te-ei para onde quer que fores.', 57, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe Jesus: As raposas têm covis, e as aves do céu, ninhos, mas o Filho do homem não tem onde reclinar a cabeça.', 58, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse a outro: Segue-me. Mas ele respondeu: Senhor, deixa que primeiro eu vá a enterrar meu pai.', 59, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus lhe observou: Deixa aos mortos o enterrar os seus mortos; porém tu vai e anuncia o reino de Deus.', 60, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse também outro: Senhor, eu te seguirei, mas deixa-me despedir primeiro dos que estão em minha casa.', 61, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhe disse: Ninguém, que lança mão do arado e olha para trás, é apto para o reino de Deus.', 62, 9, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aconteceu num sábado que, entrando ele em casa de um dos principais dos fariseus para comer pão, eles o estavam observando.', 1, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que estava ali diante dele um certo homem hidrópico.', 2, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, tomando a palavra, falou aos doutores da lei, e aos fariseus, dizendo: É lícito curar no sábado?', 3, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eles, porém, calaram-se. E, tomando-o, o curou e despediu.', 4, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E respondendo-lhes disse: Qual será de vós o que, caindo-lhe num poço, em dia de sábado, o jumento ou o boi, o não tire logo?', 5, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nada lhe podiam replicar sobre isto.', 6, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse aos convidados uma parábola, reparando como escolhiam os primeiros assentos, dizendo-lhes:', 7, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando por alguém fores convidado às bodas, não te assentes no primeiro lugar; não aconteça que esteja convidado outro mais digno do que tu;', 8, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vindo o que te convidou a ti e a ele, te diga: Dá o lugar a este; e então, com vergonha, tenhas de tomar o derradeiro lugar.', 9, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando fores convidado, vai, e assenta-te no derradeiro lugar, para que, quando vier o que te convidou, te diga: Amigo, sobe mais para cima. Então terás honra diante dos que estiverem contigo à mesa.', 10, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto qualquer que a si mesmo se exaltar será humilhado, e aquele que a si mesmo se humilhar será exaltado.', 11, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia também ao que o tinha convidado: Quando deres um jantar, ou uma ceia, não chames os teus amigos, nem os teus irmãos, nem os teus parentes, nem vizinhos ricos, para que não suceda que também eles te tornem a convidar, e te seja isso recompensado.', 12, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando fizeres convite, chama os pobres, aleijados, mancos e cegos,', 13, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E serás bem-aventurado; porque eles não têm com que to recompensar; mas recompensado te será na ressurreição dos justos.', 14, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo isto, um dos que estavam com ele à mesa, disse-lhe: Bem-aventurado o que comer pão no reino de Deus.', 15, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porém, ele lhe disse: Um certo homem fez uma grande ceia, e convidou a muitos.', 16, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E à hora da ceia mandou o seu servo dizer aos convidados: Vinde, que já tudo está preparado.', 17, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos à uma começaram a escusar-se. Disse-lhe o primeiro: Comprei um campo, e importa ir vê-lo; rogo-te que me hajas por escusado.', 18, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outro disse: Comprei cinco juntas de bois, e vou experimentá-los; rogo-te que me hajas por escusado.', 19, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outro disse: Casei, e portanto não posso ir.', 20, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, voltando aquele servo, anunciou estas coisas ao seu senhor. Então o pai de família, indignado, disse ao seu servo: Sai depressa pelas ruas e bairros da cidade, e traze aqui os pobres, e aleijados, e mancos e cegos.', 21, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse o servo: Senhor, feito está como mandaste; e ainda há lugar.', 22, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse o senhor ao servo: Sai pelos caminhos e valados, e força-os a entrar, para que a minha casa se encha.', 23, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu vos digo que nenhum daqueles homens que foram convidados provará a minha ceia.', 24, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, ia com ele uma grande multidão; e, voltando-se, disse-lhe:', 25, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém vier a mim, e não aborrecer a seu pai, e mãe, e mulher, e filhos, e irmãos, e irmãs, e ainda também a sua própria vida, não pode ser meu discípulo.', 26, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qualquer que não levar a sua cruz, e não vier após mim, não pode ser meu discípulo.', 27, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois qual de vós, querendo edificar uma torre, não se assenta primeiro a fazer as contas dos gastos, para ver se tem com que a acabar?', 28, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que não aconteça que, depois de haver posto os alicerces, e não a podendo acabar, todos os que a virem comecem a escarnecer dele,', 29, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Este homem começou a edificar e não pôde acabar.', 30, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou qual é o rei que, indo à guerra a pelejar contra outro rei, não se assenta primeiro a tomar conselho sobre se com dez mil pode sair ao encontro do que vem contra ele com vinte mil?', 31, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De outra maneira, estando o outro ainda longe, manda embaixadores, e pede condições de paz.', 32, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, pois, qualquer de vós, que não renuncia a tudo quanto tem, não pode ser meu discípulo.', 33, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bom é o sal; mas, se o sal degenerar, com que se há de salgar?', 34, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem presta para a terra, nem para o monturo; lançam-no fora. Quem tem ouvidos para ouvir, ouça.', 35, 14, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia também aos seus discípulos: Havia um certo homem rico, o qual tinha um mordomo; e este foi acusado perante ele de dissipar os seus bens.', 1, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, chamando-o, disse-lhe: Que é isto que ouço de ti? Dá contas da tua mordomia, porque já não poderás ser mais meu mordomo.', 2, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o mordomo disse consigo: Que farei, pois que o meu senhor me tira a mordomia? Cavar, não posso; de mendigar, tenho vergonha.', 3, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sei o que hei de fazer, para que, quando for desapossado da mordomia, me recebam em suas casas.', 4, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando a si cada um dos devedores do seu senhor, disse ao primeiro: Quanto deves ao meu senhor?', 5, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele respondeu: Cem medidas de azeite. E disse-lhe: Toma a tua obrigação, e assentando-te já, escreve cinqüenta.', 6, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse depois a outro: E tu, quanto deves? E ele respondeu: Cem alqueires de trigo. E disse-lhe: Toma a tua obrigação, e escreve oitenta.', 7, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E louvou aquele senhor o injusto mordomo por haver procedido prudentemente, porque os filhos deste mundo são mais prudentes na sua geração do que os filhos da luz.', 8, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu vos digo: Granjeai amigos com as riquezas da injustiça; para que, quando estas vos faltarem, vos recebam eles nos tabernáculos eternos.', 9, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem é fiel no mínimo, também é fiel no muito; quem é injusto no mínimo, também é injusto no muito.', 10, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, se nas riquezas injustas não fostes fiéis, quem vos confiará as verdadeiras?', 11, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se no alheio não fostes fiéis, quem vos dará o que é vosso?', 12, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nenhum servo pode servir dois senhores; porque, ou há de odiar um e amar o outro, ou se há de chegar a um e desprezar o outro. Não podeis servir a Deus e a Mamom.', 13, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os fariseus, que eram avarentos, ouviam todas estas coisas, e zombavam dele.', 14, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Vós sois os que vos justificais a vós mesmos diante dos homens, mas Deus conhece os vossos corações, porque o que entre os homens é elevado, perante Deus é abominação.', 15, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A lei e os profetas duraram até João; desde então é anunciado o reino de Deus, e todo o homem emprega força para entrar nele.', 16, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E é mais fácil passar o céu e a terra do que cair um til da lei.', 17, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que deixa sua mulher, e casa com outra, adultera; e aquele que casa com a repudiada pelo marido, adultera também.', 18, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, havia um homem rico, e vestia-se de púrpura e de linho finíssimo, e vivia todos os dias regalada e esplendidamente.', 19, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Havia também um certo mendigo, chamado Lázaro, que jazia cheio de chagas à porta daquele;', 20, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desejava alimentar-se com as migalhas que caíam da mesa do rico; e os próprios cães vinham lamber-lhe as chagas.', 21, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que o mendigo morreu, e foi levado pelos anjos para o seio de Abraão; e morreu também o rico, e foi sepultado.', 22, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no inferno, ergueu os olhos, estando em tormentos, e viu ao longe Abraão, e Lázaro no seu seio.', 23, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, clamando, disse: Pai Abraão, tem misericórdia de mim, e manda a Lázaro, que molhe na água a ponta do seu dedo e me refresque a língua, porque estou atormentado nesta chama.', 24, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse, porém, Abraão: Filho, lembra-te de que recebeste os teus bens em tua vida, e Lázaro somente males; e agora este é consolado e tu atormentado.', 25, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, além disso, está posto um grande abismo entre nós e vós, de sorte que os que quisessem passar daqui para vós não poderiam, nem tampouco os de lá passar para cá.', 26, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse ele: Rogo-te, pois, ó pai, que o mandes à casa de meu pai,', 27, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois tenho cinco irmãos; para que lhes dê testemunho, a fim de que não venham também para este lugar de tormento.', 28, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhe Abraão: Têm Moisés e os profetas; ouçam-nos.', 29, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse ele: Não, pai Abraão; mas, se algum dentre os mortos fosse ter com eles, arrepender-se-iam.', 30, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porém, Abraão lhe disse: Se não ouvem a Moisés e aos profetas, tampouco acreditarão, ainda que algum dos mortos ressuscite.', 31, 16, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois disto designou o Senhor ainda outros setenta, e mandou-os adiante da sua face, de dois em dois, a todas as cidades e lugares aonde ele havia de ir.', 1, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia-lhes: Grande é, em verdade, a seara, mas os obreiros são poucos; rogai, pois, ao Senhor da seara que envie obreiros para a sua seara.', 2, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ide; eis que vos mando como cordeiros ao meio de lobos.', 3, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não leveis bolsa, nem alforje, nem alparcas; e a ninguém saudeis pelo caminho.', 4, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, em qualquer casa onde entrardes, dizei primeiro: Paz seja nesta casa.', 5, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se ali houver algum filho de paz, repousará sobre ele a vossa paz; e, se não, voltará para vós.', 6, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ficai na mesma casa, comendo e bebendo do que eles tiverem, pois digno é o obreiro de seu salário. Não andeis de casa em casa.', 7, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, em qualquer cidade em que entrardes, e vos receberem, comei do que vos for oferecido.', 8, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E curai os enfermos que nela houver, e dizei-lhes: É chegado a vós o reino de Deus.', 9, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas em qualquer cidade, em que entrardes e vos não receberem, saindo por suas ruas, dizei:', 10, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Até o pó, que da vossa cidade se nos pegou, sacudimos sobre vós. Sabei, contudo, isto, que já o reino de Deus é chegado a vós.', 11, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E digo-vos que mais tolerância haverá naquele dia para Sodoma do que para aquela cidade.', 12, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de ti, Corazim, ai de ti, Betsaida! Porque, se em Tiro e em Sidom se fizessem as maravilhas que em vós foram feitas, já há muito, assentadas em saco e cinza, se teriam arrependido.', 13, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, para Tiro e Sidom haverá menos rigor, no juízo, do que para vós.', 14, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tu, Cafarnaum, que te levantaste até ao céu, até ao inferno serás abatida.', 15, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem vos ouve a vós, a mim me ouve; e quem vos rejeita a vós, a mim me rejeita; e quem a mim me rejeita, rejeita aquele que me enviou.', 16, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E voltaram os setenta com alegria, dizendo: Senhor, pelo teu nome, até os demônios se nos sujeitam.', 17, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Eu via Satanás, como raio, cair do céu.', 18, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que vos dou poder para pisar serpentes e escorpiões, e toda a força do inimigo, e nada vos fará dano algum.', 19, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, não vos alegreis porque se vos sujeitem os espíritos; alegrai-vos antes por estarem os vossos nomes escritos nos céus.', 20, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Naquela mesma hora se alegrou Jesus no Espírito Santo, e disse: Graças te dou, ó Pai, Senhor do céu e da terra, que escondeste estas coisas aos sábios e inteligentes, e as revelaste às criancinhas; assim é, ó Pai, porque assim te aprouve.', 21, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tudo por meu Pai foi entregue; e ninguém conhece quem é o Filho senão o Pai, nem quem é o Pai senão o Filho, e aquele a quem o Filho o quiser revelar.', 22, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, voltando-se para os discípulos, disse-lhes em particular: Bem-aventurados os olhos que vêem o que vós vedes.', 23, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois vos digo que muitos profetas e reis desejaram ver o que vós vedes, e não o viram; e ouvir o que ouvis, e não o ouviram.', 24, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que se levantou um certo doutor da lei, tentando-o, e dizendo: Mestre, que farei para herdar a vida eterna?', 25, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhe disse: Que está escrito na lei? Como lês?', 26, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo ele, disse: Amarás ao Senhor teu Deus de todo o teu coração, e de toda a tua alma, e de todas as tuas forças, e de todo o teu entendimento, e ao teu próximo como a ti mesmo.', 27, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe: Respondeste bem; faze isso, e viverás.', 28, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, querendo justificar-se a si mesmo, disse a Jesus: E quem é o meu próximo?', 29, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo Jesus, disse: Descia um homem de Jerusalém para Jericó, e caiu nas mãos dos salteadores, os quais o despojaram, e espancando-o, se retiraram, deixando-o meio morto.', 30, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ocasionalmente descia pelo mesmo caminho certo sacerdote; e, vendo-o, passou de largo.', 31, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de igual modo também um levita, chegando àquele lugar, e, vendo-o, passou de largo.', 32, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas um samaritano, que ia de viagem, chegou ao pé dele e, vendo-o, moveu-se de íntima compaixão;', 33, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, aproximando-se, atou-lhe as feridas, deitando-lhes azeite e vinho; e, pondo-o sobre o seu animal, levou-o para uma estalagem, e cuidou dele;', 34, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, partindo no outro dia, tirou dois dinheiros, e deu-os ao hospedeiro, e disse-lhe: Cuida dele; e tudo o que de mais gastares eu to pagarei quando voltar.', 35, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qual, pois, destes três te parece que foi o próximo daquele que caiu nas mãos dos salteadores?', 36, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse: O que usou de misericórdia para com ele. Disse, pois, Jesus: Vai, e faze da mesma maneira.', 37, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, indo eles de caminho, entrou Jesus numa aldeia; e certa mulher, por nome Marta, o recebeu em sua casa;', 38, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tinha esta uma irmã chamada Maria, a qual, assentando-se também aos pés de Jesus, ouvia a sua palavra.', 39, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Marta, porém, andava distraída em muitos serviços; e, aproximando-se, disse: Senhor, não se te dá de que minha irmã me deixe servir só? Dize-lhe que me ajude.', 40, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E respondendo Jesus, disse-lhe: Marta, Marta, estás ansiosa e afadigada com muitas coisas, mas uma só é necessária;', 41, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Maria escolheu a boa parte, a qual não lhe será tirada.', 42, 10, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo Jesus entrado em Jericó, ia passando.', 1, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que havia ali um homem chamado Zaqueu; e era este um chefe dos publicanos, e era rico.', 2, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E procurava ver quem era Jesus, e não podia, por causa da multidão, pois era de pequena estatura.', 3, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, correndo adiante, subiu a uma figueira brava para o ver; porque havia de passar por ali.', 4, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando Jesus chegou àquele lugar, olhando para cima, viu-o e disse-lhe: Zaqueu, desce depressa, porque hoje me convém pousar em tua casa.', 5, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, apressando-se, desceu, e recebeu-o alegremente.', 6, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo todos isto, murmuravam, dizendo que entrara para ser hóspede de um homem pecador.', 7, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se Zaqueu, disse ao Senhor: Senhor, eis que eu dou aos pobres metade dos meus bens; e, se nalguma coisa tenho defraudado alguém, o restituo quadruplicado.', 8, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe Jesus: Hoje veio a salvação a esta casa, pois também este é filho de Abraão.', 9, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Filho do homem veio buscar e salvar o que se havia perdido.', 10, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo eles estas coisas, ele prosseguiu, e contou uma parábola; porquanto estava perto de Jerusalém, e cuidavam que logo se havia de manifestar o reino de Deus.', 11, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse pois: Certo homem nobre partiu para uma terra remota, a fim de tomar para si um reino e voltar depois.', 12, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando dez servos seus, deu-lhes dez minas, e disse-lhes: Negociai até que eu venha.', 13, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os seus concidadãos odiavam-no, e mandaram após ele embaixadores, dizendo: Não queremos que este reine sobre nós.', 14, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, voltando ele, depois de ter tomado o reino, disse que lhe chamassem aqueles servos, a quem tinha dado o dinheiro, para saber o que cada um tinha ganhado, negociando.', 15, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E veio o primeiro, dizendo: Senhor, a tua mina rendeu dez minas.', 16, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhe disse: Bem está, servo bom, porque no mínimo foste fiel, sobre dez cidades terás autoridade.', 17, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E veio o segundo, dizendo: Senhor, a tua mina rendeu cinco minas.', 18, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a este disse também: Sê tu também sobre cinco cidades.', 19, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E veio outro, dizendo: Senhor, aqui está a tua mina, que guardei num lenço;', 20, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tive medo de ti, que és homem rigoroso, que tomas o que não puseste, e segas o que não semeaste.', 21, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porém, ele lhe disse: Mau servo, pela tua boca te julgarei. Sabias que eu sou homem rigoroso, que tomo o que não pus, e sego o que não semeei;', 22, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por que não puseste, pois, o meu dinheiro no banco, para que eu, vindo, o exigisse com os juros?', 23, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse aos que estavam com ele: Tirai-lhe a mina, e dai-a ao que tem dez minas.', 24, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(E disseram-lhe eles: Senhor, ele tem dez minas. )', 25, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois eu vos digo que a qualquer que tiver ser-lhe-á dado, mas ao que não tiver, até o que tem lhe será tirado.', 26, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quanto àqueles meus inimigos que não quiseram que eu reinasse sobre eles, trazei-os aqui, e matai-os diante de mim.', 27, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dito isto, ia caminhando adiante, subindo para Jerusalém.', 28, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, chegando perto de Betfagé, e de Betânia, ao monte chamado das Oliveiras, mandou dois dos seus discípulos,', 29, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Ide à aldeia que está defronte, e aí, ao entrar, achareis preso um jumentinho em que nenhum homem ainda montou; soltai-o e trazei-o.', 30, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se alguém vos perguntar: Por que o soltais? assim lhe direis: Porque o Senhor o há de mister.', 31, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo os que haviam sido mandados, acharam como lhes dissera.', 32, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando soltaram o jumentinho, seus donos lhes disseram: Por que soltais o jumentinho?', 33, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles responderam: O Senhor o há de mister.', 34, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E trouxeram-no a Jesus; e, lançando sobre o jumentinho as suas vestes, puseram Jesus em cima.', 35, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo ele, estendiam no caminho as suas vestes.', 36, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando já chegava perto da descida do Monte das Oliveiras, toda a multidão dos discípulos, regozijando-se, começou a dar louvores a Deus em alta voz, por todas as maravilhas que tinham visto,', 37, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Bendito o Rei que vem em nome do Senhor; paz no céu, e glória nas alturas.', 38, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disseram-lhe de entre a multidão alguns dos fariseus: Mestre, repreende os teus discípulos.', 39, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo ele, disse-lhes: Digo-vos que, se estes se calarem, as próprias pedras clamarão.', 40, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando ia chegando, vendo a cidade, chorou sobre ela,', 41, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Ah! se tu conhecesses também, ao menos neste teu dia, o que à tua paz pertence! Mas agora isto está encoberto aos teus olhos.', 42, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque dias virão sobre ti, em que os teus inimigos te cercarão de trincheiras, e te sitiarão, e te estreitarão de todos os lados;', 43, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E te derrubarão, a ti e aos teus filhos que dentro de ti estiverem, e não deixarão em ti pedra sobre pedra, pois que não conheceste o tempo da tua visitação.', 44, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando no templo, começou a expulsar todos os que nele vendiam e compravam,', 45, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo-lhes: Está escrito: A minha casa é casa de oração; mas vós fizestes dela covil de salteadores.', 46, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os dias ensinava no templo; mas os principais dos sacerdotes, e os escribas, e os principais do povo procuravam matá-lo.', 47, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não achavam meio de o fazer, porque todo o povo pendia para ele, escutando-o.', 48, 19, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, olhando ele, viu os ricos lançarem as suas ofertas na arca do tesouro;', 1, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E viu também uma pobre viúva lançar ali duas pequenas moedas;', 2, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse: Em verdade vos digo que lançou mais do que todos, esta pobre viúva;', 3, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos aqueles deitaram para as ofertas de Deus do que lhes sobeja; mas esta, da sua pobreza, deitou todo o sustento que tinha.', 4, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dizendo alguns a respeito do templo, que estava ornado de formosas pedras e dádivas, disse:', 5, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto a estas coisas que vedes, dias virão em que não se deixará pedra sobre pedra, que não seja derrubada.', 6, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntaram-lhe, dizendo: Mestre, quando serão, pois, estas coisas? E que sinal haverá quando isto estiver para acontecer?', 7, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse então ele: Vede não vos enganem, porque virão muitos em meu nome, dizendo: Sou eu, e o tempo está próximo. Não vades, portanto, após eles.', 8, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando ouvirdes de guerras e sedições, não vos assusteis. Porque é necessário que isto aconteça primeiro, mas o fim não será logo.', 9, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então lhes disse: Levantar-se-á nação contra nação, e reino contra reino;', 10, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E haverá em vários lugares grandes terremotos, e fomes e pestilências; haverá também coisas espantosas, e grandes sinais do céu.', 11, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas antes de todas estas coisas lançarão mão de vós, e vos perseguirão, entregando-vos às sinagogas e às prisões, e conduzindo-vos à presença de reis e presidentes, por amor do meu nome.', 12, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vos acontecerá isto para testemunho.', 13, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Proponde, pois, em vossos corações não premeditar como haveis de responder;', 14, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu vos darei boca e sabedoria a que não poderão resistir nem contradizer todos quantos se vos opuserem.', 15, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E até pelos pais, e irmãos, e parentes, e amigos sereis entregues; e matarão alguns de vós.', 16, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de todos sereis odiados por causa do meu nome.', 17, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não perecerá um único cabelo da vossa cabeça.', 18, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na vossa paciência possuí as vossas almas.', 19, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando virdes Jerusalém cercada de exércitos, sabei então que é chegada a sua desolação.', 20, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, os que estiverem na Judéia, fujam para os montes; os que estiverem no meio da cidade, saiam; e os que nos campos não entrem nela.', 21, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque dias de vingança são estes, para que se cumpram todas as coisas que estão escritas.', 22, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ai das grávidas, e das que criarem naqueles dias! porque haverá grande aperto na terra, e ira sobre este povo.', 23, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E cairão ao fio da espada, e para todas as nações serão levados cativos; e Jerusalém será pisada pelos gentios, até que os tempos dos gentios se completem.', 24, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E haverá sinais no sol e na lua e nas estrelas; e na terra angústia das nações, em perplexidade pelo bramido do mar e das ondas.', 25, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Homens desmaiando de terror, na expectação das coisas que sobrevirão ao mundo; porquanto as virtudes do céu serão abaladas.', 26, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E então verão vir o Filho do homem numa nuvem, com poder e grande glória.', 27, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, quando estas coisas começarem a acontecer, olhai para cima e levantai as vossas cabeças, porque a vossa redenção está próxima.', 28, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes uma parábola: Olhai para a figueira, e para todas as árvores;', 29, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando já têm rebentado, vós sabeis por vós mesmos, vendo-as, que perto está já o verão.', 30, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também vós, quando virdes acontecer estas coisas, sabei que o reino de Deus está perto.', 31, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que não passará esta geração até que tudo aconteça.', 32, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Passará o céu e a terra, mas as minhas palavras não hão de passar.', 33, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E olhai por vós, não aconteça que os vossos corações se carreguem de glutonaria, de embriaguez, e dos cuidados da vida, e venha sobre vós de improviso aquele dia.', 34, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque virá como um laço sobre todos os que habitam na face de toda a terra.', 35, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vigiai, pois, em todo o tempo, orando, para que sejais havidos por dignos de evitar todas estas coisas que hão de acontecer, e de estar em pé diante do Filho do homem.', 36, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de dia ensinava no templo, e à noite, saindo, ficava no monte chamado das Oliveiras.', 37, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todo o povo ia ter com ele ao templo, de manhã cedo, para o ouvir.', 38, 21, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ajuntando-se entretanto muitos milhares de pessoas, de sorte que se atropelavam uns aos outros, começou a dizer aos seus discípulos: Acautelai-vos primeiramente do fermento dos fariseus, que é a hipocrisia.', 1, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nada há encoberto que não haja de ser descoberto; nem oculto, que não haja de ser sabido.', 2, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto tudo o que em trevas dissestes, à luz será ouvido; e o que falastes ao ouvido no gabinete, sobre os telhados será apregoado.', 3, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E digo-vos, amigos meus: Não temais os que matam o corpo e, depois, não têm mais que fazer.', 4, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eu vos mostrarei a quem deveis temer; temei aquele que, depois de matar, tem poder para lançar no inferno; sim, vos digo, a esse temei.', 5, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não se vendem cinco passarinhos por dois ceitis? E nenhum deles está esquecido diante de Deus.', 6, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E até os cabelos da vossa cabeça estão todos contados. Não temais pois; mais valeis vós do que muitos passarinhos.', 7, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E digo-vos que todo aquele que me confessar diante dos homens também o Filho do homem o confessará diante dos anjos de Deus.', 8, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas quem me negar diante dos homens será negado diante dos anjos de Deus.', 9, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a todo aquele que disser uma palavra contra o Filho do homem ser-lhe-á perdoada, mas ao que blasfemar contra o Espírito Santo não lhe será perdoado.', 10, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando vos conduzirem às sinagogas, aos magistrados e potestades, não estejais solícitos de como ou do que haveis de responder, nem do que haveis de dizer.', 11, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque na mesma hora vos ensinará o Espírito Santo o que vos convenha falar.', 12, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe um da multidão: Mestre, dize a meu irmão que reparta comigo a herança.', 13, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele lhe disse: Homem, quem me pôs a mim por juiz ou repartidor entre vós?', 14, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Acautelai-vos e guardai-vos da avareza; porque a vida de qualquer não consiste na abundância do que possui.', 15, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E propôs-lhe uma parábola, dizendo: A herdade de um homem rico tinha produzido com abundância;', 16, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E arrazoava ele entre si, dizendo: Que farei? Não tenho onde recolher os meus frutos.', 17, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse: Farei isto: Derrubarei os meus celeiros, e edificarei outros maiores, e ali recolherei todas as minhas novidades e os meus bens;', 18, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E direi a minha alma: Alma, tens em depósito muitos bens para muitos anos; descansa, come, bebe e folga.', 19, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus lhe disse: Louco! esta noite te pedirão a tua alma; e o que tens preparado, para quem será?', 20, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim é aquele que para si ajunta tesouros, e não é rico para com Deus.', 21, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse aos seus discípulos: Portanto vos digo: Não estejais apreensivos pela vossa vida, sobre o que comereis, nem pelo corpo, sobre o que vestireis.', 22, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mais é a vida do que o sustento, e o corpo mais do que as vestes.', 23, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Considerai os corvos, que nem semeiam, nem segam, nem têm despensa nem celeiro, e Deus os alimenta; quanto mais valeis vós do que as aves?', 24, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qual de vós, sendo solícito, pode acrescentar um côvado à sua estatura?', 25, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, se nem ainda podeis as coisas mínimas, por que estais ansiosos pelas outras?', 26, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Considerai os lírios, como eles crescem; não trabalham, nem fiam; e digo-vos que nem ainda Salomão, em toda a sua glória, se vestiu como um deles.', 27, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se Deus assim veste a erva que hoje está no campo e amanhã é lançada no forno, quanto mais a vós, homens de pouca fé?', 28, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não pergunteis, pois, que haveis de comer, ou que haveis de beber, e não andeis inquietos.', 29, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque as nações do mundo buscam todas essas coisas; mas vosso Pai sabe que precisais delas.', 30, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Buscai antes o reino de Deus, e todas estas coisas vos serão acrescentadas.', 31, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não temais, ó pequeno rebanho, porque a vosso Pai agradou dar-vos o reino.', 32, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vendei o que tendes, e dai esmolas. Fazei para vós bolsas que não se envelheçam; tesouro nos céus que nunca acabe, aonde não chega ladrão e a traça não rói.', 33, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, onde estiver o vosso tesouro, ali estará também o vosso coração.', 34, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estejam cingidos os vossos lombos, e acesas as vossas candeias.', 35, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sede vós semelhantes aos homens que esperam o seu senhor, quando houver de voltar das bodas, para que, quando vier, e bater, logo possam abrir-lhe.', 36, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados aqueles servos, os quais, quando o Senhor vier, achar vigiando! Em verdade vos digo que se cingirá, e os fará assentar à mesa e, chegando-se, os servirá.', 37, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se vier na segunda vigília, e se vier na terceira vigília, e os achar assim, bem-aventurados são os tais servos.', 38, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabei, porém, isto: que, se o pai de família soubesse a que hora havia de vir o ladrão, vigiaria, e não deixaria minar a sua casa.', 39, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, estai vós também apercebidos; porque virá o Filho do homem à hora que não imaginais.', 40, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe Pedro: Senhor, dizes essa parábola a nós, ou também a todos?', 41, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse o Senhor: Qual é, pois, o mordomo fiel e prudente, a quem o senhor pôs sobre os seus servos, para lhes dar a tempo a ração?', 42, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurado aquele servo a quem o seu senhor, quando vier, achar fazendo assim.', 43, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que sobre todos os seus bens o porá.', 44, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se aquele servo disser em seu coração: O meu senhor tarda em vir; e começar a espancar os criados e criadas, e a comer, e a beber, e a embriagar-se,', 45, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Virá o senhor daquele servo no dia em que o não espera, e numa hora que ele não sabe, e separá-lo-á, e lhe dará a sua parte com os infiéis.', 46, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o servo que soube a vontade do seu senhor, e não se aprontou, nem fez conforme a sua vontade, será castigado com muitos açoites;', 47, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que a não soube, e fez coisas dignas de açoites, com poucos açoites será castigado. E, a qualquer que muito for dado, muito se lhe pedirá, e ao que muito se lhe confiou, muito mais se lhe pedirá.', 48, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vim lançar fogo na terra; e que mais quero, se já está aceso?', 49, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Importa, porém, que seja batizado com um certo batismo; e como me angustio até que venha a cumprir-se!', 50, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cuidais vós que vim trazer paz à terra? Não, vos digo, mas antes dissensão;', 51, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque daqui em diante estarão cinco divididos numa casa: três contra dois, e dois contra três.', 52, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O pai estará dividido contra o filho, e o filho contra o pai; a mãe contra a filha, e a filha contra a mãe; a sogra contra sua nora, e a nora contra sua sogra.', 53, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia também à multidão: Quando vedes a nuvem que vem do ocidente, logo dizeis: Lá vem chuva, e assim sucede.', 54, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando assopra o sul, dizeis: Haverá calma; e assim sucede.', 55, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Hipócritas, sabeis discernir a face da terra e do céu; como não sabeis então discernir este tempo?', 56, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por que não julgais também por vós mesmos o que é justo?', 57, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando, pois, vais com o teu adversário ao magistrado, procura livrar-te dele no caminho; para que não suceda que te conduza ao juiz, e o juiz te entregue ao meirinho, e o meirinho te encerre na prisão.', 58, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo-te que não sairás dali enquanto não pagares o derradeiro ceitil.', 59, 12, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, estando ele a orar num certo lugar, quando acabou, lhe disse um dos seus discípulos: Senhor, ensina-nos a orar, como também João ensinou aos seus discípulos.', 1, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Quando orardes, dizei: Pai nosso, que estás nos céus, santificado seja o teu nome; venha o teu reino; seja feita a tua vontade, assim na terra, como no céu.', 2, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dá-nos cada dia o nosso pão cotidiano;', 3, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perdoa-nos os nossos pecados, pois também nós perdoamos a qualquer que nos deve, e não nos conduzas à tentação, mas livra-nos do mal.', 4, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes também: Qual de vós terá um amigo, e, se for procurá-lo à meia-noite, e lhe disser: Amigo, empresta-me três pães,', 5, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois que um amigo meu chegou a minha casa, vindo de caminho, e não tenho que apresentar-lhe;', 6, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se ele, respondendo de dentro, disser: Não me importunes; já está a porta fechada, e os meus filhos estão comigo na cama; não posso levantar-me para tos dar;', 7, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo-vos que, ainda que não se levante a dar-lhos, por ser seu amigo, levantar-se-á, todavia, por causa da sua importunação, e lhe dará tudo o que houver mister.', 8, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu vos digo a vós: Pedi, e dar-se-vos-á; buscai, e achareis; batei, e abrir-se-vos-á;', 9, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque qualquer que pede recebe; e quem busca acha; e a quem bate abrir-se-lhe-á.', 10, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qual o pai de entre vós que, se o filho lhe pedir pão, lhe dará uma pedra? Ou, também, se lhe pedir peixe, lhe dará por peixe uma serpente?', 11, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou, também, se lhe pedir um ovo, lhe dará um escorpião?', 12, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois se vós, sendo maus, sabeis dar boas dádivas aos vossos filhos, quanto mais dará o Pai celestial o Espírito Santo àqueles que lho pedirem?', 13, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava ele expulsando um demônio, o qual era mudo. E aconteceu que, saindo o demônio, o mudo falou; e maravilhou-se a multidão.', 14, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas alguns deles diziam: Ele expulsa os demônios por Belzebu, príncipe dos demônios.', 15, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outros, tentando-o, pediam-lhe um sinal do céu.', 16, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, conhecendo ele os seus pensamentos, disse-lhes: Todo o reino, dividido contra si mesmo, será assolado; e a casa, dividida contra si mesma, cairá.', 17, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se também Satanás está dividido contra si mesmo, como subsistirá o seu reino? Pois dizeis que eu expulso os demônios por Belzebu.', 18, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se eu expulso os demônios por Belzebu, por quem os expulsam vossos filhos? Eles, pois, serão os vossos juízes.', 19, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se eu expulso os demônios pelo dedo de Deus, certamente a vós é chegado o reino de Deus.', 20, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando o valente guarda, armado, a sua casa, em segurança está tudo quanto tem;', 21, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, sobrevindo outro mais valente do que ele, e vencendo-o, tira-lhe toda a sua armadura em que confiava, e reparte os seus despojos.', 22, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem não é comigo é contra mim; e quem comigo não ajunta, espalha.', 23, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando o espírito imundo tem saído do homem, anda por lugares secos, buscando repouso; e, não o achando, diz: Tornarei para minha casa, de onde saí.', 24, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando, acha-a varrida e adornada.', 25, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então vai, e leva consigo outros sete espíritos piores do que ele e, entrando, habitam ali; e o último estado desse homem é pior do que o primeiro.', 26, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, dizendo ele estas coisas, uma mulher dentre a multidão, levantando a voz, lhe disse: Bem-aventurado o ventre que te trouxe e os peitos em que mamaste.', 27, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele disse: Antes bemaventurados os que ouvem a palavra de Deus e a guardam.', 28, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ajuntando-se a multidão, começou a dizer: Maligna é esta geração; ela pede um sinal; e não lhe será dado outro sinal, senão o sinal do profeta Jonas;', 29, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto, assim como Jonas foi sinal para os ninivitas, assim o Filho do homem o será também para esta geração.', 30, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A rainha do sul se levantará no juízo com os homens desta geração, e os condenará; pois até dos confins da terra veio ouvir a sabedoria de Salomão; e eis aqui está quem é maior do que Salomão.', 31, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os homens de Nínive se levantarão no juízo com esta geração, e a condenarão; pois se converteram com a pregação de Jonas; e eis aqui está quem é maior do que Jonas.', 32, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ninguém, acendendo uma candeia, a põe em oculto, nem debaixo do alqueire, mas no velador, para que os que entram vejam a luz.', 33, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A candeia do corpo é o olho. Sendo, pois, o teu olho simples, também todo o teu corpo será luminoso; mas, se for mau, também o teu corpo será tenebroso.', 34, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vê, pois, que a luz que em ti há não sejam trevas.', 35, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, pois, todo o teu corpo é luminoso, não tendo em trevas parte alguma, todo será luminoso, como quando a candeia te ilumina com o seu resplendor.', 36, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando ele ainda falando, rogou-lhe um fariseu que fosse jantar com ele; e, entrando, assentou-se à mesa.', 37, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o fariseu admirou-se, vendo que não se lavara antes de jantar.', 38, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o Senhor lhe disse: Agora vós, os fariseus, limpais o exterior do copo e do prato; mas o vosso interior está cheio de rapina e maldade.', 39, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Loucos! Quem fez o exterior não fez também o interior?', 40, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes dai esmola do que tiverdes, e eis que tudo vos será limpo.', 41, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ai de vós, fariseus, que dizimais a hortelã, e a arruda, e toda a hortaliça, e desprezais o juízo e o amor de Deus. Importava fazer estas coisas, e não deixar as outras.', 42, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, fariseus, que amais os primeiros assentos nas sinagogas, e as saudações nas praças.', 43, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, escribas e fariseus, hipócritas! que sois como as sepulturas que não aparecem, e os homens que sobre elas andam não o sabem.', 44, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo um dos doutores da lei, disse-lhe: Mestre, quando dizes isso, também nos afrontas a nós.', 45, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhe disse: Ai de vós também, doutores da lei, que carregais os homens com cargas difíceis de transportar, e vós mesmos nem ainda com um dos vossos dedos tocais essas cargas.', 46, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós que edificais os sepulcros dos profetas, e vossos pais os mataram.', 47, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem testificais, pois, que consentis nas obras de vossos pais; porque eles os mataram, e vós edificais os seus sepulcros.', 48, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso diz também a sabedoria de Deus: Profetas e apóstolos lhes mandarei; e eles matarão uns, e perseguirão outros;', 49, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que desta geração seja requerido o sangue de todos os profetas que, desde a fundação do mundo, foi derramado;', 50, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde o sangue de Abel, até ao sangue de Zacarias, que foi morto entre o altar e o templo; assim, vos digo, será requerido desta geração.', 51, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai de vós, doutores da lei, que tirastes a chave da ciência; vós mesmos não entrastes, e impedistes os que entravam.', 52, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dizendo-lhes ele isto, começaram os escribas e os fariseus a apertá-lo fortemente, e a fazê-lo falar acerca de muitas coisas,', 53, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Armando-lhe ciladas, e procurando apanhar da sua boca alguma coisa para o acusarem.', 54, 11, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, não podendo esperar mais, achamos por bem ficar sozinhos em Atenas;', 1, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E enviamos Timóteo, nosso irmão, e ministro de Deus, e nosso cooperador no evangelho de Cristo, para vos confortar e vos exortar acerca da vossa fé;', 2, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que ninguém se comova por estas tribulações; porque vós mesmos sabeis que para isto fomos ordenados,', 3, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, estando ainda convosco, vos predizíamos que havíamos de ser afligidos, como sucedeu, e vós o sabeis.', 4, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, não podendo eu também esperar mais, mandei-o saber da vossa fé, temendo que o tentador vos tentasse, e o nosso trabalho viesse a ser inútil.', 5, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vindo, porém, agora Timóteo de vós para nós, e trazendo-nos boas novas da vossa fé e amor, e de como sempre tendes boa lembrança de nós, desejando muito ver-nos, como nós também a vós;', 6, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por esta razão, irmãos, ficamos consolados acerca de vós, em toda a nossa aflição e necessidade, pela vossa fé,', 7, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque agora vivemos, se estais firmes no Senhor.', 8, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, que ação de graças poderemos dar a Deus por vós, por todo o gozo com que nos regozijamos por vossa causa diante do nosso Deus,', 9, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Orando abundantemente dia e noite, para que possamos ver o vosso rosto, e supramos o que falta à vossa fé?', 10, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o mesmo nosso Deus e Pai, e nosso Senhor Jesus Cristo, encaminhem a nossa viagem para vós.', 11, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o Senhor vos aumente, e faça crescer em amor uns para com os outros, e para com todos, como também o fazemos para convosco;', 12, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para confirmar os vossos corações, para que sejais irrepreensíveis em santidade diante de nosso Deus e Pai, na vinda de nosso Senhor Jesus Cristo com todos os seus santos.', 13, 3, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu, porém, fala o que convém à sã doutrina.', 1, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os velhos, que sejam sóbrios, graves, prudentes, sãos na fé, no amor, e na paciência;', 2, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As mulheres idosas, semelhantemente, que sejam sérias no seu viver, como convém a santas, não caluniadoras, não dadas a muito vinho, mestras no bem;', 3, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que ensinem as mulheres novas a serem prudentes, a amarem seus maridos, a amarem seus filhos,', 4, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A serem moderadas, castas, boas donas de casa, sujeitas a seus maridos, a fim de que a palavra de Deus não seja blasfemada.', 5, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Exorta semelhantemente os jovens a que sejam moderados.', 6, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em tudo te dá por exemplo de boas obras; na doutrina mostra incorrupção, gravidade, sinceridade,', 7, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Linguagem sã e irrepreensível, para que o adversário se envergonhe, não tendo nenhum mal que dizer de nós.', 8, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Exorta os servos a que se sujeitem a seus senhores, e em tudo agradem, não contradizendo,', 9, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não defraudando, antes mostrando toda a boa lealdade, para que em tudo sejam ornamento da doutrina de Deus, nosso Salvador.', 10, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a graça salvadora de Deus se há manifestado a todos os homens,', 11, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ensinando-nos que, renunciando à impiedade e às concupiscências mundanas, vivamos neste presente século sóbria, e justa, e piamente,', 12, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aguardando a bem-aventurada esperança e o aparecimento da glória do grande Deus e nosso Senhor Jesus Cristo;', 13, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual se deu a si mesmo por nós para nos remir de toda a iniqüidade, e purificar para si um povo seu especial, zeloso de boas obras.', 14, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fala disto, e exorta e repreende com toda a autoridade. Ninguém te despreze.', 15, 2, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O ancião à senhora eleita, e a seus filhos, aos quais amo na verdade, e não somente eu, mas também todos os que têm conhecido a verdade,', 1, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por amor da verdade que está em nós, e para sempre estará conosco:', 2, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graça, misericórdia e paz, da parte de Deus Pai e da do Senhor Jesus Cristo, o Filho do Pai, seja convosco na verdade e amor.', 3, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Muito me alegro por achar que alguns de teus filhos andam na verdade, assim como temos recebido o mandamento do Pai.', 4, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora, senhora, rogo-te, não como escrevendo-te um novo mandamento, mas aquele mesmo que desde o princípio tivemos: que nos amemos uns aos outros.', 5, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o amor é este: que andemos segundo os seus mandamentos. Este é o mandamento, como já desde o princípio ouvistes, que andeis nele.', 6, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já muitos enganadores entraram no mundo, os quais não confessam que Jesus Cristo veio em carne. Este tal é o enganador e o anticristo.', 7, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Olhai por vós mesmos, para que não percamos o que temos ganho, antes recebamos o inteiro galardão.', 8, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todo aquele que prevarica, e não persevera na doutrina de Cristo, não tem a Deus. Quem persevera na doutrina de Cristo, esse tem tanto ao Pai como ao Filho.', 9, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém vem ter convosco, e não traz esta doutrina, não o recebais em casa, nem tampouco o saudeis.', 10, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque quem o saúda tem parte nas suas más obras.', 11, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo muito que escrever-vos, não quis fazê-lo com papel e tinta; mas espero ir ter convosco e falar face a face, para que o nosso gozo seja cumprido.', 12, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saúdam-te os filhos de tua irmã, a eleita. Amém.', 13, 1, 24);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, senhores, fazei o que for de justiça e eqüidade a vossos servos, sabendo que também tendes um Senhor nos céus.', 1, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Perseverai em oração, velando nela com ação de graças;', 2, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Orando também juntamente por nós, para que Deus nos abra a porta da palavra, a fim de falarmos do mistério de Cristo, pelo qual estou também preso;', 3, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que o manifeste, como me convém falar.', 4, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Andai com sabedoria para com os que estão de fora, remindo o tempo.', 5, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A vossa palavra seja sempre agradável, temperada com sal, para que saibais como vos convém responder a cada um.', 6, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tíquico, irmão amado e fiel ministro, e conservo no Senhor, vos fará saber o meu estado;', 7, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual vos enviei para o mesmo fim, para que saiba do vosso estado e console os vossos corações;', 8, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Juntamente com Onésimo, amado e fiel irmão, que é dos vossos; eles vos farão saber tudo o que por aqui se passa.', 9, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aristarco, que está preso comigo, vos saúda, e Marcos, o sobrinho de Barnabé, acerca do qual já recebestes mandamentos; se ele for ter convosco, recebei-o;', 10, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, chamado Justo; os quais são da circuncisão; são estes unicamente os meus cooperadores no reino de Deus; e para mim têm sido consolação.', 11, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saúda-vos Epafras, que é dos vossos, servo de Cristo, combatendo sempre por vós em orações, para que vos conserveis firmes, perfeitos e consumados em toda a vontade de Deus.', 12, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois eu lhe dou testemunho de que tem grande zelo por vós, e pelos que estão em Laodicéia, e pelos que estão em Hierápolis.', 13, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saúda-vos Lucas, o médico amado, e Demas.', 14, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai aos irmãos que estão em Laodicéia e a Ninfa e à igreja que está em sua casa.', 15, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando esta epístola tiver sido lida entre vós, fazei que também o seja na igreja dos laodicenses, e a que veio de Laodicéia lede-a vós também.', 16, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizei a Arquipo: Atenta para o ministério que recebeste no Senhor, para que o cumpras.', 17, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudação de minha mão, de Paulo. Lembrai-vos das minhas prisões. A graça seja convosco. Amém.', 18, 4, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque quero que saibais quão grande combate tenho por vós, e pelos que estão em Laodicéia, e por quantos não viram o meu rosto em carne;', 1, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que os seus corações sejam consolados, e estejam unidos em amor, e enriquecidos da plenitude da inteligência, para conhecimento do mistério de Deus e Pai, e de Cristo,', 2, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em quem estão escondidos todos os tesouros da sabedoria e da ciência.', 3, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E digo isto, para que ninguém vos engane com palavras persuasivas.', 4, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, ainda que esteja ausente quanto ao corpo, contudo, em espírito estou convosco, regozijando-me e vendo a vossa ordem e a firmeza da vossa fé em Cristo.', 5, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como, pois, recebestes o Senhor Jesus Cristo, assim também andai nele,', 6, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Arraigados e sobreedificados nele, e confirmados na fé, assim como fostes ensinados, nela abundando em ação de graças.', 7, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tende cuidado, para que ninguém vos faça presa sua, por meio de filosofias e vãs sutilezas, segundo a tradição dos homens, segundo os rudimentos do mundo, e não segundo Cristo;', 8, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nele habita corporalmente toda a plenitude da divindade;', 9, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estais perfeitos nele, que é a cabeça de todo o principado e potestade;', 10, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No qual também estais circuncidados com a circuncisão não feita por mão no despojo do corpo dos pecados da carne, pela circuncisão de Cristo;', 11, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sepultados com ele no batismo, nele também ressuscitastes pela fé no poder de Deus, que o ressuscitou dentre os mortos.', 12, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando vós estáveis mortos nos pecados, e na incircuncisão da vossa carne, vos vivificou juntamente com ele, perdoando-vos todas as ofensas,', 13, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Havendo riscado a cédula que era contra nós nas suas ordenanças, a qual de alguma maneira nos era contrária, e a tirou do meio de nós, cravando-a na cruz.', 14, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, despojando os principados e potestades, os expôs publicamente e deles triunfou em si mesmo.', 15, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, ninguém vos julgue pelo comer, ou pelo beber, ou por causa dos dias de festa, ou da lua nova, ou dos sábados,', 16, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que são sombras das coisas futuras, mas o corpo é de Cristo.', 17, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém vos domine a seu bel-prazer com pretexto de humildade e culto dos anjos, envolvendo-se em coisas que não viu; estando debalde inchado na sua carnal compreensão,', 18, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não ligado à cabeça, da qual todo o corpo, provido e organizado pelas juntas e ligaduras, vai crescendo em aumento de Deus.', 19, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, pois, estais mortos com Cristo quanto aos rudimentos do mundo, por que vos carregam ainda de ordenanças, como se vivêsseis no mundo, tais como:', 20, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não toques, não proves, não manuseies?', 21, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As quais coisas todas perecem pelo uso, segundo os preceitos e doutrinas dos homens;', 22, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As quais têm, na verdade, alguma aparência de sabedoria, em devoção voluntária, humildade, e em disciplina do corpo, mas não são de valor algum senão para a satisfação da carne.', 23, 2, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, apóstolo de Jesus Cristo, pela vontade de Deus, e o irmão Timóteo,', 1, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aos santos e irmãos fiéis em Cristo, que estão em Colossos: Graça a vós, e paz da parte de Deus nosso Pai e do Senhor Jesus Cristo.', 2, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graças damos a Deus, Pai de nosso Senhor Jesus Cristo, orando sempre por vós,', 3, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto ouvimos da vossa fé em Cristo Jesus, e do amor que tendes para com todos os santos;', 4, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por causa da esperança que vos está reservada nos céus, da qual já antes ouvistes pela palavra da verdade do evangelho,', 5, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que já chegou a vós, como também está em todo o mundo; e já vai frutificando, como também entre vós, desde o dia em que ouvistes e conhecestes a graça de Deus em verdade;', 6, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como aprendestes de Epafras, nosso amado conservo, que para vós é um fiel ministro de Cristo,', 7, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual nos declarou também o vosso amor no Espírito.', 8, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por esta razão, nós também, desde o dia em que o ouvimos, não cessamos de orar por vós, e de pedir que sejais cheios do conhecimento da sua vontade, em toda a sabedoria e inteligência espiritual;', 9, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que possais andar dignamente diante do Senhor, agradando-lhe em tudo, frutificando em toda a boa obra, e crescendo no conhecimento de Deus;', 10, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Corroborados em toda a fortaleza, segundo a força da sua glória, em toda a paciência, e longanimidade com gozo;', 11, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dando graças ao Pai que nos fez idôneos para participar da herança dos santos na luz;', 12, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual nos tirou da potestade das trevas, e nos transportou para o reino do Filho do seu amor;', 13, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em quem temos a redenção pelo seu sangue, a saber, a remissão dos pecados;', 14, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual é imagem do Deus invisível, o primogênito de toda a criação;', 15, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nele foram criadas todas as coisas que há nos céus e na terra, visíveis e invisíveis, sejam tronos, sejam dominações, sejam principados, sejam potestades. Tudo foi criado por ele e para ele.', 16, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele é antes de todas as coisas, e todas as coisas subsistem por ele.', 17, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele é a cabeça do corpo, da igreja; é o princípio e o primogênito dentre os mortos, para que em tudo tenha a preeminência.', 18, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque foi do agrado do Pai que toda a plenitude nele habitasse,', 19, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que, havendo por ele feito a paz pelo sangue da sua cruz, por meio dele reconciliasse consigo mesmo todas as coisas, tanto as que estão na terra, como as que estão nos céus.', 20, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A vós também, que noutro tempo éreis estranhos, e inimigos no entendimento pelas vossas obras más, agora contudo vos reconciliou', 21, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No corpo da sua carne, pela morte, para perante ele vos apresentar santos, e irrepreensíveis, e inculpáveis,', 22, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, na verdade, permanecerdes fundados e firmes na fé, e não vos moverdes da esperança do evangelho que tendes ouvido, o qual foi pregado a toda criatura que há debaixo do céu, e do qual eu, Paulo, estou feito ministro.', 23, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Regozijo-me agora no que padeço por vós, e na minha carne cumpro o resto das aflições de Cristo, pelo seu corpo, que é a igreja;', 24, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da qual eu estou feito ministro segundo a dispensação de Deus, que me foi concedida para convosco, para cumprir a palavra de Deus;', 25, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O mistério que esteve oculto desde todos os séculos, e em todas as gerações, e que agora foi manifesto aos seus santos;', 26, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aos quais Deus quis fazer conhecer quais são as riquezas da glória deste mistério entre os gentios, que é Cristo em vós, esperança da glória;', 27, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A quem anunciamos, admoestando a todo o homem, e ensinando a todo o homem em toda a sabedoria; para que apresentemos todo o homem perfeito em Jesus Cristo;', 28, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E para isto também trabalho, combatendo segundo a sua eficácia, que opera em mim poderosamente.', 29, 1, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse aos discípulos: É impossível que não venham escândalos, mas ai daquele por quem vierem!', 1, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Melhor lhe fora que lhe pusessem ao pescoço uma mó de atafona, e fosse lançado ao mar, do que fazer tropeçar um destes pequenos.', 2, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Olhai por vós mesmos. E, se teu irmão pecar contra ti, repreende-o e, se ele se arrepender, perdoa-lhe.', 3, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se pecar contra ti sete vezes no dia, e sete vezes no dia vier ter contigo, dizendo: Arrependo-me; perdoa-lhe.', 4, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disseram então os apóstolos ao Senhor: Acrescenta-nos a fé.', 5, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse o Senhor: Se tivésseis fé como um grão de mostarda, diríeis a esta amoreira: Desarraiga-te daqui, e planta-te no mar; e ela vos obedeceria.', 6, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qual de vós terá um servo a lavrar ou a apascentar gado, a quem, voltando ele do campo, diga: Chega-te, e assenta-te à mesa?', 7, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não lhe diga antes: Prepara-me a ceia, e cinge-te, e serve-me até que tenha comido e bebido, e depois comerás e beberás tu?', 8, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura dá graças ao tal servo, porque fez o que lhe foi mandado? Creio que não.', 9, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também vós, quando fizerdes tudo o que vos for mandado, dizei: Somos servos inúteis, porque fizemos somente o que devíamos fazer.', 10, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, indo ele a Jerusalém, passou pelo meio de Samaria e da Galiléia;', 11, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando numa certa aldeia, saíram-lhe ao encontro dez homens leprosos, os quais pararam de longe;', 12, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levantaram a voz, dizendo: Jesus, Mestre, tem misericórdia de nós.', 13, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, vendo-os, disse-lhes: Ide, e mostrai-vos aos sacerdotes. E aconteceu que, indo eles, ficaram limpos.', 14, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um deles, vendo que estava são, voltou glorificando a Deus em alta voz;', 15, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E caiu aos seus pés, com o rosto em terra, dando-lhe graças; e este era samaritano.', 16, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo Jesus, disse: Não foram dez os limpos? E onde estão os nove?', 17, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não houve quem voltasse para dar glória a Deus senão este estrangeiro?', 18, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe: Levanta-te, e vai; a tua fé te salvou.', 19, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, interrogado pelos fariseus sobre quando havia de vir o reino de Deus, respondeu-lhes, e disse: O reino de Deus não vem com aparência exterior.', 20, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem dirão: Ei-lo aqui, ou: Ei-lo ali; porque eis que o reino de Deus está entre vós.', 21, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse aos discípulos: Dias virão em que desejareis ver um dos dias do Filho do homem, e não o vereis.', 22, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dir-vos-ão: Ei-lo aqui, ou: Ei-lo ali. Não vades, nem os sigais;', 23, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, como o relâmpago ilumina desde uma extremidade inferior do céu até à outra extremidade, assim será também o Filho do homem no seu dia.', 24, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas primeiro convém que ele padeça muito, e seja reprovado por esta geração.', 25, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como aconteceu nos dias de Noé, assim será também nos dias do Filho do homem.', 26, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Comiam, bebiam, casavam, e davam-se em casamento, até ao dia em que Noé entrou na arca, e veio o dilúvio, e os consumiu a todos.', 27, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como também da mesma maneira aconteceu nos dias de Ló: Comiam, bebiam, compravam, vendiam, plantavam e edificavam;', 28, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas no dia em que Ló saiu de Sodoma choveu do céu fogo e enxofre, e os consumiu a todos.', 29, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim será no dia em que o Filho do homem se há de manifestar.', 30, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Naquele dia, quem estiver no telhado, tendo as suas alfaias em casa, não desça a tomá-las; e, da mesma sorte, o que estiver no campo não volte para trás.', 31, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lembrai-vos da mulher de Ló.', 32, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que procurar salvar a sua vida, perdê-la-á, e qualquer que a perder, salvá-la-á.', 33, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo-vos que naquela noite estarão dois numa cama; um será tomado, e outro será deixado.', 34, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Duas estarão juntas, moendo; uma será tomada, e outra será deixada.', 35, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dois estarão no campo; um será tomado, e o outro será deixado.', 36, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo, disseram-lhe: Onde, Senhor? E ele lhes disse: Onde estiver o corpo, aí se ajuntarão as águias.', 37, 17, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu num daqueles dias que, estando ele ensinando o povo no templo, e anunciando o evangelho, sobrevieram os principais dos sacerdotes e os escribas com os anciãos,', 1, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E falaram-lhe, dizendo: Dize-nos, com que autoridade fazes estas coisas? Ou, quem é que te deu esta autoridade?', 2, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo ele, disse-lhes: Também eu vos farei uma pergunta: Dizei-me pois:', 3, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O batismo de João era do céu ou dos homens?', 4, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles arrazoavam entre si, dizendo: Se dissermos: Do céu, ele nos dirá: Então por que o não crestes?', 5, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se dissermos: Dos homens; todo o povo nos apedrejará, pois têm por certo que João era profeta.', 6, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E responderam que não sabiam de onde era.', 7, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhes disse: Tampouco vos direi com que autoridade faço isto.', 8, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E começou a dizer ao povo esta parábola: Certo homem plantou uma vinha, e arrendou-a a uns lavradores, e partiu para fora da terra por muito tempo;', 9, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no tempo próprio mandou um servo aos lavradores, para que lhe dessem dos frutos da vinha; mas os lavradores, espancando-o, mandaram-no vazio.', 10, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tornou ainda a mandar outro servo; mas eles, espancando também a este, e afrontando-o, mandaram-no vazio.', 11, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tornou ainda a mandar um terceiro; mas eles, ferindo também a este, o expulsaram.', 12, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse o senhor da vinha: Que farei? Mandarei o meu filho amado; talvez que, vendo, o respeitem.', 13, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, vendo-o os lavradores, arrazoaram entre si, dizendo: Este é o herdeiro; vinde, matemo-lo, para que a herança seja nossa.', 14, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, lançando-o fora da vinha, o mataram. Que lhes fará, pois, o senhor da vinha?', 15, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irá, e destruirá estes lavradores, e dará a outros a vinha. E, ouvindo eles isto, disseram: Não seja assim!', 16, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, olhando para eles, disse: Que é isto, pois, que está escrito? A pedra, que os edificadores reprovaram, Essa foi feita cabeça da esquina.', 17, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que cair sobre aquela pedra ficará em pedaços, e aquele sobre quem ela cair será feito em pó.', 18, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os principais dos sacerdotes e os escribas procuravam lançar mão dele naquela mesma hora; mas temeram o povo; porque entenderam que contra eles dissera esta parábola.', 19, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, observando-o, mandaram espias, que se fingissem justos, para o apanharem nalguma palavra, e o entregarem à jurisdição e poder do presidente.', 20, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntaram-lhe, dizendo: Mestre, nós sabemos que falas e ensinas bem e retamente, e que não consideras a aparência da pessoa, mas ensinas com verdade o caminho de Deus.', 21, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É-nos lícito dar tributo a César ou não?', 22, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entendendo ele a sua astúcia, disse-lhes: Por que me tentais?', 23, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mostrai-me uma moeda. De quem tem a imagem e a inscrição? E, respondendo eles, disseram: De César.', 24, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes então: Dai, pois, a César o que é de César, e a Deus o que é de Deus.', 25, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não puderam apanhá-lo em palavra alguma diante do povo; e, maravilhados da sua resposta, calaram-se.', 26, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando-se alguns dos saduceus, que dizem não haver ressurreição, perguntaram-lhe,', 27, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Mestre, Moisés nos deixou escrito que, se o irmão de algum falecer, tendo mulher, e não deixar filhos, o irmão dele tome a mulher, e suscite posteridade a seu irmão.', 28, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Houve, pois, sete irmãos, e o primeiro tomou mulher, e morreu sem filhos;', 29, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tomou-a o segundo por mulher, e ele morreu sem filhos.', 30, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tomou-a o terceiro, e igualmente também os sete; e morreram, e nào deixaram filhos.', 31, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por último, depois de todos, morreu também a mulher.', 32, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, na ressurreição, de qual deles será a mulher, pois que os sete por mulher a tiveram?', 33, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo Jesus, disse-lhes: Os filhos deste mundo casam-se, e dão-se em casamento;', 34, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os que forem havidos por dignos de alcançar o mundo vindouro, e a ressurreição dentre os mortos, nem hão de casar, nem ser dados em casamento;', 35, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já não podem mais morrer; pois são iguais aos anjos, e são filhos de Deus, sendo filhos da ressurreição.', 36, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que os mortos hão de ressuscitar também o mostrou Moisés junto da sarça, quando chama ao Senhor Deus de Abraão, e Deus de Isaque, e Deus de Jacó.', 37, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, Deus não é Deus de mortos, mas de vivos; porque para ele vivem todos.', 38, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo alguns dos escribas, disseram: Mestre, disseste bem.', 39, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não ousavam perguntar-lhe mais coisa alguma.', 40, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Como dizem que o Cristo é filho de Davi?', 41, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto como o mesmo Davi diz no livro dos Salmos: Disse o SENHOR ao meu Senhor: Assenta-te à minha direita,', 42, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Até que eu ponha os teus inimigos por escabelo de teus pés.', 43, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se Davi lhe chama Senhor, como é ele seu filho?', 44, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo-o todo o povo, disse Jesus aos seus discípulos:', 45, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Guardai-vos dos escribas, que querem andar com vestes compridas; e amam as saudações nas praças, e as principais cadeiras nas sinagogas, e os primeiros lugares nos banquetes;', 46, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que devoram as casas das viúvas, fazendo, por pretexto, largas orações. Estes receberão maior condenação.', 47, 20, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque vós mesmos, irmãos, bem sabeis que a nossa entrada para convosco não foi vã;', 1, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, mesmo depois de termos antes padecido, e sido agravados em Filipos, como sabeis, tornamo-nos ousados em nosso Deus, para vos falar o evangelho de Deus com grande combate.', 2, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a nossa exortação não foi com engano, nem com imundícia, nem com fraudulência;', 3, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, como fomos aprovados de Deus para que o evangelho nos fosse confiado, assim falamos, não como para agradar aos homens, mas a Deus, que prova os nossos corações.', 4, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, como bem sabeis, nunca usamos de palavras lisonjeiras, nem houve um pretexto de avareza; Deus é testemunha;', 5, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não buscamos glória dos homens, nem de vós, nem de outros, ainda que podíamos, como apóstolos de Cristo, ser-vos pesados;', 6, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes fomos brandos entre vós, como a ama que cria seus filhos.', 7, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim nós, sendo-vos tão afeiçoados, de boa vontade quiséramos comunicar-vos, não somente o evangelho de Deus, mas ainda as nossas próprias almas; porquanto nos éreis muito queridos.', 8, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque bem vos lembrais, irmãos, do nosso trabalho e fadiga; pois, trabalhando noite e dia, para não sermos pesados a nenhum de vós, vos pregamos o evangelho de Deus.', 9, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós e Deus sois testemunhas de quão santa, e justa, e irrepreensivelmente nos houvemos para convosco, os que crestes.', 10, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim como bem sabeis de que modo vos exortávamos e consolávamos e testemunhávamos, a cada um de vós, como o pai a seus filhos;', 11, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que vos conduzísseis dignamente para com Deus, que vos chama para o seu reino e glória.', 12, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso também damos, sem cessar, graças a Deus, pois, havendo recebido de nós a palavra da pregação de Deus, a recebestes, não como palavra de homens, mas (segundo é, na verdade), como palavra de Deus, a qual também opera em vós, os que crestes.', 13, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque vós, irmãos, haveis sido feitos imitadores das igrejas de Deus que na Judéia estão em Jesus Cristo; porquanto também padecestes de vossos próprios concidadãos o mesmo que os judeus lhes fizeram a eles,', 14, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais também mataram o Senhor Jesus e os seus próprios profetas, e nos têm perseguido; e não agradam a Deus, e são contrários a todos os homens,', 15, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nos impedem de pregar aos gentios as palavras da salvação, a fim de encherem sempre a medida de seus pecados; mas a ira de Deus caiu sobre eles até ao fim.', 16, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nós, porém, irmãos, sendo privados de vós por um momento de tempo, de vista, mas não do coração, tanto mais procuramos com grande desejo ver o vosso rosto;', 17, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso bem quisemos uma e outra vez ir ter convosco, pelo menos eu, Paulo, mas Satanás no-lo impediu.', 18, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, qual é a nossa esperança, ou gozo, ou coroa de glória? Porventura não o sois vós também diante de nosso Senhor Jesus Cristo em sua vinda?', 19, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade vós sois a nossa glória e gozo.', 20, 2, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se toda a multidão deles, o levaram a Pilatos.', 1, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E começaram a acusá-lo, dizendo: Havemos achado este pervertendo a nação, proibindo dar o tributo a César, e dizendo que ele mesmo é Cristo, o rei.', 2, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pilatos perguntou-lhe, dizendo: Tu és o Rei dos Judeus? E ele, respondendo, disse-lhe: Tu o dizes.', 3, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Pilatos aos principais dos sacerdotes, e à multidão: Não acho culpa alguma neste homem.', 4, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eles insistiam cada vez mais, dizendo: Alvoroça o povo ensinando por toda a Judéia, começando desde a Galiléia até aqui.', 5, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pilatos, ouvindo falar da Galiléia perguntou se aquele homem era galileu.', 6, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sabendo que era da jurisdição de Herodes, remeteu-o a Herodes, que também naqueles dias estava em Jerusalém.', 7, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Herodes, quando viu a Jesus, alegrou-se muito; porque havia muito que desejava vê-lo, por ter ouvido dele muitas coisas; e esperava que lhe veria fazer algum sinal.', 8, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E interrogava-o com muitas palavras, mas ele nada lhe respondia.', 9, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estavam os principais dos sacerdotes, e os escribas, acusando-o com grande veemência.', 10, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Herodes, com os seus soldados, desprezou-o e, escarnecendo dele, vestiu-o de uma roupa resplandecente e tornou a enviá-lo a Pilatos.', 11, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no mesmo dia, Pilatos e Herodes entre si se fizeram amigos; pois dantes andavam em inimizade um com o outro.', 12, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, convocando Pilatos os principais dos sacerdotes, e os magistrados, e o povo,', 13, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes: Haveis-me apresentado este homem como pervertedor do povo; e eis que, examinando-o na vossa presença, nenhuma culpa, das de que o acusais, acho neste homem.', 14, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem mesmo Herodes, porque a ele vos remeti, e eis que não tem feito coisa alguma digna de morte.', 15, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Castigá-lo-ei, pois, e soltá-lo-ei.', 16, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E era-lhe necessário soltar-lhes um pela festa.', 17, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas toda a multidão clamou a uma, dizendo: Fora daqui com este, e solta-nos Barrabás.', 18, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual fora lançado na prisão por causa de uma sedição feita na cidade, e de um homicídio.', 19, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Falou, pois, outra vez Pilatos, querendo soltar a Jesus.', 20, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eles clamavam em contrário, dizendo: Crucifica-o, crucifica-o.', 21, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então ele, pela terceira vez, lhes disse: Mas que mal fez este? Não acho nele culpa alguma de morte. Castigá-lo-ei pois, e soltá-lo-ei.', 22, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eles instavam com grandes gritos, pedindo que fosse crucificado. E os seus gritos, e os dos principais dos sacerdotes, prevaleciam.', 23, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pilatos julgou que devia fazer o que eles pediam.', 24, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E soltou-lhes o que fora lançado na prisão por uma sedição e homicídio, que era o que pediam; mas entregou Jesus à vontade deles.', 25, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando o iam levando, tomaram um certo Simão, cireneu, que vinha do campo, e puseram-lhe a cruz às costas, para que a levasse após Jesus.', 26, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E seguia-o grande multidão de povo e de mulheres, as quais batiam nos peitos, e o lamentavam.', 27, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, voltando-se para elas, disse: Filhas de Jerusalém, não choreis por mim; chorai antes por vós mesmas, e por vossos filhos.', 28, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eis que hão de vir dias em que dirão: Bem-aventuradas as estéreis, e os ventres que não geraram, e os peitos que não amamentaram!', 29, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então começarão a dizer aos montes: Caí sobre nós, e aos outeiros: Cobri-nos.', 30, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se ao madeiro verde fazem isto, que se fará ao seco?', 31, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também conduziram outros dois, que eram malfeitores, para com ele serem mortos.', 32, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando chegaram ao lugar chamado a Caveira, ali o crucificaram, e aos malfeitores, um à direita e outro à esquerda.', 33, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia Jesus: Pai, perdoa-lhes, porque não sabem o que fazem. E, repartindo as suas vestes, lançaram sortes.', 34, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o povo estava olhando. E também os príncipes zombavam dele, dizendo: Aos outros salvou, salve-se a si mesmo, se este é o Cristo, o escolhido de Deus.', 35, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também os soldados o escarneciam, chegando-se a ele, e apresentando-lhe vinagre.', 36, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizendo: Se tu és o Rei dos Judeus, salva-te a ti mesmo.', 37, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também por cima dele, estava um título, escrito em letras gregas, romanas, e hebraicas: ESTE É O REI DOS JUDEUS.', 38, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um dos malfeitores que estavam pendurados blasfemava dele, dizendo: Se tu és o Cristo, salva-te a ti mesmo, e a nós.', 39, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondendo, porém, o outro, repreendia-o, dizendo: Tu nem ainda temes a Deus, estando na mesma condenação?', 40, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nós, na verdade, com justiça, porque recebemos o que os nossos feitos mereciam; mas este nenhum mal fez.', 41, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse a Jesus: Senhor, lembra-te de mim, quando entrares no teu reino.', 42, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe Jesus: Em verdade te digo que hoje estarás comigo no Paraíso.', 43, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E era já quase a hora sexta, e houve trevas em toda a terra até à hora nona, escurecendo-se o sol;', 44, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rasgou-se ao meio o véu do templo.', 45, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, clamando Jesus com grande voz, disse: Pai, nas tuas mãos entrego o meu espírito. E, havendo dito isto, expirou.', 46, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o centurião, vendo o que tinha acontecido, deu glória a Deus, dizendo: Na verdade, este homem era justo.', 47, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E toda a multidão que se ajuntara a este espetáculo, vendo o que havia acontecido, voltava batendo nos peitos.', 48, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os seus conhecidos, e as mulheres que juntamente o haviam seguido desde a Galiléia, estavam de longe vendo estas coisas.', 49, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que um homem por nome José, senador, homem de bem e justo,', 50, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que não tinha consentido no conselho e nos atos dos outros, de Arimatéia, cidade dos judeus, e que também esperava o reino de Deus;', 51, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esse, chegando a Pilatos, pediu o corpo de Jesus.', 52, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-o tirado, envolveu-o num lençol, e pô-lo num sepulcro escavado numa penha, onde ninguém ainda havia sido posto.', 53, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E era o dia da preparação, e amanhecia o sábado.', 54, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as mulheres, que tinham vindo com ele da Galiléia, seguiram também e viram o sepulcro, e como foi posto o seu corpo.', 55, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, voltando elas, prepararam especiarias e ungüentos; e no sábado repousaram, conforme o mandamento.', 56, 23, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, e Silvano, e Timóteo, à igreja dos tessalonicenses em Deus, o Pai, e no Senhor Jesus Cristo: Graça e paz tenhais de Deus nosso Pai e do Senhor Jesus Cristo.', 1, 1, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sempre damos graças a Deus por vós todos, fazendo menção de vós em nossas orações,', 2, 1, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lembrando-nos sem cessar da obra da vossa fé, do trabalho do amor, e da paciência da esperança em nosso Senhor Jesus Cristo, diante de nosso Deus e Pai,', 3, 1, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo, amados irmãos, que a vossa eleição é de Deus;', 4, 1, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o nosso evangelho não foi a vós somente em palavras, mas também em poder, e no Espírito Santo, e em muita certeza, como bem sabeis quais fomos entre vós, por amor de vós.', 5, 1, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós fostes feitos nossos imitadores, e do Senhor, recebendo a palavra em muita tribulação, com gozo do Espírito Santo.', 6, 1, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que fostes exemplo para todos os fiéis na macedônia e Acaia.', 7, 1, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque por vós soou a palavra do Senhor, não somente na macedônia e Acaia, mas também em todos os lugares a vossa fé para com Deus se espalhou, de tal maneira que já dela não temos necessidade de falar coisa alguma;', 8, 1, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eles mesmos anunciam de nós qual a entrada que tivemos para convosco, e como dos ídolos vos convertestes a Deus, para servir o Deus vivo e verdadeiro,', 9, 1, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E esperar dos céus o seu Filho, a quem ressuscitou dentre os mortos, a saber, Jesus, que nos livra da ira futura.', 10, 1, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, Naquele mesmo tempo, estavam presentes ali alguns que lhe falavam dos galileus, cujo sangue Pilatos misturara com os seus sacrifícios.', 1, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo Jesus, disse-lhes: Cuidais vós que esses galileus foram mais pecadores do que todos os galileus, por terem padecido tais coisas?', 2, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não, vos digo; antes, se não vos arrependerdes, todos de igual modo perecereis.', 3, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aqueles dezoito, sobre os quais caiu a torre de Siloé e os matou, cuidais que foram mais culpados do que todos quantos homens habitam em Jerusalém?', 4, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não, vos digo; antes, se não vos arrependerdes, todos de igual modo perecereis.', 5, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia esta parábola: Um certo homem tinha uma figueira plantada na sua vinha, e foi procurar nela fruto, não o achando;', 6, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse ao vinhateiro: Eis que há três anos venho procurar fruto nesta figueira, e não o acho. Corta-a; por que ocupa ainda a terra inutilmente?', 7, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo ele, disse-lhe: Senhor, deixa-a este ano, até que eu a escave e a esterque;', 8, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se der fruto, ficará e, se não, depois a mandarás cortar.', 9, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ensinava no sábado, numa das sinagogas.', 10, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que estava ali uma mulher que tinha um espírito de enfermidade, havia já dezoito anos; e andava curvada, e não podia de modo algum endireitar-se.', 11, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo-a Jesus, chamou-a a si, e disse-lhe: Mulher, estás livre da tua enfermidade.', 12, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pôs as mãos sobre ela, e logo se endireitou, e glorificava a Deus.', 13, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando a palavra o príncipe da sinagoga, indignado porque Jesus curava no sábado, disse à multidão: Seis dias há em que é mister trabalhar; nestes, pois, vinde para serdes curados, e não no dia de sábado.', 14, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Respondeu-lhe, porém, o Senhor, e disse: Hipócrita, no sábado não desprende da manjedoura cada um de vós o seu boi, ou jumento, e não o leva a beber?', 15, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não convinha soltar desta prisão, no dia de sábado, esta filha de Abraão, a qual há dezoito anos Satanás tinha presa?', 16, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dizendo ele isto, todos os seus adversários ficaram envergonhados, e todo o povo se alegrava por todas as coisas gloriosas que eram feitas por ele.', 17, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia: A que é semelhante o reino de Deus, e a que o compararei?', 18, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É semelhante ao grão de mostarda que um homem, tomando-o, lançou na sua horta; e cresceu, e fez-se grande árvore, e em seus ramos se aninharam as aves do céu.', 19, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse outra vez: A que compararei o reino de Deus?', 20, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É semelhante ao fermento que uma mulher, tomando-o, escondeu em três medidas de farinha, até que tudo levedou.', 21, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E percorria as cidades e as aldeias, ensinando, e caminhando para Jerusalém.', 22, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe um: Senhor, são poucos os que se salvam? E ele lhe respondeu:', 23, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porfiai por entrar pela porta estreita; porque eu vos digo que muitos procurarão entrar, e não poderão.', 24, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando o pai de família se levantar e cerrar a porta, e começardes, de fora, a bater à porta, dizendo: Senhor, Senhor, abre-nos; e, respondendo ele, vos disser: Não sei de onde vós sois;', 25, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então começareis a dizer: Temos comido e bebido na tua presença, e tu tens ensinado nas nossas ruas.', 26, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele vos responderá: Digo-vos que não vos conheço nem sei de onde vós sois; apartai-vos de mim, vós todos os que praticais a iniqüidade.', 27, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ali haverá choro e ranger de dentes, quando virdes Abraão, e Isaque, e Jacó, e todos os profetas no reino de Deus, e vós lançados fora.', 28, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E virão do oriente, e do ocidente, e do norte, e do sul, e assentar-se-ão à mesa no reino de Deus.', 29, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que derradeiros há que serão os primeiros; e primeiros há que serão os derradeiros.', 30, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Naquele mesmo dia chegaram uns fariseus, dizendo-lhe: Sai, e retira-te daqui, porque Herodes quer matar-te.', 31, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E respondeu-lhes: Ide, e dizei àquela raposa: Eis que eu expulso demônios, e efetuo curas, hoje e amanhã, e no terceiro dia sou consumado.', 32, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Importa, porém, caminhar hoje, amanhã, e no dia seguinte, para que não suceda que morra um profeta fora de Jerusalém.', 33, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jerusalém, Jerusalém, que matas os profetas, e apedrejas os que te são enviados! Quantas vezes quis eu ajuntar os teus filhos, como a galinha os seus pintos debaixo das asas, e não quiseste?', 34, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que a vossa casa se vos deixará deserta. E em verdade vos digo que não me vereis até que venha o tempo em que digais: Bendito aquele que vem em nome do Senhor.', 35, 13, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, prisioneiro de Jesus Cristo, e o irmão Timóteo, ao amado Filemom, nosso cooperador,', 1, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E à nossa amada Áfia, e a Arquipo, nosso camarada, e à igreja que está em tua casa:', 2, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graça a vós e paz da parte de Deus nosso Pai, e do Senhor Jesus Cristo.', 3, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graças dou ao meu Deus, lembrando-me sempre de ti nas minhas orações;', 4, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvindo do teu amor e da fé que tens para com o Senhor Jesus Cristo, e para com todos os santos;', 5, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que a comunicação da tua fé seja eficaz no conhecimento de todo o bem que em vós há por Cristo Jesus.', 6, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque temos grande gozo e consolação do teu amor, porque por ti, ó irmão, as entranhas dos santos foram recreadas.', 7, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, ainda que tenha em Cristo grande confiança para te mandar o que te convém,', 8, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia peço-te antes por amor, sendo eu tal como sou, Paulo o velho, e também agora prisioneiro de Jesus Cristo.', 9, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Peço-te por meu filho Onésimo, que gerei nas minhas prisões;', 10, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual noutro tempo te foi inútil, mas agora a ti e a mim muito útil; eu to tornei a enviar.', 11, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tu torna a recebê-lo como às minhas entranhas.', 12, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu bem o quisera conservar comigo, para que por ti me servisse nas prisões do evangelho;', 13, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nada quis fazer sem o teu parecer, para que o teu benefício não fosse como por força, mas, voluntário.', 14, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque bem pode ser que ele se tenha separado de ti por algum tempo, para que o retivesses para sempre,', 15, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não já como servo, antes, mais do que servo, como irmão amado, particularmente de mim, e quanto mais de ti, assim na carne como no Senhor?', 16, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, pois, se me tens por companheiro, recebe-o como a mim mesmo.', 17, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se te fez algum dano, ou te deve alguma coisa, põe isso à minha conta.', 18, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, Paulo, de minha própria mão o escrevi; eu o pagarei, para te não dizer que ainda mesmo a ti próprio a mim te deves.', 19, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sim, irmão, eu me regozijarei de ti no Senhor; recreia as minhas entranhas no Senhor.', 20, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Escrevi-te confiado na tua obediência, sabendo que ainda farás mais do que digo.', 21, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E juntamente prepara-me também pousada, porque espero que pelas vossas orações vos hei de ser concedido.', 22, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saúdam-te Epafras, meu companheiro de prisão por Cristo Jesus,', 23, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Marcos, Aristarco, Demas e Lucas, meus cooperadores.', 24, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça de nosso Senhor Jesus Cristo seja com o vosso espírito. Amém.', 25, 1, 18);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Finalmente, irmãos, vos rogamos e exortamos no Senhor Jesus, que assim como recebestes de nós, de que maneira convém andar e agradar a Deus, assim andai, para que possais progredir cada vez mais.', 1, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque vós bem sabeis que mandamentos vos temos dado pelo Senhor Jesus.', 2, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque esta é a vontade de Deus, a vossa santificação; que vos abstenhais da fornicação;', 3, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que cada um de vós saiba possuir o seu vaso em santificação e honra;', 4, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não na paixão da concupiscência, como os gentios, que não conhecem a Deus.', 5, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém oprima ou engane a seu irmão em negócio algum, porque o Senhor é vingador de todas estas coisas, como também antes vo-lo dissemos e testificamos.', 6, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não nos chamou Deus para a imundícia, mas para a santificação.', 7, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, quem despreza isto não despreza ao homem, mas sim a Deus, que nos deu também o seu Espírito Santo.', 8, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto, porém, ao amor fraternal, não necessitais de que vos escreva, visto que vós mesmos estais instruídos por Deus que vos ameis uns aos outros;', 9, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também já assim o fazeis para com todos os irmãos que estão por toda a macedônia. Exortamo-vos, porém, a que ainda nisto aumenteis cada vez mais.', 10, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E procureis viver quietos, e tratar dos vossos próprios negócios, e trabalhar com vossas próprias mãos, como já vo-lo temos mandado;', 11, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que andeis honestamente para com os que estão de fora, e não necessiteis de coisa alguma.', 12, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não quero, porém, irmãos, que sejais ignorantes acerca dos que já dormem, para que não vos entristeçais, como os demais, que não têm esperança.', 13, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se cremos que Jesus morreu e ressuscitou, assim também aos que em Jesus dormem, Deus os tornará a trazer com ele.', 14, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizemo-vos, pois, isto, pela palavra do Senhor: que nós, os que ficarmos vivos para a vinda do Senhor, não precederemos os que dormem.', 15, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o mesmo Senhor descerá do céu com alarido, e com voz de arcanjo, e com a trombeta de Deus; e os que morreram em Cristo ressuscitarão primeiro.', 16, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois nós, os que ficarmos vivos, seremos arrebatados juntamente com eles nas nuvens, a encontrar o Senhor nos ares, e assim estaremos sempre com o Senhor.', 17, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, consolai-vos uns aos outros com estas palavras.', 18, 4, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no primeiro dia da semana, muito de madrugada, foram elas ao sepulcro, levando as especiarias que tinham preparado, e algumas outras com elas.', 1, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E acharam a pedra revolvida do sepulcro.', 2, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando, não acharam o corpo do Senhor Jesus.', 3, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, estando elas muito perplexas a esse respeito, eis que pararam junto delas dois homens, com vestes resplandecentes.', 4, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando elas muito atemorizadas, e abaixando o rosto para o chão, eles lhes disseram: Por que buscais o vivente entre os mortos?', 5, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não está aqui, mas ressuscitou. Lembrai-vos como vos falou, estando ainda na Galiléia,', 6, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Convém que o Filho do homem seja entregue nas mãos de homens pecadores, e seja crucificado, e ao terceiro dia ressuscite.', 7, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lembraram-se das suas palavras.', 8, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, voltando do sepulcro, anunciaram todas estas coisas aos onze e a todos os demais.', 9, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eram Maria Madalena, e Joana, e Maria, mãe de Tiago, e as outras que com elas estavam, as que diziam estas coisas aos apóstolos.', 10, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as suas palavras lhes pareciam como desvario, e não as creram.', 11, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pedro, porém, levantando-se, correu ao sepulcro e, abaixando-se, viu só os lençóis ali postos; e retirou-se, admirando consigo aquele caso.', 12, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que no mesmo dia iam dois deles para uma aldeia, que distava de Jerusalém sessenta estádios, cujo nome era Emaús.', 13, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E iam falando entre si de tudo aquilo que havia sucedido.', 14, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, indo eles falando entre si, e fazendo perguntas um ao outro, o mesmo Jesus se aproximou, e ia com eles.', 15, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os olhos deles estavam como que fechados, para que o não conhecessem.', 16, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Que palavras são essas que, caminhando, trocais entre vós, e por que estais tristes?', 17, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo um, cujo nome era Cléopas, disse-lhe: És tu só peregrino em Jerusalém, e não sabes as coisas que nela têm sucedido nestes dias?', 18, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes perguntou: Quais? E eles lhe disseram: As que dizem respeito a Jesus Nazareno, que foi homem profeta, poderoso em obras e palavras diante de Deus e de todo o povo;', 19, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E como os principais dos sacerdotes e os nossos príncipes o entregaram à condenação de morte, e o crucificaram.', 20, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nós esperávamos que fosse ele o que remisse Israel; mas agora, sobre tudo isso, é já hoje o terceiro dia desde que essas coisas aconteceram.', 21, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É verdade que também algumas mulheres dentre nós nos maravilharam, as quais de madrugada foram ao sepulcro;', 22, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, não achando o seu corpo, voltaram, dizendo que também tinham visto uma visão de anjos, que dizem que ele vive.', 23, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns dos que estavam conosco foram ao sepulcro, e acharam ser assim como as mulheres haviam dito; porém, a ele não o viram.', 24, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Ó néscios, e tardos de coração para crer tudo o que os profetas disseram!', 25, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura não convinha que o Cristo padecesse estas coisas e entrasse na sua glória?', 26, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, começando por Moisés, e por todos os profetas, explicava-lhes o que dele se achava em todas as Escrituras.', 27, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegaram à aldeia para onde iam, e ele fez como quem ia para mais longe.', 28, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles o constrangeram, dizendo: Fica conosco, porque já é tarde, e já declinou o dia. E entrou para ficar com eles.', 29, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, estando com eles à mesa, tomando o pão, o abençoou e partiu-o, e lho deu.', 30, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Abriram-se-lhes então os olhos, e o conheceram, e ele desapareceu-lhes.', 31, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disseram um para o outro: Porventura não ardia em nós o nosso coração quando, pelo caminho, nos falava, e quando nos abria as Escrituras?', 32, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E na mesma hora, levantando-se, tornaram para Jerusalém, e acharam congregados os onze, e os que estavam com eles,', 33, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais diziam: Ressuscitou verdadeiramente o Senhor, e já apareceu a Simão.', 34, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles lhes contaram o que lhes acontecera no caminho, e como deles fora conhecido no partir do pão.', 35, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E falando eles destas coisas, o mesmo Jesus se apresentou no meio deles, e disse-lhes: Paz seja convosco.', 36, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles, espantados e atemorizados, pensavam que viam algum espírito.', 37, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Por que estais perturbados, e por que sobem tais pensamentos aos vossos corações?', 38, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede as minhas mãos e os meus pés, que sou eu mesmo; apalpai-me e vede, pois um espírito não tem carne nem ossos, como vedes que eu tenho.', 39, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, dizendo isto, mostrou-lhes as mãos e os pés.', 40, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, não o crendo eles ainda por causa da alegria, e estando maravilhados, disse-lhes: Tendes aqui alguma coisa que comer?', 41, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então eles apresentaram-lhe parte de um peixe assado, e um favo de mel;', 42, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que ele tomou, e comeu diante deles.', 43, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: São estas as palavras que vos disse estando ainda convosco: Que convinha que se cumprisse tudo o que de mim estava escrito na lei de Moisés, e nos profetas e nos Salmos.', 44, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então abriu-lhes o entendimento para compreenderem as Escrituras.', 45, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Assim está escrito, e assim convinha que o Cristo padecesse, e ao terceiro dia ressuscitasse dentre os mortos,', 46, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E em seu nome se pregasse o arrependimento e a remissão dos pecados, em todas as nações, começando por Jerusalém.', 47, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E destas coisas sois vós testemunhas.', 48, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que sobre vós envio a promessa de meu Pai; ficai, porém, na cidade de Jerusalém, até que do alto sejais revestidos de poder.', 49, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levou-os fora, até betânia; e, levantando as suas mãos, os abençoou.', 50, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, abençoando-os ele, se apartou deles e foi elevado ao céu.', 51, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, adorando-o eles, tornaram com grande júbilo para Jerusalém.', 52, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estavam sempre no templo, louvando e bendizendo a Deus. Amém.', 53, 24, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, irmãos, acerca dos tempos e das estações, não necessitais de que se vos escreva;', 1, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque vós mesmos sabeis muito bem que o dia do Senhor virá como o ladrão de noite;', 2, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois que, quando disserem: Há paz e segurança, então lhes sobrevirá repentina destruição, como as dores de parto àquela que está grávida, e de modo nenhum escaparão.', 3, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós, irmãos, já não estais em trevas, para que aquele dia vos surpreenda como um ladrão;', 4, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos vós sois filhos da luz e filhos do dia; nós não somos da noite nem das trevas.', 5, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não durmamos, pois, como os demais, mas vigiemos, e sejamos sóbrios;', 6, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os que dormem, dormem de noite, e os que se embebedam, embebedam-se de noite.', 7, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nós, que somos do dia, sejamos sóbrios, vestindo-nos da couraça da fé e do amor, e tendo por capacete a esperança da salvação;', 8, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus não nos destinou para a ira, mas para a aquisição da salvação, por nosso Senhor Jesus Cristo,', 9, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que morreu por nós, para que, quer vigiemos, quer durmamos, vivamos juntamente com ele.', 10, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso exortai-vos uns aos outros, e edificai-vos uns aos outros, como também o fazeis.', 11, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rogamo-vos, irmãos, que reconheçais os que trabalham entre vós e que presidem sobre vós no Senhor, e vos admoestam;', 12, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que os tenhais em grande estima e amor, por causa da sua obra. Tende paz entre vós.', 13, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Rogamo-vos, também, irmãos, que admoesteis os desordeiros, consoleis os de pouco ânimo, sustenteis os fracos, e sejais pacientes para com todos.', 14, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede que ninguém dê a outrem mal por mal, mas segui sempre o bem, tanto uns para com os outros, como para com todos.', 15, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Regozijai-vos sempre.', 16, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Orai sem cessar.', 17, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em tudo dai graças, porque esta é a vontade de Deus em Cristo Jesus para convosco.', 18, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não extingais o Espírito.', 19, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não desprezeis as profecias.', 20, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Examinai tudo. Retende o bem.', 21, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Abstende-vos de toda a aparência do mal.', 22, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o mesmo Deus de paz vos santifique em tudo; e todo o vosso espírito, e alma, e corpo, sejam plenamente conservados irrepreensíveis para a vinda de nosso Senhor Jesus Cristo.', 23, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fiel é o que vos chama, o qual também o fará.', 24, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, orai por nós.', 25, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a todos os irmãos com ósculo santo.', 26, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelo Senhor vos conjuro que esta epístola seja lida a todos os santos irmãos.', 27, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça de nosso Senhor Jesus Cristo seja convosco. Amém.', 28, 5, 13);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estava, pois, perto a festa dos pães ázimos, chamada a páscoa.', 1, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os principais dos sacerdotes, e os escribas, andavam procurando como o matariam; porque temiam o povo.', 2, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entrou, porém, Satanás em Judas, que tinha por sobrenome Iscariotes, o qual era do número dos doze.', 3, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi, e falou com os principais dos sacerdotes, e com os capitães, de como lho entregaria;', 4, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais se alegraram, e convieram em lhe dar dinheiro.', 5, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele concordou; e buscava oportunidade para lho entregar sem alvoroço.', 6, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chegou, porém, o dia dos ázimos, em que importava sacrificar a páscoa.', 7, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mandou a Pedro e a João, dizendo: Ide, preparai-nos a páscoa, para que a comamos.', 8, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles lhe perguntaram: Onde queres que a preparemos?', 9, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Eis que, quando entrardes na cidade, encontrareis um homem, levando um cântaro de água; segui-o até à casa em que ele entrar.', 10, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E direis ao pai de família da casa: O Mestre te diz: Onde está o aposento em que hei de comer a páscoa com os meus discípulos?', 11, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então ele vos mostrará um grande cenáculo mobilado; aí fazei preparativos.', 12, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo eles, acharam como lhes havia sido dito; e prepararam a páscoa.', 13, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegada a hora, pôs-se à mesa, e com ele os doze apóstolos.', 14, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Desejei muito comer convosco esta páscoa, antes que padeça;', 15, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque vos digo que não a comerei mais até que ela se cumpra no reino de Deus.', 16, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando o cálice, e havendo dado graças, disse: Tomai-o, e reparti-o entre vós;', 17, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque vos digo que já não beberei do fruto da vide, até que venha o reino de Deus.', 18, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando o pão, e havendo dado graças, partiu-o, e deu-lho, dizendo: Isto é o meu corpo, que por vós é dado; fazei isto em memória de mim.', 19, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Semelhantemente, tomou o cálice, depois da ceia, dizendo: Este cálice é o novo testamento no meu sangue, que é derramado por vós.', 20, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eis que a mão do que me trai está comigo à mesa.', 21, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, na verdade, o Filho do homem vai segundo o que está determinado; mas ai daquele homem por quem é traído!', 22, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E começaram a perguntar entre si qual deles seria o que havia de fazer isto.', 23, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E houve também entre eles contenda, sobre qual deles parecia ser o maior.', 24, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Os reis dos gentios dominam sobre eles, e os que têm autoridade sobre eles são chamados benfeitores.', 25, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não sereis vós assim; antes o maior entre vós seja como o menor; e quem governa como quem serve.', 26, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois qual é maior: quem está à mesa, ou quem serve? Porventura não é quem está à mesa? Eu, porém, entre vós sou como aquele que serve.', 27, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós sois os que tendes permanecido comigo nas minhas tentações.', 28, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu vos destino o reino, como meu Pai mo destinou,', 29, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que comais e bebais à minha mesa no meu reino, e vos assenteis sobre tronos, julgando as doze tribos de Israel.', 30, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse também o Senhor: Simão, Simão, eis que Satanás vos pediu para vos cirandar como trigo;', 31, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eu roguei por ti, para que a tua fé não desfaleça; e tu, quando te converteres, confirma teus irmãos.', 32, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhe disse: Senhor, estou pronto a ir contigo até à prisão e à morte.', 33, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele disse: Digo-te, Pedro, que não cantará hoje o galo antes que três vezes negues que me conheces.', 34, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Quando vos mandei sem bolsa, alforje, ou alparcas, faltou-vos porventura alguma coisa? Eles responderam: Nada.', 35, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Disse-lhes pois: Mas agora, aquele que tiver bolsa, tome-a, como também o alforje; e, o que não tem espada, venda a sua capa e compre-a;', 36, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto vos digo que importa que em mim se cumpra aquilo que está escrito: E com os malfeitores foi contado. Porque o que está escrito de mim terá cumprimento.', 37, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles disseram: Senhor, eis aqui duas espadas. E ele lhes disse: Basta.', 38, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo, foi, como costumava, para o Monte das Oliveiras; e também os seus discípulos o seguiram.', 39, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando chegou àquele lugar, disse-lhes: Orai, para que não entreis em tentação.', 40, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E apartou-se deles cerca de um tiro de pedra; e, pondo-se de joelhos, orava,', 41, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Pai, se queres, passa de mim este cálice; todavia não se faça a minha vontade, mas a tua.', 42, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E apareceu-lhe um anjo do céu, que o fortalecia.', 43, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, posto em agonia, orava mais intensamente. E o seu suor tornou-se como grandes gotas de sangue, que corriam até ao chão.', 44, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se da oração, veio para os seus discípulos, e achou-os dormindo de tristeza.', 45, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Por que estais dormindo? Levantai-vos, e orai, para que não entreis em tentação.', 46, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando ele ainda a falar, surgiu uma multidão; e um dos doze, que se chamava Judas, ia adiante dela, e chegou-se a Jesus para o beijar.', 47, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhe disse: Judas, com um beijo trais o Filho do homem?', 48, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo os que estavam com ele o que ia suceder, disseram-lhe: Senhor, feriremos à espada?', 49, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um deles feriu o servo do sumo sacerdote, e cortou-lhe a orelha direita.', 50, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo Jesus, disse: Deixai-os; basta. E, tocando-lhe a orelha, o curou.', 51, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Jesus aos principais dos sacerdotes, e capitães do templo, e anciãos, que tinham ido contra ele: Saístes, como a um salteador, com espadas e varapaus?', 52, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho estado todos os dias convosco no templo, e não estendestes as mãos contra mim, mas esta é a vossa hora e o poder das trevas.', 53, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, prendendo-o, o levaram, e o puseram em casa do sumo sacerdote. E Pedro seguia-o de longe.', 54, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-se acendido fogo no meio do pátio, estando todos sentados, assentou-se Pedro entre eles.', 55, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E como certa criada, vendo-o estar assentado ao fogo, pusesse os olhos nele, disse: Este também estava com ele.', 56, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porém, ele negou-o, dizendo: Mulher, não o conheço.', 57, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, um pouco depois, vendo-o outro, disse: Tu és também deles. Mas Pedro disse: Homem, não sou.', 58, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passada quase uma hora, um outro afirmava, dizendo: Também este verdadeiramente estava com ele, pois também é galileu.', 59, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro disse: Homem, não sei o que dizes. E logo, estando ele ainda a falar, cantou o galo.', 60, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, virando-se o Senhor, olhou para Pedro, e Pedro lembrou-se da palavra do Senhor, como lhe havia dito: Antes que o galo cante hoje, me negarás três vezes.', 61, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo Pedro para fora, chorou amargamente.', 62, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os homens que detinham Jesus zombavam dele, ferindo-o.', 63, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendando-lhe os olhos, feriam-no no rosto, e perguntavam-lhe, dizendo: Profetiza, quem é que te feriu?', 64, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outras muitas coisas diziam contra ele, blasfemando.', 65, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo que foi dia ajuntaram-se os anciãos do povo, e os principais dos sacerdotes e os escribas, e o conduziram ao seu concílio, e lhe perguntaram:', 66, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('És tu o Cristo? Dize-no-lo. Ele replicou: Se vo-lo disser, não o crereis;', 67, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também, se vos perguntar, não me respondereis, nem me soltareis.', 68, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde agora o Filho do homem se assentará à direita do poder de Deus.', 69, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disseram todos: Logo, és tu o Filho de Deus? E ele lhes disse: Vós dizeis que eu sou.', 70, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então disseram: De que mais testemunho necessitamos? pois nós mesmos o ouvimos da sua boca.', 71, 22, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, servo de Deus, e apóstolo de Jesus Cristo, segundo a fé dos eleitos de Deus, e o conhecimento da verdade, que é segundo a piedade,', 1, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em esperança da vida eterna, a qual Deus, que não pode mentir, prometeu antes dos tempos dos séculos;', 2, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a seu tempo manifestou a sua palavra pela pregação que me foi confiada segundo o mandamento de Deus, nosso Salvador;', 3, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A Tito, meu verdadeiro filho, segundo a fé comum: Graça, misericórdia, e paz da parte de Deus Pai, e da do Senhor Jesus Cristo, nosso Salvador.', 4, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por esta causa te deixei em Creta, para que pusesses em boa ordem as coisas que ainda restam, e de cidade em cidade estabelecesses presbíteros, como já te mandei:', 5, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que for irrepreensível, marido de uma mulher, que tenha filhos fiéis, que não possam ser acusados de dissolução nem são desobedientes.', 6, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque convém que o bispo seja irrepreensível, como despenseiro da casa de Deus, não soberbo, nem iracundo, nem dado ao vinho, nem espancador, nem cobiçoso de torpe ganância;', 7, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas dado à hospitalidade, amigo do bem, moderado, justo, santo, temperante;', 8, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Retendo firme a fiel palavra, que é conforme a doutrina, para que seja poderoso, tanto para admoestar com a sã doutrina, como para convencer os contradizentes.', 9, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque há muitos desordenados, faladores, vãos e enganadores, principalmente os da circuncisão,', 10, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aos quais convém tapar a boca; homens que transtornam casas inteiras ensinando o que não convém, por torpe ganância.', 11, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Um deles, seu próprio profeta, disse: Os cretenses são sempre mentirosos, bestas ruins, ventres preguiçosos.', 12, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este testemunho é verdadeiro. Portanto, repreende-os severamente, para que sejam sãos na fé.', 13, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não dando ouvidos às fábulas judaicas, nem aos mandamentos de homens que se desviam da verdade.', 14, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todas as coisas são puras para os puros, mas nada é puro para os contaminados e infiéis; antes o seu entendimento e consciência estão contaminados.', 15, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Confessam que conhecem a Deus, mas negam-no com as obras, sendo abomináveis, e desobedientes, e reprovados para toda a boa obra.', 16, 1, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Simão Pedro, servo e apóstolo de Jesus Cristo, aos que conosco alcançaram fé igualmente preciosa pela justiça do nosso Deus e Salvador Jesus Cristo:', 1, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graça e paz vos sejam multiplicadas, pelo conhecimento de Deus, e de Jesus nosso Senhor;', 2, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto como o seu divino poder nos deu tudo o que diz respeito à vida e piedade, pelo conhecimento daquele que nos chamou pela sua glória e virtude;', 3, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelas quais ele nos tem dado grandíssimas e preciosas promessas, para que por elas fiqueis participantes da natureza divina, havendo escapado da corrupção, que pela concupiscência há no mundo.', 4, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós também, pondo nisto mesmo toda a diligência, acrescentai à vossa fé a virtude, e à virtude a ciência,', 5, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E à ciência a temperança, e à temperança a paciência, e à paciência a piedade,', 6, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E à piedade o amor fraternal, e ao amor fraternal a caridade.', 7, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se em vós houver e abundarem estas coisas, não vos deixarão ociosos nem estéreis no conhecimento de nosso Senhor Jesus Cristo.', 8, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois aquele em quem não há estas coisas é cego, nada vendo ao longe, havendo-se esquecido da purificação dos seus antigos pecados.', 9, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, irmãos, procurai fazer cada vez mais firme a vossa vocação e eleição; porque, fazendo isto, nunca jamais tropeçareis.', 10, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque assim vos será amplamente concedida a entrada no reino eterno de nosso Senhor e Salvador Jesus Cristo.', 11, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso não deixarei de exortar-vos sempre acerca destas coisas, ainda que bem as saibais, e estejais confirmados na presente verdade.', 12, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tenho por justo, enquanto estiver neste tabernáculo, despertar-vos com admoestações,', 13, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo que brevemente hei de deixar este meu tabernáculo, como também nosso Senhor Jesus Cristo já mo tem revelado.', 14, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas também eu procurarei em toda a ocasião que depois da minha morte tenhais lembrança destas coisas.', 15, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não vos fizemos saber a virtude e a vinda de nosso Senhor Jesus Cristo, seguindo fábulas artificialmente compostas; mas nós mesmos vimos a sua majestade.', 16, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto ele recebeu de Deus Pai honra e glória, quando da magnífica glória lhe foi dirigida a seguinte voz: Este é o meu Filho amado, em quem me tenho comprazido.', 17, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvimos esta voz dirigida do céu, estando nós com ele no monte santo;', 18, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E temos, mui firme, a palavra dos profetas, à qual bem fazeis em estar atentos, como a uma luz que alumia em lugar escuro, até que o dia amanheça, e a estrela da alva apareça em vossos corações.', 19, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo primeiramente isto: que nenhuma profecia da Escritura é de particular interpretação.', 20, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a profecia nunca foi produzida por vontade de homem algum, mas os homens santos de Deus falaram inspirados pelo Espírito Santo.', 21, 1, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, se já ressuscitastes com Cristo, buscai as coisas que são de cima, onde Cristo está assentado à destra de Deus.', 1, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pensai nas coisas que são de cima, e não nas que são da terra;', 2, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já estais mortos, e a vossa vida está escondida com Cristo em Deus.', 3, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando Cristo, que é a nossa vida, se manifestar, então também vós vos manifestareis com ele em glória.', 4, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mortificai, pois, os vossos membros, que estão sobre a terra: a fornicação, a impureza, o afeição desordenada, a vil concupiscência, e a avareza, que é idolatria;', 5, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelas quais coisas vem a ira de Deus sobre os filhos da desobediência;', 6, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nas quais, também, em outro tempo andastes, quando vivíeis nelas.', 7, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora, despojai-vos também de tudo: da ira, da cólera, da malícia, da maledicência, das palavras torpes da vossa boca.', 8, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não mintais uns aos outros, pois que já vos despistes do velho homem com os seus feitos,', 9, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vos vestistes do novo, que se renova para o conhecimento, segundo a imagem daquele que o criou;', 10, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde não há grego, nem judeu, circuncisão, nem incircuncisão, bárbaro, cita, servo ou livre; mas Cristo é tudo, e em todos.', 11, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Revesti-vos, pois, como eleitos de Deus, santos e amados, de entranhas de misericórdia, de benignidade, humildade, mansidão, longanimidade;', 12, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Suportando-vos uns aos outros, e perdoando-vos uns aos outros, se alguém tiver queixa contra outro; assim como Cristo vos perdoou, assim fazei vós também.', 13, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sobre tudo isto, revesti-vos de amor, que é o vínculo da perfeição.', 14, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a paz de Deus, para a qual também fostes chamados em um corpo, domine em vossos corações; e sede agradecidos.', 15, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A palavra de Cristo habite em vós abundantemente, em toda a sabedoria, ensinando-vos e admoestando-vos uns aos outros, com salmos, hinos e cânticos espirituais, cantando ao Senhor com graça em vosso coração.', 16, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quanto fizerdes por palavras ou por obras, fazei tudo em nome do Senhor Jesus, dando por ele graças a Deus Pai.', 17, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, mulheres, estai sujeitas a vossos próprios maridos, como convém no Senhor.', 18, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, maridos, amai a vossas mulheres, e não vos irriteis contra elas.', 19, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, filhos, obedecei em tudo a vossos pais, porque isto é agradável ao Senhor.', 20, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, pais, não irriteis a vossos filhos, para que não percam o ânimo.', 21, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, servos, obedecei em tudo a vossos senhores segundo a carne, não servindo só na aparência, como para agradar aos homens, mas em simplicidade de coração, temendo a Deus.', 22, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tudo quanto fizerdes, fazei-o de todo o coração, como ao Senhor, e não aos homens,', 23, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo que recebereis do Senhor o galardão da herança, porque a Cristo, o Senhor, servis.', 24, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas quem fizer agravo receberá o agravo que fizer; pois não há acepção de pessoas.', 25, 3, 12);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sede, pois, imitadores de Deus, como filhos amados;', 1, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E andai em amor, como também Cristo vos amou, e se entregou a si mesmo por nós, em oferta e sacrifício a Deus, em cheiro suave.', 2, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a fornicação, e toda a impureza ou avareza, nem ainda se nomeie entre vós, como convém a santos;', 3, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem torpezas, nem parvoíces, nem chocarrices, que não convêm; mas antes, ações de graças.', 4, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque bem sabeis isto: que nenhum devasso, ou impuro, ou avarento, o qual é idólatra, tem herança no reino de Cristo e de Deus.', 5, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém vos engane com palavras vãs; porque por estas coisas vem a ira de Deus sobre os filhos da desobediência.', 6, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, não sejais seus companheiros.', 7, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque noutro tempo éreis trevas, mas agora sois luz no Senhor; andai como filhos da luz', 8, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Porque o fruto do Espírito está em toda a bondade, e justiça e verdade);', 9, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aprovando o que é agradável ao Senhor.', 10, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não comuniqueis com as obras infrutuosas das trevas, mas antes condenai-as.', 11, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o que eles fazem em oculto até dizê-lo é torpe.', 12, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas todas estas coisas se manifestam, sendo condenadas pela luz, porque a luz tudo manifesta.', 13, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso diz: Desperta, tu que dormes, e levanta-te dentre os mortos, e Cristo te esclarecerá.', 14, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, vede prudentemente como andais, não como néscios, mas como sábios,', 15, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Remindo o tempo; porquanto os dias são maus.', 16, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso não sejais insensatos, mas entendei qual seja a vontade do Senhor.', 17, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não vos embriagueis com vinho, em que há contenda, mas enchei-vos do Espírito;', 18, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Falando entre vós em salmos, e hinos, e cânticos espirituais; cantando e salmodiando ao Senhor no vosso coração;', 19, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dando sempre graças por tudo a nosso Deus e Pai, em nome de nosso Senhor Jesus Cristo;', 20, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sujeitando-vos uns aos outros no temor de Deus.', 21, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, mulheres, sujeitai-vos a vossos maridos, como ao Senhor;', 22, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o marido é a cabeça da mulher, como também Cristo é a cabeça da igreja, sendo ele próprio o salvador do corpo.', 23, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que, assim como a igreja está sujeita a Cristo, assim também as mulheres sejam em tudo sujeitas a seus maridos.', 24, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, maridos, amai vossas mulheres, como também Cristo amou a igreja, e a si mesmo se entregou por ela,', 25, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para a santificar, purificando-a com a lavagem da água, pela palavra,', 26, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para a apresentar a si mesmo igreja gloriosa, sem mácula, nem ruga, nem coisa semelhante, mas santa e irrepreensível.', 27, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim devem os maridos amar as suas próprias mulheres, como a seus próprios corpos. Quem ama a sua mulher, ama-se a si mesmo.', 28, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nunca ninguém odiou a sua própria carne; antes a alimenta e sustenta, como também o Senhor à igreja;', 29, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque somos membros do seu corpo, da sua carne, e dos seus ossos.', 30, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso deixará o homem seu pai e sua mãe, e se unirá a sua mulher; e serão dois numa carne.', 31, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Grande é este mistério; digo-o, porém, a respeito de Cristo e da igreja.', 32, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também vós, cada um em particular, ame a sua própria mulher como a si mesmo, e a mulher reverencie o marido.', 33, 5, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Judas, servo de Jesus Cristo, e irmão de Tiago, aos chamados, santificados em Deus Pai, e conservados por Jesus Cristo:', 1, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Misericórdia, e paz, e amor vos sejam multiplicados.', 2, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amados, procurando eu escrever-vos com toda a diligência acerca da salvação comum, tive por necessidade escrever-vos, e exortar-vos a batalhar pela fé que uma vez foi dada aos santos.', 3, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque se introduziram alguns, que já antes estavam escritos para este mesmo juízo, homens ímpios, que convertem em dissolução a graça de Deus, e negam a Deus, único dominador e Senhor nosso, Jesus Cristo.', 4, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas quero lembrar-vos, como a quem já uma vez soube isto, que, havendo o Senhor salvo um povo, tirando-o da terra do Egito, destruiu depois os que não creram;', 5, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aos anjos que não guardaram o seu principado, mas deixaram a sua própria habitação, reservou na escuridão e em prisões eternas até ao juízo daquele grande dia;', 6, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim como Sodoma e Gomorra, e as cidades circunvizinhas, que, havendo-se entregue à fornicação como aqueles, e ido após outra carne, foram postas por exemplo, sofrendo a pena do fogo eterno.', 7, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, contudo, também estes, semelhantemente adormecidos, contaminam a sua carne, e rejeitam a dominação, e vituperam as dignidades.', 8, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o arcanjo Miguel, quando contendia com o diabo, e disputava a respeito do corpo de Moisés, não ousou pronunciar juízo de maldição contra ele; mas disse: O Senhor te repreenda.', 9, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes, porém, dizem mal do que não sabem; e, naquilo que naturalmente conhecem, como animais irracionais se corrompem.', 10, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ai deles! porque entraram pelo caminho de Caim, e foram levados pelo engano do prêmio de Balaão, e pereceram na contradição de Coré.', 11, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes são manchas em vossas festas de amor, banqueteando-se convosco, e apascentando-se a si mesmos sem temor; são nuvens sem água, levadas pelos ventos de uma para outra parte; são como árvores murchas, infrutíferas, duas vezes mortas, desarraigadas;', 12, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ondas impetuosas do mar, que escumam as suas mesmas abominações; estrelas errantes, para os quais está eternamente reservada a negrura das trevas.', 13, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E destes profetizou também Enoque, o sétimo depois de Adão, dizendo: Eis que é vindo o Senhor com milhares de seus santos;', 14, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para fazer juízo contra todos e condenar dentre eles todos os ímpios, por todas as suas obras de impiedade, que impiamente cometeram, e por todas as duras palavras que ímpios pecadores disseram contra ele.', 15, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes são murmuradores, queixosos da sua sorte, andando segundo as suas concupiscências, e cuja boca diz coisas mui arrogantes, admirando as pessoas por causa do interesse.', 16, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós, amados, lembrai-vos das palavras que vos foram preditas pelos apóstolos de nosso Senhor Jesus Cristo;', 17, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais vos diziam que nos últimos tempos haveria escarnecedores que andariam segundo as suas ímpias concupiscências.', 18, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes são os que a si mesmos se separam, sensuais, que não têm o Espírito.', 19, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós, amados, edificando-vos a vós mesmos sobre a vossa santíssima fé, orando no Espírito Santo,', 20, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conservai-vos a vós mesmos no amor de Deus, esperando a misericórdia de nosso Senhor Jesus Cristo para a vida eterna.', 21, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E apiedai-vos de alguns, usando de discernimento;', 22, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E salvai alguns com temor, arrebatando-os do fogo, odiando até a túnica manchada da carne.', 23, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, àquele que é poderoso para vos guardar de tropeçar, e apresentar-vos irrepreensíveis, com alegria, perante a sua glória,', 24, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao único Deus sábio, Salvador nosso, seja glória e majestade, domínio e poder, agora, e para todo o sempre. Amém.', 25, 1, 26);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, apóstolo de Jesus Cristo, pela vontade de Deus, aos santos que estão em Éfeso, e fiéis em Cristo Jesus:', 1, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A vós graça, e paz da parte de Deus nosso Pai e do Senhor Jesus Cristo!', 2, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bendito o Deus e Pai de nosso Senhor Jesus Cristo, o qual nos abençoou com todas as bênçãos espirituais nos lugares celestiais em Cristo;', 3, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como também nos elegeu nele antes da fundação do mundo, para que fôssemos santos e irrepreensíveis diante dele em amor;', 4, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nos predestinou para filhos de adoção por Jesus Cristo, para si mesmo, segundo o beneplácito de sua vontade,', 5, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para louvor da glória de sua graça, pela qual nos fez agradáveis a si no Amado,', 6, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em quem temos a redenção pelo seu sangue, a remissão das ofensas, segundo as riquezas da sua graça,', 7, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que ele fez abundar para conosco em toda a sabedoria e prudência;', 8, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Descobrindo-nos o mistério da sua vontade, segundo o seu beneplácito, que propusera em si mesmo,', 9, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De tornar a congregar em Cristo todas as coisas, na dispensação da plenitude dos tempos, tanto as que estão nos céus como as que estão na terra;', 10, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nele, digo, em quem também fomos feitos herança, havendo sido predestinados, conforme o propósito daquele que faz todas as coisas, segundo o conselho da sua vontade;', 11, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Com o fim de sermos para louvor da sua glória, nós os que primeiro esperamos em Cristo;', 12, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em quem também vós estais, depois que ouvistes a palavra da verdade, o evangelho da vossa salvação; e, tendo nele também crido, fostes selados com o Espírito Santo da promessa;', 13, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual é o penhor da nossa herança, para redenção da possessão adquirida, para louvor da sua glória.', 14, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, ouvindo eu também a fé que entre vós há no Senhor Jesus, e o vosso amor para com todos os santos,', 15, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não cesso de dar graças a Deus por vós, lembrando-me de vós nas minhas orações:', 16, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que o Deus de nosso Senhor Jesus Cristo, o Pai da glória, vos dê em seu conhecimento o espírito de sabedoria e de revelação;', 17, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo iluminados os olhos do vosso entendimento, para que saibais qual seja a esperança da sua vocação, e quais as riquezas da glória da sua herança nos santos;', 18, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qual a sobreexcelente grandeza do seu poder sobre nós, os que cremos, segundo a operação da força do seu poder,', 19, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que manifestou em Cristo, ressuscitando-o dentre os mortos, e pondo-o à sua direita nos céus,', 20, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Acima de todo o principado, e poder, e potestade, e domínio, e de todo o nome que se nomeia, não só neste século, mas também no vindouro;', 21, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sujeitou todas as coisas a seus pés, e sobre todas as coisas o constituiu como cabeça da igreja,', 22, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que é o seu corpo, a plenitude daquele que cumpre tudo em todos.', 23, 1, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Rogo-vos, pois, eu, o preso do Senhor, que andeis como é digno da vocação com que fostes chamados,', 1, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Com toda a humildade e mansidão, com longanimidade, suportando-vos uns aos outros em amor,', 2, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Procurando guardar a unidade do Espírito pelo vínculo da paz.', 3, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Há um só corpo e um só Espírito, como também fostes chamados em uma só esperança da vossa vocação;', 4, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Um só Senhor, uma só fé, um só batismo;', 5, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Um só Deus e Pai de todos, o qual é sobre todos, e por todos e em todos vós.', 6, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a graça foi dada a cada um de nós segundo a medida do dom de Cristo.', 7, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso diz: Subindo ao alto, levou cativo o cativeiro,e deu dons aos homens.', 8, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, isto - ele subiu - que é, senão que também antes tinha descido às partes mais baixas da terra?', 9, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que desceu é também o mesmo que subiu acima de todos os céus, para cumprir todas as coisas.', 10, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele mesmo deu uns para apóstolos, e outros para profetas, e outros para evangelistas, e outros para pastores e doutores,', 11, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Querendo o aperfeiçoamento dos santos, para a obra do ministério, para edificação do corpo de Cristo;', 12, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Até que todos cheguemos à unidade da fé, e ao conhecimento do Filho de Deus, a homem perfeito, à medida da estatura completa de Cristo,', 13, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que não sejamos mais meninos inconstantes, levados em roda por todo o vento de doutrina, pelo engano dos homens que com astúcia enganam fraudulosamente.', 14, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, seguindo a verdade em amor, cresçamos em tudo naquele que é a cabeça, Cristo,', 15, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Do qual todo o corpo, bem ajustado, e ligado pelo auxílio de todas as juntas, segundo a justa operação de cada parte, faz o aumento do corpo, para sua edificação em amor.', 16, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E digo isto, e testifico no Senhor, para que não andeis mais como andam também os outros gentios, na vaidade da sua mente.', 17, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entenebrecidos no entendimento, separados da vida de Deus pela ignorância que há neles, pela dureza do seu coração;', 18, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais, havendo perdido todo o sentimento, se entregaram à dissolução, para com avidez cometerem toda a impureza.', 19, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós não aprendestes assim a Cristo,', 20, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se é que o tendes ouvido, e nele fostes ensinados, como está a verdade em Jesus;', 21, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que, quanto ao trato passado, vos despojeis do velho homem, que se corrompe pelas concupiscências do engano;', 22, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vos renoveis no espírito da vossa mente;', 23, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vos revistais do novo homem, que segundo Deus é criado em verdadeira justiça e santidade.', 24, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso deixai a mentira, e falai a verdade cada um com o seu próximo; porque somos membros uns dos outros.', 25, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irai-vos, e não pequeis; não se ponha o sol sobre a vossa ira.', 26, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não deis lugar ao diabo.', 27, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que furtava, não furte mais; antes trabalhe, fazendo com as mãos o que é bom, para que tenha o que repartir com o que tiver necessidade.', 28, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não saia da vossa boca nenhuma palavra torpe, mas só a que for boa para promover a edificação, para que dê graça aos que a ouvem.', 29, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não entristeçais o Espírito Santo de Deus, no qual estais selados para o dia da redenção.', 30, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Toda a amargura, e ira, e cólera, e gritaria, e blasfêmia e toda a malícia sejam tiradas dentre vós,', 31, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes sede uns para com os outros benignos, misericordiosos, perdoando-vos uns aos outros, como também Deus vos perdoou em Cristo.', 32, 4, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura começamos outra vez a louvar-nos a nós mesmos? Ou necessitamos, como alguns, de cartas de recomendação para vós, ou de recomendação de vós?', 1, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós sois a nossa carta, escrita em nossos corações, conhecida e lida por todos os homens.', 2, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já é manifesto que vós sois a carta de Cristo, ministrada por nós, e escrita, não com tinta, mas com o Espírito do Deus vivo, não em tábuas de pedra, mas nas tábuas de carne do coração.', 3, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E é por Cristo que temos tal confiança em Deus;', 4, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não que sejamos capazes, por nós, de pensar alguma coisa, como de nós mesmos; mas a nossa capacidade vem de Deus,', 5, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual nos fez também capazes de ser ministros de um novo testamento, não da letra, mas do espírito; porque a letra mata e o espírito vivifica.', 6, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se o ministério da morte, gravado com letras em pedras, veio em glória, de maneira que os filhos de Israel não podiam fitar os olhos na face de Moisés, por causa da glória do seu rosto, a qual era transitória,', 7, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como não será de maior glória o ministério do Espírito?', 8, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se o ministério da condenação foi glorioso, muito mais excederá em glória o ministério da justiça.', 9, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também o que foi glorificado nesta parte não foi glorificado, por causa desta excelente glória.', 10, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se o que era transitório foi para glória, muito mais é em glória o que permanece.', 11, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo, pois, tal esperança, usamos de muita ousadia no falar.', 12, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não somos como Moisés, que punha um véu sobre a sua face, para que os filhos de Israel não olhassem firmemente para o fim daquilo que era transitório.', 13, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os seus sentidos foram endurecidos; porque até hoje o mesmo véu está por levantar na lição do velho testamento, o qual foi por Cristo abolido;', 14, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E até hoje, quando é lido Moisés, o véu está posto sobre o coração deles.', 15, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando se converterem ao Senhor, então o véu se tirará.', 16, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o Senhor é o Espírito; e onde está o Espírito do Senhor, aí há liberdade.', 17, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas todos nós, com rosto descoberto, refletindo como um espelho a glória do Senhor, somos transformados de glória em glória na mesma imagem, como pelo Espírito do Senhor.', 18, 3, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quisera eu me suportásseis um pouco na minha loucura! Suportai-me, porém, ainda.', 1, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque estou zeloso de vós com zelo de Deus; porque vos tenho preparado para vos apresentar como uma virgem pura a um marido, a saber, a Cristo.', 2, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas temo que, assim como a serpente enganou Eva com a sua astúcia, assim também sejam de alguma sorte corrompidos os vossos sentidos, e se apartem da simplicidade que há em Cristo.', 3, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se alguém for pregar-vos outro Jesus que nós não temos pregado, ou se recebeis outro espírito que não recebestes, ou outro evangelho que não abraçastes, com razão o sofreríeis.', 4, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque penso que em nada fui inferior aos mais excelentes apóstolos.', 5, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se sou rude na palavra, não o sou contudo na ciência; mas já em todas as coisas nos temos feito conhecer totalmente entre vós.', 6, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pequei, porventura, humilhando-me a mim mesmo, para que vós fôsseis exaltados, porque de graça vos anunciei o evangelho de Deus?', 7, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Outras igrejas despojei eu para vos servir, recebendo delas salário; e quando estava presente convosco, e tinha necessidade, a ninguém fui pesado.', 8, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os irmãos que vieram da macedônia supriram a minha necessidade; e em tudo me guardei de vos ser pesado, e ainda me guardarei.', 9, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como a verdade de Cristo está em mim, esta glória não me será impedida nas regiões da Acaia.', 10, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por quê? Porque não vos amo? Deus o sabe.', 11, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que eu faço o farei, para cortar ocasião aos que buscam ocasião, a fim de que, naquilo em que se gloriam, sejam achados assim como nós.', 12, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tais falsos apóstolos são obreiros fraudulentos, transfigurando-se em apóstolos de Cristo.', 13, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não é maravilha, porque o próprio Satanás se transfigura em anjo de luz.', 14, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não é muito, pois, que os seus ministros se transfigurem em ministros da justiça; o fim dos quais será conforme as suas obras.', 15, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Outra vez digo: Ninguém me julgue insensato, ou então recebei-me como insensato, para que também me glorie um pouco.', 16, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que digo, não o digo segundo o Senhor, mas como por loucura, nesta confiança de gloriar-me.', 17, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois que muitos se gloriam segundo a carne, eu também me gloriarei.', 18, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, sendo vós sensatos, de boa mente tolerais os insensatos.', 19, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois sois sofredores, se alguém vos põe em servidão, se alguém vos devora, se alguém vos apanha, se alguém se exalta, se alguém vos fere no rosto.', 20, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Envergonhado o digo, como se nós fôssemos fracos, mas no que qualquer tem ousadia (com insensatez falo) também eu tenho ousadia.', 21, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('São hebreus? também eu. São israelitas? também eu. São descendência de Abraão? também eu.', 22, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('São ministros de Cristo? (falo como fora de mim) eu ainda mais: em trabalhos, muito mais; em açoites, mais do que eles; em prisões, muito mais; em perigo de morte, muitas vezes.', 23, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Recebi dos judeus cinco quarentenas de açoites menos um.', 24, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Três vezes fui açoitado com varas, uma vez fui apedrejado, três vezes sofri naufrágio, uma noite e um dia passei no abismo;', 25, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em viagens muitas vezes, em perigos de rios, em perigos de salteadores, em perigos dos da minha nação, em perigos dos gentios, em perigos na cidade, em perigos no deserto, em perigos no mar, em perigos entre os falsos irmãos;', 26, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em trabalhos e fadiga, em vigílias muitas vezes, em fome e sede, em jejum muitas vezes, em frio e nudez.', 27, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Além das coisas exteriores, me oprime cada dia o cuidado de todas as igrejas.', 28, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem enfraquece, que eu também não enfraqueça? Quem se escandaliza, que eu me não abrase?', 29, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se convém gloriar-me, gloriar-me-ei no que diz respeito à minha fraqueza.', 30, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Deus e Pai de nosso Senhor Jesus Cristo, que é eternamente bendito, sabe que não minto.', 31, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em Damasco, o que governava sob o rei Aretas pôs guardas às portas da cidade dos damascenos, para me prenderem.', 32, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E fui descido num cesto por uma janela da muralha; e assim escapei das suas mãos.', 33, 11, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vos vivificou, estando vós mortos em ofensas e pecados,', 1, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em que noutro tempo andastes segundo o curso deste mundo, segundo o príncipe das potestades do ar, do espírito que agora opera nos filhos da desobediência;', 2, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entre os quais todos nós também antes andávamos nos desejos da nossa carne, fazendo a vontade da carne e dos pensamentos; e éramos por natureza filhos da ira, como os outros também.', 3, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus, que é riquíssimo em misericórdia, pelo seu muito amor com que nos amou,', 4, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estando nós ainda mortos em nossas ofensas, nos vivificou juntamente com Cristo (pela graça sois salvos),', 5, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nos ressuscitou juntamente com ele e nos fez assentar nos lugares celestiais, em Cristo Jesus;', 6, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para mostrar nos séculos vindouros as abundantes riquezas da sua graça pela sua benignidade para conosco em Cristo Jesus.', 7, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque pela graça sois salvos, por meio da fé; e isto não vem de vós, é dom de Deus.', 8, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vem das obras, para que ninguém se glorie;', 9, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque somos feitura sua, criados em Cristo Jesus para as boas obras, as quais Deus preparou para que andássemos nelas.', 10, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, lembrai-vos de que vós noutro tempo éreis gentios na carne, e chamados incircuncisão pelos que na carne se chamam circuncisão feita pela mão dos homens;', 11, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que naquele tempo estáveis sem Cristo, separados da comunidade de Israel, e estranhos às alianças da promessa, não tendo esperança, e sem Deus no mundo.', 12, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora em Cristo Jesus, vós, que antes estáveis longe, já pelo sangue de Cristo chegastes perto.', 13, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ele é a nossa paz, o qual de ambos os povos fez um; e, derrubando a parede de separação que estava no meio,', 14, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na sua carne desfez a inimizade, isto é, a lei dos mandamentos, que consistia em ordenanças, para criar em si mesmo dos dois um novo homem, fazendo a paz,', 15, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pela cruz reconciliar ambos com Deus em um corpo, matando com ela as inimizades.', 16, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vindo, ele evangelizou a paz, a vós que estáveis longe, e aos que estavam perto;', 17, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque por ele ambos temos acesso ao Pai em um mesmo Espírito.', 18, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim que já não sois estrangeiros, nem forasteiros, mas concidadãos dos santos, e da família de Deus;', 19, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Edificados sobre o fundamento dos apóstolos e dos profetas, de que Jesus Cristo é a principal pedra da esquina;', 20, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No qual todo o edifício, bem ajustado, cresce para templo santo no Senhor.', 21, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No qual também vós juntamente sois edificados para morada de Deus em Espírito.', 22, 2, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, filhos, sede obedientes a vossos pais no Senhor, porque isto é justo.', 1, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Honra a teu pai e a tua mãe, que é o primeiro mandamento com promessa;', 2, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que te vá bem, e vivas muito tempo sobre a terra.', 3, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós, pais, não provoqueis à ira a vossos filhos, mas criai-os na doutrina e admoestação do Senhor.', 4, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, servos, obedecei a vossos senhores segundo a carne, com temor e tremor, na sinceridade de vosso coração, como a Cristo;', 5, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não servindo à vista, como para agradar aos homens, mas como servos de Cristo, fazendo de coração a vontade de Deus;', 6, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Servindo de boa vontade como ao Senhor, e não como aos homens.', 7, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo que cada um receberá do Senhor todo o bem que fizer, seja servo, seja livre.', 8, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós, senhores, fazei o mesmo para com eles, deixando as ameaças, sabendo também que o Senhor deles e vosso está no céu, e que para com ele não há acepção de pessoas.', 9, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No demais, irmãos meus, fortalecei-vos no Senhor e na força do seu poder.', 10, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Revesti-vos de toda a armadura de Deus, para que possais estar firmes contra as astutas ciladas do diabo.', 11, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não temos que lutar contra a carne e o sangue, mas, sim, contra os principados, contra as potestades, contra os príncipes das trevas deste século, contra as hostes espirituais da maldade, nos lugares celestiais.', 12, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, tomai toda a armadura de Deus, para que possais resistir no dia mau e, havendo feito tudo, ficar firmes.', 13, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estai, pois, firmes, tendo cingidos os vossos lombos com a verdade, e vestida a couraça da justiça;', 14, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E calçados os pés na preparação do evangelho da paz;', 15, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tomando sobretudo o escudo da fé, com o qual podereis apagar todos os dardos inflamados do maligno.', 16, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tomai também o capacete da salvação, e a espada do Espírito, que é a palavra de Deus;', 17, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Orando em todo o tempo com toda a oração e súplica no Espírito, e vigiando nisto com toda a perseverança e súplica por todos os santos,', 18, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por mim; para que me seja dada, no abrir da minha boca, a palavra com confiança, para fazer notório o mistério do evangelho,', 19, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelo qual sou embaixador em cadeias; para que possa falar dele livremente, como me convém falar.', 20, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, para que vós também possais saber dos meus negócios, e o que eu faço, Tíquico, irmão amado, e fiel ministro do Senhor, vos informará de tudo.', 21, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual vos enviei para o mesmo fim, para que saibais do nosso estado, e ele console os vossos corações.', 22, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paz seja com os irmãos, e amor com fé da parte de Deus Pai e da do Senhor Jesus Cristo.', 23, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça seja com todos os que amam a nosso Senhor Jesus Cristo em sinceridade. Amém.', 24, 6, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No demais, irmãos, rogai por nós, para que a palavra do Senhor tenha livre curso e seja glorificada, como também o é entre vós;', 1, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E para que sejamos livres de homens dissolutos e maus; porque a fé não é de todos.', 2, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas fiel é o Senhor, que vos confirmará, e guardará do maligno.', 3, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E confiamos quanto a vós no Senhor, que não só fazeis como fareis o que vos mandamos.', 4, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora o Senhor encaminhe os vossos corações no amor de Deus, e na paciência de Cristo.', 5, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mandamo-vos, porém, irmãos, em nome de nosso Senhor Jesus Cristo, que vos aparteis de todo o irmão que anda desordenadamente, e não segundo a tradição que de nós recebeu.', 6, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque vós mesmos sabeis como convém imitar-nos, pois que não nos houvemos desordenadamente entre vós,', 7, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem de graça comemos o pão de homem algum, mas com trabalho e fadiga, trabalhando noite e dia, para não sermos pesados a nenhum de vós.', 8, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não porque não tivéssemos autoridade, mas para vos dar em nós mesmos exemplo, para nos imitardes.', 9, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, quando ainda estávamos convosco, vos mandamos isto, que, se alguém não quiser trabalhar, não coma também.', 10, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto ouvimos que alguns entre vós andam desordenadamente, não trabalhando, antes fazendo coisas vãs.', 11, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A esses tais, porém, mandamos, e exortamos por nosso Senhor Jesus Cristo, que, trabalhando com sossego, comam o seu próprio pão.', 12, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós, irmãos, não vos canseis de fazer o bem.', 13, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se alguém não obedecer à nossa palavra por esta carta, notai o tal, e não vos mistureis com ele, para que se envergonhe.', 14, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia não o tenhais como inimigo, mas admoestai-o como irmão.', 15, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o mesmo Senhor da paz vos dê sempre paz de toda a maneira.O Senhor seja com todos vós.', 16, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudação da minha própria mão, de mim, Paulo, que é o sinal em todas as epístolas; assim escrevo.', 17, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça de nosso Senhor Jesus Cristo seja com todos vós. Amém.', 18, 3, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por esta causa eu, Paulo, sou o prisioneiro de Jesus Cristo por vós, os gentios;', 1, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se é que tendes ouvido a dispensação da graça de Deus, que para convosco me foi dada;', 2, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como me foi este mistério manifestado pela revelação, como antes um pouco vos escrevi;', 3, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, quando ledes, podeis perceber a minha compreensão do mistério de Cristo,', 4, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual noutros séculos não foi manifestado aos filhos dos homens, como agora tem sido revelado pelo Espírito aos seus santos apóstolos e profetas;', 5, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A saber, que os gentios são co-herdeiros, e de um mesmo corpo, e participantes da promessa em Cristo pelo evangelho;', 6, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Do qual fui feito ministro, pelo dom da graça de Deus, que me foi dado segundo a operação do seu poder.', 7, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A mim, o mínimo de todos os santos, me foi dada esta graça de anunciar entre os gentios, por meio do evangelho, as riquezas incompreensíveis de Cristo,', 8, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E demonstrar a todos qual seja a comunhão do mistério, que desde os séculos esteve oculto em Deus, que tudo criou por meio de Jesus Cristo;', 9, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que agora, pela igreja, a multiforme sabedoria de Deus seja conhecida dos principados e potestades nos céus,', 10, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segundo o eterno propósito que fez em Cristo Jesus nosso Senhor,', 11, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No qual temos ousadia e acesso com confiança, pela nossa fé nele.', 12, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, vos peço que não desfaleçais nas minhas tribulações por vós, que são a vossa glória.', 13, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por causa disto me ponho de joelhos perante o Pai de nosso Senhor Jesus Cristo,', 14, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Do qual toda a família nos céus e na terra toma o nome,', 15, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que, segundo as riquezas da sua glória, vos conceda que sejais corroborados com poder pelo seu Espírito no homem interior;', 16, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que Cristo habite pela fé nos vossos corações; a fim de, estando arraigados e fundados em amor,', 17, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Poderdes perfeitamente compreender, com todos os santos, qual seja a largura, e o comprimento, e a altura, e a profundidade,', 18, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E conhecer o amor de Cristo, que excede todo o entendimento, para que sejais cheios de toda a plenitude de Deus.', 19, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, àquele que é poderoso para fazer tudo muito mais abundantemente além daquilo que pedimos ou pensamos, segundo o poder que em nós opera,', 20, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A esse glória na igreja, por Jesus Cristo, em todas as gerações, para todo o sempre. Amém.', 21, 3, 10);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Chegavam-se a ele todos os publicanos e pecadores para o ouvir.', 1, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os fariseus e os escribas murmuravam, dizendo: Este recebe pecadores, e come com eles.', 2, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes propôs esta parábola, dizendo:', 3, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que homem dentre vós, tendo cem ovelhas, e perdendo uma delas, não deixa no deserto as noventa e nove, e vai após a perdida até que venha a achá-la?', 4, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E achando-a, a põe sobre os seus ombros, jubiloso;', 5, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando a casa, convoca os amigos e vizinhos, dizendo-lhes: Alegrai-vos comigo, porque já achei a minha ovelha perdida.', 6, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo-vos que assim haverá alegria no céu por um pecador que se arrepende, mais do que por noventa e nove justos que não necessitam de arrependimento.', 7, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou qual a mulher que, tendo dez dracmas, se perder uma dracma, não acende a candeia, e varre a casa, e busca com diligência até a achar?', 8, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E achando-a, convoca as amigas e vizinhas, dizendo: Alegrai-vos comigo, porque já achei a dracma perdida.', 9, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim vos digo que há alegria diante dos anjos de Deus por um pecador que se arrepende.', 10, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse: Um certo homem tinha dois filhos;', 11, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o mais moço deles disse ao pai: Pai, dá-me a parte dos bens que me pertence. E ele repartiu por eles a fazenda.', 12, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, poucos dias depois, o filho mais novo, ajuntando tudo, partiu para uma terra longínqua, e ali desperdiçou os seus bens, vivendo dissolutamente.', 13, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo ele gastado tudo, houve naquela terra uma grande fome, e começou a padecer necessidades.', 14, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi, e chegou-se a um dos cidadãos daquela terra, o qual o mandou para os seus campos, a apascentar porcos.', 15, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desejava encher o seu estômago com as bolotas que os porcos comiam, e ninguém lhe dava nada.', 16, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tornando em si, disse: Quantos jornaleiros de meu pai têm abundância de pão, e eu aqui pereço de fome!', 17, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Levantar-me-ei, e irei ter com meu pai, e dir-lhe-ei: Pai, pequei contra o céu e perante ti;', 18, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Já não sou digno de ser chamado teu filho; faze-me como um dos teus jornaleiros.', 19, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se, foi para seu pai; e, quando ainda estava longe, viu-o seu pai, e se moveu de íntima compaixão e, correndo, lançou-se-lhe ao pescoço e o beijou.', 20, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o filho lhe disse: Pai, pequei contra o céu e perante ti, e já não sou digno de ser chamado teu filho.', 21, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o pai disse aos seus servos: Trazei depressa a melhor roupa; e vesti-lho, e ponde-lhe um anel na mão, e alparcas nos pés;', 22, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E trazei o bezerro cevado, e matai-o; e comamos, e alegremo-nos;', 23, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque este meu filho estava morto, e reviveu, tinha-se perdido, e foi achado. E começaram a alegrar-se.', 24, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o seu filho mais velho estava no campo; e quando veio, e chegou perto de casa, ouviu a música e as danças.', 25, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando um dos servos, perguntou-lhe que era aquilo.', 26, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhe disse: Veio teu irmão; e teu pai matou o bezerro cevado, porque o recebeu são e salvo.', 27, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele se indignou, e não queria entrar.', 28, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E saindo o pai, instava com ele. Mas, respondendo ele, disse ao pai: Eis que te sirvo há tantos anos, sem nunca transgredir o teu mandamento, e nunca me deste um cabrito para alegrar-me com os meus amigos;', 29, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vindo, porém, este teu filho, que desperdiçou os teus bens com as meretrizes, mataste-lhe o bezerro cevado.', 30, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhe disse: Filho, tu sempre estás comigo, e todas as minhas coisas são tuas;', 31, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas era justo alegrarmo-nos e folgarmos, porque este teu irmão estava morto, e reviveu; e tinha-se perdido, e achou-se.', 32, 15, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade que não convém gloriar-me; mas passarei às visões e revelações do Senhor.', 1, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conheço um homem em Cristo que há catorze anos (se no corpo, não sei, se fora do corpo, não sei; Deus o sabe) foi arrebatado ao terceiro céu.', 2, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sei que o tal homem (se no corpo, se fora do corpo, não sei; Deus o sabe)', 3, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Foi arrebatado ao paraíso; e ouviu palavras inefáveis, que ao homem não é lícito falar.', 4, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De alguém assim me gloriarei eu, mas de mim mesmo não me gloriarei, senão nas minhas fraquezas.', 5, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se quiser gloriar-me, não serei néscio, porque direi a verdade; mas deixo isto, para que ninguém cuide de mim mais do que em mim vê ou de mim ouve.', 6, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, para que não me exaltasse pela excelência das revelações, foi-me dado um espinho na carne, a saber, um mensageiro de Satanás para me esbofetear, a fim de não me exaltar.', 7, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Acerca do qual três vezes orei ao Senhor para que se desviasse de mim.', 8, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-me: A minha graça te basta, porque o meu poder se aperfeiçoa na fraqueza. De boa vontade, pois, me gloriarei nas minhas fraquezas, para que em mim habite o poder de Cristo.', 9, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso sinto prazer nas fraquezas, nas injúrias, nas necessidades, nas perseguições, nas angústias por amor de Cristo. Porque quando estou fraco então sou forte.', 10, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fui néscio em gloriar-me; vós me constrangestes. Eu devia ter sido louvado por vós, visto que em nada fui inferior aos mais excelentes apóstolos, ainda que nada sou.', 11, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os sinais do meu apostolado foram manifestados entre vós com toda a paciência, por sinais, prodígios e maravilhas.', 12, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, em que tendes vós sido inferiores às outras igrejas, a não ser que eu mesmo vos não fui pesado? Perdoai-me este agravo.', 13, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis aqui estou pronto para pela terceira vez ir ter convosco, e não vos serei pesado, pois que não busco o que é vosso, mas sim a vós: porque não devem os filhos entesourar para os pais, mas os pais para os filhos.', 14, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu de muito boa vontade gastarei, e me deixarei gastar pelas vossas almas, ainda que, amando-vos cada vez mais, seja menos amado.', 15, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas seja assim; eu não vos fui pesado mas, sendo astuto, vos tomei com dolo.', 16, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura aproveitei-me de vós por algum daqueles que vos enviei?', 17, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Roguei a Tito, e enviei com ele um irmão. Porventura Tito se aproveitou de vós? Não andamos porventura no mesmo espírito, sobre as mesmas pisadas?', 18, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cuidais que ainda nos desculpamos convosco? Falamos em Cristo perante Deus, e tudo isto, ó amados, para vossa edificação.', 19, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque receio que, quando chegar, não vos ache como eu quereria, e eu seja achado de vós como não quereríeis; que de alguma maneira haja pendências, invejas, iras, porfias, detrações, mexericos, orgulhos, tumultos;', 20, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que, quando for outra vez, o meu Deus me humilhe para convosco, e chore por muitos daqueles que dantes pecaram, e não se arrependeram da imundícia, e fornicação, e desonestidade que cometeram.', 21, 12, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois destas coisas vi descer do céu outro anjo, que tinha grande poder, e a terra foi iluminada com a sua glória.', 1, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E clamou fortemente com grande voz, dizendo: Caiu, caiu a grande Babilônia, e se tornou morada de demônios, e coito de todo espírito imundo, e coito de toda ave imunda e odiável.', 2, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todas as nações beberam do vinho da ira da sua fornicação, e os reis da terra fornicaram com ela; e os mercadores da terra se enriqueceram com a abundância de suas delícias.', 3, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi outra voz do céu, que dizia: Sai dela, povo meu, para que não sejas participante dos seus pecados, e para que não incorras nas suas pragas.', 4, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já os seus pecados se acumularam até ao céu, e Deus se lembrou das iniqüidades dela.', 5, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tornai-lhe a dar como ela vos tem dado, e retribuí-lhe em dobro conforme as suas obras; no cálice em que vos deu de beber, dai-lhe a ela em dobro.', 6, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto ela se glorificou, e em delícias esteve, foi-lhe outro tanto de tormento e pranto; porque diz em seu coração: Estou assentada como rainha, e não sou viúva, e não verei o pranto.', 7, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, num dia virão as suas pragas, a morte, e o pranto, e a fome; e será queimada no fogo; porque é forte o Senhor Deus que a julga.', 8, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os reis da terra, que fornicaram com ela, e viveram em delícias, a chorarão, e sobre ela prantearão, quando virem a fumaça do seu incêndio;', 9, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estando de longe pelo temor do seu tormento, dizendo: Ai! ai daquela grande cidade de Babilônia, aquela forte cidade! pois em uma hora veio o seu juízo.', 10, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sobre ela choram e lamentam os mercadores da terra; porque ninguém mais compra as suas mercadorias:', 11, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mercadorias de ouro, e de prata, e de pedras preciosas, e de pérolas, e de linho fino, e de púrpura, e de seda, e de escarlata; e toda a madeira odorífera, e todo o vaso de marfim, e todo o vaso de madeira preciosíssima, de bronze e de ferro, e de mármore;', 12, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E canela, e perfume, e mirra, e incenso, e vinho, e azeite, e flor de farinha, e trigo, e gado, e ovelhas; e cavalos, e carros, e corpos e almas de homens.', 13, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o fruto do desejo da tua alma foi-se de ti; e todas as coisas gostosas e excelentes se foram de ti, e não mais as acharás.', 14, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os mercadores destas coisas, que dela se enriqueceram, estarão de longe, pelo temor do seu tormento, chorando e lamentando,', 15, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizendo: Ai, ai daquela grande cidade! que estava vestida de linho fino, de púrpura, de escarlata; e adornada com ouro e pedras preciosas e pérolas! porque numa hora foram assoladas tantas riquezas.', 16, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todo piloto, e todo o que navega em naus, e todo marinheiro, e todos os que negociam no mar se puseram de longe;', 17, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo a fumaça do seu incêndio, clamaram, dizendo: Que cidade é semelhante a esta grande cidade?', 18, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lançaram pó sobre as suas cabeças, e clamaram, chorando, e lamentando, e dizendo: Ai, ai daquela grande cidade! na qual todos os que tinham naus no mar se enriqueceram em razão da sua opulência; porque numa hora foi assolada.', 19, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Alegra-te sobre ela, ó céu, e vós, santos apóstolos e profetas; porque já Deus julgou a vossa causa quanto a ela.', 20, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um forte anjo levantou uma pedra como uma grande mó, e lançou-a no mar, dizendo: Com igual ímpeto será lançada babilônia, aquela grande cidade, e não será jamais achada.', 21, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E em ti não se ouvirá mais a voz de harpistas, e de músicos, e de flautistas, e de trombeteiros, e nenhum artífice de arte alguma se achará mais em ti; e ruído de mó em ti não se ouvirá mais;', 22, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E luz de candeia não mais luzirá em ti, e voz de esposo e de esposa não mais em ti se ouvirá; porque os teus mercadores eram os grandes da terra; porque todas as nações foram enganadas pelas tuas feitiçarias.', 23, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nela se achou o sangue dos profetas, e dos santos, e de todos os que foram mortos na terra.', 24, 18, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Escreve ao anjo da igreja de Éfeso: Isto diz aquele que tem na sua destra as sete estrelas, que anda no meio dos sete castiçais de ouro:', 1, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conheço as tuas obras, e o teu trabalho, e a tua paciência, e que não podes sofrer os maus; e puseste à prova os que dizem ser apóstolos, e o não são, e tu os achaste mentirosos.', 2, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sofreste, e tens paciência; e trabalhaste pelo meu nome, e não te cansaste.', 3, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho, porém, contra ti que deixaste o teu primeiro amor.', 4, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lembra-te, pois, de onde caíste, e arrepende-te, e pratica as primeiras obras; quando não, brevemente a ti virei, e tirarei do seu lugar o teu castiçal, se não te arrependeres.', 5, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tens, porém, isto: que odeias as obras dos nicolaítas, as quais eu também odeio.', 6, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem tem ouvidos, ouça o que o Espírito diz às igrejas: Ao que vencer, dar-lhe-ei a comer da árvore da vida, que está no meio do paraíso de Deus.', 7, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao anjo da igreja em Esmirna, escreve: Isto diz o primeiro e o último, que foi morto, e reviveu:', 8, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conheço as tuas obras, e tribulação, e pobreza (mas tu és rico), e a blasfêmia dos que se dizem judeus, e não o são, mas são a sinagoga de Satanás.', 9, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nada temas das coisas que hás de padecer. Eis que o diabo lançará alguns de vós na prisão, para que sejais tentados; e tereis uma tribulação de dez dias. Sê fiel até à morte, e dar-te-ei a coroa da vida.', 10, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem tem ouvidos, ouça o que o Espírito diz às igrejas: O que vencer não receberá o dano da segunda morte.', 11, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao anjo da igreja que está em Pérgamo escreve: Isto diz aquele que tem a espada aguda de dois fios:', 12, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conheço as tuas obras, e onde habitas, que é onde está o trono de Satanás; e reténs o meu nome, e não negaste a minha fé, ainda nos dias de Antipas, minha fiel testemunha, o qual foi morto entre vós, onde Satanás habita.', 13, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas algumas poucas coisas tenho contra ti, porque tens lá os que seguem a doutrina de Balaão, o qual ensinava Balaque a lançar tropeços diante dos filhos de Israel, para que comessem dos sacrifícios da idolatria, e fornicassem.', 14, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim tens também os que seguem a doutrina dos nicolaítas, o que eu odeio.', 15, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Arrepende-te, pois, quando não em breve virei a ti, e contra eles batalharei com a espada da minha boca.', 16, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem tem ouvidos, ouça o que o Espírito diz às igrejas: Ao que vencer darei eu a comer do maná escondido, e dar-lhe-ei uma pedra branca, e na pedra um novo nome escrito, o qual ninguém conhece senão aquele que o recebe.', 17, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao anjo da igreja de Tiatira escreve: Isto diz o Filho de Deus, que tem seus olhos como chama de fogo, e os pés semelhantes ao latão reluzente:', 18, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu conheço as tuas obras, e o teu amor, e o teu serviço, e a tua fé, e a tua paciência, e que as tuas últimas obras são mais do que as primeiras.', 19, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas algumas poucas coisas tenho contra ti que deixas Jezabel, mulher que se diz profetisa, ensinar e enganar os meus servos, para que forniquem e comam dos sacrifícios da idolatria.', 20, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dei-lhe tempo para que se arrependesse da sua fornicação; e não se arrependeu.', 21, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que a porei numa cama, e sobre os que adulteram com ela virá grande tribulação, se não se arrependerem das suas obras.', 22, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ferirei de morte a seus filhos, e todas as igrejas saberão que eu sou aquele que sonda os rins e os corações. E darei a cada um de vós segundo as vossas obras.', 23, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eu vos digo a vós, e aos restantes que estão em Tiatira, a todos quantos não têm esta doutrina, e não conheceram, como dizem, as profundezas de Satanás, que outra carga vos não porei.', 24, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que tendes, retende-o até que eu venha.', 25, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao que vencer, e guardar até ao fim as minhas obras, eu lhe darei poder sobre as nações,', 26, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E com vara de ferro as regerá; e serão quebradas como vasos de oleiro; como também recebi de meu Pai.', 27, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dar-lhe-ei a estrela da manhã.', 28, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem tem ouvidos, ouça o que o Espírito diz às igrejas.', 29, 2, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois destas coisas vi quatro anjos que estavam sobre os quatro cantos da terra, retendo os quatro ventos da terra, para que nenhum vento soprasse sobre a terra, nem sobre o mar, nem contra árvore alguma.', 1, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi outro anjo subir do lado do sol nascente, e que tinha o selo do Deus vivo; e clamou com grande voz aos quatro anjos, a quem fora dado o poder de danificar a terra e o mar,', 2, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Não danifiqueis a terra, nem o mar, nem as árvores, até que hajamos selado nas suas testas os servos do nosso Deus.', 3, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi o número dos selados, e eram cento e quarenta e quatro mil selados, de todas as tribos dos filhos de Israel.', 4, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da tribo de Judá, havia doze mil selados; da tribo de Rúbem, doze mil selados; da tribo de Gade, doze mil selados;', 5, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da tribo de Aser, doze mil selados; da tribo de Naftali, doze mil selados; da tribo de Manassés, doze mil selados;', 6, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da tribo de Simeão, doze mil selados; da tribo de Levi, doze mil selados; da tribo de Issacar, doze mil selados;', 7, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da tribo de Zebulom, doze mil selados; da tribo de José, doze mil selados; da tribo de Benjamim, doze mil selados.', 8, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois destas coisas olhei, e eis aqui uma multidão, a qual ninguém podia contar, de todas as nações, e tribos, e povos, e línguas, que estavam diante do trono, e perante o Cordeiro, trajando vestes brancas e com palmas nas suas mãos;', 9, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E clamavam com grande voz, dizendo: Salvação ao nosso Deus, que está assentado no trono, e ao Cordeiro.', 10, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os anjos estavam ao redor do trono, e dos anciãos, e dos quatro animais; e prostraram-se diante do trono sobre seus rostos, e adoraram a Deus,', 11, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Amém. Louvor, e glória, e sabedoria, e ação de graças, e honra, e poder, e força ao nosso Deus, para todo o sempre. Amém.', 12, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um dos anciãos me falou, dizendo: Estes que estão vestidos de vestes brancas, quem são, e de onde vieram?', 13, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu disse-lhe: Senhor, tu sabes. E ele disse-me: Estes são os que vieram da grande tribulação, e lavaram as suas vestes e as branquearam no sangue do Cordeiro.', 14, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso estão diante do trono de Deus, e o servem de dia e de noite no seu templo; e aquele que está assentado sobre o trono os cobrirá com a sua sombra.', 15, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nunca mais terão fome, nunca mais terão sede; nem sol nem calma alguma cairá sobre eles.', 16, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Cordeiro que está no meio do trono os apascentará, e lhes servirá de guia para as fontes vivas das águas; e Deus limpará de seus olhos toda a lágrima.', 17, 7, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, e Silvano, e Timóteo, à igreja dos tessalonicenses, em Deus nosso Pai, e no Senhor Jesus Cristo:', 1, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graça e paz a vós da parte de Deus nosso Pai, e da do Senhor Jesus Cristo.', 2, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sempre devemos, irmãos, dar graças a Deus por vós, como é justo, porque a vossa fé cresce muitíssimo e o amor de cada um de vós aumenta de uns para com os outros,', 3, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que nós mesmos nos gloriamos de vós nas igrejas de Deus por causa da vossa paciência e fé, e em todas as vossas perseguições e aflições que suportais;', 4, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Prova clara do justo juízo de Deus, para que sejais havidos por dignos do reino de Deus, pelo qual também padeceis;', 5, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se de fato é justo diante de Deus que dê em paga tribulação aos que vos atribulam,', 6, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a vós, que sois atribulados, descanso conosco, quando se manifestar o Senhor Jesus desde o céu com os anjos do seu poder,', 7, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Com labareda de fogo, tomando vingança dos que não conhecem a Deus e dos que não obedecem ao evangelho de nosso Senhor Jesus Cristo;', 8, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais, por castigo, padecerão eterna perdição, longe da face do Senhor e da glória do seu poder,', 9, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando vier para ser glorificado nos seus santos, e para se fazer admirável naquele dia em todos os que crêem (porquanto o nosso testemunho foi crido entre vós).', 10, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso também rogamos sempre por vós, para que o nosso Deus vos faça dignos da sua vocação, e cumpra todo o desejo da sua bondade, e a obra da fé com poder;', 11, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que o nome de nosso Senhor Jesus Cristo seja em vós glorificado, e vós nele, segundo a graça de nosso Deus e do Senhor Jesus Cristo.', 12, 1, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Admoesta-os a que se sujeitem aos principados e potestades, que lhes obedeçam, e estejam preparados para toda a boa obra;', 1, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que a ninguém infamem, nem sejam contenciosos, mas modestos, mostrando toda a mansidão para com todos os homens.', 2, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também nós éramos noutro tempo insensatos, desobedientes, extraviados, servindo a várias concupiscências e deleites, vivendo em malícia e inveja, odiosos, odiando-nos uns aos outros.', 3, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas quando apareceu a benignidade e amor de Deus, nosso Salvador, para com os homens,', 4, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não pelas obras de justiça que houvéssemos feito, mas segundo a sua misericórdia, nos salvou pela lavagem da regeneração e da renovação do Espírito Santo,', 5, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que abundantemente ele derramou sobre nós por Jesus Cristo nosso Salvador;', 6, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que, sendo justificados pela sua graça, sejamos feitos herdeiros segundo a esperança da vida eterna.', 7, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fiel é a palavra, e isto quero que deveras afirmes, para que os que crêem em Deus procurem aplicar-se às boas obras; estas coisas são boas e proveitosas aos homens.', 8, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não entres em questões loucas, genealogias e contendas, e nos debates acerca da lei; porque são coisas inúteis e vãs.', 9, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao homem hereje, depois de uma e outra admoestação, evita-o,', 10, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo que esse tal está pervertido, e peca, estando já em si mesmo condenado.', 11, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando te enviar Ártemas, ou Tíquico, procura vir ter comigo a Nicópolis; porque deliberei invernar ali.', 12, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Acompanha com muito cuidado Zenas, doutor da lei, e Apolo, para que nada lhes falte.', 13, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os nossos aprendam também a aplicar-se às boas obras, nas coisas necessárias, para que não sejam infrutuosos.', 14, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saúdam-te todos os que estão comigo. Saúda tu os que nos amam na fé. A graça seja com vós todos. Amém.', 15, 3, 17);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Além disto, eu, Paulo, vos rogo, pela mansidão e benignidade de Cristo, eu que, na verdade, quando presente entre vós, sou humilde, mas ausente, ousado para convosco;', 1, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Rogo-vos, pois, que, quando estiver presente, não me veja obrigado a usar com confiança da ousadia que espero ter com alguns, que nos julgam, como se andássemos segundo a carne.', 2, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, andando na carne, não militamos segundo a carne.', 3, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque as armas da nossa milícia não são carnais, mas sim poderosas em Deus para destruição das fortalezas;', 4, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Destruindo os conselhos, e toda a altivez que se levanta contra o conhecimento de Deus, e levando cativo todo o entendimento à obediência de Cristo;', 5, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estando prontos para vingar toda a desobediência, quando for cumprida a vossa obediência.', 6, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Olhais para as coisas segundo a aparência? Se alguém confia de si mesmo que é de Cristo, pense outra vez isto consigo, que, assim como ele é de Cristo, também nós de Cristo somos.', 7, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, ainda que eu me glorie mais alguma coisa do nosso poder, o qual o Senhor nos deu para edificação, e não para vossa destruição, não me envergonharei.', 8, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que não pareça como se quisera intimidar-vos por cartas.', 9, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque as suas cartas, dizem, são graves e fortes, mas a presença do corpo é fraca, e a palavra desprezível.', 10, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pense o tal isto, que, quais somos na palavra por cartas, estando ausentes, tais seremos também por obra, estando presentes.', 11, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não ousamos classificar-nos, ou comparar-nos com alguns, que se louvam a si mesmos; mas estes que se medem a si mesmos, e se comparam consigo mesmos, estão sem entendimento.', 12, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porém, não nos gloriaremos fora da medida, mas conforme a reta medida que Deus nos deu, para chegarmos até vós;', 13, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não nos estendemos além do que convém, como se não houvéssemos de chegar até vós, pois já chegamos também até vós no evangelho de Cristo,', 14, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não nos gloriando fora da medida nos trabalhos alheios; antes tendo esperança de que, crescendo a vossa fé, seremos abundantemente engrandecidos entre vós, conforme a nossa regra,', 15, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para anunciar o evangelho nos lugares que estão além de vós e não em campo de outrem, para não nos gloriarmos no que estava já preparado.', 16, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele, porém, que se gloria, glorie-se no Senhor.', 17, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não é aprovado quem a si mesmo se louva, mas, sim, aquele a quem o Senhor louva.', 18, 10, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi outro grande e admirável sinal no céu: sete anjos, que tinham as sete últimas pragas; porque nelas é consumada a ira de Deus.', 1, 15, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi um como mar de vidro misturado com fogo; e também os que saíram vitoriosos da besta, e da sua imagem, e do seu sinal, e do número do seu nome, que estavam junto ao mar de vidro, e tinham as harpas de Deus.', 2, 15, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E cantavam o cântico de Moisés, servo de Deus, e o cântico do Cordeiro, dizendo: Grandes e maravilhosas são as tuas obras, Senhor Deus Todo-Poderoso! Justos e verdadeiros são os teus caminhos, ó Rei dos santos.', 3, 15, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem te não temerá, ó Senhor, e não magnificará o teu nome? Porque só tu és santo; por isso todas as nações virão, e se prostrarão diante de ti, porque os teus juízos são manifestos.', 4, 15, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois disto olhei, e eis que o templo do tabernáculo do testemunho se abriu no céu.', 5, 15, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os sete anjos que tinham as sete pragas saíram do templo, vestidos de linho puro e resplandecente, e cingidos com cintos de ouro pelos peitos.', 6, 15, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um dos quatro animais deu aos sete anjos sete taças de ouro, cheias da ira de Deus, que vive para todo o sempre.', 7, 15, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o templo encheu-se com a fumaça da glória de Deus e do seu poder; e ninguém podia entrar no templo, até que se consumassem as sete pragas dos sete anjos.', 8, 15, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois destas coisas ouvi no céu uma grande voz de uma grande multidão, que dizia: Aleluia! A salvação, e a glória, e a honra, e o poder pertencem ao Senhor nosso Deus;', 1, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque verdadeiros e justos são os seus juízos, pois julgou a grande prostituta, que havia corrompido a terra com a sua fornicação, e das mãos dela vingou o sangue dos seus servos.', 2, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra vez disseram: Aleluia! E a fumaça dela sobe para todo o sempre.', 3, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os vinte e quatro anciãos, e os quatro animais, prostraram-se e adoraram a Deus, que estava assentado no trono, dizendo: Amém. Aleluia!', 4, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E saiu uma voz do trono, que dizia: Louvai o nosso Deus, vós, todos os seus servos, e vós que o temeis, assim pequenos como grandes.', 5, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi como que a voz de uma grande multidão, e como que a voz de muitas águas, e como que a voz de grandes trovões, que dizia: Aleluia! pois já o Senhor Deus Todo-Poderoso reina.', 6, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Regozijemo-nos, e alegremo-nos, e demos-lhe glória; porque vindas são as bodas do Cordeiro, e já a sua esposa se aprontou.', 7, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi-lhe dado que se vestisse de linho fino, puro e resplandecente; porque o linho fino são as justiças dos santos.', 8, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-me: Escreve: Bemaventurados aqueles que são chamados à ceia das bodas do Cordeiro. E disse-me: Estas são as verdadeiras palavras de Deus.', 9, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu lancei-me a seus pés para o adorar; mas ele disse-me: Olha não faças tal; sou teu conservo, e de teus irmãos, que têm o testemunho de Jesus. Adora a Deus; porque o testemunho de Jesus é o espírito de profecia.', 10, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi o céu aberto, e eis um cavalo branco; e o que estava assentado sobre ele chama-se Fiel e Verdadeiro; e julga e peleja com justiça.', 11, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus olhos eram como chama de fogo; e sobre a sua cabeça havia muitos diademas; e tinha um nome escrito, que ninguém sabia senão ele mesmo.', 12, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava vestido de veste tingida em sangue; e o nome pelo qual se chama é A Palavra de Deus.', 13, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E seguiam-no os exércitos no céu em cavalos brancos, e vestidos de linho fino, branco e puro.', 14, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E da sua boca saía uma aguda espada, para ferir com ela as nações; e ele as regerá com vara de ferro; e ele mesmo é o que pisa o lagar do vinho do furor e da ira do Deus Todo-Poderoso.', 15, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no manto e na sua coxa tem escrito este nome: Rei dos reis, e Senhor dos senhores.', 16, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi um anjo que estava no sol, e clamou com grande voz, dizendo a todas as aves que voavam pelo meio do céu: Vinde, e ajuntai-vos à ceia do grande Deus;', 17, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que comais a carne dos reis, e a carne dos tribunos, e a carne dos fortes, e a carne dos cavalos e dos que sobre eles se assentam; e a carne de todos os homens, livres e servos, pequenos e grandes.', 18, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi a besta, e os reis da terra, e os seus exércitos reunidos, para fazerem guerra àquele que estava assentado sobre o cavalo, e ao seu exército.', 19, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a besta foi presa, e com ela o falso profeta, que diante dela fizera os sinais, com que enganou os que receberam o sinal da besta, e adoraram a sua imagem. Estes dois foram lançados vivos no lago de fogo que arde com enxofre.', 20, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os demais foram mortos com a espada que saía da boca do que estava assentado sobre o cavalo, e todas as aves se fartaram das suas carnes.', 21, 19, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Também, irmãos, vos fazemos conhecer a graça de Deus dada às igrejas da macedônia;', 1, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como em muita prova de tribulação houve abundância do seu gozo, e como a sua profunda pobreza abundou em riquezas da sua generosidade.', 2, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, segundo o seu poder (o que eu mesmo testifico) e ainda acima do seu poder, deram voluntariamente.', 3, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pedindo-nos com muitos rogos que aceitássemos a graça e a comunicação deste serviço, que se fazia para com os santos.', 4, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não somente fizeram como nós esperávamos, mas a si mesmos se deram primeiramente ao Senhor, e depois a nós, pela vontade de Deus.', 5, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que exortamos a Tito que, assim como antes tinha começado, assim também acabasse esta graça entre vós.', 6, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, assim como em tudo abundais em fé, e em palavra, e em ciência, e em toda a diligência, e em vosso amor para conosco, assim também abundeis nesta graça.', 7, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não digo isto como quem manda, mas para provar, pela diligência dos outros, a sinceridade de vosso amor.', 8, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já sabeis a graça de nosso Senhor Jesus Cristo que, sendo rico, por amor de vós se fez pobre; para que pela sua pobreza enriquecêsseis.', 9, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nisto dou o meu parecer; pois isto convém a vós que, desde o ano passado, começastes; e não foi só praticar, mas também querer.', 10, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora, porém, completai também o já começado, para que, assim como houve a prontidão de vontade, haja também o cumprimento, segundo o que tendes.', 11, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se há prontidão de vontade, será aceita segundo o que qualquer tem, e não segundo o que não tem.', 12, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, não digo isto para que os outros tenham alívio, e vós opressão,', 13, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas para igualdade; neste tempo presente, a vossa abundância supra a falta dos outros, para que também a sua abundância supra a vossa falta, e haja igualdade;', 14, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como está escrito: O que muito colheu não teve demais; e o que pouco, não teve de menos.', 15, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, graças a Deus, que pôs a mesma solicitude por vós no coração de Tito;', 16, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois aceitou a exortação, e muito diligente partiu voluntariamente para vós.', 17, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E com ele enviamos aquele irmão cujo louvor no evangelho está espalhado em todas as igrejas.', 18, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não só isto, mas foi também escolhido pelas igrejas para companheiro da nossa viagem, nesta graça que por nós é ministrada para glória do mesmo Senhor, e prontidão do vosso ânimo;', 19, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Evitando isto, que alguém nos vitupere por esta abundância, que por nós é ministrada;', 20, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois zelamos do que é honesto, não só diante do Senhor, mas também diante dos homens.', 21, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Com eles enviamos também outro nosso irmão, o qual muitas vezes, e em muitas coisas, já experimentamos ser diligente, e agora muito mais diligente ainda pela muita confiança que em vós tem.', 22, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto a Tito, é meu companheiro, e cooperador para convosco; quanto a nossos irmãos, são embaixadores das igrejas e glória de Cristo.', 23, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, mostrai para com eles, e perante a face das igrejas, a prova do vosso amor, e da nossa glória acerca de vós.', 24, 8, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi outro anjo forte, que descia do céu, vestido de uma nuvem; e por cima da sua cabeça estava o arco celeste, e o seu rosto era como o sol, e os seus pés como colunas de fogo;', 1, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tinha na sua mão um livrinho aberto. E pôs o seu pé direito sobre o mar, e o esquerdo sobre a terra;', 2, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E clamou com grande voz, como quando ruge um leão; e, havendo clamado, os sete trovões emitiram as suas vozes.', 3, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando os sete trovões acabaram de emitir as suas vozes, eu ia escrever; mas ouvi uma voz do céu, que me dizia: Sela o que os sete trovões emitiram, e nào o escrevas.', 4, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o anjo que vi estar sobre o mar e sobre a terra levantou a sua mão ao céu,', 5, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E jurou por aquele que vive para todo o sempre, o qual criou o céu e o que nele há, e a terra e o que nela há, e o mar e o que nele há, que não haveria mais demora;', 6, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nos dias da voz do sétimo anjo, quando tocar a sua trombeta, se cumprirá o segredo de Deus, como anunciou aos profetas, seus servos.', 7, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a voz que eu do céu tinha ouvido tornou a falar comigo, e disse: Vai, e toma o livrinho aberto da mão do anjo que está em pé sobre o mar e sobre a terra.', 8, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E fui ao anjo, dizendo-lhe: Dá-me o livrinho. E ele disse-me: Toma-o, e come-o, e ele fará amargo o teu ventre, mas na tua boca será doce como mel.', 9, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tomei o livrinho da mão do anjo, e comi-o; e na minha boca era doce como mel; e, havendo-o comido, o meu ventre ficou amargo.', 10, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse-me: Importa que profetizes outra vez a muitos povos, e nações, e línguas e reis.', 11, 10, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o quinto anjo tocou a sua trombeta, e vi uma estrela que do céu caiu na terra; e foi-lhe dada a chave do poço do abismo.', 1, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E abriu o poço do abismo, e subiu fumaça do poço, como a fumaça de uma grande fornalha, e com a fumaça do poço escureceu-se o sol e o ar.', 2, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E da fumaça vieram gafanhotos sobre a terra; e foi-lhes dado poder, como o poder que têm os escorpiões da terra.', 3, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi-lhes dito que não fizessem dano à erva da terra, nem a verdura alguma, nem a árvore alguma, mas somente aos homens que não têm nas suas testas o selo de Deus.', 4, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi-lhes permitido, não que os matassem, mas que por cinco meses os atormentassem; e o seu tormento era semelhante ao tormento do escorpião, quando fere o homem.', 5, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E naqueles dias os homens buscarão a morte, e não a acharão; e desejarão morrer, e a morte fugirá deles.', 6, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o parecer dos gafanhotos era semelhante ao de cavalos aparelhados para a guerra; e sobre as suas cabeças havia umas como coroas semelhantes ao ouro; e os seus rostos eram como rostos de homens.', 7, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tinham cabelos como cabelos de mulheres, e os seus dentes eram como de leões.', 8, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tinham couraças como couraças de ferro; e o ruído das suas asas era como o ruído de carros, quando muitos cavalos correm ao combate.', 9, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tinham caudas semelhantes às dos escorpiões, e aguilhões nas suas caudas; e o seu poder era para danificar os homens por cinco meses.', 10, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tinham sobre si rei, o anjo do abismo; em hebreu era o seu nome Abadom, e em grego Apoliom.', 11, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Passado é já um ai; eis que depois disso vêm ainda dois ais.', 12, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tocou o sexto anjo a sua trombeta, e ouvi uma voz que vinha das quatro pontas do altar de ouro, que estava diante de Deus,', 13, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A qual dizia ao sexto anjo, que tinha a trombeta: Solta os quatro anjos, que estão presos junto ao grande rio Eufrates.', 14, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram soltos os quatro anjos, que estavam preparados para a hora, e dia, e mês, e ano, a fim de matarem a terça parte dos homens.', 15, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o número dos exércitos dos cavaleiros era de duzentos milhões; e ouvi o número deles.', 16, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim vi os cavalos nesta visão; e os que sobre eles cavalgavam tinham couraças de fogo, e de jacinto, e de enxofre; e as cabeças dos cavalos eram como cabeças de leões; e de suas bocas saía fogo e fumaça e enxofre.', 17, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por estes três foi morta a terça parte dos homens, isto é pelo fogo, pela fumaça, e pelo enxofre, que saíam das suas bocas.', 18, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o poder dos cavalos está na sua boca e nas suas caudas. Porquanto as suas caudas são semelhantes a serpentes, e têm cabeças, e com elas danificam.', 19, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os outros homens, que não foram mortos por estas pragas, não se arrependeram das obras de suas mãos, para não adorarem os demônios, e os ídolos de ouro, e de prata, e de bronze, e de pedra, e de madeira, que nem podem ver, nem ouvir, nem andar.', 20, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não se arrependeram dos seus homicídios, nem das suas feitiçarias, nem da sua fornicação, nem dos seus furtos.', 21, 9, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois destas coisas, olhei, e eis que estava uma porta aberta no céu; e a primeira voz, que como de trombeta ouvira falar comigo, disse: Sobe aqui, e mostrar-te-ei as coisas que depois destas devem acontecer.', 1, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo fui arrebatado em espírito, e eis que um trono estava posto no céu, e um assentado sobre o trono.', 2, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que estava assentado era, na aparência, semelhante à pedra jaspe e sardônica; e o arco celeste estava ao redor do trono, e parecia semelhante à esmeralda.', 3, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao redor do trono havia vinte e quatro tronos; e vi assentados sobre os tronos vinte e quatro anciãos vestidos de vestes brancas; e tinham sobre suas cabeças coroas de ouro.', 4, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E do trono saíam relâmpagos, e trovões, e vozes; e diante do trono ardiam sete lâmpadas de fogo, as quais são os sete espíritos de Deus.', 5, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia diante do trono um como mar de vidro, semelhante ao cristal. E no meio do trono, e ao redor do trono, quatro animais cheios de olhos, por diante e por detrás.', 6, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o primeiro animal era semelhante a um leão, e o segundo animal semelhante a um bezerro, e tinha o terceiro animal o rosto como de homem, e o quarto animal era semelhante a uma águia voando.', 7, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os quatro animais tinham, cada um de per si, seis asas, e ao redor, e por dentro, estavam cheios de olhos; e não descansam nem de dia nem de noite, dizendo: Santo, Santo, Santo, é o Senhor Deus, o Todo-Poderoso, que era, e que é, e que há de vir.', 8, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando os animais davam glória, e honra, e ações de graças ao que estava assentado sobre o trono, ao que vive para todo o sempre,', 9, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os vinte e quatro anciãos prostravam-se diante do que estava assentado sobre o trono, e adoravam o que vive para todo o sempre; e lançavam as suas coroas diante do trono, dizendo:', 10, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digno és, Senhor, de receber glória, e honra, e poder; porque tu criaste todas as coisas, e por tua vontade são e foram criadas.', 11, 4, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E viu-se um grande sinal no céu: uma mulher vestida do sol, tendo a lua debaixo dos seus pés, e uma coroa de doze estrelas sobre a sua cabeça.', 1, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava grávida, e com dores de parto, e gritava com ânsias de dar à luz.', 2, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E viu-se outro sinal no céu; e eis que era um grande dragão vermelho, que tinha sete cabeças e dez chifres, e sobre as suas cabeças sete diademas.', 3, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a sua cauda levou após si a terça parte das estrelas do céu, e lançou-as sobre a terra; e o dragão parou diante da mulher que havia de dar à luz, para que, dando ela à luz, lhe tragasse o filho.', 4, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E deu à luz um filho homem que há de reger todas as nações com vara de ferro; e o seu filho foi arrebatado para Deus e para o seu trono.', 5, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a mulher fugiu para o deserto, onde já tinha lugar preparado por Deus, para que ali fosse alimentada durante mil duzentos e sessenta dias.', 6, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E houve batalha no céu; Miguel e os seus anjos batalhavam contra o dragão, e batalhavam o dragão e os seus anjos;', 7, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não prevaleceram, nem mais o seu lugar se achou nos céus.', 8, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi precipitado o grande dragão, a antiga serpente, chamada o Diabo, e Satanás, que engana todo o mundo; ele foi precipitado na terra, e os seus anjos foram lançados com ele.', 9, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi uma grande voz no céu, que dizia: Agora é chegada a salvação, e a força, e o reino do nosso Deus, e o poder do seu Cristo; porque já o acusador de nossos irmãos é derrubado, o qual diante do nosso Deus os acusava de dia e de noite.', 10, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles o venceram pelo sangue do Cordeiro e pela palavra do seu testemunho; e não amaram as suas vidas até à morte.', 11, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso alegrai-vos, ó céus, e vós que neles habitais. Ai dos que habitam na terra e no mar; porque o diabo desceu a vós, e tem grande ira, sabendo que já tem pouco tempo.', 12, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando o dragão viu que fora lançado na terra, perseguiu a mulher que dera à luz o filho homem.', 13, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram dadas à mulher duas asas de grande águia, para que voasse para o deserto, ao seu lugar, onde é sustentada por um tempo, e tempos, e metade de um tempo, fora da vista da serpente.', 14, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a serpente lançou da sua boca, atrás da mulher, água como um rio, para que pela corrente a fizesse arrebatar.', 15, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a terra ajudou a mulher; e a terra abriu a sua boca, e tragou o rio que o dragão lançara da sua boca.', 16, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o dragão irou-se contra a mulher, e foi fazer guerra ao remanescente da sua semente, os que guardam os mandamentos de Deus, e têm o testemunho de Jesus Cristo.', 17, 12, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É esta a terceira vez que vou ter convosco. Por boca de duas ou três testemunhas será confirmada toda a palavra.', 1, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Já anteriormente o disse, e segunda vez o digo como quando estava presente; mas agora, estando ausente, o escrevo aos que antes pecaram e a todos os mais, que, se outra vez for, não lhes perdoarei;', 2, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto que buscais uma prova de Cristo que fala em mim, o qual não é fraco para convosco, antes é poderoso entre vós.', 3, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, ainda que foi crucificado por fraqueza, vive, contudo, pelo poder de Deus. Porque nós também somos fracos nele, mas viveremos com ele pelo poder de Deus em vós.', 4, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Examinai-vos a vós mesmos, se permaneceis na fé; provai-vos a vós mesmos. Ou não sabeis quanto a vós mesmos, que Jesus Cristo está em vós? Se não é que já estais reprovados.', 5, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas espero que entendereis que nós não somos reprovados.', 6, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, eu rogo a Deus que não façais mal algum, não para que sejamos achados aprovados, mas para que vós façais o bem, embora nós sejamos como reprovados.', 7, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nada podemos contra a verdade, senão pela verdade.', 8, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nos regozijamos de estar fracos, quando vós estais fortes; e o que desejamos é a vossa perfeição.', 9, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, escrevo estas coisas estando ausente, para que, estando presente, não use de rigor, segundo o poder que o Senhor me deu para edificação, e não para destruição.', 10, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto ao mais, irmãos, regozijai-vos, sede perfeitos, sede consolados, sede de um mesmo parecer, vivei em paz; e o Deus de amor e de paz será convosco.', 11, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai-vos uns aos outros com ósculo santo.', 12, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos os santos vos saúdam.', 13, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça do Senhor Jesus Cristo, e o amor de Deus, e a comunhão do Espírito Santo seja com todos vós. Amém.', 14, 13, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E veio um dos sete anjos que tinham as sete taças, e falou comigo, dizendo-me: Vem, mostrar-te-ei a condenação da grande prostituta que está assentada sobre muitas águas;', 1, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Com a qual fornicaram os reis da terra; e os que habitam na terra se embebedaram com o vinho da sua fornicação.', 2, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levou-me em espírito a um deserto, e vi uma mulher assentada sobre uma besta de cor de escarlata, que estava cheia de nomes de blasfêmia, e tinha sete cabeças e dez chifres.', 3, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a mulher estava vestida de púrpura e de escarlata, e adornada com ouro, e pedras preciosas e pérolas; e tinha na sua mão um cálice de ouro cheio das abominações e da imundícia da sua fornicação;', 4, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E na sua testa estava escrito o nome: Mistério, a grande babilônia, a mãe das prostituições e abominações da terra.', 5, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi que a mulher estava embriagada do sangue dos santos, e do sangue das testemunhas de Jesus. E, vendo-a eu, maravilhei-me com grande admiração.', 6, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o anjo me disse: Por que te admiras? Eu te direi o mistério da mulher, e da besta que a traz, a qual tem sete cabeças e dez chifres.', 7, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A besta que viste foi e já não é, e há de subir do abismo, e irá à perdição; e os que habitam na terra (cujos nomes não estão escritos no livro da vida, desde a fundação do mundo) se admirarão, vendo a besta que era e já não é, ainda que é.', 8, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aqui o sentido, que tem sabedoria. As sete cabeças são sete montes, sobre os quais a mulher está assentada.', 9, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E são também sete reis; cinco já caíram, e um existe; outro ainda não é vindo; e, quando vier, convém que dure um pouco de tempo.', 10, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a besta que era e já não é, é ela também o oitavo, e é dos sete, e vai à perdição.', 11, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os dez chifres que viste são dez reis, que ainda não receberam o reino, mas receberão poder como reis por uma hora, juntamente com a besta.', 12, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes têm um mesmo intento, e entregarão o seu poder e autoridade à besta.', 13, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes combaterão contra o Cordeiro, e o Cordeiro os vencerá, porque é o Senhor dos senhores e o Rei dos reis; vencerão os que estão com ele, chamados, e eleitos, e fiéis.', 14, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-me: As águas que viste, onde se assenta a prostituta, são povos, e multidões, e nações, e línguas.', 15, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os dez chifres que viste na besta são os que odiarào a prostituta, e a colocarão desolada e nua, e comerão a sua carne, e a queimarão no fogo.', 16, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus tem posto em seus corações, que cumpram o seu intento, e tenham uma mesma idéia, e que dêem à besta o seu reino, até que se cumpram as palavras de Deus.', 17, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a mulher que viste é a grande cidade que reina sobre os reis da terra.', 18, 17, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nós, cooperando também com ele, vos exortamos a que não recebais a graça de Deus em vão', 1, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Porque diz:Ouvi-te em tempo aceitável e socorri-te no dia da salvação;eis aqui agora o tempo aceitável, eis aqui agora o dia da salvação).', 2, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não dando nós escândalo em coisa alguma, para que o nosso ministério não seja censurado;', 3, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, como ministros de Deus, tornando-nos recomendáveis em tudo; na muita paciência, nas aflições, nas necessidades, nas angústias,', 4, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nos açoites, nas prisões, nos tumultos, nos trabalhos, nas vigílias, nos jejuns,', 5, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na pureza, na ciência, na longanimidade, na benignidade, no Espírito Santo, no amor não fingido,', 6, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na palavra da verdade, no poder de Deus, pelas armas da justiça, à direita e à esquerda,', 7, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por honra e por desonra, por infâmia e por boa fama; como enganadores, e sendo verdadeiros;', 8, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como desconhecidos, mas sendo bem conhecidos; como morrendo, e eis que vivemos; como castigados, e não mortos;', 9, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como contristados, mas sempre alegres; como pobres, mas enriquecendo a muitos; como nada tendo, e possuindo tudo.', 10, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ó coríntios, a nossa boca está aberta para vós, o nosso coração está dilatado.', 11, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não estais estreitados em nós; mas estais estreitados nos vossos próprios afetos.', 12, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, em recompensa disto, (falo como a filhos) dilatai-vos também vós.', 13, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos prendais a um jugo desigual com os infiéis; porque, que sociedade tem a justiça com a injustiça? E que comunhão tem a luz com as trevas?', 14, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que concórdia há entre Cristo e Belial? Ou que parte tem o fiel com o infiel?', 15, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que consenso tem o templo de Deus com os ídolos? Porque vós sois o templo do Deus vivente, como Deus disse: Neles habitarei, e entre eles andarei; e eu serei o seu Deus e eles serão o meu povo.', 16, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso saí do meio deles, e apartai-vos, diz o Senhor;E não toqueis nada imundo,E eu vos receberei;', 17, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu serei para vós Pai,E vós sereis para mim filhos e filhas, Diz o Senhor Todo-Poderoso.', 18, 6, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao anjo da igreja que está em Sardes escreve: Isto diz o que tem os sete espíritos de Deus, e as sete estrelas: Conheço as tuas obras, que tens nome de que vives, e estás morto.', 1, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sê vigilante, e confirma os restantes, que estavam para morrer; porque não achei as tuas obras perfeitas diante de Deus.', 2, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lembra-te, pois, do que tens recebido e ouvido, e guarda-o, e arrepende-te. E, se não vigiares, virei sobre ti como um ladrão, e não saberás a que hora sobre ti virei.', 3, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas também tens em Sardes algumas poucas pessoas que não contaminaram suas vestes, e comigo andarão de branco; porquanto são dignas disso.', 4, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que vencer será vestido de vestes brancas, e de maneira nenhuma riscarei o seu nome do livro da vida; e confessarei o seu nome diante de meu Pai e diante dos seus anjos.', 5, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem tem ouvidos, ouça o que o Espírito diz às igrejas.', 6, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao anjo da igreja que está em Filadélfia escreve: Isto diz o que é santo, o que é verdadeiro, o que tem a chave de Davi; o que abre, e ninguém fecha; e fecha, e ninguém abre:', 7, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conheço as tuas obras; eis que diante de ti pus uma porta aberta, e ninguém a pode fechar; tendo pouca força, guardaste a minha palavra, e não negaste o meu nome.', 8, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que eu farei aos da sinagoga de Satanás, aos que se dizem judeus, e não são, mas mentem: eis que eu farei que venham, e adorem prostrados a teus pés, e saibam que eu te amo.', 9, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como guardaste a palavra da minha paciência, também eu te guardarei da hora da tentação que há de vir sobre todo o mundo, para tentar os que habitam na terra.', 10, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que venho sem demora; guarda o que tens, para que ninguém tome a tua coroa.', 11, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A quem vencer, eu o farei coluna no templo do meu Deus, e dele nunca sairá; e escreverei sobre ele o nome do meu Deus, e o nome da cidade do meu Deus, a nova Jerusalém, que desce do céu, do meu Deus, e também o meu novo nome.', 12, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem tem ouvidos, ouça o que o Espírito diz às igrejas.', 13, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao anjo da igreja de Laodicéia escreve: Isto diz o Amém, a testemunha fiel e verdadeira, o princípio da criação de Deus:', 14, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conheço as tuas obras, que nem és frio nem quente; quem dera foras frio ou quente!', 15, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, porque és morno, e não és frio nem quente, vomitar-te-ei da minha boca.', 16, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como dizes: Rico sou, e estou enriquecido, e de nada tenho falta; e não sabes que és um desgraçado, e miserável, e pobre, e cego, e nu;', 17, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aconselho-te que de mim compres ouro provado no fogo, para que te enriqueças; e roupas brancas, para que te vistas, e não apareça a vergonha da tua nudez; e que unjas os teus olhos com colírio, para que vejas.', 18, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu repreendo e castigo a todos quantos amo; sê pois zeloso, e arrepende-te.', 19, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que estou à porta, e bato; se alguém ouvir a minha voz, e abrir a porta, entrarei em sua casa, e com ele cearei, e ele comigo.', 20, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao que vencer lhe concederei que se assente comigo no meu trono; assim como eu venci, e me assentei com meu Pai no seu trono.', 21, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem tem ouvidos, ouça o que o Espírito diz às igrejas.', 22, 3, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu pus-me sobre a areia do mar, e vi subir do mar uma besta que tinha sete cabeças e dez chifres, e sobre os seus chifres dez diademas, e sobre as suas cabeças um nome de blasfêmia.', 1, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a besta que vi era semelhante ao leopardo, e os seus pés como os de urso, e a sua boca como a de leão; e o dragão deu-lhe o seu poder, e o seu trono, e grande poderio.', 2, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi uma das suas cabeças como ferida de morte, e a sua chaga mortal foi curada; e toda a terra se maravilhou após a besta.', 3, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E adoraram o dragão que deu à besta o seu poder; e adoraram a besta, dizendo: Quem é semelhante à besta? Quem poderá batalhar contra ela?', 4, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi-lhe dada uma boca, para proferir grandes coisas e blasfêmias; e deu-se-lhe poder para agir por quarenta e dois meses.', 5, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E abriu a sua boca em blasfêmias contra Deus, para blasfemar do seu nome, e do seu tabernáculo, e dos que habitam no céu.', 6, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi-lhe permitido fazer guerra aos santos, e vencê-los; e deu-se-lhe poder sobre toda a tribo, e língua, e nação.', 7, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E adoraram-na todos os que habitam sobre a terra, esses cujos nomes não estão escritos no livro da vida do Cordeiro que foi morto desde a fundação do mundo.', 8, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém tem ouvidos, ouça.', 9, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém leva em cativeiro, em cativeiro irá; se alguém matar à espada, necessário é que à espada seja morto. Aqui está a paciência e a fé dos santos.', 10, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi subir da terra outra besta, e tinha dois chifres semelhantes aos de um cordeiro; e falava como o dragão.', 11, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E exerce todo o poder da primeira besta na sua presença, e faz que a terra e os que nela habitam adorem a primeira besta, cuja chaga mortal fora curada.', 12, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E faz grandes sinais, de maneira que até fogo faz descer do céu à terra, à vista dos homens.', 13, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E engana os que habitam na terra com sinais que lhe foi permitido que fizesse em presença da besta, dizendo aos que habitam na terra que fizessem uma imagem à besta que recebera a ferida da espada e vivia.', 14, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi-lhe concedido que desse espírito à imagem da besta, para que também a imagem da besta falasse, e fizesse que fossem mortos todos os que não adorassem a imagem da besta.', 15, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E faz que a todos, pequenos e grandes, ricos e pobres, livres e servos, lhes seja posto um sinal na sua mão direita, ou nas suas testas,', 16, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que ninguém possa comprar ou vender, senão aquele que tiver o sinal, ou o nome da besta, ou o número do seu nome.', 17, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aqui há sabedoria. Aquele que tem entendimento, calcule o número da besta; porque é o número de um homem, e o seu número é seiscentos e sessenta e seis.', 18, 13, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, irmãos santos, participantes da vocação celestial, considerai a Jesus Cristo, apóstolo e sumo sacerdote da nossa confissão,', 1, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo fiel ao que o constituiu, como também o foi Moisés em toda a sua casa.', 2, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ele é tido por digno de tanto maior glória do que Moisés, quanto maior honra do que a casa tem aquele que a edificou.', 3, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque toda a casa é edificada por alguém, mas o que edificou todas as coisas é Deus.', 4, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, na verdade, Moisés foi fiel em toda a sua casa, como servo, para testemunho das coisas que se haviam de anunciar;', 5, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Cristo, como Filho, sobre a sua própria casa; a qual casa somos nós, se tão somente conservarmos firme a confiança e a glória da esperança até ao fim.', 6, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, como diz o Espírito Santo: Se ouvirdes hoje a sua voz,', 7, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não endureçais os vossos corações,Como na provocação, no dia da tentação no deserto.', 8, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde vossos pais me tentaram, me provaram,E viram por quarenta anos as minhas obras.', 9, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso me indignei contra esta geração, E disse: Estes sempre erram em seu coração, E não conheceram os meus caminhos.', 10, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim jurei na minha ira Que não entrarão no meu repouso.', 11, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede, irmãos, que nunca haja em qualquer de vós um coração mau e infiel, para se apartar do Deus vivo.', 12, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, exortai-vos uns aos outros todos os dias, durante o tempo que se chama Hoje, para que nenhum de vós se endureça pelo engano do pecado;', 13, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nos tornamos participantes de Cristo, se retivermos firmemente o princípio da nossa confiança até ao fim.', 14, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Enquanto se diz: Hoje, se ouvirdes a sua voz, Não endureçais os vossos corações, como na provocação.', 15, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, havendo-a alguns ouvido, o provocaram; mas não todos os que saíram do Egito por meio de Moisés.', 16, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas com quem se indignou por quarenta anos? Não foi porventura com os que pecaram, cujos corpos caíram no deserto?', 17, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a quem jurou que não entrariam no seu repouso, senão aos que foram desobedientes?', 18, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vemos que não puderam entrar por causa da sua incredulidade.', 19, 3, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, tendo este ministério, segundo a misericórdia que nos foi feita, não desfalecemos;', 1, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, rejeitamos as coisas que por vergonha se ocultam, não andando com astúcia nem falsificando a palavra de Deus; e assim nos recomendamos à consciência de todo o homem, na presença de Deus, pela manifestação da verdade.', 2, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se ainda o nosso evangelho está encoberto, para os que se perdem está encoberto.', 3, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nos quais o deus deste século cegou os entendimentos dos incrédulos, para que lhes não resplandeça a luz do evangelho da glória de Cristo, que é a imagem de Deus.', 4, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não nos pregamos a nós mesmos, mas a Cristo Jesus, o Senhor; e nós mesmos somos vossos servos por amor de Jesus.', 5, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus, que disse que das trevas resplandecesse a luz, é quem resplandeceu em nossos corações, para iluminação do conhecimento da glória de Deus, na face de Jesus Cristo.', 6, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Temos, porém, este tesouro em vasos de barro, para que a excelência do poder seja de Deus, e não de nós.', 7, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em tudo somos atribulados, mas não angustiados; perplexos, mas não desanimados.', 8, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Perseguidos, mas não desamparados; abatidos, mas não destruídos;', 9, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Trazendo sempre por toda a parte a mortificação do Senhor Jesus no nosso corpo, para que a vida de Jesus se manifeste também nos nossos corpos;', 10, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim nós, que vivemos, estamos sempre entregues à morte por amor de Jesus, para que a vida de Jesus se manifeste também na nossa carne mortal.', 11, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que em nós opera a morte, mas em vós a vida.', 12, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E temos portanto o mesmo espírito de fé, como está escrito: Cri, por isso falei; nós cremos também, por isso também falamos.', 13, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo que o que ressuscitou o Senhor Jesus nos ressuscitará também por Jesus, e nos apresentará convosco.', 14, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tudo isto é por amor de vós, para que a graça, multiplicada por meio de muitos, faça abundar a ação de graças para glória de Deus.', 15, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso não desfalecemos; mas, ainda que o nosso homem exterior se corrompa, o interior, contudo, se renova de dia em dia.', 16, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a nossa leve e momentânea tribulação produz para nós um peso eterno de glória mui excelente;', 17, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não atentando nós nas coisas que se vêem, mas nas que se não vêem; porque as que se vêem são temporais, e as que se não vêem são eternas.', 18, 4, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto à administração que se faz a favor dos santos, não necessito escrever-vos;', 1, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque bem sei a prontidão do vosso ânimo, da qual me glorio de vós para com os macedônios; que a Acaia está pronta desde o ano passado; e o vosso zelo tem estimulado muitos.', 2, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas enviei estes irmãos, para que a nossa glória, acerca de vós, não seja vã nesta parte; para que (como já disse) possais estar prontos,', 3, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A fim de, se acaso os macedônios vierem comigo, e vos acharem desapercebidos, não nos envergonharmos nós (para não dizermos vós) deste firme fundamento de glória.', 4, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, tive por coisa necessária exortar estes irmãos, para que primeiro fossem ter convosco, e preparassem de antemão a vossa bênção, já antes anunciada, para que esteja pronta como bênção, e não como avareza.', 5, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E digo isto: Que o que semeia pouco, pouco também ceifará; e o que semeia em abundância, em abundância ceifará.', 6, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cada um contribua segundo propôs no seu coração; não com tristeza, ou por necessidade; porque Deus ama ao que dá com alegria.', 7, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Deus é poderoso para fazer abundar em vós toda a graça, a fim de que tendo sempre, em tudo, toda a suficiência, abundeis em toda a boa obra;', 8, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conforme está escrito:Espalhou, deu aos pobres;a sua justiça permanece para sempre.', 9, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, aquele que dá a semente ao que semeia, também vos dê pão para comer, e multiplique a vossa sementeira, e aumente os frutos da vossa justiça;', 10, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que em tudo enriqueçais para toda a beneficência, a qual faz que por nós se dêem graças a Deus.', 11, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a administração deste serviço, não só supre as necessidades dos santos, mas também é abundante em muitas graças, que se dão a Deus.', 12, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto como, na prova desta administração, glorificam a Deus pela submissão, que confessais quanto ao evangelho de Cristo, e pela liberalidade de vossos dons para com eles, e para com todos;', 13, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pela sua oração por vós, tendo de vós saudades, por causa da excelente graça de Deus que em vós há.', 14, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graças a Deus, pois, pelo seu dom inefável.', 15, 9, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todo o sumo sacerdote, tomado dentre os homens, é constituído a favor dos homens nas coisas concernentes a Deus, para que ofereça dons e sacrifícios pelos pecados;', 1, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E possa compadecer-se ternamente dos ignorantes e errados; pois também ele mesmo está rodeado de fraqueza.', 2, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por esta causa deve ele, tanto pelo povo, como também por si mesmo, fazer oferta pelos pecados.', 3, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ninguém toma para si esta honra, senão o que é chamado por Deus, como Arão.', 4, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também Cristo não se glorificou a si mesmo, para se fazer sumo sacerdote, mas aquele que lhe disse: Tu és meu Filho,Hoje te gerei.', 5, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como também diz, noutro lugar: Tu és sacerdote eternamente, Segundo a ordem de Melquisedeque.', 6, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, nos dias da sua carne, oferecendo, com grande clamor e lágrimas, orações e súplicas ao que o podia livrar da morte, foi ouvido quanto ao que temia.', 7, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ainda que era Filho, aprendeu a obediência, por aquilo que padeceu.', 8, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo ele consumado, veio a ser a causa da eterna salvação para todos os que lhe obedecem;', 9, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chamado por Deus sumo sacerdote, segundo a ordem de Melquisedeque.', 10, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Do qual muito temos que dizer, de difícil interpretação; porquanto vos fizestes negligentes para ouvir.', 11, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, devendo já ser mestres pelo tempo, ainda necessitais de que se vos torne a ensinar quais sejam os primeiros rudimentos das palavras de Deus; e vos haveis feito tais que necessitais de leite, e não de sólido mantimento.', 12, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque qualquer que ainda se alimenta de leite não está experimentado na palavra da justiça, porque é menino.', 13, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o mantimento sólido é para os perfeitos, os quais, em razão do costume, têm os sentidos exercitados para discernir tanto o bem como o mal.', 14, 5, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi um novo céu, e uma nova terra. Porque já o primeiro céu e a primeira terra passaram, e o mar já não existe.', 1, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu, João, vi a santa cidade, a nova Jerusalém, que de Deus descia do céu, adereçada como uma esposa ataviada para o seu marido.', 2, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi uma grande voz do céu, que dizia: Eis aqui o tabernáculo de Deus com os homens, pois com eles habitará, e eles serão o seu povo, e o mesmo Deus estará com eles, e será o seu Deus.', 3, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Deus limpará de seus olhos toda a lágrima; e não haverá mais morte, nem pranto, nem clamor, nem dor; porque já as primeiras coisas são passadas.', 4, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que estava assentado sobre o trono disse: Eis que faço novas todas as coisas. E disse-me: Escreve; porque estas palavras são verdadeiras e fiéis.', 5, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-me mais: Está cumprido. Eu sou o Alfa e o Ômega, o princípio e o fim. A quem quer que tiver sede, de graça lhe darei da fonte da água da vida.', 6, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem vencer, herdará todas as coisas; e eu serei seu Deus, e ele será meu filho.', 7, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quanto aos tímidos, e aos incrédulos, e aos abomináveis, e aos homicidas, e aos que se prostituem, e aos feiticeiros, e aos idólatras e a todos os mentirosos, a sua parte será no lago que arde com fogo e enxofre; o que é a segunda morte.', 8, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E veio a mim um dos sete anjos que tinham as sete taças cheias das últimas sete pragas, e falou comigo, dizendo: Vem, mostrar-te-ei a esposa, a mulher do Cordeiro.', 9, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levou-me em espírito a um grande e alto monte, e mostrou-me a grande cidade, a santa Jerusalém, que de Deus descia do céu.', 10, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tinha a glória de Deus; e a sua luz era semelhante a uma pedra preciosíssima, como a pedra de jaspe, como o cristal resplandecente.', 11, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tinha um grande e alto muro com doze portas, e nas portas doze anjos, e nomes escritos sobre elas, que são os nomes das doze tribos dos filhos de Israel.', 12, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Do lado do levante tinha três portas, do lado do norte, três portas, do lado do sul, três portas, do lado do poente, três portas.', 13, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o muro da cidade tinha doze fundamentos, e neles os nomes dos doze apóstolos do Cordeiro.', 14, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele que falava comigo tinha uma cana de ouro, para medir a cidade, e as suas portas, e o seu muro.', 15, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a cidade estava situada em quadrado; e o seu comprimento era tanto como a sua largura. E mediu a cidade com a cana até doze mil estádios; e o seu comprimento, largura e altura eram iguais.', 16, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mediu o seu muro, de cento e quarenta e quatro côvados, conforme à medida de homem, que é a de um anjo.', 17, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a construção do seu muro era de jaspe, e a cidade de ouro puro, semelhante a vidro puro.', 18, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os fundamentos do muro da cidade estavam adornados de toda a pedra preciosa. O primeiro fundamento era jaspe; o segundo, safira; o terceiro, calcedônia; o quarto, esmeralda;', 19, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O quinto, sardônica; o sexto, sárdio; o sétimo, crisólito; o oitavo, berilo; o nono, topázio; o décimo, crisópraso; o undécimo, jacinto; o duodécimo, ametista.', 20, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as doze portas eram doze pérolas; cada uma das portas era uma pérola; e a praça da cidade de ouro puro, como vidro transparente.', 21, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nela não vi templo, porque o seu templo é o Senhor Deus Todo-Poderoso, e o Cordeiro.', 22, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a cidade não necessita de sol nem de lua, para que nela resplandeçam, porque a glória de Deus a tem iluminado, e o Cordeiro é a sua lâmpada.', 23, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as nações dos salvos andarão à sua luz; e os reis da terra trarão para ela a sua glória e honra.', 24, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as suas portas não se fecharão de dia, porque ali não haverá noite.', 25, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a ela trarão a glória e honra das nações.', 26, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não entrará nela coisa alguma que contamine, e cometa abominação e mentira; mas só os que estão inscritos no livro da vida do Cordeiro.', 27, 21, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Temamos, pois, que, porventura, deixada a promessa de entrar no seu repouso, pareça que algum de vós fica para trás.', 1, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também a nós foram pregadas as boas novas, como a eles, mas a palavra da pregação nada lhes aproveitou, porquanto não estava misturada com a fé naqueles que a ouviram.', 2, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nós, os que temos crido, entramos no repouso, tal como disse:Assim jurei na minha ira Que não entrarão no meu repouso; embora as suas obras estivessem acabadas desde a fundação do mundo.', 3, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque em certo lugar disse assim do dia sétimo: E repousou Deus de todas as suas obras no sétimo dia.', 4, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra vez neste lugar:Não entrarão no meu repouso.', 5, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto, pois, que resta que alguns entrem nele, e que aqueles a quem primeiro foram pregadas as boas novas não entraram por causa da desobediência,', 6, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Determina outra vez um certo dia, Hoje, dizendo por Davi, muito tempo depois, como está dito: Hoje, se ouvirdes a sua voz, Não endureçais os vossos corações.', 7, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se Josué lhes houvesse dado repouso, não falaria depois disso de outro dia.', 8, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, resta ainda um repouso para o povo de Deus.', 9, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque aquele que entrou no seu repouso, ele próprio repousou de suas obras, como Deus das suas.', 10, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Procuremos, pois, entrar naquele repouso, para que ninguém caia no mesmo exemplo de desobediência.', 11, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a palavra de Deus é viva e eficaz, e mais penetrante do que espada alguma de dois gumes, e penetra até à divisão da alma e do espírito, e das juntas e medulas, e é apta para discernir os pensamentos e intenções do coração.', 12, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não há criatura alguma encoberta diante dele; antes todas as coisas estão nuas e patentes aos olhos daquele com quem temos de tratar.', 13, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto que temos um grande sumo sacerdote, Jesus, Filho de Deus, que penetrou nos céus, retenhamos firmemente a nossa confissão.', 14, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não temos um sumo sacerdote que não possa compadecer-se das nossas fraquezas; porém, um que, como nós, em tudo foi tentado, mas sem pecado.', 15, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cheguemos, pois, com confiança ao trono da graça, para que possamos alcançar misericórdia e achar graça, a fim de sermos ajudados em tempo oportuno.', 16, 4, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, também a primeira tinha ordenanças de culto divino, e um santuário terrestre.', 1, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque um tabernáculo estava preparado, o primeiro, em que havia o candeeiro, e a mesa, e os pães da proposição; ao que se chama o santuário.', 2, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas depois do segundo véu estava o tabernáculo que se chama o santo dos santos,', 3, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que tinha o incensário de ouro, e a arca da aliança, coberta de ouro toda em redor; em que estava um vaso de ouro, que continha o maná, e a vara de Arão, que tinha florescido, e as tábuas da aliança;', 4, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sobre a arca os querubins da glória, que faziam sombra no propiciatório; das quais coisas não falaremos agora particularmente.', 5, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, estando estas coisas assim preparadas, a todo o tempo entravam os sacerdotes no primeiro tabernáculo, cumprindo os serviços;', 6, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, no segundo, só o sumo sacerdote, uma vez no ano, não sem sangue, que oferecia por si mesmo e pelas culpas do povo;', 7, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dando nisto a entender o Espírito Santo que ainda o caminho do santuário não estava descoberto enquanto se conservava em pé o primeiro tabernáculo,', 8, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que é uma alegoria para o tempo presente, em que se oferecem dons e sacrifícios que, quanto à consciência, não podem aperfeiçoar aquele que faz o serviço;', 9, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Consistindo somente em comidas, e bebidas, e várias abluções e justificações da carne, impostas até ao tempo da correção.', 10, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, vindo Cristo, o sumo sacerdote dos bens futuros, por um maior e mais perfeito tabernáculo, não feito por mãos, isto é, não desta criação,', 11, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem por sangue de bodes e bezerros, mas por seu próprio sangue, entrou uma vez no santuário, havendo efetuado uma eterna redenção.', 12, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se o sangue dos touros e bodes, e a cinza de uma novilha esparzida sobre os imundos, os santifica, quanto à purificação da carne,', 13, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto mais o sangue de Cristo, que pelo Espírito eterno se ofereceu a si mesmo imaculado a Deus, purificará as vossas consciências das obras mortas, para servirdes ao Deus vivo?', 14, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por isso é Mediador de um novo testamento, para que, intervindo a morte para remissão das transgressões que havia debaixo do primeiro testamento, os chamados recebam a promessa da herança eterna.', 15, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque onde há testamento, é necessário que intervenha a morte do testador.', 16, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque um testamento tem força onde houve morte; ou terá ele algum valor enquanto o testador vive?', 17, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso também o primeiro não foi consagrado sem sangue;', 18, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, havendo Moisés anunciado a todo o povo todos os mandamentos segundo a lei, tomou o sangue dos bezerros e dos bodes, com água, lã purpúrea e hissopo, e aspergiu tanto o mesmo livro como todo o povo,', 19, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Este é o sangue do testamento que Deus vos tem mandado.', 20, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E semelhantemente aspergiu com sangue o tabernáculo e todos os vasos do ministério.', 21, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quase todas as coisas, segundo a lei, se purificam com sangue; e sem derramamento de sangue não há remissão.', 22, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que era bem necessário que as figuras das coisas que estão no céu assim se purificassem; mas as próprias coisas celestiais com sacrifícios melhores do que estes.', 23, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Cristo não entrou num santuário feito por mãos, figura do verdadeiro, porém no mesmo céu, para agora comparecer por nós perante a face de Deus;', 24, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem também para a si mesmo se oferecer muitas vezes, como o sumo sacerdote cada ano entra no santuário com sangue alheio;', 25, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De outra maneira, necessário lhe fora padecer muitas vezes desde a fundação do mundo. Mas agora na consumação dos séculos uma vez se manifestou, para aniquilar o pecado pelo sacrifício de si mesmo.', 26, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como aos homens está ordenado morrerem uma vez, vindo depois disso o juízo,', 27, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também Cristo, oferecendo-se uma vez para tirar os pecados de muitos, aparecerá segunda vez, sem pecado, aos que o esperam para salvação.', 28, 9, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, servo de Jesus Cristo, chamado para apóstolo, separado para o evangelho de Deus.', 1, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual antes prometeu pelos seus profetas nas santas escrituras,', 2, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Acerca de seu Filho, que nasceu da descendência de Davi segundo a carne,', 3, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Declarado Filho de Deus em poder, segundo o Espírito de santificação, pela ressurreição dentre os mortos, Jesus Cristo, nosso Senhor,', 4, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelo qual recebemos a graça e o apostolado, para a obediência da fé entre todas as gentes pelo seu nome,', 5, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entre as quais sois também vós chamados para serdes de Jesus Cristo.', 6, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A todos os que estais em Roma, amados de Deus, chamados santos: Graça e paz de Deus nosso Pai, e do Senhor Jesus Cristo.', 7, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Primeiramente dou graças ao meu Deus por Jesus Cristo, acerca de vós todos, porque em todo o mundo é anunciada a vossa fé.', 8, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus, a quem sirvo em meu espírito, no evangelho de seu Filho, me é testemunha de como incessantemente faço menção de vós,', 9, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pedindo sempre em minhas orações que nalgum tempo, pela vontade de Deus, se me ofereça boa ocasião de ir ter convosco.', 10, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque desejo ver-vos, para vos comunicar algum dom espiritual, a fim de que sejais confortados;', 11, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto é, para que juntamente convosco eu seja consolado pela fé mútua, assim vossa como minha.', 12, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não quero, porém, irmãos, que ignoreis que muitas vezes propus ir ter convosco (mas até agora tenho sido impedido) para também ter entre vós algum fruto, como também entre os demais gentios.', 13, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou devedor, tanto a gregos como a bárbaros, tanto a sábios como a ignorantes.', 14, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim, quanto está em mim, estou pronto para também vos anunciar o evangelho, a vós que estais em Roma.', 15, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não me envergonho do evangelho de Cristo, pois é o poder de Deus para salvação de todo aquele que crê; primeiro do judeu, e também do grego.', 16, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nele se descobre a justiça de Deus de fé em fé, como está escrito: Mas o justo viverá pela fé.', 17, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque do céu se manifesta a ira de Deus sobre toda a impiedade e injustiça dos homens, que detêm a verdade em injustiça.', 18, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto o que de Deus se pode conhecer neles se manifesta, porque Deus lho manifestou.', 19, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque as suas coisas invisíveis, desde a criação do mundo, tanto o seu eterno poder, como a sua divindade, se entendem, e claramente se vêem pelas coisas que estão criadas, para que eles fiquem inescusáveis;', 20, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto, tendo conhecido a Deus, não o glorificaram como Deus, nem lhe deram graças, antes em seus discursos se desvaneceram, e o seu coração insensato se obscureceu.', 21, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo-se sábios, tornaram-se loucos.', 22, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mudaram a glória do Deus incorruptível em semelhança da imagem de homem corruptível, e de aves, e de quadrúpedes, e de répteis.', 23, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso também Deus os entregou às concupiscências de seus corações, à imundícia, para desonrarem seus corpos entre si;', 24, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois mudaram a verdade de Deus em mentira, e honraram e serviram mais a criatura do que o Criador, que é bendito eternamente. Amém.', 25, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso Deus os abandonou às paixões infames. Porque até as suas mulheres mudaram o uso natural, no contrário à natureza.', 26, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, semelhantemente, também os homens, deixando o uso natural da mulher, se inflamaram em sua sensualidade uns para com os outros, homens com homens, cometendo torpeza e recebendo em si mesmos a recompensa que convinha ao seu erro.', 27, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como eles não se importaram de ter conhecimento de Deus, assim Deus os entregou a um sentimento perverso, para fazerem coisas que não convêm;', 28, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estando cheios de toda a iniqüidade, fornicação, malícia, avareza, maldade; cheios de inveja, homicídio, contenda, engano, malignidade;', 29, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo murmuradores, detratores, aborrecedores de Deus, injuriadores, soberbos, presunçosos, inventores de males, desobedientes aos pais e às mães;', 30, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Néscios, infiéis nos contratos, sem afeição natural, irreconciliáveis, sem misericórdia;', 31, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais, conhecendo o juízo de Deus (que são dignos de morte os que tais coisas praticam), não somente as fazem, mas também consentem aos que as fazem.', 32, 1, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, a fé é o firme fundamento das coisas que se esperam, e a prova das coisas que se não vêem.', 1, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque por ela os antigos alcançaram testemunho.', 2, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé entendemos que os mundos pela palavra de Deus foram criados; de maneira que aquilo que se vê não foi feito do que é aparente.', 3, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé Abel ofereceu a Deus maior sacrifício do que Caim, pelo qual alcançou testemunho de que era justo, dando Deus testemunho dos seus dons, e por ela, depois de morto, ainda fala.', 4, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé Enoque foi trasladado para não ver a morte, e não foi achado, porque Deus o trasladara; visto como antes da sua trasladação alcançou testemunho de que agradara a Deus.', 5, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, sem fé é impossível agradar-lhe; porque é necessário que aquele que se aproxima de Deus creia que ele existe, e que é galardoador dos que o buscam.', 6, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé Noé, divinamente avisado das coisas que ainda não se viam, temeu e, para salvação da sua família, preparou a arca, pela qual condenou o mundo, e foi feito herdeiro da justiça que é segundo a fé.', 7, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé Abraão, sendo chamado, obedeceu, indo para um lugar que havia de receber por herança; e saiu, sem saber para onde ia.', 8, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé habitou na terra da promessa, como em terra alheia, morando em cabanas com Isaque e Jacó, herdeiros com ele da mesma promessa.', 9, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque esperava a cidade que tem fundamentos, da qual o artífice e construtor é Deus.', 10, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé também a mesma Sara recebeu a virtude de conceber, e deu à luz já fora da idade; porquanto teve por fiel aquele que lho tinha prometido.', 11, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso também de um, e esse já amortecido, descenderam tantos, em multidão, como as estrelas do céu, e como a areia inumerável que está na praia do mar.', 12, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos estes morreram na fé, sem terem recebido as promessas; mas vendo-as de longe, e crendo-as e abraçando-as, confessaram que eram estrangeiros e peregrinos na terra.', 13, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, os que isto dizem, claramente mostram que buscam uma pátria.', 14, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se, na verdade, se lembrassem daquela de onde haviam saído, teriam oportunidade de tornar.', 15, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora desejam uma melhor, isto é, a celestial. Por isso também Deus não se envergonha deles, de se chamar seu Deus, porque já lhes preparou uma cidade.', 16, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé ofereceu Abraão a Isaque, quando foi provado; sim, aquele que recebera as promessas ofereceu o seu unigênito.', 17, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo-lhe dito: Em Isaque será chamada a tua descendência, considerou que Deus era poderoso para até dentre os mortos o ressuscitar;', 18, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E daí também em figura ele o recobrou.', 19, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé Isaque abençoou Jacó e Esaú, no tocante às coisas futuras.', 20, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé Jacó, próximo da morte, abençoou cada um dos filhos de José, e adorou encostado à ponta do seu bordão.', 21, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé José, próximo da morte, fez menção da saída dos filhos de Israel, e deu ordem acerca de seus ossos.', 22, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé Moisés, já nascido, foi escondido três meses por seus pais, porque viram que era um menino formoso; e não temeram o mandamento do rei.', 23, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé Moisés, sendo já grande, recusou ser chamado filho da filha de Faraó,', 24, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Escolhendo antes ser maltratado com o povo de Deus, do que por um pouco de tempo ter o gozo do pecado;', 25, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo por maiores riquezas o vitupério de Cristo do que os tesouros do Egito; porque tinha em vista a recompensa.', 26, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé deixou o Egito, não temendo a ira do rei; porque ficou firme, como vendo o invisível.', 27, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé celebrou a páscoa e a aspersão do sangue, para que o destruidor dos primogênitos lhes não tocasse.', 28, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé passaram o Mar Vermelho, como por terra seca; o que intentando os egípcios, se afogaram.', 29, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé caíram os muros de Jericó, sendo rodeados durante sete dias.', 30, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela fé Raabe, a meretriz, não pereceu com os incrédulos, acolhendo em paz os espias.', 31, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que mais direi? Faltar-me-ia o tempo contando de Gideão, e de Baraque, e de Sansão, e de Jefté, e de Davi, e de Samuel e dos profetas,', 32, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais pela fé venceram reinos, praticaram a justiça, alcançaram promessas, fecharam as bocas dos leões,', 33, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Apagaram a força do fogo, escaparam do fio da espada, da fraqueza tiraram forças, na batalha se esforçaram, puseram em fuga os exércitos dos estranhos.', 34, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As mulheres receberam pela ressurreição os seus mortos; uns foram torturados, não aceitando o seu livramento, para alcançarem uma melhor ressurreição;', 35, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outros experimentaram escárnios e açoites, e até cadeias e prisões.', 36, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Foram apedrejados, serrados, tentados, mortos ao fio da espada; andaram vestidos de peles de ovelhas e de cabras, desamparados, aflitos e maltratados', 37, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Dos quais o mundo não era digno), errantes pelos desertos, e montes, e pelas covas e cavernas da terra.', 38, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos estes, tendo tido testemunho pela fé, não alcançaram a promessa,', 39, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Provendo Deus alguma coisa melhor a nosso respeito, para que eles sem nós não fossem aperfeiçoados.', 40, 11, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Revelação de Jesus Cristo, a qual Deus lhe deu, para mostrar aos seus servos as coisas que brevemente devem acontecer; e pelo seu anjo as enviou, e as notificou a João seu servo;', 1, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual testificou da palavra de Deus, e do testemunho de Jesus Cristo, e de tudo o que tem visto.', 2, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurado aquele que lê, e os que ouvem as palavras desta profecia, e guardam as coisas que nela estão escritas; porque o tempo está próximo.', 3, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('João, às sete igrejas que estão na Ásia: Graça e paz seja convosco da parte daquele que é, e que era, e que há de vir, e da dos sete espíritos que estão diante do seu trono;', 4, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E da parte de Jesus Cristo, que é a fiel testemunha, o primogênito dentre os mortos e o príncipe dos reis da terra. Àquele que nos amou, e em seu sangue nos lavou dos nossos pecados,', 5, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nos fez reis e sacerdotes para Deus e seu Pai; a ele glória e poder para todo o sempre. Amém.', 6, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que vem com as nuvens, e todo o olho o verá, até os mesmos que o traspassaram; e todas as tribos da terra se lamentarão sobre ele. Sim. Amém.', 7, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou o Alfa e o Ômega, o princípio e o fim, diz o Senhor, que é, e que era, e que há de vir, o Todo-Poderoso.', 8, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, João, que também sou vosso irmão, e companheiro na aflição, e no reino, e paciência de Jesus Cristo, estava na ilha chamada Patmos, por causa da palavra de Deus, e pelo testemunho de Jesus Cristo.', 9, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu fui arrebatado no Espírito no dia do Senhor, e ouvi detrás de mim uma grande voz, como de trombeta,', 10, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que dizia: Eu sou o Alfa e o Ômega, o primeiro e o derradeiro; e o que vês, escreve-o num livro, e envia-o às sete igrejas que estão na Ásia: a Éfeso, e a Esmirna, e a Pérgamo, e a Tiatira, e a Sardes, e a Filadélfia, e a Laodicéia.', 11, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E virei-me para ver quem falava comigo. E, virando-me, vi sete castiçais de ouro;', 12, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E no meio dos sete castiçais um semelhante ao Filho do homem, vestido até aos pés de uma roupa comprida, e cingido pelos peitos com um cinto de ouro.', 13, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a sua cabeça e cabelos eram brancos como lã branca, como a neve, e os seus olhos como chama de fogo;', 14, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus pés, semelhantes a latão reluzente, como se tivessem sido refinados numa fornalha, e a sua voz como a voz de muitas águas.', 15, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele tinha na sua destra sete estrelas; e da sua boca saía uma aguda espada de dois fios; e o seu rosto era como o sol, quando na sua força resplandece.', 16, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu, quando o vi, caí a seus pés como morto; e ele pôs sobre mim a sua destra, dizendo-me: Não temas; Eu sou o primeiro e o último;', 17, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que vivo e fui morto, mas eis aqui estou vivo para todo o sempre. Amém. E tenho as chaves da morte e do inferno.', 18, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Escreve as coisas que tens visto, e as que são, e as que depois destas hão de acontecer;', 19, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O mistério das sete estrelas, que viste na minha destra, e dos sete castiçais de ouro. As sete estrelas são os anjos das sete igrejas, e os sete castiçais, que viste, são as sete igrejas.', 20, 1, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas deliberei isto comigo mesmo: não ir mais ter convosco em tristeza.', 1, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se eu vos entristeço, quem é que me alegrará, senão aquele que por mim foi contristado?', 2, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E escrevi-vos isto mesmo, para que, quando lá for, não tenha tristeza da parte dos que deveriam alegrar-me; confiando em vós todos, que a minha alegria é a de todos vós.', 3, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque em muita tribulação e angústia do coração vos escrevi, com muitas lágrimas, não para que vos entristecêsseis, mas para que conhecêsseis o amor que abundantemente vos tenho.', 4, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se alguém me contristou, não me contristou a mim senão em parte, para vos não sobrecarregar a vós todos.', 5, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Basta-lhe ao tal esta repreensão feita por muitos.', 6, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que pelo contrário deveis antes perdoar-lhe e consolá-lo, para que o tal não seja de modo algum devorado de demasiada tristeza.', 7, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso vos rogo que confirmeis para com ele o vosso amor.', 8, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E para isso vos escrevi também, para por esta prova saber se sois obedientes em tudo.', 9, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a quem perdoardes alguma coisa, também eu; porque, o que eu também perdoei, se é que tenho perdoado, por amor de vós o fiz na presença de Cristo; para que não sejamos vencidos por Satanás;', 10, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não ignoramos os seus ardís.', 11, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, quando cheguei a Trôade para pregar o evangelho de Cristo, e abrindo-se-me uma porta no Senhor,', 12, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não tive descanso no meu espírito, porque não achei ali meu irmão Tito; mas, despedindo-me deles, parti para a macedônia.', 13, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E graças a Deus, que sempre nos faz triunfar em Cristo, e por meio de nós manifesta em todo o lugar a fragrância do seu conhecimento.', 14, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque para Deus somos o bom perfume de Cristo, nos que se salvam e nos que se perdem.', 15, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para estes certamente cheiro de morte para morte; mas para aqueles cheiro de vida para vida. E para estas coisas quem é idôneo?', 16, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nós não somos, como muitos, falsificadores da palavra de Deus, antes falamos de Cristo com sinceridade, como de Deus na presença de Deus.', 17, 2, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, a suma do que temos dito é que temos um sumo sacerdote tal, que está assentado nos céus à destra do trono da majestade,', 1, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ministro do santuário, e do verdadeiro tabernáculo, o qual o Senhor fundou, e não o homem.', 2, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todo o sumo sacerdote é constituído para oferecer dons e sacrifícios; por isso era necessário que este também tivesse alguma coisa que oferecer.', 3, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, se ele estivesse na terra, nem tão pouco sacerdote seria, havendo ainda sacerdotes que oferecem dons segundo a lei,', 4, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais servem de exemplo e sombra das coisas celestiais, como Moisés divinamente foi avisado, estando já para acabar o tabernáculo; porque foi dito: Olha, faze tudo conforme o modelo que no monte se te mostrou.', 5, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora alcançou ele ministério tanto mais excelente, quanto é mediador de uma melhor aliança que está confirmada em melhores promessas.', 6, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se aquela primeira fora irrepreensível, nunca se teria buscado lugar para a segunda.', 7, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, repreendendo-os, lhes diz: Eis que virão dias, diz o Senhor, Em que com a casa de Israel e com a casa de Judá estabelecerei uma nova aliança,', 8, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não segundo a aliança que fiz com seus pais No dia em que os tomei pela mão, para os tirar da terra do Egito;Como não permaneceram naquela minha aliança,Eu para eles não atentei, diz o Senhor.', 9, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque esta é a aliança que depois daqueles diasFarei com a casa de Israel, diz o Senhor; Porei as minhas leis no seu entendimento, E em seu coração as escreverei; E eu lhes serei por Deus, E eles me serão por povo;', 10, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não ensinará cada um a seu próximo, Nem cada um ao seu irmão, dizendo: Conhece o Senhor; Porque todos me conhecerão, Desde o menor deles até ao maior.', 11, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque serei misericordioso para com suas iniqüidades, E de seus pecados e de suas prevaricações não me lembrarei mais.', 12, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo Nova aliança, envelheceu a primeira. Ora, o que foi tornado velho, e se envelhece, perto está de acabar.', 13, 8, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque este Melquisedeque, que era rei de Salém, sacerdote do Deus Altíssimo, e que saiu ao encontro de Abraão quando ele regressava da matança dos reis, e o abençoou;', 1, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A quem também Abraão deu o dízimo de tudo, e primeiramente é, por interpretação, rei de justiça, e depois também rei de Salém, que é rei de paz;', 2, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sem pai, sem mãe, sem genealogia, não tendo princípio de dias nem fim de vida, mas sendo feito semelhante ao Filho de Deus, permanece sacerdote para sempre.', 3, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Considerai, pois, quão grande era este, a quem até o patriarca Abraão deu os dízimos dos despojos.', 4, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que dentre os filhos de Levi recebem o sacerdócio têm ordem, segundo a lei, de tomar o dízimo do povo, isto é, de seus irmãos, ainda que tenham saído dos lombos de Abraão.', 5, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas aquele, cuja genealogia não é contada entre eles, tomou dízimos de Abraão, e abençoou o que tinha as promessas.', 6, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, sem contradição alguma, o menor é abençoado pelo maior.', 7, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aqui certamente tomam dízimos homens que morrem; ali, porém, aquele de quem se testifica que vive.', 8, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, por assim dizer, por meio de Abraão, até Levi, que recebe dízimos, pagou dízimos.', 9, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ainda ele estava nos lombos de seu pai quando Melquisedeque lhe saiu ao encontro.', 10, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que, se a perfeição fosse pelo sacerdócio levítico (porque sob ele o povo recebeu a lei), que necessidade havia logo de que outro sacerdote se levantasse, segundo a ordem de Melquisedeque, e não fosse chamado segundo a ordem de Arão?', 11, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, mudando-se o sacerdócio, necessariamente se faz também mudança da lei.', 12, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque aquele de quem estas coisas se dizem pertence a outra tribo, da qual ninguém serviu ao altar,', 13, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto ser manifesto que nosso Senhor procedeu de Judá, e concernente a essa tribo nunca Moisés falou de sacerdócio.', 14, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muito mais manifesto é ainda, se à semelhança de Melquisedeque se levantar outro sacerdote,', 15, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que não foi feito segundo a lei do mandamento carnal, mas segundo a virtude da vida incorruptível.', 16, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ele assim testifica: Tu és sacerdote eternamente, Segundo a ordem de Melquisedeque.', 17, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o precedente mandamento é abrogado por causa da sua fraqueza e inutilidade', 18, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Pois a lei nenhuma coisa aperfeiçoou) e desta sorte é introduzida uma melhor esperança, pela qual chegamos a Deus.', 19, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E visto como não é sem prestar juramento (porque certamente aqueles, sem juramento, foram feitos sacerdotes,', 20, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas este com juramento por aquele que lhe disse: Jurou o Senhor, e não se arrependerá; Tu és sacerdote eternamente, Segundo a ordem de Melquisedeque,', 21, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De tanto melhor aliança Jesus foi feito fiador.', 22, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, na verdade, aqueles foram feitos sacerdotes em grande número, porque pela morte foram impedidos de permanecer,', 23, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas este, porque permanece eternamente, tem um sacerdócio perpétuo.', 24, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, pode também salvar perfeitamente os que por ele se chegam a Deus, vivendo sempre para interceder por eles.', 25, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nos convinha tal sumo sacerdote, santo, inocente, imaculado, separado dos pecadores, e feito mais sublime do que os céus;', 26, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que não necessitasse, como os sumos sacerdotes, de oferecer cada dia sacrifícios, primeiramente por seus próprios pecados, e depois pelos do povo; porque isto fez ele, uma vez, oferecendo-se a si mesmo.', 27, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a lei constitui sumos sacerdotes a homens fracos, mas a palavra do juramento, que veio depois da lei, constitui ao Filho, perfeito para sempre.', 28, 7, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, deixando os rudimentos da doutrina de Cristo, prossigamos até à perfeição, não lançando de novo o fundamento do arrependimento de obras mortas e de fé em Deus,', 1, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E da doutrina dos batismos, e da imposição das mãos, e da ressurreição dos mortos, e do juízo eterno.', 2, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E isto faremos, se Deus o permitir.', 3, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque é impossível que os que já uma vez foram iluminados, e provaram o dom celestial, e se fizeram participantes do Espírito Santo,', 4, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E provaram a boa palavra de Deus, e as virtudes do século futuro,', 5, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E recaíram, sejam outra vez renovados para arrependimento; pois assim, quanto a eles, de novo crucificam o Filho de Deus, e o expõem ao vitupério.', 6, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a terra que embebe a chuva, que muitas vezes cai sobre ela, e produz erva proveitosa para aqueles por quem é lavrada, recebe a bênção de Deus;', 7, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a que produz espinhos e abrolhos, é reprovada, e perto está da maldição; o seu fim é ser queimada.', 8, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas de vós, ó amados, esperamos coisas melhores, e coisas que acompanham a salvação, ainda que assim falamos.', 9, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus não é injusto para se esquecer da vossa obra, e do trabalho do amor que para com o seu nome mostrastes, enquanto servistes aos santos; e ainda servis.', 10, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas desejamos que cada um de vós mostre o mesmo cuidado até ao fim, para completa certeza da esperança;', 11, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que vos não façais negligentes, mas sejais imitadores dos que pela fé e paciência herdam as promessas.', 12, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, quando Deus fez a promessa a Abraão, como não tinha outro maior por quem jurasse, jurou por si mesmo,', 13, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Certamente, abençoando te abençoarei, e multiplicando te multiplicarei.', 14, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim, esperando com paciência, alcançou a promessa.', 15, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os homens certamente juram por alguém superior a eles, e o juramento para confirmação é, para eles, o fim de toda a contenda.', 16, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, querendo Deus mostrar mais abundantemente a imutabilidade do seu conselho aos herdeiros da promessa, se interpôs com juramento;', 17, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que por duas coisas imutáveis, nas quais é impossível que Deus minta, tenhamos a firme consolação, nós, os que pomos o nosso refúgio em reter a esperança proposta;', 18, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A qual temos como âncora da alma, segura e firme, e que penetra até ao interior do véu,', 19, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde Jesus, nosso precursor, entrou por nós, feito eternamente sumo sacerdote, segundo a ordem de Melquisedeque.', 20, 6, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qual é logo a vantagem do judeu? Ou qual a utilidade da circuncisão?', 1, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Muita, em toda a maneira, porque, primeiramente, as palavras de Deus lhe foram confiadas.', 2, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois quê? Se alguns foram incrédulos, a sua incredulidade aniquilará a fidelidade de Deus?', 3, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira nenhuma; sempre seja Deus verdadeiro, e todo o homem mentiroso; como está escrito:Para que sejas justificado em tuas palavras,E venças quando fores julgado.', 4, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se a nossa injustiça for causa da justiça de Deus, que diremos? Porventura será Deus injusto, trazendo ira sobre nós? (Falo como homem. )', 5, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira nenhuma; de outro modo, como julgará Deus o mundo?', 6, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se pela minha mentira abundou mais a verdade de Deus para glória sua, por que sou eu ainda julgado também como pecador?', 7, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por que não dizemos (como somos blasfemados, e como alguns dizem que dizemos): Façamos males, para que venham bens? A condenação desses é justa.', 8, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois quê? Somos nós mais excelentes? De maneira nenhuma, pois já dantes demonstramos que, tanto judeus como gregos, todos estão debaixo do pecado;', 9, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como está escrito:Não há um justo, nem um sequer.', 10, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não há ninguém que entenda;Não há ninguém que busque a Deus.', 11, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos se extraviaram, e juntamente se fizeram inúteis.Não há quem faça o bem, não há nem um só.', 12, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A sua garganta é um sepulcro aberto;Com as suas línguas tratam enganosamente;Peçonha de áspides está debaixo de seus lábios;', 13, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cuja boca está cheia de maldição e amargura.', 14, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os seus pés são ligeiros para derramar sangue.', 15, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em seus caminhos há destruição e miséria;', 16, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não conheceram o caminho da paz.', 17, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não há temor de Deus diante de seus olhos.', 18, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, nós sabemos que tudo o que a lei diz, aos que estão debaixo da lei o diz, para que toda a boca esteja fechada e todo o mundo seja condenável diante de Deus.', 19, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso nenhuma carne será justificada diante dele pelas obras da lei, porque pela lei vem o conhecimento do pecado.', 20, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora se manifestou sem a lei a justiça de Deus, tendo o testemunho da lei e dos profetas;', 21, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto é, a justiça de Deus pela fé em Jesus Cristo para todos e sobre todos os que crêem; porque não há diferença.', 22, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos pecaram e destituídos estão da glória de Deus;', 23, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sendo justificados gratuitamente pela sua graça, pela redenção que há em Cristo Jesus.', 24, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao qual Deus propôs para propiciação pela fé no seu sangue, para demonstrar a sua justiça pela remissão dos pecados dantes cometidos, sob a paciência de Deus;', 25, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para demonstração da sua justiça neste tempo presente, para que ele seja justo e justificador daquele que tem fé em Jesus.', 26, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde está logo a jactância? É excluída. Por qual lei? Das obras? Não; mas pela lei da fé.', 27, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Concluímos, pois, que o homem é justificado pela fé sem as obras da lei.', 28, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É porventura Deus somente dos judeus? E não o é também dos gentios? Também dos gentios, certamente,', 29, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto que Deus é um só, que justifica pela fé a circuncisão, e por meio da fé a incircuncisão.', 30, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Anulamos, pois, a lei pela fé? De maneira nenhuma, antes estabelecemos a lei.', 31, 3, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, convém-nos atentar com mais diligência para as coisas que já temos ouvido, para que em tempo algum nos desviemos delas.', 1, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se a palavra falada pelos anjos permaneceu firme, e toda a transgressão e desobediência recebeu a justa retribuição,', 2, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como escaparemos nós, se não atentarmos para uma tão grande salvação, a qual, começando a ser anunciada pelo Senhor, foi-nos depois confirmada pelos que a ouviram;', 3, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Testificando também Deus com eles, por sinais, e milagres, e várias maravilhas e dons do Espírito Santo, distribuídos por sua vontade?', 4, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não foi aos anjos que sujeitou o mundo futuro, de que falamos.', 5, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas em certo lugar testificou alguém, dizendo:Que é o homem, para que dele te lembres?Ou o filho do homem, para que o visites?', 6, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu o fizeste um pouco menor do que os anjos,De glória e de honra o coroaste,E o constituíste sobre as obras de tuas mãos;', 7, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todas as coisas lhe sujeitaste debaixo dos pés.Ora, visto que lhe sujeitou todas as coisas, nada deixou que lhe não esteja sujeito. Mas agora ainda não vemos que todas as coisas lhe estejam sujeitas.', 8, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vemos, porém, coroado de glória e de honra aquele Jesus que fora feito um pouco menor do que os anjos, por causa da paixão da morte, para que, pela graça de Deus, provasse a morte por todos.', 9, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque convinha que aquele, para quem são todas as coisas, e mediante quem tudo existe, trazendo muitos filhos à glória, consagrasse pelas aflições o príncipe da salvação deles.', 10, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, assim o que santifica, como os que são santificados, são todos de um; por cuja causa não se envergonha de lhes chamar irmãos,', 11, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo:Anunciarei o teu nome a meus irmãos,Cantar-te-ei louvores no meio da congregação.', 12, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra vez: Porei nele a minha confiança. E outra vez: Eis-me aqui a mim, e aos filhos que Deus me deu.', 13, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, visto como os filhos participam da carne e do sangue, também ele participou das mesmas coisas, para que pela morte aniquilasse o que tinha o império da morte, isto é, o diabo;', 14, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E livrasse todos os que, com medo da morte, estavam por toda a vida sujeitos à servidão.', 15, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, na verdade, ele não tomou os anjos, mas tomou a descendência de Abraão.', 16, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso convinha que em tudo fosse semelhante aos irmãos, para ser misericordioso e fiel sumo sacerdote naquilo que é de Deus, para expiar os pecados do povo.', 17, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque naquilo que ele mesmo, sendo tentado, padeceu, pode socorrer aos que são tentados.', 18, 2, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Havendo Deus antigamente falado muitas vezes, e de muitas maneiras, aos pais, pelos profetas, a nós falou-nos nestes últimos dias pelo Filho,', 1, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A quem constituiu herdeiro de tudo, por quem fez também o mundo.', 2, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, sendo o resplendor da sua glória, e a expressa imagem da sua pessoa, e sustentando todas as coisas pela palavra do seu poder, havendo feito por si mesmo a purificação dos nossos pecados, assentou-se à destra da majestade nas alturas;', 3, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Feito tanto mais excelente do que os anjos, quanto herdou mais excelente nome do que eles.', 4, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, a qual dos anjos disse jamais: Tu és meu Filho, Hoje te gerei? E outra vez: Eu lhe serei por Pai, E ele me será por Filho?', 5, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra vez, quando introduz no mundo o primogênito, diz:E todos os anjos de Deus o adorem.', 6, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quanto aos anjos, diz: Faz dos seus anjos espíritos, E de seus ministros labareda de fogo.', 7, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, do Filho, diz: Ó Deus, o teu trono subsiste pelos séculos dos séculos; Cetro de eqüidade é o cetro do teu reino.', 8, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amaste a justiça e odiaste a iniqüidade; por isso Deus, o teu Deus, te ungiuCom óleo de alegria mais do que a teus companheiros.', 9, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E: Tu, Senhor, no princípio fundaste a terra, E os céus são obra de tuas mãos.', 10, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eles perecerão, mas tu permanecerás; E todos eles, como roupa, envelhecerão,', 11, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E como um manto os enrolarás, e serão mudados. Mas tu és o mesmo, E os teus anos não acabarão.', 12, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a qual dos anjos disse jamais: Assenta-te à minha destra, Até que ponha a teus inimigos por escabelo de teus pés?', 13, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não são porventura todos eles espíritos ministradores, enviados para servir a favor daqueles que hão de herdar a salvação?', 14, 1, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amados, escrevo-vos agora esta segunda carta, em ambas as quais desperto com exortação o vosso ânimo sincero;', 1, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que vos lembreis das palavras que primeiramente foram ditas pelos santos profetas, e do nosso mandamento, como apòstolos do Senhor e Salvador.', 2, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo primeiro isto, que nos últimos dias virão escarnecedores, andando segundo as suas próprias concupiscências,', 3, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizendo: Onde está a promessa da sua vinda? porque desde que os pais dormiram, todas as coisas permanecem como desde o princípio da criação.', 4, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eles voluntariamente ignoram isto, que pela palavra de Deus já desde a antiguidade existiram os céus, e a terra, que foi tirada da água e no meio da água subsiste.', 5, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelas quais coisas pereceu o mundo de então, coberto com as águas do dilúvio,', 6, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os céus e a terra que agora existem pela mesma palavra se reservam como tesouro, e se guardam para o fogo, até o dia do juízo, e da perdição dos homens ímpios.', 7, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, amados, não ignoreis uma coisa, que um dia para o Senhor é como mil anos, e mil anos como um dia.', 8, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Senhor não retarda a sua promessa, ainda que alguns a têm por tardia; mas é longânimo para conosco, não querendo que alguns se percam, senão que todos venham a arrepender-se.', 9, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o dia do Senhor virá como o ladrão de noite; no qual os céus passarão com grande estrondo, e os elementos, ardendo, se desfarão, e a terra, e as obras que nela há, se queimarão.', 10, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Havendo, pois, de perecer todas estas coisas, que pessoas vos convém ser em santo trato, e piedade,', 11, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aguardando, e apressando-vos para a vinda do dia de Deus, em que os céus, em fogo se desfarão, e os elementos, ardendo, se fundirão?', 12, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nós, segundo a sua promessa, aguardamos novos céus e nova terra, em que habita a justiça.', 13, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, amados, aguardando estas coisas, procurai que dele sejais achados imaculados e irrepreensíveis em paz.', 14, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tende por salvação a longanimidade de nosso Senhor; como também o nosso amado irmão Paulo vos escreveu, segundo a sabedoria que lhe foi dada;', 15, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Falando disto, como em todas as suas epístolas, entre as quais há pontos difíceis de entender, que os indoutos e inconstantes torcem, e igualmente as outras Escrituras, para sua própria perdição.', 16, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, portanto, amados, sabendo isto de antemão, guardai-vos de que, pelo engano dos homens abomináveis, sejais juntamente arrebatados, e descaiais da vossa firmeza;', 17, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes crescei na graça e conhecimento de nosso Senhor e Salvador, Jesus Cristo. A ele seja dada a glória, assim agora, como no dia da eternidade. Amém.', 18, 3, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tendo a lei a sombra dos bens futuros, e não a imagem exata das coisas, nunca, pelos mesmos sacrifícios que continuamente se oferecem cada ano, pode aperfeiçoar os que a eles se chegam.', 1, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Doutra maneira, teriam deixado de se oferecer, porque, purificados uma vez os ministrantes, nunca mais teriam consciência de pecado.', 2, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nesses sacrifícios, porém, cada ano se faz comemoração dos pecados,', 3, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque é impossível que o sangue dos touros e dos bodes tire os pecados.', 4, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, entrando no mundo, diz: Sacrifício e oferta não quiseste, Mas corpo me preparaste;', 5, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Holocaustos e oblações pelo pecado não te agradaram.', 6, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então disse: Eis aqui venho(No princípio do livro está escrito de mim),Para fazer, ó Deus, a tua vontade.', 7, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como acima diz: Sacrifício e oferta, e holocaustos e oblações pelo pecado não quiseste, nem te agradaram (os quais se oferecem segundo a lei).', 8, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então disse: Eis aqui venho, para fazer, ó Deus, a tua vontade. Tira o primeiro, para estabelecer o segundo.', 9, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na qual vontade temos sido santificados pela oblação do corpo de Jesus Cristo, feita uma vez.', 10, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim todo o sacerdote aparece cada dia, ministrando e oferecendo muitas vezes os mesmos sacrifícios, que nunca podem tirar os pecados;', 11, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas este, havendo oferecido para sempre um único sacrifício pelos pecados, está assentado à destra de Deus,', 12, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Daqui em diante esperando até que os seus inimigos sejam postos por escabelo de seus pés.', 13, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque com uma só oblação aperfeiçoou para sempre os que são santificados.', 14, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também o Espírito Santo no-lo testifica, porque depois de haver dito:', 15, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esta é a aliança que farei com eles Depois daqueles dias, diz o Senhor:Porei as minhas leis em seus corações,E as escreverei em seus entendimentos; acrescenta:', 16, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E jamais me lembrarei de seus pecados e de suas iniqüidades.', 17, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, onde há remissão destes, não há mais oblação pelo pecado.', 18, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo, pois, irmãos, ousadia para entrar no santuário, pelo sangue de Jesus,', 19, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelo novo e vivo caminho que ele nos consagrou, pelo véu, isto é, pela sua carne,', 20, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tendo um grande sacerdote sobre a casa de Deus,', 21, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cheguemo-nos com verdadeiro coração, em inteira certeza de fé, tendo os corações purificados da má consciência, e o corpo lavado com água limpa,', 22, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Retenhamos firmes a confissão da nossa esperança; porque fiel é o que prometeu.', 23, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E consideremo-nos uns aos outros, para nos estimularmos ao amor e às boas obras,', 24, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não deixando a nossa congregação, como é costume de alguns, antes admoestando-nos uns aos outros; e tanto mais, quanto vedes que se vai aproximando aquele dia.', 25, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se pecarmos voluntariamente, depois de termos recebido o conhecimento da verdade, já não resta mais sacrifício pelos pecados,', 26, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas uma certa expectação horrível de juízo, e ardor de fogo, que há de devorar os adversários.', 27, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quebrantando alguém a lei de Moisés, morre sem misericórdia, só pela palavra de duas ou três testemunhas.', 28, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De quanto maior castigo cuidais vós será julgado merecedor aquele que pisar o Filho de Deus, e tiver por profano o sangue da aliança com que foi santificado, e fizer agravo ao Espírito da graça?', 29, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque bem conhecemos aquele que disse: Minha é a vingança, eu darei a recompensa, diz o Senhor. E outra vez: O Senhor julgará o seu povo.', 30, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Horrenda coisa é cair nas mãos do Deus vivo.', 31, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lembrai-vos, porém, dos dias passados, em que, depois de serdes iluminados, suportastes grande combate de aflições.', 32, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em parte fostes feitos espetáculo com vitupérios e tribulações, e em parte fostes participantes com os que assim foram tratados.', 33, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também vos compadecestes das minhas prisões, e com alegria permitistes o roubo dos vossos bens, sabendo que em vós mesmos tendes nos céus uma possessão melhor e permanente.', 34, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não rejeiteis, pois, a vossa confiança, que tem grande e avultado galardão.', 35, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque necessitais de paciência, para que, depois de haverdes feito a vontade de Deus, possais alcançar a promessa.', 36, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ainda um pouquinho de tempo, E o que há de vir virá, e não tardará.', 37, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o justo viverá pela fé; E, se ele recuar, a minha alma não tem prazer nele.', 38, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nós, porém, não somos daqueles que se retiram para a perdição, mas daqueles que crêem para a conservação da alma.', 39, 10, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mostrou-me o rio puro da água da vida, claro como cristal, que procedia do trono de Deus e do Cordeiro.', 1, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No meio da sua praça, e de um e de outro lado do rio, estava a árvore da vida, que produz doze frutos, dando seu fruto de mês em mês; e as folhas da árvore são para a saúde das nações.', 2, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ali nunca mais haverá maldição contra alguém; e nela estará o trono de Deus e do Cordeiro, e os seus servos o servirão.', 3, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E verão o seu rosto, e nas suas testas estará o seu nome.', 4, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ali não haverá mais noite, e não necessitarão de lâmpada nem de luz do sol, porque o Senhor Deus os ilumina; e reinarão para todo o sempre.', 5, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-me: Estas palavras são fiéis e verdadeiras; e o Senhor, o Deus dos santos profetas, enviou o seu anjo, para mostrar aos seus servos as coisas que em breve hão de acontecer.', 6, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que presto venho: Bem-aventurado aquele que guarda as palavras da profecia deste livro.', 7, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu, João, sou aquele que vi e ouvi estas coisas. E, havendo-as ouvido e visto, prostrei-me aos pés do anjo que mas mostrava para o adorar.', 8, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-me: Olha, não faças tal; porque eu sou conservo teu e de teus irmãos, os profetas, e dos que guardam as palavras deste livro. Adora a Deus.', 9, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-me: Não seles as palavras da profecia deste livro; porque próximo está o tempo.', 10, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem é injusto, seja injusto ainda; e quem é sujo, seja sujo ainda; e quem é justo, seja justificado ainda; e quem é santo, seja santificado ainda.', 11, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, eis que cedo venho, e o meu galardão está comigo, para dar a cada um segundo a sua obra.', 12, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sou o Alfa e o Ômega, o princípio e o fim, o primeiro e o derradeiro.', 13, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados aqueles que guardam os seus mandamentos, para que tenham direito à árvore da vida, e possam entrar na cidade pelas portas.', 14, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, ficarão de fora os cães e os feiticeiros, e os que se prostituem, e os homicidas, e os idólatras, e qualquer que ama e comete a mentira.', 15, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, Jesus, enviei o meu anjo, para vos testificar estas coisas nas igrejas. Eu sou a raiz e a geração de Davi, a resplandecente estrela da manhã.', 16, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o Espírito e a esposa dizem: Vem. E quem ouve, diga: Vem. E quem tem sede, venha; e quem quiser, tome de graça da água da vida.', 17, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu testifico a todo aquele que ouvir as palavras da profecia deste livro que, se alguém lhes acrescentar alguma coisa, Deus fará vir sobre ele as pragas que estão escritas neste livro;', 18, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se alguém tirar quaisquer palavras do livro desta profecia, Deus tirará a sua parte do livro da vida, e da cidade santa, e das coisas que estão escritas neste livro.', 19, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que testifica estas coisas diz: Certamente cedo venho. Amém. Ora vem, Senhor Jesus.', 20, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça de nosso Senhor Jesus Cristo seja com todos vós. Amém.', 21, 22, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque sabemos que, se a nossa casa terrestre deste tabernáculo se desfizer, temos de Deus um edifício, uma casa não feita por mãos, eterna, nos céus.', 1, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por isso também gememos, desejando ser revestidos da nossa habitação, que é do céu;', 2, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, todavia, estando vestidos, não formos achados nus.', 3, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também nós, os que estamos neste tabernáculo, gememos carregados; não porque queremos ser despidos, mas revestidos, para que o mortal seja absorvido pela vida.', 4, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, quem para isto mesmo nos preparou foi Deus, o qual nos deu também o penhor do Espírito.', 5, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso estamos sempre de bom ânimo, sabendo que, enquanto estamos no corpo, vivemos ausentes do Senhor', 6, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Porque andamos por fé, e não por vista).', 7, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas temos confiança e desejamos antes deixar este corpo, para habitar com o Senhor.', 8, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois que muito desejamos também ser-lhe agradáveis, quer presentes, quer ausentes.', 9, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos devemos comparecer ante o tribunal de Cristo, para que cada um receba segundo o que tiver feito por meio do corpo, ou bem, ou mal.', 10, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim que, sabendo o temor que se deve ao Senhor, persuadimos os homens à fé, mas somos manifestos a Deus; e espero que nas vossas consciências sejamos também manifestos.', 11, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não nos recomendamos outra vez a vós; mas damo-vos ocasião de vos gloriardes de nós, para que tenhais que responder aos que se gloriam na aparência e não no coração.', 12, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se enlouquecemos, é para Deus; e, se conservamos o juízo, é para vós.', 13, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o amor de Cristo nos constrange, julgando nós assim: que, se um morreu por todos, logo todos morreram.', 14, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele morreu por todos, para que os que vivem não vivam mais para si, mas para aquele que por eles morreu e ressuscitou.', 15, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim que daqui por diante a ninguém conhecemos segundo a carne, e, ainda que também tenhamos conhecido Cristo segundo a carne, contudo agora já não o conhecemos deste modo.', 16, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim que, se alguém está em Cristo, nova criatura é; as coisas velhas já passaram; eis que tudo se fez novo.', 17, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tudo isto provém de Deus, que nos reconciliou consigo mesmo por Jesus Cristo, e nos deu o ministério da reconciliação;', 18, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto é, Deus estava em Cristo reconciliando consigo o mundo, não lhes imputando os seus pecados; e pôs em nós a palavra da reconciliação.', 19, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que somos embaixadores da parte de Cristo, como se Deus por nós rogasse. Rogamo-vos, pois, da parte de Cristo, que vos reconcilieis com Deus.', 20, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Àquele que não conheceu pecado, o fez pecado por nós; para que nele fôssemos feitos justiça de Deus.', 21, 5, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, és inescusável quando julgas, ó homem, quem quer que sejas, porque te condenas a ti mesmo naquilo em que julgas a outro; pois tu, que julgas, fazes o mesmo.', 1, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E bem sabemos que o juízo de Deus é segundo a verdade sobre os que tais coisas fazem.', 2, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tu, ó homem, que julgas os que fazem tais coisas, cuidas que, fazendo-as tu, escaparás ao juízo de Deus?', 3, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou desprezas tu as riquezas da sua benignidade, e paciência e longanimidade, ignorando que a benignidade de Deus te leva ao arrependimento?', 4, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, segundo a tua dureza e teu coração impenitente, entesouras ira para ti no dia da ira e da manifestação do juízo de Deus;', 5, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual recompensará cada um segundo as suas obras; a saber:', 6, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A vida eterna aos que, com perseverança em fazer bem, procuram glória, honra e incorrupção;', 7, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a indignação e a ira aos que são contenciosos, desobedientes à verdade e obedientes à iniqüidade;', 8, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tribulação e angústia sobre toda a alma do homem que faz o mal; primeiramente do judeu e também do grego;', 9, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Glória, porém, e honra e paz a qualquer que pratica o bem; primeiramente ao judeu e também ao grego;', 10, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, para com Deus, não há acepção de pessoas.', 11, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos os que sem lei pecaram, sem lei também perecerão; e todos os que sob a lei pecaram, pela lei serão julgados.', 12, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os que ouvem a lei não são justos diante de Deus, mas os que praticam a lei hão de ser justificados.', 13, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, quando os gentios, que não têm lei, fazem naturalmente as coisas que são da lei, não tendo eles lei, para si mesmos são lei;', 14, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais mostram a obra da lei escrita em seus corações, testificando juntamente a sua consciência, e os seus pensamentos, quer acusando-os, quer defendendo-os;', 15, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No dia em que Deus há de julgar os segredos dos homens, por Jesus Cristo, segundo o meu evangelho.', 16, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que tu que tens por sobrenome judeu, e repousas na lei, e te glorias em Deus;', 17, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sabes a sua vontade e aprovas as coisas excelentes, sendo instruído por lei;', 18, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E confias que és guia dos cegos, luz dos que estão em trevas,', 19, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Instruidor dos néscios, mestre de crianças, que tens a forma da ciência e da verdade na lei;', 20, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu, pois, que ensinas a outro, não te ensinas a ti mesmo? Tu, que pregas que não se deve furtar, furtas?', 21, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu, que dizes que não se deve adulterar, adulteras? Tu, que abominas os ídolos, cometes sacrilégio?', 22, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu, que te glorias na lei, desonras a Deus pela transgressão da lei?', 23, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, como está escrito, o nome de Deus é blasfemado entre os gentios por causa de vós.', 24, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a circuncisão é, na verdade, proveitosa, se tu guardares a lei; mas, se tu és transgressor da lei, a tua circuncisão se torna em incircuncisão.', 25, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, pois, a incircuncisão guardar os preceitos da lei, porventura a incircuncisão não será reputada como circuncisão?', 26, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a incircuncisão que por natureza o é, se cumpre a lei, não te julgará porventura a ti, que pela letra e circuncisão és transgressor da lei?', 27, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não é judeu o que o é exteriormente, nem é circuncisão a que o é exteriormente na carne.', 28, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas é judeu o que o é no interior, e circuncisão a que é do coração, no espírito, não na letra; cujo louvor não provém dos homens, mas de Deus.', 29, 2, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, amados, pois que temos tais promessas, purifiquemo-nos de toda a imundícia da carne e do espírito, aperfeiçoando a santificação no temor de Deus.', 1, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Recebei-nos em vossos corações; a ninguém agravamos, a ninguém corrompemos, de ninguém buscamos o nosso proveito.', 2, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não digo isto para vossa condenação; pois já antes tinha dito que estais em nossos corações para juntamente morrer e viver.', 3, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Grande é a ousadia da minha fala para convosco, e grande a minha jactância a respeito de vós; estou cheio de consolação; transbordo de gozo em todas as nossas tribulações.', 4, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, mesmo quando chegamos à macedônia, a nossa carne não teve repouso algum; antes em tudo fomos atribulados: por fora combates, temores por dentro.', 5, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus, que consola os abatidos, nos consolou com a vinda de Tito.', 6, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não somente com a sua vinda, mas também pela consolação com que foi consolado por vós, contando-nos as vossas saudades, o vosso choro, o vosso zelo por mim, de maneira que muito me regozijei.', 7, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto, ainda que vos contristei com a minha carta, não me arrependo, embora já me tivesse arrependido por ver que aquela carta vos contristou, ainda que por pouco tempo.', 8, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora folgo, não porque fostes contristados, mas porque fostes contristados para arrependimento; pois fostes contristados segundo Deus; de maneira que por nós não padecestes dano em coisa alguma.', 9, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a tristeza segundo Deus opera arrependimento para a salvação, da qual ninguém se arrepende; mas a tristeza do mundo opera a morte.', 10, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, quanto cuidado não produziu isto mesmo em vós que, segundo Deus, fostes contristados! que apologia, que indignação, que temor, que saudades, que zelo, que vingança! Em tudo mostrastes estar puros neste negócio.', 11, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, ainda que vos escrevi, não foi por causa do que fez o agravo, nem por causa do que sofreu o agravo, mas para que o vosso grande cuidado por nós fosse manifesto diante de Deus.', 12, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso fomos consolados pela vossa consolação, e muito mais nos alegramos pela alegria de Tito, porque o seu espírito foi recreado por vós todos.', 13, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se nalguma coisa me gloriei de vós para com ele, não fiquei envergonhado; mas, como vos dissemos tudo com verdade, também a nossa glória para com Tito se achou verdadeira.', 14, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o seu entranhável afeto para convosco é mais abundante, lembrando-se da obediência de vós todos, e de como o recebestes com temor e tremor.', 15, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Regozijo-me de em tudo poder confiar em vós.', 16, 7, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que diremos, pois, ter alcançado Abraão, nosso pai segundo a carne?', 1, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se Abraão foi justificado pelas obras, tem de que se gloriar, mas não diante de Deus.', 2, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, que diz a Escritura? Creu Abraão a Deus, e isso lhe foi imputado como justiça.', 3, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, àquele que faz qualquer obra não lhe é imputado o galardão segundo a graça, mas segundo a dívida.', 4, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, àquele que não pratica, mas crê naquele que justifica o ímpio, a sua fé lhe é imputada como justiça.', 5, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também Davi declara bem-aventurado o homem a quem Deus imputa a justiça sem as obras, dizendo:', 6, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurados aqueles cujas maldades são perdoadas,E cujos pecados são cobertos.', 7, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurado o homem a quem o Senhor não imputa o pecado.', 8, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vem, pois, esta bem-aventurança sobre a circuncisão somente, ou também sobre a incircuncisão? Porque dizemos que a fé foi imputada como justiça a Abraão.', 9, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como lhe foi, pois, imputada? Estando na circuncisão ou na incircuncisão? Não na circuncisão, mas na incircuncisão.', 10, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E recebeu o sinal da circuncisão, selo da justiça da fé quando estava na incircuncisão, para que fosse pai de todos os que crêem, estando eles também na incircuncisão; a fim de que também a justiça lhes seja imputada;', 11, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E fosse pai da circuncisão, daqueles que não somente são da circuncisão, mas que também andam nas pisadas daquela fé que teve nosso pai Abraão, que tivera na incircuncisão.', 12, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a promessa de que havia de ser herdeiro do mundo não foi feita pela lei a Abraão, ou à sua posteridade, mas pela justiça da fé.', 13, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se os que são da lei são herdeiros, logo a fé é vã e a promessa é aniquilada.', 14, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a lei opera a ira. Porque onde não há lei também não há transgressão.', 15, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, é pela fé, para que seja segundo a graça, a fim de que a promessa seja firme a toda a posteridade, não somente à que é da lei, mas também à que é da fé que teve Abraão, o qual é pai de todos nós,', 16, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Como está escrito: Por pai de muitas nações te constituí) perante aquele no qual creu, a saber, Deus, o qual vivifica os mortos, e chama as coisas que não são como se já fossem.', 17, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual, em esperança, creu contra a esperança, tanto que ele tornou-se pai de muitas nações, conforme o que lhe fora dito: Assim será a tua descendência.', 18, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não enfraquecendo na fé, nào atentou para o seu próprio corpo já amortecido, pois era já de quase cem anos, nem tampouco para o amortecimento do ventre de Sara.', 19, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não duvidou da promessa de Deus por incredulidade, mas foi fortificado na fé, dando glória a Deus,', 20, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estando certíssimo de que o que ele tinha prometido também era poderoso para o fazer.', 21, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim isso lhe foi também imputado como justiça.', 22, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, não só por causa dele está escrito, que lhe fosse tomado em conta,', 23, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas também por nós, a quem será tomado em conta, os que cremos naquele que dentre os mortos ressuscitou a Jesus nosso Senhor;', 24, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual por nossos pecados foi entregue, e ressuscitou para nossa justificação.', 25, 4, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto nós também, pois que estamos rodeados de uma tão grande nuvem de testemunhas, deixemos todo o embaraço, e o pecado que tão de perto nos rodeia, e corramos com paciência a carreira que nos está proposta,', 1, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Olhando para Jesus, autor e consumador da fé, o qual, pelo gozo que lhe estava proposto, suportou a cruz, desprezando a afronta, e assentou-se à destra do trono de Deus.', 2, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Considerai, pois, aquele que suportou tais contradições dos pecadores contra si mesmo, para que não enfraqueçais, desfalecendo em vossos ânimos.', 3, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ainda não resististes até ao sangue, combatendo contra o pecado.', 4, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E já vos esquecestes da exortação que argumenta convosco como filhos: Filho meu, não desprezes a correção do Senhor, E não desmaies quando por ele fores repreendido;', 5, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Senhor corrige o que ama,E açoita a qualquer que recebe por filho.', 6, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se suportais a correção, Deus vos trata como filhos; porque, que filho há a quem o pai não corrija?', 7, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se estais sem disciplina, da qual todos são feitos participantes, sois então bastardos, e não filhos.', 8, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Além do que, tivemos nossos pais segundo a carne, para nos corrigirem, e nós os reverenciamos; não nos sujeitaremos muito mais ao Pai dos espíritos, para vivermos?', 9, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque aqueles, na verdade, por um pouco de tempo, nos corrigiam como bem lhes parecia; mas este, para nosso proveito, para sermos participantes da sua santidade.', 10, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, na verdade, toda a correção, ao presente, não parece ser de gozo, senão de tristeza, mas depois produz um fruto pacífico de justiça nos exercitados por ela.', 11, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, tornai a levantar as mãos cansadas, e os joelhos desconjuntados,', 12, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E fazei veredas direitas para os vossos pés, para que o que manqueja não se desvie inteiramente, antes seja sarado.', 13, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segui a paz com todos, e a santificação, sem a qual ninguém verá o Senhor;', 14, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo cuidado de que ninguém se prive da graça de Deus, e de que nenhuma raiz de amargura, brotando, vos perturbe, e por ela muitos se contaminem.', 15, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ninguém seja devasso, ou profano, como Esaú, que por uma refeição vendeu o seu direito de primogenitura.', 16, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque bem sabeis que, querendo ele ainda depois herdar a bênção, foi rejeitado, porque não achou lugar de arrependimento, ainda que com lágrimas o buscou.', 17, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não chegastes ao monte palpável, aceso em fogo, e à escuridão, e às trevas, e à tempestade,', 18, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ao sonido da trombeta, e à voz das palavras, a qual os que a ouviram pediram que se lhes não falasse mais;', 19, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não podiam suportar o que se lhes mandava: Se até um animal tocar o monte será apedrejado ou passado com um dardo.', 20, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tão terrível era a visão, que Moisés disse: Estou todo assombrado, e tremendo.', 21, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas chegastes ao monte Sião, e à cidade do Deus vivo, à Jerusalém celestial, e aos muitos milhares de anjos;', 22, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('À universal assembléia e igreja dos primogênitos, que estão inscritos nos céus, e a Deus, o juiz de todos, e aos espíritos dos justos aperfeiçoados;', 23, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a Jesus, o Mediador de uma nova aliança, e ao sangue da aspersão, que fala melhor do que o de Abel.', 24, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede que não rejeiteis ao que fala; porque, se não escaparam aqueles que rejeitaram o que na terra os advertia, muito menos nós, se nos desviarmos daquele que é dos céus;', 25, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A voz do qual moveu então a terra, mas agora anunciou, dizendo: Ainda uma vez comoverei, não só a terra, senão também o céu.', 26, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E esta palavra: Ainda uma vez, mostra a mudança das coisas móveis, como coisas feitas, para que as imóveis permaneçam.', 27, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, tendo recebido um reino que não pode ser abalado, retenhamos a graça, pela qual sirvamos a Deus agradavelmente, com reverência e piedade;', 28, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o nosso Deus é um fogo consumidor.', 29, 12, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, irmãos, rogamo-vos, pela vinda de nosso Senhor Jesus Cristo, e pela nossa reunião com ele,', 1, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que não vos movais facilmente do vosso entendimento, nem vos perturbeis, quer por espírito, quer por palavra, quer por epístola, como de nós, como se o dia de Cristo estivesse já perto.', 2, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém de maneira alguma vos engane; porque não será assim sem que antes venha a apostasia, e se manifeste o homem do pecado, o filho da perdição,', 3, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual se opõe, e se levanta contra tudo o que se chama Deus, ou se adora; de sorte que se assentará, como Deus, no templo de Deus, querendo parecer Deus.', 4, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos lembrais de que estas coisas vos dizia quando ainda estava convosco?', 5, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora vós sabeis o que o detém, para que a seu próprio tempo seja manifestado.', 6, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já o mistério da injustiça opera; somente há um que agora o retém até que do meio seja tirado;', 7, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E então será revelado o iníquo, a quem o Senhor desfará pelo assopro da sua boca, e aniquilará pelo esplendor da sua vinda;', 8, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A esse cuja vinda é segundo a eficácia de Satanás, com todo o poder, e sinais e prodígios de mentira,', 9, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E com todo o engano da injustiça para os que perecem, porque não receberam o amor da verdade para se salvarem.', 10, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por isso Deus lhes enviará a operação do erro, para que creiam a mentira;', 11, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que sejam julgados todos os que não creram a verdade, antes tiveram prazer na iniqüidade.', 12, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas devemos sempre dar graças a Deus por vós, irmãos amados do Senhor, por vos ter Deus elegido desde o princípio para a salvação, em santificação do Espírito, e fé da verdade;', 13, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para o que pelo nosso evangelho vos chamou, para alcançardes a glória de nosso Senhor Jesus Cristo.', 14, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, irmãos, estai firmes e retende as tradições que vos foram ensinadas, seja por palavra, seja por epístola nossa.', 15, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o próprio nosso Senhor Jesus Cristo e nosso Deus e Pai, que nos amou, e em graça nos deu uma eterna consolação e boa esperança,', 16, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Console os vossos corações, e vos confirme em toda a boa palavra e obra.', 17, 2, 14);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo o Cordeiro aberto um dos selos, olhei, e ouvi um dos quatro animais, que dizia como em voz de trovão: Vem, e vê.', 1, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E olhei, e eis um cavalo branco; e o que estava assentado sobre ele tinha um arco; e foi-lhe dada uma coroa, e saiu vitorioso, e para vencer.', 2, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo aberto o segundo selo, ouvi o segundo animal, dizendo: Vem, e vê.', 3, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E saiu outro cavalo, vermelho; e ao que estava assentado sobre ele foi dado que tirasse a paz da terra, e que se matassem uns aos outros; e foi-lhe dada uma grande espada.', 4, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo aberto o terceiro selo, ouvi dizer o terceiro animal: Vem, e vê. E olhei, e eis um cavalo preto e o que sobre ele estava assentado tinha uma balança em sua mão.', 5, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi uma voz no meio dos quatro animais, que dizia: Uma medida de trigo por um dinheiro, e três medidas de cevada por um dinheiro; e não danifiques o azeite e o vinho.', 6, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo aberto o quarto selo, ouvi a voz do quarto animal, que dizia: Vem, e vê.', 7, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E olhei, e eis um cavalo amarelo, e o que estava assentado sobre ele tinha por nome Morte; e o inferno o seguia; e foi-lhes dado poder para matar a quarta parte da terra, com espada, e com fome, e com peste, e com as feras da terra.', 8, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo aberto o quinto selo, vi debaixo do altar as almas dos que foram mortos por amor da palavra de Deus e por amor do testemunho que deram.', 9, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E clamavam com grande voz, dizendo: Até quando, ó verdadeiro e santo Dominador, não julgas e vingas o nosso sangue dos que habitam sobre a terra?', 10, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram dadas a cada um compridas vestes brancas e foi-lhes dito que repousassem ainda um pouco de tempo, até que também se completasse o número de seus conservos e seus irmãos, que haviam de ser mortos como eles foram.', 11, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo aberto o sexto selo, olhei, e eis que houve um grande tremor de terra; e o sol tornou-se negro como saco de cilício, e a lua tornou-se como sangue;', 12, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as estrelas do céu caíram sobre a terra, como quando a figueira lança de si os seus figos verdes, abalada por um vento forte.', 13, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o céu retirou-se como um livro que se enrola; e todos os montes e ilhas foram removidos dos seus lugares.', 14, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os reis da terra, e os grandes, e os ricos, e os tribunos, e os poderosos, e todo o servo, e todo o livre, se esconderam nas cavernas e nas rochas das montanhas;', 15, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E diziam aos montes e aos rochedos: Caí sobre nós, e escondei-nos do rosto daquele que está assentado sobre o trono, e da ira do Cordeiro;', 16, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque é vindo o grande dia da sua ira; e quem poderá subsistir?', 17, 6, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Permaneça o amor fraternal.', 1, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos esqueçais da hospitalidade, porque por ela alguns, não o sabendo, hospedaram anjos.', 2, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lembrai-vos dos presos, como se estivésseis presos com eles, e dos maltratados, como sendo-o vós mesmos também no corpo.', 3, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Venerado seja entre todos o matrimônio e o leito sem mácula; porém, aos que se dão à prostituição, e aos adúlteros, Deus os julgará.', 4, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sejam vossos costumes sem avareza, contentando-vos com o que tendes; porque ele disse: Não te deixarei, nem te desampararei.', 5, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim com confiança ousemos dizer: O Senhor é o meu ajudador, e não temerei O que me possa fazer o homem.', 6, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Lembrai-vos dos vossos pastores, que vos falaram a palavra de Deus, a fé dos quais imitai, atentando para a sua maneira de viver.', 7, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus Cristo é o mesmo, ontem, e hoje, e eternamente.', 8, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos deixeis levar em redor por doutrinas várias e estranhas, porque bom é que o coração se fortifique com graça, e não com alimentos que de nada aproveitaram aos que a eles se entregaram.', 9, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Temos um altar, de que não têm direito de comer os que servem ao tabernáculo.', 10, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os corpos dos animais, cujo sangue é, pelo pecado, trazido pelo sumo sacerdote para o santuário, são queimados fora do arraial.', 11, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por isso também Jesus, para santificar o povo pelo seu próprio sangue, padeceu fora da porta.', 12, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saiamos, pois, a ele fora do arraial, levando o seu vitupério.', 13, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não temos aqui cidade permanente, mas buscamos a futura.', 14, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, ofereçamos sempre por ele a Deus sacrifício de louvor, isto é, o fruto dos lábios que confessam o seu nome.', 15, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não vos esqueçais da beneficência e comunicação, porque com tais sacrifícios Deus se agrada.', 16, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Obedecei a vossos pastores, e sujeitai-vos a eles; porque velam por vossas almas, como aqueles que hão de dar conta delas; para que o façam com alegria e não gemendo, porque isso não vos seria útil.', 17, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Orai por nós, porque confiamos que temos boa consciência, como aqueles que em tudo querem portar-se honestamente.', 18, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rogo-vos com instância que assim o façais, para que eu mais depressa vos seja restituído.', 19, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o Deus de paz, que pelo sangue da aliança eterna tornou a trazer dos mortos a nosso Senhor Jesus Cristo, grande pastor das ovelhas,', 20, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vos aperfeiçoe em toda a boa obra, para fazerdes a sua vontade, operando em vós o que perante ele é agradável por Cristo Jesus, ao qual seja glória para todo o sempre. Amém.', 21, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Rogo-vos, porém, irmãos, que suporteis a palavra desta exortação; porque abreviadamente vos escrevi.', 22, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabei que já está solto o irmão Timóteo, com o qual, se ele vier depressa, vos verei.', 23, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a todos os vossos chefes e a todos os santos. Os da Itália vos saúdam.', 24, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça seja com todos vós. Amém.', 25, 13, 19);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que diremos pois? Permaneceremos no pecado, para que a graça abunde?', 1, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De modo nenhum. Nós, que estamos mortos para o pecado, como viveremos ainda nele?', 2, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou não sabeis que todos quantos fomos batizados em Jesus Cristo fomos batizados na sua morte?', 3, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que fomos sepultados com ele pelo batismo na morte; para que, como Cristo foi ressuscitado dentre os mortos, pela glória do Pai, assim andemos nós também em novidade de vida.', 4, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se fomos plantados juntamente com ele na semelhança da sua morte, também o seremos na da sua ressurreição;', 5, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo isto, que o nosso homem velho foi com ele crucificado, para que o corpo do pecado seja desfeito, para que não sirvamos mais ao pecado.', 6, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque aquele que está morto está justificado do pecado.', 7, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, se já morremos com Cristo, cremos que também com ele viveremos;', 8, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo que, tendo sido Cristo ressuscitado dentre os mortos, já não morre; a morte não mais tem domínio sobre ele.', 9, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, quanto a ter morrido, de uma vez morreu para o pecado; mas, quanto a viver, vive para Deus.', 10, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também vós considerai-vos certamente mortos para o pecado, mas vivos para Deus em Cristo Jesus nosso Senhor.', 11, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não reine, portanto, o pecado em vosso corpo mortal, para lhe obedecerdes em suas concupiscências;', 12, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem tampouco apresenteis os vossos membros ao pecado por instrumentos de iniqüidade; mas apresentai-vos a Deus, como vivos dentre mortos, e os vossos membros a Deus, como instrumentos de justiça.', 13, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o pecado não terá domínio sobre vós, pois não estais debaixo da lei, mas debaixo da graça.', 14, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois que? Pecaremos porque não estamos debaixo da lei, mas debaixo da graça? De modo nenhum.', 15, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sabeis vós que a quem vos apresentardes por servos para lhe obedecer, sois servos daquele a quem obedeceis, ou do pecado para a morte, ou da obediência para a justiça?', 16, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas graças a Deus que, tendo sido servos do pecado, obedecestes de coração à forma de doutrina a que fostes entregues.', 17, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, libertados do pecado, fostes feitos servos da justiça.', 18, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Falo como homem, pela fraqueza da vossa carne; pois que, assim como apresentastes os vossos membros para servirem à imundícia, e à maldade para maldade, assim apresentai agora os vossos membros para servirem à justiça para santificação.', 19, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, quando éreis servos do pecado, estáveis livres da justiça.', 20, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que fruto tínheis então das coisas de que agora vos envergonhais? Porque o fim delas é a morte.', 21, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora, libertados do pecado, e feitos servos de Deus, tendes o vosso fruto para santificação, e por fim a vida eterna.', 22, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o salário do pecado é a morte, mas o dom gratuito de Deus é a vida eterna, por Cristo Jesus nosso Senhor.', 23, 6, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi na destra do que estava assentado sobre o trono um livro escrito por dentro e por fora, selado com sete selos.', 1, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi um anjo forte, bradando com grande voz: Quem é digno de abrir o livro e de desatar os seus selos?', 2, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ninguém no céu, nem na terra, nem debaixo da terra, podia abrir o livro, nem olhar para ele.', 3, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu chorava muito, porque ninguém fora achado digno de abrir o livro, nem de o ler, nem de olhar para ele.', 4, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-me um dos anciãos: Não chores; eis aqui o Leão da tribo de Judá, a raiz de Davi, que venceu, para abrir o livro e desatar os seus sete selos.', 5, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E olhei, e eis que estava no meio do trono e dos quatro animais viventes e entre os anciãos um Cordeiro, como havendo sido morto, e tinha sete pontas e sete olhos, que são os sete espíritos de Deus enviados a toda a terra.', 6, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E veio, e tomou o livro da destra do que estava assentado no trono.', 7, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo tomado o livro, os quatro animais e os vinte e quatro anciãos prostraram-se diante do Cordeiro, tendo todos eles harpas e salvas de ouro cheias de incenso, que são as orações dos santos.', 8, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E cantavam um novo cântico, dizendo: Digno és de tomar o livro, e de abrir os seus selos; porque foste morto, e com o teu sangue nos compraste para Deus de toda a tribo, e língua, e povo, e nação;', 9, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E para o nosso Deus nos fizeste reis e sacerdotes; e reinaremos sobre a terra.', 10, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E olhei, e ouvi a voz de muitos anjos ao redor do trono, e dos animais, e dos anciãos; e era o número deles milhões de milhões, e milhares de milhares,', 11, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que com grande voz diziam: Digno é o Cordeiro, que foi morto, de receber o poder, e riquezas, e sabedoria, e força, e honra, e glória, e ações de graças.', 12, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi a toda a criatura que está no céu, e na terra, e debaixo da terra, e que estão no mar, e a todas as coisas que neles há, dizer: Ao que está assentado sobre o trono, e ao Cordeiro, sejam dadas ações de graças, e honra, e glória, e poder para todo o sempre.', 13, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os quatro animais diziam: Amém. E os vinte e quatro anciãos prostraram-se, e adoraram ao que vive para todo o sempre.', 14, 5, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sabeis vós, irmãos (pois que falo aos que sabem a lei), que a lei tem domínio sobre o homem por todo o tempo que vive?', 1, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a mulher que está sujeita ao marido, enquanto ele viver, está-lhe ligada pela lei; mas, morto o marido, está livre da lei do marido.', 2, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que, vivendo o marido, será chamada adúltera se for de outro marido; mas, morto o marido, livre está da lei, e assim não será adúltera, se for de outro marido.', 3, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, meus irmãos, também vós estais mortos para a lei pelo corpo de Cristo, para que sejais de outro, daquele que ressuscitou dentre os mortos, a fim de que demos fruto para Deus.', 4, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, quando estávamos na carne, as paixões dos pecados, que são pela lei, operavam em nossos membros para darem fruto para a morte.', 5, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora temos sido libertados da lei, tendo morrido para aquilo em que estávamos retidos; para que sirvamos em novidade de espírito, e não na velhice da letra.', 6, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que diremos pois? É a lei pecado? De modo nenhum. Mas eu não conheci o pecado senão pela lei; porque eu não conheceria a concupiscência, se a lei não dissesse: Não cobiçarás.', 7, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o pecado, tomando ocasião pelo mandamento, operou em mim toda a concupiscência; porquanto sem a lei estava morto o pecado.', 8, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu, nalgum tempo, vivia sem lei, mas, vindo o mandamento, reviveu o pecado, e eu morri.', 9, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o mandamento que era para vida, achei eu que me era para morte.', 10, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o pecado, tomando ocasião pelo mandamento, me enganou, e por ele me matou.', 11, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim a lei é santa, e o mandamento santo, justo e bom.', 12, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Logo tornou-se-me o bom em morte? De modo nenhum; mas o pecado, para que se mostrasse pecado, operou em mim a morte pelo bem; a fim de que pelo mandamento o pecado se fizesse excessivamente maligno.', 13, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque bem sabemos que a lei é espiritual; mas eu sou carnal, vendido sob o pecado.', 14, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o que faço não o aprovo; pois o que quero isso não faço, mas o que aborreço isso faço.', 15, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se faço o que não quero, consinto com a lei, que é boa.', 16, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que agora já não sou eu que faço isto, mas o pecado que habita em mim.', 17, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu sei que em mim, isto é, na minha carne, não habita bem algum; e com efeito o querer está em mim, mas não consigo realizar o bem.', 18, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não faço o bem que quero, mas o mal que não quero esse faço.', 19, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, se eu faço o que não quero, já o não faço eu, mas o pecado que habita em mim.', 20, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Acho então esta lei em mim, que, quando quero fazer o bem, o mal está comigo.', 21, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, segundo o homem interior, tenho prazer na lei de Deus;', 22, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vejo nos meus membros outra lei, que batalha contra a lei do meu entendimento, e me prende debaixo da lei do pecado que está nos meus membros.', 23, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Miserável homem que eu sou! quem me livrará do corpo desta morte?', 24, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dou graças a Deus por Jesus Cristo nosso Senhor. Assim que eu mesmo com o entendimento sirvo à lei de Deus, mas com a carne à lei do pecado.', 25, 7, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo aberto o sétimo selo, fez-se silêncio no céu quase por meia hora.', 1, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi os sete anjos, que estavam diante de Deus, e foram-lhes dadas sete trombetas.', 2, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E veio outro anjo, e pôs-se junto ao altar, tendo um incensário de ouro; e foi-lhe dado muito incenso, para o pôr com as orações de todos os santos sobre o altar de ouro, que está diante do trono.', 3, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a fumaça do incenso subiu com as orações dos santos desde a mão do anjo até diante de Deus.', 4, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o anjo tomou o incensário, e o encheu do fogo do altar, e o lançou sobre a terra; e houve depois vozes, e trovões, e relâmpagos e terremotos.', 5, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os sete anjos, que tinham as sete trombetas, prepararam-se para tocá-las.', 6, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o primeiro anjo tocou a sua trombeta, e houve saraiva e fogo misturado com sangue, e foram lançados na terra, que foi queimada na sua terça parte; queimou-se a terça parte das árvores, e toda a erva verde foi queimada.', 7, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o segundo anjo tocou a trombeta; e foi lançada no mar uma coisa como um grande monte ardendo em fogo, e tornou-se em sangue a terça parte do mar.', 8, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E morreu a terça parte das criaturas que tinham vida no mar; e perdeu-se a terça parte das naus.', 9, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o terceiro anjo tocou a sua trombeta, e caiu do céu uma grande estrela ardendo como uma tocha, e caiu sobre a terça parte dos rios, e sobre as fontes das águas.', 10, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o nome da estrela era Absinto, e a terça parte das águas tornou-se em absinto, e muitos homens morreram das águas, porque se tornaram amargas.', 11, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o quarto anjo tocou a sua trombeta, e foi ferida a terça parte do sol, e a terça parte da lua, e a terça parte das estrelas; para que a terça parte deles se escurecesse, e a terça parte do dia não brilhasse, e semelhantemente a noite.', 12, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E olhei, e ouvi um anjo voar pelo meio do céu, dizendo com grande voz: Ai! ai! ai! dos que habitam sobre a terra! por causa das outras vozes das trombetas dos três anjos que hão de ainda tocar.', 13, 8, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi-me dada uma cana semelhante a uma vara; e chegou o anjo, e disse: Levanta-te, e mede o templo de Deus, e o altar, e os que nele adoram.', 1, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E deixa o átrio que está fora do templo, e não o meças; porque foi dado às nações, e pisarão a cidade santa por quarenta e dois meses.', 2, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E darei poder às minhas duas testemunhas, e profetizarão por mil duzentos e sessenta dias, vestidas de saco.', 3, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estas são as duas oliveiras e os dois castiçais que estão diante do Deus da terra.', 4, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se alguém lhes quiser fazer mal, fogo sairá da sua boca, e devorará os seus inimigos; e, se alguém lhes quiser fazer mal, importa que assim seja morto.', 5, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes têm poder para fechar o céu, para que não chova, nos dias da sua profecia; e têm poder sobre as águas para convertê-las em sangue, e para ferir a terra com toda a sorte de pragas, todas quantas vezes quiserem.', 6, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando acabarem o seu testemunho, a besta que sobe do abismo lhes fará guerra, e os vencerá, e os matará.', 7, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E jazerão os seus corpos mortos na praça da grande cidade que espiritualmente se chama Sodoma e Egito, onde o nosso Senhor também foi crucificado.', 8, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E homens de vários povos, e tribos, e línguas, e nações verão seus corpos mortos por três dias e meio, e não permitirão que os seus corpos mortos sejam postos em sepulcros.', 9, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que habitam na terra se regozijarão sobre eles, e se alegrarão, e mandarão presentes uns aos outros; porquanto estes dois profetas tinham atormentado os que habitam sobre a terra.', 10, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois daqueles três dias e meio o espírito de vida, vindo de Deus, entrou neles; e puseram-se sobre seus pés, e caiu grande temor sobre os que os viram.', 11, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouviram uma grande voz do céu, que lhes dizia: Subi para aqui. E subiram ao céu em uma nuvem; e os seus inimigos os viram.', 12, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E naquela mesma hora houve um grande terremoto, e caiu a décima parte da cidade, e no terremoto foram mortos sete mil homens; e os demais ficaram muito atemorizados, e deram glória ao Deus do céu.', 13, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É passado o segundo ai; eis que o terceiro ai cedo virá.', 14, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o sétimo anjo tocou a sua trombeta, e houve no céu grandes vozes, que diziam: Os reinos do mundo vieram a ser de nosso Senhor e do seu Cristo, e ele reinará para todo o sempre.', 15, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os vinte e quatro anciãos, que estão assentados em seus tronos diante de Deus, prostraram-se sobre seus rostos e adoraram a Deus,', 16, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Graças te damos, Senhor Deus Todo-Poderoso, que és, e que eras, e que hás de vir, que tomaste o teu grande poder, e reinaste.', 17, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E iraram-se as nações, e veio a tua ira, e o tempo dos mortos, para que sejam julgados, e o tempo de dares o galardão aos profetas, teus servos, e aos santos, e aos que temem o teu nome, a pequenos e a grandes, e o tempo de destruíres os que destroem a terra.', 18, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E abriu-se no céu o templo de Deus, e a arca da sua aliança foi vista no seu templo; e houve relâmpagos, e vozes, e trovões, e terremotos e grande saraiva.', 19, 11, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Recomendo-vos, pois, Febe, nossa irmã, a qual serve na igreja que está em Cencréia,', 1, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que a recebais no Senhor, como convém aos santos, e a ajudeis em qualquer coisa que de vós necessitar; porque tem hospedado a muitos, como também a mim mesmo.', 2, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Priscila e a Áqüila, meus cooperadores em Cristo Jesus,', 3, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais pela minha vida expuseram as suas cabeças; o que não só eu lhes agradeço, mas também todas as igrejas dos gentios.', 4, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai também a igreja que está em sua casa. Saudai a Epêneto, meu amado, que é as primícias da Acáia em Cristo.', 5, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Maria, que trabalhou muito por nós.', 6, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Andrônico e a Júnias, meus parentes e meus companheiros na prisão, os quais se distinguiram entre os apóstolos e que foram antes de mim em Cristo.', 7, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Amplias, meu amado no Senhor.', 8, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Urbano, nosso cooperador em Cristo, e a Estáquis, meu amado.', 9, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Apeles, aprovado em Cristo. Saudai aos da família de Aristóbulo.', 10, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Herodião, meu parente. Saudai aos da família de Narciso, os que estão no Senhor.', 11, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Trifena e a Trifosa, as quais trabalham no Senhor. Saudai à amada Pérside, a qual muito trabalhou no Senhor.', 12, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Rufo, eleito no Senhor, e a sua mãe e minha.', 13, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Asíncrito, a Flegonte, a Hermes, a Pátrobas, a Hermas, e aos irmãos que estão com eles.', 14, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai a Filólogo e a Júlia, a Nereu e a sua irmã, e a Olimpas, e a todos os santos que com eles estão.', 15, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudai-vos uns aos outros com santo ósculo. As igrejas de Cristo vos saúdam.', 16, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rogo-vos, irmãos, que noteis os que promovem dissensões e escândalos contra a doutrina que aprendestes; desviai-vos deles.', 17, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os tais não servem a nosso Senhor Jesus Cristo, mas ao seu ventre; e com suaves palavras e lisonjas enganam os corações dos simples.', 18, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quanto à vossa obediência, é ela conhecida de todos. Comprazo-me, pois, em vós; e quero que sejais sábios no bem, mas simples no mal.', 19, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o Deus de paz esmagará em breve Satanás debaixo dos vossos pés. A graça de nosso Senhor Jesus Cristo seja convosco. Amém.', 20, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saúdam-vos Timóteo, meu cooperador, e Lúcio, Jasom e Sosípatro, meus parentes.', 21, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, Tércio, que esta carta escrevi, vos saúdo no Senhor.', 22, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saúda-vos Gaio, meu hospedeiro, e de toda a igreja. Saúda-vos Erasto, procurador da cidade, e também o irmão Quarto.', 23, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça de nosso Senhor Jesus Cristo seja com todos vós. Amém.', 24, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, àquele que é poderoso para vos confirmar segundo o meu evangelho e a pregação de Jesus Cristo, conforme a revelação do mistério que desde tempos eternos esteve oculto,', 25, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas que se manifestou agora, e se notificou pelas Escrituras dos profetas, segundo o mandamento do Deus eterno, a todas as nações para obediência da fé;', 26, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao único Deus, sábio, seja dada glória por Jesus Cristo para todo o sempre. Amém.', 27, 16, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Rogo-vos, pois, irmãos, pela compaixão de Deus, que apresenteis os vossos corpos em sacrifício vivo, santo e agradável a Deus, que é o vosso culto racional.', 1, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não sede conformados com este mundo, mas sede transformados pela renovação do vosso entendimento, para que experimenteis qual seja a boa, agradável, e perfeita vontade de Deus.', 2, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque pela graça que me é dada, digo a cada um dentre vós que não pense de si mesmo além do que convém; antes, pense com moderação, conforme a medida da fé que Deus repartiu a cada um.', 3, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque assim como em um corpo temos muitos membros, e nem todos os membros têm a mesma operação,', 4, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim nós, que somos muitos, somos um só corpo em Cristo, mas individualmente somos membros uns dos outros.', 5, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De modo que, tendo diferentes dons, segundo a graça que nos é dada, se é profecia, seja ela segundo a medida da fé;', 6, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se é ministério, seja em ministrar; se é ensinar, haja dedicação ao ensino;', 7, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou o que exorta, use esse dom em exortar; o que reparte, faça-o com liberalidade; o que preside, com cuidado; o que exercita misericórdia, com alegria.', 8, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O amor seja não fingido. Aborrecei o mal e apegai-vos ao bem.', 9, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amai-vos cordialmente uns aos outros com amor fraternal, preferindo-vos em honra uns aos outros.', 10, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sejais vagarosos no cuidado; sede fervorosos no espírito, servindo ao Senhor;', 11, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Alegrai-vos na esperança, sede pacientes na tribulação, perseverai na oração;', 12, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Comunicai com os santos nas suas necessidades, segui a hospitalidade;', 13, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Abençoai aos que vos perseguem, abençoai, e não amaldiçoeis.', 14, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Alegrai-vos com os que se alegram; e chorai com os que choram;', 15, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sede unânimes entre vós; não ambicioneis coisas altas, mas acomodai-vos às humildes; não sejais sábios em vós mesmos;', 16, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A ninguém torneis mal por mal; procurai as coisas honestas, perante todos os homens.', 17, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se for possível, quanto estiver em vós, tende paz com todos os homens.', 18, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos vingueis a vós mesmos, amados, mas dai lugar à ira, porque está escrito: Minha é a vingança; eu recompensarei, diz o Senhor.', 19, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, se o teu inimigo tiver fome, dá-lhe de comer; se tiver sede, dá-lhe de beber; porque, fazendo isto, amontoarás brasas de fogo sobre a sua cabeça.', 20, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não te deixes vencer do mal, mas vence o mal com o bem.', 21, 12, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Geralmente se ouve que há entre vós fornicação, e fornicação tal, que nem ainda entre os gentios se nomeia, como é haver quem possua a mulher de seu pai.', 1, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estais ensoberbecidos, e nem ao menos vos entristecestes por não ter sido dentre vós tirado quem cometeu tal ação.', 2, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, na verdade, ainda que ausente no corpo, mas presente no espírito, já determinei, como se estivesse presente, que o que tal ato praticou,', 3, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em nome de nosso Senhor Jesus Cristo, juntos vós e o meu espírito, pelo poder de nosso Senhor Jesus Cristo,', 4, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Seja, este tal, entregue a Satanás para destruição da carne, para que o espírito seja salvo no dia do Senhor Jesus.', 5, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não é boa a vossa jactância. Não sabeis que um pouco de fermento faz levedar toda a massa?', 6, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Alimpai-vos, pois, do fermento velho, para que sejais uma nova massa, assim como estais sem fermento. Porque Cristo, nossa páscoa, foi sacrificado por nós.', 7, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso façamos a festa, não com o fermento velho, nem com o fermento da maldade e da malícia, mas com os ázimos da sinceridade e da verdade.', 8, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Já por carta vos tenho escrito, que não vos associeis com os que se prostituem;', 9, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto não quer dizer absolutamente com os devassos deste mundo, ou com os avarentos, ou com os roubadores, ou com os idólatras; porque então vos seria necessário sair do mundo.', 10, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora vos escrevi que não vos associeis com aquele que, dizendo-se irmão, for devasso, ou avarento, ou idólatra, ou maldizente, ou beberrão, ou roubador; com o tal nem ainda comais.', 11, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, que tenho eu em julgar também os que estão de fora? Não julgais vós os que estão dentro?', 12, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus julga os que estão de fora. Tirai pois dentre vós a esse iníquo.', 13, 5, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi, vinda do templo, uma grande voz, que dizia aos sete anjos: Ide, e derramaisobre a terra as sete taças da ira de Deus.', 1, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi o primeiro, e derramou a sua taça sobre a terra, e fez-se uma chaga má e maligna nos homens que tinham o sinal da besta e que adoravam a sua imagem.', 2, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o segundo anjo derramou a sua taça no mar, que se tornou em sangue como de um morto, e morreu no mar toda a alma vivente.', 3, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o terceiro anjo derramou a sua taça nos rios e nas fontes das águas, e se tornaram em sangue.', 4, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi o anjo das águas, que dizia: Justo és tu, ó Senhor, que és, e que eras, e hás de ser, porque julgaste estas coisas.', 5, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto como derramaram o sangue dos santos e dos profetas, também tu lhes deste o sangue a beber; porque disto são merecedores.', 6, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi outro do altar, que dizia: Na verdade, ó Senhor Deus Todo-Poderoso, verdadeiros e justos são os teus juízos.', 7, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o quarto anjo derramou a sua taça sobre o sol, e foi-lhe permitido que abrasasse os homens com fogo.', 8, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os homens foram abrasados com grandes calores, e blasfemaram o nome de Deus, que tem poder sobre estas pragas; e não se arrependeram para lhe darem glória.', 9, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o quinto anjo derramou a sua taça sobre o trono da besta, e o seu reino se fez tenebroso; e eles mordiam as suas línguas de dor.', 10, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por causa das suas dores, e por causa das suas chagas, blasfemaram do Deus do céu; e não se arrependeram das suas obras.', 11, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o sexto anjo derramou a sua taça sobre o grande rio Eufrates; e a sua água secou-se, para que se preparasse o caminho dos reis do oriente.', 12, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E da boca do dragão, e da boca da besta, e da boca do falso profeta vi sair três espíritos imundos, semelhantes a rãs.', 13, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque são espíritos de demônios, que fazem prodígios; os quais vão ao encontro dos reis da terra e de todo o mundo, para os congregar para a batalha, naquele grande dia do Deus Todo-Poderoso.', 14, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que venho como ladrão. Bem-aventurado aquele que vigia, e guarda as suas roupas, para que não ande nu, e não se vejam as suas vergonhas.', 15, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os congregaram no lugar que em hebreu se chama Armagedom.', 16, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o sétimo anjo derramou a sua taça no ar, e saiu grande voz do templo do céu, do trono, dizendo: Está feito.', 17, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E houve vozes, e trovões, e relâmpagos, e houve um grande terremoto, como nunca houve desde que há homens sobre a terra; tal foi este tão grande terremoto.', 18, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a grande cidade fendeu-se em três partes, e as cidades das nações caíram; e da grande babilônia se lembrou Deus, para lhe dar o cálice do vinho da indignação da sua ira.', 19, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E toda a ilha fugiu; e os montes não se acharam.', 20, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sobre os homens caiu do céu uma grande saraiva, pedras do peso de um talento; e os homens blasfemaram de Deus por causa da praga da saraiva; porque a sua praga era mui grande.', 21, 16, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sou eu apóstolo? Não sou livre? Não vi eu a Jesus Cristo Senhor nosso? Não sois vós a minha obra no Senhor?', 1, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se eu não sou apóstolo para os outros, ao menos o sou para vós; porque vós sois o selo do meu apostolado no Senhor.', 2, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esta é minha defesa para com os que me condenam.', 3, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não temos nós direito de comer e beber?', 4, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não temos nós direito de levar conosco uma esposa crente, como também os demais apóstolos, e os irmãos do Senhor, e Cefas?', 5, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou só eu e Barnabé não temos direito de deixar de trabalhar?', 6, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem jamais milita à sua própria custa? Quem planta a vinha e não come do seu fruto? Ou quem apascenta o gado e não se alimenta do leite do gado?', 7, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo eu isto segundo os homens? Ou não diz a lei também o mesmo?', 8, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque na lei de Moisés está escrito: Não atarás a boca ao boi que trilha o grão. Porventura tem Deus cuidado dos bois?', 9, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou não o diz certamente por nós? Certamente que por nós está escrito; porque o que lavra deve lavrar com esperança e o que debulha deve debulhar com esperança de ser participante.', 10, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se nós vos semeamos as coisas espirituais, será muito que de vós recolhamos as carnais?', 11, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se outros participam deste poder sobre vós, por que não, e mais justamente, nós? Mas nós não usamos deste direito; antes suportamos tudo, para não pormos impedimento algum ao evangelho de Cristo.', 12, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sabeis vós que os que administram o que é sagrado comem do que é do templo? E que os que de contínuo estão junto ao altar, participam do altar?', 13, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim ordenou também o Senhor aos que anunciam o evangelho, que vivam do evangelho.', 14, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eu de nenhuma destas coisas usei, e não escrevi isto para que assim se faça comigo; porque melhor me fora morrer, do que alguém fazer vã esta minha glória.', 15, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se anuncio o evangelho, não tenho de que me gloriar, pois me é imposta essa obrigação; e ai de mim, se não anunciar o evangelho!', 16, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por isso, se o faço de boa mente, terei prêmio; mas, se de má vontade, apenas uma dispensação me é confiada.', 17, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Logo, que prêmio tenho? Que, evangelizando, proponha de graça o evangelho de Cristo para não abusar do meu poder no evangelho.', 18, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, sendo livre para com todos, fiz-me servo de todos para ganhar ainda mais.', 19, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E fiz-me como judeu para os judeus, para ganhar os judeus; para os que estão debaixo da lei, como se estivesse debaixo da lei, para ganhar os que estão debaixo da lei.', 20, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para os que estão sem lei, como se estivesse sem lei (não estando sem lei para com Deus, mas debaixo da lei de Cristo), para ganhar os que estão sem lei.', 21, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fiz-me como fraco para os fracos, para ganhar os fracos. Fiz-me tudo para todos, para por todos os meios chegar a salvar alguns.', 22, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu faço isto por causa do evangelho, para ser também participante dele.', 23, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sabeis vós que os que correm no estádio, todos, na verdade, correm, mas um só leva o prêmio? Correi de tal maneira que o alcanceis.', 24, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todo aquele que luta de tudo se abstém; eles o fazem para alcançar uma coroa corruptível; nós, porém, uma incorruptível.', 25, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois eu assim corro, não como a coisa incerta; assim combato, não como batendo no ar.', 26, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes subjugo o meu corpo, e o reduzo à servidão, para que, pregando aos outros, eu mesmo não venha de alguma maneira a ficar reprovado.', 27, 9, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ousa algum de vós, tendo algum negócio contra outro, ir a juízo perante os injustos, e não perante os santos?', 1, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sabeis vós que os santos hão de julgar o mundo? Ora, se o mundo deve ser julgado por vós, sois porventura indignos de julgar as coisas mínimas?', 2, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sabeis vós que havemos de julgar os anjos? Quanto mais as coisas pertencentes a esta vida?', 3, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, se tiverdes negócios em juízo, pertencentes a esta vida, pondes para julga-los os que são de menos estima na igreja?', 4, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para vos envergonhar o digo. Não há, pois, entre vós sábios, nem mesmo um, que possa julgar entre seus irmãos?', 5, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o irmão vai a juízo com o irmão, e isto perante infiéis.', 6, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade é já realmente uma falta entre vós, terdes demandas uns contra os outros. Por que não sofreis antes a injustiça? Por que não sofreis antes o dano?', 7, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós mesmos fazeis a injustiça e fazeis o dano, e isto aos irmãos.', 8, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sabeis que os injustos não hão de herdar o reino de Deus?', 9, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não erreis: nem os devassos, nem os idólatras, nem os adúlteros, nem os efeminados, nem os sodomitas, nem os ladrões, nem os avarentos, nem os bêbados, nem os maldizentes, nem os roubadores herdarão o reino de Deus.', 10, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E é o que alguns têm sido; mas haveis sido lavados, mas haveis sido santificados, mas haveis sido justificados em nome do Senhor Jesus, e pelo Espírito do nosso Deus.', 11, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todas as coisas me são lícitas, mas nem todas as coisas convêm. Todas as coisas me são lícitas, mas eu não me deixarei dominar por nenhuma.', 12, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os alimentos são para o estômago e o estômago para os alimentos; Deus, porém, aniquilará tanto um como os outros. Mas o corpo não é para a fornicação, senão para o Senhor, e o Senhor para o corpo.', 13, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, Deus, que também ressuscitou o Senhor, nos ressuscitará a nós pelo seu poder.', 14, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sabeis vós que os vossos corpos são membros de Cristo? Tomarei, pois, os membros de Cristo, e os farei membros de uma meretriz? Não, por certo.', 15, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou não sabeis que o que se ajunta com a meretriz, faz-se um corpo com ela? Porque serão, disse, dois numa só carne.', 16, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que se ajunta com o Senhor é um mesmo espírito.', 17, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fugi da fornicação. Todo o pecado que o homem comete é fora do corpo; mas o que fornica peca contra o seu próprio corpo.', 18, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou não sabeis que o vosso corpo é o templo do Espírito Santo, que habita em vós, proveniente de Deus, e que não sois de vós mesmos?', 19, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque fostes comprados por bom preço; glorificai, pois, a Deus no vosso corpo, e no vosso espírito, os quais pertencem a Deus.', 20, 6, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nós, que somos fortes, devemos suportar as fraquezas dos fracos, e não agradar a nós mesmos.', 1, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto cada um de nós agrade ao seu próximo no que é bom para edificação.', 2, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também Cristo não agradou a si mesmo, mas, como está escrito: Sobre mim caíram as injúrias dos que te injuriavam.', 3, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tudo o que dantes foi escrito, para nosso ensino foi escrito, para que pela paciência e consolação das Escrituras tenhamos esperança.', 4, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o Deus de paciência e consolação vos conceda o mesmo sentimento uns para com os outros, segundo Cristo Jesus,', 5, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que concordes, a uma boca, glorifiqueis ao Deus e Pai de nosso Senhor Jesus Cristo.', 6, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto recebei-vos uns aos outros, como também Cristo nos recebeu para glória de Deus.', 7, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo, pois, que Jesus Cristo foi ministro da circuncisão, por causa da verdade de Deus, para que confirmasse as promessas feitas aos pais;', 8, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E para que os gentios glorifiquem a Deus pela sua misericórdia, como está escrito:Portanto eu te louvarei entre os gentios,E cantarei ao teu nome.', 9, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra vez diz:Alegrai-vos, gentios, com o seu povo.', 10, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra vez:Louvai ao Senhor, todos os gentios,E celebrai-o todos os povos.', 11, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Outra vez diz Isaías:Uma raiz em Jessé haverá,E naquele que se levantar para reger os gentios,Os gentios esperarão.', 12, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora o Deus de esperança vos encha de todo o gozo e paz em crença, para que abundeis em esperança pela virtude do Espírito Santo.', 13, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu próprio, meus irmãos, certo estou, a respeito de vós, que vós mesmos estais cheios de bondade, cheios de todo o conhecimento, podendo admoestar-vos uns aos outros.', 14, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, irmãos, em parte vos escrevi mais ousadamente, como para vos trazer outra vez isto à memória, pela graça que por Deus me foi dada;', 15, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que seja ministro de Jesus Cristo para os gentios, ministrando o evangelho de Deus, para que seja agradável a oferta dos gentios, santificada pelo Espírito Santo.', 16, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que tenho glória em Jesus Cristo nas coisas que pertencem a Deus.', 17, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não ousarei dizer coisa alguma, que Cristo por mim não tenha feito, para fazer obedientes os gentios, por palavra e por obras;', 18, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelo poder dos sinais e prodígios, e pela virtude do Espírito de Deus; de maneira que desde Jerusalém, e arredores, até ao Ilírico, tenho pregado o evangelho de Jesus Cristo.', 19, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desta maneira me esforcei por anunciar o evangelho, não onde Cristo foi nomeado, para não edificar sobre fundamento alheio;', 20, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, como está escrito:Aqueles a quem não foi anunciado, o verão,E os que não ouviram o entenderão.', 21, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso também muitas vezes tenho sido impedido de ir ter convosco.', 22, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora, que não tenho mais demora nestes sítios, e tendo já há muitos anos grande desejo de ir ter convosco,', 23, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando partir para Espanha irei ter convosco; pois espero que de passagem vos verei, e que para lá seja encaminhado por vós, depois de ter gozado um pouco da vossa companhia.', 24, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora vou a Jerusalém para ministrar aos santos.', 25, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque pareceu bem à macedônia e à Acaia fazerem uma coleta para os pobres dentre os santos que estão em Jerusalém.', 26, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto lhes pareceu bem, como devedores que são para com eles. Porque, se os gentios foram participantes dos seus bens espirituais, devem também ministrar-lhes os temporais.', 27, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim que, concluído isto, e havendo-lhes consignado este fruto, de lá, passando por vós, irei à Espanha.', 28, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E bem sei que, indo ter convosco, chegarei com a plenitude da bênção do evangelho de Cristo.', 29, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rogo-vos, irmãos, por nosso Senhor Jesus Cristo e pelo amor do Espírito, que combatais comigo nas vossas orações por mim a Deus;', 30, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que seja livre dos rebeldes que estão na Judéia, e que esta minha administração, que em Jerusalém faço, seja bem aceita pelos santos;', 31, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A fim de que, pela vontade de Deus, chegue a vós com alegria, e possa recrear-me convosco.', 32, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o Deus de paz seja com todos vós. Amém.', 33, 15, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi descer do céu um anjo, que tinha a chave do abismo, e uma grande cadeia na sua mão.', 1, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele prendeu o dragão, a antiga serpente, que é o Diabo e Satanás, e amarrou-o por mil anos.', 2, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lançou-o no abismo, e ali o encerrou, e pôs selo sobre ele, para que não mais engane as nações, até que os mil anos se acabem. E depois importa que seja solto por um pouco de tempo.', 3, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi tronos; e assentaram-se sobre eles, e foi-lhes dado o poder de julgar; e vi as almas daqueles que foram degolados pelo testemunho de Jesus, e pela palavra de Deus, e que não adoraram a besta, nem a sua imagem, e não receberam o sinal em suas testas nem em suas mãos; e viveram, e reinaram com Cristo durante mil anos.', 4, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os outros mortos não reviveram, até que os mil anos se acabaram. Esta é a primeira ressurreição.', 5, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bem-aventurado e santo aquele que tem parte na primeira ressurreição; sobre estes não tem poder a segunda morte; mas serão sacerdotes de Deus e de Cristo, e reinarão com ele mil anos.', 6, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, acabando-se os mil anos, Satanás será solto da sua prisão,', 7, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sairá a enganar as nações que estão sobre os quatro cantos da terra, Gogue e Magogue, cujo número é como a areia do mar, para as ajuntar em batalha.', 8, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E subiram sobre a largura da terra, e cercaram o arraial dos santos e a cidade amada; e de Deus desceu fogo, do céu, e os devorou.', 9, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o diabo, que os enganava, foi lançado no lago de fogo e enxofre, onde estão a besta e o falso profeta; e de dia e de noite serão atormentados para todo o sempre.', 10, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi um grande trono branco, e o que estava assentado sobre ele, de cuja presença fugiu a terra e o céu; e não se achou lugar para eles.', 11, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi os mortos, grandes e pequenos, que estavam diante de Deus, e abriram-se os livros; e abriu-se outro livro, que é o da vida. E os mortos foram julgados pelas coisas que estavam escritas nos livros, segundo as suas obras.', 12, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E deu o mar os mortos que nele havia; e a morte e o inferno deram os mortos que neles havia; e foram julgados cada um segundo as suas obras.', 13, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a morte e o inferno foram lançados no lago de fogo. Esta é a segunda morte.', 14, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele que não foi achado escrito no livro da vida foi lançado no lago de fogo.', 15, 20, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, o bom desejo do meu coração e a oração a Deus por Israel é para sua salvação.', 1, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque lhes dou testemunho de que têm zelo de Deus, mas não com entendimento.', 2, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto, não conhecendo a justiça de Deus, e procurando estabelecer a sua própria justiça, não se sujeitaram à justiça de Deus.', 3, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o fim da lei é Cristo para justiça de todo aquele que crê.', 4, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, Moisés descreve a justiça que é pela lei, dizendo: O homem que fizer estas coisas viverá por elas.', 5, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a justiça que é pela fé diz assim: Não digas em teu coração: Quem subirá ao céu? (isto é, a trazer do alto a Cristo. )', 6, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou: Quem descerá ao abismo? (isto é, a tornar a trazer dentre os mortos a Cristo. )', 7, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas que diz? A palavra está junto de ti, na tua boca e no teu coração; esta é a palavra da fé, que pregamos,', 8, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A saber: Se com a tua boca confessares ao Senhor Jesus, e em teu coração creres que Deus o ressuscitou dentre os mortos, serás salvo.', 9, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto que com o coração se crê para a justiça, e com a boca se faz confissão para a salvação.', 10, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a Escritura diz: Todo aquele que nele crer não será confundido.', 11, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto não há diferença entre judeu e grego; porque um mesmo é o Senhor de todos, rico para com todos os que o invocam.', 12, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todo aquele que invocar o nome do Senhor será salvo.', 13, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como, pois, invocarão aquele em quem não creram? e como crerão naquele de quem não ouviram? e como ouvirão, se não há quem pregue?', 14, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E como pregarão, se não forem enviados? como está escrito: Quão formosos os pés dos que anunciam o evangelho de paz; dos que trazem alegres novas de boas coisas.', 15, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nem todos têm obedecido ao evangelho; pois Isaías diz: Senhor, quem creu na nossa pregação?', 16, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que a fé é pelo ouvir, e o ouvir pela palavra de Deus.', 17, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas digo: Porventura não ouviram? Sim, por certo, pois Por toda a terra saiu a voz deles,E as suas palavras até aos confins do mundo.', 18, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas digo: Porventura Israel não o soube? Primeiramente diz Moisés:Eu vos porei em ciúmes com aqueles que não são povo,Com gente insensata vos provocarei à ira.', 19, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Isaías ousadamente diz:Fui achado pelos que não me buscavam,Fui manifestado aos que por mim não perguntavam.', 20, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas para Israel diz:Todo o dia estendi as minhas mãos a um povo rebelde e contradizente.', 21, 10, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sede meus imitadores, como também eu de Cristo.', 1, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E louvo-vos, irmãos, porque em tudo vos lembrais de mim, e retendes os preceitos como vo-los entreguei.', 2, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas quero que saibais que Cristo é a cabeça de todo o homem, e o homem a cabeça da mulher; e Deus a cabeça de Cristo.', 3, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todo o homem que ora ou profetiza, tendo a cabeça coberta, desonra a sua própria cabeça.', 4, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas toda a mulher que ora ou profetiza com a cabeça descoberta, desonra a sua própria cabeça, porque é como se estivesse rapada.', 5, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, se a mulher não se cobre com véu, tosquie-se também. Mas, se para a mulher é coisa indecente tosquiar-se ou rapar-se, que ponha o véu.', 6, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O homem, pois, não deve cobrir a cabeça, porque é a imagem e glória de Deus, mas a mulher é a glória do homem.', 7, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o homem não provém da mulher, mas a mulher do homem.', 8, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também o homem não foi criado por causa da mulher, mas a mulher por causa do homem.', 9, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, a mulher deve ter sobre a cabeça sinal de poderio, por causa dos anjos.', 10, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia, nem o homem é sem a mulher, nem a mulher sem o homem, no Senhor.', 11, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, como a mulher provém do homem, assim também o homem provém da mulher, mas tudo vem de Deus.', 12, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Julgai entre vós mesmos: é decente que a mulher ore a Deus descoberta?', 13, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou não vos ensina a mesma natureza que é desonra para o homem ter cabelo crescido?', 14, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ter a mulher cabelo crescido lhe é honroso, porque o cabelo lhe foi dado em lugar de véu.', 15, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se alguém quiser ser contencioso, nós não temos tal costume, nem as igrejas de Deus.', 16, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto, porém, que vou dizer-vos não vos louvo; porquanto vos ajuntais, não para melhor, senão para pior.', 17, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque antes de tudo ouço que, quando vos ajuntais na igreja, há entre vós dissensões; e em parte o creio.', 18, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E até importa que haja entre vós heresias, para que os que são sinceros se manifestem entre vós.', 19, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que, quando vos ajuntais num lugar, não é para comer a ceia do Senhor.', 20, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, comendo, cada um toma antecipadamente a sua própria ceia; e assim um tem fome e outro embriaga-se.', 21, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não tendes porventura casas para comer e para beber? Ou desprezais a igreja de Deus, e envergonhais os que nada têm? Que vos direi? Louvar-vos-ei? Nisto não vos louvo.', 22, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu recebi do Senhor o que também vos ensinei: que o Senhor Jesus, na noite em que foi traído, tomou o pão;', 23, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo dado graças, o partiu e disse: Tomai, comei; isto é o meu corpo que é partido por vós; fazei isto em memória de mim.', 24, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Semelhantemente também, depois de cear, tomou o cálice, dizendo: Este cálice é o novo testamento no meu sangue; fazei isto, todas as vezes que beberdes, em memória de mim.', 25, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todas as vezes que comerdes este pão e beberdes este cálice anunciais a morte do Senhor, até que venha.', 26, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, qualquer que comer este pão, ou beber o cálice do Senhor indignamente, será culpado do corpo e do sangue do Senhor.', 27, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Examine-se, pois, o homem a si mesmo, e assim coma deste pão e beba deste cálice.', 28, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o que come e bebe indignamente, come e bebe para sua própria condenação, não discernindo o corpo do Senhor.', 29, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por causa disto há entre vós muitos fracos e doentes, e muitos que dormem.', 30, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se nós nos julgássemos a nós mesmos, não seríamos julgados.', 31, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando somos julgados, somos repreendidos pelo Senhor, para não sermos condenados com o mundo.', 32, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, meus irmãos, quando vos ajuntais para comer, esperai uns pelos outros.', 33, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se algum tiver fome, coma em casa, para que não vos ajunteis para condenação. Quanto às demais coisas, ordená-las-ei quando for.', 34, 11, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, quanto às coisas que me escrevestes, bom seria que o homem não tocasse em mulher;', 1, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, por causa da fornicação, cada um tenha a sua própria mulher, e cada uma tenha o seu próprio marido.', 2, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O marido pague à mulher a devida benevolência, e da mesma sorte a mulher ao marido.', 3, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A mulher não tem poder sobre o seu próprio corpo, mas tem-no o marido; e também da mesma maneira o marido não tem poder sobre o seu próprio corpo, mas tem-no a mulher.', 4, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos priveis um ao outro, senão por consentimento mútuo por algum tempo, para vos aplicardes ao jejum e à oração; e depois ajuntai-vos outra vez, para que Satanás não vos tente pela vossa incontinência.', 5, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo, porém, isto como que por permissão e não por mandamento.', 6, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque quereria que todos os homens fossem como eu mesmo; mas cada um tem de Deus o seu próprio dom, um de uma maneira e outro de outra.', 7, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo, porém, aos solteiros e às viúvas, que lhes é bom se ficarem como eu.', 8, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se não podem conter-se, casem-se. Porque é melhor casar do que abrasar-se.', 9, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia, aos casados mando, não eu mas o Senhor, que a mulher não se aparte do marido.', 10, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, porém, se apartar, que fique sem casar, ou que se reconcilie com o marido; e que o marido não deixe a mulher.', 11, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas aos outros digo eu, não o Senhor: Se algum irmão tem mulher descrente, e ela consente em habitar com ele, não a deixe.', 12, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se alguma mulher tem marido descrente, e ele consente em habitar com ela, não o deixe.', 13, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o marido descrente é santificado pela mulher; e a mulher descrente é santificada pelo marido; de outra sorte os vossos filhos seriam imundos; mas agora são santos.', 14, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se o descrente se apartar, aparte-se; porque neste caso o irmão, ou irmã, não está sujeito à servidão; mas Deus chamou-nos para a paz.', 15, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, de onde sabes, ó mulher, se salvarás teu marido? ou, de onde sabes, ó marido, se salvarás tua mulher?', 16, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim cada um ande como Deus lhe repartiu, cada um como o Senhor o chamou. É o que ordeno em todas as igrejas.', 17, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É alguém chamado, estando circuncidado? fique circuncidado. É alguém chamado estando incircuncidado? não se circuncide.', 18, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A circuncisão é nada e a incircuncisão nada é, mas, sim, a observância dos mandamentos de Deus.', 19, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cada um fique na vocação em que foi chamado.', 20, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Foste chamado sendo servo? não te dê cuidado; e, se ainda podes ser livre, aproveita a ocasião.', 21, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o que é chamado pelo Senhor, sendo servo, é liberto do Senhor; e da mesma maneira também o que é chamado sendo livre, servo é de Cristo.', 22, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fostes comprados por bom preço; não vos façais servos dos homens.', 23, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, cada um fique diante de Deus no estado em que foi chamado.', 24, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, quanto às virgens, não tenho mandamento do Senhor; dou, porém, o meu parecer, como quem tem alcançado misericórdia do Senhor para ser fiel.', 25, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho, pois, por bom, por causa da instante necessidade, que é bom para o homem o estar assim.', 26, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estás ligado à mulher? não busques separar-te. Estás livre de mulher? não busques mulher.', 27, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se te casares, não pecas; e, se a virgem se casar, não peca. Todavia os tais terão tribulações na carne, e eu quereria poupar-vos.', 28, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto, porém, vos digo, irmãos, que o tempo se abrevia; o que resta é que também os que têm mulheres sejam como se não as tivessem;', 29, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que choram, como se não chorassem; e os que folgam, como se não folgassem; e os que compram, como se não possuíssem;', 30, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que usam deste mundo, como se dele não abusassem, porque a aparência deste mundo passa.', 31, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E bem quisera eu que estivésseis sem cuidado. O solteiro cuida das coisas do Senhor, em como há de agradar ao Senhor;', 32, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que é casado cuida das coisas do mundo, em como há de agradar à mulher.', 33, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Há diferença entre a mulher casada e a virgem. A solteira cuida das coisas do Senhor para ser santa, tanto no corpo como no espírito; porém, a casada cuida das coisas do mundo, em como há de agradar ao marido.', 34, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E digo isto para proveito vosso; não para vos enlaçar, mas para o que é decente e conveniente, para vos unirdes ao Senhor sem distração alguma.', 35, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se alguém julga que trata indignamente a sua virgem, se tiver passado a flor da idade, e se for necessário, que faça o tal o que quiser; não peca; casem-se.', 36, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia o que está firme em seu coração, não tendo necessidade, mas com poder sobre a sua própria vontade, se resolveu no seu coração guardar a sua virgem, faz bem.', 37, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que, o que a dá em casamento faz bem; mas o que não a dá em casamento faz melhor.', 38, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A mulher casada está ligada pela lei todo o tempo que o seu marido vive; mas, se falecer o seu marido fica livre para casar com quem quiser, contanto que seja no Senhor.', 39, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Será, porém, mais bem-aventurada se ficar assim, segundo o meu parecer, e também eu cuido que tenho o Espírito de Deus.', 40, 7, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo, pois: Porventura rejeitou Deus o seu povo? De modo nenhum; porque também eu sou israelita, da descendência de Abraão, da tribo de Benjamim.', 1, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deus não rejeitou o seu povo, que antes conheceu. Ou não sabeis o que a Escritura diz de Elias, como fala a Deus contra Israel, dizendo:', 2, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Senhor, mataram os teus profetas, e derribaram os teus altares; e só eu fiquei, e buscam a minha alma?', 3, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas que lhe diz a resposta divina? Reservei para mim sete mil homens, que não dobraram os joelhos a Baal.', 4, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, pois, também agora neste tempo ficou um remanescente, segundo a eleição da graça.', 5, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas se é por graça, já não é pelas obras; de outra maneira, a graça já não é graça. Se, porém, é pelas obras, já não é mais graça; de outra maneira a obra já não é obra.', 6, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois quê? O que Israel buscava não o alcançou; mas os eleitos o alcançaram, e os outros foram endurecidos.', 7, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como está escrito: Deus lhes deu espírito de profundo sono, olhos para não verem, e ouvidos para não ouvirem, até ao dia de hoje.', 8, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Davi diz: Torne-se-lhes a sua mesa em laço, e em armadilha, E em tropeço, por sua retribuição;', 9, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Escureçam-se-lhes os olhos para não verem, E encurvem-se-lhes continuamente as costas.', 10, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo, pois: Porventura tropeçaram, para que caíssem? De modo nenhum, mas pela sua queda veio a salvação aos gentios, para os incitar à emulação.', 11, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se a sua queda é a riqueza do mundo, e a sua diminuição a riqueza dos gentios, quanto mais a sua plenitude!', 12, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque convosco falo, gentios, que, enquanto for apóstolo dos gentios, exalto o meu ministério;', 13, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para ver se de alguma maneira posso incitar à emulação os da minha carne e salvar alguns deles.', 14, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se a sua rejeição é a reconciliação do mundo, qual será a sua admissão, senão a vida dentre os mortos?', 15, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se as primícias são santas, também a massa o é; se a raiz é santa, também os ramos o são.', 16, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se alguns dos ramos foram quebrados, e tu, sendo zambujeiro, foste enxertado em lugar deles, e feito participante da raiz e da seiva da oliveira,', 17, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não te glories contra os ramos; e, se contra eles te gloriares, não és tu que sustentas a raiz, mas a raiz a ti.', 18, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dirás, pois: Os ramos foram quebrados, para que eu fosse enxertado.', 19, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Está bem; pela sua incredulidade foram quebrados, e tu estás em pé pela fé. Então não te ensoberbeças, mas teme.', 20, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se Deus não poupou os ramos naturais, teme que não te poupe a ti também.', 21, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Considera, pois, a bondade e a severidade de Deus: para com os que caíram, severidade; mas para contigo, benignidade, se permaneceres na sua benignidade; de outra maneira também tu serás cortado.', 22, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também eles, se não permanecerem na incredulidade, serão enxertados; porque poderoso é Deus para os tornar a enxertar.', 23, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se tu foste cortado do natural zambujeiro e, contra a natureza, enxertado na boa oliveira, quanto mais esses, que são naturais, serão enxertados na sua própria oliveira!', 24, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não quero, irmãos, que ignoreis este segredo (para que não presumais de vós mesmos): que o endurecimento veio em parte sobre Israel, até que a plenitude dos gentios haja entrado.', 25, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim todo o Israel será salvo, como está escrito: De Sião virá o Libertador, E desviará de Jacó as impiedades.', 26, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E esta será a minha aliança com eles, Quando eu tirar os seus pecados.', 27, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim que, quanto ao evangelho, são inimigos por causa de vós; mas, quanto à eleição, amados por causa dos pais.', 28, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os dons e a vocação de Deus são sem arrependimento.', 29, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque assim como vós também antigamente fostes desobedientes a Deus, mas agora alcançastes misericórdia pela desobediência deles,', 30, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também estes agora foram desobedientes, para também alcançarem misericórdia pela misericórdia a vós demonstrada.', 31, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus encerrou a todos debaixo da desobediência, para com todos usar de misericórdia.', 32, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ó profundidade das riquezas, tanto da sabedoria, como da ciência de Deus! Quão insondáveis são os seus juízos, e quão inescrutáveis os seus caminhos!', 33, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por que quem compreendeu a mente do Senhor? ou quem foi seu conselheiro?', 34, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou quem lhe deu primeiro a ele, para que lhe seja recompensado?', 35, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque dele e por ele, e para ele, são todas as coisas; glória, pois, a ele eternamente. Amém.', 36, 11, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, quanto à coleta que se faz para os santos, fazei vós também o mesmo que ordenei às igrejas da Galácia.', 1, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No primeiro dia da semana cada um de vós ponha de parte o que puder ajuntar, conforme a sua prosperidade, para que não se façam as coletas quando eu chegar.', 2, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando tiver chegado, mandarei os que por cartas aprovardes, para levar a vossa dádiva a Jerusalém.', 3, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se valer a pena que eu também vá, irão comigo.', 4, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irei, porém, ter convosco depois de ter passado pela macedônia (porque tenho de passar pela macedônia).', 5, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E bem pode ser que fique convosco, e passe também o inverno, para que me acompanheis aonde quer que eu for.', 6, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não vos quero agora ver de passagem, mas espero ficar convosco algum tempo, se o Senhor o permitir.', 7, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ficarei, porém, em Éfeso até ao Pentecostes;', 8, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque uma porta grande e eficaz se me abriu; e há muitos adversários.', 9, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se Timóteo for, vede que esteja sem temor convosco; porque trabalha na obra do Senhor, como eu também.', 10, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, ninguém o despreze, mas acompanhai-o em paz, para que venha ter comigo; pois o espero com os irmãos.', 11, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, acerca do irmão Apolo, roguei-lhe muito que fosse com os irmãos ter convosco, mas, na verdade, não teve vontade de ir agora; irá, porém, quando se lhe oferecer boa ocasião.', 12, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vigiai, estai firmes na fé; portai-vos varonilmente, e fortalecei-vos.', 13, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todas as vossas coisas sejam feitas com amor.', 14, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora vos rogo, irmãos (sabeis que a família de Estéfanas é as primícias da Acaia, e que se tem dedicado ao ministério dos santos),', 15, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que também vos sujeiteis aos tais, e a todo aquele que auxilia na obra e trabalha.', 16, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Folgo, porém, com a vinda de Estéfanas, de Fortunato e de Acaico; porque estes supriram o que da vossa parte me faltava.', 17, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque recrearam o meu espírito e o vosso. Reconhecei, pois, aos tais.', 18, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As igrejas da Ásia vos saúdam. Saúdam-vos afetuosamente no Senhor áqüila e Priscila, com a igreja que está em sua casa.', 19, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos os irmãos vos saúdam. Saudai-vos uns aos outros com ósculo santo.', 20, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Saudação da minha própria mão, de Paulo.', 21, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém não ama ao Senhor Jesus Cristo, seja anátema. Maranata!', 22, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça do Senhor Jesus Cristo seja convosco.', 23, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O meu amor seja com todos vós em Cristo Jesus. Amém.', 24, 16, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em Cristo digo a verdade, não minto (dando-me testemunho a minha consciência no Espírito Santo):', 1, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que tenho grande tristeza e contínua dor no meu coração.', 2, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu mesmo poderia desejar ser anátema de Cristo, por amor de meus irmãos, que são meus parentes segundo a carne;', 3, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que são israelitas, dos quais é a adoção de filhos, e a glória, e as alianças, e a lei, e o culto, e as promessas;', 4, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dos quais são os pais, e dos quais é Cristo segundo a carne, o qual é sobre todos, Deus bendito eternamente. Amém.', 5, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não que a palavra de Deus haja faltado, porque nem todos os que são de Israel são israelitas;', 6, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem por serem descendência de Abraão são todos filhos; mas: Em Isaque será chamada a tua descendência.', 7, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto é, não são os filhos da carne que são filhos de Deus, mas os filhos da promessa são contados como descendência.', 8, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a palavra da promessa é esta: Por este tempo virei, e Sara terá um filho.', 9, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não somente esta, mas também Rebeca, quando concebeu de um, de Isaque, nosso pai;', 10, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, não tendo eles ainda nascido, nem tendo feito bem ou mal (para que o propósito de Deus, segundo a eleição, ficasse firme, não por causa das obras, mas por aquele que chama),', 11, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Foi-lhe dito a ela: O maior servirá ao menor.', 12, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como está escrito: Amei a Jacó, e odiei a Esaú.', 13, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que diremos pois? que há injustiça da parte de Deus? De maneira nenhuma.', 14, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois diz a Moisés: Compadecer-me-ei de quem me compadecer, e terei misericórdia de quem eu tiver misericórdia.', 15, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, pois, isto não depende do que quer, nem do que corre, mas de Deus, que se compadece.', 16, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque diz a Escritura a Faraó: Para isto mesmo te levantei; para em ti mostrar o meu poder, e para que o meu nome seja anunciado em toda a terra.', 17, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Logo, pois, compadece-se de quem quer, e endurece a quem quer.', 18, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dir-me-ás então: Por que se queixa ele ainda? Porquanto, quem tem resistido à sua vontade?', 19, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, ó homem, quem és tu, que a Deus replicas? Porventura a coisa formada dirá ao que a formou: Por que me fizeste assim?', 20, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou não tem o oleiro poder sobre o barro, para da mesma massa fazer um vaso para honra e outro para desonra?', 21, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que direis se Deus, querendo mostrar a sua ira, e dar a conhecer o seu poder, suportou com muita paciência os vasos da ira, preparados para a perdição;', 22, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que também desse a conhecer as riquezas da sua glória nos vasos de misericórdia, que para glória já dantes preparou,', 23, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais somos nós, a quem também chamou, não só dentre os judeus, mas também dentre os gentios?', 24, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como também diz em Oséias:Chamarei meu povo ao que não era meu povo;E amada à que não era amada.', 25, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sucederá que no lugar em que lhes foi dito:Vós não sois meu povo;Aí serão chamados filhos do Deus vivo.', 26, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Também Isaías clama acerca de Israel: Ainda que o número dos filhos de Israel seja como a areia do mar, o remanescente é que será salvo.', 27, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ele completará a obra e abreviá-la-á em justiça; porque o Senhor fará breve a obra sobre a terra.', 28, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E como antes disse Isaías:Se o Senhor dos Exércitos nos não deixara descendência,Teríamos nos tornado como Sodoma, e teríamos sido feitos como Gomorra.', 29, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que diremos pois? Que os gentios, que não buscavam a justiça, alcançaram a justiça? Sim, mas a justiça que é pela fé.', 30, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Israel, que buscava a lei da justiça, não chegou à lei da justiça.', 31, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por quê? Porque não foi pela fé, mas como que pelas obras da lei; pois tropeçaram na pedra de tropeço;', 32, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como está escrito:Eis que eu ponho em Sião uma pedra de tropeço, e uma rocha de escândalo;E todo aquele que crer nela não será confundido.', 33, 9, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esta é uma palavra fiel: se alguém deseja o episcopado, excelente obra deseja.', 1, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Convém, pois, que o bispo seja irrepreensível, marido de uma mulher, vigilante, sóbrio, honesto, hospitaleiro, apto para ensinar;', 2, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não dado ao vinho, não espancador, não cobiçoso de torpe ganância, mas moderado, não contencioso, não avarento;', 3, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que governe bem a sua própria casa, tendo seus filhos em sujeição, com toda a modéstia', 4, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Porque, se alguém não sabe governar a sua própria casa, terá cuidado da igreja de Deus? );', 5, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não neófito, para que, ensoberbecendo-se, não caia na condenação do diabo.', 6, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Convém também que tenha bom testemunho dos que estão de fora, para que não caia em afronta, e no laço do diabo.', 7, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da mesma sorte os diáconos sejam honestos, não de língua dobre, não dados a muito vinho, não cobiçosos de torpe ganância;', 8, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Guardando o mistério da fé numa consciência pura.', 9, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também estes sejam primeiro provados, depois sirvam, se forem irrepreensíveis.', 10, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da mesma sorte as esposas sejam honestas, não maldizentes, sóbrias e fiéis em tudo.', 11, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os diáconos sejam maridos de uma só mulher, e governem bem a seus filhos e suas próprias casas.', 12, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os que servirem bem como diáconos, adquirirão para si uma boa posição e muita confiança na fé que há em Cristo Jesus.', 13, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Escrevo-te estas coisas, esperando ir ver-te bem depressa;', 14, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se tardar, para que saibas como convém andar na casa de Deus, que é a igreja do Deus vivo, a coluna e firmeza da verdade.', 15, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sem dúvida alguma, grande é o mistério da piedade: Deus se manifestou em carne, foi justificado no Espírito, visto dos anjos, pregado aos gentios, crido no mundo, recebido acima na glória.', 16, 3, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu, irmãos, não vos pude falar como a espirituais, mas como a carnais, como a meninos em Cristo.', 1, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Com leite vos criei, e não com carne, porque ainda não podíeis, nem tampouco ainda agora podeis,', 2, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ainda sois carnais; pois, havendo entre vós inveja, contendas e dissensões, não sois porventura carnais, e não andais segundo os homens?', 3, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, dizendo um: Eu sou de Paulo; e outro: Eu de Apolo; porventura não sois carnais?', 4, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, quem é Paulo, e quem é Apolo, senão ministros pelos quais crestes, e conforme o que o Senhor deu a cada um?', 5, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu plantei, Apolo regou; mas Deus deu o crescimento.', 6, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, nem o que planta é alguma coisa, nem o que rega, mas Deus, que dá o crescimento.', 7, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o que planta e o que rega são um; mas cada um receberá o seu galardão segundo o seu trabalho.', 8, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nós somos cooperadores de Deus; vós sois lavoura de Deus e edifício de Deus.', 9, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segundo a graça de Deus que me foi dada, pus eu, como sábio arquiteto, o fundamento, e outro edifica sobre ele; mas veja cada um como edifica sobre ele.', 10, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ninguém pode pôr outro fundamento além do que já está posto, o qual é Jesus Cristo.', 11, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se alguém sobre este fundamento formar um edifício de ouro, prata, pedras preciosas, madeira, feno, palha,', 12, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A obra de cada um se manifestará; na verdade o dia a declarará, porque pelo fogo será descoberta; e o fogo provará qual seja a obra de cada um.', 13, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se a obra que alguém edificou nessa parte permanecer, esse receberá galardão.', 14, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se a obra de alguém se queimar, sofrerá detrimento; mas o tal será salvo, todavia como pelo fogo.', 15, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sabeis vós que sois o templo de Deus e que o Espírito de Deus habita em vós?', 16, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém destruir o templo de Deus, Deus o destruirá; porque o templo de Deus, que sois vós, é santo.', 17, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém se engane a si mesmo. Se alguém dentre vós se tem por sábio neste mundo, faça-se louco para ser sábio.', 18, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a sabedoria deste mundo é loucura diante de Deus; pois está escrito: Ele apanha os sábios na sua própria astúcia.', 19, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra vez: O Senhor conhece os pensamentos dos sábios, que são vãos.', 20, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, ninguém se glorie nos homens; porque tudo é vosso;', 21, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Seja Paulo, seja Apolo, seja Cefas, seja o mundo, seja a vida, seja a morte, seja o presente, seja o futuro; tudo é vosso,', 22, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós de Cristo, e Cristo de Deus.', 23, 3, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra vez começou a ensinar junto do mar, e ajuntou-se a ele grande multidão, de sorte que ele entrou e assentou-se num barco, sobre o mar; e toda a multidão estava em terra junto do mar.', 1, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ensinava-lhes muitas coisas por parábolas, e lhes dizia na sua doutrina:', 2, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvi: Eis que saiu o semeador a semear.', 3, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que semeando ele, uma parte da semente caiu junto do caminho, e vieram as aves do céu, e a comeram;', 4, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra caiu sobre pedregais, onde não havia muita terra, e nasceu logo, porque não tinha terra profunda;', 5, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, saindo o sol, queimou-se; e, porque não tinha raiz, secou-se.', 6, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra caiu entre espinhos e, crescendo os espinhos, a sufocaram e não deu fruto.', 7, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra caiu em boa terra e deu fruto, que vingou e cresceu; e um produziu trinta, outro sessenta, e outro cem.', 8, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Quem tem ouvidos para ouvir, ouça.', 9, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando se achou só, osque estavam junto dele com os doze interrogaram-no acerca da parábola.', 10, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse-lhes: A vós vos é dado saber os mistérios do reino de Deus, mas aos que estão de fora todas estas coisas se dizem por parábolas,', 11, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que, vendo, vejam, e não percebam; e, ouvindo, ouçam, e não entendam; para que não se convertam, e lhes sejam perdoados os pecados.', 12, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Não percebeis esta parábola? Como, pois, entendereis todas as parábolas?', 13, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que semeia, semeia a palavra;', 14, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, os que estão junto do caminho são aqueles em quem a palavra é semeada; mas, tendo-a eles ouvido, vem logo Satanás e tira a palavra que foi semeada nos seus corações.', 15, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E da mesma forma os que recebem a semente sobre pedregais; os quais, ouvindo a palavra, logo com prazer a recebem;', 16, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não têm raiz em si mesmos, antes são temporãos; depois, sobrevindo tribulação ou perseguição, por causa da palavra, logo se escandalizam.', 17, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outros são os que recebem a semente entre espinhos, os quais ouvem a palavra;', 18, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os cuidados deste mundo, e os enganos das riquezas e as ambições de outras coisas, entrando, sufocam a palavra, e fica infrutífera.', 19, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estes são os que foram semeados em boa terra, os que ouvem a palavra e a recebem, e dão fruto, um trinta, e outro sessenta, e outro cem.', 20, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Vem porventura a candeia para se meter debaixo do alqueire, ou debaixo da cama? não vem antes para se colocar no velador?', 21, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nada há encoberto que não haja de ser manifesto; e nada se faz para ficar oculto, mas para ser descoberto.', 22, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém tem ouvidos para ouvir, ouça.', 23, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Atendei ao que ides ouvir.  Com a medida com que medirdes vos medirão a vós, e ser-vos-á ainda acrescentada a vós que ouvis.', 24, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ao que tem, ser-lhe-á dado; e, ao que não tem, até o que tem lhe será tirado.', 25, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia: O reino de Deus é assim como se um homem lançasse semente à terra.', 26, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dormisse, e se levantasse de noite ou de dia, e a semente brotasse e crescesse, não sabendo ele como.', 27, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a terra por si mesma frutifica, primeiro a erva, depois a espiga, por último o grão cheio na espiga.', 28, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando já o fruto se mostra, mete-se-lhe logo a foice, porque está chegada a ceifa.', 29, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia: A que assemelhare-mos o reino de Deus? ou com que parábola o representaremos?', 30, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É como um grão de mostarda, que, quando se semeia na terra, é a menor de todas as sementes que há na terra;', 31, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, tendo sido semeado, cresce; e faz-se a maior de todas as hortaliças, e cria grandes ramos, de tal maneira que as aves do céu podem aninhar-se debaixo da sua sombra.', 32, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E com muitas parábolas tais lhes dirigia a palavra, segundo o que podiam compreender.', 33, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sem parábolas nunca lhes falava; porém, tudo declarava em particular aos seus discípulos.', 34, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, naquele dia, sendo já tarde, disse-lhes: Passemos para o outro lado.', 35, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles, deixando a multidão, o levaram consigo, assim como estava, no barco; e havia também com ele outros barquinhos.', 36, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levantou-se grande temporal de vento, e subiam as ondas por cima do barco, de maneira que já se enchia.', 37, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele estava na popa, dormindo sobre uma almofada, e despertaram-no, dizendo-lhe: Mestre, não se te dá que pereçamos?', 38, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, despertando, repreendeu o vento, e disse ao mar: Cala-te, aquieta-te.  E o vento se aquietou, e houve grande bonança.', 39, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Por que sois tão tímidos? Ainda não tendes fé?', 40, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sentiram um grande temor, e diziam uns aos outros: Mas quem é este, que até o vento e o mar lhe obedecem?', 41, 4, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, quanto ao que está enfermo na fé, recebei-o, não em contendas sobre dúvidas.', 1, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque um crê que de tudo se pode comer, e outro, que é fraco, come legumes.', 2, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que come não despreze o que não come; e o que não come, não julgue o que come; porque Deus o recebeu por seu.', 3, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem és tu, que julgas o servo alheio? Para seu próprio senhor ele está em pé ou cai. Mas estará firme, porque poderoso é Deus para o firmar.', 4, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Um faz diferença entre dia e dia, mas outro julga iguais todos os dias. Cada um esteja inteiramente seguro em sua própria mente.', 5, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que faz caso do dia, para o Senhor o faz e o que não faz caso do dia para o Senhor o não faz. O que come, para o Senhor come, porque dá graças a Deus; e o que não come, para o Senhor não come, e dá graças a Deus.', 6, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nenhum de nós vive para si, e nenhum morre para si.', 7, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se vivemos, para o Senhor vivemos; se morremos, para o Senhor morremos. De sorte que, ou vivamos ou morramos, somos do Senhor.', 8, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque foi para isto que morreu Cristo, e ressurgiu, e tornou a viver, para ser Senhor, tanto dos mortos, como dos vivos.', 9, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas tu, por que julgas teu irmão? Ou tu, também, por que desprezas teu irmão? Pois todos havemos de comparecer ante o tribunal de Cristo.', 10, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque está escrito:Como eu vivo, diz o Senhor, que todo o joelho se dobrará a mim,E toda a língua confessará a Deus.', 11, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que cada um de nós dará conta de si mesmo a Deus.', 12, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim que não nos julguemos mais uns aos outros; antes seja o vosso propósito não pôr tropeço ou escândalo ao irmão.', 13, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu sei, e estou certo no Senhor Jesus, que nenhuma coisa é de si mesma imunda, a não ser para aquele que a tem por imunda; para esse é imunda.', 14, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se por causa da comida se contrista teu irmão, já não andas conforme o amor. Não destruas por causa da tua comida aquele por quem Cristo morreu.', 15, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não seja, pois, blasfemado o vosso bem;', 16, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o reino de Deus não é comida nem bebida, mas justiça, e paz, e alegria no Espírito Santo.', 17, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque quem nisto serve a Cristo agradável é a Deus e aceito aos homens.', 18, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sigamos, pois, as coisas que servem para a paz e para a edificação de uns para com os outros.', 19, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não destruas por causa da comida a obra de Deus. É verdade que tudo é limpo, mas mal vai para o homem que come com escândalo.', 20, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bom é não comer carne, nem beber vinho, nem fazer outras coisas em que teu irmão tropece, ou se escandalize, ou se enfraqueça.', 21, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tens tu fé? Tem-na em ti mesmo diante de Deus. Bem-aventurado aquele que não se condena a si mesmo naquilo que aprova.', 22, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas aquele que tem dúvidas, se come está condenado, porque não come por fé; e tudo o que não é de fé é pecado.', 23, 14, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Toda a alma esteja sujeita às potestades superiores; porque não há potestade que não venha de Deus; e as potestades que há foram ordenadas por Deus.', 1, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso quem resiste à potestade resiste à ordenação de Deus; e os que resistem trarão sobre si mesmos a condenação.', 2, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os magistrados não são terror para as boas obras, mas para as más. Queres tu, pois, não temer a potestade? Faze o bem, e terás louvor dela.', 3, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ela é ministro de Deus para teu bem. Mas, se fizeres o mal, teme, pois não traz debalde a espada; porque é ministro de Deus, e vingador para castigar o que faz o mal.', 4, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto é necessário que lhe estejais sujeitos, não somente pelo castigo, mas também pela consciência.', 5, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por esta razão também pagais tributos, porque são ministros de Deus, atendendo sempre a isto mesmo.', 6, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, dai a cada um o que deveis: a quem tributo, tributo; a quem imposto, imposto; a quem temor, temor; a quem honra, honra.', 7, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A ninguém devais coisa alguma, a não ser o amor com que vos ameis uns aos outros; porque quem ama aos outros cumpriu a lei.', 8, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Com efeito: Não adulterarás, não matarás, não furtarás, não darás falso testemunho, não cobiçarás; e se há algum outro mandamento, tudo nesta palavra se resume: Amarás ao teu próximo como a ti mesmo.', 9, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O amor não faz mal ao próximo. De sorte que o cumprimento da lei é o amor.', 10, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E isto digo, conhecendo o tempo, que já é hora de despertarmos do sono; porque a nossa salvação está agora mais perto de nós do que quando aceitamos a fé.', 11, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A noite é passada, e o dia é chegado. Rejeitemos, pois, as obras das trevas, e vistamo-nos das armas da luz.', 12, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Andemos honestamente, como de dia; não em glutonarias, nem em bebedeiras, nem em desonestidades, nem em dissoluções, nem em contendas e inveja.', 13, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas revesti-vos do Senhor Jesus Cristo, e não tenhais cuidado da carne em suas concupiscências.', 14, 13, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Segui o amor, e procurai com zelo os dons espirituais, mas principalmente o de profetizar.', 1, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o que fala em língua desconhecida não fala aos homens, senão a Deus; porque ninguém o entende, e em espírito fala mistérios.', 2, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que profetiza fala aos homens, para edificação, exortação e consolação.', 3, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que fala em língua desconhecida edifica-se a si mesmo, mas o que profetiza edifica a igreja.', 4, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu quero que todos vós faleis em línguas, mas muito mais que profetizeis; porque o que profetiza é maior do que o que fala em línguas, a não ser que também interprete para que a igreja receba edificação.', 5, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora, irmãos, se eu for ter convosco falando em línguas, que vos aproveitaria, se não vos falasse ou por meio da revelação, ou da ciência, ou da profecia, ou da doutrina?', 6, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Da mesma sorte, se as coisas inanimadas, que fazem som, seja flauta, seja cítara, não formarem sons distintos, como se conhecerá o que se toca com a flauta ou com a cítara?', 7, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se a trombeta der sonido incerto, quem se preparará para a batalha?', 8, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também vós, se com a língua não pronunciardes palavras bem inteligíveis, como se entenderá o que se diz? porque estareis como que falando ao ar.', 9, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Há, por exemplo, tanta espécie de vozes no mundo, e nenhuma delas é sem significação.', 10, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se eu ignorar o sentido da voz, serei bárbaro para aquele a quem falo, e o que fala será bárbaro para mim.', 11, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também vós, como desejais dons espirituais, procurai abundar neles, para edificação da igreja.', 12, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, o que fala em língua desconhecida, ore para que a possa interpretar.', 13, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se eu orar em língua desconhecida, o meu espírito ora bem, mas o meu entendimento fica sem fruto.', 14, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que farei, pois? Orarei com o espírito, mas também orarei com o entendimento; cantarei com o espírito, mas também cantarei com o entendimento.', 15, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De outra maneira, se tu bendisseres com o espírito, como dirá o que ocupa o lugar de indouto, o Amém, sobre a tua ação de graças, visto que não sabe o que dizes?', 16, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque realmente tu dás bem as graças, mas o outro não é edificado.', 17, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dou graças ao meu Deus, porque falo mais línguas do que vós todos.', 18, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia eu antes quero falar na igreja cinco palavras na minha própria inteligência, para que possa também instruir os outros, do que dez mil palavras em língua desconhecida.', 19, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, não sejais meninos no entendimento, mas sede meninos na malícia, e adultos no entendimento.', 20, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Está escrito na lei: Por gente de outras línguas, e por outros lábios, falarei a este povo; e ainda assim me não ouvirão, diz o Senhor.', 21, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que as línguas são um sinal, não para os fiéis, mas para os infiéis; e a profecia não é sinal para os infiéis, mas para os fiéis.', 22, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, pois, toda a igreja se congregar num lugar, e todos falarem em línguas, e entrarem indoutos ou infiéis, não dirão porventura que estais loucos?', 23, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se todos profetizarem, e algum indouto ou infiel entrar, de todos é convencido, de todos é julgado.', 24, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, portanto, os segredos do seu coração ficam manifestos, e assim, lançando-se sobre o seu rosto, adorará a Deus, publicando que Deus está verdadeiramente entre vós.', 25, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que fareis pois, irmãos? Quando vos ajuntais, cada um de vós tem salmo, tem doutrina, tem revelação, tem língua, tem interpretação. Faça-se tudo para edificação.', 26, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se alguém falar em língua desconhecida, faça-se isso por dois, ou quando muito três, e por sua vez, e haja intérprete.', 27, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se não houver intérprete, esteja calado na igreja, e fale consigo mesmo, e com Deus.', 28, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E falem dois ou três profetas, e os outros julguem.', 29, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se a outro, que estiver assentado, for revelada alguma coisa, cale-se o primeiro.', 30, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos podereis profetizar, uns depois dos outros; para que todos aprendam, e todos sejam consolados.', 31, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os espíritos dos profetas estão sujeitos aos profetas.', 32, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Deus não é Deus de confusão, senão de paz, como em todas as igrejas dos santos.', 33, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As vossas mulheres estejam caladas nas igrejas; porque não lhes é permitido falar; mas estejam sujeitas, como também ordena a lei.', 34, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se querem aprender alguma coisa, interroguem em casa a seus próprios maridos; porque é vergonhoso que as mulheres falem na igreja.', 35, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura saiu dentre vós a palavra de Deus? Ou veio ela somente para vós?', 36, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém cuida ser profeta, ou espiritual, reconheça que as coisas que vos escrevo são mandamentos do Senhor.', 37, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se alguém ignora isto, que ignore.', 38, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, irmãos, procurai, com zelo, profetizar, e não proibais falar línguas.', 39, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas faça-se tudo decentemente e com ordem.', 40, 14, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Também vos notifico, irmãos, o evangelho que já vos tenho anunciado; o qual também recebestes, e no qual também permaneceis.', 1, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelo qual também sois salvos se o retiverdes tal como vo-lo tenho anunciado; se não é que crestes em vão.', 2, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque primeiramente vos entreguei o que também recebi: que Cristo morreu por nossos pecados, segundo as Escrituras,', 3, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que foi sepultado, e que ressuscitou ao terceiro dia, segundo as Escrituras.', 4, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que foi visto por Cefas, e depois pelos doze.', 5, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois foi visto, uma vez, por mais de quinhentos irmãos, dos quais vive ainda a maior parte, mas alguns já dormem também.', 6, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois foi visto por Tiago, depois por todos os apóstolos.', 7, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por derradeiro de todos me apareceu também a mim, como a um abortivo.', 8, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu sou o menor dos apóstolos, que não sou digno de ser chamado apóstolo, pois que persegui a igreja de Deus.', 9, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas pela graça de Deus sou o que sou; e a sua graça para comigo não foi vã, antes trabalhei muito mais do que todos eles; todavia não eu, mas a graça de Deus, que está comigo.', 10, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, ou seja eu ou sejam eles, assim pregamos e assim haveis crido.', 11, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, se se prega que Cristo ressuscitou dentre os mortos, como dizem alguns dentre vós que não há ressurreição de mortos?', 12, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se não há ressurreição de mortos, também Cristo não ressuscitou.', 13, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se Cristo não ressuscitou, logo é vã a nossa pregação, e também é vã a vossa fé.', 14, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assim somos também considerados como falsas testemunhas de Deus, pois testificamos de Deus, que ressuscitou a Cristo, ao qual, porém, não ressuscitou, se, na verdade, os mortos não ressuscitam.', 15, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se os mortos não ressuscitam, também Cristo não ressuscitou.', 16, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se Cristo não ressuscitou, é vã a vossa fé, e ainda permaneceis nos vossos pecados.', 17, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também os que dormiram em Cristo estão perdidos.', 18, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se esperamos em Cristo só nesta vida, somos os mais miseráveis de todos os homens.', 19, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas de fato Cristo ressuscitou dentre os mortos, e foi feito as primícias dos que dormem.', 20, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque assim como a morte veio por um homem, também a ressurreição dos mortos veio por um homem.', 21, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, assim como todos morrem em Adão, assim também todos serão vivificados em Cristo.', 22, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas cada um por sua ordem: Cristo as primícias, depois os que são de Cristo, na sua vinda.', 23, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois virá o fim, quando tiver entregado o reino a Deus, ao Pai, e quando houver aniquilado todo o império, e toda a potestade e força.', 24, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque convém que reine até que haja posto a todos os inimigos debaixo de seus pés.', 25, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o último inimigo que há de ser aniquilado é a morte.', 26, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todas as coisas sujeitou debaixo de seus pés. Mas, quando diz que todas as coisas lhe estão sujeitas, claro está que se excetua aquele que lhe sujeitou todas as coisas.', 27, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando todas as coisas lhe estiverem sujeitas, então também o mesmo Filho se sujeitará àquele que todas as coisas lhe sujeitou, para que Deus seja tudo em todos.', 28, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Doutra maneira, que farão os que se batizam pelos mortos, se absolutamente os mortos não ressuscitam? Por que se batizam eles então pelos mortos?', 29, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por que estamos nós também a toda a hora em perigo?', 30, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu protesto que cada dia morro, gloriando-me em vós, irmãos, por Cristo Jesus nosso Senhor.', 31, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, como homem, combati em Éfeso contra as bestas, que me aproveita isso, se os mortos não ressuscitam? Comamos e bebamos, que amanhã morreremos.', 32, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos enganeis: as más conversações corrompem os bons costumes.', 33, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vigiai justamente e não pequeis; porque alguns ainda não têm o conhecimento de Deus; digo-o para vergonha vossa.', 34, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas alguém dirá: Como ressuscitarão os mortos? E com que corpo virão?', 35, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Insensato! o que tu semeias não é vivificado, se primeiro não morrer.', 36, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando semeias, não semeias o corpo que há de nascer, mas o simples grão, como de trigo, ou de outra qualquer semente.', 37, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus dá-lhe o corpo como quer, e a cada semente o seu próprio corpo.', 38, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem toda a carne é uma mesma carne, mas uma é a carne dos homens, e outra a carne dos animais, e outra a dos peixes e outra a das aves.', 39, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E há corpos celestes e corpos terrestres, mas uma é a glória dos celestes e outra a dos terrestres.', 40, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Uma é a glória do sol, e outra a glória da lua, e outra a glória das estrelas; porque uma estrela difere em glória de outra estrela.', 41, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também a ressurreição dentre os mortos. Semeia-se o corpo em corrupção; ressuscitará em incorrupção.', 42, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Semeia-se em ignomínia, ressuscitará em glória. Semeia-se em fraqueza, ressuscitará com vigor.', 43, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Semeia-se corpo natural, ressuscitará corpo espiritual. Se há corpo natural, há também corpo espiritual.', 44, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim está também escrito: O primeiro homem, Adão, foi feito em alma vivente; o último Adão em espírito vivificante.', 45, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não é primeiro o espiritual, senão o natural; depois o espiritual.', 46, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O primeiro homem, da terra, é terreno; o segundo homem, o Senhor, é do céu.', 47, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qual o terreno, tais são também os terrestres; e, qual o celestial, tais também os celestiais.', 48, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, assim como trouxemos a imagem do terreno, assim traremos também a imagem do celestial.', 49, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E agora digo isto, irmãos: que a carne e o sangue não podem herdar o reino de Deus, nem a corrupção herdar a incorrupção.', 50, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis aqui vos digo um mistério: Na verdade, nem todos dormiremos, mas todos seremos transformados;', 51, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Num momento, num abrir e fechar de olhos, ante a última trombeta; porque a trombeta soará, e os mortos ressuscitarão incorruptíveis, e nós seremos transformados.', 52, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque convém que isto que é corruptível se revista da incorruptibilidade, e que isto que é mortal se revista da imortalidade.', 53, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando isto que é corruptível se revestir da incorruptibilidade, e isto que é mortal se revestir da imortalidade, então cumprir-se-á a palavra que está escrita: Tragada foi a morte na vitória.', 54, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde está, ó morte, o teu aguilhão? Onde está, ó inferno, a tua vitória?', 55, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o aguilhão da morte é o pecado, e a força do pecado é a lei.', 56, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas graças a Deus que nos dá a vitória por nosso Senhor Jesus Cristo.', 57, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, meus amados irmãos, sede firmes e constantes, sempre abundantes na obra do Senhor, sabendo que o vosso trabalho não é vão no Senhor.', 58, 15, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, apóstolo de Jesus Cristo, segundo o mandado de Deus, nosso Salvador, e do Senhor Jesus Cristo, esperança nossa,', 1, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A Timóteo meu verdadeiro filho na fé: Graça, misericórdia e paz da parte de Deus nosso Pai, e da de Cristo Jesus, nosso Senhor.', 2, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como te roguei, quando parti para a macedônia, que ficasses em Éfeso, para advertires a alguns, que não ensinem outra doutrina,', 3, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem se dêem a fábulas ou a genealogias intermináveis, que mais produzem questões do que edificação de Deus, que consiste na fé; assim o faço agora.', 4, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o fim do mandamento é o amor de um coração puro, e de uma boa consciência, e de uma fé não fingida.', 5, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Do que, desviando-se alguns, se entregaram a vãs contendas;', 6, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Querendo ser mestres da lei, e não entendendo nem o que dizem nem o que afirmam.', 7, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabemos, porém, que a lei é boa, se alguém dela usa legitimamente;', 8, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo isto, que a lei não é feita para o justo, mas para os injustos e obstinados, para os ímpios e pecadores, para os profanos e irreligiosos, para os parricidas e matricidas, para os homicidas,', 9, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para os devassos, para os sodomitas, para os roubadores de homens, para os mentirosos, para os perjuros, e para o que for contrário à sã doutrina,', 10, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conforme o evangelho da glória de Deus bem-aventurado, que me foi confiado.', 11, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dou graças ao que me tem confortado, a Cristo Jesus Senhor nosso, porque me teve por fiel, pondo-me no ministério;', 12, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A mim, que dantes fui blasfemo, e perseguidor, e injurioso; mas alcancei misericórdia, porque o fiz ignorantemente, na incredulidade.', 13, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a graça de nosso Senhor superabundou com a fé e amor que há em Jesus Cristo.', 14, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esta é uma palavra fiel, e digna de toda a aceitação, que Cristo Jesus veio ao mundo, para salvar os pecadores, dos quais eu sou o principal.', 15, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas por isso alcancei misericórdia, para que em mim, que sou o principal, Jesus Cristo mostrasse toda a sua longanimidade, para exemplo dos que haviam de crer nele para a vida eterna.', 16, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, ao Rei dos séculos, imortal, invisível, ao único Deus sábio, seja honra e glória para todo o sempre. Amém.', 17, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Este mandamento te dou, meu filho Timóteo, que, segundo as profecias que houve acerca de ti, milites por elas boa milícia;', 18, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conservando a fé, e a boa consciência, a qual alguns, rejeitando, fizeram naufrágio na fé.', 19, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E entre esses foram Himeneu e Alexandre, os quais entreguei a Satanás, para que aprendam a não blasfemar.', 20, 1, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, agora nenhuma condenação há para os que estão em Cristo Jesus, que não andam segundo a carne, mas segundo o Espírito.', 1, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a lei do Espírito de vida, em Cristo Jesus, me livrou da lei do pecado e da morte.', 2, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto o que era impossível à lei, visto como estava enferma pela carne, Deus, enviando o seu Filho em semelhança da carne do pecado, pelo pecado condenou o pecado na carne;', 3, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que a justiça da lei se cumprisse em nós, que não andamos segundo a carne, mas segundo o Espírito.', 4, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os que são segundo a carne inclinam-se para as coisas da carne; mas os que são segundo o Espírito para as coisas do Espírito.', 5, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a inclinação da carne é morte; mas a inclinação do Espírito é vida e paz.', 6, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto a inclinação da carne é inimizade contra Deus, pois não é sujeita à lei de Deus, nem, em verdade, o pode ser.', 7, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, os que estão na carne não podem agradar a Deus.', 8, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, porém, não estais na carne, mas no Espírito, se é que o Espírito de Deus habita em vós. Mas, se alguém não tem o Espírito de Cristo, esse tal não é dele.', 9, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se Cristo está em vós, o corpo, na verdade, está morto por causa do pecado, mas o espírito vive por causa da justiça.', 10, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se o Espírito daquele que dentre os mortos ressuscitou a Jesus habita em vós, aquele que dentre os mortos ressuscitou a Cristo também vivificará os vossos corpos mortais, pelo seu Espírito que em vós habita.', 11, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que, irmãos, somos devedores, não à carne para viver segundo a carne.', 12, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se viverdes segundo a carne, morrereis; mas, se pelo Espírito mortificardes as obras do corpo, vivereis.', 13, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos os que são guiados pelo Espírito de Deus esses são filhos de Deus.', 14, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não recebestes o espírito de escravidão, para outra vez estardes em temor, mas recebestes o Espírito de adoção de filhos, pelo qual clamamos: Aba, Pai.', 15, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O mesmo Espírito testifica com o nosso espírito que somos filhos de Deus.', 16, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se nós somos filhos, somos logo herdeiros também, herdeiros de Deus, e co-herdeiros de Cristo: se é certo que com ele padecemos, para que também com ele sejamos glorificados.', 17, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque para mim tenho por certo que as aflições deste tempo presente não são para comparar com a glória que em nós há de ser revelada.', 18, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a ardente expectação da criatura espera a manifestação dos filhos de Deus.', 19, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a criação ficou sujeita à vaidade, não por sua vontade, mas por causa do que a sujeitou,', 20, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na esperança de que também a mesma criatura será libertada da servidão da corrupção, para a liberdade da glória dos filhos de Deus.', 21, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque sabemos que toda a criação geme e está juntamente com dores de parto até agora.', 22, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não só ela, mas nós mesmos, que temos as primícias do Espírito, também gememos em nós mesmos, esperando a adoção, a saber, a redenção do nosso corpo.', 23, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque em esperança fomos salvos. Ora a esperança que se vê não é esperança; porque o que alguém vê como o esperará?', 24, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se esperamos o que não vemos, com paciência o esperamos.', 25, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E da mesma maneira também o Espírito ajuda as nossas fraquezas; porque não sabemos o que havemos de pedir como convém, mas o mesmo Espírito intercede por nós com gemidos inexprimíveis.', 26, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele que examina os corações sabe qual é a intenção do Espírito; e é ele que segundo Deus intercede pelos santos.', 27, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sabemos que todas as coisas contribuem juntamente para o bem daqueles que amam a Deus, daqueles que são chamados segundo o seu propósito.', 28, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os que dantes conheceu também os predestinou para serem conformes à imagem de seu Filho, a fim de que ele seja o primogênito entre muitos irmãos.', 29, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aos que predestinou a estes também chamou; e aos que chamou a estes também justificou; e aos que justificou a estes também glorificou.', 30, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que diremos, pois, a estas coisas? Se Deus é por nós, quem será contra nós?', 31, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que nem mesmo a seu próprio Filho poupou, antes o entregou por todos nós, como nos não dará também com ele todas as coisas?', 32, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem intentará acusação contra os escolhidos de Deus? É Deus quem os justifica.', 33, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem é que condena? Pois é Cristo quem morreu, ou antes quem ressuscitou dentre os mortos, o qual está à direita de Deus, e também intercede por nós.', 34, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem nos separará do amor de Cristo? A tribulação, ou a angústia, ou a perseguição, ou a fome, ou a nudez, ou o perigo, ou a espada?', 35, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como está escrito:Por amor de ti somos entregues à morte todo o dia;Somos reputados como ovelhas para o matadouro.', 36, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas em todas estas coisas somos mais do que vencedores, por aquele que nos amou.', 37, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque estou certo de que, nem a morte, nem a vida, nem os anjos, nem os principados, nem as potestades, nem o presente, nem o porvir,', 38, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem a altura, nem a profundidade, nem alguma outra criatura nos poderá separar do amor de Deus, que está em Cristo Jesus nosso Senhor.', 39, 8, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, no tocante às coisas sacrificadas aos ídolos, sabemos que todos temos ciência. A ciência incha, mas o amor edifica.', 1, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se alguém cuida saber alguma coisa, ainda não sabe como convém saber.', 2, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se alguém ama a Deus, esse é conhecido dele.', 3, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim que, quanto ao comer das coisas sacrificadas aos ídolos, sabemos que o ídolo nada é no mundo, e que não há outro Deus, senão um só.', 4, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, ainda que haja também alguns que se chamem deuses, quer no céu quer na terra (como há muitos deuses e muitos senhores),', 5, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia para nós há um só Deus, o Pai, de quem é tudo e para quem nós vivemos; e um só Senhor, Jesus Cristo, pelo qual são todas as coisas, e nós por ele.', 6, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nem em todos há conhecimento; porque alguns até agora comem, com consciência do ídolo, coisas sacrificadas ao ídolo; e a sua consciência, sendo fraca, fica contaminada.', 7, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora a comida não nos faz agradáveis a Deus, porque, se comemos, nada temos de mais e, se não comemos, nada nos falta.', 8, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vede que essa liberdade não seja de alguma maneira escândalo para os fracos.', 9, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se alguém te vir a ti, que tens ciência, sentado à mesa no templo dos ídolos, não será a consciência do que é fraco induzida a comer das coisas sacrificadas aos ídolos?', 10, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pela tua ciência perecerá o irmão fraco, pelo qual Cristo morreu.', 11, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, pecando assim contra os irmãos, e ferindo a sua fraca consciência, pecais contra Cristo.', 12, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso, se a comida escandalizar a meu irmão, nunca mais comerei carne, para que meu irmão não se escandalize.', 13, 8, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, irmãos, não quero que ignoreis que nossos pais estiveram todos debaixo da nuvem, e todos passaram pelo mar.', 1, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos foram batizados em Moisés, na nuvem e no mar,', 2, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos comeram de uma mesma comida espiritual,', 3, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E beberam todos de uma mesma bebida espiritual, porque bebiam da pedra espiritual que os seguia; e a pedra era Cristo.', 4, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus não se agradou da maior parte deles, por isso foram prostrados no deserto.', 5, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estas coisas foram-nos feitas em figura, para que não cobicemos as coisas más, como eles cobiçaram.', 6, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não vos façais, pois, idólatras, como alguns deles, conforme está escrito: O povo assentou-se a comer e a beber, e levantou-se para folgar.', 7, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não nos forniquemos, como alguns deles fizeram; e caíram num dia vinte e três mil.', 8, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não tentemos a Cristo, como alguns deles também tentaram, e pereceram pelas serpentes.', 9, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não murmureis, como também alguns deles murmuraram, e pereceram pelo destruidor.', 10, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, tudo isto lhes sobreveio como figuras, e estão escritas para aviso nosso, para quem já são chegados os fins dos séculos.', 11, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele, pois, que cuida estar em pé, olhe não caia.', 12, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não veio sobre vós tentação, senão humana; mas fiel é Deus, que não vos deixará tentar acima do que podeis, antes com a tentação dará também o escape, para que a possais suportar.', 13, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, meus amados, fugi da idolatria.', 14, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Falo como a entendidos; julgai vós mesmos o que digo.', 15, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura o cálice de bênção, que abençoamos, não é a comunhão do sangue de Cristo? O pão que partimos não é porventura a comunhão do corpo de Cristo?', 16, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nós, sendo muitos, somos um só pão e um só corpo, porque todos participamos do mesmo pão.', 17, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede a Israel segundo a carne; os que comem os sacrifícios não são porventura participantes do altar?', 18, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas que digo? Que o ídolo é alguma coisa? Ou que o sacrificado ao ídolo é alguma coisa?', 19, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes digo que as coisas que os gentios sacrificam, as sacrificam aos demônios, e não a Deus. E não quero que sejais participantes com os demônios.', 20, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não podeis beber o cálice do Senhor e o cálice dos demônios; não podeis ser participantes da mesa do Senhor e da mesa dos demônios.', 21, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou irritaremos o Senhor? Somos nós mais fortes do que ele?', 22, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todas as coisas me são lícitas, mas nem todas as coisas convêm; todas as coisas me são lícitas, mas nem todas as coisas edificam.', 23, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém busque o proveito próprio; antes cada um o que é de outrem.', 24, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Comei de tudo quanto se vende no açougue, sem perguntar nada, por causa da consciência.', 25, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a terra é do Senhor e toda a sua plenitude.', 26, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se algum dos infiéis vos convidar, e quiserdes ir, comei de tudo o que se puser diante de vós, sem nada perguntar, por causa da consciência.', 27, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se alguém vos disser: Isto foi sacrificado aos ídolos, não comais, por causa daquele que vos advertiu e por causa da consciência; porque a terra é do Senhor, e toda a sua plenitude.', 28, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo, porém, a consciência, não a tua, mas a do outro. Pois por que há de a minha liberdade ser julgada pela consciência de outrem?', 29, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se eu com graça participo, por que sou blasfemado naquilo por que dou graças?', 30, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, quer comais quer bebais, ou façais outra qualquer coisa, fazei tudo para glória de Deus.', 31, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portai-vos de modo que não deis escândalo nem aos judeus, nem aos gregos, nem à igreja de Deus.', 32, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como também eu em tudo agrado a todos, não buscando o meu próprio proveito, mas o de muitos, para que assim se possam salvar.', 33, 10, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, se algum homem chegar a ser surpreendido nalguma ofensa, vós, que sois espirituais, encaminhai o tal com espírito de mansidão; olhando por ti mesmo, para que não sejas também tentado.', 1, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Levai as cargas uns dos outros, e assim cumprireis a lei de Cristo.', 2, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se alguém cuida ser alguma coisa, não sendo nada, engana-se a si mesmo.', 3, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas prove cada um a sua própria obra, e terá glória só em si mesmo, e não noutro.', 4, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque cada qual levará a sua própria carga.', 5, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que é instruído na palavra reparta de todos os seus bens com aquele que o instrui.', 6, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não erreis: Deus não se deixa escarnecer; porque tudo o que o homem semear, isso também ceifará.', 7, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o que semeia na sua carne, da carne ceifará a corrupção; mas o que semeia no Espírito, do Espírito ceifará a vida eterna.', 8, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não nos cansemos de fazer bem, porque a seu tempo ceifaremos, se não houvermos desfalecido.', 9, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, enquanto temos tempo, façamos bem a todos, mas principalmente aos domésticos da fé.', 10, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede com que grandes letras vos escrevi por minha mão.', 11, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos os que querem mostrar boa aparência na carne, esses vos obrigam a circuncidar-vos, somente para não serem perseguidos por causa da cruz de Cristo.', 12, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nem ainda esses mesmos que se circuncidam guardam a lei, mas querem que vos circuncideis, para se gloriarem na vossa carne.', 13, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas longe esteja de mim gloriar-me, a não ser na cruz de nosso Senhor Jesus Cristo, pela qual o mundo está crucificado para mim e eu para o mundo.', 14, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque em Cristo Jesus nem a circuncisão, nem a incircuncisão tem virtude alguma, mas sim o ser uma nova criatura.', 15, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a todos quantos andarem conforme esta regra, paz e misericórdia sobre eles e sobre o Israel de Deus.', 16, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Desde agora ninguém me inquiete; porque trago no meu corpo as marcas do Senhor Jesus.', 17, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A graça de nosso Senhor Jesus Cristo seja, irmãos, com o vosso espírito! Amém.', 18, 6, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegaram ao outro lado do mar, à província dos gadarenos.', 1, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo ele do barco, lhe saiu logo ao seu encontro, dos sepulcros, um homem com espírito imundo;', 2, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual tinha a sua morada nos sepulcros, e nem ainda com cadeias o podia alguém prender;', 3, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, tendo sido muitas vezes preso com grilhões e cadeias, as cadeias foram por ele feitas em pedaços, e os grilhões em migalhas, e ninguém o podia amansar.', 4, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E andava sempre, de dia e de noite, clamando pelos montes, e pelos sepulcros, e ferindo-se com pedras.', 5, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando viu Jesus ao longe, correu e adorou-o.', 6, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, clamando com grande voz, disse: Que tenho eu contigo, Jesus, Filho do Deus Altíssimo? conjuro-te por Deus que não me atormentes.', 7, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Porque lhe dizia: Sai deste homem, espírito imundo.)', 8, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntou-lhe: Qual é o teu nome? E lhe respondeu, dizendo: Legião é o meu nome, porque somos muitos.', 9, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rogava-lhe muito que os não enviasse para fora daquela província.', 10, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E andava ali pastando no monte uma grande manada de porcos.', 11, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos aqueles demônios lhe rogaram, dizendo: Manda-nos para aqueles porcos, para que entremos neles.', 12, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus logo lho permitiu.  E, saindo aqueles espíritos imundos, entraram nos porcos; e a manada se precipitou por um despenhadeiro no mar (eram quase dois mil), e afogaram-se no mar.', 13, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que apascentavam os porcos fugiram, e o anunciaram na cidade e nos campos; e saíram muitos a ver o que era aquilo que tinha acontecido.', 14, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram ter com Jesus, e viram o endemoninhado, o que tivera a legião, assentado, vestido e em perfeito juízo, e temeram.', 15, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que aquilo tinham visto contaram-lhes o que acontecera ao endemoninhado, e acerca dos porcos.', 16, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E começaram a rogar-lhe que saísse dos seus termos.', 17, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando ele no barco, rogava-lhe o que fora endemoninhado que o deixasse estar com ele.', 18, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, não lho permitiu, mas disse-lhe: Vai para tua casa, para os teus, e anuncia-lhes quão grandes coisas o Senhor te fez, e como teve misericórdia de ti.', 19, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele foi, e começou a anunciar em Decápolis quão grandes coisas Jesus lhe fizera; e todos se maravilharam.', 20, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passando Jesus outra vez num barco para o outro lado, ajuntou-se a ele uma grande multidão; e ele estava junto do mar.', 21, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que chegou um dos principais da sinagoga, por nome Jairo, e, vendo-o, prostrou-se aos seus pés,', 22, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rogava-lhe muito, dizendo: Minha filha está à morte; rogo-te que venhas e lhe imponhas as mãos, para que sare, e viva.', 23, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi com ele, e seguia-o uma grande multidão, que o apertava.', 24, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E certa mulher que, havia doze anos, tinha um fluxo de sangue,', 25, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que havia padecido muito com muitos médicos, e despendido tudo quanto tinha, nada lhe aproveitando isso, antes indo a pior;', 26, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ouvindo falar de Jesus, veio por detrás, entre a multidão, e tocou na sua veste.', 27, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque dizia: Se tão-somente tocar nas suas vestes, sararei.', 28, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo se lhe secou a fonte do seu sangue; e sentiu no seu corpo estar já curada daquele mal.', 29, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo Jesus, conhecendo que a virtude de si mesmo saíra, voltou-se para a multidão, e disse: Quem tocou nas minhas vestes?', 30, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disseram-lhe os seus discípulos: Vês que a multidão te aperta, e dizes: Quem me tocou?', 31, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele olhava em redor, para ver a que isto fizera.', 32, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então a mulher, que sabia o que lhe tinha acontecido, temendo e tremendo, aproximou-se, e prostrou-se diante dele, e disse-lhe toda a verdade.', 33, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhe disse: Filha, a tua fé te salvou; vai em paz, e sê curada deste teu mal.', 34, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estando ele ainda falando, chegaram alguns do principal da sinagoga, a quem disseram: A tuafilha está morta; para que enfadas mais o Mestre?', 35, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, tendo ouvido estas palavras, disse ao principal da sinagoga: Não temas, crê somente.', 36, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não permitiu que alguém o seguisse, a não ser Pedro, Tiago, e João, irmão de Tiago.', 37, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo chegado à casa do principal da sinagoga, viu o alvoroço, e os que choravam muito e pranteavam.', 38, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando, disse-lhes: Por que vos alvoroçais e chorais? A menina não está morta, mas dorme.', 39, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E riam-se dele; porém ele, tendo-os feito sair, tomou consigo o pai e a mãe da menina, e os que com ele estavam, e entrou onde a menina estava deitada.', 40, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando a mão da menina, disse-lhe: Talita cumi; que, traduzido, é: Menina, a ti te digo, levanta-te.', 41, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo a menina se levantou, e andava, pois já tinha doze anos; e assombraram-se com grande espanto.', 42, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mandou-lhes expressamente que ninguém o soubesse; e disse que lhe dessem de comer.', 43, 5, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Admoesto-te, pois, antes de tudo, que se façam deprecações, orações, intercessões, e ações de graças, por todos os homens;', 1, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelos reis, e por todos os que estão em eminência, para que tenhamos uma vida quieta e sossegada, em toda a piedade e honestidade;', 2, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque isto é bom e agradável diante de Deus nosso Salvador,', 3, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que quer que todos os homens se salvem, e venham ao conhecimento da verdade.', 4, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque há um só Deus, e um só Mediador entre Deus e os homens, Jesus Cristo homem.', 5, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual se deu a si mesmo em preço de redenção por todos, para servir de testemunho a seu tempo.', 6, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para o que (digo a verdade em Cristo, não minto) fui constituído pregador, e apóstolo, e doutor dos gentios na fé e na verdade.', 7, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quero, pois, que os homens orem em todo o lugar, levantando mãos santas, sem ira nem contenda.', 8, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que do mesmo modo as mulheres se ataviem em traje honesto, com pudor e modéstia, não com tranças, ou com ouro, ou pérolas, ou vestidos preciosos,', 9, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas (como convém a mulheres que fazem profissão de servir a Deus) com boas obras.', 10, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A mulher aprenda em silêncio, com toda a sujeição.', 11, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não permito, porém, que a mulher ensine, nem use de autoridade sobre o marido, mas que esteja em silêncio.', 12, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque primeiro foi formado Adão, depois Eva.', 13, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Adão não foi enganado, mas a mulher, sendo enganada, caiu em transgressão.', 14, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Salvar-se-á, porém, dando à luz filhos, se permanecer com modéstia na fé, no amor e na santificação.', 15, 2, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Princípio do Evangelho de Jesus Cristo, Filho de Deus;', 1, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como está escrito nos profetas: Eis que eu envio o meu anjo ante a tua face, o qual preparará o teu caminho diante de ti.', 2, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Voz do que clama no deserto: Preparai o caminho do Senhor, Endireitai as suas veredas.', 3, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Apareceu João batizando no deserto, e pregando o batismo de arrependimento, para remissão dos pecados.', 4, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E toda a província da Judéia e os de Jerusalém iam ter com ele; e todos eram batizados por ele no rio Jordão, confessando os seus pecados.', 5, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E João andava vestido de pêlos de camelo, e com um cinto de couro em redor de seus lombos, e comia gafanhotos e mel silvestre.', 6, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pregava, dizendo: Após mim vem aquele que é mais forte do que eu, do qual não sou digno de, abaixando-me, desatar a correia das suas alparcas.', 7, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, em verdade, tenho-vos batizado com água; ele, porém, vos batizará com o Espírito Santo.', 8, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu naqueles dias que Jesus, tendo ido de Nazaré da Galiléia, foi batizado por João, no Jordão.', 9, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo que saiu da água, viu os céus abertos, e o Espírito, que como pomba descia sobre ele.', 10, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouviu-se uma voz dos céus, que dizia: Tu és o meu Filho amado em quem me comprazo.', 11, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo o Espírito o impeliu para o deserto.', 12, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ali esteve no deserto quarenta dias, tentado por Satanás.  E vivia entre as feras, e os anjos o serviam.', 13, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, depois que João foi entregue à prisão, veio Jesus para a Galiléia, pregando o evangelho do reino de Deus,', 14, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizendo: O tempo está cumprido, e o reino de Deus está próximo.  Arrependei-vos, e crede no evangelho.', 15, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, andando junto do mar da Galiléia, viu Simão, e André, seu irmão, que lançavam a rede ao mar, pois eram pescadores.', 16, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhes disse: Vinde após mim, e eu farei que sejais pescadores de homens.', 17, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, deixando logo as suas redes, o seguiram.', 18, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passando dali um pouco mais adiante, viu Tiago, filho de Zebedeu, e João, seu irmão, que estavam no barco consertando as redes,', 19, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo os chamou.  E eles, deixando o seu pai Zebedeu no barco com os jornaleiros, foram após ele.', 20, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entraram em Cafarnaum e, logo no sábado, indo ele à sinagoga, ali ensinava.', 21, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E maravilharam-se da sua doutrina, porque os ensinava como tendo autoridade, e não como os escribas.', 22, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava na sinagoga deles um homem com um espírito imundo, o qual exclamou,', 23, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Ah! que temos contigo, Jesus Nazareno? Vieste destruir-nos? Bem sei quem és: o Santo de Deus.', 24, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E repreendeu-o Jesus, dizendo: Cala-te, e sai dele.', 25, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então o espírito imundo, convulsionando-o, e clamando com grande voz, saiu dele.', 26, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos se admiraram, a ponto de perguntarem entre si, dizendo: Que é isto? Que nova doutrina é esta? Pois com autoridade ordena aos espíritos imundos, e eles lhe obedecem!', 27, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo correu a sua fama por toda a província da Galiléia.', 28, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo, saindo da sinagoga, foram à casa de Simão e de André com Tiago e João.', 29, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a sogra de Simão estava deitada com febre; e logo lhe falaram dela.', 30, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, chegando-se a ela, tomou-a pela mão, e levantou-a; e imediatamente a febre a deixou, e servia-os.', 31, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo chegado a tarde, quando já se estava pondo o sol, trouxeram-lhe todos os que se achavam enfermos, e os endemoninhados.', 32, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E toda a cidade se ajuntou à porta.', 33, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E curou muitos que se achavam enfermos de diversas enfermidades, e expulsou muitos demônios, porém não deixava falar os demônios, porque o conheciam.', 34, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se de manhã, muito cedo, fazendo ainda escuro, saiu, e foi para um lugar deserto, e ali orava.', 35, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E seguiram-no Simão e os que com ele estavam.', 36, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, achando-o, lhe disseram: Todos te buscam.', 37, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Vamos às aldeias vizinhas, para que eu ali também pregue; porque para isso vim.', 38, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E pregava nas sinagogas deles, por toda a Galiléia, e expulsava os demônios.', 39, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aproximou-se dele um leproso que, rogando-lhe, e pondo-se de joelhos diante dele, lhe dizia: Se queres, bem podes limpar-me.', 40, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, movido de grande compaixão, estendeu a mão, e tocou-o, e disse-lhe: Quero, sê limpo.', 41, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo ele dito isto, logo a lepra desapareceu, e ficou limpo.', 42, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, advertindo-o severamente, logo o despediu.', 43, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe: Olha, não digas nada a ninguém; porém vai, mostra-te ao sacerdote, e oferece pela tua purificação o que Moisés determinou, para lhes servir de testemunho.', 44, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, tendo ele saído, começou a apregoar muitas coisas, e a divulgar o que acontecera; de sorte que Jesus já não podia entrar publicamente na cidade, mas conservava-se fora em lugares desertos; e de todas as partes iam ter com ele.', 45, 1, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também houve entre o povo falsos profetas, como entre vós haverá também falsos doutores, que introduzirão encobertamente heresias de perdição, e negarão o Senhor que os resgatou, trazendo sobre si mesmos repentina perdição.', 1, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos seguirão as suas dissoluções, pelos quais será blasfemado o caminho da verdade.', 2, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por avareza farão de vós negócio com palavras fingidas; sobre os quais já de largo tempo não será tardia a sentença, e a sua perdição não dormita.', 3, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se Deus não perdoou aos anjos que pecaram, mas, havendo-os lançado no inferno, os entregou às cadeias da escuridão, ficando reservados para o juízo;', 4, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não perdoou ao mundo antigo, mas guardou a Noé, a oitava pessoa, o pregoeiro da justiça, ao trazer o dilúvio sobre o mundo dos ímpios;', 5, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E condenou à destruição as cidades de Sodoma e Gomorra, reduzindo-as a cinza, e pondo-as para exemplo aos que vivessem impiamente;', 6, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E livrou o justo Ló, enfadado da vida dissoluta dos homens abomináveis', 7, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Porque este justo, habitando entre eles, afligia todos os dias a sua alma justa, por isso via e ouvia sobre as suas obras injustas);', 8, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, sabe o Senhor livrar da tentação os piedosos, e reservar os injustos para o dia do juízo, para serem castigados;', 9, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas principalmente aqueles que segundo a carne andam em concupiscências de imundícia, e desprezam as autoridades; atrevidos, obstinados, não receando blasfemar das dignidades;', 10, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Enquanto os anjos, sendo maiores em força e poder, não pronunciam contra eles juízo blasfemo diante do Senhor.', 11, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas estes, como animais irracionais, que seguem a natureza, feitos para serem presos e mortos, blasfemando do que não entendem, perecerão na sua corrupção,', 12, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Recebendo o galardão da injustiça; pois que tais homens têm prazer nos deleites quotidianos; nódoas são eles e máculas, deleitando-se em seus enganos, quando se banqueteiam convosco;', 13, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo os olhos cheios de adultério, e não cessando de pecar, engodando as almas inconstantes, tendo o coração exercitado na avareza, filhos de maldição;', 14, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os quais, deixando o caminho direito, erraram seguindo o caminho de Balaão, filho de Beor, que amou o prêmio da injustiça;', 15, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas teve a repreensão da sua transgressão; o mudo jumento, falando com voz humana, impediu a loucura do profeta.', 16, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes são fontes sem água, nuvens levadas pela força do vento, para os quais a escuridão das trevas eternamente se reserva.', 17, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, falando coisas mui arrogantes de vaidades, engodam com as concupiscências da carne, e com dissoluções, aqueles que se estavam afastando dos que andam em erro,', 18, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Prometendo-lhes liberdade, sendo eles mesmos servos da corrupção. Porque de quem alguém é vencido, do tal faz-se também servo.', 19, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto se, depois de terem escapado das corrupções do mundo, pelo conhecimento do Senhor e Salvador Jesus Cristo, forem outra vez envolvidos nelas e vencidos, tornou-se-lhes o último estado pior do que o primeiro.', 20, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque melhor lhes fora não conhecerem o caminho da justiça, do que, conhecendo-o, desviarem-se do santo mandamento que lhes fora dado;', 21, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Deste modo sobreveio-lhes o que por um verdadeiro provérbio se diz: O cão voltou ao seu próprio vômito, e a porca lavada ao espojadouro de lama.', 22, 2, 22);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu, irmãos, quando fui ter convosco, anunciando-vos o testemunho de Deus, não fui com sublimidade de palavras ou de sabedoria.', 1, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nada me propus saber entre vós, senão a Jesus Cristo, e este crucificado.', 2, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu estive convosco em fraqueza, e em temor, e em grande tremor.', 3, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a minha palavra, e a minha pregação, não consistiram em palavras persuasivas de sabedoria humana, mas em demonstração de Espírito e de poder;', 4, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que a vossa fé não se apoiasse em sabedoria dos homens, mas no poder de Deus.', 5, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia falamos sabedoria entre os perfeitos; não, porém, a sabedoria deste mundo, nem dos príncipes deste mundo, que se aniquilam;', 6, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas falamos a sabedoria de Deus, oculta em mistério, a qual Deus ordenou antes dos séculos para nossa glória;', 7, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A qual nenhum dos príncipes deste mundo conheceu; porque, se a conhecessem, nunca crucificariam ao Senhor da glória.', 8, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, como está escrito:As coisas que o olho não viu, e o ouvido não ouviu,e não subiram ao coração do homem,são as que Deus preparou para os que o amam.', 9, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus no-las revelou pelo seu Espírito; porque o Espírito penetra todas as coisas, ainda as profundezas de Deus.', 10, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, qual dos homens sabe as coisas do homem, senão o espírito do homem, que nele está? Assim também ninguém sabe as coisas de Deus, senão o Espírito de Deus.', 11, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nós não recebemos o espírito do mundo, mas o Espírito que provém de Deus, para que pudéssemos conhecer o que nos é dado gratuitamente por Deus.', 12, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As quais também falamos, não com palavras que a sabedoria humana ensina, mas com as que o Espírito Santo ensina, comparando as coisas espirituais com as espirituais.', 13, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o homem natural não compreende as coisas do Espírito de Deus, porque lhe parecem loucura; e não pode entendê-las, porque elas se discernem espiritualmente.', 14, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que é espiritual discerne bem tudo, e ele de ninguém é discernido.', 15, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, quem conheceu a mente do Senhor, para que possa instruí-lo? Mas nós temos a mente de Cristo.', 16, 2, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ó insensatos gálatas! quem vos fascinou para não obedecerdes à verdade, a vós, perante os olhos de quem Jesus Cristo foi evidenciado, crucificado, entre vós?', 1, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Só quisera saber isto de vós: recebestes o Espírito pelas obras da lei ou pela pregação da fé?', 2, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sois vós tão insensatos que, tendo começado pelo Espírito, acabeis agora pela carne?', 3, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Será em vão que tenhais padecido tanto? Se é que isso também foi em vão.', 4, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele, pois, que vos dá o Espírito, e que opera maravilhas entre vós, o faz pelas obras da lei, ou pela pregação da fé?', 5, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim como Abraão creu em Deus, e isso lhe foi imputado como justiça.', 6, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabei, pois, que os que são da fé são filhos de Abraão.', 7, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, tendo a Escritura previsto que Deus havia de justificar pela fé os gentios, anunciou primeiro o evangelho a Abraão, dizendo: Todas as nações serão benditas em ti.', 8, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De sorte que os que são da fé são benditos com o crente Abraão.', 9, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos aqueles, pois, que são das obras da lei estão debaixo da maldição; porque está escrito: Maldito todo aquele que não permanecer em todas as coisas que estão escritas no livro da lei, para fazê-las.', 10, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E é evidente que pela lei ninguém será justificado diante de Deus, porque o justo viverá pela fé.', 11, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, a lei não é da fé; mas o homem, que fizer estas coisas, por elas viverá.', 12, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Cristo nos resgatou da maldição da lei, fazendo-se maldição por nós; porque está escrito: Maldito todo aquele que for pendurado no madeiro;', 13, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que a bênção de Abraão chegasse aos gentios por Jesus Cristo, e para que pela fé nós recebamos a promessa do Espírito.', 14, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, como homem falo; se a aliança de um homem for confirmada, ninguém a anula nem a acrescenta.', 15, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, as promessas foram feitas a Abraão e à sua descendência. Não diz: E às descendências, como falando de muitas, mas como de uma só: E à tua descendência, que é Cristo.', 16, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas digo isto: Que tendo sido a aliança anteriormente confirmada por Deus em Cristo, a lei, que veio quatrocentos e trinta anos depois, não a invalida, de forma a abolir a promessa.', 17, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se a herança provém da lei, já não provém da promessa; mas Deus pela promessa a deu gratuitamente a Abraão.', 18, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Logo, para que é a lei? Foi ordenada por causa das transgressões, até que viesse a posteridade a quem a promessa tinha sido feita; e foi posta pelos anjos na mão de um medianeiro.', 19, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o medianeiro não o é de um só, mas Deus é um.', 20, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Logo, a lei é contra as promessas de Deus? De nenhuma sorte; porque, se fosse dada uma lei que pudesse vivificar, a justiça, na verdade, teria sido pela lei.', 21, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a Escritura encerrou tudo debaixo do pecado, para que a promessa pela fé em Jesus Cristo fosse dada aos crentes.', 22, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, antes que a fé viesse, estávamos guardados debaixo da lei, e encerrados para aquela fé que se havia de manifestar.', 23, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que a lei nos serviu de aio, para nos conduzir a Cristo, para que pela fé fôssemos justificados.', 24, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, depois que veio a fé, já não estamos debaixo de aio.', 25, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos sois filhos de Deus pela fé em Cristo Jesus.', 26, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos quantos fostes batizados em Cristo já vos revestistes de Cristo.', 27, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nisto não há judeu nem grego; não há servo nem livre; não há macho nem fêmea; porque todos vós sois um em Cristo Jesus.', 28, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se sois de Cristo, então sois descendência de Abraão, e herdeiros conforme a promessa.', 29, 3, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Naqueles dias, havendo uma grande multidão, e não tendo o que comer, Jesus chamou a si os seus discípulos, e disse-lhes:', 1, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tenho compaixão da multidão, porque há já três dias que estão comigo, e não têm o que comer.', 2, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se os deixar ir em jejum, para suas casas, desfalecerão no caminho, porque alguns deles vieram de longe.', 3, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus discípulos responderam-lhe: De onde poderá alguém satisfazê-los de pão aqui no deserto?', 4, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntou-lhes: Quantos pães tendes? E disseram-lhe: Sete.', 5, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ordenou à multidão que se assentasse no chão.  E, tomando os sete pães, e tendo dado graças, partiu-os, e deu-os aos seus discípulos, para que os pusessem diante deles, e puseram-nos diante da multidão.', 6, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tinham também alguns peixinhos; e, tendo dado graças, ordenou que também lhos pusessem diante.', 7, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E comeram, e saciaram-se; e dos pedaços que sobejaram levantaram sete cestos.', 8, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que comeram eram quase quatro mil; e despediu-os.', 9, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando logo no barco, com os seus discípulos, foi para as partes de Dalmanuta.', 10, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E saíram os fariseus, e começaram a disputar com ele, pedindolhe, para o tentarem, um sinal do céu.', 11, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, suspirando profundamente em seu espírito, disse: Por que pede esta geração um sinal? Em verdade vos digo que a esta geração não se dará sinal algum.', 12, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, deixando-os, tornou a entrar no barco, e foi para o outro lado.', 13, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles se esqueceram de levar pão e, no barco, não tinham consigo senão um pão.', 14, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ordenou-lhes, dizendo: Olhai, guardai-vos do fermento dos fariseus e do fermento de Herodes.', 15, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E arrazoavam entre si, dizendo: É porque não temos pão.', 16, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, conhecendo isto, disse-lhes: Para que arrazoais, que não tendes pão? não considerastes, nem compreendestes ainda? tendes ainda o vosso coração endurecido?', 17, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo olhos, não vedes? e tendo ouvidos, não ouvis? e não vos lembrais,', 18, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando parti os cinco pães entre os cinco mil, quantas alcofas cheias de pedaços levantastes? Disseram-lhe: Doze.', 19, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando parti os sete entre os quatro mil, quantos cestos cheios de pedaços levantastes? E disseram-lhe: Sete.', 20, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Como não entendeis ainda?', 21, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegou a Betsaida; e trouxeram-lhe um cego, e rogaram-lhe que o tocasse.', 22, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando o cego pela mão, levou-o para fora da aldeia; e, cuspindo-lhe nos olhos, e impondo-lhe as mãos, perguntou-lhe se via alguma coisa.', 23, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando ele os olhos, disse: Vejo os homens; pois os vejo como árvores que andam.', 24, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois disto, tornou a pôr-lhe as mãos sobre os olhos, e o fez olhar para cima: e ele ficou restaurado, e viu a todos claramente.', 25, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E mandou-o para sua casa, dizendo: Nem entres na aldeia, nem o digas a ninguém na aldeia.', 26, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E saiu Jesus, e os seus discípulos, para as aldeias de Cesaréia de Filipe; e no caminho perguntou aos seus discípulos, dizendo: Quem dizem os homens que eu sou?', 27, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles responderam: João o Batista; e outros: Elias; mas outros: Um dos profetas.', 28, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Mas vós, quem dizeis que eu sou? E, respondendo Pedro, lhe disse: Tu és o Cristo.', 29, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E admoestou-os, para que a ninguém dissessem aquilo dele.', 30, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E começou a ensinar-lhes que importava que o Filho do homem padecesse muito, e que fosse rejeitado pelos anciãos e príncipes dos sacerdotes, e pelos escribas, e que fosse morto, mas que depois de três dias ressuscitaria.', 31, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia abertamente estas palavras.  E Pedro o tomou à parte, e começou a repreendê-lo.', 32, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, virando-se, e olhando para os seus discípulos, repreendeu a Pedro, dizendo: Retira-te de diante de mim, Satanás; porque não compreendes as coisas que são de Deus, mas as que são dos homens.', 33, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chamando a si a multidão, com os seus discípulos, disse-lhes: Se alguém quiser vir após mim, negue-se a si mesmo, e tome a sua cruz, e siga-me.', 34, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque qualquer que quiser salvar a sua vida, perdê-la-á, mas, qualquer que perder a sua vida por amor de mim e do evangelho, esse a salvará.', 35, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, que aproveitaria ao homem ganhar todo o mundo e perder a sua alma?', 36, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ou, que daria o homem pelo resgate da sua alma?', 37, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto, qualquer que, entre esta geração adúltera e pecadora, se envergonhar de mim e das minhas palavras, também o Filho do homem se envergonhará dele, quando vier na glória de seu Pai, com os santos anjos.', 38, 8, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, apóstolo (não da parte dos homens, nem por homem algum, mas por Jesus Cristo, e por Deus Pai, que o ressuscitou dentre os mortos),', 1, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos os irmãos que estão comigo, às igrejas da Galácia:', 2, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graça e paz da parte de Deus Pai e do nosso Senhor Jesus Cristo,', 3, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual se deu a si mesmo por nossos pecados, para nos livrar do presente século mau, segundo a vontade de Deus nosso Pai,', 4, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ao qual seja dada glória para todo o sempre. Amém.', 5, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Maravilho-me de que tão depressa passásseis daquele que vos chamou à graça de Cristo para outro evangelho;', 6, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual não é outro, mas há alguns que vos inquietam e querem transtornar o evangelho de Cristo.', 7, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, ainda que nós mesmos ou um anjo do céu vos anuncie outro evangelho além do que já vos tenho anunciado, seja anátema.', 8, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, como já vo-lo dissemos, agora de novo também vo-lo digo. Se alguém vos anunciar outro evangelho além do que já recebestes, seja anátema.', 9, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, persuado eu agora a homens ou a Deus? ou procuro agradar a homens? Se estivesse ainda agradando aos homens, não seria servo de Cristo.', 10, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas faço-vos saber, irmãos, que o evangelho que por mim foi anunciado não é segundo os homens.', 11, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não o recebi, nem aprendi de homem algum, mas pela revelação de Jesus Cristo.', 12, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já ouvistes qual foi antigamente a minha conduta no judaísmo, como sobremaneira perseguia a igreja de Deus e a assolava.', 13, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E na minha nação excedia em judaísmo a muitos da minha idade, sendo extremamente zeloso das tradições de meus pais.', 14, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando aprouve a Deus, que desde o ventre de minha mãe me separou, e me chamou pela sua graça,', 15, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Revelar seu Filho em mim, para que o pregasse entre os gentios, não consultei a carne nem o sangue,', 16, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nem tornei a Jerusalém, a ter com os que já antes de mim eram apóstolos, mas parti para a Arábia, e voltei outra vez a Damasco.', 17, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois, passados três anos, fui a Jerusalém para ver a Pedro, e fiquei com ele quinze dias.', 18, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não vi a nenhum outro dos apóstolos, senão a Tiago, irmão do Senhor.', 19, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, acerca do que vos escrevo, eis que diante de Deus testifico que não minto.', 20, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois fui para as partes da Síria e da Cilícia.', 21, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não era conhecido de vista das igrejas da Judéia, que estavam em Cristo;', 22, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas somente tinham ouvido dizer: Aquele que já nos perseguiu anuncia agora a fé que antes destruía.', 23, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E glorificavam a Deus a respeito de mim.', 24, 1, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que os homens nos considerem como ministros de Cristo, e despenseiros dos mistérios de Deus.', 1, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Além disso requer-se dos despenseiros que cada um se ache fiel.', 2, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia, a mim mui pouco se me dá de ser julgado por vós, ou por algum juízo humano; nem eu tampouco a mim mesmo me julgo.', 3, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque em nada me sinto culpado; mas nem por isso me considero justificado, pois quem me julga é o Senhor.', 4, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, nada julgueis antes de tempo, até que o Senhor venha, o qual também trará à luz as coisas ocultas das trevas, e manifestará os desígnios dos corações; e então cada um receberá de Deus o louvor.', 5, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eu, irmãos, apliquei estas coisas, por semelhança, a mim e a Apolo, por amor de vós; para que em nós aprendais a não ir além do que está escrito, não vos ensoberbecendo a favor de um contra outro.', 6, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, quem te faz diferente? E que tens tu que não tenhas recebido? E, se o recebeste, por que te glorias, como se não o houveras recebido?', 7, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Já estais fartos! já estais ricos! sem nós reinais! e quisera reinásseis para que também nós viéssemos a reinar convosco!', 8, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tenho para mim, que Deus a nós, apóstolos, nos pôs por últimos, como condenados à morte; pois somos feitos espetáculo ao mundo, aos anjos, e aos homens.', 9, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nós somos loucos por amor de Cristo, e vós sábios em Cristo; nós fracos, e vós fortes; vós ilustres, e nós vis.', 10, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Até esta presente hora sofremos fome, e sede, e estamos nus, e recebemos bofetadas, e não temos pousada certa,', 11, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nos afadigamos, trabalhando com nossas próprias mãos. Somos injuriados, e bendizemos; somos perseguidos, e sofremos;', 12, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Somos blasfemados, e rogamos; até ao presente temos chegado a ser como o lixo deste mundo, e como a escória de todos.', 13, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não escrevo estas coisas para vos envergonhar; mas admoesto-vos como meus filhos amados.', 14, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ainda que tivésseis dez mil aios em Cristo, não teríeis, contudo, muitos pais; porque eu pelo evangelho vos gerei em Jesus Cristo.', 15, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Admoesto-vos, portanto, a que sejais meus imitadores.', 16, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por esta causa vos mandei Timóteo, que é meu filho amado, e fiel no Senhor, o qual vos lembrará os meus caminhos em Cristo, como por toda a parte ensino em cada igreja.', 17, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas alguns andam ensoberbecidos, como se eu não houvesse de ir ter convosco.', 18, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas em breve irei ter convosco, se o Senhor quiser, e então conhecerei, não as palavras dos que andam ensoberbecidos, mas o poder.', 19, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o reino de Deus não consiste em palavras, mas em poder.', 20, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que quereis? Irei ter convosco com vara ou com amor e espírito de mansidão?', 21, 4, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o Espírito expressamente diz que nos últimos tempos apostatarão alguns da fé, dando ouvidos a espíritos enganadores, e a doutrinas de demônios;', 1, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pela hipocrisia de homens que falam mentiras, tendo cauterizada a sua própria consciência;', 2, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Proibindo o casamento, e ordenando a abstinência dos alimentos que Deus criou para os fiéis, e para os que conhecem a verdade, a fim de usarem deles com ações de graças;', 3, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque toda a criatura de Deus é boa, e não há nada que rejeitar, sendo recebido com ações de graças.', 4, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque pela palavra de Deus e pela oração é santificada.', 5, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Propondo estas coisas aos irmãos, serás bom ministro de Jesus Cristo, criado com as palavras da fé e da boa doutrina que tens seguido.', 6, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas rejeita as fábulas profanas e de velhas, e exercita-te a ti mesmo em piedade;', 7, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o exercício corporal para pouco aproveita, mas a piedade para tudo é proveitosa, tendo a promessa da vida presente e da que há de vir.', 8, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esta palavra é fiel e digna de toda a aceitação;', 9, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque para isto trabalhamos e somos injuriados, pois esperamos no Deus vivo, que é o Salvador de todos os homens, principalmente dos fiéis.', 10, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Manda estas coisas e ensina-as.', 11, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém despreze a tua mocidade; mas sê o exemplo dos fiéis, na palavra, no trato, no amor, no espírito, na fé, na pureza.', 12, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Persiste em ler, exortar e ensinar, até que eu vá.', 13, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não desprezes o dom que há em ti, o qual te foi dado por profecia, com a imposição das mãos do presbitério.', 14, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Medita estas coisas; ocupa-te nelas, para que o teu aproveitamento seja manifesto a todos.', 15, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tem cuidado de ti mesmo e da doutrina. Persevera nestas coisas; porque, fazendo isto, te salvarás, tanto a ti mesmo como aos que te ouvem.', 16, 4, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo (chamado apóstolo de Jesus Cristo, pela vontade de Deus), e o irmão Sóstenes,', 1, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('À igreja de Deus que está em Corinto, aos santificados em Cristo Jesus, chamados santos, com todos os que em todo o lugar invocam o nome de nosso Senhor Jesus Cristo, Senhor deles e nosso:', 2, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graça e paz da parte de Deus nosso Pai, e do Senhor Jesus Cristo.', 3, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sempre dou graças ao meu Deus por vós pela graça de Deus que vos foi dada em Jesus Cristo.', 4, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque em tudo fostes enriquecidos nele, em toda a palavra e em todo o conhecimento', 5, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Como o testemunho de Cristo foi mesmo confirmado entre vós).', 6, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que nenhum dom vos falta, esperando a manifestação de nosso Senhor Jesus Cristo,', 7, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual vos confirmará também até ao fim, para serdes irrepreensíveis no dia de nosso Senhor Jesus Cristo.', 8, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fiel é Deus, pelo qual fostes chamados para a comunhão de seu Filho Jesus Cristo nosso Senhor.', 9, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Rogo-vos, porém, irmãos, pelo nome de nosso Senhor Jesus Cristo, que digais todos uma mesma coisa, e que não haja entre vós dissensões; antes sejais unidos em um mesmo pensamento e em um mesmo parecer.', 10, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a respeito de vós, irmãos meus, me foi comunicado pelos da família de Cloé que há contendas entre vós.', 11, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quero dizer com isto, que cada um de vós diz: Eu sou de Paulo, e eu de Apolo, e eu de Cefas, e eu de Cristo.', 12, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Está Cristo dividido? foi Paulo crucificado por vós? ou fostes vós batizados em nome de Paulo?', 13, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dou graças a Deus, porque a nenhum de vós batizei, senão a Crispo e a Gaio,', 14, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que ninguém diga que fostes batizados em meu nome.', 15, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E batizei também a família de Estéfanas; além destes, não sei se batizei algum outro.', 16, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Cristo enviou-me, não para batizar, mas para evangelizar; não em sabedoria de palavras, para que a cruz de Cristo se não faça vã.', 17, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a palavra da cruz é loucura para os que perecem; mas para nós, que somos salvos, é o poder de Deus.', 18, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque está escrito: Destruirei a sabedoria dos sábios, E aniquilarei a inteligência dos inteligentes.', 19, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde está o sábio? Onde está o escriba? Onde está o inquiridor deste século? Porventura não tornou Deus louca a sabedoria deste mundo?', 20, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Visto como na sabedoria de Deus o mundo não conheceu a Deus pela sua sabedoria, aprouve a Deus salvar os crentes pela loucura da pregação.', 21, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os judeus pedem sinal, e os gregos buscam sabedoria;', 22, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nós pregamos a Cristo crucificado, que é escândalo para os judeus, e loucura para os gregos.', 23, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas para os que são chamados, tanto judeus como gregos, lhes pregamos a Cristo, poder de Deus, e sabedoria de Deus.', 24, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a loucura de Deus é mais sábia do que os homens; e a fraqueza de Deus é mais forte do que os homens.', 25, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, vede, irmãos, a vossa vocação, que não são muitos os sábios segundo a carne, nem muitos os poderosos, nem muitos os nobres que são chamados.', 26, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus escolheu as coisas loucas deste mundo para confundir as sábias; e Deus escolheu as coisas fracas deste mundo para confundir as fortes;', 27, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Deus escolheu as coisas vis deste mundo, e as desprezíveis, e as que não são, para aniquilar as que são;', 28, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que nenhuma carne se glorie perante ele.', 29, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós sois dele, em Jesus Cristo, o qual para nós foi feito por Deus sabedoria, e justiça, e santificação, e redenção;', 30, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que, como está escrito: Aquele que se gloria glorie-se no Senhor.', 31, 1, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizia-lhes também: Em verdade vos digo que, dos que aqui estão, alguns há que não provarão a morte sem que vejam chegado o reino de Deus com poder.', 1, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E seis dias depois Jesus tomou consigo a Pedro, a Tiago, e a João, e os levou sós, em particular, a um alto monte; e transfigurou-se diante deles;', 2, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as suas vestes tornaram- se resplandecentes, extremamente brancas como a neve, tais como nenhum lavadeiro sobre a terra as poderia branquear.', 3, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E apareceu-lhes Elias, com Moisés, e falavam com Jesus.', 4, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro, tomando a palavra, disse a Jesus: Mestre, é bom que estejamos aqui; e façamos três cabanas, uma para ti, outra para Moisés, e outra para Elias.', 5, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois não sabia o que dizia, porque estavam assombrados.', 6, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E desceu uma nuvem que os cobriu com a sua sombra, e saiu da nuvem uma voz que dizia: Este é o meu filho amado; a ele ouvi.', 7, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo olhado em redor, ninguém mais viram, senão só Jesus com eles.', 8, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, descendo eles do monte, ordenou-lhes que a ninguém contassem o que tinham visto, até que o Filho do homem ressuscitasse dentre os mortos.', 9, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles retiveram o caso entre si, perguntando uns aos outros que seria aquilo, ressuscitar dentre os mortos.', 10, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E interrogaram-no, dizendo: Por que dizem os escribas que é necessário que Elias venha primeiro?', 11, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo ele, disse-lhes: Em verdade Elias virá primeiro, e todas as coisas restaurará; e, como está escrito do Filho do homem, que ele deva padecer muito e ser aviltado.', 12, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo-vos, porém, que Elias já veio, e fizeram-lhe tudo o que quiseram, como dele está escrito.', 13, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando se aproximou dos discípulos, viu ao redor deles grande multidão, e alguns escribas que disputavam com eles.', 14, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo toda a multidão, vendo-o, ficou espantada e, correndo para ele, o saudaram.', 15, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntou aos escribas: Que é que discutis com eles?', 16, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um da multidão, respondendo, disse: Mestre, trouxe-te o meu filho, que tem um espírito mudo;', 17, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E este, onde quer que o apanhe, despedaça-o, e ele espuma, e range os dentes, e vai definhando; e eu disse aos teus discípulos que o expulsassem, e não puderam.', 18, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, respondendo-lhes, disse: Ó geração incrédula! até quando estarei convosco? até quando vos sofrerei ainda? Trazei-mo.', 19, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E trouxeram-lho; e quando ele o viu, logo o espírito o agitou com violência, e, caindo o endemoninhado por terra, revolvia-se, escumando.', 20, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntou ao pai dele: Quanto tempo há que lhe sucede isto? E ele disse-lhe: Desde a infância.', 21, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitas vezes o tem lançado no fogo, e na água, para o destruir; mas, se tu podes fazer alguma coisa, tem compaixão de nós, e ajuda-nos.', 22, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus disse-lhe: Se tu podes crer, tudo é possível ao que crê.', 23, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo o pai do menino, clamando, com lágrimas, disse: Eu creio, Senhor! ajuda a minha incredulidade.', 24, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, vendo que a multidão concorria, repreendeu o espírito imundo, dizendo-lhe: Espírito mudo e surdo, eu te ordeno: Sai dele, e não entres mais nele.', 25, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, clamando, e agitando-o com violência, saiu; e ficou o menino como morto, de tal maneira que muitos diziam que estava morto.', 26, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus, tomando-o pela mão, o ergueu, e ele se levantou.', 27, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando entrou em casa, os seus discípulos lhe perguntaram à parte: Por que o não pudemos nós expulsar?', 28, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Esta casta não pode sair com coisa alguma, a não ser com oração e jejum.', 29, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo partido dali, caminharam pela Galiléia, e não queria que alguém o soubesse;', 30, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ensinava os seus discípulos, e lhes dizia: O Filho do homem será entregue nas mãos dos homens, e matá-lo-ão; e, morto ele, ressuscitará ao terceiro dia.', 31, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eles não entendiam esta palavra, e receavam interrogá-lo.', 32, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E chegou a Cafarnaum e, entrando em casa, perguntou-lhes: Que estáveis vós discutindo pelo caminho?', 33, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eles calaram-se; porque pelo caminho tinham disputado entre si qual era o maior.', 34, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, assentando-se, chamou os doze, e disse-lhes: Se alguém quiser ser o primeiro, será o derradeiro de todos e o servo de todos.', 35, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, lançando mão de um menino, pô-lo no meio deles e, tomando-o nos seus braços, disse-lhes:', 36, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer que receber um destes meninos em meu nome, a mim me recebe; e qualquer que a mim me receber, recebe, não a mim, mas ao que me enviou.', 37, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E João lhe respondeu, dizendo: Mestre, vimos um que em teu nome expulsava demônios, o qual não nos segue; e nós lho proibimos, porque não nos segue.', 38, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, disse: Não lho proibais; porque ninguém há que faça milagre em meu nome e possa logo falar mal de mim.', 39, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque quem não é contra nós, é por nós.', 40, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto, qualquer que vos der a beber um copo de água em meu nome, porque sois discípulos de Cristo, em verdade vos digo que não perderá o seu galardão.', 41, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qualquer que escandalizar um destes pequeninos que crêem em mim, melhor lhe fora que lhe pusessem ao pescoço uma mó de atafona, e que fosse lançado no mar.', 42, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se a tua mão te escandalizar, corta-a; melhor é para ti entrares na vida aleijado do que, tendo duas mãos, ires para o inferno, para o fogo que nunca se apaga,', 43, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde o seu bicho não morre, e o fogo nunca se apaga.', 44, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se o teu pé te escandalizar, corta-o; melhor é para ti entrares coxo na vida do que, tendo dois pés, seres lançado no inferno, no fogo que nunca se apaga,', 45, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde o seu bicho não morre, e o fogo nunca se apaga.', 46, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se o teu olho te escandalizar, lança-o fora; melhor é para ti entrares no reino de Deus com um só olho do que, tendo dois olhos, seres lançado no fogo do inferno,', 47, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Onde o seu bicho não morre, e o fogo nunca se apaga.', 48, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque cada um será salgado com fogo, e cada sacrifício será salgado com sal.', 49, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bom é o sal; mas, se o sal se tornar insípido, com que o temperareis? Tende sal em vós mesmos, e paz uns com os outros.', 50, 9, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo, pois, que todo o tempo que o herdeiro é menino em nada difere do servo, ainda que seja senhor de tudo;', 1, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas está debaixo de tutores e curadores até ao tempo determinado pelo pai.', 2, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também nós, quando éramos meninos, estávamos reduzidos à servidão debaixo dos primeiros rudimentos do mundo.', 3, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, vindo a plenitude dos tempos, Deus enviou seu Filho, nascido de mulher, nascido sob a lei,', 4, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para remir os que estavam debaixo da lei, a fim de recebermos a adoção de filhos.', 5, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, porque sois filhos, Deus enviou aos vossos corações o Espírito de seu Filho, que clama: Aba, Pai.', 6, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim que já não és mais servo, mas filho; e, se és filho, és também herdeiro de Deus por Cristo.', 7, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando não conhecíeis a Deus, servíeis aos que por natureza não são deuses.', 8, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora, conhecendo a Deus, ou, antes, sendo conhecidos por Deus, como tornais outra vez a esses rudimentos fracos e pobres, aos quais de novo quereis servir?', 9, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Guardais dias, e meses, e tempos, e anos.', 10, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Receio de vós, que não haja trabalhado em vão para convosco.', 11, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Irmãos, rogo-vos que sejais como eu, porque também eu sou como vós; nenhum mal me fizestes.', 12, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vós sabeis que primeiro vos anunciei o evangelho estando em fraqueza da carne;', 13, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não rejeitastes, nem desprezastes isso que era uma tentação na minha carne, antes me recebestes como um anjo de Deus, como Jesus Cristo mesmo.', 14, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qual é, logo, a vossa bem-aventurança? Porque vos dou testemunho de que, se possível fora, arrancaríeis os vossos olhos, e mos daríeis.', 15, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Fiz-me acaso vosso inimigo, dizendo a verdade?', 16, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eles têm zelo por vós, não como convém; mas querem excluir-vos, para que vós tenhais zelo por eles.', 17, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É bom ser zeloso, mas sempre do bem, e não somente quando estou presente convosco.', 18, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Meus filhinhos, por quem de novo sinto as dores de parto, até que Cristo seja formado em vós;', 19, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu bem quisera agora estar presente convosco, e mudar a minha voz; porque estou perplexo a vosso respeito.', 20, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizei-me, os que quereis estar debaixo da lei, não ouvis vós a lei?', 21, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque está escrito que Abraão teve dois filhos, um da escrava, e outro da livre.', 22, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia, o que era da escrava nasceu segundo a carne, mas, o que era da livre, por promessa.', 23, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O que se entende por alegoria; porque estas são as duas alianças; uma, do monte Sinai, gerando filhos para a servidão, que é Agar.', 24, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, esta Agar é Sinai, um monte da Arábia, que corresponde à Jerusalém que agora existe, pois é escrava com seus filhos.', 25, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a Jerusalém que é de cima é livre; a qual é mãe de todos nós.', 26, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque está escrito: Alegra-te, estéril, que não dás à luz; Esforça-te e clama, tu que não estás de parto; Porque os filhos da solitária são mais do que os da que tem marido.', 27, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nós, irmãos, somos filhos da promessa como Isaque.', 28, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, como então aquele que era gerado segundo a carne perseguia o que o era segundo o Espírito, assim é também agora.', 29, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas que diz a Escritura? Lança fora a escrava e seu filho, porque de modo algum o filho da escrava herdará com o filho da livre.', 30, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que, irmãos, somos filhos, não da escrava, mas da livre.', 31, 4, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estai, pois, firmes na liberdade com que Cristo nos libertou, e não torneis a colocar-vos debaixo do jugo da servidão.', 1, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eis que eu, Paulo, vos digo que, se vos deixardes circuncidar, Cristo de nada vos aproveitará.', 2, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de novo protesto a todo o homem, que se deixa circuncidar, que está obrigado a guardar toda a lei.', 3, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Separados estais de Cristo, vós os que vos justificais pela lei; da graça tendes caído.', 4, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nós pelo Espírito da fé aguardamos a esperança da justiça.', 5, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque em Jesus Cristo nem a circuncisão nem a incircuncisão tem valor algum; mas sim a fé que opera pelo amor.', 6, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Corríeis bem; quem vos impediu, para que não obedeçais à verdade?', 7, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esta persuasão não vem daquele que vos chamou.', 8, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Um pouco de fermento leveda toda a massa.', 9, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Confio de vós, no Senhor, que nenhuma outra coisa sentireis; mas aquele que vos inquieta, seja ele quem for, sofrerá a condenação.', 10, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu, porém, irmãos, se prego ainda a circuncisão, por que sou, pois, perseguido? Logo o escândalo da cruz está aniquilado.', 11, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eu quereria que fossem cortados aqueles que vos andam inquietando.', 12, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque vós, irmãos, fostes chamados à liberdade. Não useis então da liberdade para dar ocasião à carne, mas servi-vos uns aos outros pelo amor.', 13, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque toda a lei se cumpre numa só palavra, nesta: Amarás ao teu próximo como a ti mesmo.', 14, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se vós, porém, vos mordeis e devorais uns aos outros, vede não vos consumais também uns aos outros.', 15, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo, porém: Andai em Espírito, e não cumprireis a concupiscência da carne.', 16, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a carne cobiça contra o Espírito, e o Espírito contra a carne; e estes opõem-se um ao outro, para que não façais o que quereis.', 17, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se sois guiados pelo Espírito, não estais debaixo da lei.', 18, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque as obras da carne são manifestas, as quais são: adultério, fornicação, impureza, lascívia,', 19, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Idolatria, feitiçaria, inimizades, porfias, emulações, iras, pelejas, dissensões, heresias,', 20, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Invejas, homicídios, bebedices, glutonarias, e coisas semelhantes a estas, acerca das quais vos declaro, como já antes vos disse, que os que cometem tais coisas não herdarão o reino de Deus.', 21, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o fruto do Espírito é: amor, gozo, paz, longanimidade, benignidade, bondade, fé, mansidão, temperança.', 22, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Contra estas coisas não há lei.', 23, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que são de Cristo crucificaram a carne com as suas paixões e concupiscências.', 24, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se vivemos em Espírito, andemos também em Espírito.', 25, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não sejamos cobiçosos de vanglórias, irritando-nos uns aos outros, invejando-nos uns aos outros.', 26, 5, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, partindo dali, chegou à sua pátria, e os seus discípulos o seguiram.', 1, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando o sábado, começou a ensinar na sinagoga; e muitos, ouvindo-o, se admiravam, dizendo: De onde lhe vêm estas coisas? e que sabedoria é esta que lhe foi dada? e como se fazem tais maravilhas por suas mãos?', 2, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não é este o carpinteiro, filho de Maria, e irmão de Tiago, e de José, e de Judas e de Simão? e não estão aqui conosco suas irmãs? E escandalizavam-se nele.', 3, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhes dizia: Não há profeta sem honra senão na suapátria, entre os seus parentes, e na sua casa.', 4, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não podia fazer ali nenhuma obra maravilhosa; somente curou alguns poucos enfermos, impondo-lhes as mãos.', 5, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estava admirado da incredulidade deles.  E percorreu as aldeias vizinhas, ensinando.', 6, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chamou a si os doze, e começou a enviá-los a dois e dois, e deu-lhes poder sobre os espíritos imundos;', 7, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ordenou-lhes que nada tomassem para o caminho, senão somente um bordão; nem alforje, nem pão, nem dinheiro no cinto;', 8, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas que calçassem alparcas, e que não vestissem duas túnicas.', 9, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia-lhes: Na casa em que entrardes, ficai nela até partirdes dali.', 10, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tantos quantos vos não receberem, nem vos ouvirem, saindo dali, sacudi o pó que estiver debaixo dos vossos pés, em testemunho contra eles.  Em verdade vos digo que haverá mais tolerância no dia de juízo para Sodoma e Gomorra, do que para os daquela cidade.', 11, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo eles, pregavam que se arrependessem.', 12, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E expulsavam muitos demônios, e ungiam muitos enfermos com óleo, e os curavam.', 13, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouviu isto o rei Herodes (porque o nome de Jesus se tornara notório), e disse: João, o que batizava, ressuscitou dentre os mortos, e por isso estas maravilhas operam nele.', 14, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Outros diziam: É Elias.  E diziam outros: É um profeta, ou como um dos profetas.', 15, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Herodes, porém, ouvindo isto, disse: Este é João, que mandei degolar; ressuscitou dentre os mortos.', 16, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto o mesmo Herodes mandara prender a João, e encerrá-lo maniatado no cárcere, por causa de Herodias, mulher de Filipe, seu irmão, porquanto tinha casado com ela.', 17, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois João dizia a Herodes: Não te é lícito possuir a mulher de teu irmão.', 18, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Herodias o espiava, e queria matá-lo, mas não podia.', 19, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Herodes temia a João, sabendo que era homem justo e santo; e guardava-o com segurança, e fazia muitas coisas, atendendo-o, e de boa mente o ouvia.', 20, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando uma ocasião favorável em que Herodes, no dia dos seus anos, dava uma ceia aos grandes, e tribunos, e príncipes da Galiléia,', 21, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Entrou a filha da mesma Herodias, e dançou, e agradou a Herodes e aos que estavam com ele à mesa.  Disse então o rei à menina: Pede-me o que quiseres, e eu to darei.', 22, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E jurou-lhe, dizendo: Tudo o que me pedires te darei, até metade do meu reino.', 23, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo ela, perguntou a sua mãe: Que pedirei? E ela disse: A cabeça de João o Batista.', 24, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando logo, apressadamente, pediu ao rei, dizendo: Quero que imediatamente me dês num prato a cabeça de João o Batista.', 25, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o rei entristeceu-se muito; todavia, por causa do juramento e dos que estavam com ele à mesa, não lha quis negar.', 26, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, enviando logo o rei o executor, mandou que lhe trouxessem ali a cabeça de João.  E ele foi, e degolou-o na prisão;', 27, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E trouxe a cabeça num prato, e deu-a à menina, e a menina a deu a sua mãe.', 28, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus discípulos, tendo ouvido isto, foram, tomaram o seu corpo, e o puseram num sepulcro.', 29, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os apóstolos ajuntaram-se a Jesus, e contaram-lhe tudo, tanto o que tinham feito como o que tinham ensinado.', 30, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse-lhes: Vinde vós, aqui à parte, a um lugar deserto, e repousai um pouco.  Porque havia muitos que iam e vinham, e não tinham tempo para comer.', 31, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram sós num barco para um lugar deserto.', 32, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a multidão viu-os partir, e muitos o conheceram; e correram para lá, a pé, de todas as cidades, e ali chegaram primeiro do que eles, e aproximavam-se dele.', 33, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, saindo, viu uma grande multidão, e teve compaixão deles, porque eram como ovelhas que não têm pastor; e começou a ensinar-lhes muitas coisas.', 34, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, como o dia fosse já muito adiantado, os seus discípulos se aproximaram dele, e lhe disseram: O lugar é deserto, e o dia está já muito adiantado.', 35, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Despede-os, para que vão aos lugares e aldeias circunvizinhas, e comprem pão para si; porque não têm que comer.', 36, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, respondendo, lhes disse: Dai-lhes vós de comer.  E eles disseram-lhe: Iremos nós, e compraremos duzentos dinheiros de pão para lhes darmos de comer?', 37, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse-lhes: Quantos pães tendes? Ide ver.  E, sabendo-o eles, disseram: Cinco pães e dois peixes.', 38, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ordenou-lhes que fizessem assentar a todos, em ranchos, sobre a erva verde.', 39, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E assentaram-se repartidos de cem em cem, e de cinqüenta em cinqüenta.', 40, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando ele os cinco pães e os dois peixes, levantou os olhos ao céu, abençoou e partiu os pães, e deu-os aos seus discípulos para que os pusessem diante deles.  E repartiu os dois peixes por todos.', 41, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos comeram, e ficaram fartos;', 42, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levantaram doze alcofas cheias de pedaços de pão e de peixe.', 43, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que comeram os pães eram quase cinco mil homens.', 44, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo obrigou os seus discípulos a subir para o barco, e passar adiante, para o outro lado, a Betsaida, enquanto ele despedia a multidão.', 45, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo-os despedido, foi ao monte a orar.', 46, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sobrevindo a tarde, estava o barco no meio do mar e ele, sozinho, em terra.', 47, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vendo que se fatigavam a remar, porque o vento lhes era contrário, perto da quarta vigília da noite aproximou-se deles, andando sobre o mar, e queria passar-lhes adiante.', 48, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando eles o viram andar sobre o mar, cuidaram que era um fantasma, e deram grandes gritos.', 49, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos o viam, e perturbaram-se; mas logo falou com eles, e disse-lhes: Tende bom ânimo; sou eu, não temais.', 50, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E subiu para o barco, para estar com eles, e o vento se aquietou; e entre si ficaram muito assombrados e maravilhados;', 51, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois não tinham compreendido o milagre dos pães; antes o seu coração estava endurecido.', 52, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando já estavam no outro lado, dirigiram-se à terra de Genesaré, e ali atracaram.', 53, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo eles do barco, logo o conheceram;', 54, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, correndo toda a terra em redor, começaram a trazer em leitos, aonde quer que sabiam que ele estava, os que se achavam enfermos.', 55, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, onde quer que entrava, ou em cidade, ou aldeias, ou no campo, apresentavam os enfermos nas praças, e rogavam-lhe que os deixasse tocar ao menos na orla da sua roupa; e todos os que lhe tocavam saravam.', 56, 6, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois, passados catorze anos, subi outra vez a Jerusalém com Barnabé, levando também comigo Tito.', 1, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E subi por uma revelação, e lhes expus o evangelho, que prego entre os gentios, e particularmente aos que estavam em estima; para que de maneira alguma não corresse ou não tivesse corrido em vão.', 2, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas nem ainda Tito, que estava comigo, sendo grego, foi constrangido a circuncidar-se;', 3, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E isto por causa dos falsos irmãos que se intrometeram, e secretamente entraram a espiar a nossa liberdade, que temos em Cristo Jesus, para nos porem em servidão;', 4, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aos quais nem ainda por uma hora cedemos com sujeição, para que a verdade do evangelho permanecesse entre vós.', 5, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quanto àqueles que pareciam ser alguma coisa (quais tenham sido noutro tempo, não se me dá; Deus não aceita a aparência do homem), esses, digo, que pareciam ser alguma coisa, nada me comunicaram;', 6, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, pelo contrário, quando viram que o evangelho da incircuncisão me estava confiado, como a Pedro o da circuncisão', 7, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Porque aquele que operou eficazmente em Pedro para o apostolado da circuncisão, esse operou também em mim com eficácia para com os gentios),', 8, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E conhecendo Tiago, Cefas e João, que eram considerados como as colunas, a graça que me havia sido dada, deram-nos as destras, em comunhão comigo e com Barnabé, para que nós fôssemos aos gentios, e eles à circuncisão;', 9, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Recomendando-nos somente que nos lembrássemos dos pobres, o que também procurei fazer com diligência.', 10, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando Pedro à Antioquia, lhe resisti na cara, porque era repreensível.', 11, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, antes que alguns tivessem chegado da parte de Tiago, comia com os gentios; mas, depois que chegaram, se foi retirando, e se apartou deles, temendo os que eram da circuncisão.', 12, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os outros judeus também dissimulavam com ele, de maneira que até Barnabé se deixou levar pela sua dissimulação.', 13, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando vi que não andavam bem e direitamente conforme a verdade do evangelho, disse a Pedro na presença de todos: Se tu, sendo judeu, vives como os gentios, e não como judeu, por que obrigas os gentios a viverem como judeus?', 14, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nós somos judeus por natureza, e não pecadores dentre os gentios.', 15, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabendo que o homem não é justificado pelas obras da lei, mas pela fé em Jesus Cristo, temos também crido em Jesus Cristo, para sermos justificados pela fé em Cristo, e não pelas obras da lei; porquanto pelas obras da lei nenhuma carne será justificada.', 16, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, se nós, que procuramos ser justificados em Cristo, nós mesmos também somos achados pecadores, é porventura Cristo ministro do pecado? De maneira nenhuma.', 17, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se torno a edificar aquilo que destruí, constituo-me a mim mesmo transgressor.', 18, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque eu, pela lei, estou morto para a lei, para viver para Deus.', 19, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Já estou crucificado com Cristo; e vivo, não mais eu, mas Cristo vive em mim; e a vida que agora vivo na carne, vivo-a pela fé do Filho de Deus, o qual me amou, e se entregou a si mesmo por mim.', 20, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não aniquilo a graça de Deus; porque, se a justiça provém da lei, segue-se que Cristo morreu debalde.', 21, 2, 9);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passado o sábado, Maria Madalena, e Maria, mãe de Tiago, e Salomé, compraram aromas para irem ungi-lo.', 1, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, no primeiro dia da semana, foram ao sepulcro, de manhã cedo, ao nascer do sol.', 2, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E diziam umas às outras: Quemnos revolverá a pedra da porta do sepulcro?', 3, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, olhando, viram que já a pedra estava revolvida; e era ela muito grande.', 4, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando no sepulcro, viram um jovem assentado à direita, vestido de uma roupa comprida, branca; e ficaram espantadas.', 5, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, disse-lhes: Não vos assusteis; buscais a Jesus Nazareno, que foi crucificado; já ressuscitou, não está aqui; eis aqui o lugar onde o puseram.', 6, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ide, dizei a seus discípulos, e a Pedro, que ele vai adiante de vós para a Galiléia; ali o vereis, como ele vos disse.', 7, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo elas apressadamente, fugiram do sepulcro, porque estavam possuídas de temor e assombro; e nada diziam a ninguém porque temiam.', 8, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, tendo ressuscitado na manhã do primeiro dia da semana, apareceu primeiramente a Maria Madalena, da qual tinha expulsado sete demônios.', 9, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, partindo ela, anunciou-o àqueles que tinham estado com ele, os quais estavam tristes, e chorando.', 10, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo eles que vivia, e que tinha sido visto por ela, não o creram.', 11, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois manifestou-se de outra forma a dois deles, que iam de caminho para o campo.', 12, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo estes, anunciaram-no aos outros, mas nem ainda estes creram.', 13, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Finalmente apareceu aos onze, estando eles assentados juntamente, e lançou-lhes em rosto a sua incredulidade e dureza de coração, por não haverem crido nos que o tinham visto já ressuscitado.', 14, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Ide por todo omundo, pregai o evangelho a toda criatura.', 15, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quem crer e for batizado será salvo; mas quem não crer será condenado.', 16, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estes sinais seguirão aos que crerem: Em meu nome expulsarão os demônios; falarão novas línguas;', 17, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pegarão nas serpentes; e, se beberem alguma coisa mortífera, não lhes fará dano algum; e porãoas mãos sobre os enfermos, e os curarão.', 18, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o Senhor, depois de lhes ter falado, foi recebido no céu, e assentou-se à direita de Deus.', 19, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles, tendo partido, pregaram por todas as partes, cooperando com eles o Senhor, e confirmando a palavra com os sinais que se seguiram.  Amém.', 20, 16, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo ao amanhecer, os principais dos sacerdotes, com os anciãos, e os escribas, e todo o Sinédrio, tiveram conselho; e, ligando Jesus, o levaram e entregaram a Pilatos.', 1, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pilatos lhe perguntou: Tu és o Rei dos Judeus? E ele, respondendo, disse-lhe: Tu o dizes.', 2, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os principais dos sacerdotes o acusavam de muitas coisas; porém ele nada respondia.', 3, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pilatos o interrogou outra vez, dizendo: Nada respondes? Vê quantas coisas testificam contra ti.', 4, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus nada mais respondeu, de maneira que Pilatos se maravilhava.', 5, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, no dia da festa costumava soltar-lhes um preso qualquer que eles pedissem.', 6, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E havia um chamado Barrabás, que, preso com outros amotinado- res, tinha num motim cometido uma morte.', 7, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a multidão, dando gritos, começou a pedir que fizesse como sempre lhes tinha feito.', 8, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pilatos lhes respondeu, dizendo: Quereis que vos solte o Rei dos Judeus?', 9, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque ele bem sabia que por inveja os principais dos sacerdotes o tinham entregado.', 10, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os principais dos sacerdotes incitaram a multidão para que fosse solto antes Barrabás.', 11, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pilatos, respondendo, lhes disse outra vez: Que quereis, pois, que faça daquele a quem chamais Rei dos Judeus?', 12, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles tornaram a clamar: Crucifica-o.', 13, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Pilatos lhes disse: Mas que mal fez? E eles cada vez clamavam mais: Crucifica-o.', 14, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Pilatos, querendo satisfazer a multidão, soltou-lhe Barrabás e, açoitado Jesus, o entregou para ser crucificado.', 15, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os soldados o levaram dentro à sala, que é a da audiência, e convocaram toda a coorte.', 16, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vestiram-no de púrpura, e tecendo uma coroa de espinhos, lha puseram na cabeça.', 17, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E começaram a saudá-lo, dizendo: Salve, Rei dos Judeus!', 18, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E feriram-no na cabeça com uma cana, e cuspiram nele e, postos de joelhos, o adoraram.', 19, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-o escarnecido, despiram-lhe a púrpura, e o vestiram com as suas próprias vestes; e o levaram para fora a fim de o crucificarem.', 20, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E constrangeram um certo Simão, cireneu, pai de Alexandre e de Rufo, que por ali passava, vindo do campo, a que levasse a cruz.', 21, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levaram-no ao lugar doGólgota, que se traduz por lugar da Caveira.', 22, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E deram-lhe a beber vinho com mirra, mas ele não o tomou.', 23, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-o crucificado, repartiram as suas vestes, lançando sobre elas sortes, para saber o que cada um levaria.', 24, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E era a hora terceira, e o crucificaram.', 25, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por cima dele estava escrita a sua acusação: O REI DOS JUDEUS.', 26, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E crucificaram com ele dois salteadores, um à sua direita, e outro à esquerda.', 27, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E cumprindo-se a escritura que diz: E com os malfeitores foi contado.', 28, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que passavam blasfemavam dele, meneando as suas cabeças, e dizendo: Ah! tu que derrubas o templo, e em três dias o edificas,', 29, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Salva-te a ti mesmo, e desce da cruz.', 30, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E da mesma maneira também os principais dos sacerdotes, com os escribas, diziam uns para os outros, zombando: Salvou os outros, e não pode salvar-se a si mesmo.', 31, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O Cristo, o Rei de Israel, desça agora da cruz, para que o vejamos e acreditemos.  Também os que com ele foram crucificados o injuriavam.', 32, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegada a hora sexta, houve trevas sobre toda a terra até a hora nona.', 33, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, à hora nona, Jesus exclamou com grande voz, dizendo: Eloí, Eloí, lamá sabactâni? que, traduzido, é: Deus meu, Deus meu, por que me desamparaste?', 34, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns dos que ali estavam, ouvindo isto, diziam: Eis que chama por Elias.', 35, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um deles correu a embeber uma esponja em vinagre e, pondo-a numa cana, deu-lho a beber, dizendo: Deixai, vejamos se virá Elias tirá-lo.', 36, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, dando um grande brado, expirou.', 37, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o véu do templo se rasgou em dois, de alto a baixo.', 38, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o centurião, que estava defronte dele, vendo que assim clamando expirara, disse: Verdadeiramente este homem era o Filho de Deus.', 39, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E também ali estavam algumas mulheres, olhando de longe, entre as quais também Maria Madalena, e Maria, mãe de Tiago, o menor, e de José, e Salomé;', 40, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As quais também o seguiam, e o serviam, quando estava na Galiléia; e muitas outras, que tinham subido com ele a Jerusalém.', 41, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegada a tarde, porquanto era o dia da preparação, isto é, a véspera do sábado,', 42, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chegou José de Arimatéia, senador honrado, que também esperava o reino de Deus, e ousadamente foi a Pilatos, e pediu o corpo de Jesus.', 43, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pilatos se maravilhou de que já estivesse morto.  E, chamando o centurião, perguntou-lhe se já havia muito que tinha morrido.', 44, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo-se certificado pelo centurião, deu o corpo a José;', 45, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual comprara um lençol fino, e, tirando-o da cruz, o envolveu nele, e o depositou num sepulcro lavrado numa rocha; e revolveu uma pedra para a porta do sepulcro.', 46, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Maria Madalena e Maria, mãe de José, observavam onde o punham.', 47, 15, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra vez entrou na sinagoga, e estava ali um homem que tinha uma das mãos mirrada.', 1, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estavam observando-o securaria no sábado, para o acusarem.', 2, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse ao homem que tinha a mão mirrada: Levanta-te e vem para o meio.', 3, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntou-lhes: É lícito no sábado fazer bem, ou fazer mal? salvar a vida, ou matar? E eles calaram-se.', 4, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, olhando para eles em redor com indignação, condoendo-se da dureza do seu coração, disse ao homem: Estende a tua mão.  E ele a estendeu, e foi-lhe restituída a sua mão, sã como a outra.', 5, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo saído os fariseus, tomaram logo conselho com os herodianos contra ele, procurando ver como o matariam.', 6, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E retirou-se Jesus com os seus discípulos para o mar, e seguia-o uma grande multidão da Galiléia e da Judéia,', 7, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E de Jerusalém, e da Iduméia, e de além do Jordão, e de perto de Tiro e de Sidom; uma grande multidão que, ouvindo quão grandes coisas fazia, vinha ter com ele.', 8, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse aos seus discípulos que lhe tivessem sempre pronto um barquinho junto dele, por causa da multidão, para que o não oprimisse,', 9, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tinha curado a muitos, de tal maneira que todos quantos tinham algum mal se arrojavam sobre ele, para lhe tocarem.', 10, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os espíritos imundos vendo-o, prostravam-se diante dele, e clamavam, dizendo: Tu és o Filho de Deus.', 11, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele os ameaçava muito, para que não o manifestassem.', 12, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E subiu ao monte, e chamou para si os que ele quis; e vieram a ele.', 13, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nomeou doze para que estivessem com ele e os mandasse a pregar,', 14, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E para que tivessem o poderde curar as enfermidades e expulsar os demônios:', 15, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A Simão, a quem pôs o nome de Pedro,', 16, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a Tiago, filho de Zebedeu, e a João, irmão de Tiago, aos quais pôs o nome de Boanerges, que significa: Filhos do trovão;', 17, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a André, e a Filipe, e a Bartolomeu, e a Mateus, e a Tomé, e a Tiago, filho de Alfeu, e a Tadeu, e a Simão, o Cananita,', 18, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a Judas Iscariotes, o que o entregou.', 19, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram para uma casa.  E afluiu outra vez a multidão, de tal maneira que nem sequer podiam comer pão.', 20, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando os seus ouviram isto, saíram para o prender; porque diziam: Está fora de si.', 21, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os escribas, que tinham descido de Jerusalém, diziam: Tem Belzebu, e pelo príncipe dos demônios expulsa os demônios.', 22, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando-os a si, disse-lhes por parábolas: Como pode Satanás expulsar Satanás?', 23, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se um reino se dividir contra si mesmo, tal reino não pode subsistir;', 24, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se uma casa se dividir contra si mesma, tal casa não pode subsistir.', 25, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se Satanás se levantar contra si mesmo, e for dividido, não pode subsistir; antes tem fim.', 26, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém pode roubar os bens do valente, entrando-lhe em sua casa, se primeiro não maniatar o valente; e então roubará a sua casa.', 27, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade vos digo que todos os pecados serão perdoados aos filhos dos homens, e toda a sorte de blasfêmias, com que blasfemarem;', 28, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qualquer, porém, que blasfemar contra o Espírito Santo, nunca obterá perdão, mas será réu do eterno juízo', 29, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('(Porque diziam: Tem espírito imundo).', 30, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chegaram, então, seus irmãos e sua mãe; e, estando fora, mandaram-no chamar.', 31, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a multidão estava assentada ao redor dele, e disseram-lhe: Eis que tua mãe e teus irmãos te procuram, e estão lá fora.', 32, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes respondeu, dizendo: Quem é minha mãe e meus irmãos?', 33, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, olhando em redor para os que estavam assentados junto dele, disse: Eis aqui minha mãe e meus irmãos.', 34, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto, qualquer que fizer a vontade de Deus, esse é meu irmão, e minha irmã, e minha mãe.', 35, 3, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo que se aproximaram de Jerusalém, de Betfagé e de Betânia, junto do Monte das Oliveiras, enviou dois dos seus discípulos,', 1, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Ide à aldeia que está defronte de vós; e, logo que ali entrardes, encontrareis preso um jumentinho, sobre o qual ainda não montou homem algum; soltai-o, e trazei-mo.', 2, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se alguém vos disser: Por que fazeis isso? dizei-lhe que o Senhor precisa dele, e logo o deixará trazer para aqui.', 3, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram, e encontraram o jumentinho preso fora da porta, entre dois caminhos, e o soltaram.', 4, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns dos que ali estavam lhes disseram: Que fazeis, soltando o jumentinho?', 5, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Eles, porém, disseram-lhes como Jesus lhes tinha mandado; e deixaram-nos ir.', 6, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levaram o jumentinho a Jesus, e lançaram sobre ele as suas vestes, e assentou-se sobre ele.', 7, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos estendiam as suas vestes pelo caminho, e outros cortavam ramos das árvores, e os espalhavam pelo caminho.', 8, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aqueles que iam adiante, e os que seguiam, clamavam, dizendo: Hosana, bendito o que vem em nome do Senhor;', 9, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bendito o reino do nosso pai Davi, que vem em nome do Senhor.  Hosana nas alturas.', 10, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus entrou em Jerusalém, no templo, e, tendo visto tudo em redor, como fosse já tarde, saiu para Betânia com os doze.', 11, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, no dia seguinte, quando saíram de Betânia, teve fome.', 12, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo de longe uma figueira que tinha folhas, foi ver se nela acharia alguma coisa; e, chegando a ela, não achou senão folhas, porque não era tempo de figos.', 13, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, falando, disse à figueira: Nunca mais coma alguém fruto de ti.  E os seus discípulos ouviram isto.', 14, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vieram a Jerusalém; e Jesus, entrando no templo, começou a expulsar os que vendiam e compravam no templo; e derrubou as mesas dos cambiadores e as cadeiras dos que vendiam pombas.', 15, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não consentia que alguém levasse algum vaso pelo templo.', 16, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os ensinava, dizendo: Não está escrito: A minha casa será chamada, por todas as nações, casa de oração? Mas vós a tendes feito covil de ladrões.', 17, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os escribas e príncipes dos sacerdotes, tendo ouvido isto, buscavam ocasião para o matar; pois eles o temiam, porque toda a multidão estava admirada acerca da sua doutrina.', 18, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, sendo já tarde, saiu para fora da cidade.', 19, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles, passando pela manhã, viram que a figueira se tinha secado desde as raízes.', 20, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro, lembrando-se, disse-lhe: Mestre, eis que a figueira, que tu amaldiçoaste, se secou.', 21, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhes: Tende fé em Deus;', 22, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque em verdade vos digo que qualquer que disser a este monte: Ergue-te e lança-te no mar, e não duvidar em seu coração, mas crer que se fará aquilo que diz, tudo o que disser lhe será feito.', 23, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso vos digo que todas as coisas que pedirdes, orando, crede receber, e tê-las-eis.', 24, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando estiverdes orando, perdoai, se tendes alguma coisa contra alguém, para que vosso Pai, que está nos céus, vos perdoe as vossas ofensas.', 25, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se vós não perdoardes, também vosso Pai, que está nos céus, vos não perdoará as vossas ofensas.', 26, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tornaram a Jerusalém, e, andando ele pelo templo, os principais dos sacerdotes, e os escribas, e os anciãos, se aproximaram dele.', 27, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lhe disseram: Com que autoridade fazes tu estas coisas? ou quem te deu tal autoridade para fazer estas coisas?', 28, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus, respondendo, disse-lhes: Também eu vos perguntarei uma coisa, e respondei-me; e então vos direi com que autoridade faço estas coisas:', 29, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O batismo de João era do céu ou dos homens? respondei-me.', 30, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles arrazoavam entre si, dizendo: Se dissermos: Do céu, ele nos dirá: Então por que o não crestes?', 31, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se, porém, dissermos: Dos homens, tememos o povo.  Porque todos sustentavam que João verdadeiramente era profeta.', 32, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo, disseram a Jesus: Não sabemos.  E Jesus lhes replicou: Também eu vos não direi com que autoridade faço estas coisas.', 33, 11, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns dias depois entrou outra vez em Cafarnaum, e soube-se que estava em casa.', 1, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo se ajuntaram tantos, que nem ainda nos lugares junto à porta cabiam; e anunciava-lhes a palavra.', 2, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vieram ter com ele conduzindo um paralítico, trazido por quatro.', 3, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, não podendo aproximar-se dele, por causa da multidão, descobriram o telhado onde estava, e, fazendo um buraco, baixaram o leito em que jazia o paralítico.', 4, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, vendo a fé deles, disse ao paralítico: Filho, perdoados estão os teus pecados.', 5, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E estavam ali assentados alguns dos escribas, que arrazoavam em seus corações, dizendo:', 6, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por que diz este assim blasfêmias? Quem pode perdoar pecados, senão Deus?', 7, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, conhecendo logo em seu espírito que assim arrazoavam entre si, lhes disse: Por que arrazoais sobre estas coisas em vossos corações?', 8, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Qual é mais fácil? dizer ao paralítico: Estão perdoados os teus pecados; ou dizer-lhe: Levanta-te, e toma o teu leito, e anda?', 9, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, para que saibais que o Filho do homem tem na terra poder para perdoar pecados (disse ao paralítico),', 10, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A ti te digo: Levanta-te, toma o teu leito, e vai para tua casa.', 11, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levantou-se e, tomando logo o leito, saiu em presença de todos, de sorte que todos se admiraram e glorificaram a Deus, dizendo: Nunca tal vimos.', 12, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tornou a sair para o mar, e toda a multidão ia ter com ele, e ele os ensinava.', 13, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, passando, viu Levi, filho de Alfeu, sentado na alfândega, e disse-lhe: Segue-me.  E, levantando-se, o seguiu.', 14, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, estando sentado à mesa em casa deste, também estavam sentados à mesa com Jesus e seus discípulos muitos publicanos e pecadores; porque eram muitos, e o tinham seguido.', 15, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os escribas e fariseus, vendo-o comer com os publicanos e pecadores, disseram aos seus discípulos: Por que come e bebe ele com os publicanos e pecadores?', 16, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, tendo ouvido isto, disse-lhes: Os sãos não necessitam de médico, mas, sim, os que estão doentes; eu não vim chamar os justos, mas, sim, os pecadores ao arrependimento.', 17, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, os discípulos de João e os fariseus jejuavam; e foram e disseram-lhe: Por que jejuam os discípulos de João e os dos fariseus, e não jejuam os teus discípulos?', 18, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus disse-lhes: Podem porventura os filhos das bodas jejuar enquanto está com eles o esposo? Enquanto têm consigo o esposo, não podem jejuar;', 19, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas dias virão em que lhes será tirado o esposo, e então jejuarão naqueles dias.', 20, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ninguém deita remendo de pano novo em roupa velha; doutra sorte o mesmo remendo novo rompe o velho, e a rotura fica maior.', 21, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ninguém deita vinho novo em odres velhos; doutra sorte, o vinho novo rompe os odres e entorna-se o vinho, e os odres estragam-se; o vinho novo deve ser deitado em odres novos.', 22, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, passando ele num sábado pelas searas, os seus discípulos, caminhando, começaram a colher espigas.', 23, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os fariseus lhe disseram: Vês? Por que fazem no sábado o que não é lícito?', 24, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele disse-lhes: Nunca lestes o que fez Davi, quando estava em necessidade e teve fome, ele e os que com ele estavam?', 25, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como entrou na casa de Deus, no tempo de Abiatar, sumo sacerdote, e comeu os pães da proposição, dos quais não era lícito comer senão aos sacerdotes, dando também aos que com ele estavam?', 26, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: O sábado foi feito por causa do homem, e não o homem por causa do sábado.', 27, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim o Filho do homem até do sábado é Senhor.', 28, 2, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('NÃO repreendas asperamente o ancião, mas admoesta-o como a pai; aos moços como a irmãos;', 1, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('As mulheres idosas, como a mães, às moças, como a irmãs, em toda a pureza.', 2, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Honra as viúvas que verdadeiramente são viúvas.', 3, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se alguma viúva tiver filhos, ou netos, aprendam primeiro a exercer piedade para com a sua própria família, e a recompensar seus pais; porque isto é bom e agradável diante de Deus.', 4, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, a que é verdadeiramente viúva e desamparada espera em Deus, e persevera de noite e de dia em rogos e orações;', 5, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a que vive em deleites, vivendo está morta.', 6, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Manda, pois, estas coisas, para que elas sejam irrepreensíveis.', 7, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se alguém não tem cuidado dos seus, e principalmente dos da sua família, negou a fé, e é pior do que o infiel.', 8, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nunca seja inscrita viúva com menos de sessenta anos, e só a que tenha sido mulher de um só marido;', 9, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo testemunho de boas obras: Se criou os filhos, se exercitou hospitalidade, se lavou os pés aos santos, se socorreu os aflitos, se praticou toda a boa obra.', 10, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não admitas as viúvas mais novas, porque, quando se tornam levianas contra Cristo, querem casar-se;', 11, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo já a sua condenação por haverem aniquilado a primeira fé.', 12, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, além disto, aprendem também a andar ociosas de casa em casa; e não só ociosas, mas também paroleiras e curiosas, falando o que não convém.', 13, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quero, pois, que as que são moças se casem, gerem filhos, governem a casa, e não dêem ocasião ao adversário de maldizer;', 14, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque já algumas se desviaram, indo após Satanás.', 15, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se algum crente ou alguma crente tem viúvas, socorra-as, e não se sobrecarregue a igreja, para que se possam sustentar as que deveras são viúvas.', 16, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os presbíteros que governam bem sejam estimados por dignos de duplicada honra, principalmente os que trabalham na palavra e na doutrina;', 17, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque diz a Escritura: Não ligarás a boca ao boi que debulha. E: Digno é o obreiro do seu salário.', 18, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não aceites acusação contra o presbítero, senão com duas ou três testemunhas.', 19, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aos que pecarem, repreende-os na presença de todos, para que também os outros tenham temor.', 20, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Conjuro-te diante de Deus, e do Senhor Jesus Cristo, e dos anjos eleitos, que sem prevenção guardes estas coisas, nada fazendo por parcialidade.', 21, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A ninguém imponhas precipitadamente as mãos, nem participes dos pecados alheios; conserva-te a ti mesmo puro.', 22, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não bebas mais água só, mas usa de um pouco de vinho, por causa do teu estômago e das tuas freqüentes enfermidades.', 23, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os pecados de alguns homens são manifestos, precedendo o juízo; e em alguns manifestam-se depois.', 24, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim mesmo também as boas obras são manifestas, e as que são de outra maneira não podem ocultar-se.', 25, 5, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ajuntaram-se a ele os fariseus, e alguns dos escribas que tinham vindo de Jerusalém.', 1, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo que alguns dos seus discípulos comiam pão com as mãos impuras, isto é, por lavar, os repreendiam.', 2, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os fariseus, e todos os judeus, conservando a tradição dos antigos, não comem sem lavar as mãos muitas vezes;', 3, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando voltam do mercado, se não se lavarem, não comem.  E muitas outras coisas há que receberam para observar, como lavar os copos, e os jarros, e os vasos de metal e as camas.', 4, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois perguntaram-lhe os fariseus e os escribas: Por que não andam os teus discípulos conforme a tradição dos antigos, mas comem o pão com as mãos por lavar?', 5, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, respondendo, disse-lhes: Bem profetizou Isaías acerca de vós, hipócritas, como está escrito:Este povo honra-me com os lábios,Mas o seu coração está longe de mim;', 6, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em vão, porém, me honram,Ensinando doutrinas que são mandamentos de homens.', 7, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, deixando o mandamento de Deus, retendes a tradição dos homens; como o lavar dos jarros e dos copos; e fazeis muitas outras coisas semelhantes a estas.', 8, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia-lhes: Bem invalidais o mandamento de Deus para guardardes a vossa tradição.', 9, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Moisés disse: Honra a teu pai e a tua mãe; e quem maldisser, ou o pai ou a mãe, certamente morrerá.', 10, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós, porém, dizeis: Se um homem disser ao pai ou à mãe: Aquilo que poderias aproveitar de mim é Corbã, isto é, oferta ao Senhor;', 11, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nada mais lhe deixais fazer por seu pai ou por sua mãe,', 12, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Invalidando assim a palavra de Deus pela vossa tradição, que vós ordenastes.  E muitas coisas fazeis semelhantes a estas.', 13, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando outra vez a multidão, disse-lhes: Ouvi-me vós, todos, e compreendei.', 14, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nada há, fora do homem, que, entrando nele, o possa contaminar; mas o que sai dele isso é que contamina o homem.', 15, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém tem ouvidos para ouvir, ouça.', 16, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Depois, quando deixou a multidão, e entrou em casa, os seus discípulos o interrogavam acerca desta parábola.', 17, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse-lhes: Assim também vós estais sem entendimento? Não compreendeis que tudo o que de fora entra no homem não o pode contaminar,', 18, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não entra no seu coração, mas no ventre, e é lançado fora, ficando puras todas as comidas?', 19, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dizia: O que sai do homem isso contamina o homem.', 20, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque do interior do coração dos homens saem os maus pensamentos, os adultérios, as fornicações, os homicídios,', 21, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Os furtos, a avareza, as maldades, o engano, a dissolução, a inveja, a blasfêmia, a soberba, a loucura.', 22, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos estes males procedem de dentro e contaminam o homem.', 23, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se dali, foi para os termos de Tiro e de Sidom.  E, entrando numa casa, não queria que alguém o soubesse, mas não pôde esconder-se;', 24, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque uma mulher, cuja filha tinha um espírito imundo, ouvindo falar dele, foi e lançou-se aos seus pés.', 25, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E esta mulher era grega, siro-fenícia de nação, e rogava-lhe que expulsasse de sua filha o demônio.', 26, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus disse-lhe: Deixa primeiro saciar os filhos; porque não convém tomar o pão dos filhos e lançá-lo aos cachorrinhos.', 27, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ela, porém, respondeu, e disse-lhe: Sim, Senhor; mas também os cachorrinhos comem, debaixo da mesa, as migalhas dos filhos.', 28, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então ele disse-lhe: Por essa palavra, vai; o demônio já saiu de tua filha.', 29, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, indo ela para sua casa, achou a filha deitada sobre a cama, e que o demônio já tinha saído.', 30, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, tornando a sair dos termos de Tiro e de Sidom, foi até ao mar da Galiléia, pelos confins de Decápolis.', 31, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E trouxeram-lhe um surdo, que falava dificilmente; e rogaram-lhe que pusesse a mão sobre ele.', 32, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tirando-o à parte, de entre a multidão, pôs-lhe os dedos nos ouvidos; e, cuspindo, tocou-lhe na língua.', 33, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando os olhos ao céu, suspirou, e disse: Efatá; isto é, Abre-te.', 34, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo se abriram os seus ouvidos, e a prisão da língua se desfez, e falava perfeitamente.', 35, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ordenou-lhes que a ninguém o dissessem; mas, quanto mais lhos proibia, tanto mais o divulgavam.', 36, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, admirando-se sobremaneira, diziam: Tudo faz bem; faz ouvir os surdos e falar os mudos.', 37, 7, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se dali, foi para os termos da Judéia, além do Jordão, e a multidão se reuniu em torno dele; e tornou a ensiná-los, como tinha por costume.', 1, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, aproximando-se dele os fariseus, perguntaram-lhe, tentando-o: É lícito ao homem repudiar sua mulher?', 2, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, respondendo, disse-lhes: Que vos mandou Moisés?', 3, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles disseram: Moisés permitiu escrever carta de divórcio e repudiar.', 4, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhes: Pela dureza dos vossos corações vos deixou ele escrito esse mandamento;', 5, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porém, desde o princípio da criação, Deus os fez macho e fêmea.', 6, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Por isso deixará o homem a seu pai e a sua mãe, e unir-se-á a sua mulher,', 7, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E serão os dois uma só carne; e assim já não serão dois, mas uma só carne.', 8, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, o que Deus ajuntou não o separe o homem.', 9, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E em casa tornaram os discípulos a interrogá-lo acerca disto mesmo.', 10, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Qualquer que deixar a sua mulher e casar com outra, adultera contra ela.', 11, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se a mulher deixar a seu marido, e casar com outro, adultera.', 12, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E traziam-lhe meninos para que lhes tocasse, mas os discípulos repreendiam aos que lhos traziam.', 13, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, vendo isto, indignou-se, e disse-lhes: Deixai vir os meninos a mim, e não os impeçais; porque dos tais é o reino de Deus.', 14, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que qualquer que não receber o reino de Deus como menino, de maneira nenhuma entrará nele.', 15, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando-os nos seus braços, e impondo-lhes as mãos, os abençoou.', 16, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pondo-se a caminho, correu para ele um homem, o qual se ajoelhou diante dele, e lhe perguntou: Bom Mestre, que farei para herdar a vida eterna?', 17, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhe disse: Por que me chamas bom? Ninguém há bom senão um, que é Deus.', 18, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tu sabes os mandamentos: Não adulterarás; não matarás; não furtarás; não dirás falso testemunho; não defraudarás alguém; honra a teu pai e a tua mãe.', 19, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ele, porém, respondendo, lhe disse: Mestre, tudo isso guardei desde a minha mocidade.', 20, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, olhando para ele, o amou e lhe disse: Falta-te uma coisa: vai, vende tudo quanto tens, e dá-o aos pobres, e terás um tesouro no céu; e vem, toma a cruz, e segue-me.', 21, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, pesaroso desta palavra, retirou-se triste; porque possuía muitas propriedades.', 22, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus, olhando em redor, disse aos seus discípulos: Quão dificilmente entrarão no reino de Deus os que têm riquezas!', 23, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os discípulos se admiraram destas suas palavras; mas Jesus, tornando a falar, disse-lhes: Filhos, quão difícil é, para os que confiam nas riquezas, entrar no reino de Deus!', 24, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É mais fácil passar um camelo pelo fundo de uma agulha, do que entrar um rico no reino de Deus.', 25, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles se admiravam ainda mais, dizendo entre si: Quem poderá, pois, salvar-se?', 26, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, olhando para eles, disse: Para os homens é impossível, mas não para Deus, porque para Deus todas as coisas são possíveis.', 27, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro começou a dizer-lhe: Eis que nós tudo deixamos, e te seguimos.', 28, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse: Em verdade vos digo que ninguém há, que tenha deixado casa, ou irmãos, ou irmãs, ou pai, ou mãe, ou mulher, ou filhos, ou campos, por amor de mim e do evangelho,', 29, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que não receba cem vezes tanto, já neste tempo, em casas, e irmãos, e irmãs, e mães, e filhos, e campos, com perseguições; e no século futuro a vida eterna.', 30, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porém muitos primeiros serão derradeiros, e muitos derradeiros serão primeiros.', 31, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E iam no caminho, subindo para Jerusalém; e Jesus ia adiante deles.  E eles maravilhavam-se, e seguiam-no atemorizados.  E, tornando a tomar consigo os doze, começou a dizer-lhes as coisas que lhe deviam sobrevir,', 32, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Eis que nós subimos a Jerusalém, e o Filho do homem será entregue aos príncipes dos sacerdotes, e aos escribas, e o condenarão à morte, e o entregarão aos gentios.', 33, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o escarnecerão, e açoitarão, e cuspirão nele, e o matarão; e, ao terceiro dia, ressuscitará.', 34, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aproximaram-se dele Tiago e João, filhos de Zebedeu, dizendo: Mestre, queremos que nos faças o que te pedirmos.', 35, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Que quereis que vos faça?', 36, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles lhe disseram: Concede-nos que na tua glória nos assentemos, um à tua direita, e outro à tua esquerda.', 37, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus lhes disse: Não sabeis o que pedis; podeis vós beber o cálice que eu bebo, e ser batizados com o batismo com que eu sou batizado?', 38, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles lhe disseram: Podemos.  Jesus, porém, disse-lhes: Em verdade, vós bebereis o cálice que eu beber, e sereis batizados com o batismo com que eu sou batizado;', 39, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, o assentar-se à minha direita, ou à minha esquerda, não me pertence a mim concedê-lo, mas isso é para aqueles a quem está reservado.', 40, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os dez, tendo ouvido isto, começaram a indignar-se contra Tiago e João.', 41, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus, chamando-os a si, disse-lhes: Sabeis que os que julgam ser príncipes dos gentios, deles se assenhoreiam, e os seus grandes usam de autoridade sobre eles;', 42, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas entre vós não será assim; antes, qualquer que entre vós quiser ser grande, será vosso serviçal;', 43, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E qualquer que dentre vós quiser ser o primeiro, será servo de todos.', 44, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Filho do homem também não veio para ser servido, mas para servir e dar a sua vida em resgate de muitos.', 45, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E depois, foram para Jericó.  E, saindo ele de Jericó com seus discípulos e uma grande multidão, Bartimeu, o cego, filho de Timeu, estava assentado junto do caminho, mendigando.', 46, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo que era Jesus de Nazaré, começou a clamar, e a dizer: Jesus, filho de Davi, tem misericórdia de mim.', 47, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E muitos o repreendiam, para que se calasse; mas ele clamava cada vez mais: Filho de Davi! tem misericórdia de mim.', 48, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, parando, disse que o chamassem; e chamaram o cego, dizendo-lhe: Tem bom ânimo; levanta-te, que ele te chama.', 49, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele, lançando de si a sua capa, levantou-se, e foi ter com Jesus.', 50, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, falando, disse-lhe: Que queres que te faça? E o cego lhe disse: Mestre, que eu tenha vista.', 51, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhe disse: Vai, a tua fé te salvou.  E logo viu, e seguiu a Jesus pelo caminho.', 52, 10, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E dali a dois dias era a páscoa, e a festa dos pães ázimos; e os principais dos sacerdotes e os escribas buscavam como o prenderiam com dolo, e o matariam.', 1, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas eles diziam: Não na festa, para que porventura não se faça alvoroço entre o povo.', 2, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando ele em betânia, assentado à mesa, em casa de Simão, o leproso, veio uma mulher, que trazia um vaso de alabastro, com ungüento de nardo puro, de muito preço, e quebrando o vaso, lho derramou sobre a cabeça.', 3, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns houve que em si mesmos se indignaram, e disseram: Para que se fez este desperdício de ungüento?', 4, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque podia vender-se por mais de trezentos dinheiros, e dá-lo aos pobres.  E bramavam contra ela.', 5, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, disse: Deixai-a, por que a molestais? Ela fez-me boa obra.', 6, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque sempre tendes os pobres convosco, e podeis fazer-lhes bem, quando quiserdes; mas a mim nem sempre me tendes.', 7, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esta fez o que podia; antecipou-se a ungir o meu corpo para a sepultura.', 8, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que, em todas as partes do mundo onde este evangelho for pregado, também o que ela fez será contado para sua memória.', 9, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Judas Iscariotes, um dos doze, foi ter com os principais dos sacerdotes para lho entregar.', 10, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles, ouvindo-o, folgaram, e prometeram dar-lhe dinheiro; e buscava como o entregaria em ocasião oportuna.', 11, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, no primeiro dia dos pães ázimos, quando sacrificavam a páscoa, disseram-lhe os discípulos: Aonde queres que vamos fazer os preparativos para comer a páscoa?', 12, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E enviou dois dos seus discípulos, e disse-lhes: Ide à cidade, e um homem, que leva um cântaro de água, vos encontrará; segui-o.', 13, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, onde quer que entrar, dizei ao senhor da casa: O Mestre diz: Onde está o aposento em que hei de comer a páscoa com os meus discípulos?', 14, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele vos mostrará um grande cenáculo mobilado e preparado; preparai-a ali.', 15, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo os seus discípulos, foram à cidade, e acharam como lhes tinha dito, e prepararam a páscoa.', 16, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegada a tarde, foi com os doze.', 17, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando estavam assentados a comer, disse Jesus: Em verdade vos digo que um de vós, que comigo come, há de trair-me.', 18, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles começaram a entristecer-se e a dizer-lhe um após outro: Sou eu? E outro disse: Sou eu?', 19, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, respondendo, disse-lhes: É um dos doze, que põe comigo a mão no prato.', 20, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade o Filho do homem vai, como dele está escrito, mas ai daquele homem por quem o Filho do homem é traído! Bom seria para o tal homem não haver nascido.', 21, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, comendo eles, tomou Jesus pão e, abençoando-o, o partiu e deu-lho, e disse: Tomai, comei, isto é o meu corpo.', 22, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando o cálice, e dando graças, deu-lho; e todos beberam dele.', 23, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Isto é o meu sangue, o sangue do novo testamento, que por muitos é derramado.', 24, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que não beberei mais do fruto da vide, até àquele dia em que o beber, novo, no reino de Deus.', 25, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo cantado o hino, saíram para o Monte das Oliveiras.', 26, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes Jesus: Todos vós esta noite vos escandalizareis em mim; porque está escrito: Ferirei o pastor, e as ovelhas se dispersarão.', 27, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, depois que eu houver ressuscitado, irei adiante de vós para a Galiléia.', 28, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe Pedro: Ainda que todos se escandalizem, nunca, porém, eu.', 29, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhe Jesus: Em verdade te digo que hoje, nesta noite, antes que o galo cante duas vezes, três vezes me negarás.', 30, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele disse com mais veemência: Ainda que me seja necessário morrer contigo, de modo nenhum te negarei.  E da mesma maneira diziam todos também.', 31, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram a um lugar chamado Getsêmani, e disse aos seus discípulos: Assentai-vos aqui, enquanto eu oro.', 32, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tomou consigo a Pedro, e a Tiago, e a João, e começou a ter pavor, e a angustiar-se.', 33, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: A minha alma está profundamente triste até a morte; ficai aqui, e vigiai.', 34, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo ido um pouco mais adiante, prostrou-se em terra; e orou para que, se fosse possível, passasse dele aquela hora.', 35, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse: Aba, Pai, todas as coisas te são possíveis; afasta de mim este cálice; não seja, porém, o que eu quero, mas o que tu queres.', 36, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando, achou-os dormindo; e disse a Pedro: Simão, dormes? não podes vigiar uma hora?', 37, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vigiai e orai, para que não entreis em tentação; o espírito, na verdade, está pronto, mas a carne é fraca.', 38, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi outra vez e orou, dizendo as mesmas palavras.', 39, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, voltando, achou-os outra vez dormindo, porque os seus olhos estavam pesados, e não sabiam o que responder-lhe.', 40, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E voltou terceira vez, e disse-lhes: Dormi agora, e descansai.  Basta; é chegada a hora.  Eis que o Filho do homem vai ser entregue nas mãos dos pecadores.', 41, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Levantai-vos, vamos; eis que está perto o que me trai.', 42, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo, falando ele ainda, veio Judas, que era um dos doze, da parte dos principais dos sacerdotes, e dos escribas e dos anciãos, e com ele uma grande multidão com espadas e varapaus.', 43, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, o que o traía, tinha-lhes dado um sinal, dizendo: Aquele que eu beijar, esse é; prendei-o, e levai-o com segurança.', 44, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, logo que chegou, aproximou-se dele, e disse-lhe: Rabi, Rabi.  E beijou-o.', 45, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E lançaram-lhe as mãos, e o prenderam.', 46, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um dos que ali estavam presentes, puxando da espada, feriu o servo do sumo sacerdote, e cortou-lhe uma orelha.', 47, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo Jesus, disse-lhes: Saístes com espadas e varapaus a prender-me, como a um salteador?', 48, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos os dias estava convosco ensinando no templo, e não me prendestes; mas isto é para que as Escrituras se cumpram.', 49, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, deixando-o, todos fugiram.', 50, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E um certo jovem o seguia, envolto em um lençol sobre o corpo nu.  E lançaram-lhe a mão.', 51, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, largando o lençol, fugiu nu. Jesus perante o Sinédrio.  Negação de Pedro', 52, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E levaram Jesus ao sumo sacerdote, e ajuntaram-se todos os principais dos sacerdotes, e os anciãos e os escribas.', 53, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Pedro o seguiu de longe até dentro do pátio do sumo sacerdote, e estava assentado com os servidores, aquentando-se ao lume.', 54, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os principais dos sacerdotes e todo o concílio buscavam algum testemunho contra Jesus, para o matar, e não o achavam.', 55, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque muitos testificavam falsamente contra ele, mas os testemunhos não eram coerentes.', 56, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se alguns, testificaram falsamente contra ele, dizendo:', 57, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Nós ouvimos-lhe dizer: Eu derrubarei este templo, construído por mãos de homens, e em três dias edificarei outro, não feito por mãos de homens.', 58, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E nem assim o seu testemunho era coerente.', 59, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, levantando-se o sumo sacerdote no Sinédrio, perguntou a Jesus, dizendo: Nada respondes? Que testificam estes contra ti?', 60, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele calou-se, e nada respondeu.  O sumo sacerdote lhe tornou a perguntar, e disse-lhe: És tu o Cristo, Filho do Deus Bendito?', 61, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus disse-lhe: Eu o sou, e vereis o Filho do homem assentado à direita do poder de Deus, e vindo sobre as nuvens do céu.', 62, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o sumo sacerdote, rasgando as suas vestes, disse: Para que necessitamos de mais testemunhas?', 63, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós ouvistes a blasfêmia; que vos parece? E todos o consideraram culpado de morte.', 64, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E alguns começaram a cuspir nele, e a cobrir-lhe o rosto, e a dar-lhe punhadas, e a dizer-lhe: Profetiza.  E os servidores davam-lhe bofetadas.', 65, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando Pedro embaixo, noátrio, chegou uma das criadas do sumo sacerdote;', 66, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo a Pedro, que se estava aquentando, olhou para ele, e disse: Tu também estavas com Jesus, o Nazareno.', 67, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele negou-o, dizendo: Não o conheço, nem sei o que dizes.  E saiu fora ao alpendre, e o galo cantou.', 68, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a criada, vendo-o outra vez, começou a dizer aos que ali estavam: Este é um dos tais.', 69, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele o negou outra vez.  E pouco depois os que ali estavam disseram outra vez a Pedro: Verdadeiramente tu és um deles, porque és também galileu, e tua fala é semelhante.', 70, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele começou a praguejar, e a jurar: Não conheço esse homem de quem falais.', 71, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o galo cantou segunda vez.  E Pedro lembrou-se da palavra que Jesus lhe tinha dito: Antes que o galo cante duas vezes, três vezes me negarás.  E, retirando-se dali, chorou.', 72, 14, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ainda que eu falasse as línguas dos homens e dos anjos, e não tivesse amor, seria como o metal que soa ou como o sino que tine.', 1, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ainda que tivesse o dom de profecia, e conhecesse todos os mistérios e toda a ciência, e ainda que tivesse toda a fé, de maneira tal que transportasse os montes, e não tivesse amor, nada seria.', 2, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ainda que distribuísse toda a minha fortuna para sustento dos pobres, e ainda que entregasse o meu corpo para ser queimado, e não tivesse amor, nada disso me aproveitaria.', 3, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O amor é sofredor, é benigno; o amor não é invejoso; o amor não trata com leviandade, não se ensoberbece.', 4, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não se porta com indecência, não busca os seus interesses, não se irrita, não suspeita mal;', 5, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não folga com a injustiça, mas folga com a verdade;', 6, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tudo sofre, tudo crê, tudo espera, tudo suporta.', 7, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O amor nunca falha; mas havendo profecias, serão aniquiladas; havendo línguas, cessarão; havendo ciência, desaparecerá;', 8, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, em parte, conhecemos, e em parte profetizamos;', 9, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, quando vier o que é perfeito, então o que o é em parte será aniquilado.', 10, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando eu era menino, falava como menino, sentia como menino, discorria como menino, mas, logo que cheguei a ser homem, acabei com as coisas de menino.', 11, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque agora vemos por espelho em enigma, mas então veremos face a face; agora conheço em parte, mas então conhecerei como também sou conhecido.', 12, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Agora, pois, permanecem a fé, a esperança e o amor, estes três, mas o maior destes é o amor.', 13, 13, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E começou a falar-lhes por parábolas: Um homem plantou uma vinha, e cercou-a de um valado, e fundou nela um lagar, e edificou uma torre, e arrendou-a a uns lavradores, e partiu para fora da terra.', 1, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegado o tempo, mandou um servo aos lavradores para que recebesse, dos lavradores, do fruto da vinha.', 2, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas estes, apoderando-se dele, o feriram e o mandaram embora vazio.', 3, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tornou a enviar-lhes outro servo; e eles, apedrejando-o, o feriram na cabeça, e o mandaram embora, tendo-o afrontado.', 4, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tornou a enviar-lhes outro, e a este mataram; e a outros muitos, dos quais a uns feriram e a outros mataram.', 5, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo ele, pois, ainda um seu filho amado, enviou-o também a estes por derradeiro, dizendo: Ao menos terão respeito ao meu filho.', 6, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas aqueles lavradores disseram entre si: Este é o herdeiro; vamos, matemo-lo, e a herança será nossa.', 7, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, pegando dele, o mataram, e o lançaram fora da vinha.', 8, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que fará, pois, o senhor da vinha? Virá, e destruirá os lavradores, e dará a vinha a outros.', 9, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ainda não lestes esta Escritura:A pedra, que os edificadores rejeitaram,Esta foi posta por cabeça de esquina;', 10, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Isto foi feito pelo Senhor E é coisa maravilhosa aos nossos olhos?', 11, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E buscavam prendê-lo, mas temiam a multidão; porque entendiam que contra eles dizia esta parábola; e, deixando-o, foram-se.', 12, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E enviaram-lhe alguns dos fariseus e dos herodianos, para que o apanhassem nalguma palavra.', 13, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando eles, disseram-lhe: Mestre, sabemos que és homem de verdade, e de ninguém se te dá, porque não olhas à aparência dos homens, antes com verdade ensinas o caminho de Deus; é lícito dar o tributo a César, ou não? Daremos, ou não daremos?', 14, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então ele, conhecendo a sua hipocrisia, disse-lhes: Por que me tentais? Trazei-me uma moeda, para que a veja.', 15, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles lha trouxeram.  E disse-lhes: De quem é esta imagem e inscrição? E eles lhe disseram: De César.', 16, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhes: Dai pois a César o que é de César, e a Deus o que é de Deus.  E maravilharam-se dele.', 17, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então os saduceus, que dizem que não há ressurreição, aproximaram-se dele, e perguntaram-lhe, dizendo:', 18, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mestre, Moisés nos escreveu que, se morresse o irmão de alguém, e deixasse a mulher e não deixasse filhos, seu irmão tomasse a mulher dele, e suscitasse descendência a seu irmão.', 19, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, havia sete irmãos, e o primeiro tomou a mulher, e morreu sem deixar descendência;', 20, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o segundo também a tomou e morreu, e nem este deixou descendência; e o terceiro da mesma maneira.', 21, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E tomaram-na os sete, sem, contudo, terem deixado descendência.  Finalmente, depois de todos, morreu também a mulher.', 22, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na ressurreição, pois, quando ressuscitarem, de qual destes será a mulher? porque os sete a tiveram por mulher.', 23, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo, disse-lhes: Porventura não errais vós em razão de não saberdes as Escrituras nem o poder de Deus?', 24, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porquanto, quando ressuscitarem dentre os mortos, nem casarão, nem se darão em casamento, mas serão como os anjos que estão nos céus.', 25, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, acerca dos mortos que houverem de ressuscitar, não tendes lido no livro de Moisés como Deus lhe falou na sarça, dizendo: Eu sou o Deus de Abraão, e o Deus de Isaque, e o Deus de Jacó?', 26, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, Deus não é de mortos, mas sim, é Deus de vivos.  Por isso vós errais muito.', 27, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aproximou-se dele um dos escribas que os tinha ouvido disputar, e sabendo que lhes tinha respondido bem, perguntou-lhe: Qual é o primeiro de todos os mandamentos?', 28, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus respondeu-lhe: O primeiro de todos os mandamentos é: Ouve, Israel, o Senhor nosso Deus é o único Senhor.', 29, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Amarás, pois, ao Senhor teu Deus de todo o teu coração, e de toda a tua alma, e de todo o teu entendimento, e de todas as tuas forças; este é o primeiro mandamento.', 30, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o segundo, semelhante a este, é: Amarás o teu próximo como a ti mesmo.  Não há outro mandamento maior do que estes.', 31, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o escriba lhe disse: Muito bem, Mestre, e com verdade disseste que há um só Deus, e que não há outro além dele;', 32, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E que amá-lo de todo o coração, e de todo o entendimento, e de toda a alma, e de todas as forças, e amar o próximo como a si mesmo, é mais do que todos os holocaustos e sacrifícios.', 33, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, vendo que havia respondido sabiamente, disse-lhe: Não estás longe do reino de Deus.  E já ninguém ousava perguntar-lhe mais nada.', 34, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, falando Jesus, dizia, ensinando no templo: Como dizem os escribas que o Cristo é filho de Davi?', 35, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O próprio Davi disse pelo Espírito Santo:O Senhor disse ao meu Senhor:Assenta-te à minha direita Até que eu ponha os teus inimigos por escabelo dos teus pés.', 36, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois, se Davi mesmo lhe chama Senhor, como é logo seu filho? E a grande multidão o ouvia de boa vontade.', 37, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ensinando-os, dizia-lhes: Guardai-vos dos escribas, que gostam de andar com vestes compridas, e das saudações nas praças,', 38, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E das primeiras cadeiras nas sinagogas, e dos primeiros assentos nas ceias;', 39, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que devoram as casas das viúvas, e isso com pretexto de largas orações.  Estes receberão mais grave condenação.', 40, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, estando Jesus assentado defronte da arca do tesouro, observava a maneira como a multidão lançava o dinheiro na arca do tesouro; e muitos ricos deitavam muito.', 41, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vindo, porém, uma pobre viúva, deitou duas pequenas moedas, que valiam meio centavo.', 42, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chamando os seus discípulos, disse-lhes: Em verdade vos digo que esta pobre viúva deitou mais do que todos os que deitaram na arca do tesouro;', 43, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todos ali deitaram do que lhes sobejava, mas esta, da sua pobreza, deitou tudo o que tinha, todo o seu sustento.', 44, 12, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Acerca dos dons espirituais, não quero, irmãos, que sejais ignorantes.', 1, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vós bem sabeis que éreis gentios, levados aos ídolos mudos, conforme éreis guiados.', 2, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, vos quero fazer compreender que ninguém que fala pelo Espírito de Deus diz: Jesus é anátema, e ninguém pode dizer que Jesus é o Senhor, senão pelo Espírito Santo.', 3, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, há diversidade de dons, mas o Espírito é o mesmo.', 4, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E há diversidade de ministérios, mas o Senhor é o mesmo.', 5, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E há diversidade de operações, mas é o mesmo Deus que opera tudo em todos.', 6, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas a manifestação do Espírito é dada a cada um, para o que for útil.', 7, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a um pelo Espírito é dada a palavra da sabedoria; e a outro, pelo mesmo Espírito, a palavra da ciência;', 8, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a outro, pelo mesmo Espírito, a fé; e a outro, pelo mesmo Espírito, os dons de curar;', 9, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a outro a operação de maravilhas; e a outro a profecia; e a outro o dom de discernir os espíritos; e a outro a variedade de línguas; e a outro a interpretação das línguas.', 10, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas um só e o mesmo Espírito opera todas estas coisas, repartindo particularmente a cada um como quer.', 11, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, assim como o corpo é um, e tem muitos membros, e todos os membros, sendo muitos, são um só corpo, assim é Cristo também.', 12, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois todos nós fomos batizados em um Espírito, formando um corpo, quer judeus, quer gregos, quer servos, quer livres, e todos temos bebido de um Espírito.', 13, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque também o corpo não é um só membro, mas muitos.', 14, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se o pé disser: Porque não sou mão, não sou do corpo; não será por isso do corpo?', 15, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E se a orelha disser: Porque não sou olho não sou do corpo; não será por isso do corpo?', 16, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se todo o corpo fosse olho, onde estaria o ouvido? Se todo fosse ouvido, onde estaria o olfato?', 17, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas agora Deus colocou os membros no corpo, cada um deles como quis.', 18, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se todos fossem um só membro, onde estaria o corpo?', 19, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim, pois, há muitos membros, mas um corpo.', 20, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o olho não pode dizer à mão: Não tenho necessidade de ti; nem ainda a cabeça aos pés: Não tenho necessidade de vós.', 21, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, os membros do corpo que parecem ser os mais fracos são necessários;', 22, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que reputamos serem menos honrosos no corpo, a esses honramos muito mais; e aos que em nós são menos decorosos damos muito mais honra.', 23, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque os que em nós são mais nobres não têm necessidade disso, mas Deus assim formou o corpo, dando muito mais honra ao que tinha falta dela;', 24, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que não haja divisão no corpo, mas antes tenham os membros igual cuidado uns dos outros.', 25, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('De maneira que, se um membro padece, todos os membros padecem com ele; e, se um membro é honrado, todos os membros se regozijam com ele.', 26, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, vós sois o corpo de Cristo, e seus membros em particular.', 27, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a uns pôs Deus na igreja, primeiramente apóstolos, em segundo lugar profetas, em terceiro doutores, depois milagres, depois dons de curar, socorros, governos, variedades de línguas.', 28, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porventura são todos apóstolos? são todos profetas? são todos doutores? são todos operadores de milagres?', 29, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Têm todos o dom de curar? falam todos diversas línguas? interpretam todos?', 30, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, procurai com zelo os melhores dons; e eu vos mostrarei um caminho mais excelente.', 31, 12, 7);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E contou-lhes também uma parábola sobre o dever de orar sempre, e nunca desfalecer,', 1, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Havia numa cidade um certo juiz, que nem a Deus temia, nem respeitava o homem.', 2, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Havia também, naquela mesma cidade, uma certa viúva, que ia ter com ele, dizendo: Faze-me justiça contra o meu adversário.', 3, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por algum tempo não quis atendê-la; mas depois disse consigo: Ainda que não temo a Deus, nem respeito os homens,', 4, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todavia, como esta viúva me molesta, hei de fazer-lhe justiça, para que enfim não volte, e me importune muito.', 5, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse o Senhor: Ouvi o que diz o injusto juiz.', 6, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Deus não fará justiça aos seus escolhidos, que clamam a ele de dia e de noite, ainda que tardio para com eles?', 7, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo-vos que depressa lhes fará justiça. Quando porém vier o Filho do homem, porventura achará fé na terra?', 8, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse também esta parábola a uns que confiavam em si mesmos, crendo que eram justos, e desprezavam os outros:', 9, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dois homens subiram ao templo, para orar; um, fariseu, e o outro, publicano.', 10, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O fariseu, estando em pé, orava consigo desta maneira: Ó Deus, graças te dou porque não sou como os demais homens, roubadores, injustos e adúlteros; nem ainda como este publicano.', 11, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jejuo duas vezes na semana, e dou os dízimos de tudo quanto possuo.', 12, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O publicano, porém, estando em pé, de longe, nem ainda queria levantar os olhos ao céu, mas batia no peito, dizendo: Ó Deus, tem misericórdia de mim, pecador!', 13, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Digo-vos que este desceu justificado para sua casa, e não aquele; porque qualquer que a si mesmo se exalta será humilhado, e qualquer que a si mesmo se humilha será exaltado.', 14, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E traziam-lhe também meninos, para que ele lhes tocasse; e os discípulos, vendo isto, repreendiam-nos.', 15, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Jesus, chamando-os para si, disse: Deixai vir a mim os meninos, e não os impeçais, porque dos tais é o reino de Deus.', 16, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Em verdade vos digo que, qualquer que não receber o reino de Deus como menino, não entrará nele.', 17, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntou-lhe um certo príncipe, dizendo: Bom Mestre, que hei de fazer para herdar a vida eterna?', 18, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus lhe disse: Por que me chamas bom? Ninguém há bom, senão um, que é Deus.', 19, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Sabes os mandamentos: Não adulterarás, não matarás, não furtarás, não dirás falso testemunho, honra a teu pai e a tua mãe.', 20, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse ele: Todas essas coisas tenho observado desde a minha mocidade.', 21, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E quando Jesus ouviu isto, disse-lhe: Ainda te falta uma coisa; vende tudo quanto tens, reparte-o pelos pobres, e terás um tesouro no céu; vem, e segue-me.', 22, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, ouvindo ele isto, ficou muito triste, porque era muito rico.', 23, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, vendo Jesus que ele ficara muito triste, disse: Quão dificilmente entrarão no reino de Deus os que têm riquezas!', 24, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque é mais fácil entrar um camelo pelo fundo de uma agulha do que entrar um rico no reino de Deus.', 25, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que ouviram isto disseram: Logo quem pode salvar-se?', 26, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele respondeu: As coisas que são impossíveis aos homens são possíveis a Deus.', 27, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Pedro: Eis que nós deixamos tudo e te seguimos.', 28, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhes disse: Na verdade vos digo que ninguém há, que tenha deixado casa, ou pais, ou irmãos, ou mulher, ou filhos, pelo reino de Deus,', 29, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que não haja de receber muito mais neste mundo, e na idade vindoura a vida eterna.', 30, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tomando consigo os doze, disse-lhes: Eis que subimos a Jerusalém, e se cumprirá no Filho do homem tudo o que pelos profetas foi escrito;', 31, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois há de ser entregue aos gentios, e escarnecido, injuriado e cuspido;', 32, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, havendo-o açoitado, o matarão; e ao terceiro dia ressuscitará.', 33, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eles nada disto entendiam, e esta palavra lhes era encoberta, não percebendo o que se lhes dizia.', 34, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que chegando ele perto de Jericó, estava um cego assentado junto do caminho, mendigando.', 35, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ouvindo passar a multidão, perguntou que era aquilo.', 36, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disseram-lhe que Jesus Nazareno passava.', 37, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então clamou, dizendo: Jesus, Filho de Davi, tem misericórdia de mim.', 38, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que iam passando repreendiam-no para que se calasse; mas ele clamava ainda mais: Filho de Davi, tem misericórdia de mim!', 39, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então Jesus, parando, mandou que lho trouxessem; e, chegando ele, perguntou-lhe,', 40, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo: Que queres que te faça? E ele disse: Senhor, que eu veja.', 41, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus lhe disse: Vê; a tua fé te salvou.', 42, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E logo viu, e seguia-o, glorificando a Deus. E todo o povo, vendo isto, dava louvores a Deus.', 43, 18, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Todos os servos que estão debaixo do jugo estimem a seus senhores por dignos de toda a honra, para que o nome de Deus e a doutrina não sejam blasfemados.', 1, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que têm senhores crentes não os desprezem, por serem irmãos; antes os sirvam melhor, porque eles, que participam do benefício, são crentes e amados. Isto ensina e exorta.', 2, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Se alguém ensina alguma outra doutrina, e se não conforma com as sãs palavras de nosso Senhor Jesus Cristo, e com a doutrina que é segundo a piedade,', 3, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É soberbo, e nada sabe, mas delira acerca de questões e contendas de palavras, das quais nascem invejas, porfias, blasfêmias, ruins suspeitas,', 4, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Perversas contendas de homens corruptos de entendimento, e privados da verdade, cuidando que a piedade seja causa de ganho; aparta-te dos tais.', 5, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas é grande ganho a piedade com contentamento.', 6, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nada trouxemos para este mundo, e manifesto é que nada podemos levar dele.', 7, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo, porém, sustento, e com que nos cobrirmos, estejamos com isso contentes.', 8, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas os que querem ser ricos caem em tentação, e em laço, e em muitas concupiscências loucas e nocivas, que submergem os homens na perdição e ruína.', 9, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o amor ao dinheiro é a raiz de toda a espécie de males; e nessa cobiça alguns se desviaram da fé, e se traspassaram a si mesmos com muitas dores.', 10, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas tu, ó homem de Deus, foge destas coisas, e segue a justiça, a piedade, a fé, o amor, a paciência, a mansidão.', 11, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Milita a boa milícia da fé, toma posse da vida eterna, para a qual também foste chamado, tendo já feito boa confissão diante de muitas testemunhas.', 12, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mando-te diante de Deus, que todas as coisas vivifica, e de Cristo Jesus, que diante de Pôncio Pilatos deu o testemunho de boa confissão,', 13, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que guardes este mandamento sem mácula e repreensão, até à aparição de nosso Senhor Jesus Cristo;', 14, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A qual a seu tempo mostrará o bem-aventurado, e único poderoso Senhor, Rei dos reis e Senhor dos senhores;', 15, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aquele que tem, ele só, a imortalidade, e habita na luz inacessível; a quem nenhum dos homens viu nem pode ver, ao qual seja honra e poder sempiterno. Amém.', 16, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Manda aos ricos deste mundo que não sejam altivos, nem ponham a esperança na incerteza das riquezas, mas em Deus, que abundantemente nos dá todas as coisas para delas gozarmos;', 17, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que façam bem, enriqueçam em boas obras, repartam de boa mente, e sejam comunicáveis;', 18, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que entesourem para si mesmos um bom fundamento para o futuro, para que possam se apoderar da vida eterna.', 19, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ó Timóteo, guarda o depósito que te foi confiado, tendo horror aos clamores vãos e profanos e às oposições da falsamente chamada ciência,', 20, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('A qual professando-a alguns, se desviaram da fé. A graça seja contigo. Amém.', 21, 6, 15);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Tendo sido, pois, justificados pela fé, temos paz com Deus, por nosso Senhor Jesus Cristo;', 1, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pelo qual também temos entrada pela fé a esta graça, na qual estamos firmes, e nos gloriamos na esperança da glória de Deus.', 2, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não somente isto, mas também nos gloriamos nas tribulações; sabendo que a tribulação produz a paciência,', 3, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a paciência a experiência, e a experiência a esperança.', 4, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a esperança não traz confusão, porquanto o amor de Deus está derramado em nossos corações pelo Espírito Santo que nos foi dado.', 5, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque Cristo, estando nós ainda fracos, morreu a seu tempo pelos ímpios.', 6, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque apenas alguém morrerá por um justo; pois poderá ser que pelo bom alguém ouse morrer.', 7, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas Deus prova o seu amor para conosco, em que Cristo morreu por nós, sendo nós ainda pecadores.', 8, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Logo muito mais agora, tendo sido justificados pelo seu sangue, seremos por ele salvos da ira.', 9, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque se nós, sendo inimigos, fomos reconciliados com Deus pela morte de seu Filho, muito mais, tendo sido já reconciliados, seremos salvos pela sua vida.', 10, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não somente isto, mas também nos gloriamos em Deus por nosso Senhor Jesus Cristo, pelo qual agora alcançamos a reconciliação.', 11, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Portanto, como por um homem entrou o pecado no mundo, e pelo pecado a morte, assim também a morte passou a todos os homens por isso que todos pecaram.', 12, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque até à lei estava o pecado no mundo, mas o pecado não é imputado, não havendo lei.', 13, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('No entanto, a morte reinou desde Adão até Moisés, até sobre aqueles que não tinham pecado à semelhança da transgressão de Adão, o qual é a figura daquele que havia de vir.', 14, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas não é assim o dom gratuito como a ofensa. Porque, se pela ofensa de um morreram muitos, muito mais a graça de Deus, e o dom pela graça, que é de um só homem, Jesus Cristo, abundou sobre muitos.', 15, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E não foi assim o dom como a ofensa, por um só que pecou. Porque o juízo veio de uma só ofensa, na verdade, para condenação, mas o dom gratuito veio de muitas ofensas para justificação.', 16, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, se pela ofensa de um só, a morte reinou por esse, muito mais os que recebem a abundância da graça, e do dom da justiça, reinarão em vida por um só, Jesus Cristo.', 17, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Pois assim como por uma só ofensa veio o juízo sobre todos os homens para condenação, assim também por um só ato de justiça veio a graça sobre todos os homens para justificação de vida.', 18, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, como pela desobediência de um só homem, muitos foram feitos pecadores, assim pela obediência de um muitos serão feitos justos.', 19, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Veio, porém, a lei para que a ofensa abundasse; mas, onde o pecado abundou, superabundou a graça;', 20, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que, assim como o pecado reinou na morte, também a graça reinasse pela justiça para a vida eterna, por Jesus Cristo nosso Senhor.', 21, 5, 6);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, saindo ele do templo, disse-lhe um dos seus discípulos: Mestre, olha que pedras, e que edifícios!', 1, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, respondendo Jesus, disse-lhe: Vês estes grandes edifícios? Não ficará pedra sobre pedra que não seja derrubada.', 2, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, assentando-se ele no Monte das Oliveiras, defronte do templo, Pedro, e Tiago, e João e André lhe perguntaram em particular:', 3, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dize-nos, quando serão essas coisas, e que sinal haverá quando todas elas estiverem para se cumprir.', 4, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Jesus, respondendo-lhes, começou a dizer: Olhai que ninguém vos engane;', 5, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque muitos virão em meu nome, dizendo: Eu sou o Cristo; e enganarão a muitos.', 6, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando ouvirdes de guerras e de rumores de guerras, não vos perturbeis; porque assim deve acontecer; mas ainda não será o fim.', 7, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque se levantará nação contra nação, e reino contra reino, e haverá terremotos em diversos lugares, e haverá fomes e tribulações.  Estas coisas sào os princípios das dores.', 8, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas olhai por vós mesmos, porque vos entregarão aos concílios e às sinagogas; e sereis açoitados, e sereis apresentados perante presidentes e reis, por amor de mim, para lhes servir de testemunho.', 9, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas importa que o evangelho seja primeiramente pregado entre todas as nações.', 10, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Quando, pois, vos conduzirem e vos entregarem, não estejais solícitos de antemão pelo que haveis de dizer, nem premediteis; mas, o que vos for dado naquela hora, isso falai, porque não sois vós os que falais, mas o Espírito Santo.', 11, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o irmão entregará à morte o irmão, e o pai ao filho; e levantar-se-ão os filhos contra os pais, e os farão morrer.', 12, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E sereis odiados por todos por amor do meu nome; mas quem perseverar até ao fim, esse será salvo.', 13, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, quando vós virdes a abominação do assolamento, que foi predita por Daniel o profeta, estar onde não deve estar (quem lê, entenda), então os que estiverem na Judéia fujam para os montes.', 14, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que estiver sobre o telhado não desça para casa, nem entre a tomar coisa alguma de sua casa;', 15, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o que estiver no campo não volte atrás, para tomar as suas vestes.', 16, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ai das grávidas, e das que criarem naqueles dias!', 17, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Orai, pois, para que a vossa fuga não suceda no inverno.', 18, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque naqueles dias haverá uma aflição tal, qual nunca houve desde o princípio da criação, que Deus criou, até agora, nem jamais haverá.', 19, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, se o Senhor não abreviasse aqueles dias, nenhuma carne se salvaria; mas, por causa dos eleitosque escolheu, abreviou aqueles dias.', 20, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E então, se alguém vos disser: Eis aqui o Cristo; ou: Ei-lo ali; não acrediteis.', 21, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque se levantarão falsos cristos, e falsos profetas, e farão sinais e prodígios, para enganarem, se for possível, até os escolhidos.', 22, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas vós vede; eis que de antemão vos tenho dito tudo.', 23, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ora, naqueles dias, depois daquela aflição, o sol se escurecerá, e a lua não dará a sua luz.', 24, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as estrelas cairão do céu, e as forças que estão nos céus serão abaladas.', 25, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E então verão vir o Filho do homem nas nuvens, com grande poder e glória.', 26, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele enviará os seus anjos, e ajuntará os seus escolhidos, desde os quatro ventos, da extremidade da terra até a extremidade do céu.', 27, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aprendei, pois, a parábola da figueira: Quando já o seu ramo se torna tenro, e brota folhas, bem sabeis que já está próximo o verão.', 28, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Assim também vós, quando virdes sucederem estas coisas, sabei que já está perto, às portas.', 29, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Na verdade vos digo que não passará esta geração, sem que todas estas coisas aconteçam.', 30, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Passará o céu e a terra, mas as minhas palavras não passarão.', 31, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas daquele dia e hora ninguém sabe, nem os anjos que estão no céu, nem o Filho, senão o Pai.', 32, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Olhai, vigiai e orai; porque não sabeis quando chegará o tempo.', 33, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('É como se um homem, partindo para fora da terra, deixasse a sua casa, e desse autoridade aos seus servos, e a cada um a sua obra, e mandasse ao porteiro que vigiasse.', 34, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vigiai, pois, porque não sabeis quando virá o senhor da casa; se à tarde, se à meia-noite, se ao cantar do galo, se pela manhã,', 35, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Para que, vindo de improviso, não vos ache dormindo.', 36, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E as coisas que vos digo, digo-as a todos: Vigiai.', 37, 13, 2);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Paulo, apóstolo de Jesus Cristo, pela vontade de Deus, e o irmão Timóteo, à igreja de Deus, que está em Corinto, com todos os santos que estão em toda a Acaia.', 1, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Graça a vós e paz da parte de Deus nosso Pai, e da do Senhor Jesus Cristo.', 2, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Bendito seja o Deus e Pai de nosso Senhor Jesus Cristo, o Pai das misericórdias e o Deus de toda a consolação;', 3, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Que nos consola em toda a nossa tribulação, para que também possamos consolar os que estiverem em alguma tribulação, com a consolação com que nós mesmos somos consolados por Deus.', 4, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque, como as aflições de Cristo são abundantes em nós, assim também é abundante a nossa consolação por meio de Cristo.', 5, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, se somos atribulados, é para vossa consolação e salvação; ou, se somos consolados, para vossa consolação é, a qual se opera suportando com paciência as mesmas aflições que nós também padecemos;', 6, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a nossa esperança acerca de vós é firme, sabendo que, como sois participantes das aflições, assim o sereis também da consolação.', 7, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não queremos, irmãos, que ignoreis a tribulação que nos sobreveio na Ásia, pois que fomos sobremaneira agravados mais do que podíamos suportar, de modo tal que até da vida desesperamos.', 8, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas já em nós mesmos tínha- mos a sentença de morte, para que não confiássemos em nós, mas em Deus, que ressuscita os mortos;', 9, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual nos livrou de tão grande morte, e livra; em quem esperamos que também nos livrará ainda,', 10, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Ajudando-nos também vós com orações por nós, para que pela mercê, que por muitas pessoas nos foi feita, por muitas também sejam dadas graças a nosso respeito.', 11, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque a nossa glória é esta: o testemunho da nossa consciência, de que com simplicidade e sinceridade de Deus, não com sabedoria carnal, mas na graça de Deus, temos vivido no mundo, e de modo particular convosco.', 12, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque nenhumas outras coisas vos escrevemos, senão as que já sabeis ou também reconheceis; e espero que também até ao fim as reconhecereis.', 13, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Como também já em parte reconhecestes em nós, que somos a vossa glória, como também vós sereis a nossa no dia do Senhor Jesus.', 14, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E com esta confiança quis primeiro ir ter convosco, para que tivésseis uma segunda graça;', 15, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E por vós passar à macedônia, e da macedônia ir outra vez ter convosco, e ser guiado por vós à Judéia.', 16, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, deliberando isto, usei porventura de leviandade? Ou o que delibero, o delibero segundo a carne, para que haja em mim sim, sim, e não, não?', 17, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Antes, como Deus é fiel, a nossa palavra para convosco não foi sim e não.', 18, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque o Filho de Deus, Jesus Cristo, que entre vós foi pregado por nós, isto é, por mim, Silvano e Timóteo, não foi sim e não; mas nele houve sim.', 19, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque todas quantas promessas há de Deus, são nele sim, e por ele o Amém, para glória de Deus por nós.', 20, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas o que nos confirma convosco em Cristo, e o que nos ungiu, é Deus,', 21, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('O qual também nos selou e deu o penhor do Espírito em nossos corações.', 22, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Invoco, porém, a Deus por testemunha sobre a minha alma, que para vos poupar não tenho até agora ido a Corinto;', 23, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Não que tenhamos domínio sobre a vossa fé, mas porque somos cooperadores de vosso gozo; porque pela fé estais em pé.', 24, 1, 8);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E olhei, e eis que estava o Cordeiro sobre o monte Sião, e com ele cento e quarenta e quatro mil, que em suas testas tinham escrito o nome de seu Pai.', 1, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi uma voz do céu, como a voz de muitas águas, e como a voz de um grande trovão; e ouvi uma voz de harpistas, que tocavam com as suas harpas.', 2, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E cantavam um como cântico novo diante do trono, e diante dos quatro animais e dos anciãos; e ninguém podia aprender aquele cântico, senão os cento e quarenta e quatro mil que foram comprados da terra.', 3, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estes são os que não estão contaminados com mulheres; porque são virgens. Estes são os que seguem o Cordeiro para onde quer que vá. Estes são os que dentre os homens foram comprados como primícias para Deus e para o Cordeiro.', 4, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E na sua boca não se achou engano; porque são irrepreensíveis diante do trono de Deus.', 5, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E vi outro anjo voar pelo meio do céu, e tinha o evangelho eterno, para o proclamar aos que habitam sobre a terra, e a toda a nação, e tribo, e língua, e povo,', 6, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Dizendo com grande voz: Temei a Deus, e dai-lhe glória; porque é vinda a hora do seu juízo. E adorai aquele que fez o céu, e a terra, e o mar, e as fontes das águas.', 7, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outro anjo seguiu, dizendo: Caiu, caiu Babilônia, aquela grande cidade, que a todas as nações deu a beber do vinho da ira da sua fornicação.', 8, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E seguiu-os o terceiro anjo, dizendo com grande voz: Se alguém adorar a besta, e a sua imagem, e receber o sinal na sua testa, ou na sua mão,', 9, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Também este beberá do vinho da ira de Deus, que se deitou, não misturado, no cálice da sua ira; e será atormentado com fogo e enxofre diante dos santos anjos e diante do Cordeiro.', 10, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a fumaça do seu tormento sobe para todo o sempre; e não têm repouso nem de dia nem de noite os que adoram a besta e a sua imagem, e aquele que receber o sinal do seu nome.', 11, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Aqui está a paciência dos santos; aqui estão os que guardam os mandamentos de Deus e a fé em Jesus.', 12, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ouvi uma voz do céu, que me dizia: Escreve: Bem-aventurados os mortos que desde agora morrem no Senhor. Sim, diz o Espírito, para que descansem dos seus trabalhos, e as suas obras os seguem.', 13, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E olhei, e eis uma nuvem branca, e assentado sobre a nuvem um semelhante ao Filho do homem, que tinha sobre a sua cabeça uma coroa de ouro, e na sua mão uma foice aguda.', 14, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outro anjo saiu do templo, clamando com grande voz ao que estava assentado sobre a nuvem: Lança a tua foice, e sega; a hora de segar te é vinda, porque já a seara da terra está madura.', 15, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele que estava assentado sobre a nuvem meteu a sua foice à terra, e a terra foi segada.', 16, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E saiu do templo, que está no céu, outro anjo, o qual também tinha uma foice aguda.', 17, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E saiu do altar outro anjo, que tinha poder sobre o fogo, e clamou com grande voz ao que tinha a foice aguda, dizendo: Lança a tua foice aguda, e vindima os cachos da vinha da terra, porque já as suas uvas estão maduras.', 18, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o anjo lançou a sua foice à terra e vindimou as uvas da vinha da terra, e atirou-as no grande lagar da ira de Deus.', 19, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o lagar foi pisado fora da cidade, e saiu sangue do lagar até aos freios dos cavalos, pelo espaço de mil e seiscentos estádios.', 20, 14, 27);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu, depois disto, que andava de cidade em cidade, e de aldeia em aldeia, pregando e anunciando o evangelho do reino de Deus; e os doze iam com ele,', 1, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E algumas mulheres que haviam sido curadas de espíritos malignos e de enfermidades: Maria, chamada Madalena, da qual saíram sete demônios;', 2, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E Joana, mulher de Cuza, procurador de Herodes, e Suzana, e muitas outras que o serviam com seus bens.', 3, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, ajuntando-se uma grande multidão, e vindo de todas as cidades ter com ele, disse por parábola:', 4, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Um semeador saiu a semear a sua semente e, quando semeava, caiu alguma junto do caminho, e foi pisada, e as aves do céu a comeram;', 5, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra caiu sobre pedra e, nascida, secou-se, pois que não tinha umidade;', 6, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra caiu entre espinhos e crescendo com ela os espinhos, a sufocaram;', 7, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E outra caiu em boa terra, e, nascida, produziu fruto, a cento por um. Dizendo ele estas coisas, clamava: Quem tem ouvidos para ouvir, ouça.', 8, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os seus discípulos o interrogaram, dizendo: Que parábola é esta?', 9, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele disse: A vós vos é dado conhecer os mistérios do reino de Deus, mas aos outros por parábolas, para que vendo, não vejam, e ouvindo, não entendam.', 10, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Esta é, pois, a parábola: A semente é a palavra de Deus;', 11, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que estão junto do caminho, estes são os que ouvem; depois vem o diabo, e tira-lhes do coração a palavra, para que não se salvem, crendo;', 12, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que estão sobre pedra, estes são os que, ouvindo a palavra, a recebem com alegria, mas, como não têm raiz, apenas crêem por algum tempo, e no tempo da tentação se desviam;', 13, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a que caiu entre espinhos, esses são os que ouviram e, indo pordiante, são sufocados com os cuidados e riquezas e deleites da vida, e não dão fruto com perfeição;', 14, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E a que caiu em boa terra, esses são os que, ouvindo a palavra, a conservam num coração honesto e bom, e dão fruto com perseverança.', 15, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ninguém, acendendo uma candeia, a cobre com algum vaso, ou a põe debaixo da cama; mas põe-na no velador, para que os que entram vejam a luz.', 16, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque não há coisa oculta que não haja de manifestar-se, nem escondida que não haja de saber-se e vir à luz.', 17, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Vede, pois, como ouvis; porque a qualquer que tiver lhe será dado, e a qualquer que não tiver até o que parece ter lhe será tirado.', 18, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foram ter com ele sua mãe e seus irmãos, e não podiam aproximar-se dele, por causa da multidão.', 19, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E foi-lhe dito: Estão lá fora tua mãe e teus irmãos, que querem ver-te.', 20, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas, respondendo ele, disse-lhes: Minha mãe e meus irmãos são aqueles que ouvem a palavra de Deus e a executam.', 21, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, num daqueles dias, entrou num barco com seus discípulos, e disse-lhes: Passemos para o outro lado do lago. E partiram.', 22, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, navegando eles, adormeceu; e sobreveio uma tempestade de vento no lago, e enchiam-se de água, estando em perigo.', 23, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, chegando-se a ele, o despertaram, dizendo: Mestre, Mestre, perecemos. E ele, levantando-se, repreendeu o vento e a fúria da água; e cessaram, e fez-se bonança.', 24, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse-lhes: Onde está a vossa fé? E eles, temendo, maravilharam-se, dizendo uns aos outros: Quem é este, que até aos ventos e à água manda, e lhe obedecem?', 25, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E navegaram para a terra dos gadarenos, que está defronte da Galiléia.', 26, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando desceu para terra, saiu-lhe ao encontro, vindo da cidade, um homem que desde muito tempo estava possesso de demônios, e não andava vestido, nem habitava em qualquer casa, mas nos sepulcros.', 27, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, quando viu a Jesus, prostrou-se diante dele, exclamando, e dizendo com grande voz: Que tenho eu contigo, Jesus, Filho do Deus Altíssimo? Peço-te que não me atormentes.', 28, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tinha ordenado ao espírito imundo que saísse daquele homem; pois já havia muito tempo que o arrebatava. E guardavamno preso, com grilhões e cadeias; mas, quebrando as prisões, era impelido pelo demônio para os desertos.', 29, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E perguntou-lhe Jesus, dizendo: Qual é o teu nome? E ele disse: Legião; porque tinham entrado nele muitos demônios.', 30, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E rogavam-lhe que os não mandasse para o abismo.', 31, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E andava ali pastando no monte uma vara de muitos porcos; e rogaram-lhe que lhes concedesse entrar neles; e concedeu-lho.', 32, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, tendo saído os demônios do homem, entraram nos porcos, e a manada precipitou-se de um despenhadeiro no lago, e afogou-se.', 33, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aqueles que os guardavam, vendo o que acontecera, fugiram, e foram anunciá-lo na cidade e nos campos.', 34, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E saíram a ver o que tinha acontecido, e vieram ter com Jesus. Acharam então o homem, de quem haviam saído os demônios, vestido, e em seu juízo, assentado aos pés de Jesus; e temeram.', 35, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E os que tinham visto contaram-lhes também como fora salvo aquele endemoninhado.', 36, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E toda a multidão da terra dos gadarenos ao redor lhe rogou que se retirasse deles; porque estavam possuídos de grande temor. E entrando ele no barco, voltou.', 37, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aquele homem, de quem haviam saído os demônios, rogou-lhe que o deixasse estar com ele; mas Jesus o despediu, dizendo:', 38, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Torna para tua casa, e conta quão grandes coisas te fez Deus. E ele foi apregoando por toda a cidade quão grandes coisas Jesus lhe tinha feito.', 39, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E aconteceu que, quando voltou Jesus, a multidão o recebeu, porque todos o estavam esperando.', 40, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E eis que chegou um homem de nome Jairo, que era príncipe da sinagoga; e, prostrando-se aos pés de Jesus, rogava-lhe que entrasse em sua casa;', 41, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Porque tinha uma filha única, quase de doze anos, que estava à morte. E indo ele, apertava-o a multidão.', 42, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E uma mulher, que tinha um fluxo de sangue, havia doze anos, e gastara com os médicos todos os seus haveres, e por nenhum pudera ser curada,', 43, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Chegando por detrás dele, tocou na orla do seu vestido, e logo estancou o fluxo do seu sangue.', 44, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Jesus: Quem é que me tocou? E, negando todos, disse Pedro e os que estavam com ele: Mestre, a multidão te aperta e te oprime, e dizes: Quem é que me tocou?', 45, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E disse Jesus: Alguém me tocou, porque bem conheci que de mim saiu virtude.', 46, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Então, vendo a mulher que não podia ocultar-se, aproximou-se tremendo e, prostrando-se ante ele, declarou-lhe diante de todo o povo a causa por que lhe havia tocado, e como logo sarara.', 47, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E ele lhe disse: Tem bom ânimo, filha, a tua fé te salvou; vai em paz.', 48, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Estando ele ainda falando, chegou um dos do príncipe da sinagoga, dizendo: A tua filha já está morta, não incomodes o Mestre.', 49, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Jesus, porém, ouvindo-o, respondeu-lhe, dizendo: Não temas; crê somente, e será salva.', 50, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E, entrando em casa, a ninguém deixou entrar, senão a Pedro, e a Tiago, e a João, e ao pai e a mãe da menina.', 51, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E todos choravam, e a pranteavam; e ele disse: Não choreis; não está morta, mas dorme.', 52, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E riam-se dele, sabendo que estava morta.', 53, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('Mas ele, pondo-os todos fora, e pegando-lhe na mão, clamou, dizendo: Levanta-te, menina.', 54, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E o seu espírito voltou, e ela logo se levantou; e Jesus mandou que lhe dessem de comer.', 55, 8, 3);
INSERT INTO Versiculo(content, position, chapter, book) VALUES ('E seus pais ficaram maravilhados; e ele lhes mandou que a ninguém dissessem o que havia sucedido.', 56, 8, 3);